const { ESLint } = require("eslint");

async function checkUnsafeArgument() {
  const eslint = new ESLint({
    overrideConfig: {
      rules: {
        "@typescript-eslint/no-unsafe-argument": "error"
      }
    }
  });

  const results = await eslint.lintFiles(["src/**/*.ts", "src/**/*.svelte"]);
  const warnings = results.flatMap(file => 
    file.messages
      .filter(msg => msg.ruleId === "@typescript-eslint/no-unsafe-argument")
      .map(msg => ({
        file: file.filePath.replace(/^.*[\/]src[\/]/, 'src/'),
        line: msg.line,
        message: msg.message
      }))
  );

  console.log(`\nTotal no-unsafe-argument warnings: ${warnings.length}\n`);

  if (warnings.length > 0) {
    warnings.forEach(w => {
      console.log(`${w.file}:${w.line} - ${w.message}`);
    });
  } else {
    console.log("‚úÖ No no-unsafe-argument warnings found!");
  }
}

checkUnsafeArgument().catch(console.error);

/**
 * Animation Path Cache Service
 *
 * Pre-computes entire animation sequence at high frame rate to provide
 * smooth, gap-free trail rendering regardless of actual playback performance.
 *
 * Benefits:
 * - Frame-independent: No gaps when device stutters
 * - Smooth interpolation: Can look up positions between frames
 * - Performance: One-time computation, reused for entire playback
 */

import type { PropState } from "$shared";

import type { TrailPoint } from "../../domain/types/TrailTypes";

/**
 * Pre-computed position data for a single prop at a specific time
 */
export interface PrecomputedPropPosition {
  /** Beat number (fractional, e.g., 2.5) */
  beat: number;
  /** Timestamp in milliseconds from animation start */
  timestamp: number;
  /** Prop state (angles, positions) */
  propState: PropState;
  /** Calculated endpoint positions for both ends */
  endpoints: {
    left: { x: number; y: number }; // endType = 0
    right: { x: number; y: number }; // endType = 1 (tip)
  };
}

/**
 * Pre-computed path data for a single prop (blue or red)
 */
export interface PrecomputedPropPath {
  /** Array of positions ordered by time */
  positions: PrecomputedPropPosition[];
  /** Quick lookup by beat number (rounded to nearest cache frame) */
  beatLookup: Map<number, number>; // beat -> index in positions array
}

/**
 * Complete pre-computed animation cache
 */
export interface AnimationPathCacheData {
  /** Pre-computed path for blue prop */
  bluePropPath: PrecomputedPropPath;
  /** Pre-computed path for red prop */
  redPropPath: PrecomputedPropPath;
  /** Total duration in milliseconds */
  totalDurationMs: number;
  /** Total number of beats in sequence */
  totalBeats: number;
  /** Cache frame rate (FPS) */
  cacheFps: number;
  /** Whether cache is valid and ready to use */
  isValid: boolean;
}

/**
 * Configuration for animation path caching
 */
export interface PathCacheConfig {
  /**
   * Frame rate for cache generation
   * Higher = smoother but more memory
   * Recommended: 60-120 FPS
   */
  cacheFps: number;

  /**
   * Canvas size for endpoint calculations
   * Should match actual canvas size
   */
  canvasSize: number;

  /**
   * Prop dimensions for endpoint calculations
   */
  propDimensions: {
    width: number;
    height: number;
  };
}

export const DEFAULT_PATH_CACHE_CONFIG: PathCacheConfig = {
  cacheFps: 120, // High FPS for ultra-smooth trails
  canvasSize: 950,
  propDimensions: { width: 120, height: 15 },
};

/**
 * Animation Path Cache Service
 *
 * Pre-computes animation paths to enable smooth, gap-free trail rendering.
 */
export class AnimationPathCache {
  private cacheData: AnimationPathCacheData | null = null;
  private config: PathCacheConfig;

  // Constants matching AnimatorCanvas.svelte
  private readonly GRID_HALFWAY_POINT_OFFSET = 150;
  private readonly INWARD_FACTOR = 0.95;

  constructor(config: Partial<PathCacheConfig> = {}) {
    this.config = { ...DEFAULT_PATH_CACHE_CONFIG, ...config };
  }

  /**
   * Pre-compute animation paths for entire sequence
   *
   * @param orchestrator - Animation orchestrator to calculate prop states
   * @param totalBeats - Total number of beats in sequence
   * @param beatDurationMs - Duration of each beat in milliseconds
   */
  precomputePaths(
    calculateStateFunc: (beat: number) => {
      blueProp: PropState;
      redProp: PropState;
    },
    totalBeats: number,
    beatDurationMs: number
  ): AnimationPathCacheData {
    const totalDurationMs = totalBeats * beatDurationMs;
    const frameTimeMs = 1000 / this.config.cacheFps;
    const totalFrames = Math.ceil(totalDurationMs / frameTimeMs);

    const bluePositions: PrecomputedPropPosition[] = [];
    const redPositions: PrecomputedPropPosition[] = [];

    // Pre-compute positions for every virtual frame
    for (let frame = 0; frame <= totalFrames; frame++) {
      const timestamp = frame * frameTimeMs;
      const beat = timestamp / beatDurationMs; // Fractional beat number

      // Get prop states from orchestrator
      const { blueProp, redProp } = calculateStateFunc(beat);

      // Calculate endpoints for blue prop
      const blueEndpoints = {
        left: this.calculatePropEndpoint(blueProp, 0),
        right: this.calculatePropEndpoint(blueProp, 1),
      };

      // Calculate endpoints for red prop
      const redEndpoints = {
        left: this.calculatePropEndpoint(redProp, 0),
        right: this.calculatePropEndpoint(redProp, 1),
      };

      // Store positions
      bluePositions.push({
        beat,
        timestamp,
        propState: { ...blueProp },
        endpoints: blueEndpoints,
      });

      redPositions.push({
        beat,
        timestamp,
        propState: { ...redProp },
        endpoints: redEndpoints,
      });
    }

    // Build beat lookup tables
    const blueBeatLookup = this.buildBeatLookup(bluePositions);
    const redBeatLookup = this.buildBeatLookup(redPositions);

    // Create cache data
    this.cacheData = {
      bluePropPath: {
        positions: bluePositions,
        beatLookup: blueBeatLookup,
      },
      redPropPath: {
        positions: redPositions,
        beatLookup: redBeatLookup,
      },
      totalDurationMs,
      totalBeats,
      cacheFps: this.config.cacheFps,
      isValid: true,
    };

    return this.cacheData;
  }

  /**
   * Get pre-computed trail points for a beat range
   *
   * @param propIndex - 0 for blue, 1 for red
   * @param endType - 0 for left end, 1 for right end (tip)
   * @param startBeat - Start beat number (fractional, e.g., 2.5)
   * @param endBeat - End beat number (fractional, e.g., 4.2)
   * @returns Array of trail points with beat-relative timestamps
   */
  getTrailPoints(
    propIndex: 0 | 1,
    endType: 0 | 1,
    startBeat: number,
    endBeat: number
  ): TrailPoint[] {
    if (!this.cacheData?.isValid) {
      return [];
    }

    const propPath =
      propIndex === 0
        ? this.cacheData.bluePropPath
        : this.cacheData.redPropPath;

    // Convert beat range to frame indices
    const beatDurationMs =
      this.cacheData.totalDurationMs / this.cacheData.totalBeats;
    const frameTimeMs = 1000 / this.config.cacheFps;

    const startTime = startBeat * beatDurationMs;
    const endTime = endBeat * beatDurationMs;

    const startFrame = Math.floor(startTime / frameTimeMs);
    const endFrame = Math.ceil(endTime / frameTimeMs);

    const trailPoints: TrailPoint[] = [];

    for (let frame = startFrame; frame <= endFrame; frame++) {
      if (frame >= 0 && frame < propPath.positions.length) {
        const position = propPath.positions[frame]!;
        const endpoint =
          endType === 0 ? position.endpoints.left : position.endpoints.right;

        trailPoints.push({
          x: endpoint.x,
          y: endpoint.y,
          timestamp: position.timestamp, // Beat-relative timestamp (0ms to totalDurationMs)
          propIndex,
          endType,
        });
      }
    }

    return trailPoints;
  }

  /**
   * Get pre-computed trail points for a time range (animation-relative)
   *
   * @param propIndex - 0 for blue, 1 for red
   * @param endType - 0 for left end, 1 for right end (tip)
   * @param startTimeMs - Start time in ms from animation start (0-based)
   * @param endTimeMs - End time in ms from animation start (0-based)
   * @returns Array of trail points
   */
  getTrailPointsByTime(
    propIndex: 0 | 1,
    endType: 0 | 1,
    startTimeMs: number,
    endTimeMs: number
  ): TrailPoint[] {
    if (!this.cacheData?.isValid) {
      return [];
    }

    const beatDurationMs =
      this.cacheData.totalDurationMs / this.cacheData.totalBeats;
    const startBeat = startTimeMs / beatDurationMs;
    const endBeat = endTimeMs / beatDurationMs;

    return this.getTrailPoints(propIndex, endType, startBeat, endBeat);
  }

  /**
   * Get cached points for IAnimationCacheService interface compatibility
   * This is a wrapper around getTrailPoints to match the expected interface signature
   *
   * @param propIndex - 0 for blue, 1 for red
   * @param endType - 0 for left end, 1 for right end (tip)
   * @param startBeat - Start beat number (fractional, e.g., 2.5)
   * @param endBeat - End beat number (fractional, e.g., 4.2)
   * @param canvasSize - Canvas size (unused, kept for interface compatibility)
   * @returns Array of trail points with beat-relative timestamps
   */
  getCachedPoints(
    propIndex: 0 | 1,
    endType: 0 | 1,
    startBeat: number,
    endBeat: number,
    canvasSize: number
  ): TrailPoint[] {
    // canvasSize is ignored - cache uses standard coordinate system (950x950)
    // and points are transformed by AnimatorCanvas as needed
    return this.getTrailPoints(propIndex, endType, startBeat, endBeat);
  }

  /**
   * Get position at specific beat number
   *
   * @param propIndex - 0 for blue, 1 for red
   * @param beat - Beat number (can be fractional)
   * @returns Position data or null if not found
   */
  getPositionAtBeat(
    propIndex: 0 | 1,
    beat: number
  ): PrecomputedPropPosition | null {
    if (!this.cacheData?.isValid) {
      return null;
    }

    const propPath =
      propIndex === 0
        ? this.cacheData.bluePropPath
        : this.cacheData.redPropPath;

    const roundedBeat =
      Math.round(beat * this.config.cacheFps) / this.config.cacheFps;
    const index = propPath.beatLookup.get(roundedBeat);

    if (
      index !== undefined &&
      index >= 0 &&
      index < propPath.positions.length
    ) {
      return propPath.positions[index]!;
    }

    return null;
  }

  /**
   * Get all cached positions for a prop
   *
   * @param propIndex - 0 for blue, 1 for red
   * @returns Array of all pre-computed positions
   */
  getAllPositions(propIndex: 0 | 1): readonly PrecomputedPropPosition[] {
    if (!this.cacheData?.isValid) {
      return [];
    }

    const propPath =
      propIndex === 0
        ? this.cacheData.bluePropPath
        : this.cacheData.redPropPath;

    return propPath.positions;
  }

  /**
   * Check if cache is valid and ready
   */
  isValid(): boolean {
    return this.cacheData !== null && this.cacheData.isValid;
  }

  /**
   * Get cache metadata
   */
  getCacheInfo() {
    if (!this.cacheData) {
      return null;
    }

    return {
      totalBeats: this.cacheData.totalBeats,
      totalDurationMs: this.cacheData.totalDurationMs,
      cacheFps: this.cacheData.cacheFps,
      bluePointCount: this.cacheData.bluePropPath.positions.length,
      redPointCount: this.cacheData.redPropPath.positions.length,
    };
  }

  /**
   * Clear cache and free memory
   */
  clear(): void {
    this.cacheData = null;
  }

  /**
   * Update cache configuration
   */
  setConfig(config: Partial<PathCacheConfig>): void {
    this.config = { ...this.config, ...config };
    // Invalidate cache if config changes
    if (this.cacheData) {
      this.cacheData.isValid = false;
    }
  }

  /**
   * Calculate prop endpoint position
   * Matches logic in AnimatorCanvas.svelte:calculatePropEndpoint
   */
  private calculatePropEndpoint(
    prop: PropState,
    endType: 0 | 1
  ): { x: number; y: number } {
    const canvasSize = this.config.canvasSize;
    const centerX = canvasSize / 2;
    const centerY = canvasSize / 2;
    const gridScaleFactor = canvasSize / 950;
    const scaledHalfwayRadius =
      this.GRID_HALFWAY_POINT_OFFSET * gridScaleFactor;

    let propCenterX: number;
    let propCenterY: number;

    // Calculate prop center position
    if (prop.x !== undefined && prop.y !== undefined) {
      // Cartesian coordinates (for DASH motions)
      propCenterX = centerX + prop.x * scaledHalfwayRadius * this.INWARD_FACTOR;
      propCenterY = centerY + prop.y * scaledHalfwayRadius * this.INWARD_FACTOR;
    } else {
      // Polar coordinates (for circular motions)
      propCenterX =
        centerX +
        Math.cos(prop.centerPathAngle) *
          scaledHalfwayRadius *
          this.INWARD_FACTOR;
      propCenterY =
        centerY +
        Math.sin(prop.centerPathAngle) *
          scaledHalfwayRadius *
          this.INWARD_FACTOR;
    }

    // Calculate endpoint based on staff rotation
    const staffHalfWidth =
      (this.config.propDimensions.width / 2) * gridScaleFactor;
    const staffEndOffset = endType === 1 ? staffHalfWidth : -staffHalfWidth;

    const endpointX =
      propCenterX + Math.cos(prop.staffRotationAngle) * staffEndOffset;
    const endpointY =
      propCenterY + Math.sin(prop.staffRotationAngle) * staffEndOffset;

    return { x: endpointX, y: endpointY };
  }

  /**
   * Build beat lookup table for fast position retrieval
   */
  private buildBeatLookup(
    positions: PrecomputedPropPosition[]
  ): Map<number, number> {
    const lookup = new Map<number, number>();

    for (let i = 0; i < positions.length; i++) {
      const position = positions[i]!;
      const roundedBeat =
        Math.round(position.beat * this.config.cacheFps) / this.config.cacheFps;
      lookup.set(roundedBeat, i);
    }

    return lookup;
  }
}

/**
 * Global cache instance (singleton pattern)
 * Can be shared across components for efficiency
 */
let globalCacheInstance: AnimationPathCache | null = null;

/**
 * Get or create global animation path cache instance
 */
export function getGlobalPathCache(
  config?: Partial<PathCacheConfig>
): AnimationPathCache {
  if (!globalCacheInstance) {
    globalCacheInstance = new AnimationPathCache(config);
  } else if (config) {
    globalCacheInstance.setConfig(config);
  }
  return globalCacheInstance;
}

/**
 * Clear global cache instance
 */
export function clearGlobalPathCache(): void {
  if (globalCacheInstance) {
    globalCacheInstance.clear();
    globalCacheInstance = null;
  }
}

/**
 * Endpoint Calculation Service
 *
 * Handles calculation of motion endpoints and staff angles
 * for different motion types.
 */

// HMR deep path test - testing file watcher in nested directory with spaces

import { inject, injectable } from "inversify";

import type { MotionData, MotionEndpoints } from "$shared";
import { MotionType, Orientation, RotationDirection } from "$shared";
import { TYPES } from "$shared/inversify/types";

import { PI } from "../../domain/constants/math-constants.js";
import type { IAngleCalculator } from "../contracts/IAngleCalculator";
import type { IEndpointCalculator } from "../contracts/IEndpointCalculator";
import type { IMotionCalculator } from "../contracts/IMotionCalculator";

// ‚úÖ ELIMINATED: StepEndpoints and StepDefinition - pointless reshuffling!
// Work directly with MotionData and return simple objects

@injectable()
export class EndpointCalculator implements IEndpointCalculator {
  constructor(
    @inject(TYPES.IAngleCalculator) private angleCalculator: IAngleCalculator,
    @inject(TYPES.IMotionCalculator) private motionCalculator: IMotionCalculator
  ) {}

  /**
   * Calculate motion endpoints directly from MotionData (NATIVE!)
   */
  calculateMotionEndpoints(motionData: MotionData): MotionEndpoints {
    const {
      startLocation,
      endLocation,
      startOrientation,
      endOrientation,
      motionType,
      rotationDirection,
      turns = 0,
    } = motionData;

    // Logging removed - too noisy

    const startCenterAngle =
      this.angleCalculator.mapPositionToAngle(startLocation);
    const startStaffAngle = this.angleCalculator.mapOrientationToAngle(
      startOrientation,
      startCenterAngle
    );
    const targetCenterAngle =
      this.angleCalculator.mapPositionToAngle(endLocation);

    let calculatedTargetStaffAngle: number;
    let calculatedStaffRotationDelta: number;

    // Calculate target staff angle based on motion type
    switch (motionType) {
      case MotionType.PRO: {
        const numericTurns = turns;
        const effectiveRotDir = rotationDirection;

        // Calculate delta for interpolation (un-normalized)
        const centerMovement = this.angleCalculator.normalizeAngleSigned(
          targetCenterAngle - startCenterAngle
        );
        const dir =
          effectiveRotDir === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
        const propRotation = dir * numericTurns * PI;
        const staffMovement = centerMovement; // PRO: same direction as grid movement
        calculatedStaffRotationDelta = staffMovement + propRotation;

        // Calculate normalized target angle
        calculatedTargetStaffAngle =
          this.motionCalculator.calculateProTargetAngle(
            startCenterAngle,
            targetCenterAngle,
            startStaffAngle,
            numericTurns,
            effectiveRotDir
          );
        break;
      }
      case MotionType.ANTI: {
        const numericTurns = turns;
        const effectiveRotDir = rotationDirection;

        // Calculate delta for interpolation (un-normalized)
        const centerMovement = this.angleCalculator.normalizeAngleSigned(
          targetCenterAngle - startCenterAngle
        );
        const dir =
          effectiveRotDir === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
        const propRotation = dir * numericTurns * PI;
        const staffMovement = -centerMovement; // ANTI: opposite direction to grid movement
        calculatedStaffRotationDelta = staffMovement + propRotation;

        // Calculate normalized target angle
        calculatedTargetStaffAngle =
          this.motionCalculator.calculateAntispinTargetAngle(
            startCenterAngle,
            targetCenterAngle,
            startStaffAngle,
            numericTurns,
            effectiveRotDir
          );
        break;
      }
      case MotionType.STATIC: {
        const numericTurns = turns;
        const effectiveRotDir = rotationDirection;

        // Calculate base orientation angle (without turns)
        const baseTargetAngle = this.motionCalculator.calculateStaticStaffAngle(
          startStaffAngle,
          endOrientation,
          targetCenterAngle
        );

        // If there are turns, calculate rotation delta respecting direction
        if (
          numericTurns > 0 &&
          effectiveRotDir !== RotationDirection.NO_ROTATION
        ) {
          // STATIC with turns: rotation is determined ONLY by turns and direction
          // DO NOT add orientation change - the turns value is the total rotation
          const dir =
            effectiveRotDir === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
          calculatedStaffRotationDelta = dir * numericTurns * PI; // 1 turn = 180¬∞ (œÄ)
          calculatedTargetStaffAngle =
            this.angleCalculator.normalizeAnglePositive(
              startStaffAngle + calculatedStaffRotationDelta
            );
        } else {
          // No turns or NO_ROTATION: use orientation (shortest path)
          calculatedTargetStaffAngle = baseTargetAngle;
          calculatedStaffRotationDelta =
            this.angleCalculator.normalizeAngleSigned(
              calculatedTargetStaffAngle - startStaffAngle
            );
        }
        break;
      }
      case MotionType.DASH: {
        const numericTurns = turns;
        const effectiveRotDir = rotationDirection;

        calculatedTargetStaffAngle =
          this.motionCalculator.calculateDashTargetAngle(
            startStaffAngle,
            endOrientation,
            targetCenterAngle,
            numericTurns,
            effectiveRotDir
          );

        // DASH with turns: rotation is determined ONLY by turns and direction
        // DO NOT add orientation change - the turns value is the total rotation
        // This ensures multi-turn dashes rotate the full amount, not shortest path
        if (numericTurns > 0) {
          const dir =
            effectiveRotDir === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
          calculatedStaffRotationDelta = dir * numericTurns * PI; // 1 turn = 180¬∞ (œÄ)
        } else {
          // No turns: calculate based on orientation change only
          const baseAngle = this.motionCalculator.calculateDashTargetAngle(
            startStaffAngle,
            endOrientation,
            targetCenterAngle,
            0,
            effectiveRotDir
          );
          calculatedStaffRotationDelta =
            this.angleCalculator.normalizeAngleSigned(
              baseAngle - startStaffAngle
            );
        }
        break;
      }
      case MotionType.FLOAT: {
        calculatedTargetStaffAngle =
          this.motionCalculator.calculateFloatStaffAngle(startStaffAngle);
        // For FLOAT, no rotation
        calculatedStaffRotationDelta = 0;
        break;
      }
      default: {
        const exhaustiveCheck: never = motionType;
        console.warn(
          `Unknown motion type '${String(exhaustiveCheck)}'. Treating as static.`
        );
        calculatedTargetStaffAngle = startStaffAngle;
        calculatedStaffRotationDelta = 0;
        break;
      }
    }

    return {
      startCenterAngle,
      startStaffAngle,
      targetCenterAngle,
      targetStaffAngle: calculatedTargetStaffAngle,
      staffRotationDelta: calculatedStaffRotationDelta,
      rotationDirection, // Pass through for interpolation
    };
  }

  /**
   * Calculate endpoint staff angle for a motion (NATIVE MotionData!)
   */
  calculateEndpointStaffAngle(motionData: MotionData): number {
    const endpoints = this.calculateMotionEndpoints(motionData);
    return endpoints.targetStaffAngle;
  }
}

/**
 * PixiJS Animation Renderer Implementation (Refactored)
 *
 * High-performance WebGL-based renderer for animation visualization.
 * Uses PixiJS for GPU-accelerated rendering of props, trails, grid, and glyphs.
 *
 * Performance improvements over Canvas 2D:
 * - 3-5x faster trail rendering (WebGL batched line drawing)
 * - 2-3x faster prop rendering (GPU transforms)
 * - Near-zero cost for rotation/scaling (GPU matrix math)
 * - Better mobile performance (efficient on mobile GPUs)
 *
 * Architecture:
 * This class now follows Single Responsibility Principle by delegating to specialized services:
 * - PixiApplicationManager: App lifecycle and canvas management
 * - PixiTextureLoader: Texture loading and caching
 * - PixiSpriteManager: Sprite lifecycle and updates
 * - PixiTrailRenderer: Trail rendering logic
 * - PixiPropRenderer: Prop positioning calculations
 * - PixiFadeTransitionManager: Glyph fade transitions
 */

import { injectable } from "inversify";
import { Container, Graphics } from "pixi.js";

import type { PropState } from "../../domain/types/PropState";
import type { TrailPoint, TrailSettings } from "../../domain/types/TrailTypes";
import type { IPixiAnimationRenderer } from "../contracts/IPixiAnimationRenderer";
import { PixiApplicationManager } from "./pixi/PixiApplicationManager";
import { PixiFadeTransitionManager } from "./pixi/PixiFadeTransitionManager";
import { PixiPropRenderer } from "./pixi/PixiPropRenderer";
import { PixiSpriteManager } from "./pixi/PixiSpriteManager";
import { PixiTextureLoader } from "./pixi/PixiTextureLoader";
import { PixiTrailRenderer } from "./pixi/PixiTrailRenderer";

@injectable()
export class PixiAnimationRenderer implements IPixiAnimationRenderer {
  // Specialized service managers
  private appManager: PixiApplicationManager;
  private textureLoader: PixiTextureLoader;
  private spriteManager: PixiSpriteManager | null = null;
  private trailRenderer: PixiTrailRenderer | null = null;
  private propRenderer: PixiPropRenderer | null = null;
  private fadeManager: PixiFadeTransitionManager;

  // Layer containers (only needed for initialization)
  private gridContainer: Container | null = null;
  private trailContainer: Container | null = null;
  private propContainer: Container | null = null;
  private glyphContainer: Container | null = null;

  constructor() {
    this.appManager = new PixiApplicationManager();
    this.textureLoader = new PixiTextureLoader();
    this.fadeManager = new PixiFadeTransitionManager();
  }

  async initialize(
    container: HTMLElement,
    size: number,
    backgroundAlpha: number = 1
  ): Promise<void> {
    // Initialize PixiJS application
    const app = await this.appManager.initialize(
      container,
      size,
      backgroundAlpha
    );

    // Create layer hierarchy (directly on stage)
    this.gridContainer = new Container();
    this.trailContainer = new Container();
    this.propContainer = new Container();
    this.glyphContainer = new Container();

    // Add layers directly to stage in rendering order (bottom to top)
    app.stage.addChild(this.gridContainer);
    app.stage.addChild(this.trailContainer);
    app.stage.addChild(this.propContainer);
    app.stage.addChild(this.glyphContainer);

    // Create trail graphics (persistent, cleared and redrawn each frame)
    const blueTrailGraphics = new Graphics();
    const redTrailGraphics = new Graphics();
    this.trailContainer.addChild(blueTrailGraphics);
    this.trailContainer.addChild(redTrailGraphics);

    // Initialize specialized managers
    this.spriteManager = new PixiSpriteManager(
      this.gridContainer,
      this.propContainer,
      this.glyphContainer
    );
    this.trailRenderer = new PixiTrailRenderer(this.trailContainer);
    this.propRenderer = new PixiPropRenderer(size);

    console.log("[PixiAnimationRenderer] Initialized successfully");
  }

  resize(newSize: number): void {
    this.appManager.resize(newSize);
    this.spriteManager?.resizeAllSprites(newSize);
    this.propRenderer?.updateSize(newSize);
  }

  async loadPropTextures(propType: string): Promise<void> {
    await this.textureLoader.loadPropTextures(propType);
    console.log(`[PixiAnimationRenderer] Loaded prop textures for ${propType}`);
  }

  async loadGridTexture(gridMode: string): Promise<void> {
    const canvasSize = this.appManager.getCurrentSize();
    const gridTexture = await this.textureLoader.loadGridTexture(
      gridMode,
      canvasSize
    );

    // Update grid sprite
    this.spriteManager?.updateGridSprite(gridTexture, canvasSize);

    // Trigger a render to show the grid
    this.appManager.render();

    console.log(`[PixiAnimationRenderer] Loaded grid texture for ${gridMode}`);
  }

  async loadGlyphTexture(
    svgString: string,
    _width: number,
    _height: number
  ): Promise<void> {
    const { current, previous } = await this.textureLoader.loadGlyphTexture(
      svgString,
      _width,
      _height
    );

    const canvasSize = this.appManager.getCurrentSize();
    const sprites = this.spriteManager!.updateGlyphSprite(
      current,
      canvasSize,
      previous
    );

    // Start fade transition
    if (sprites.previous) {
      this.fadeManager.startFadeTransition();
    } else {
      // First glyph - no fade, just show it
      this.spriteManager!.setGlyphAlpha(1);
    }

    console.log("[PixiAnimationRenderer] Loaded glyph texture");
  }

  renderScene(params: {
    blueProp: PropState | null;
    redProp: PropState | null;
    secondaryBlueProp?: PropState | null;
    secondaryRedProp?: PropState | null;
    gridVisible: boolean;
    gridMode: string | null;
    letter: string | null;
    turnsTuple: string | null;
    bluePropDimensions: { width: number; height: number };
    redPropDimensions: { width: number; height: number };
    blueTrailPoints: TrailPoint[];
    redTrailPoints: TrailPoint[];
    secondaryBlueTrailPoints?: TrailPoint[];
    secondaryRedTrailPoints?: TrailPoint[];
    trailSettings: TrailSettings;
    currentTime: number;
  }): void {
    if (
      !this.appManager.isReady() ||
      !this.spriteManager ||
      !this.trailRenderer ||
      !this.propRenderer
    ) {
      return;
    }

    // Update fade progress if fading
    if (this.fadeManager.isFadingInProgress()) {
      const fadeComplete = this.fadeManager.updateFadeProgress(
        params.currentTime,
        this.spriteManager.getGlyphSprite(),
        this.spriteManager.getPreviousGlyphSprite()
      );

      if (fadeComplete) {
        // Remove and destroy previous glyph
        this.spriteManager.removePreviousGlyph();
        this.textureLoader.clearPreviousGlyphTexture();
      }
    }

    // Update grid visibility
    this.spriteManager.setGridVisibility(params.gridVisible);

    // Render trails
    this.trailRenderer.renderTrails(
      params.blueTrailPoints,
      params.redTrailPoints,
      params.trailSettings,
      params.currentTime,
      !!params.blueProp,
      !!params.redProp
    );

    // Get textures
    const bluePropTexture = this.textureLoader.getBluePropTexture();
    const redPropTexture = this.textureLoader.getRedPropTexture();

    // Render primary blue prop
    if (params.blueProp && bluePropTexture && !params.trailSettings.hideProps) {
      const transform = this.propRenderer.calculatePropTransform(
        params.blueProp,
        params.bluePropDimensions
      );
      this.spriteManager.updatePropSprite(
        "blue",
        bluePropTexture,
        { x: transform.x, y: transform.y },
        { width: transform.width, height: transform.height },
        transform.rotation
      );
    } else {
      this.spriteManager.setPropVisibility("blue", false);
    }

    // Render primary red prop
    if (params.redProp && redPropTexture && !params.trailSettings.hideProps) {
      const transform = this.propRenderer.calculatePropTransform(
        params.redProp,
        params.redPropDimensions
      );
      this.spriteManager.updatePropSprite(
        "red",
        redPropTexture,
        { x: transform.x, y: transform.y },
        { width: transform.width, height: transform.height },
        transform.rotation
      );
    } else {
      this.spriteManager.setPropVisibility("red", false);
    }

    // Render secondary blue prop (tunnel mode)
    if (
      params.secondaryBlueProp &&
      bluePropTexture &&
      !params.trailSettings.hideProps
    ) {
      const transform = this.propRenderer.calculatePropTransform(
        params.secondaryBlueProp,
        params.bluePropDimensions
      );
      this.spriteManager.updatePropSprite(
        "secondaryBlue",
        bluePropTexture,
        { x: transform.x, y: transform.y },
        { width: transform.width, height: transform.height },
        transform.rotation
      );
    } else {
      this.spriteManager.setPropVisibility("secondaryBlue", false);
    }

    // Render secondary red prop (tunnel mode)
    if (
      params.secondaryRedProp &&
      redPropTexture &&
      !params.trailSettings.hideProps
    ) {
      const transform = this.propRenderer.calculatePropTransform(
        params.secondaryRedProp,
        params.redPropDimensions
      );
      this.spriteManager.updatePropSprite(
        "secondaryRed",
        redPropTexture,
        { x: transform.x, y: transform.y },
        { width: transform.width, height: transform.height },
        transform.rotation
      );
    } else {
      this.spriteManager.setPropVisibility("secondaryRed", false);
    }

    // Manual render since autoStart is false
    this.appManager.render();
  }

  getApplication() {
    return this.appManager.getApplication();
  }

  getCanvas() {
    return this.appManager.getCanvas();
  }

  destroy(): void {
    console.log("[PixiAnimationRenderer] Starting cleanup...");

    try {
      // Destroy specialized managers
      this.spriteManager?.destroy();
      this.trailRenderer?.destroy();
      this.textureLoader.destroy();
      this.fadeManager.reset();

      // Get app for container cleanup
      const app = this.appManager.getApplication();
      if (app?.stage) {
        app.stage.removeChildren();
      }

      // Destroy containers
      try {
        this.gridContainer?.destroy();
        this.trailContainer?.destroy();
        this.propContainer?.destroy();
        this.glyphContainer?.destroy();
      } catch (e) {
        // Ignore container destroy errors
      }

      // Destroy application (handles canvas removal)
      this.appManager.destroy();

      // Clear references
      this.spriteManager = null;
      this.trailRenderer = null;
      this.propRenderer = null;
      this.gridContainer = null;
      this.trailContainer = null;
      this.propContainer = null;
      this.glyphContainer = null;

      console.log("[PixiAnimationRenderer] Destroyed");
    } catch (error) {
      console.error("[PixiAnimationRenderer] Error during destroy:", error);
    }
  }
}

/**
 * Trail Capture Service Implementation
 *
 * Handles real-time trail point capture with distance-based adaptive sampling.
 *
 * Key Features:
 * - Distance-based sampling: Only captures points when prop moves >N pixels
 * - Adaptive density: Adjusts spacing based on device performance
 * - Cache backfill: Fills gaps during device stutters with pre-computed paths
 * - Loop detection: Automatically clears trails when animation loops (LOOP_CLEAR mode)
 * - Fade mode: Automatically prunes old points based on fade duration
 *
 * Architecture:
 * - Uses CircularBuffer for O(1) point insertion and memory efficiency
 * - Coordinates with PerformanceMonitor for adaptive spacing
 * - Coordinates with AnimationCacheService for backfill during stutters
 */

import type { PropState } from "../../domain/types/PropState";
import type {
  TrailPoint,
  TrailSettings,
} from "../../domain/types/TrailTypes";
import { TrackingMode, TrailMode, TrailStyle } from "../../domain/types/TrailTypes";
import { CircularBuffer } from "../../utils/CircularBuffer";
import type {
  IAnimationCacheService,
  IPerformanceMonitorService,
  ITrailCaptureService,
  PropDimensions,
  PropStates,
  TrailCaptureConfig,
} from "../contracts/ITrailCaptureService";

/**
 * Last captured point tracking for distance-based sampling
 */
interface LastCapturedPoint {
  x: number;
  y: number;
  beat: number;
  timestamp: number; // Animation-relative timestamp (0ms to totalDurationMs)
}

export class TrailCaptureService implements ITrailCaptureService {
  // Configuration
  private config: TrailCaptureConfig = {
    canvasSize: 500,
    bluePropDimensions: { width: 252.8, height: 77.8 },
    redPropDimensions: { width: 252.8, height: 77.8 },
    trailSettings: {
      enabled: false,
      mode: TrailMode.OFF,
      trackingMode: TrackingMode.RIGHT_END,
      style: TrailStyle.SMOOTH_LINE, // Will be overridden by actual settings
      fadeDurationMs: 3000,
      maxPoints: 1000,
      lineWidth: 2,
      glowEnabled: false,
      glowBlur: 0,
      blueColor: "#4A9EFF",
      redColor: "#FF6B6B",
      minOpacity: 0.2,
      maxOpacity: 0.8,
      hideProps: false,
      usePathCache: true,
      previewMode: false,
    },
  };

  // Trail buffers (one per prop/end combination)
  private blueTrailBuffer = new CircularBuffer<TrailPoint>(1000);
  private redTrailBuffer = new CircularBuffer<TrailPoint>(1000);
  private secondaryBlueTrailBuffer = new CircularBuffer<TrailPoint>(1000);
  private secondaryRedTrailBuffer = new CircularBuffer<TrailPoint>(1000);

  // Last captured points for distance-based sampling
  // Key format: "propIndex-endType" (e.g., "0-1" = blue prop, right end)
  private lastCapturedPoints = new Map<string, LastCapturedPoint>();

  // Animation timing
  private animationStartTime: number | null = null;
  private previousBeatForLoopDetection = 0;

  // Optional dependencies for advanced features
  private animationCacheService: IAnimationCacheService | null = null;
  private performanceMonitor: IPerformanceMonitorService | null = null;

  // Default point spacing (used if no performance monitor)
  private readonly DEFAULT_POINT_SPACING = 0.75;

  // Constants
  private readonly GRID_HALFWAY_POINT_OFFSET = 150; // Matches strict grid points
  private readonly INWARD_FACTOR = 1.0; // No inward adjustment for animation mode
  private readonly INITIALIZATION_DELAY_MS = 500; // Wait for panel open and textures
  private readonly LARGE_BEAT_GAP_THRESHOLD = 3.0; // >3 beats = seeking/major stutter
  private readonly INITIAL_JUMP_DISTANCE_THRESHOLD = 200; // Skip trails for huge jumps

  initialize(config: TrailCaptureConfig): void {
    this.config = { ...config };
    this.clearTrails();
  }

  updateConfig(config: Partial<TrailCaptureConfig>): void {
    this.config = { ...this.config, ...config };

    // If settings changed, update trail settings
    if (config.trailSettings) {
      this.updateSettings(config.trailSettings);
    }
  }

  updateSettings(settings: TrailSettings): void {
    this.config.trailSettings = settings;

    // Clear trails if disabled or mode is OFF
    if (!settings.enabled || settings.mode === TrailMode.OFF) {
      this.clearTrails();
    }
  }

  setAnimationCacheService(cacheService: IAnimationCacheService | null): void {
    this.animationCacheService = cacheService;
  }

  setPerformanceMonitor(monitor: IPerformanceMonitorService | null): void {
    this.performanceMonitor = monitor;
  }

  captureFrame(
    props: PropStates,
    currentBeat: number | undefined,
    currentTime: number
  ): void {
    const { trailSettings } = this.config;

    // Skip if trails disabled
    if (!trailSettings.enabled || trailSettings.mode === TrailMode.OFF) {
      return;
    }

    // Initialize animation start time on first call
    if (this.animationStartTime === null) {
      this.animationStartTime = currentTime;
    }

    // Calculate animation-relative time (0ms to totalDurationMs)
    const animRelativeTime = currentTime - this.animationStartTime;

    // Use current beat (fallback to 0 if undefined)
    const beat = currentBeat ?? 0;

    // Check for loop and clear trails if in LOOP_CLEAR mode
    if (
      trailSettings.mode === TrailMode.LOOP_CLEAR &&
      this.detectAnimationLoop(beat)
    ) {
      this.clearTrails();
      // Reset animation start time
      this.animationStartTime = currentTime;
    }

    // Capture trail points for each prop
    if (props.blueProp) {
      this.captureTrailPoint(
        props.blueProp,
        this.config.bluePropDimensions,
        0,
        animRelativeTime,
        beat
      );
    }
    if (props.redProp) {
      this.captureTrailPoint(
        props.redProp,
        this.config.redPropDimensions,
        1,
        animRelativeTime,
        beat
      );
    }
    if (props.secondaryBlueProp) {
      this.captureTrailPoint(
        props.secondaryBlueProp,
        this.config.bluePropDimensions,
        2,
        animRelativeTime,
        beat
      );
    }
    if (props.secondaryRedProp) {
      this.captureTrailPoint(
        props.secondaryRedProp,
        this.config.redPropDimensions,
        3,
        animRelativeTime,
        beat
      );
    }

    // Prune old trail points (fade mode only)
    this.pruneOldTrailPoints(animRelativeTime);
  }

  getTrailPoints(propIndex: 0 | 1 | 2 | 3, endType: 0 | 1): TrailPoint[] {
    const buffer = this.getBufferForProp(propIndex);
    const allPoints = buffer.toArray();

    // Filter points for this specific end
    return allPoints.filter((p) => p.endType === endType);
  }

  getAllTrailPoints(): {
    blue: TrailPoint[];
    red: TrailPoint[];
    secondaryBlue: TrailPoint[];
    secondaryRed: TrailPoint[];
  } {
    return {
      blue: this.blueTrailBuffer.toArray(),
      red: this.redTrailBuffer.toArray(),
      secondaryBlue: this.secondaryBlueTrailBuffer.toArray(),
      secondaryRed: this.secondaryRedTrailBuffer.toArray(),
    };
  }

  clearTrails(): void {
    this.blueTrailBuffer.clear();
    this.redTrailBuffer.clear();
    this.secondaryBlueTrailBuffer.clear();
    this.secondaryRedTrailBuffer.clear();
    this.lastCapturedPoints.clear();
    this.animationStartTime = null;
  }

  // ============================================================================
  // PRIVATE METHODS
  // ============================================================================

  /**
   * Get the appropriate buffer for a prop index
   */
  private getBufferForProp(
    propIndex: 0 | 1 | 2 | 3
  ): CircularBuffer<TrailPoint> {
    switch (propIndex) {
      case 0:
        return this.blueTrailBuffer;
      case 1:
        return this.redTrailBuffer;
      case 2:
        return this.secondaryBlueTrailBuffer;
      case 3:
        return this.secondaryRedTrailBuffer;
    }
  }

  /**
   * Detect if animation has looped (for LOOP_CLEAR mode)
   */
  private detectAnimationLoop(currentBeat: number | undefined): boolean {
    if (currentBeat === undefined) return false;
    const hasLooped =
      this.previousBeatForLoopDetection > 0.5 && currentBeat < 0.5;
    this.previousBeatForLoopDetection = currentBeat;
    return hasLooped;
  }

  /**
   * Capture trail point with distance-based sampling and intelligent cache backfill
   *
   * Strategy:
   * 1. Distance-based sampling: Only add points when prop moves >N pixels
   * 2. Intelligent backfill: Use cache to fill gaps during device stutters
   * 3. Adaptive density: Adjust spacing based on device performance
   */
  private captureTrailPoint(
    prop: PropState,
    propDimensions: PropDimensions,
    propIndex: 0 | 1 | 2 | 3,
    currentTime: number,
    currentBeat: number
  ): void {
    const { trailSettings } = this.config;

    // Determine which ends to track based on tracking mode
    const endsToTrack: Array<0 | 1> =
      trailSettings.trackingMode === TrackingMode.BOTH_ENDS
        ? [0, 1]
        : trailSettings.trackingMode === TrackingMode.LEFT_END
          ? [0]
          : [1]; // RIGHT_END

    // Select buffer based on prop index
    const buffer = this.getBufferForProp(propIndex);

    // Get adaptive point spacing
    const minSpacing = this.getAdaptivePointSpacing();

    for (const endType of endsToTrack) {
      const key = `${propIndex}-${endType}`;
      const lastPoint = this.lastCapturedPoints.get(key);

      // Calculate current endpoint position
      const endpoint = this.calculatePropEndpoint(
        prop,
        propDimensions,
        this.config.canvasSize,
        endType
      );

      // FIRST POINT: Wait for animation initialization
      if (lastPoint === undefined) {
        // Only capture first point after initialization delay
        if (currentTime >= this.INITIALIZATION_DELAY_MS) {
          // Map propIndex to 0|1 for storage (secondary props map to primary)
          const storagePropIndex: 0 | 1 = propIndex === 0 || propIndex === 2 ? 0 : 1;
          const point: TrailPoint = {
            x: endpoint.x,
            y: endpoint.y,
            timestamp: currentTime,
            propIndex: storagePropIndex,
            endType,
          };
          buffer.push(point);
        }

        // Always update tracking position (even if we don't capture the point yet)
        this.lastCapturedPoints.set(key, {
          x: endpoint.x,
          y: endpoint.y,
          beat: currentBeat,
          timestamp: currentTime,
        });
      } else {
        // SUBSEQUENT POINTS: Use distance-based sampling with optional cache backfill

        const beatDelta = Math.abs(currentBeat - lastPoint.beat);

        // Check if we have a LARGE beat gap (seeking or major stutter)
        const hasLargeBeatGap = beatDelta > this.LARGE_BEAT_GAP_THRESHOLD;

        if (
          hasLargeBeatGap &&
          trailSettings.usePathCache &&
          this.animationCacheService &&
          this.animationCacheService.isValid()
        ) {
          // CACHE BACKFILL: Device stuttered - fill gap with pre-computed points
          // Map all prop indices to primary props (0/1) for cache lookup
          const cachePropIndex: 0 | 1 = propIndex === 0 || propIndex === 2 ? 0 : 1;
          const cachedPoints = this.animationCacheService.getCachedPoints(
            cachePropIndex,
            endType,
            lastPoint.beat,
            currentBeat,
            this.config.canvasSize
          );

          // Add cached points but apply distance filtering to maintain consistent spacing
          let lastAddedX = lastPoint.x;
          let lastAddedY = lastPoint.y;
          let addedCount = 0;

          for (const cachedPoint of cachedPoints) {
            const dist = Math.hypot(
              cachedPoint.x - lastAddedX,
              cachedPoint.y - lastAddedY
            );

            if (dist >= minSpacing) {
              buffer.push(cachedPoint);
              lastAddedX = cachedPoint.x;
              lastAddedY = cachedPoint.y;
              addedCount++;
            }
          }

          // Debug log backfill events
          if (addedCount > 0) {
            const propName = ["blue", "red", "blue2", "red2"][propIndex];
            const endName = endType === 0 ? "left" : "right";
            console.log(
              `üîÑ BACKFILL: ${propName}-${endName}, gap ${beatDelta.toFixed(3)} beats, added ${addedCount} points`
            );
          }

          // Update last captured point
          this.lastCapturedPoints.set(key, {
            x: endpoint.x,
            y: endpoint.y,
            beat: currentBeat,
            timestamp: currentTime,
          });
        } else {
          // REAL-TIME SAMPLING: Normal playback - use distance-based sampling
          const distance = Math.hypot(
            endpoint.x - lastPoint.x,
            endpoint.y - lastPoint.y
          );

          // Detect initial jump (from default position to first beat position)
          const isInitialJump = distance > this.INITIAL_JUMP_DISTANCE_THRESHOLD;

          if (isInitialJump) {
            // Just update the tracking position without adding a trail point
            this.lastCapturedPoints.set(key, {
              x: endpoint.x,
              y: endpoint.y,
              beat: currentBeat,
              timestamp: currentTime,
            });
          } else if (distance >= minSpacing) {
            // Normal trail capture - add point if prop moved far enough
            // Map propIndex to 0|1 for storage (secondary props map to primary)
            const storagePropIndex: 0 | 1 = propIndex === 0 || propIndex === 2 ? 0 : 1;
            const point: TrailPoint = {
              x: endpoint.x,
              y: endpoint.y,
              timestamp: currentTime,
              propIndex: storagePropIndex,
              endType,
            };

            buffer.push(point);
            this.lastCapturedPoints.set(key, {
              x: endpoint.x,
              y: endpoint.y,
              beat: currentBeat,
              timestamp: currentTime,
            });
          }
          // If distance < minSpacing, skip this point (prevents oversaturation)
        }
      }
    }
  }

  /**
   * Calculate an endpoint position of a prop
   * Uses strict point positioning for animation viewer
   */
  private calculatePropEndpoint(
    prop: PropState,
    propDimensions: PropDimensions,
    canvasSize: number,
    endType: 0 | 1
  ): { x: number; y: number } {
    const centerX = canvasSize / 2;
    const centerY = canvasSize / 2;
    const gridScaleFactor = canvasSize / 950;
    const scaledHalfwayRadius =
      this.GRID_HALFWAY_POINT_OFFSET * gridScaleFactor;

    let propCenterX: number;
    let propCenterY: number;

    if (prop.x !== undefined && prop.y !== undefined) {
      propCenterX =
        centerX + prop.x * scaledHalfwayRadius * this.INWARD_FACTOR;
      propCenterY =
        centerY + prop.y * scaledHalfwayRadius * this.INWARD_FACTOR;
    } else {
      propCenterX =
        centerX +
        Math.cos(prop.centerPathAngle) *
          scaledHalfwayRadius *
          this.INWARD_FACTOR;
      propCenterY =
        centerY +
        Math.sin(prop.centerPathAngle) *
          scaledHalfwayRadius *
          this.INWARD_FACTOR;
    }

    const staffHalfWidth = (propDimensions.width / 2) * gridScaleFactor;
    const staffEndOffset = endType === 1 ? staffHalfWidth : -staffHalfWidth;

    const endX =
      propCenterX + Math.cos(prop.staffRotationAngle) * staffEndOffset;
    const endY =
      propCenterY + Math.sin(prop.staffRotationAngle) * staffEndOffset;

    return { x: endX, y: endY };
  }

  /**
   * Get minimum point spacing based on device performance
   * Returns distance in pixels that prop must move before adding a new trail point
   */
  private getAdaptivePointSpacing(): number {
    if (this.performanceMonitor) {
      return this.performanceMonitor.getAdaptivePointSpacing();
    }
    return this.DEFAULT_POINT_SPACING;
  }

  /**
   * Remove old trail points based on fade duration
   * Uses animation-relative timestamps (0ms to totalDurationMs)
   */
  private pruneOldTrailPoints(currentTime: number): void {
    if (this.config.trailSettings.mode !== TrailMode.FADE) return;

    const cutoffTime = currentTime - this.config.trailSettings.fadeDurationMs;

    // O(n) but only when needed (fade mode)
    this.blueTrailBuffer.filterInPlace((p) => p.timestamp > cutoffTime);
    this.redTrailBuffer.filterInPlace((p) => p.timestamp > cutoffTime);
    this.secondaryBlueTrailBuffer.filterInPlace((p) => p.timestamp > cutoffTime);
    this.secondaryRedTrailBuffer.filterInPlace((p) => p.timestamp > cutoffTime);
  }
}

/**
 * PixiJS Application Manager
 *
 * Handles PixiJS application lifecycle:
 * - Initialization and configuration
 * - Canvas management
 * - Resize operations
 * - Cleanup and disposal
 *
 * Single Responsibility: PixiJS app setup and lifecycle
 */

import { Application } from "pixi.js";

export class PixiApplicationManager {
  private app: Application | null = null;
  private currentSize: number = 500;
  private isInitialized: boolean = false;

  async initialize(
    container: HTMLElement,
    size: number,
    backgroundAlpha: number = 1
  ): Promise<Application> {
    if (this.isInitialized) {
      console.warn("[PixiApplicationManager] Already initialized");
      return this.app!;
    }

    this.currentSize = size;

    try {
      // Create PixiJS application with autoStart: false to prevent automatic render loop
      this.app = new Application();
      await this.app.init({
        width: size,
        height: size,
        backgroundColor: 0xffffff,
        backgroundAlpha, // Support transparent canvas for overlay rendering
        antialias: true,
        resolution: window.devicePixelRatio || 1,
        autoDensity: true,
        autoStart: false, // Prevent automatic ticker
      });

      // Wait a tick for canvas to be available
      await new Promise((resolve) => setTimeout(resolve, 0));

      // Append canvas to container
      const canvas = this.app.canvas;
      container.appendChild(canvas);

      this.isInitialized = true;
      console.log("[PixiApplicationManager] Initialized successfully");

      return this.app;
    } catch (error) {
      console.error("[PixiApplicationManager] Initialization failed:", error);
      // Clean up on failure
      if (this.app) {
        try {
          this.app.destroy();
        } catch (e) {
          // Ignore cleanup errors
        }
        this.app = null;
      }
      throw error;
    }
  }

  resize(newSize: number): void {
    if (!this.app) return;

    this.currentSize = newSize;
    this.app.renderer.resize(newSize, newSize);
  }

  render(): void {
    if (this.app?.renderer) {
      this.app.renderer.render(this.app.stage);
    }
  }

  getApplication(): Application | null {
    return this.app;
  }

  getCanvas(): HTMLCanvasElement | null {
    return this.app ? this.app.canvas : null;
  }

  getCurrentSize(): number {
    return this.currentSize;
  }

  isReady(): boolean {
    return this.isInitialized && this.app !== null;
  }

  destroy(): void {
    if (!this.app || !this.isInitialized) return;

    try {
      // CRITICAL: Remove canvas from DOM before destroying renderer
      const canvas = this.app.canvas;
      canvas.parentElement?.removeChild(canvas);

      // Destroy app
      this.app.renderer.destroy();

      this.app = null;
      this.isInitialized = false;
      console.log("[PixiApplicationManager] Destroyed");
    } catch (error) {
      console.error("[PixiApplicationManager] Error during destroy:", error);
      this.app = null;
      this.isInitialized = false;
    }
  }
}

/**
 * PixiJS Sprite Manager
 *
 * Handles sprite lifecycle and updates:
 * - Creating and caching sprites
 * - Updating sprite properties (position, rotation, scale, alpha)
 * - Managing sprite visibility
 * - Sprite cleanup
 *
 * Single Responsibility: Sprite management and updates
 */

import type { Container, Texture } from "pixi.js";
import { Sprite } from "pixi.js";

export class PixiSpriteManager {
  // Layer containers
  private gridContainer: Container;
  private propContainer: Container;
  private glyphContainer: Container;

  // Sprites
  private gridSprite: Sprite | null = null;
  private bluePropSprite: Sprite | null = null;
  private redPropSprite: Sprite | null = null;
  private secondaryBluePropSprite: Sprite | null = null;
  private secondaryRedPropSprite: Sprite | null = null;
  private glyphSprite: Sprite | null = null;
  private previousGlyphSprite: Sprite | null = null;

  constructor(
    gridContainer: Container,
    propContainer: Container,
    glyphContainer: Container
  ) {
    this.gridContainer = gridContainer;
    this.propContainer = propContainer;
    this.glyphContainer = glyphContainer;
  }

  updateGridSprite(texture: Texture, size: number): void {
    if (this.gridSprite) {
      this.gridSprite.texture = texture;
      this.gridSprite.width = size;
      this.gridSprite.height = size;
    } else {
      this.gridSprite = new Sprite(texture);
      this.gridSprite.width = size;
      this.gridSprite.height = size;
      this.gridContainer.addChild(this.gridSprite);
    }
  }

  setGridVisibility(visible: boolean): void {
    if (this.gridSprite) {
      this.gridSprite.visible = visible;
    }
  }

  updateGlyphSprite(
    texture: Texture,
    size: number,
    _previousTexture: Texture | null
  ): {
    current: Sprite;
    previous: Sprite | null;
  } {
    // Save previous glyph for fade transition
    if (this.glyphSprite) {
      this.previousGlyphSprite = this.glyphSprite;
    }

    // Create new glyph sprite
    this.glyphSprite = new Sprite(texture);
    this.glyphSprite.width = size;
    this.glyphSprite.height = size;
    this.glyphSprite.alpha = 0; // Start invisible for fade-in

    this.glyphContainer.addChild(this.glyphSprite);

    return {
      current: this.glyphSprite,
      previous: this.previousGlyphSprite,
    };
  }

  setGlyphAlpha(currentAlpha: number, previousAlpha?: number): void {
    if (this.glyphSprite) {
      this.glyphSprite.alpha = currentAlpha;
    }
    if (this.previousGlyphSprite && previousAlpha !== undefined) {
      this.previousGlyphSprite.alpha = previousAlpha;
    }
  }

  removePreviousGlyph(): void {
    if (this.previousGlyphSprite) {
      this.glyphContainer.removeChild(this.previousGlyphSprite);
      this.previousGlyphSprite.destroy();
      this.previousGlyphSprite = null;
    }
  }

  updatePropSprite(
    color: "blue" | "red" | "secondaryBlue" | "secondaryRed",
    texture: Texture,
    position: { x: number; y: number },
    size: { width: number; height: number },
    rotation: number
  ): void {
    // Get or create sprite
    let sprite: Sprite | null =
      color === "blue"
        ? this.bluePropSprite
        : color === "red"
          ? this.redPropSprite
          : color === "secondaryBlue"
            ? this.secondaryBluePropSprite
            : this.secondaryRedPropSprite;

    if (!sprite) {
      sprite = new Sprite(texture);
      if (color === "blue") {
        this.bluePropSprite = sprite;
      } else if (color === "red") {
        this.redPropSprite = sprite;
      } else if (color === "secondaryBlue") {
        this.secondaryBluePropSprite = sprite;
      } else {
        this.secondaryRedPropSprite = sprite;
      }
      this.propContainer.addChild(sprite);
    } else {
      sprite.texture = texture;
    }

    // Update sprite properties
    sprite.visible = true;
    sprite.width = size.width;
    sprite.height = size.height;
    sprite.anchor.set(0.5, 0.5); // Center anchor point
    sprite.position.set(position.x, position.y);
    sprite.rotation = rotation;
  }

  setPropVisibility(
    color: "blue" | "red" | "secondaryBlue" | "secondaryRed",
    visible: boolean
  ): void {
    const sprite =
      color === "blue"
        ? this.bluePropSprite
        : color === "red"
          ? this.redPropSprite
          : color === "secondaryBlue"
            ? this.secondaryBluePropSprite
            : this.secondaryRedPropSprite;

    if (sprite) {
      sprite.visible = visible;
    }
  }

  resizeAllSprites(newSize: number): void {
    if (this.gridSprite) {
      this.gridSprite.width = newSize;
      this.gridSprite.height = newSize;
    }

    if (this.glyphSprite) {
      this.glyphSprite.width = newSize;
      this.glyphSprite.height = newSize;
    }

    if (this.previousGlyphSprite) {
      this.previousGlyphSprite.width = newSize;
      this.previousGlyphSprite.height = newSize;
    }

    // Props will be resized when they're next rendered
  }

  getGlyphSprite(): Sprite | null {
    return this.glyphSprite;
  }

  getPreviousGlyphSprite(): Sprite | null {
    return this.previousGlyphSprite;
  }

  destroy(): void {
    try {
      this.bluePropSprite?.destroy();
      this.redPropSprite?.destroy();
      this.secondaryBluePropSprite?.destroy();
      this.secondaryRedPropSprite?.destroy();
      this.gridSprite?.destroy();
      this.glyphSprite?.destroy();
      this.previousGlyphSprite?.destroy();
    } catch (e) {
      // Ignore sprite destroy errors
    }

    this.bluePropSprite = null;
    this.redPropSprite = null;
    this.secondaryBluePropSprite = null;
    this.secondaryRedPropSprite = null;
    this.gridSprite = null;
    this.glyphSprite = null;
    this.previousGlyphSprite = null;
  }
}

/**
 * PixiJS Texture Loader
 *
 * Handles texture creation and caching:
 * - SVG to texture conversion
 * - Prop texture loading
 * - Grid texture loading
 * - Glyph texture loading
 * - Texture lifecycle management
 *
 * Single Responsibility: Texture loading and management
 */

import { Texture } from "pixi.js";

import type { ISVGGenerator, PropSvgData } from "$modules/animate/services/contracts/ISVGGenerator";

const VIEWBOX_SIZE = 950;

export class PixiTextureLoader {
  // Texture cache
  private bluePropTexture: Texture | null = null;
  private redPropTexture: Texture | null = null;
  private gridTexture: Texture | null = null;
  private glyphTexture: Texture | null = null;
  private previousGlyphTexture: Texture | null = null;

  async loadPropTextures(propType: string): Promise<{
    blue: Texture;
    red: Texture;
  }> {
    try {
      // Import SVGGenerator to generate prop SVGs
      const { TYPES } = await import("$shared/inversify/types");
      const { resolve } = await import("$shared");
      const svgGenerator = resolve<ISVGGenerator>(TYPES.ISVGGenerator);

      // Generate blue and red prop SVGs
      const [bluePropData, redPropData]: [PropSvgData, PropSvgData] = await Promise.all([
        svgGenerator.generateBluePropSvg(propType),
        svgGenerator.generateRedPropSvg(propType),
      ]);

      // Load textures from SVG strings
      this.bluePropTexture = await this.createTextureFromSVG(
        bluePropData.svg,
        bluePropData.width,
        bluePropData.height
      );
      this.redPropTexture = await this.createTextureFromSVG(
        redPropData.svg,
        redPropData.width,
        redPropData.height
      );

      console.log(`[PixiTextureLoader] Loaded prop textures for ${propType}`);

      return {
        blue: this.bluePropTexture,
        red: this.redPropTexture,
      };
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load prop textures:", error);
      throw error;
    }
  }

  async loadGridTexture(
    gridMode: string,
    canvasSize: number
  ): Promise<Texture> {
    try {
      const { TYPES } = await import("$shared/inversify/types");
      const { resolve, GridMode } = await import("$shared");
      const svgGenerator = resolve<ISVGGenerator>(TYPES.ISVGGenerator);

      // Convert gridMode string to GridMode enum
      const gridModeEnum =
        GridMode[gridMode.toUpperCase() as keyof typeof GridMode] ?? GridMode.DIAMOND;

      const gridSvg: string = svgGenerator.generateGridSvg(gridModeEnum);

      // Load texture at current canvas size for optimal quality
      this.gridTexture = await this.createTextureFromSVG(
        gridSvg,
        canvasSize,
        canvasSize
      );

      console.log(`[PixiTextureLoader] Loaded grid texture for ${gridMode}`);

      return this.gridTexture;
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load grid texture:", error);
      throw error;
    }
  }

  async loadGlyphTexture(
    svgString: string,
    _width: number,
    _height: number
  ): Promise<{
    current: Texture;
    previous: Texture | null;
  }> {
    try {
      // Save previous glyph for fade transition
      if (this.glyphTexture) {
        this.previousGlyphTexture = this.glyphTexture;
      }

      // Create texture from SVG (full 950x950 viewBox)
      this.glyphTexture = await this.createTextureFromSVG(
        svgString,
        VIEWBOX_SIZE,
        VIEWBOX_SIZE
      );

      console.log("[PixiTextureLoader] Loaded glyph texture");

      return {
        current: this.glyphTexture,
        previous: this.previousGlyphTexture,
      };
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load glyph texture:", error);
      throw error;
    }
  }

  clearPreviousGlyphTexture(): void {
    if (this.previousGlyphTexture) {
      this.previousGlyphTexture.destroy();
      this.previousGlyphTexture = null;
    }
  }

  private async createTextureFromSVG(
    svgString: string,
    width: number,
    height: number
  ): Promise<Texture> {
    // Convert SVG to data URL
    const base64 = btoa(unescape(encodeURIComponent(svgString)));
    const dataUrl = `data:image/svg+xml;base64,${base64}`;

    // Scale by devicePixelRatio for crisp rendering on high-DPI displays
    const scale =
      typeof window !== "undefined" ? window.devicePixelRatio || 1 : 1;
    const targetWidth = width * scale;
    const targetHeight = height * scale;

    return new Promise((resolve, reject) => {
      const img = new Image();

      img.onload = () => {
        try {
          // Render SVG to canvas at high resolution for crisp textures
          const canvas = document.createElement("canvas");
          canvas.width = targetWidth;
          canvas.height = targetHeight;

          const ctx = canvas.getContext("2d");
          if (!ctx) {
            reject(new Error("Failed to get 2D context"));
            return;
          }

          // Draw the SVG image to canvas at target size
          ctx.drawImage(img, 0, 0, targetWidth, targetHeight);

          // Create texture from canvas
          const texture = Texture.from(canvas);
          resolve(texture);
        } catch (error) {
          console.error("Texture creation error:", error);
          reject(error);
        }
      };

      img.onerror = (error) => {
        console.error("Image load error:", error);
        reject(new Error("Failed to load SVG image"));
      };

      img.src = dataUrl;
    });
  }

  getBluePropTexture(): Texture | null {
    return this.bluePropTexture;
  }

  getRedPropTexture(): Texture | null {
    return this.redPropTexture;
  }

  getGridTexture(): Texture | null {
    return this.gridTexture;
  }

  getGlyphTexture(): Texture | null {
    return this.glyphTexture;
  }

  destroy(): void {
    try {
      this.bluePropTexture?.destroy(true);
      this.redPropTexture?.destroy(true);
      this.gridTexture?.destroy(true);
      this.glyphTexture?.destroy(true);
      this.previousGlyphTexture?.destroy(true);
    } catch (e) {
      // Ignore texture destroy errors
    }

    this.bluePropTexture = null;
    this.redPropTexture = null;
    this.gridTexture = null;
    this.glyphTexture = null;
    this.previousGlyphTexture = null;
  }
}

/**
 * PixiJS Trail Renderer
 *
 * Handles trail rendering:
 * - Smooth trail rendering (Catmull-Rom splines)
 * - Segmented trail rendering
 * - Trail opacity calculations (fade/gradient)
 * - Multi-end trail support (both ends tracking)
 *
 * Single Responsibility: Trail rendering logic
 */

import type { Container } from "pixi.js";
import { Graphics } from "pixi.js";

import type {
  TrailPoint,
  TrailSettings,
} from "../../../domain/types/TrailTypes";
import {
  TrackingMode,
  TrailMode,
  TrailStyle,
} from "../../../domain/types/TrailTypes";
import {
  createSmoothCurve,
  type Point2D,
} from "../../../utils/CatmullRomSpline";

export class PixiTrailRenderer {
  private trailContainer: Container;
  private blueTrailGraphics: Graphics;
  private redTrailGraphics: Graphics;
  private hasLoggedTrailStatus = false;

  constructor(trailContainer: Container) {
    this.trailContainer = trailContainer;

    // Create trail graphics (persistent, cleared and redrawn each frame)
    this.blueTrailGraphics = new Graphics();
    this.redTrailGraphics = new Graphics();
    this.trailContainer.addChild(this.blueTrailGraphics);
    this.trailContainer.addChild(this.redTrailGraphics);
  }

  renderTrails(
    blueTrailPoints: TrailPoint[],
    redTrailPoints: TrailPoint[],
    trailSettings: TrailSettings,
    currentTime: number,
    hasBlue: boolean,
    hasRed: boolean
  ): void {
    // Debug log once
    if (!this.hasLoggedTrailStatus) {
      console.log(
        `üîç Trail Status: enabled=${trailSettings.enabled}, mode=${trailSettings.mode}`
      );
      console.log(
        `   Blue points: ${blueTrailPoints.length}, Red points: ${redTrailPoints.length}`
      );
      this.hasLoggedTrailStatus = true;
    }

    if (!trailSettings.enabled || trailSettings.mode === TrailMode.OFF) {
      this.blueTrailGraphics.clear();
      this.redTrailGraphics.clear();
      return;
    }

    // Render blue trail
    if (hasBlue) {
      this.blueTrailGraphics.clear();
      this.renderTrailSegments(
        this.blueTrailGraphics,
        blueTrailPoints,
        trailSettings.blueColor,
        trailSettings,
        currentTime
      );
    }

    // Render red trail
    if (hasRed) {
      this.redTrailGraphics.clear();
      this.renderTrailSegments(
        this.redTrailGraphics,
        redTrailPoints,
        trailSettings.redColor,
        trailSettings,
        currentTime
      );
    }
  }

  private renderTrailSegments(
    graphics: Graphics,
    points: TrailPoint[],
    colorString: string,
    settings: TrailSettings,
    currentTime: number
  ): void {
    if (points.length < 2) return;

    // Separate points by endType if tracking both ends
    const pointSets: TrailPoint[][] =
      settings.trackingMode === TrackingMode.BOTH_ENDS
        ? [
            points.filter((p) => p.endType === 0), // Left end
            points.filter((p) => p.endType === 1), // Right end
          ]
        : [points]; // Single end (left or right)

    // Convert color string to hex number (e.g., "#2E3192" -> 0x2E3192)
    const color = parseInt(colorString.replace("#", ""), 16);

    for (const pointSet of pointSets) {
      if (pointSet.length < 2) continue;

      // Use smooth curves if enabled, otherwise line segments
      if (settings.style === TrailStyle.SMOOTH_LINE) {
        this.renderSmoothTrail(
          graphics,
          pointSet,
          color,
          settings,
          currentTime
        );
      } else {
        this.renderSegmentedTrail(
          graphics,
          pointSet,
          color,
          settings,
          currentTime
        );
      }
    }
  }

  private renderSmoothTrail(
    graphics: Graphics,
    points: TrailPoint[],
    color: number,
    settings: TrailSettings,
    currentTime: number
  ): void {
    if (points.length < 2) return;

    // Convert trail points to Point2D for spline interpolation
    const controlPoints: Point2D[] = points.map((p) => ({ x: p.x, y: p.y }));

    // Adaptive subdivision based on point count
    const subdivisionsPerSegment = Math.max(
      2, // Minimum 2 subdivisions
      Math.min(
        10, // Maximum 10 subdivisions
        Math.floor(150 / points.length) // Scale inversely with point count
      )
    );

    const smoothPoints = createSmoothCurve(controlPoints, {
      alpha: 0.5, // Centripetal Catmull-Rom
      subdivisionsPerSegment,
    });

    if (smoothPoints.length < 2) return;

    // Calculate average opacity for the entire trail
    const avgOpacity = this.calculateOpacity(
      Math.floor(smoothPoints.length / 2),
      smoothPoints.length,
      points,
      settings,
      currentTime
    );

    // Draw as ONE continuous path (no gaps!)
    const firstPoint = smoothPoints[0]!;
    graphics.moveTo(firstPoint.x, firstPoint.y);

    for (let i = 1; i < smoothPoints.length; i++) {
      const point = smoothPoints[i]!;

      // Skip invalid points
      if (isNaN(point.x) || isNaN(point.y)) {
        continue;
      }

      graphics.lineTo(point.x, point.y);
    }

    // Stroke the entire path at once (smooth and gap-free!)
    graphics.stroke({
      width: settings.lineWidth,
      color: color,
      alpha: avgOpacity,
      cap: "round",
      join: "round",
    });
  }

  private renderSegmentedTrail(
    graphics: Graphics,
    points: TrailPoint[],
    color: number,
    settings: TrailSettings,
    currentTime: number
  ): void {
    if (points.length < 2) return;

    // Draw trail segments with varying opacity
    for (let i = 0; i < points.length - 1; i++) {
      const point = points[i]!;
      const nextPoint = points[i + 1]!;

      // Skip invalid points
      if (
        isNaN(point.x) ||
        isNaN(point.y) ||
        isNaN(nextPoint.x) ||
        isNaN(nextPoint.y)
      ) {
        continue;
      }

      let opacity: number;

      if (settings.mode === TrailMode.FADE) {
        const age = currentTime - point.timestamp;
        const progress = age / settings.fadeDurationMs;
        opacity =
          settings.maxOpacity -
          progress * (settings.maxOpacity - settings.minOpacity);
        opacity = Math.max(
          settings.minOpacity,
          Math.min(settings.maxOpacity, opacity)
        );
      } else {
        // LOOP_CLEAR and PERSISTENT - gradient from old to new
        const progress = i / (points.length - 1);
        opacity =
          settings.minOpacity +
          progress * (settings.maxOpacity - settings.minOpacity);
      }

      // Draw line segment
      graphics.moveTo(point.x, point.y);
      graphics.lineTo(nextPoint.x, nextPoint.y);
      graphics.stroke({
        width: settings.lineWidth,
        color: color,
        alpha: opacity,
        cap: "round",
        join: "round",
      });
    }
  }

  private calculateOpacity(
    smoothPointIndex: number,
    totalSmoothPoints: number,
    originalPoints: TrailPoint[],
    settings: TrailSettings,
    currentTime: number
  ): number {
    if (settings.mode === TrailMode.FADE) {
      // Map smooth point index back to original trail point for timestamp
      const originalPointIndex = Math.floor(
        (smoothPointIndex / totalSmoothPoints) * originalPoints.length
      );
      const originalPoint = originalPoints[originalPointIndex];

      if (originalPoint) {
        const age = currentTime - originalPoint.timestamp;
        const progress = age / settings.fadeDurationMs;
        const opacity =
          settings.maxOpacity -
          progress * (settings.maxOpacity - settings.minOpacity);
        return Math.max(
          settings.minOpacity,
          Math.min(settings.maxOpacity, opacity)
        );
      } else {
        return settings.maxOpacity;
      }
    } else {
      // LOOP_CLEAR and PERSISTENT - gradient from old to new
      const progress = smoothPointIndex / (totalSmoothPoints - 1);
      return (
        settings.minOpacity +
        progress * (settings.maxOpacity - settings.minOpacity)
      );
    }
  }

  destroy(): void {
    try {
      this.blueTrailGraphics.destroy();
      this.redTrailGraphics.destroy();
    } catch (e) {
      // Ignore graphics destroy errors
    }
  }
}

/**
 * Motion Analyzer Implementation
 *
 * Handles analysis of motion data for reversal counting based on rotation direction comparison.
 * Extracted from OptionPickerService for better separation of concerns.
 */

import { injectable } from "inversify";

import type { MotionData, MotionType, PictographData, RotationDirection } from "$shared";

import type { IReversalChecker } from "../contracts/IReversalChecker";

/**
 * Coordinate point in a motion path
 */
interface PathPoint {
  x: number;
  y: number;
}

/**
 * Type guard to check if a value is a valid MotionData
 */
function isMotionData(value: unknown): value is MotionData {
  if (!value || typeof value !== "object") {
    return false;
  }
  const motion = value as Partial<MotionData>;
  return (
    motion.motionType !== undefined &&
    motion.rotationDirection !== undefined
  );
}

/**
 * Type guard to check if a value is a PathPoint
 */
function isPathPoint(value: unknown): value is PathPoint {
  if (!value || typeof value !== "object") {
    return false;
  }
  const point = value as Partial<PathPoint>;
  return (
    typeof point.x === "number" &&
    typeof point.y === "number"
  );
}

@injectable()
export class ReversalChecker implements IReversalChecker {
  /**
   * Calculate the number of reversals found in a pictograph. We look at both the
   * intrinsic motion data (paths, motion types, turns) and, when available, the
   * surrounding sequence context to determine direction changes.
   */
  getReversalCount(
    option: PictographData,
    sequence: PictographData[] = []
  ): number {
    let maxReversals = 0;

    // Inspect each motion on the pictograph for intrinsic reversal cues
    Object.values(option.motions).forEach((motion) => {
      if (!motion) return;
      const reversals = this.analyzeMotionForReversals(motion);
      maxReversals = Math.max(maxReversals, reversals);
    });

    // Incorporate sequence-based comparison of rotationDirection metadata
    if (sequence.length > 0) {
      const sequenceReversals = this.analyzeSequenceContext(option, sequence);
      maxReversals = Math.max(maxReversals, sequenceReversals);
    }

    return Math.min(maxReversals, 2); // Keep within the available filter buckets
  }

  hasReversals(option: PictographData): boolean {
    return this.getReversalCount(option) > 0;
  }

  /**
   * Analyze a single motion for reversal patterns using the heuristics from the
   * previous monolithic service implementation.
   */
  private analyzeMotionForReversals(motion: MotionData): number {
    let reversalCount = 0;

    // Check motion type for reversal indicators
    const motionTypeStr = motion.motionType.toString().toLowerCase();

    if (motionTypeStr.includes("pro") && motionTypeStr.includes("anti")) {
      reversalCount = Math.max(reversalCount, 1);
    } else if (
      motionTypeStr.includes("bi") ||
      motionTypeStr.includes("switch")
    ) {
      reversalCount = Math.max(reversalCount, 2);
    }

    // Analyze path for reversals if it exists and is an array
    // Note: path is not part of the standard MotionData interface,
    // but may exist on extended motion objects
    const motionWithPath = motion as MotionData & { path?: unknown[] };
    if (Array.isArray(motionWithPath.path)) {
      reversalCount = Math.max(
        reversalCount,
        this.analyzePathForReversals(motionWithPath.path)
      );
    }

    // Check turns for reversals
    if (typeof motion.turns === "number" && motion.turns > 1) {
      reversalCount = Math.max(reversalCount, Math.floor(motion.turns / 2));
    }

    return reversalCount;
  }

  /**
   * Inspects a motion path for direction changes using a simplified
   * clockwise/counter-clockwise heuristic.
   */
  private analyzePathForReversals(path: unknown[]): number {
    if (path.length < 3) return 0;

    let reversals = 0;
    let lastDirection: "cw" | "ccw" | null = null;

    for (let i = 0; i < path.length - 1; i++) {
      const current = path[i];
      const next = path[i + 1];

      // Only process if both are valid PathPoints
      if (!isPathPoint(current) || !isPathPoint(next)) {
        continue;
      }

      const direction = this.determinePathDirection(current, next);

      if (lastDirection && direction && lastDirection !== direction) {
        reversals++;
      }

      if (direction) {
        lastDirection = direction;
      }
    }

    return reversals;
  }

  /**
   * Determine direction between two path points using a simple cross-product
   * style heuristic. Returns `cw`, `ccw`, or `null` if not enough movement.
   */
  private determinePathDirection(from: PathPoint, to: PathPoint): "cw" | "ccw" | null {
    const dx = to.x - from.x;
    const dy = to.y - from.y;
    const magnitude = Math.sqrt(dx * dx + dy * dy);

    if (magnitude < 0.01) {
      return null;
    }

    return dx > 0 ? "cw" : "ccw";
  }

  /**
   * Compare the current pictograph's rotation metadata against the prior
   * sequence to detect direction switches.
   */
  private analyzeSequenceContext(
    option: PictographData,
    sequence: PictographData[]
  ): number {
    let reversalCount = 0;

    (["blue", "red"] as const).forEach((color) => {
      const currentMotion = option.motions[color];
      const currentRotation = currentMotion?.rotationDirection;

      // Skip if no rotation or is NO_ROTATION enum value
      if (!currentRotation) {
        return;
      }

      // Import RotationDirection to properly compare enum values
      // Check string value to avoid unsafe enum comparison
      if (String(currentRotation) === "noRotation") {
        return;
      }

      for (let i = sequence.length - 1; i >= 0; i--) {
        const previousPictograph = sequence[i];
        if (!previousPictograph) continue;

        const previousMotion = previousPictograph.motions[color];
        const previousRotation = previousMotion?.rotationDirection;

        // Skip if no rotation or is NO_ROTATION enum value
        if (!previousRotation) {
          continue;
        }

        if (String(previousRotation) === "noRotation") {
          continue;
        }

        // Compare string representations to avoid enum comparison warning
        if (String(previousRotation) !== String(currentRotation)) {
          reversalCount++;
        }

        break;
      }
    });

    return reversalCount;
  }
}

/**
 * Simplified Start Position State
 *
 * Based on the working legacy implementation - simple and effective.
 * No over-engineering, just the core functionality needed.
 */

import {
  GridMode,
  type ISettingsService,
  type PictographData,
  resolve,
} from "$shared";
import { TYPES } from "$shared/inversify/types";

import type { IStartPositionService } from "../services/contracts";

export function createSimplifiedStartPositionState() {
  // Lazy service resolution to avoid effect_orphan error
  let startPositionService: IStartPositionService | null = null;
  let settingsService: ISettingsService | null = null;

  function getService(): IStartPositionService {
    if (!startPositionService) {
      startPositionService = resolve(TYPES.IStartPositionService)!;
    }
    return startPositionService;
  }

  function getSettingsService(): ISettingsService {
    if (!settingsService) {
      settingsService = resolve(TYPES.ISettingsService)!;
    }
    return settingsService;
  }

  // Load grid mode from settings (defaults to DIAMOND if not set)
  function getInitialGridMode(): GridMode {
    try {
      const settings = getSettingsService();
      return settings.currentSettings.gridMode;
    } catch (error) {
      console.warn(
        "Failed to load grid mode from settings, defaulting to DIAMOND",
        error
      );
      return GridMode.DIAMOND;
    }
  }

  // Simple reactive state - just what we need
  let positions = $state<PictographData[]>([]);
  let allVariations = $state<PictographData[]>([]);
  let selectedPosition = $state<PictographData | null>(null);
  let currentGridMode = $state<GridMode>(getInitialGridMode());
  const selectionListeners = new Set<
    (position: PictographData | null, source: "user" | "sync") => void
  >();

  function notifySelectionChange(
    position: PictographData | null,
    source: "user" | "sync" = "user"
  ) {
    selectionListeners.forEach((listener) => {
      try {
        listener(position, source);
      } catch (error) {
        console.error("? start-position-state: listener error", error);
      }
    });
  }

  // Load positions on initialization - always succeeds with hardcoded positions
  async function loadPositions(gridMode: GridMode = currentGridMode) {
    currentGridMode = gridMode;
    positions = getService().getStartPositions(gridMode);

    // Persist grid mode to settings when it changes
    try {
      await getSettingsService().updateSetting("gridMode", gridMode);
    } catch (error) {
      console.warn("Failed to persist grid mode to settings", error);
    }
  }

  // Load all 16 start position variations for the current grid mode
  async function loadAllVariations(gridMode: GridMode = currentGridMode) {
    currentGridMode = gridMode;
    allVariations = getService().getAllStartPositionVariations(gridMode);

    // Persist grid mode to settings when it changes
    try {
      await getSettingsService().updateSetting("gridMode", gridMode);
    } catch (error) {
      console.warn("Failed to persist grid mode to settings", error);
    }
  }

  // Select a position
  function selectPosition(position: PictographData) {
    getService().selectStartPosition(position);
    selectedPosition = position;
    notifySelectionChange(position, "user");
  }

  function setSelectedPosition(position: PictographData | null) {
    selectedPosition = position;
    notifySelectionChange(position, "sync");
  }

  function clearSelectedPosition() {
    setSelectedPosition(null);
  }

  function onSelectedPositionChange(
    listener: (position: PictographData | null, source: "user" | "sync") => void
  ) {
    selectionListeners.add(listener);
    return () => {
      selectionListeners.delete(listener);
    };
  }

  // Initialize on creation
  void loadPositions();

  return {
    // State
    get positions() {
      return positions;
    },
    get allVariations() {
      return allVariations;
    },
    get selectedPosition() {
      return selectedPosition;
    },
    get currentGridMode() {
      return currentGridMode;
    },

    // Actions
    selectPosition,
    setSelectedPosition,
    clearSelectedPosition,
    loadPositions,
    loadAllVariations,
    onSelectedPositionChange,
  };
}

export type SimplifiedStartPositionState = ReturnType<
  typeof createSimplifiedStartPositionState
>;

/**
 * Rotated Complementary CAP Executor
 *
 * Executes the rotated-complementary CAP (Circular Arrangement Pattern) by combining:
 * 1. ROTATED: Rotate locations based on handpath direction (90¬∞, 180¬∞, or 270¬∞)
 * 2. COMPLEMENTARY: Flip letters (A‚ÜîB), flip motion types (PRO‚ÜîANTI), flip prop rotation (CW‚ÜîCCW)
 *
 * This creates a sequence where:
 * - Letters are flipped (complementary effect)
 * - Motion types are flipped (PRO ‚Üî ANTI) (complementary effect)
 * - Prop rotation directions are flipped (CW ‚Üî CCW) (complementary effect)
 * - Locations are rotated based on the handpath direction
 * - **Colors are NOT swapped** (Blue stays Blue, Red stays Red)
 *
 * IMPORTANT: Supports both quartered and halved slice sizes
 * IMPORTANT: End position is calculated from rotated locations
 */

import { inject, injectable } from "inversify";

import type { BeatData } from "$create/shared/workspace-panel";
import type { Letter } from "$shared";
import type { IGridPositionDeriver } from "$shared";
import { MotionColor, type MotionData,MotionType } from "$shared";
import { TYPES } from "$shared/inversify/types";
import type {
  GridLocation,
  GridPosition,
} from "$shared/pictograph/grid/domain/enums/grid-enums";
import { RotationDirection } from "$shared/pictograph/shared/domain/enums/pictograph-enums";

import type { IOrientationCalculationService } from "../../../shared/services/contracts";
import type { IComplementaryLetterService } from "../../../shared/services/contracts";
import {
  getHandRotationDirection,
  getLocationMapForHandRotation,
  HALVED_CAPS,
  QUARTERED_CAPS,
} from "../../domain/constants/circular-position-maps";
import { SliceSize } from "../../domain/models/circular-models";

@injectable()
export class RotatedComplementaryCAPExecutor {
  constructor(
    @inject(TYPES.IOrientationCalculationService)
    private orientationCalculationService: IOrientationCalculationService,
    @inject(TYPES.IGridPositionDeriver)
    private gridPositionDeriver: IGridPositionDeriver,
    @inject(TYPES.IComplementaryLetterService)
    private complementaryLetterService: IComplementaryLetterService
  ) {}

  /**
   * Execute the rotated-complementary CAP
   *
   * @param sequence - The partial sequence to complete (must include start position at index 0)
   * @param sliceSize - Slice size for the CAP (quartered or halved)
   * @returns The complete circular sequence with all beats
   */
  executeCAP(sequence: BeatData[], sliceSize: SliceSize): BeatData[] {
    // Validate the sequence
    this._validateSequence(sequence, sliceSize);

    // Remove start position (index 0) for processing
    const startPosition = sequence.shift();
    if (!startPosition) {
      throw new Error("Sequence must have a start position");
    }

    // Calculate how many beats to generate based on slice size
    const sequenceLength = sequence.length;
    let entriesToAdd: number;

    if (sliceSize === SliceSize.QUARTERED) {
      // Quartered adds 3x the original length
      entriesToAdd = sequenceLength * 3;
    } else {
      // Halved adds 1x the original length (doubles total)
      entriesToAdd = sequenceLength;
    }

    // Generate the new beats
    const generatedBeats: BeatData[] = [];
    let lastBeat = sequence[sequence.length - 1]!;
    let nextBeatNumber = lastBeat.beatNumber + 1;

    // Generate CAP beats
    const finalIntendedLength = sequenceLength + entriesToAdd;
    for (let i = 0; i < entriesToAdd; i++) {
      const nextBeat = this._createNewCAPEntry(
        sequence,
        lastBeat,
        nextBeatNumber,
        finalIntendedLength,
        sliceSize
      );

      generatedBeats.push(nextBeat);
      sequence.push(nextBeat);
      lastBeat = nextBeat;
      nextBeatNumber++;
    }

    // Re-insert start position at the beginning
    sequence.unshift(startPosition);

    return sequence;
  }

  /**
   * Validate that the sequence can perform a rotated-complementary CAP
   */
  private _validateSequence(sequence: BeatData[], sliceSize: SliceSize): void {
    if (sequence.length < 2) {
      throw new Error(
        "Sequence must have at least 2 beats (start position + 1 beat)"
      );
    }

    const startPos = sequence[0]!.startPosition;
    const endPos = sequence[sequence.length - 1]!.endPosition;

    if (!startPos || !endPos) {
      throw new Error("Sequence beats must have valid start and end positions");
    }

    // Check if the (start, end) pair is valid for the requested slice size
    const key = `${startPos},${endPos}`;
    const validationSet =
      sliceSize === SliceSize.QUARTERED ? QUARTERED_CAPS : HALVED_CAPS;

    if (!validationSet.has(key)) {
      throw new Error(
        `Invalid position pair for rotated-complementary ${sliceSize} CAP: ${startPos} ‚Üí ${endPos}. ` +
          `The end position must match the ${sliceSize} rotation requirement.`
      );
    }
  }

  /**
   * Create a new CAP entry by transforming a previous beat with ROTATION + COMPLEMENTARY
   */
  private _createNewCAPEntry(
    sequence: BeatData[],
    previousBeat: BeatData,
    beatNumber: number,
    finalIntendedLength: number,
    sliceSize: SliceSize
  ): BeatData {
    // Get the corresponding beat from the first section using index mapping
    const previousMatchingBeat = this._getPreviousMatchingBeat(
      sequence,
      beatNumber,
      finalIntendedLength,
      sliceSize
    );

    // Get the complementary letter (COMPLEMENTARY effect)
    if (!previousMatchingBeat.letter) {
      throw new Error("Previous matching beat must have a letter");
    }
    const complementaryLetter =
      this.complementaryLetterService.getComplementaryLetter(
        previousMatchingBeat.letter as string
      ) as Letter;

    // Calculate the rotated end position
    const rotatedEndPosition = this._getRotatedEndPosition(
      previousBeat,
      previousMatchingBeat
    );

    // Create the new beat with rotated and complementary attributes
    // KEY: No color swapping - Blue stays Blue, Red stays Red
    //      Motion types are flipped (PRO ‚Üî ANTI)
    //      Prop rotations are flipped (CW ‚Üî CCW)
    //      Locations are rotated based on handpath direction
    const newBeat: BeatData = {
      ...previousMatchingBeat,
      id: `beat-${beatNumber}`,
      beatNumber,
      letter: complementaryLetter, // COMPLEMENTARY: Flip letter
      startPosition: previousBeat.endPosition ?? null,
      endPosition: rotatedEndPosition,
      motions: {
        [MotionColor.BLUE]: this._createRotatedComplementaryMotion(
          MotionColor.BLUE,
          previousBeat,
          previousMatchingBeat
        ),
        [MotionColor.RED]: this._createRotatedComplementaryMotion(
          MotionColor.RED,
          previousBeat,
          previousMatchingBeat
        ),
      },
    };

    // Update orientations
    const beatWithStartOri =
      this.orientationCalculationService.updateStartOrientations(
        newBeat,
        previousBeat
      );
    const finalBeat =
      this.orientationCalculationService.updateEndOrientations(
        beatWithStartOri
      );

    return finalBeat;
  }

  /**
   * Get the previous matching beat using index mapping
   */
  private _getPreviousMatchingBeat(
    sequence: BeatData[],
    beatNumber: number,
    finalLength: number,
    sliceSize: SliceSize
  ): BeatData {
    const indexMap = this._getIndexMap(finalLength, sliceSize);
    const matchingBeatNumber = indexMap[beatNumber];

    if (matchingBeatNumber === undefined) {
      throw new Error(`No index mapping found for beatNumber ${beatNumber}`);
    }

    // Convert 1-based beatNumber to 0-based array index
    const arrayIndex = matchingBeatNumber - 1;

    if (arrayIndex < 0 || arrayIndex >= sequence.length) {
      throw new Error(
        `Invalid index mapping: beatNumber ${beatNumber} ‚Üí matchingBeatNumber ${matchingBeatNumber} ‚Üí arrayIndex ${arrayIndex} (sequence length: ${sequence.length})`
      );
    }

    return sequence[arrayIndex]!;
  }

  /**
   * Generate index mapping for retrieving corresponding beats
   * Works for both quartered and halved patterns
   */
  private _getIndexMap(
    length: number,
    sliceSize: SliceSize
  ): Record<number, number> {
    const map: Record<number, number> = {};

    // Edge case handling
    if (sliceSize === SliceSize.QUARTERED && length < 4) {
      for (let i = 1; i <= length; i++) {
        map[i] = Math.max(i - 1, 1);
      }
      return map;
    }

    if (sliceSize === SliceSize.HALVED && length < 2) {
      for (let i = 1; i <= length; i++) {
        map[i] = Math.max(i - 1, 1);
      }
      return map;
    }

    if (sliceSize === SliceSize.QUARTERED) {
      // Quartered: length = base * 4, so base = length / 4
      const baseLength = Math.floor(length / 4);
      for (let i = baseLength + 1; i <= length; i++) {
        map[i] = i - baseLength;
      }
    } else {
      // Halved: length = base * 2, so base = length / 2
      const baseLength = Math.floor(length / 2);
      for (let i = baseLength + 1; i <= length; i++) {
        map[i] = i - baseLength;
      }
    }

    return map;
  }

  /**
   * Get the rotated end position by rotating both colors' locations
   */
  private _getRotatedEndPosition(
    previousBeat: BeatData,
    previousMatchingBeat: BeatData
  ): GridPosition | null {
    // Get hand rotation directions from the matching beat (same color)
    const blueHandRotDir = getHandRotationDirection(
      previousMatchingBeat.motions[MotionColor.BLUE]!
        .startLocation,
      previousMatchingBeat.motions[MotionColor.BLUE]!
        .endLocation
    );
    const redHandRotDir = getHandRotationDirection(
      previousMatchingBeat.motions[MotionColor.RED]!
        .startLocation,
      previousMatchingBeat.motions[MotionColor.RED]!.endLocation
    );

    // Get the location maps for rotation
    const blueLocationMap = getLocationMapForHandRotation(blueHandRotDir);
    const redLocationMap = getLocationMapForHandRotation(redHandRotDir);

    // Rotate the locations from the previous beat
    const newBlueEndLoc =
      blueLocationMap[
        previousBeat.motions[MotionColor.BLUE]!.endLocation
      ];
    const newRedEndLoc =
      redLocationMap[
        previousBeat.motions[MotionColor.RED]!.endLocation
      ];

    // Derive position from both locations
    const newEndPosition =
      this.gridPositionDeriver.getGridPositionFromLocations(
        newBlueEndLoc,
        newRedEndLoc
      );

    return newEndPosition;
  }

  /**
   * Create rotated-complementary motion data for the new beat
   * Combines location rotation with motion type and prop rotation flipping
   */
  private _createRotatedComplementaryMotion(
    color: MotionColor,
    previousBeat: BeatData,
    previousMatchingBeat: BeatData
  ): MotionData {
    const previousMotion = previousBeat.motions[color];
    const matchingMotion = previousMatchingBeat.motions[color]; // Same color (no swap)

    if (!previousMotion || !matchingMotion) {
      throw new Error(`Missing motion data for ${color}`);
    }

    // Get hand rotation direction from the matching motion
    const handRotDir = getHandRotationDirection(
      matchingMotion.startLocation,
      matchingMotion.endLocation
    );

    // Get location map for this rotation direction
    const locationMap = getLocationMapForHandRotation(handRotDir);

    // Rotate the end location (ROTATED effect)
    const rotatedEndLocation =
      locationMap[previousMotion.endLocation];

    // Flip the motion type (COMPLEMENTARY effect)
    const complementaryMotionType = this._getComplementaryMotionType(
      matchingMotion.motionType
    );

    // Flip the prop rotation direction (COMPLEMENTARY effect)
    const complementaryPropRotDir = this._getComplementaryPropRotDir(
      matchingMotion.rotationDirection
    );

    // Create rotated-complementary motion
    const rotatedComplementaryMotion = {
      ...matchingMotion,
      color, // Preserve the color (no swap)
      motionType: complementaryMotionType, // COMPLEMENTARY: Flip motion type
      startLocation: previousMotion.endLocation,
      endLocation: rotatedEndLocation, // ROTATED: Rotate location
      rotationDirection: complementaryPropRotDir, // COMPLEMENTARY: Flip prop rotation
      // Start orientation will be set by orientationCalculationService
      // End orientation will be calculated by orientationCalculationService
    };

    return rotatedComplementaryMotion;
  }

  /**
   * Get the complementary motion type (flip PRO ‚Üî ANTI)
   * STATIC and DASH stay the same
   */
  private _getComplementaryMotionType(motionType: MotionType): MotionType {
    if (motionType === MotionType.PRO) {
      return MotionType.ANTI;
    } else if (motionType === MotionType.ANTI) {
      return MotionType.PRO;
    }

    // STATIC and DASH stay the same
    return motionType;
  }

  /**
   * Get the complementary prop rotation direction (flip CW ‚Üî CCW)
   * NO_ROTATION stays NO_ROTATION
   */
  private _getComplementaryPropRotDir(
    propRotDir: RotationDirection
  ): RotationDirection {
    if (propRotDir === RotationDirection.CLOCKWISE) {
      return RotationDirection.COUNTER_CLOCKWISE;
    } else if (propRotDir === RotationDirection.COUNTER_CLOCKWISE) {
      return RotationDirection.CLOCKWISE;
    }

    // NO_ROTATION stays NO_ROTATION
    return propRotDir;
  }
}

/**
 * Rotated Swapped CAP Executor
 *
 * Executes the rotated-swapped CAP (Circular Arrangement Pattern) by combining:
 * 1. SWAPPED: Blue does what Red did, Red does what Blue did
 * 2. ROTATED: Rotate locations based on handpath direction (90¬∞, 180¬∞, or 270¬∞)
 *
 * This creates a sequence where:
 * - Colors are swapped (Blue performs Red's actions and vice versa)
 * - Locations are rotated based on the handpath direction of each color's motion
 * - Motion types stay the same (from opposite color due to swap)
 * - Prop rotation directions stay the same (from opposite color due to swap)
 * - Letters stay the same
 *
 * IMPORTANT: Supports both quartered and halved slice sizes
 * IMPORTANT: End position is calculated from rotated locations
 */

import { inject, injectable } from "inversify";

import type { BeatData } from "$create/shared/workspace-panel";
import { type IGridPositionDeriver, MotionColor, type MotionData } from "$shared";
import { TYPES } from "$shared/inversify/types";
import type {
  GridLocation,
  GridPosition,
} from "$shared/pictograph/grid/domain/enums/grid-enums";

import type { IOrientationCalculationService } from "../../../shared/services/contracts";
import {
  getHandRotationDirection,
  getLocationMapForHandRotation,
  HALVED_CAPS,
  QUARTERED_CAPS,
} from "../../domain/constants/circular-position-maps";
import { SliceSize } from "../../domain/models/circular-models";

@injectable()
export class RotatedSwappedCAPExecutor {
  constructor(
    @inject(TYPES.IOrientationCalculationService)
    private orientationCalculationService: IOrientationCalculationService,
    @inject(TYPES.IGridPositionDeriver)
    private gridPositionDeriver: IGridPositionDeriver
  ) {}

  /**
   * Execute the rotated-swapped CAP
   *
   * @param sequence - The partial sequence to complete (must include start position at index 0)
   * @param sliceSize - Slice size for the CAP (quartered or halved)
   * @returns The complete circular sequence with all beats
   */
  executeCAP(sequence: BeatData[], sliceSize: SliceSize): BeatData[] {
    // Validate the sequence
    this._validateSequence(sequence, sliceSize);

    // Remove start position (index 0) for processing
    const startPosition = sequence.shift();
    if (!startPosition) {
      throw new Error("Sequence must have a start position");
    }

    // Calculate how many beats to generate based on slice size
    const sequenceLength = sequence.length;
    let entriesToAdd: number;

    if (sliceSize === SliceSize.QUARTERED) {
      // Quartered adds 3x the original length
      entriesToAdd = sequenceLength * 3;
    } else {
      // Halved adds 1x the original length (doubles total)
      entriesToAdd = sequenceLength;
    }

    // Generate the new beats
    const generatedBeats: BeatData[] = [];
    let lastBeat = sequence[sequence.length - 1]!;
    let nextBeatNumber = lastBeat.beatNumber + 1;

    // Skip first two beats in the loop (start from beat 2)
    const finalIntendedLength = sequenceLength + entriesToAdd;
    for (let i = 0; i < entriesToAdd; i++) {
      const nextBeat = this._createNewCAPEntry(
        sequence,
        lastBeat,
        nextBeatNumber,
        finalIntendedLength
      );

      generatedBeats.push(nextBeat);
      sequence.push(nextBeat);
      lastBeat = nextBeat;
      nextBeatNumber++;
    }

    // Re-insert start position at the beginning
    sequence.unshift(startPosition);

    return sequence;
  }

  /**
   * Validate that the sequence can perform a rotated-swapped CAP
   */
  private _validateSequence(sequence: BeatData[], sliceSize: SliceSize): void {
    if (sequence.length < 2) {
      throw new Error(
        "Sequence must have at least 2 beats (start position + 1 beat)"
      );
    }

    const startPos = sequence[0]!.startPosition;
    const endPos = sequence[sequence.length - 1]!.endPosition;

    if (!startPos || !endPos) {
      throw new Error("Sequence beats must have valid start and end positions");
    }

    // Check if the (start, end) pair is valid for the requested slice size
    const key = `${startPos},${endPos}`;
    const validationSet =
      sliceSize === SliceSize.QUARTERED ? QUARTERED_CAPS : HALVED_CAPS;

    if (!validationSet.has(key)) {
      throw new Error(
        `Invalid position pair for rotated-swapped ${sliceSize} CAP: ${startPos} ‚Üí ${endPos}. ` +
          `The end position must match the ${sliceSize} rotation requirement.`
      );
    }
  }

  /**
   * Create a new CAP entry by transforming a previous beat with SWAP + ROTATION
   */
  private _createNewCAPEntry(
    sequence: BeatData[],
    previousBeat: BeatData,
    beatNumber: number,
    finalIntendedLength: number
  ): BeatData {
    // Get the corresponding beat from the first section using index mapping
    const previousMatchingBeat = this._getPreviousMatchingBeat(
      sequence,
      beatNumber,
      finalIntendedLength
    );

    // Calculate the rotated end position
    const rotatedEndPosition = this._getRotatedEndPosition(
      previousBeat,
      previousMatchingBeat
    );

    // Create the new beat with swapped and rotated attributes
    // KEY: Blue gets attributes from Red's matching beat (SWAP)
    //      Red gets attributes from Blue's matching beat (SWAP)
    //      Then locations are rotated based on handpath direction
    const newBeat: BeatData = {
      ...previousMatchingBeat,
      id: `beat-${beatNumber}`,
      beatNumber,
      letter: previousMatchingBeat.letter ?? null, // Same letter
      startPosition: previousBeat.endPosition ?? null,
      endPosition: rotatedEndPosition,
      motions: {
        // SWAP: Blue does what Red did, but with rotated transformation
        [MotionColor.BLUE]: this._createRotatedSwappedMotion(
          MotionColor.BLUE,
          previousBeat,
          previousMatchingBeat,
          true // isSwapped = true (use opposite color's data)
        ),
        // SWAP: Red does what Blue did, but with rotated transformation
        [MotionColor.RED]: this._createRotatedSwappedMotion(
          MotionColor.RED,
          previousBeat,
          previousMatchingBeat,
          true // isSwapped = true (use opposite color's data)
        ),
      },
    };

    // Update orientations
    const beatWithStartOri =
      this.orientationCalculationService.updateStartOrientations(
        newBeat,
        previousBeat
      );
    const finalBeat =
      this.orientationCalculationService.updateEndOrientations(
        beatWithStartOri
      );

    return finalBeat;
  }

  /**
   * Get the previous matching beat using index mapping
   */
  private _getPreviousMatchingBeat(
    sequence: BeatData[],
    beatNumber: number,
    finalLength: number
  ): BeatData {
    const indexMap = this._getIndexMap(finalLength);
    const matchingBeatNumber = indexMap[beatNumber];

    if (matchingBeatNumber === undefined) {
      throw new Error(`No index mapping found for beatNumber ${beatNumber}`);
    }

    // Convert 1-based beatNumber to 0-based array index
    const arrayIndex = matchingBeatNumber - 1;

    if (arrayIndex < 0 || arrayIndex >= sequence.length) {
      throw new Error(
        `Invalid index mapping: beatNumber ${beatNumber} ‚Üí matchingBeatNumber ${matchingBeatNumber} ‚Üí arrayIndex ${arrayIndex} (sequence length: ${sequence.length})`
      );
    }

    return sequence[arrayIndex]!;
  }

  /**
   * Generate index mapping for retrieving corresponding beats
   * Works for both quartered and halved patterns
   */
  private _getIndexMap(length: number): Record<number, number> {
    const map: Record<number, number> = {};

    if (length < 2) {
      // Edge case: very short sequences
      for (let i = 1; i <= length; i++) {
        map[i] = Math.max(i - 1, 1);
      }
      return map;
    }

    // For both quartered and halved, we calculate the base length
    // Quartered: length = base * 4, so base = length / 4
    // Halved: length = base * 2, so base = length / 2
    // The mapping is: beats beyond the base length map back to beats within the base
    const baseLength = Math.floor(length / 2); // Works for both cases due to how they're structured

    for (let i = baseLength + 1; i <= length; i++) {
      map[i] = i - baseLength;
    }

    return map;
  }

  /**
   * Get the rotated end position by rotating both colors' locations
   */
  private _getRotatedEndPosition(
    previousBeat: BeatData,
    previousMatchingBeat: BeatData
  ): GridPosition | null {
    // Get hand rotation directions from the matching beat (before swap)
    // Blue will use Red's handpath (due to swap)
    // Red will use Blue's handpath (due to swap)
    const blueHandRotDir = getHandRotationDirection(
      previousMatchingBeat.motions[MotionColor.RED]!
        .startLocation,
      previousMatchingBeat.motions[MotionColor.RED]!.endLocation
    );
    const redHandRotDir = getHandRotationDirection(
      previousMatchingBeat.motions[MotionColor.BLUE]!
        .startLocation,
      previousMatchingBeat.motions[MotionColor.BLUE]!
        .endLocation
    );

    // Get the location maps for rotation
    const blueLocationMap = getLocationMapForHandRotation(blueHandRotDir);
    const redLocationMap = getLocationMapForHandRotation(redHandRotDir);

    // Rotate the locations from the previous beat
    const newBlueEndLoc =
      blueLocationMap[
        previousBeat.motions[MotionColor.BLUE]!.endLocation
      ];
    const newRedEndLoc =
      redLocationMap[
        previousBeat.motions[MotionColor.RED]!.endLocation
      ];

    // Derive position from both locations
    const newEndPosition =
      this.gridPositionDeriver.getGridPositionFromLocations(
        newBlueEndLoc,
        newRedEndLoc
      );

    return newEndPosition;
  }

  /**
   * Create rotated-swapped motion data for the new beat
   * Combines color swapping with location rotation
   */
  private _createRotatedSwappedMotion(
    color: MotionColor,
    previousBeat: BeatData,
    previousMatchingBeat: BeatData,
    isSwapped: boolean
  ): MotionData {
    const previousMotion = previousBeat.motions[color];

    // SWAP: Get the opposite color's motion data
    const oppositeColor =
      color === MotionColor.BLUE ? MotionColor.RED : MotionColor.BLUE;
    const matchingMotion = isSwapped
      ? previousMatchingBeat.motions[oppositeColor]
      : previousMatchingBeat.motions[color];

    if (!previousMotion || !matchingMotion) {
      throw new Error(`Missing motion data for ${color}`);
    }

    // Get hand rotation direction from the matching motion
    const handRotDir = getHandRotationDirection(
      matchingMotion.startLocation,
      matchingMotion.endLocation
    );

    // Get location map for this rotation direction
    const locationMap = getLocationMapForHandRotation(handRotDir);

    // Rotate the end location
    const rotatedEndLocation =
      locationMap[previousMotion.endLocation];

    // Create rotated-swapped motion
    const rotatedSwappedMotion = {
      ...matchingMotion,
      color, // IMPORTANT: Preserve the color (Blue stays Blue, Red stays Red)
      motionType: matchingMotion.motionType, // Same motion type (from opposite color due to swap)
      startLocation: previousMotion.endLocation,
      endLocation: rotatedEndLocation,
      rotationDirection: matchingMotion.rotationDirection, // Same rotation direction (from opposite color due to swap)
      // Start orientation will be set by orientationCalculationService
      // End orientation will be calculated by orientationCalculationService
    };

    return rotatedSwappedMotion;
  }
}

/**
 * Strict Rotated CAP Executor
 *
 * Executes the strict rotated CAP (Circular Arrangement Pattern) by:
 * 1. Taking a partial sequence (first half or quarter)
 * 2. Applying rotational transformations to each beat
 * 3. Generating the remaining beats to complete the circular pattern
 *
 * The rotation works by:
 * - Taking each pictograph from the first section
 * - Rotating its hand locations based on the hand's rotation direction
 * - Maintaining the same motion types, turns, and letter patterns
 * - Creating new beats that fit the rotated positions
 */

import { inject, injectable } from "inversify";

import type { BeatData } from "$create/shared/workspace-panel";
import type { GridLocation } from "$shared";
import type { IGridPositionDeriver } from "$shared";
import { MotionColor, type MotionData } from "$shared";
import { TYPES } from "$shared/inversify/types";
import type { GridPosition } from "$shared/pictograph/grid/domain/enums/grid-enums";

import type { IOrientationCalculationService } from "../../../shared/services/contracts";
import {
  getHandRotationDirection,
  getLocationMapForHandRotation,
  HALVED_CAPS,
  QUARTERED_CAPS,
} from "../../domain/constants/circular-position-maps";
import { SliceSize } from "../../domain/models/circular-models";

@injectable()
export class StrictRotatedCAPExecutor {
  constructor(
    @inject(TYPES.IOrientationCalculationService)
    private orientationCalculationService: IOrientationCalculationService,
    @inject(TYPES.IGridPositionDeriver)
    private gridPositionDeriver: IGridPositionDeriver
  ) {}

  /**
   * Execute the strict rotated CAP
   *
   * @param sequence - The partial sequence to complete (must include start position at index 0)
   * @param sliceSize - Whether to use halved (180¬∞) or quartered (90¬∞) rotation
   * @returns The complete circular sequence with all beats
   */
  executeCAP(sequence: BeatData[], sliceSize: SliceSize): BeatData[] {
    // Validate the sequence
    this._validateSequence(sequence, sliceSize);

    // Remove start position (index 0) for processing
    const startPosition = sequence.shift();
    if (!startPosition) {
      throw new Error("Sequence must have a start position");
    }

    // Calculate how many beats to generate
    const sequenceLength = sequence.length;
    const entriesToAdd = this._calculateEntriesToAdd(sequenceLength, sliceSize);

    // Generate the new beats
    const generatedBeats: BeatData[] = [];
    let lastBeat = sequence[sequence.length - 1]!;
    let nextBeatNumber = lastBeat.beatNumber + 1;

    for (let i = 0; i < entriesToAdd; i++) {
      const finalIntendedLength = sequenceLength + entriesToAdd;
      const nextBeat = this._createNewCAPEntry(
        sequence,
        lastBeat,
        nextBeatNumber,
        finalIntendedLength,
        sliceSize
      );

      generatedBeats.push(nextBeat);
      sequence.push(nextBeat);
      lastBeat = nextBeat;
      nextBeatNumber++;
    }

    // Re-insert start position at the beginning
    sequence.unshift(startPosition);

    return sequence;
  }

  /**
   * Validate that the sequence can perform the requested CAP
   */
  private _validateSequence(sequence: BeatData[], sliceSize: SliceSize): void {
    if (sequence.length < 2) {
      throw new Error(
        "Sequence must have at least 2 beats (start position + 1 beat)"
      );
    }

    const startPos = sequence[0]!.startPosition;
    const endPos = sequence[sequence.length - 1]!.endPosition;

    if (!startPos || !endPos) {
      throw new Error("Sequence beats must have valid start and end positions");
    }

    // Check if the (start, end) pair is valid for the slice size
    const key = `${startPos},${endPos}`;
    const validationSet =
      sliceSize === SliceSize.HALVED ? HALVED_CAPS : QUARTERED_CAPS;

    if (!validationSet.has(key)) {
      throw new Error(
        `Invalid position pair for ${sliceSize} CAP: ${startPos} ‚Üí ${endPos}. ` +
          `This pair cannot complete a ${sliceSize} rotation.`
      );
    }
  }

  /**
   * Calculate how many beats need to be added based on slice size
   */
  private _calculateEntriesToAdd(
    sequenceLength: number,
    sliceSize: SliceSize
  ): number {
    if (sliceSize === SliceSize.HALVED) {
      return sequenceLength; // Double the sequence
    }
    // SliceSize.QUARTERED
    return sequenceLength * 3; // Quadruple the sequence
  }

  /**
   * Create a new CAP entry by transforming a previous beat
   */
  private _createNewCAPEntry(
    sequence: BeatData[],
    previousBeat: BeatData,
    beatNumber: number,
    finalIntendedLength: number,
    sliceSize: SliceSize
  ): BeatData {
    // Get the corresponding beat from the first section using index mapping
    const previousMatchingBeat = this._getPreviousMatchingBeat(
      sequence,
      beatNumber,
      finalIntendedLength,
      sliceSize
    );

    // Calculate new end position
    const newEndPosition = this._calculateNewEndPosition(
      previousMatchingBeat,
      previousBeat
    );

    // Create the new beat with transformed attributes
    const newBeat: BeatData = {
      ...previousMatchingBeat,
      id: `beat-${beatNumber}`,
      beatNumber,
      startPosition: previousBeat.endPosition ?? null,
      endPosition: newEndPosition,
      motions: {
        [MotionColor.BLUE]: this._createTransformedMotion(
          MotionColor.BLUE,
          previousBeat,
          previousMatchingBeat
        ),
        [MotionColor.RED]: this._createTransformedMotion(
          MotionColor.RED,
          previousBeat,
          previousMatchingBeat
        ),
      },
    };

    // Update orientations
    const beatWithStartOri =
      this.orientationCalculationService.updateStartOrientations(
        newBeat,
        previousBeat
      );
    const finalBeat =
      this.orientationCalculationService.updateEndOrientations(
        beatWithStartOri
      );

    return finalBeat;
  }

  /**
   * Get the previous matching beat using index mapping
   */
  private _getPreviousMatchingBeat(
    sequence: BeatData[],
    beatNumber: number,
    finalLength: number,
    sliceSize: SliceSize
  ): BeatData {
    const indexMap = this._getIndexMap(sliceSize, finalLength);
    const matchingBeatNumber = indexMap[beatNumber];

    if (matchingBeatNumber === undefined) {
      throw new Error(`No index mapping found for beatNumber ${beatNumber}`);
    }

    // Convert 1-based beatNumber to 0-based array index
    const arrayIndex = matchingBeatNumber - 1;

    if (arrayIndex < 0 || arrayIndex >= sequence.length) {
      throw new Error(
        `Invalid index mapping: beatNumber ${beatNumber} ‚Üí matchingBeatNumber ${matchingBeatNumber} ‚Üí arrayIndex ${arrayIndex} (sequence length: ${sequence.length})`
      );
    }

    return sequence[arrayIndex]!;
  }

  /**
   * Generate index mapping for retrieving corresponding beats
   */
  private _getIndexMap(
    sliceSize: SliceSize,
    length: number
  ): Record<number, number> {
    // Handle edge cases for very short sequences
    if (length < 4 && sliceSize === SliceSize.QUARTERED) {
      const map: Record<number, number> = {};
      for (let i = 1; i <= length; i++) {
        map[i] = Math.max(i - 1, 1);
      }
      return map;
    }

    if (length < 2 && sliceSize === SliceSize.HALVED) {
      const map: Record<number, number> = {};
      for (let i = 1; i <= length; i++) {
        map[i] = Math.max(i - 1, 1);
      }
      return map;
    }

    // Normal index mapping
    const map: Record<number, number> = {};

    if (sliceSize === SliceSize.QUARTERED) {
      const quarterLength = Math.floor(length / 4);
      for (let i = quarterLength + 1; i <= length; i++) {
        map[i] = i - quarterLength;
      }
    } else {
      // SliceSize.HALVED
      const halfLength = Math.floor(length / 2);
      for (let i = halfLength + 1; i <= length; i++) {
        map[i] = i - halfLength;
      }
    }

    return map;
  }

  /**
   * Calculate the new end position by rotating locations
   */
  private _calculateNewEndPosition(
    previousMatchingBeat: BeatData,
    previousBeat: BeatData
  ): GridPosition | null {
    const blueMotion = previousMatchingBeat.motions[MotionColor.BLUE];
    const redMotion = previousMatchingBeat.motions[MotionColor.RED];

    if (!blueMotion || !redMotion) {
      throw new Error(
        "Previous matching beat must have both blue and red motions"
      );
    }

    // Get hand rotation directions
    const blueHandRotDir = getHandRotationDirection(
      blueMotion.startLocation,
      blueMotion.endLocation
    );
    const redHandRotDir = getHandRotationDirection(
      redMotion.startLocation,
      redMotion.endLocation
    );

    // Get location maps
    const blueLocationMap = getLocationMapForHandRotation(blueHandRotDir);
    const redLocationMap = getLocationMapForHandRotation(redHandRotDir);

    // Calculate new end locations
    const previousBlueEndLoc =
      previousBeat.motions[MotionColor.BLUE]!.endLocation;
    const previousRedEndLoc =
      previousBeat.motions[MotionColor.RED]!.endLocation;

    const newBlueEndLoc = blueLocationMap[previousBlueEndLoc];
    const newRedEndLoc = redLocationMap[previousRedEndLoc];

    // Derive GridPosition from (blue, red) location tuple using GridPositionDeriver
    const newPosition = this.gridPositionDeriver.getGridPositionFromLocations(
      newBlueEndLoc,
      newRedEndLoc
    );

    return newPosition;
  }

  /**
   * Create transformed motion data for the new beat
   */
  private _createTransformedMotion(
    color: MotionColor,
    previousBeat: BeatData,
    previousMatchingBeat: BeatData
  ): MotionData {
    const previousMotion = previousBeat.motions[color];
    const matchingMotion = previousMatchingBeat.motions[color];

    if (!previousMotion || !matchingMotion) {
      throw new Error(`Missing motion data for ${color}`);
    }

    // Get hand rotation direction
    const handRotDir = getHandRotationDirection(
      matchingMotion.startLocation,
      matchingMotion.endLocation
    );

    // Get the appropriate location map
    const locationMap = getLocationMapForHandRotation(handRotDir);

    // Calculate rotated end location
    const newEndLocation =
      locationMap[previousMotion.endLocation];

    // Create transformed motion
    return {
      ...matchingMotion,
      startLocation: previousMotion.endLocation,
      endLocation: newEndLocation,
      // Start orientation will be set by orientationCalculationService
      // End orientation will be calculated by orientationCalculationService
    };
  }
}

/**
 * Strict Swapped CAP Executor
 *
 * Executes the strict swapped CAP (Circular Arrangement Pattern) by:
 * 1. Taking a partial sequence (always first half - no quartering)
 * 2. Swapping blue and red attributes between beats
 * 3. Generating the remaining beats to complete the circular pattern
 *
 * The swapping works by:
 * - Swapping blue ‚Üî red motion attributes entirely
 * - Transforming positions according to the swap map
 * - Maintaining the same letters, motion types, and prop rotation directions
 * - Only the color assignment changes
 *
 * IMPORTANT: Slice size is ALWAYS halved (no user choice like STRICT_ROTATED)
 */

import { inject, injectable } from "inversify";

import type { BeatData } from "$create/shared/workspace-panel";
import { type IGridPositionDeriver, MotionColor, type MotionData } from "$shared";
import { TYPES } from "$shared/inversify/types";
import type { GridPosition } from "$shared/pictograph/grid/domain/enums/grid-enums";

import type { IOrientationCalculationService } from "../../../shared/services/contracts";
import {
  SWAPPED_CAP_VALIDATION_SET,
  SWAPPED_POSITION_MAP,
} from "../../domain/constants/strict-cap-position-maps";
import type { SliceSize } from "../../domain/models/circular-models";

@injectable()
export class StrictSwappedCAPExecutor {
  constructor(
    @inject(TYPES.IOrientationCalculationService)
    private orientationCalculationService: IOrientationCalculationService,
    @inject(TYPES.IGridPositionDeriver)
    private gridPositionDeriver: IGridPositionDeriver
  ) {}

  /**
   * Execute the strict swapped CAP
   *
   * @param sequence - The partial sequence to complete (must include start position at index 0)
   * @param sliceSize - Ignored (swapped CAP always uses halved)
   * @returns The complete circular sequence with all beats
   */
  executeCAP(sequence: BeatData[], _sliceSize: SliceSize): BeatData[] {
    // Validate the sequence
    this._validateSequence(sequence);

    // Remove start position (index 0) for processing
    const startPosition = sequence.shift();
    if (!startPosition) {
      throw new Error("Sequence must have a start position");
    }

    // Calculate how many beats to generate (always doubles for swapped)
    const sequenceLength = sequence.length;
    const entriesToAdd = sequenceLength; // Always halved = doubles the sequence

    // Generate the new beats
    const generatedBeats: BeatData[] = [];
    let lastBeat = sequence[sequence.length - 1]!;
    const nextBeatNumber = lastBeat.beatNumber + 1;

    // Skip first two beats in the loop (start from beat 2)
    for (let i = 2; i < sequenceLength + 2; i++) {
      const finalIntendedLength = sequenceLength + entriesToAdd;
      const nextBeat = this._createNewCAPEntry(
        sequence,
        lastBeat,
        nextBeatNumber + i - 2,
        finalIntendedLength
      );

      generatedBeats.push(nextBeat);
      sequence.push(nextBeat);
      lastBeat = nextBeat;
    }

    // Re-insert start position at the beginning
    sequence.unshift(startPosition);

    return sequence;
  }

  /**
   * Validate that the sequence can perform a swapped CAP
   * Requirement: swapped_position(start_position) === end_position
   */
  private _validateSequence(sequence: BeatData[]): void {
    if (sequence.length < 2) {
      throw new Error(
        "Sequence must have at least 2 beats (start position + 1 beat)"
      );
    }

    const startPos = sequence[0]!.startPosition;
    const endPos = sequence[sequence.length - 1]!.endPosition;

    if (!startPos || !endPos) {
      throw new Error("Sequence beats must have valid start and end positions");
    }

    // Check if the (start, end) pair is valid for swapping
    const key = `${startPos},${endPos}`;

    if (!SWAPPED_CAP_VALIDATION_SET.has(key)) {
      const expectedEnd = SWAPPED_POSITION_MAP[startPos];
      throw new Error(
        `Invalid position pair for swapped CAP: ${startPos} ‚Üí ${endPos}. ` +
          `For a swapped CAP from ${startPos}, the sequence must end at ${expectedEnd}.`
      );
    }
  }

  /**
   * Create a new CAP entry by transforming a previous beat
   */
  private _createNewCAPEntry(
    sequence: BeatData[],
    previousBeat: BeatData,
    beatNumber: number,
    finalIntendedLength: number
  ): BeatData {
    // Get the corresponding beat from the first section using index mapping
    const previousMatchingBeat = this._getPreviousMatchingBeat(
      sequence,
      beatNumber,
      finalIntendedLength
    );

    // Create the swapped motions first
    const blueMotion = this._createSwappedMotion(
      MotionColor.BLUE,
      previousBeat,
      previousMatchingBeat.motions[MotionColor.RED] // Use RED from matching beat
    );
    const redMotion = this._createSwappedMotion(
      MotionColor.RED,
      previousBeat,
      previousMatchingBeat.motions[MotionColor.BLUE] // Use BLUE from matching beat
    );

    // CRITICAL: Recalculate the actual grid positions from the hand locations
    // The swapped map is just a validation - we need to derive positions from actual hand locations
    const actualStartPosition =
      this.gridPositionDeriver.getGridPositionFromLocations(
        blueMotion.startLocation,
        redMotion.startLocation
      );
    const actualEndPosition =
      this.gridPositionDeriver.getGridPositionFromLocations(
        blueMotion.endLocation,
        redMotion.endLocation
      );

    // Create the new beat with swapped attributes (BLUE ‚Üî RED)
    // Note: We swap the color assignments - blue gets red's data and vice versa
    const newBeat: BeatData = {
      ...previousMatchingBeat,
      id: `beat-${beatNumber}`,
      beatNumber,
      startPosition: actualStartPosition,
      endPosition: actualEndPosition,
      motions: {
        [MotionColor.BLUE]: blueMotion,
        [MotionColor.RED]: redMotion,
      },
    };

    // Update orientations
    const beatWithStartOri =
      this.orientationCalculationService.updateStartOrientations(
        newBeat,
        previousBeat
      );
    const finalBeat =
      this.orientationCalculationService.updateEndOrientations(
        beatWithStartOri
      );

    return finalBeat;
  }

  /**
   * Get the previous matching beat using index mapping (halved pattern)
   */
  private _getPreviousMatchingBeat(
    sequence: BeatData[],
    beatNumber: number,
    finalLength: number
  ): BeatData {
    const indexMap = this._getIndexMap(finalLength);
    const matchingBeatNumber = indexMap[beatNumber];

    if (matchingBeatNumber === undefined) {
      throw new Error(`No index mapping found for beatNumber ${beatNumber}`);
    }

    // Convert 1-based beatNumber to 0-based array index
    const arrayIndex = matchingBeatNumber - 1;

    if (arrayIndex < 0 || arrayIndex >= sequence.length) {
      throw new Error(
        `Invalid index mapping: beatNumber ${beatNumber} ‚Üí matchingBeatNumber ${matchingBeatNumber} ‚Üí arrayIndex ${arrayIndex} (sequence length: ${sequence.length})`
      );
    }

    return sequence[arrayIndex]!;
  }

  /**
   * Generate index mapping for retrieving corresponding beats (halved pattern only)
   * Maps second half beats to first half beats
   */
  private _getIndexMap(length: number): Record<number, number> {
    const map: Record<number, number> = {};
    const halfLength = Math.floor(length / 2);

    // Map beats in second half to their corresponding beats in first half
    for (let i = halfLength + 1; i <= length; i++) {
      map[i] = i - halfLength;
    }

    return map;
  }

  /**
   * Create swapped motion data for the new beat
   * Uses the opposite color's matching motion attributes
   */
  private _createSwappedMotion(
    color: MotionColor,
    previousBeat: BeatData,
    matchingMotion: MotionData // This is from the OPPOSITE color in the matching beat
  ): MotionData {
    const previousMotion = previousBeat.motions[color];

    if (!previousMotion) {
      throw new Error(`Missing motion data for ${color}`);
    }

    // CRITICAL: We must explicitly set the color to match THIS motion,
    // not copy it from the opposite color's matching motion!
    // We want Blue to DO what Red did, but still BE Blue.
    const swappedMotion = {
      ...matchingMotion,
      color, // FIXED: Set to THIS motion's color, not matchingMotion's color
      startLocation: previousMotion.endLocation,
      endLocation: matchingMotion.endLocation, // Keep same end location from swapped motion
      // motionType, rotationDirection, turns all come from matchingMotion (opposite color)
      // Start orientation will be set by orientationCalculationService
      // End orientation will be calculated by orientationCalculationService
    };

    return swappedMotion;
  }
}

/**
 * Beat Converter Service
 *
 * Handles conversion of PictographData to BeatData.
 * Single Responsibility: Transform pictograph data into beat format with proper initialization.
 */

import { injectable } from "inversify";

import type { BeatData, GridMode,MotionData, PictographData } from "$shared";
import { createMotionData } from "$shared";
import { GridLocation } from "$shared/pictograph/grid/domain/enums/grid-enums";
import {
  MotionType,
  Orientation,
  RotationDirection,
} from "$shared/pictograph/shared/domain/enums/pictograph-enums";

export interface IBeatConverterService {
  /**
   * Convert PictographData to BeatData - creates proper domain object
   */
  convertToBeat(
    pictograph: PictographData,
    beatNumber: number,
    gridMode: GridMode
  ): BeatData;
}

@injectable()
export class BeatConverterService implements IBeatConverterService {
  /**
   * Convert PictographData to BeatData - creates proper domain object
   */
  convertToBeat(
    pictograph: PictographData,
    beatNumber: number,
    gridMode: GridMode
  ): BeatData {
    // Ensure motions exist for blue and red with proper defaults
    const defaultMotion: MotionData = createMotionData({
      motionType: MotionType.STATIC,
      rotationDirection: RotationDirection.NO_ROTATION,
      startLocation: GridLocation.NORTH,
      endLocation: GridLocation.NORTH,
      turns: 0,
      startOrientation: Orientation.IN,
      endOrientation: Orientation.IN,
      gridMode: gridMode, // Add gridMode to default motion
    });

    // Add gridMode to the pictograph motions
    const motions = {
      blue: pictograph.motions.blue
        ? { ...pictograph.motions.blue, gridMode }
        : defaultMotion,
      red: pictograph.motions.red
        ? { ...pictograph.motions.red, gridMode }
        : defaultMotion,
    };

    return {
      ...pictograph, // Spread PictographData properties since BeatData extends PictographData
      id: crypto.randomUUID(),
      beatNumber: beatNumber,
      duration: 1.0,
      blueReversal: false,
      redReversal: false,
      isBlank: false,
      motions, // Override motions with the enhanced version
    };
  }
}

import { inject, injectable } from "inversify";

import type { IReversalDetectionService } from "$create/shared/services/contracts";
import type { BeatData, SequenceData } from "$shared";
// Import TYPES directly from inversify/types to avoid HMR issues with re-exports
import { TYPES } from "$shared/inversify/types";

import type { ICAPEndPositionSelector } from "../../../circular/services/contracts/ICAPEndPositionSelector";
import type { ICAPExecutorSelector } from "../../../circular/services/contracts/ICAPExecutorSelector";
import type { IPartialSequenceGenerator } from "../../../circular/services/contracts/IPartialSequenceGenerator";
import type { IRotationDirectionService } from "../../../circular/services/contracts/IRotationDirectionService";
import type { GenerationOptions } from "../../domain";
import { GenerationMode, PropContinuity } from "../../domain";
import type { IBeatGenerationOrchestrator } from "../contracts/IBeatGenerationOrchestrator";
import type { BeatGenerationOptions } from "../contracts/IBeatGenerationOrchestrator";
import type { IGenerationOrchestrationService } from "../contracts/IGenerationOrchestrationService";
import type { ISequenceMetadataService } from "../contracts/ISequenceMetadataService";
import type { IStartPositionSelector } from "../contracts/IStartPositionSelector";
import type { ITurnAllocator } from "../contracts/ITurnAllocator";
/**
 * Service orchestrating the complete sequence generation pipeline
 *
 * Extracted from generate-actions.svelte.ts to separate orchestration logic
 * from state management. This service composes multiple focused services to
 * build complete sequences for both freeform and circular modes.
 */
@injectable()
export class GenerationOrchestrationService
  implements IGenerationOrchestrationService
{
  constructor(
    @inject(TYPES.IStartPositionSelector)
    private readonly startPositionSelector: IStartPositionSelector,

    @inject(TYPES.IRotationDirectionService)
    private readonly rotationDirectionService: IRotationDirectionService,

    @inject(TYPES.ITurnAllocationCalculator)
    private readonly turnAllocationCalculator: ITurnAllocator,

    @inject(TYPES.IBeatGenerationOrchestrator)
    private readonly beatGenerationOrchestrator: IBeatGenerationOrchestrator,

    @inject(TYPES.ISequenceMetadataService)
    private readonly metadataService: ISequenceMetadataService,

    @inject(TYPES.IReversalDetectionService)
    private readonly reversalDetectionService: IReversalDetectionService,

    @inject(TYPES.IPartialSequenceGenerator)
    private readonly partialSequenceGenerator: IPartialSequenceGenerator,

    @inject(TYPES.ICAPEndPositionSelector)
    private readonly capEndPositionSelector: ICAPEndPositionSelector,

    @inject(TYPES.ICAPExecutorSelector)
    private readonly capExecutorSelector: ICAPExecutorSelector
  ) {}

  /**
   * Generate complete sequence - routes to appropriate mode
   */
  async generateSequence(options: GenerationOptions): Promise<SequenceData> {
    // Route to appropriate generation mode
    if (options.mode === GenerationMode.CIRCULAR) {
      return this.generateCircularSequence(options);
    }

    return this.generateFreeformSequence(options);
  }

  /**
   * Generate freeform sequence using focused service composition
   * EXACT ORIGINAL LOGIC from SequenceGenerationService.generateSequence
   */
  private async generateFreeformSequence(
    options: GenerationOptions
  ): Promise<SequenceData> {
    // Step 1: Get random start position
    const startPosition = await this.startPositionSelector.selectStartPosition(
      options.gridMode
    );
    const sequence: BeatData[] = [startPosition];

    // Step 2: Determine rotation directions
    const rotationDirections =
      this.rotationDirectionService.determineRotationDirections(
        options.propContinuity
      );

    // Step 3: Calculate turn allocation
    const level = this.metadataService.mapDifficultyToLevel(options.difficulty);
    const turnIntensity = options.turnIntensity ?? 1;
    const turnAllocation = await this.turnAllocationCalculator.allocateTurns(
      options.length,
      level,
      turnIntensity
    );

    // Step 4: Generate beats
    const beatGenOptions: BeatGenerationOptions = {
      level,
      turnAllocation,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: rotationDirections.blueRotationDirection,
      redRotationDirection: rotationDirections.redRotationDirection,
      gridMode: options.gridMode,
    };

    const generatedBeats = await this.beatGenerationOrchestrator.generateBeats(
      sequence,
      options.length,
      beatGenOptions
    );

    // Step 5: Build sequence data structure
    const word = this.metadataService.calculateWordFromBeats(generatedBeats);
    const metadata = this.metadataService.createGenerationMetadata({
      beatsGenerated: generatedBeats.length,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: rotationDirections.blueRotationDirection,
      redRotationDirection: rotationDirections.redRotationDirection,
      turnIntensity,
      level,
    });

    // Import shared utilities dynamically to avoid circular dependencies
    const { createSequenceData } = await import("$shared");

    const sequenceData = createSequenceData({
      name: word || `Sequence ${Date.now()}`,
      word,
      beats: generatedBeats,
      startingPositionBeat: startPosition,
      startPosition,
      gridMode: options.gridMode,
      propType: options.propType,
      difficultyLevel: options.difficulty,
      isFavorite: false,
      isCircular: false,
      tags: ["generated", "freeform"],
      metadata,
    });

    // Step 6: Apply reversal detection
    const finalSequence =
      this.reversalDetectionService.processReversals(sequenceData);

    return finalSequence;
  }

  /**
   * Generate circular sequence using CAP executor
   * EXACT ORIGINAL LOGIC from SequenceGenerationService.generatePatternSequence
   */
  private async generateCircularSequence(
    options: GenerationOptions
  ): Promise<SequenceData> {
    // Import circular-specific models
    const { CAPType, SliceSize } = await import(
      "../../../circular/domain/models/circular-models"
    );

    // Use constructor-injected services to avoid HMR issues
    // Determine which CAP executor to use based on capType option
    const capTypeValue: CAPType | string | undefined = options.capType ?? CAPType.STRICT_ROTATED;
    const isValidCAPType = (value: CAPType | string | undefined): value is CAPType => {
      return value !== undefined && Object.values(CAPType).includes(value as CAPType);
    };
    const capType: CAPType = isValidCAPType(capTypeValue)
      ? capTypeValue
      : CAPType.STRICT_ROTATED;
    const capExecutor = this.capExecutorSelector.getExecutor(capType);

    // Get slice size
    const sliceSizeValue: SliceSize | string | undefined = options.sliceSize ?? SliceSize.HALVED;
    const isValidSliceSize = (value: SliceSize | string | undefined): value is SliceSize => {
      return value !== undefined && Object.values(SliceSize).includes(value as SliceSize);
    };
    const sliceSize: SliceSize = isValidSliceSize(sliceSizeValue)
      ? sliceSizeValue
      : SliceSize.HALVED;

    // Determine start and required end positions
    const { GridMode, GridPosition } = await import("$shared");
    const basicStartPositions =
      options.gridMode === GridMode.DIAMOND
        ? [GridPosition.ALPHA1, GridPosition.BETA5, GridPosition.GAMMA11]
        : [GridPosition.ALPHA2, GridPosition.BETA4, GridPosition.GAMMA12];

    const startPos =
      basicStartPositions[
        Math.floor(Math.random() * basicStartPositions.length)
      ];
    // Use CAP-specific end position selector (different end positions for rotated/mirrored/swapped/complementary)
    const requiredEndPos = this.capEndPositionSelector.determineEndPosition(
      capType,
      startPos!,
      sliceSize
    );

    // Generate partial sequence ending at required position
    const partialSequence =
      await this.partialSequenceGenerator.generatePartialSequence(
        startPos,
        requiredEndPos,
        sliceSize,
        options
      );

    // Execute CAP to complete the circle
    const circularBeats = capExecutor.executeCAP(partialSequence, sliceSize);

    // Build sequence data
    const word = this.metadataService.calculateWordFromBeats(
      circularBeats.slice(1)
    ); // Exclude start position
    const metadata = this.metadataService.createGenerationMetadata({
      beatsGenerated: circularBeats.length - 1,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: "",
      redRotationDirection: "",
      turnIntensity: options.turnIntensity ?? 1,
      level: this.metadataService.mapDifficultyToLevel(options.difficulty),
    });

    const { createSequenceData } = await import("$shared");
    const capTypeTag = typeof capType === 'string' ? capType.replace("_", "-") : String(capType);
    const sequence = createSequenceData({
      name: `Circular ${word}`,
      word,
      beats: circularBeats.slice(1), // Exclude start position beat
      ...(circularBeats[0] && { startingPositionBeat: circularBeats[0] }),
      ...(circularBeats[0] && { startPosition: circularBeats[0] }),
      gridMode: options.gridMode,
      propType: options.propType,
      difficultyLevel: options.difficulty,
      isFavorite: false,
      isCircular: true,
      tags: ["circular", "cap", capTypeTag],
      metadata,
    });

    return this.reversalDetectionService.processReversals(sequence);
  }
}

/**
 * Generation Actions State - Reactive wrapper for generation orchestration
 *
 * Delegates complex generation logic to IGenerationOrchestrationService.
 * Manages reactive state and workbench animation updates.
 */

import type { SequenceState } from "$create/shared/state";
import type { SequenceData } from "$shared/foundation/domain/models";
import { resolve } from "$shared/inversify";
import { TYPES } from "$shared/inversify/types";

import type { GenerationOptions } from "../shared/domain";
import type { IGenerationOrchestrationService } from "../shared/services/contracts";

export function createGenerationActionsState(
  sequenceState?: SequenceState,
  getIsSequential?: () => boolean
) {
  let isGenerating = $state(false);
  let lastGeneratedSequence = $state<SequenceData | null>(null);
  let generationError = $state<string | null>(null);
  let orchestrationService: IGenerationOrchestrationService | null = null;

  async function onGenerateClicked(options: GenerationOptions) {
    if (isGenerating) return;

    isGenerating = true;
    generationError = null;

    try {
      if (!orchestrationService) {
        orchestrationService = resolve<IGenerationOrchestrationService>(
          TYPES.IGenerationOrchestrationService
        );
      }

      const generatedSequence =
        await orchestrationService.generateSequence(options);
      lastGeneratedSequence = generatedSequence;
      await updateWorkbenchWithSequence(generatedSequence);
    } catch (error) {
      generationError =
        error instanceof Error ? error.message : "Unknown generation error";
      console.error("‚ùå Generation failed:", error);
    } finally {
      isGenerating = false;
    }
  }

  async function updateWorkbenchWithSequence(sequence: SequenceData) {
    try {
      if (!sequenceState) return;

      const hasExistingSequence = sequenceState.getCurrentBeats().length > 0;

      if (hasExistingSequence) {
        window.dispatchEvent(new CustomEvent("clear-sequence-animation"));
        await new Promise((resolve) => setTimeout(resolve, 300));
      }

      const isSequential = getIsSequential?.() ?? false;

      // Dispatch BEFORE updating sequence to prepare BeatGrid for animation
      window.dispatchEvent(
        new CustomEvent("prepare-sequence-animation", {
          detail: {
            isSequential,
            beatCount: sequence.beats.length,
          },
        })
      );

      sequenceState.setCurrentSequence(sequence);
    } catch (error) {
      throw new Error(
        `Workbench update failed: ${error instanceof Error ? error.message : "Unknown error"}`
      );
    }
  }

  function clearError() {
    generationError = null;
  }

  function getGenerationSummary() {
    return {
      isGenerating,
      hasLastGenerated: lastGeneratedSequence !== null,
      lastGeneratedName: lastGeneratedSequence?.name ?? null,
      lastGeneratedBeats: lastGeneratedSequence?.beats.length ?? 0,
      hasError: generationError !== null,
      errorMessage: generationError,
    };
  }

  return {
    get isGenerating() {
      return isGenerating;
    },
    get lastGeneratedSequence() {
      return lastGeneratedSequence;
    },
    get generationError() {
      return generationError;
    },
    onGenerateClicked,
    clearError,
    getGenerationSummary,
  };
}

import type { BeatData } from "../models/BeatData";

export function createBeatData(data: Partial<BeatData> = {}): BeatData {
  return {
    // PictographData properties
    id: data.id ?? crypto.randomUUID(),
    letter: data.letter ?? null,
    startPosition: data.startPosition ?? null,
    endPosition: data.endPosition ?? null,
    motions: data.motions ?? {},

    // Beat context properties
    beatNumber: data.beatNumber ?? 1,
    duration: data.duration ?? 1.0,
    blueReversal: data.blueReversal ?? false,
    redReversal: data.redReversal ?? false,
    isBlank: data.isBlank ?? false,
    // Conditionally include isSelected only if it's defined
    ...(data.isSelected !== undefined && { isSelected: data.isSelected }),
  };
}

import type { IDeviceDetector, IViewportService, SequenceData } from "$shared";
import { ensureContainerInitialized, GridMode, resolve } from "$shared";
import { TYPES } from "$shared/inversify/types";

import type { IStartPositionService } from "../../construct/start-position-picker/services/contracts";
import type {
  CreateModuleServices,
  CreateModuleStates,
  InitializationResult,
  InitializationStatus,
} from "../orchestration/types";
import type {
  ICreateModuleService,
  ISequencePersistenceService,
  ISequenceService,
} from "../services/contracts";
import { getCreateModuleEventService } from "../services/implementations/CreateModuleEventService";
import { createModeSpecificPersistenceService } from "../services/implementations/ModeSpecificPersistenceService";
import { createConstructTabState,createCreateModuleState } from "../state";
import { createAssemblerTabState } from "../state/assembler-tab-state.svelte";
import { createGeneratorTabState } from "../state/generator-tab-state.svelte";
import { createSequenceState } from "../state/SequenceStateOrchestrator.svelte";

/**
 * Handles all CreateModule initialization logic in one place
 * Extracted from CreateModule.svelte onMount to improve testability
 */
export class CreateModuleInitializer {
  /**
   * Resolve all required services from DI container
   */
  async resolveServices(): Promise<CreateModuleServices> {
    await ensureContainerInitialized();

    const services: CreateModuleServices = {
      sequenceService: resolve<ISequenceService>(TYPES.ISequenceService),
      sequencePersistenceService: resolve<ISequencePersistenceService>(
        TYPES.ISequencePersistenceService
      ),
      startPositionService: resolve<IStartPositionService>(
        TYPES.IStartPositionService
      ),
      CreateModuleService: resolve<ICreateModuleService>(
        TYPES.ICreateModuleService
      ),
      deviceDetector: resolve<IDeviceDetector>(TYPES.IDeviceDetector),
      viewportService: resolve<IViewportService>(TYPES.IViewportService),
    };

    // Validate all services resolved successfully
    const missingServices = Object.entries(services)
      .filter(([_, service]) => !service)
      .map(([key]) => key);

    if (missingServices.length > 0) {
      throw new Error(
        `Failed to resolve services: ${missingServices.join(", ")}`
      );
    }

    return services;
  }

  /**
   * Create state factories from resolved services
   */
  async createStates(
    services: CreateModuleServices
  ): Promise<CreateModuleStates> {
    const { sequenceService, sequencePersistenceService, CreateModuleService } =
      services;

    // Wait a tick to ensure component context is fully established
    await new Promise((resolve) => setTimeout(resolve, 0));

    const CreateModuleState = createCreateModuleState(
      sequenceService,
      sequencePersistenceService
    );

    // Create mode-specific persistence services for each tab
    // This ensures each tab saves/loads from its own localStorage key
    const constructorPersistence = createModeSpecificPersistenceService("constructor", sequencePersistenceService);
    const assemblerPersistence = createModeSpecificPersistenceService("assembler", sequencePersistenceService);
    const generatorPersistence = createModeSpecificPersistenceService("generator", sequencePersistenceService);

    // Create constructor's own independent sequence state
    // Previously this was sharing CreateModuleState.sequenceState, causing tabs to share beat grids
    const constructorSequenceState = createSequenceState({
      sequenceService,
      sequencePersistenceService: constructorPersistence,
    });

    const constructTabState = createConstructTabState(
      CreateModuleService,
      constructorSequenceState,
      constructorPersistence
    );

    // Create tab-specific states for assembler and generator
    // Each tab gets its own independent sequence state and persistence
    const assemblerTabState = createAssemblerTabState(
      sequenceService,
      assemblerPersistence
    );

    const generatorTabState = createGeneratorTabState(
      sequenceService,
      generatorPersistence
    );

    // Register tab states with CreateModuleState so getActiveTabSequenceState() works
    CreateModuleState.constructorTabState = constructTabState;
    CreateModuleState.assemblerTabState = assemblerTabState;
    CreateModuleState.generatorTabState = generatorTabState;

    // Also set the legacy constructTabState accessor for backwards compatibility
    CreateModuleState.constructTabState = constructTabState;

    return { CreateModuleState, constructTabState };
  }

  /**
   * Initialize services and wire up callbacks
   */
  initializeServices(
    services: CreateModuleServices,
    states: CreateModuleStates
  ): void {
    const { CreateModuleService, startPositionService } = services;
    const { CreateModuleState } = states;

    // Initialize Create Module Service
    CreateModuleService.initialize();

    // Set up sequence state callbacks for CreateModuleEventService
    const CreateModuleEventService = getCreateModuleEventService();
    CreateModuleEventService.setSequenceStateCallbacks(
      () => CreateModuleState.sequenceState.getCurrentSequence(),
      (sequence) => CreateModuleState.sequenceState.setCurrentSequence(sequence)
    );

    // Set up option history callback
    CreateModuleEventService.setAddOptionToHistoryCallback(
      (beatIndex, beatData) => {
        CreateModuleState.addOptionToHistory(beatIndex, beatData);
      }
    );

    // Load start positions
    startPositionService.getDefaultStartPositions(GridMode.DIAMOND);
  }

  /**
   * Initialize states with persisted data
   */
  async initializeStates(states: CreateModuleStates): Promise<void> {
    const { CreateModuleState, constructTabState } = states;

    await CreateModuleState.initializeWithPersistence();
    await constructTabState.initializeConstructTab();

    // Initialize tab-specific sequence states with their persisted data
    // Each tab loads from its own localStorage key
    if (CreateModuleState.constructorTabState?.sequenceState) {
      await (CreateModuleState.constructorTabState.sequenceState.initializeWithPersistence as () => Promise<void>)();
    }
    if (CreateModuleState.assemblerTabState) {
      await (CreateModuleState.assemblerTabState.initializeAssemblerTab as () => Promise<void>)();
    }
    if (CreateModuleState.generatorTabState) {
      await (CreateModuleState.generatorTabState.initializeGeneratorTab as () => Promise<void>)();
    }
  }

  /**
   * Main initialization - orchestrates all setup steps with error handling and performance tracking
   */
  async initialize(): Promise<InitializationResult> {
    const startTime = performance.now();

    try {
      // Step 1: Resolve services
      const services = await this.resolveServices();

      // Step 2: Create state factories
      const states = await this.createStates(services);

      // Step 3: Initialize services and wire up callbacks
      this.initializeServices(services, states);

      // Step 4: Load persisted data
      await this.initializeStates(states);

      const initTime = performance.now() - startTime;
      console.log(`‚úÖ CreateModule initialized in ${initTime.toFixed(2)}ms`);

      // Success status
      const status: InitializationStatus = {
        servicesResolved: true,
        statesInitialized: true,
        persistenceLoaded: true,
        ready: true,
      };

      return {
        services,
        states,
        status,
      };
    } catch (error) {
      const initTime = performance.now() - startTime;
      console.error(
        `‚ùå CreateModule initialization failed after ${initTime.toFixed(2)}ms:`,
        error
      );

      // Error status
      const status: InitializationStatus = {
        servicesResolved: false,
        statesInitialized: false,
        persistenceLoaded: false,
        ready: false,
        error: error instanceof Error ? error.message : String(error),
      };

      throw new Error(`CreateModule initialization failed: ${status.error}`);
    }
  }
}

/**
 * Construct Tab Coordination Service - Implementation
 *
 * Coordinates between construct tab components (start position picker, option picker).
 * Based on desktop ConstructTabCoordinationService but simplified for web with runes.
 *
 * FIXED: Added proper state synchronization to resolve start position selection getting stuck
 */

import { injectable } from "inversify";

import type { SequenceData } from "$shared";

import type { BeatData } from "../../domain/models/BeatData";
import type { IBuildConstructSectionCoordinator as IConstructCoordinator } from "../contracts/IConstructCoordinator";

// Note: This service will need to be updated to use the new DI pattern
// For now, we'll comment out the direct import to avoid build errors
// import { sequenceStateService } from "../../SequenceStateService.svelte";

interface ComponentWithEventHandler {
  handleEvent?: (eventType: string, data: unknown) => void;
}

interface StartPositionSelectedDetail {
  startPosition: BeatData;
}

interface OptionSelectedDetail {
  beatData: BeatData;
}

interface SequenceModifiedDetail {
  sequence: SequenceData;
}

@injectable()
export class ConstructCoordinator implements IConstructCoordinator {
  private components: Record<string, ComponentWithEventHandler> = {};
  private isHandlingSequenceModification = false;
  private eventListenersSetup = false;
  private boundEventHandlers: {
    startPositionSelected: (event: CustomEvent<StartPositionSelectedDetail>) => void;
    optionSelected: (event: CustomEvent<OptionSelectedDetail>) => void;
    sequenceModified: (event: CustomEvent<SequenceModifiedDetail>) => void;
  } | null = null;

  constructor() {}

  /**
   * Clean up resources when service is destroyed
   */
  destroy(): void {
    this.disconnectComponentSignals();
    this.components = {};
  }

  setupComponentCoordination(
    components: Record<string, ComponentWithEventHandler>
  ): void {
    this.components = components;

    // Set up any cross-component communication here (only once)
    if (!this.eventListenersSetup) {
      this.connectComponentSignals();
      this.eventListenersSetup = true;
    }
  }

  async handleSequenceModified(sequence: SequenceData): Promise<void> {
    if (this.isHandlingSequenceModification) {
      return;
    }

    try {
      this.isHandlingSequenceModification = true;

      // State management is handled by individual components via DI

      // Update UI based on sequence state
      this.updateUIBasedOnSequence(sequence);

      // Notify components about sequence change
      this.notifyComponents("sequence_modified", { sequence });
    } catch (error) {
      console.error("‚ùå Error handling sequence modification:", error);
    } finally {
      this.isHandlingSequenceModification = false;
    }
  }

  handleStartPositionSet(_startPosition: BeatData): void {
    try {
      return;
    } catch (error) {
      console.error("‚ùå Error handling start position set:", error);
      // Error handling is managed by individual components
    }
  }

  handleBeatAdded(beatData: BeatData): void {
    try {
      // Beat addition is handled by workbench components directly
      // This coordinator just notifies other components of the change
      this.notifyComponents("beat_added", { beatData });
    } catch (error) {
      console.error("‚ùå Error handling beat added:", error);
    }
  }

  handleGenerationRequest(
    config: Record<string, unknown>
  ): void {
    try {
      // Generation is handled by the generate module components
      // This coordinator just facilitates communication between components
      this.notifyComponents("generation_requested", { config });

      // Simulate generation completion
      setTimeout(() => {
        this.notifyComponents("generation_completed", {
          success: true,
          message: "Generation completed",
        });
      }, 1000);
    } catch (error) {
      console.error("‚ùå Error handling generation request:", error);
    }
  }

  handleUITransitionRequest(targetPanel: string): void {
    try {
      // Emit custom events for UI transitions (similar to legacy implementation)
      const transitionEvent = new CustomEvent("construct-tab-transition", {
        detail: { targetPanel },
        bubbles: true,
      });

      if (typeof window !== "undefined") {
        document.dispatchEvent(transitionEvent);
      }

      // Notify components about the transition
      this.notifyComponents("ui_transition", { targetPanel });
    } catch (error) {
      console.error("‚ùå Error handling UI transition:", error);
    }
  }

  private connectComponentSignals(): void {
    // Set up event listeners for component coordination
    if (typeof window !== "undefined") {
      // Create bound event handlers to allow proper cleanup
      this.boundEventHandlers = {
        startPositionSelected: ((event: CustomEvent<StartPositionSelectedDetail>) => {
          void this.handleStartPositionSet(event.detail.startPosition);
        }) as (event: CustomEvent<StartPositionSelectedDetail>) => void,

        optionSelected: ((event: CustomEvent<OptionSelectedDetail>) => {
          void this.handleBeatAdded(event.detail.beatData);
        }) as (event: CustomEvent<OptionSelectedDetail>) => void,

        sequenceModified: ((event: CustomEvent<SequenceModifiedDetail>) => {
          void this.handleSequenceModified(event.detail.sequence);
        }) as (event: CustomEvent<SequenceModifiedDetail>) => void,
      };

      // Add event listeners
      document.addEventListener(
        "start-position-selected",
        this.boundEventHandlers.startPositionSelected as EventListener
      );
      document.addEventListener(
        "option-selected",
        this.boundEventHandlers.optionSelected as EventListener
      );
      document.addEventListener(
        "sequence-modified",
        this.boundEventHandlers.sequenceModified as EventListener
      );
    }
  }

  /**
   * Clean up event listeners to prevent memory leaks and duplicate handlers
   */
  private disconnectComponentSignals(): void {
    if (typeof window !== "undefined" && this.boundEventHandlers) {
      document.removeEventListener(
        "start-position-selected",
        this.boundEventHandlers.startPositionSelected as EventListener
      );
      document.removeEventListener(
        "option-selected",
        this.boundEventHandlers.optionSelected as EventListener
      );
      document.removeEventListener(
        "sequence-modified",
        this.boundEventHandlers.sequenceModified as EventListener
      );
      this.boundEventHandlers = null;
      this.eventListenersSetup = false;
    }
  }

  private updateUIBasedOnSequence(sequence: SequenceData): void {
    try {
      // Determine which panel to show based on sequence state
      const hasStartPosition = sequence.startingPositionBeat !== undefined;
      const hasBeats = sequence.beats.length > 0;

      let targetPanel: string;

      if (hasStartPosition || hasBeats) {
        targetPanel = "option_picker";
      } else {
        targetPanel = "start_position_picker";
      }

      // Transition to appropriate panel
      this.handleUITransitionRequest(targetPanel);
    } catch (error) {
      console.error("‚ùå Error updating UI based on sequence:", error);
    }
  }

  private notifyComponents(eventType: string, data: unknown): void {
    // Notify individual components if they have handlers
    Object.entries(this.components).forEach(([name, component]) => {
      if (typeof component.handleEvent === "function") {
        try {
          component.handleEvent(eventType, data);
        } catch (error) {
          console.error(`‚ùå Error notifying component ${name}:`, error);
        }
      }
    });

    // Emit global event for any listeners
    if (typeof window !== "undefined") {
      const event = new CustomEvent(`construct-coordination-${eventType}`, {
        detail: data,
        bubbles: true,
      });
      document.dispatchEvent(event);
    }
  }
}

/**
 * ConstructTab Event Service
 *
 * Centralized event handling for the ConstructTab component.
 * This service handles all the event coordination between different child components
 * that was previously scattered throughout the massive ConstructTab component.
 */

import { injectable } from "inversify";

import {
  type BeatData,
  createBeatData,
  type IOrientationCalculationService,
  type PictographData,
  resolve,
  type SequenceData,
} from "$shared";
import { TYPES } from "$shared/inversify/types";

import type {
  IBuildConstructSectionCoordinator,
  ICreateModuleEventService,
} from "../contracts";

@injectable()
export class CreateModuleEventService implements ICreateModuleEventService {
  private constructCoordinator: IBuildConstructSectionCoordinator | null = null;
  private orientationCalculationService: IOrientationCalculationService | null =
    null;
  private initialized = false;

  // Callback to access current sequence from component state
  private getCurrentSequenceCallback: (() => SequenceData | null) | null = null;
  private updateSequenceCallback: ((sequence: SequenceData) => void) | null =
    null;

  // Callback to add option to history
  private addOptionToHistoryCallback:
    | ((beatIndex: number, beatData: BeatData) => void)
    | null = null;

  // Callback to push undo snapshot
  private pushUndoSnapshotCallback:
    | ((type: "ADD_BEAT", metadata?: Record<string, unknown>) => void)
    | null = null;

  constructor() {
    // Don't initialize services in constructor - wait for lazy initialization
  }

  private initializeServices() {
    if (this.initialized) {
      return; // Already initialized
    }

    try {
      this.constructCoordinator = resolve<IBuildConstructSectionCoordinator>(
        TYPES.IBuildConstructTabCoordinator
      );
      this.orientationCalculationService =
        resolve<IOrientationCalculationService>(
          TYPES.IOrientationCalculationService
        );
      this.initialized = true;
    } catch (error) {
      // This is expected during SSR - services will be resolved once client-side DI container is ready
      // Services will remain null and methods will handle gracefully
    }
  }

  /**
   * Set callbacks to access sequence state from component
   */
  setSequenceStateCallbacks(
    getCurrentSequence: () => SequenceData | null,
    updateSequence: (sequence: SequenceData) => void
  ): void {
    this.getCurrentSequenceCallback = getCurrentSequence;
    this.updateSequenceCallback = updateSequence;
  }

  /**
   * Set callback to add option to history
   */
  setAddOptionToHistoryCallback(
    addOptionToHistory: (beatIndex: number, beatData: BeatData) => void
  ): void {
    this.addOptionToHistoryCallback = addOptionToHistory;
  }

  /**
   * Set callback to push undo snapshot
   */
  setPushUndoSnapshotCallback(
    pushUndoSnapshot: (type: "ADD_BEAT", metadata?: Record<string, unknown>) => void
  ): void {
    this.pushUndoSnapshotCallback = pushUndoSnapshot;
  }

  private ensureInitialized() {
    if (!this.initialized) {
      this.initializeServices();
    }
  }

  /**
   * Handle option selection in the Create module
   * OPTIMIZED: Add to sequence immediately for responsive UX, then process in background
   */
  handleOptionSelected(option: PictographData): void {
    try {
      performance.mark("event-service-start");
      this.ensureInitialized();

      // Get current sequence from component state
      const currentSequence = this.getCurrentSequenceCallback?.();
      if (!currentSequence) {
        throw new Error("No current sequence available");
      }

      performance.mark("initialization-complete");

      // Calculate correct beat number based on current sequence length
      const nextBeatNumber = currentSequence.beats.length + 1;

      // üì∏ PUSH UNDO SNAPSHOT: Save state BEFORE adding beat (now deferred via queueMicrotask)
      this.pushUndoSnapshotCallback?.("ADD_BEAT", {
        beatNumber: nextBeatNumber,
        description: `Add beat ${nextBeatNumber}`,
      });

      // Create initial beat data from option with correct beat number
      let beatData = createBeatData({
        ...option, // Spread PictographData properties since BeatData extends PictographData
        beatNumber: nextBeatNumber,
        isBlank: false, // This is a real beat with pictograph data
      });

      performance.mark("beat-data-created");

      // üîÑ OPTIMIZATION: Calculate orientations BEFORE UI update to batch into single update
      if (
        currentSequence.beats.length > 0 &&
        this.orientationCalculationService
      ) {
        const lastBeat =
          currentSequence.beats[currentSequence.beats.length - 1];

        // Only apply orientation calculations if both beats have motion data
        if (lastBeat && !lastBeat.isBlank && !beatData.isBlank) {
          try {
            // Update start orientations from the last beat's end orientations
            beatData =
              this.orientationCalculationService.updateStartOrientations(
                beatData,
                lastBeat
              );
            performance.mark("start-orientations-complete");

            // Update end orientations based on the motion calculations
            beatData =
              this.orientationCalculationService.updateEndOrientations(
                beatData
              );
            performance.mark("end-orientations-complete");
          } catch (orientationError) {
            console.warn(
              `‚ö†Ô∏è CreateModuleEventService: Failed to calculate orientations for beat ${nextBeatNumber}:`,
              orientationError
            );
            // Continue without orientation updates rather than failing completely
          }
        }
      }
      performance.mark("orientation-processing-complete");

      // üöÄ SINGLE UI UPDATE: Add beat with orientations already calculated
      const finalSequence = {
        ...currentSequence,
        beats: [...currentSequence.beats, beatData],
      };
      performance.mark("sequence-updated");

      this.updateSequenceCallback?.(finalSequence);
      performance.mark("ui-callback-complete");

      // üìù ADD TO HISTORY: Track this option addition for undo functionality
      this.addOptionToHistoryCallback?.(nextBeatNumber - 1, beatData); // beatIndex is 0-based
      performance.mark("history-updated");

      // üì° COORDINATION: Notify other components (async, non-blocking)
      performance.mark("coordination-start");
      if (this.constructCoordinator) {
        this.constructCoordinator.handleBeatAdded(beatData).catch((error) => {
          console.warn(
            "‚ö†Ô∏è CreateModuleEventService: Coordination service error:",
            error
          );
        });
      }
      performance.mark("coordination-complete");
    } catch (error) {
      console.error("‚ùå Error handling option selection:", error);
      throw error;
    }
  }

