/**
 * Undo Controller
 *
 * Wraps the undo service so the create module state can stay declarative.
 * Handles snapshotting, callbacks, and the animation-friendly undo flow.
 */

import type { BuildModeId, SequenceData } from "$shared";

import type {
  IUndoService,
  UndoMetadata,
} from "../../services/contracts/IUndoService";
import { UndoOperationType } from "../../services/contracts/IUndoService";
import type { SequenceState } from "../SequenceStateOrchestrator.svelte";

type UndoControllerDeps = {
  undoService: IUndoService;
  sequenceState: SequenceState;
  getActiveSection: () => BuildModeId;
  setActiveSectionInternal: (
    panel: BuildModeId,
    addToHistory?: boolean
  ) => Promise<void>;
};

export type UndoSnapshotType =
  | "REMOVE_BEATS"
  | "CLEAR_SEQUENCE"
  | "ADD_BEAT"
  | "SELECT_START_POSITION";

export function createUndoController({
  undoService,
  sequenceState,
  getActiveSection,
  setActiveSectionInternal,
}: UndoControllerDeps) {
  let showStartPositionPickerCallback: (() => void) | null = null;
  let syncPickerStateCallback: (() => void) | null = null;
  let onUndoingOptionCallback: ((isUndoing: boolean) => void) | null = null;

  let undoChangeCounter = $state(0);

  undoService.onChange(() => {
    undoChangeCounter++;
  });

  function pushUndoSnapshot(type: UndoSnapshotType, metadata?: UndoMetadata) {
    if (!sequenceState.currentSequence && type !== "SELECT_START_POSITION") {
      return;
    }

    const operationType =
      type === "SELECT_START_POSITION"
        ? UndoOperationType.SELECT_START_POSITION
        : type === "ADD_BEAT"
          ? UndoOperationType.ADD_BEAT
          : type === "REMOVE_BEATS"
            ? UndoOperationType.REMOVE_BEATS
            : UndoOperationType.CLEAR_SEQUENCE;

    const currentSequenceRef = sequenceState.currentSequence;
    const selectedBeatNumberRef = sequenceState.selectedBeatNumber;
    const activeSectionRef = getActiveSection();
    const timestampRef = Date.now();

    queueMicrotask(() => {
      const sequenceCopy: SequenceData | null = currentSequenceRef
        ? {
            ...currentSequenceRef,
            beats: currentSequenceRef.beats.map((beat) => ({ ...beat })),
          }
        : null;

      const beforeState = {
        sequence: sequenceCopy,
        selectedBeatNumber: selectedBeatNumberRef,
        activeSection: activeSectionRef,
        shouldShowStartPositionPicker:
          type === "SELECT_START_POSITION" ? true : false,
        timestamp: timestampRef,
      };

      undoService.pushUndo(operationType, beforeState, metadata);
    });
  }

  function undo(): boolean {
    const lastEntry = undoService.undo();
    if (!lastEntry) {
      return false;
    }

    if (lastEntry.type === UndoOperationType.SELECT_START_POSITION) {
      void sequenceState.clearSequenceCompletely();
      if (showStartPositionPickerCallback) {
        showStartPositionPickerCallback();
      }
      return true;
    }

    const currentSequence = sequenceState.currentSequence;
    const restoredSequence = lastEntry.beforeState.sequence;

    if (currentSequence && restoredSequence) {
      const currentBeatCount = currentSequence.beats.length;
      const restoredBeatCount = restoredSequence.beats.length;

      if (currentBeatCount > restoredBeatCount) {
        const beatsToRemove: number[] = [];
        for (let i = restoredBeatCount; i < currentBeatCount; i++) {
          beatsToRemove.push(i);
        }

        sequenceState.animationState.startRemovingBeats(beatsToRemove);
        if (onUndoingOptionCallback) {
          onUndoingOptionCallback(true);
        }

        const fadeAnimationDuration = 250;
        setTimeout(() => {
          sequenceState.setCurrentSequence(restoredSequence);
          sequenceState.animationState.endRemovingBeats();

          if (onUndoingOptionCallback) {
            onUndoingOptionCallback(false);
          }

          restoreSelection(lastEntry.beforeState.selectedBeatNumber);
          if (lastEntry.beforeState.activeSection) {
            void setActiveSectionInternal(
              lastEntry.beforeState.activeSection,
              false
            );
          }

          if (syncPickerStateCallback) {
            syncPickerStateCallback();
          }
        }, fadeAnimationDuration);

        return true;
      }
    }

    sequenceState.setCurrentSequence(restoredSequence);
    restoreSelection(lastEntry.beforeState.selectedBeatNumber);
    if (lastEntry.beforeState.activeSection) {
      void setActiveSectionInternal(lastEntry.beforeState.activeSection, false);
    }

    if (syncPickerStateCallback) {
      syncPickerStateCallback();
    }

    return true;
  }

  function restoreSelection(selectedBeatNumber: number | null) {
    if (selectedBeatNumber !== null) {
      sequenceState.selectBeat(selectedBeatNumber);
    } else {
      sequenceState.clearSelection();
    }
  }

  function clearUndoHistory() {
    undoService.clearHistory();
  }

  function setShowStartPositionPickerCallback(callback: () => void) {
    showStartPositionPickerCallback = callback;
  }

  function setSyncPickerStateCallback(callback: () => void) {
    syncPickerStateCallback = callback;
  }

  function setOnUndoingOptionCallback(callback: (isUndoing: boolean) => void) {
    onUndoingOptionCallback = callback;
  }

  return {
    pushUndoSnapshot,
    undo,
    clearUndoHistory,
    setShowStartPositionPickerCallback,
    setSyncPickerStateCallback,
    setOnUndoingOptionCallback,
    get canUndo() {
      void undoChangeCounter;
      return undoService.canUndo;
    },
    get canRedo() {
      void undoChangeCounter;
      return undoService.canRedo;
    },
    get undoHistory() {
      return undoService.undoHistory;
    },
  };
}

export type UndoController = ReturnType<typeof createUndoController>;

/**
 * Hand Path Coordinator State
 *
 * Manages state coordination between HandPathWorkspace and HandPathToolPanel.
 * Provides a single source of truth for hand path drawing state.
 */

import { GridLocation, GridMode, PropType, resolve, TYPES } from "$shared";

import type {
  IHandPathDirectionDetector,
  IPathToMotionConverter,
} from "../../assemble/handpath-builder/services/contracts";
import {
  createGesturalPathState,
  type GesturalPathState,
} from "../../assemble/handpath-builder/state";

export function createHandPathCoordinator() {
  // Services
  let handPathDirectionDetector: IHandPathDirectionDetector | null = null;
  let pathToMotionConverter: IPathToMotionConverter | null = null;

  // State
  const pathState: GesturalPathState = createGesturalPathState();
  let sequenceLength = $state(16);
  let gridMode = $state<GridMode>(GridMode.DIAMOND);
  let isStarted = $state(false);

  // Initialize services
  function initializeServices() {
    if (!handPathDirectionDetector) {
      handPathDirectionDetector = resolve<IHandPathDirectionDetector>(
        TYPES.IHandPathDirectionDetector
      );
    }
    if (!pathToMotionConverter) {
      pathToMotionConverter = resolve<IPathToMotionConverter>(
        TYPES.IPathToMotionConverter
      );
    }
  }

  // Start or restart drawing
  function startDrawing(): void {
    const startLocation =
      gridMode === GridMode.DIAMOND
        ? GridLocation.NORTH
        : GridLocation.NORTHEAST;
    pathState.initializeSession(sequenceLength, gridMode, startLocation);
    isStarted = true;
  }

  // Handle segment complete
  function handleSegmentComplete(start: GridLocation, end: GridLocation): void {
    if (!handPathDirectionDetector || !pathState.config) return;

    const handMotionType = handPathDirectionDetector.getHandMotionType(
      start,
      end,
      pathState.config.gridMode
    );

    pathState.recordSegment(start, end, handMotionType);

    // Auto-complete hand if all beats drawn
    if (pathState.isCurrentHandComplete) {
      pathState.completeCurrentHand();
    }
  }

  // Handle advance button press
  function handleAdvancePressed(): void {
    pathState.pressAdvanceButton();
  }

  // Handle advance button release
  function handleAdvanceReleased(): void {
    pathState.releaseAdvanceButton();
  }

  // Handle hand complete
  function handleHandComplete(): void {
    pathState.completeCurrentHand();
  }

  // Handle restart - reset current drawing session
  function handleRestart(): void {
    pathState.reset();
  }

  // Handle back to settings - reset and return to pre-flight view
  function handleBackToSettings(): void {
    pathState.reset();
    isStarted = false;
  }

  // Handle finish
  function handleFinish(
    onSequenceComplete?: (motions: { blue: MotionData[]; red: MotionData[] }) => void
  ): void {
    if (!pathToMotionConverter || !pathState.selectedRotationDirection) {
      alert("Please select a rotation direction before finishing.");
      return;
    }

    const blueMotions = pathState.blueHandPath
      ? pathToMotionConverter.convertHandPathToMotions(
          pathState.blueHandPath,
          pathState.selectedRotationDirection,
          PropType.HAND
        )
      : [];

    const redMotions = pathState.redHandPath
      ? pathToMotionConverter.convertHandPathToMotions(
          pathState.redHandPath,
          pathState.selectedRotationDirection,
          PropType.HAND
        )
      : [];

    onSequenceComplete?.({ blue: blueMotions, red: redMotions });

    // Reset state and return to settings after completion
    handleBackToSettings();
  }

  // Handle back to blue
  function handleBackToBlue(): void {
    pathState.backToBlueHand();
  }

  return {
    // State (read-only getters)
    get pathState() {
      return pathState;
    },
    get sequenceLength() {
      return sequenceLength;
    },
    set sequenceLength(value: number) {
      sequenceLength = value;
    },
    get gridMode() {
      return gridMode;
    },
    set gridMode(value: GridMode) {
      gridMode = value;
    },
    get isStarted() {
      return isStarted;
    },

    // Actions
    initializeServices,
    startDrawing,
    handleSegmentComplete,
    handleAdvancePressed,
    handleAdvanceReleased,
    handleHandComplete,
    handleRestart,
    handleBackToSettings,
    handleFinish,
    handleBackToBlue,
  };
}

export type HandPathCoordinator = ReturnType<typeof createHandPathCoordinator>;

/**
 * Auto Edit Panel Manager
 *
 * Handles automatic opening of edit panel when multiple beats are selected.
 * Consolidates multi-select edit panel logic from CreateModule.svelte.
 *
 * Domain: Create module - Edit Panel Automation
 */

import type { BeatData, PictographData } from "$shared";
import { createComponentLogger } from "$shared";

import type { createCreateModuleState as CreateModuleStateType } from "../create-module-state.svelte";
import type { PanelCoordinationState } from "../panel-coordination-state.svelte";

type CreateModuleState = ReturnType<typeof CreateModuleStateType>;

// Lazy logger initialization to avoid circular dependency issues
let logger: ReturnType<typeof createComponentLogger> | null = null;
const getLogger = () => {
  if (!logger) {
    logger = createComponentLogger("AutoEditPanelManager");
  }
  return logger;
};

const START_POSITION_BEAT_NUMBER = 0;

export interface AutoEditPanelConfig {
  CreateModuleState: CreateModuleState;
  panelState: PanelCoordinationState;
}

/**
 * Creates auto-open edit panel effect for multi-select
 * @returns Cleanup function
 */
export function createAutoEditPanelEffect(
  config: AutoEditPanelConfig
): () => void {
  const { CreateModuleState, panelState } = config;

  return $effect.root(() => {
    $effect(() => {
      const selectedBeatNumbers =
        CreateModuleState.sequenceState.selectedBeatNumbers;
      const selectedCount = selectedBeatNumbers.size;

      if (selectedCount > 1 && !panelState.isEditPanelOpen) {
        // Map beat numbers to beat data
        const beatNumbersArray = Array.from(selectedBeatNumbers).sort(
          (a, b) => a - b
        );
        const beatsData = beatNumbersArray
          .map((beatNumber): BeatData | PictographData | null | undefined => {
            if (beatNumber === START_POSITION_BEAT_NUMBER) {
              // Beat 0 is the start position
              return CreateModuleState.sequenceState.selectedStartPosition;
            } else {
              // Beats are numbered 1, 2, 3... but stored in array at indices 0, 1, 2...
              const beatIndex = beatNumber - 1;
              return CreateModuleState.sequenceState.currentSequence?.beats[
                beatIndex
              ];
            }
          })
          .filter(
            (beat): beat is BeatData => beat !== null && beat !== undefined
          ); // Remove any null/undefined values

        getLogger().log(
          `Auto-opening batch edit panel: ${selectedCount} beats selected`
        );
        panelState.openBatchEditPanel(beatsData);
      }
    });
  });
}

/**
 * Creates single-beat edit panel effect
 * @returns Cleanup function
 */
export function createSingleBeatEditEffect(
  config: AutoEditPanelConfig
): () => void {
  const { CreateModuleState, panelState } = config;

  return $effect.root(() => {
    $effect(() => {
      const selectedBeatNumber =
        CreateModuleState.sequenceState.selectedBeatNumber;
      const selectedData = CreateModuleState.sequenceState.selectedBeatData;

      // If a beat is selected, open the edit panel
      if (selectedBeatNumber !== null && selectedData) {
        panelState.openEditPanel(selectedBeatNumber, selectedData);
        getLogger().log(`Opening edit panel for beat ${selectedBeatNumber}`);
      }
    });
  });
}

/**
 * PWA Engagement Manager
 *
 * Tracks PWA engagement when user creates a sequence.
 * Consolidates PWA tracking logic from CreateModule.svelte.
 *
 * Domain: Create module - PWA Engagement Tracking
 */

import { createComponentLogger, resolve, TYPES } from "$shared";
import type { IPWAEngagementService } from "$shared/mobile/services/contracts/IPWAEngagementService";

import type { createCreateModuleState as CreateModuleStateType } from "../create-module-state.svelte";

type CreateModuleState = ReturnType<typeof CreateModuleStateType>;

// Lazy logger initialization to avoid circular dependency issues
let logger: ReturnType<typeof createComponentLogger> | null = null;
const getLogger = () => {
  if (!logger) {
    logger = createComponentLogger("PWAEngagementManager");
  }
  return logger;
};

export interface PWAEngagementConfig {
  CreateModuleState: CreateModuleState;
}

/**
 * Creates PWA engagement tracking effect
 * @returns Cleanup function
 */
export function createPWAEngagementEffect(
  config: PWAEngagementConfig
): () => void {
  const { CreateModuleState } = config;
  let hasTrackedSequenceCreation = false;

  return $effect.root(() => {
    $effect(() => {
      if (hasTrackedSequenceCreation) return;
      if (!CreateModuleState.hasSequence()) return;

      try {
        const engagementService = resolve<IPWAEngagementService>(
          TYPES.IPWAEngagementService
        );
        engagementService.recordSequenceCreated();
        engagementService.recordInteraction(); // Also count as interaction
        hasTrackedSequenceCreation = true;
        getLogger().log("PWA engagement: sequence created");
      } catch (error) {
        // Service may not be available, that's ok
      }
    });
  });
}

/**
 * Sequence Beat Operations
 *
 * Handles beat-level operations with animation support:
 * - Adding/removing beats
 * - Updating beat data
 * - Beat insertion
 * - Animated beat removal
 *
 * RESPONSIBILITY: Beat operations coordinator, orchestrates state + services
 */

import type { BeatData, SequenceData } from "$shared";

import type { SequenceAnimationState } from "../animation/SequenceAnimationState.svelte";
import type { SequenceCoreState } from "../core/SequenceCoreState.svelte";
import type { SequenceSelectionState } from "../selection/SequenceSelectionState.svelte";

export interface BeatOperationsConfig {
  coreState: SequenceCoreState;
  selectionState: SequenceSelectionState;
  animationState: SequenceAnimationState;
  onError?: (error: string) => void;
  onSave?: () => Promise<void>;
}

export function createSequenceBeatOperations(config: BeatOperationsConfig) {
  const { coreState, selectionState, animationState, onError, onSave } = config;

  // Helper functions for in-memory sequence manipulation
  function addBeatToSequence(
    sequence: SequenceData,
    beatData?: Partial<BeatData>
  ): SequenceData {
    const newBeat: BeatData = {
      id: beatData?.id ?? crypto.randomUUID(),
      beatNumber: sequence.beats.length + 1,
      isBlank: beatData?.isBlank ?? true,
      duration: beatData?.duration ?? 1,
      blueReversal: beatData?.blueReversal ?? false,
      redReversal: beatData?.redReversal ?? false,
      letter: beatData?.letter ?? null,
      startPosition: beatData?.startPosition ?? null,
      endPosition: beatData?.endPosition ?? null,
      motions: beatData?.motions ?? {},
      ...(beatData ?? {}),
    };
    return { ...sequence, beats: [...sequence.beats, newBeat] };
  }

  function removeBeatFromSequence(
    sequence: SequenceData,
    beatIndex: number
  ): SequenceData {
    if (beatIndex < 0 || beatIndex >= sequence.beats.length) {
      return sequence;
    }
    const newBeats = sequence.beats.filter((_, index) => index !== beatIndex);
    return { ...sequence, beats: newBeats };
  }

  function removeBeatAndSubsequentFromSequence(
    sequence: SequenceData,
    beatIndex: number
  ): SequenceData {
    if (beatIndex < 0 || beatIndex >= sequence.beats.length) {
      return sequence;
    }
    const newBeats = sequence.beats.slice(0, beatIndex);
    return { ...sequence, beats: newBeats };
  }

  function updateBeatInSequence(
    sequence: SequenceData,
    beatIndex: number,
    beatData: Partial<BeatData>
  ): SequenceData {
    if (beatIndex < 0 || beatIndex >= sequence.beats.length) {
      return sequence;
    }
    const newBeats = [...sequence.beats];
    newBeats[beatIndex] = { ...newBeats[beatIndex], ...beatData } as BeatData;
    return { ...sequence, beats: newBeats };
  }

  function insertBeatInSequence(
    sequence: SequenceData,
    beatIndex: number,
    beatData: Partial<BeatData>
  ): SequenceData {
    const newBeat: BeatData = {
      id: beatData.id ?? crypto.randomUUID(),
      beatNumber: beatIndex + 1,
      isBlank: beatData.isBlank ?? true,
      duration: beatData.duration ?? 1,
      blueReversal: beatData.blueReversal ?? false,
      redReversal: beatData.redReversal ?? false,
      letter: beatData.letter ?? null,
      startPosition: beatData.startPosition ?? null,
      endPosition: beatData.endPosition ?? null,
      motions: beatData.motions ?? {},
      ...beatData,
    };
    const newBeats = [
      ...sequence.beats.slice(0, beatIndex),
      newBeat,
      ...sequence.beats.slice(beatIndex),
    ];
    return { ...sequence, beats: newBeats };
  }

  function clearSequenceBeats(sequence: SequenceData): SequenceData {
    return { ...sequence, beats: [] };
  }

  function getSelectedBeat(
    sequence: SequenceData,
    index: number
  ): BeatData | null {
    if (index < 0 || index >= sequence.beats.length) {
      return null;
    }
    return sequence.beats[index] ?? null;
  }

  function handleError(message: string, error?: unknown) {
    const errorMsg = error instanceof Error ? error.message : message;
    coreState.setError(errorMsg);
    onError?.(errorMsg);
    console.error(message, error);
  }

  return {
    addBeat(beatData?: Partial<BeatData>) {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = addBeatToSequence(
          coreState.currentSequence,
          beatData
        );
        coreState.setCurrentSequence(updatedSequence);
        coreState.clearError();
      } catch (error) {
        handleError("Failed to add beat", error);
      }
    },

    removeBeat(beatIndex: number) {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = removeBeatFromSequence(
          coreState.currentSequence,
          beatIndex
        );
        coreState.setCurrentSequence(updatedSequence);
        selectionState.adjustSelectionForRemovedBeat(beatIndex);
        coreState.clearError();
        onSave?.().catch((err) =>
          console.error("Failed to auto-save after beat removal:", err)
        );
      } catch (error) {
        handleError("Failed to remove beat", error);
      }
    },

    removeBeatWithAnimation(beatIndex: number, onComplete?: () => void) {
      if (!coreState.currentSequence) return;

      animationState.startRemovingBeat(beatIndex);

      // Wait for fade animation (updated to match new faster animation: 250ms)
      setTimeout(() => {
        try {
          if (!coreState.currentSequence) return;

          const updatedSequence = removeBeatFromSequence(
            coreState.currentSequence,
            beatIndex
          );
          coreState.setCurrentSequence(updatedSequence);
          selectionState.adjustSelectionForRemovedBeat(beatIndex);
          coreState.clearError();
          onSave?.().catch((err) =>
            console.error("Failed to auto-save after beat removal:", err)
          );

          // Wait for slide animation (updated to match new faster animation: 200ms)
          setTimeout(() => {
            animationState.endRemovingBeat();
            onComplete?.();
          }, 200);
        } catch (error) {
          handleError("Failed to remove beat", error);
          animationState.endRemovingBeat();
        }
      }, 250);
    },

    removeBeatAndSubsequent(beatIndex: number) {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = removeBeatAndSubsequentFromSequence(
          coreState.currentSequence,
          beatIndex
        );
        coreState.setCurrentSequence(updatedSequence);
        selectionState.clearSelection();
        coreState.clearError();
        onSave?.().catch((err) =>
          console.error("Failed to auto-save after beat removal:", err)
        );
      } catch (error) {
        handleError("Failed to remove beat and subsequent beats", error);
      }
    },

    removeBeatAndSubsequentWithAnimation(
      beatIndex: number,
      onComplete?: () => void
    ) {
      if (!coreState.currentSequence) return;

      const beatsToRemove = coreState.currentSequence.beats.length - beatIndex;
      const removingIndices: number[] = [];
      for (let i = beatIndex; i < coreState.currentSequence.beats.length; i++) {
        removingIndices.push(i);
      }

      console.log(
        `üé¨ Starting REVERSE staggered fade-out for ${beatsToRemove} beats starting from index ${beatIndex}`
      );

      const staggerDelay = 200;
      const fadeAnimationDuration = 250; // Updated to match new faster animation
      const reversedIndices = [...removingIndices].reverse();

      // Start animations
      animationState.startRemovingBeats([]);
      reversedIndices.forEach((index, arrayIndex) => {
        setTimeout(() => {
          animationState.addRemovingBeat(index);
          console.log(
            `üé≠ Fading out beat ${index} (${arrayIndex + 1}/${beatsToRemove}) - REVERSE ORDER`
          );
        }, arrayIndex * staggerDelay);
      });

      // Remove beats after animations
      const totalAnimationTime =
        (beatsToRemove - 1) * staggerDelay + fadeAnimationDuration;
      setTimeout(() => {
        try {
          if (!coreState.currentSequence) return;

          const updatedSequence = removeBeatAndSubsequentFromSequence(
            coreState.currentSequence,
            beatIndex
          );
          coreState.setCurrentSequence(updatedSequence);
          selectionState.clearSelection();
          coreState.clearError();
          onSave?.().catch((err) =>
            console.error("Failed to auto-save after beat removal:", err)
          );

          setTimeout(() => {
            animationState.endRemovingBeats();
            console.log(
              `‚úÖ Reverse staggered removal complete for ${beatsToRemove} beats`
            );
            onComplete?.();
          }, 200); // Updated to match new faster animation
        } catch (error) {
          handleError("Failed to remove beat and subsequent beats", error);
          animationState.endRemovingBeats();
        }
      }, totalAnimationTime);
    },

    updateBeat(beatIndex: number, beatData: Partial<BeatData>) {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = updateBeatInSequence(
          coreState.currentSequence,
          beatIndex,
          beatData
        );
        coreState.setCurrentSequence(updatedSequence);
        coreState.clearError();
      } catch (error) {
        handleError("Failed to update beat", error);
      }
    },

    insertBeat(beatIndex: number, beatData?: Partial<BeatData>) {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = insertBeatInSequence(
          coreState.currentSequence,
          beatIndex,
          beatData ?? {}
        );
        coreState.setCurrentSequence(updatedSequence);
        selectionState.adjustSelectionForInsertedBeat(beatIndex);
        coreState.clearError();
      } catch (error) {
        handleError("Failed to insert beat", error);
      }
    },

    clearSequence() {
      if (!coreState.currentSequence) return;

      try {
        const updatedSequence = clearSequenceBeats(coreState.currentSequence);
        coreState.setCurrentSequence(updatedSequence);
        selectionState.clearSelection();
        coreState.clearError();
      } catch (error) {
        handleError("Failed to clear sequence", error);
      }
    },

    getBeat(index: number): BeatData | null {
      if (!coreState.currentSequence) return null;
      return getSelectedBeat(coreState.currentSequence, index);
    },

    getBeatCount(): number {
      return coreState.currentSequence?.beats.length ?? 0;
    },

    hasContent(): boolean {
      return coreState.currentSequence?.beats.some((b) => !b.isBlank) ?? false;
    },
  };
}

export type SequenceBeatOperations = ReturnType<
  typeof createSequenceBeatOperations
>;

/**
 * Create Module Panel Coordination State Factory
