    get isAnyPanelOpen() {
      return (
        isEditPanelOpen ||
        isAnimationPanelOpen ||
        isSharePanelOpen ||
        isFilterPanelOpen ||
        isSequenceActionsPanelOpen ||
        isCAPPanelOpen
      );
    },
  };
}

