    console.warn("rotateSequence not yet implemented");
    return sequence;
  }

  /**
   * Clear all beats in a sequence, making them blank
   */
  clearSequence(sequence: SequenceData): SequenceData {
    const clearedBeats = sequence.beats.map((beat) => ({
      ...beat,
      isBlank: true,
      pictographData: null,
      blueReversal: false,
      redReversal: false,
    }));

    return updateSequenceData(sequence, {
      beats: clearedBeats,
    });
  }

  /**
   * Duplicate a sequence with a new ID and optional new name
   */
  duplicateSequence(sequence: SequenceData, newName?: string): SequenceData {
    return createSequenceData({
      ...sequence,
      id: crypto.randomUUID(),
      name: newName ?? `${sequence.name} (Copy)`,
      beats: sequence.beats.map((beat) => ({
        ...beat,
        id: crypto.randomUUID(),
      })),
    });
  }
}

/**
 * Explore Metadata Extractor Service
 *
 * Extracts metadata from sequence files (PNG, WebP, or JSON sidecars)
 * with proper error handling and type safety.
 */

import { inject, injectable } from "inversify";

import type { BeatData } from "$shared";
import {
  createMotionData,
  GridLocation,
  GridMode,
  MotionColor,
  MotionType,
  Orientation,
  RotationDirection,
} from "$shared";
import { TYPES } from "$shared/inversify/types";
import { PropType } from "$shared/pictograph/prop/domain/enums/PropType";
import { UniversalMetadataExtractor } from "$shared/services/UniversalMetadataExtractor";

import type {
  IExploreMetadataExtractor,
  SequenceMetadata,
} from "../contracts/IExploreMetadataExtractor";
import type { ISequenceDifficultyCalculator } from "../contracts/ISequenceDifficultyCalculator";
// Constants for metadata extraction
// Using function to avoid module-level enum reference (fixes test initialization)
const getDefaultMetadata = (): SequenceMetadata => ({
  beats: [],
  author: "Unknown",
  difficultyLevel: "beginner",
  dateAdded: new Date(),
  gridMode: GridMode.DIAMOND,
  isCircular: false,
  propType: "Staff" as PropType,
  sequenceLength: 0,
  startingPosition: "alpha",
});

const DATE_FIELD_NAMES = [
  "date_added",
  "dateAdded",
  "date",
  "created_date",
  "timestamp",
] as const;

@injectable()
export class ExploreMetadataExtractor implements IExploreMetadataExtractor {
  constructor(
    @inject(TYPES.ISequenceDifficultyCalculator)
    private readonly difficultyCalculator: ISequenceDifficultyCalculator
  ) {}

  async extractMetadata(
    sequenceName: string,
    thumbnailPath?: string
  ): Promise<SequenceMetadata> {
    try {
      const sequenceWithVersion = this.determineSequenceVersion(
        sequenceName,
        thumbnailPath
      );

      const result =
        await UniversalMetadataExtractor.extractMetadata(sequenceWithVersion);

      if (!result.success || !result.data) {
        return getDefaultMetadata();
      }

      return this.parseMetadataResult(sequenceName, result.data);
    } catch (error) {
      const errorMessage =
        error instanceof Error ? error.message : String(error);

      // Only log non-routine errors
      if (!errorMessage.includes("No valid version found")) {
        console.warn(
          `‚ö†Ô∏è Failed to extract metadata for ${sequenceName}:`,
          error
        );
      }

      throw error;
    }
  }

  /**
   * Determine the sequence version from thumbnail path or default to ver1
   */
  private determineSequenceVersion(
    sequenceName: string,
    thumbnailPath?: string
  ): string {
    if (!thumbnailPath) {
      return `${sequenceName}_ver1`;
    }

    const versionMatch = thumbnailPath.match(/_ver(\d+)\.webp$/);
    if (versionMatch) {
      const version = versionMatch[1];
      return `${sequenceName}_ver${version}`;
    }

    return `${sequenceName}_ver1`;
  }

  /**
   * Parse raw metadata into typed SequenceMetadata
   */
  private parseMetadataResult(
    sequenceName: string,
    rawData: Record<string, unknown>
  ): SequenceMetadata {
    const beats = this.parseBeats(sequenceName, rawData["sequence"]);
    const gridMode = this.parseGridMode(rawData["grid_mode"]);

    // Calculate difficulty from actual sequence data instead of stored level
    const difficultyLevel = this.calculateDifficultyLevel(beats);

    const dateAdded = this.parseDateAdded(rawData);
    const startingPosition = this.parseStartingPosition(beats);

    return {
      beats,
      author: String(rawData["author"] || "Unknown"),
      difficultyLevel,
      dateAdded,
      gridMode,
      isCircular: Boolean(rawData["is_circular"]),
      propType: String(rawData["prop_type"] || "Staff") as PropType,
      sequenceLength: beats.length,
      startingPosition,
    };
  }

  /**
   * Parse beat data from sequence array with full motion parsing
   */
  private parseBeats(sequenceName: string, sequence: unknown): BeatData[] {
    if (!Array.isArray(sequence)) {
      return [];
    }

    return sequence.map((step: unknown, index: number) => {
      const stepData = step as Record<string, unknown>;
      const blueAttrs = stepData["blue_attributes"] as Record<string, unknown> | undefined;
      const redAttrs = stepData["red_attributes"] as Record<string, unknown> | undefined;

      return {
        // PictographData properties
        id: `beat-${sequenceName}-${index + 1}`,
        letter: String(stepData["letter"] || ""),
        startPosition: null,
        endPosition: null,
        motions: {
          [MotionColor.BLUE]:
            blueAttrs
              ? createMotionData({
                color: MotionColor.BLUE,
                motionType: this.parseMotionType(blueAttrs["motion_type"]),
                startLocation: this.parseLocation(blueAttrs["start_loc"]),
                endLocation: this.parseLocation(blueAttrs["end_loc"]),
                startOrientation: this.parseOrientation(blueAttrs["start_ori"]),
                endOrientation: this.parseOrientation(blueAttrs["end_ori"]),
                rotationDirection: this.parseRotationDirection(
                  blueAttrs["prop_rot_dir"]
                ),
                turns: this.parseTurns(blueAttrs["turns"]),
                isVisible: true,
                propType: PropType.STAFF,
                arrowLocation: this.parseLocation(blueAttrs["start_loc"]),
                gridMode: GridMode.DIAMOND,
              })
              : undefined,
          [MotionColor.RED]:
            redAttrs
              ? createMotionData({
                color: MotionColor.RED,
                motionType: this.parseMotionType(redAttrs["motion_type"]),
                startLocation: this.parseLocation(redAttrs["start_loc"]),
                endLocation: this.parseLocation(redAttrs["end_loc"]),
                startOrientation: this.parseOrientation(redAttrs["start_ori"]),
                endOrientation: this.parseOrientation(redAttrs["end_ori"]),
                rotationDirection: this.parseRotationDirection(
                  redAttrs["prop_rot_dir"]
                ),
                turns: this.parseTurns(redAttrs["turns"]),
                isVisible: true,
                propType: PropType.STAFF,
                arrowLocation: this.parseLocation(redAttrs["start_loc"]),
                gridMode: GridMode.DIAMOND,
              })
            : undefined,
        },
        // Beat context properties
        beatNumber: Number(stepData["beat"] || index + 1),
        duration: 1.0,
        blueReversal: false,
        redReversal: false,
        isBlank: false,
      } as BeatData;
    });
  }

  /**
   * Parse grid mode with fallback
   */
  private parseGridMode(gridModeValue: unknown): GridMode {
    if (gridModeValue === "diamond") {
      return GridMode.DIAMOND;
    }
    if (gridModeValue === "box") {
      return GridMode.BOX;
    }
    return GridMode.DIAMOND;
  }

  /**
   * Calculate difficulty level from actual beat data
   * Replaces the old parseDifficultyLevel that just read a stored value
   */
  private calculateDifficultyLevel(beats: BeatData[]): string {
    const numericLevel =
      this.difficultyCalculator.calculateDifficultyLevel(beats);
    return this.difficultyCalculator.levelToString(numericLevel);
  }

  /**
   * Parse motion type with fallback
   */
  private parseMotionType(value: unknown): MotionType {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "pro":
        return MotionType.PRO;
      case "anti":
        return MotionType.ANTI;
      case "float":
        return MotionType.FLOAT;
      case "dash":
        return MotionType.DASH;
      case "static":
        return MotionType.STATIC;
      default:
        return MotionType.STATIC;
    }
  }

  /**
   * Parse grid location with fallback
   */
  private parseLocation(value: unknown): GridLocation {
    const str = String(value || "").toUpperCase();
    // Map common location strings to GridLocation enum values
    const locationMap: Record<string, GridLocation> = {
      N: GridLocation.NORTH,
      NORTH: GridLocation.NORTH,
      E: GridLocation.EAST,
      EAST: GridLocation.EAST,
      S: GridLocation.SOUTH,
      SOUTH: GridLocation.SOUTH,
      W: GridLocation.WEST,
      WEST: GridLocation.WEST,
      NE: GridLocation.NORTHEAST,
      NORTHEAST: GridLocation.NORTHEAST,
      SE: GridLocation.SOUTHEAST,
      SOUTHEAST: GridLocation.SOUTHEAST,
      SW: GridLocation.SOUTHWEST,
      SOUTHWEST: GridLocation.SOUTHWEST,
      NW: GridLocation.NORTHWEST,
      NORTHWEST: GridLocation.NORTHWEST,
    };
    return locationMap[str] ?? GridLocation.NORTH;
  }

  /**
   * Parse orientation with fallback
   */
  private parseOrientation(value: unknown): Orientation {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "in":
        return Orientation.IN;
      case "out":
        return Orientation.OUT;
      case "clock":
      case "clockwise":
        return Orientation.CLOCK;
      case "counter":
      case "counterclockwise":
        return Orientation.COUNTER;
      default:
        return Orientation.IN;
    }
  }

  /**
   * Parse rotation direction with fallback
   */
  private parseRotationDirection(value: unknown): RotationDirection {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "cw":
      case "clockwise":
        return RotationDirection.CLOCKWISE;
      case "ccw":
      case "counterclockwise":
      case "counter_clockwise":
        return RotationDirection.COUNTER_CLOCKWISE;
      case "no_rotation":
      case "norotation":
        return RotationDirection.NO_ROTATION;
      default:
        return RotationDirection.NO_ROTATION;
    }
  }

  /**
   * Parse turns value (can be number or "fl" for float)
   */
  private parseTurns(value: unknown): number | "fl" {
    if (value === "fl" || value === "float") {
      return "fl";
    }
    const num = Number(value);
    return isNaN(num) ? 0 : num;
  }

  /**
   * Parse date from various possible field names
   */
  private parseDateAdded(rawData: Record<string, unknown>): Date {
    for (const fieldName of DATE_FIELD_NAMES) {
      const fieldValue = rawData[fieldName];
      if (fieldValue) {
        try {
          const date = new Date(String(fieldValue));
          if (!isNaN(date.getTime())) {
            return date;
          }
        } catch {
          // Continue to next field
        }
      }
    }

    return new Date();
  }

  /**
   * Extract starting position from first beat
   */
  private parseStartingPosition(beats: BeatData[]): string {
    if (beats.length > 0) {
      const firstLetter = beats[0]?.letter;
      if (firstLetter) {
        return firstLetter;
      }
    }
    return "alpha";
  }
}

/**
 * Explore Sort Service
 *
 * Handles sorting and grouping of gallery sequences.
 * Provides consistent sorting behavior across the gallery.
 */

import { injectable } from "inversify";

import type { SequenceData } from "$shared";

import { ExploreSortMethod } from "../../../../shared/domain/enums";
import type { IExploreSortService } from "../contracts/IExploreSortService";

@injectable()
export class ExploreSortService implements IExploreSortService {
  sortSequences(
    sequences: SequenceData[],
    sortMethod: ExploreSortMethod
  ): SequenceData[] {
    const sorted = [...sequences];

    switch (sortMethod) {
      case ExploreSortMethod.ALPHABETICAL:
        return this.sortAlphabetically(sorted);
      case ExploreSortMethod.DATE_ADDED:
        return this.sortByDateAdded(sorted);
      case ExploreSortMethod.DIFFICULTY_LEVEL:
        return this.sortByDifficulty(sorted);
      case ExploreSortMethod.SEQUENCE_LENGTH:
        return this.sortByLength(sorted);
      case ExploreSortMethod.AUTHOR:
        return this.sortByAuthor(sorted);
      case ExploreSortMethod.POPULARITY:
        return this.sortByPopularity(sorted);
      default:
        return sorted;
    }
  }

  groupSequencesIntoSections(
    sequences: SequenceData[],
    sortMethod: ExploreSortMethod
  ): Record<string, SequenceData[]> {
    const sections: Record<string, SequenceData[]> = {};

    for (const sequence of sequences) {
      const sectionKey = this.getSectionKey(sequence, sortMethod);

      if (!sections[sectionKey]) {
        sections[sectionKey] = [];
      }

      sections[sectionKey].push(sequence);
    }

    return sections;
  }

  // ============================================================================
  // Sorting Methods
  // ============================================================================

  private sortAlphabetically(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort((a, b) => {
      // First compare by first letter
      const letterCompare = (a.word[0]?.toUpperCase() ?? "").localeCompare(
        b.word[0]?.toUpperCase() ?? ""
      );

      // If same letter, sort by sequence length (ascending)
      if (letterCompare === 0) {
        return (a.sequenceLength ?? 0) - (b.sequenceLength ?? 0);
      }

      return letterCompare;
    });
  }

  private sortByDateAdded(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort((a, b) => {
      const dateA = a.dateAdded ?? new Date(0);
      const dateB = b.dateAdded ?? new Date(0);
      return dateB.getTime() - dateA.getTime(); // Newest first
    });
  }

  private sortByDifficulty(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort((a, b) => {
      const levelA = this.getDifficultyOrder(a.difficultyLevel);
      const levelB = this.getDifficultyOrder(b.difficultyLevel);
      return levelA - levelB; // Easiest first
    });
  }

  private sortByLength(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort(
      (a, b) => (a.sequenceLength ?? 0) - (b.sequenceLength ?? 0)
    );
  }

  private sortByAuthor(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort((a, b) =>
      (a.author ?? "").localeCompare(b.author ?? "")
    );
  }

  private sortByPopularity(sequences: SequenceData[]): SequenceData[] {
    return sequences.sort(
      (a, b) => Number(b.isFavorite) - Number(a.isFavorite)
    );
  }

  // ============================================================================
  // Section Key Generation
  // ============================================================================

  private getSectionKey(
    sequence: SequenceData,
    sortMethod: ExploreSortMethod
  ): string {
    switch (sortMethod) {
      case ExploreSortMethod.ALPHABETICAL:
        return this.getAlphabeticalSection(sequence);
      case ExploreSortMethod.DIFFICULTY_LEVEL:
        return sequence.difficultyLevel ?? "Unknown";
      case ExploreSortMethod.AUTHOR:
        return sequence.author ?? "Unknown";
      case ExploreSortMethod.SEQUENCE_LENGTH:
        return this.getLengthSection(sequence);
      default:
        return "All";
    }
  }

  private getAlphabeticalSection(sequence: SequenceData): string {
    return sequence.word[0]?.toUpperCase() ?? "#";
  }

  private getLengthSection(sequence: SequenceData): string {
    const length = sequence.sequenceLength ?? 0;

    if (length <= 4) return "3-4 beats";
    if (length <= 6) return "5-6 beats";
    if (length <= 8) return "7-8 beats";
    return "9+ beats";
  }

  // ============================================================================
  // Helper Methods
  // ============================================================================

  private getDifficultyOrder(difficulty?: string): number {
    switch (difficulty) {
      case "beginner":
        return 1;
      case "intermediate":
        return 2;
      case "advanced":
        return 3;
      default:
        return 0;
    }
  }
}

