        </svg>
    `;

  // Use encodeURIComponent instead of btoa to handle Unicode characters
  return `data:image/svg+xml;charset=utf-8,${encodeURIComponent(svg)}`;
}

/**
 * Generate thumbnail placeholder for act.
 */
export function generateActThumbnail(act: ActData): string {
  const sequenceCount = act.sequences.length;

  // Escape the act name to prevent XML issues
  const escapedName = act.name.replace(/[<>&"']/g, (char) => {
    switch (char) {
      case "<":
        return "&lt;";
      case ">":
        return "&gt;";
      case "&":
        return "&amp;";
      case '"':
        return "&quot;";
      case "'":
        return "&#39;";
      default:
        return char;
    }
  });

  const svg = `
        <svg width="160" height="120" xmlns="http://www.w3.org/2000/svg">
            <rect width="160" height="120" fill="rgba(40,40,50,0.8)" stroke="rgba(80,80,100,0.5)" stroke-width="2" rx="8"/>
            <text x="80" y="50" text-anchor="middle" fill="white" font-family="Arial" font-size="32">üìÑ</text>
            <text x="80" y="75" text-anchor="middle" fill="rgba(255,255,255,0.9)" font-family="Arial" font-size="12">${escapedName}</text>
            <text x="80" y="95" text-anchor="middle" fill="rgba(255,255,255,0.7)" font-family="Arial" font-size="10">${sequenceCount} sequence${sequenceCount !== 1 ? "s" : ""}</text>
            ${act.musicFile ? '<text x="80" y="110" text-anchor="middle" fill="rgba(100,200,100,0.9)" font-family="Arial" font-size="10">‚ô™ Music</text>' : ""}
        </svg>
    `;

  // Use encodeURIComponent instead of btoa to handle Unicode characters
  return `data:image/svg+xml;charset=utf-8,${encodeURIComponent(svg)}`;
}

/**
 * Page Image Export Service Implementation
 *
 * Handles exporting page elements as images using html2canvas.
 * Supports PNG, JPEG, and WebP formats with quality and scaling options.
 */

import { injectable } from "inversify";

import type {
  ExportResult,
  Html2CanvasFunction,
  WindowWithHtml2Canvas,
} from "$shared";

import type {
  ExportProgress,
  SequenceExportOptions,
} from "../../../../shared/render";
import type { Page } from "../../domain/models/PageLayout";
import type {
  BatchExportResult,
  WordCardExportOptions,
  WordCardExportResultWithMetadata,
} from "../../domain/models/word-card-export";
import type { IPageImageExportService } from "../contracts";

@injectable()
export class PageImageExportService implements IPageImageExportService {
  exportPage(_page: Page, _options: WordCardExportOptions): Promise<Blob> {
    throw new Error("Method not implemented.");
  }
  exportPages(
    _pages: Page[],
    _options: WordCardExportOptions
  ): Promise<Blob[]> {
    throw new Error("Method not implemented.");
  }
  private abortController: AbortController | null = null;
  private currentProgress: ExportProgress | null = null;

  async exportPageAsImage(
    pageElement: HTMLElement,
    options: SequenceExportOptions
  ): Promise<ExportResult> {
    const startTime = performance.now();

    try {
      // Validate options
      const validation = this.validateExportOptions(options);
      if (!validation) {
        throw new Error(`Invalid export options`);
      }

      // Dynamic import of html2canvas (only load when needed)
      const html2canvas = await this.loadHtml2Canvas();

      // Prepare element for capture
      const preparedElement = this.prepareElementForCapture(pageElement);

      // Calculate dimensions
      const dimensions = this.calculateCaptureDimensions(
        preparedElement,
        options
      );

      // Capture the element
      const canvas = await html2canvas(preparedElement, {
        width: dimensions.width,
        height: dimensions.height,
        scale: options.scale,
        backgroundColor: options.backgroundColor ?? "#ffffff",
        useCORS: true,
        allowTaint: false,
        removeContainer: true,
        logging: false,
        // High quality rendering
        scrollX: 0,
        scrollY: 0,
        windowWidth: dimensions.width,
        windowHeight: dimensions.height,
      });

      // Convert canvas to blob
      const blob = await this.canvasToBlob(canvas, options);

      const processingTime = performance.now() - startTime;

      return {
        success: true,
        data: blob,
        filename: "page-export.png",
        metadata: {
          format: options.format,
          size: blob.size,
          dimensions: { width: canvas.width, height: canvas.height },
          beatCount: 0,
          processingTime,
        },
      };
    } catch (error) {
      const processingTime = performance.now() - startTime;

      return {
        success: false,
        filename: "page-export.png",
        error: error instanceof Error ? error.message : String(error),
        metadata: {
          format: options.format,
          size: 0,
          dimensions: { width: 0, height: 0 },
          beatCount: 0,
          processingTime,
        },
      };
    }
  }

  async exportPagesAsImages(
    pageElements: HTMLElement[],
    options: SequenceExportOptions,
    onProgress?: (progress: ExportProgress) => void
  ): Promise<BatchExportResult> {
    const startTime = performance.now();
    const results: ExportResult[] = [];
    const errors: Error[] = [];
    let successCount = 0;
    let failureCount = 0;

    // Create abort controller for cancellation
    this.abortController = new AbortController();

    try {
      // Load html2canvas once for batch operation
      const html2canvas = await this.loadHtml2Canvas();

      for (let i = 0; i < pageElements.length; i++) {
        // Check for cancellation
        if (this.abortController.signal.aborted) {
          break;
        }

        // Update progress
        const progress: ExportProgress = {
          stage: "share",
          progress: ((i + 1) / pageElements.length) * 100,
          message: `Exporting page ${i + 1} of ${pageElements.length}`,
        };

        this.currentProgress = progress;
        onProgress?.(progress);

        try {
          const pageElement = pageElements[i];
          if (!pageElement) continue;
          const result = await this.exportSinglePageWithCanvas(
            pageElement,
            html2canvas,
            options,
            i + 1
          );

          results.push(result);

          if (result.success) {
            successCount++;
          } else {
            failureCount++;
            if (result.error) {
              errors.push(new Error(result.error));
            }
          }
        } catch (error) {
          failureCount++;
          const err = error as Error;
          errors.push(err);

          results.push({
            success: false,
            filename: `page-${i + 1}.png`,
            error: err instanceof Error ? err.message : String(err),
            metadata: {
              format: options.format,
              size: 0,
              dimensions: { width: 0, height: 0 },
              beatCount: 0,
              processingTime: 0,
            },
          });
        }

        // Brief pause to allow UI updates and prevent blocking
        await this.pauseForUI();
      }
    } catch (error) {
      errors.push(error as Error);
    } finally {
      this.abortController = null;
      this.currentProgress = null;
    }

    const totalProcessingTime = performance.now() - startTime;

    // Map ExportResult[] to WordCardExportResultWithMetadata[]
    const mappedResults: WordCardExportResultWithMetadata[] = results.map(
      (result, index) => {
        // Extract error separately and convert string to Error object
        const { error, ...restResult } = result;

        const baseResult: WordCardExportResultWithMetadata = {
          ...restResult,
          sequenceId: `page-${index}`, // Add required sequenceId property
          success: result.success,
        };

        // Only add error if it exists, and ensure it's an Error object
        if (error) {
          baseResult.error =
            typeof error === "string" ? new Error(error) : error;
        }

        return baseResult;
      }
    );

    return {
      success: errors.length === 0,
      totalPages: pageElements.length,
      successCount,
      failureCount,
      results: mappedResults,
      totalProcessingTime,
      errors,
    };
  }

  calculateOptimalDimensions(
    pageElement: HTMLElement,
    targetDPI: number
  ): { width: number; height: number } {
    const rect = pageElement.getBoundingClientRect();
    const scaleFactor = targetDPI / 96; // 96 DPI is standard screen DPI

    return {
      width: Math.round(rect.width * scaleFactor),
      height: Math.round(rect.height * scaleFactor),
    };
  }

  validateExportOptions(options: SequenceExportOptions): boolean {
    // Validate format
    if (!["PNG", "JPEG", "WebP"].includes(options.format)) {
      return false;
    }

    // Validate quality
    if (options.quality < 0 || options.quality > 1) {
      return false;
    }

    // Validate scale
    if (options.scale <= 0) {
      return false;
    }

    // Validate dimensions if provided
    if (options.width && options.width <= 0) {
      return false;
    }

    if (options.height && options.height <= 0) {
      return false;
    }

    return true;
  }

  getSupportedFormats(): string[] {
    return ["PNG", "JPEG", "WebP"];
  }

  getRecommendedSettings(
    useCase: "print" | "web" | "archive"
  ): SequenceExportOptions {
    const baseOptions: SequenceExportOptions = {
      // Core export settings
      includeStartPosition: true,
      addBeatNumbers: true,
      addReversalSymbols: true,
      addUserInfo: true,
      addWord: true,
      combinedGrids: false,
      addDifficultyLevel: true,

      // Scaling and sizing
      beatScale: 1.0,
      beatSize: 100,
      margin: 20,

      // Visibility settings
      redVisible: true,
      blueVisible: true,

      // User information
      userName: "TKA Studio User",
      exportDate: new Date().toISOString().split("T")[0] ?? "",
      notes: "Exported from TKA",

      // Output format (will be overridden)
      format: "PNG",
      quality: 0.9,
      scale: 1.0,
      backgroundColor: "#ffffff",
    };

    switch (useCase) {
      case "print":
        return {
          ...baseOptions,
          format: "PNG" as const,
          scale: 2.0,
          quality: 1.0,
        };
      case "web":
        return {
          ...baseOptions,
          format: "JPEG" as const,
          scale: 1.0,
          quality: 0.8,
        };
      case "archive":
        return {
          ...baseOptions,
          format: "PNG" as const,
          scale: 1.5,
          quality: 1.0,
        };
      default:
        return {
          ...baseOptions,
          format: "PNG" as const,
        };
    }
  }

  // Cancel ongoing export
  cancelExport(): void {
    if (this.abortController) {
      this.abortController.abort();
    }
  }

  // Get current progress
  getCurrentProgress(): ExportProgress | null {
    return this.currentProgress;
  }

  // Private helper methods

  private async loadHtml2Canvas(): Promise<Html2CanvasFunction> {
    try {
      // Check if html2canvas is already loaded
      const windowAny = window as { html2canvas?: Html2CanvasFunction };

      if (!windowAny.html2canvas) {
        // Dynamically load html2canvas from CDN
        const script = document.createElement("script");
        script.src =
          "https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js";

        await new Promise((resolve, reject) => {
          script.onload = resolve;
          script.onerror = reject;
          document.head.appendChild(script);
        });
      }

      // Access html2canvas after it's been loaded
      const html2canvas = (window as { html2canvas?: Html2CanvasFunction }).html2canvas;
      if (!html2canvas) {
        throw new Error("html2canvas failed to load");
      }

      return html2canvas;
    } catch (error) {
      throw new Error(
        "Failed to load html2canvas library. Check internet connection."
      );
    }
  }

  private prepareElementForCapture(element: HTMLElement): HTMLElement {
    // Clone the element to avoid modifying the original
    const clonedElement = element.cloneNode(true) as HTMLElement;

    // Apply capture-specific styles
    clonedElement.style.position = "static";
    clonedElement.style.transform = "none";
    clonedElement.style.margin = "0";
    clonedElement.style.padding = "0";

    // Ensure all images are loaded
    const images = clonedElement.querySelectorAll("img");
    images.forEach((img) => {
      if (!img.complete) {
        // Replace with placeholder if image not loaded
        img.src =
          "data:image/svg+xml;base64,";
      }
    });

    return clonedElement;
  }

  private calculateCaptureDimensions(
    element: HTMLElement,
    options: SequenceExportOptions
  ): { width: number; height: number } {
    const rect = element.getBoundingClientRect();

    if (options.width && options.height) {
      return { width: options.width, height: options.height };
    }

    if (options.width) {
      const aspectRatio = rect.height / rect.width;
      return { width: options.width, height: options.width * aspectRatio };
    }

    if (options.height) {
      const aspectRatio = rect.width / rect.height;
      return { width: options.height * aspectRatio, height: options.height };
    }

    return { width: rect.width, height: rect.height };
  }

  private async canvasToBlob(
    canvas: HTMLCanvasElement,
    options: SequenceExportOptions
  ): Promise<Blob> {
    return new Promise((resolve, reject) => {
      const mimeType = this.getMimeType(options.format);
      const quality = options.format === "PNG" ? undefined : options.quality;

      canvas.toBlob(
        (blob) => {
          if (blob) {
            resolve(blob);
          } else {
            reject(new Error("Failed to create blob from canvas"));
          }
        },
        mimeType,
        quality
      );
    });
  }

  private getMimeType(format: string): string {
    switch (format) {
      case "PNG":
        return "image/png";
      case "JPEG":
        return "image/jpeg";
      case "WebP":
        return "image/webp";
      default:
        return "image/png";
    }
  }

  private async exportSinglePageWithCanvas(
    pageElement: HTMLElement,
    html2canvas: Html2CanvasFunction,
    options: SequenceExportOptions,
    pageNumber: number
  ): Promise<ExportResult> {
    const startTime = performance.now();

    try {
      const preparedElement = this.prepareElementForCapture(pageElement);
      const dimensions = this.calculateCaptureDimensions(
        preparedElement,
        options
      );

      const canvas = await html2canvas(preparedElement, {
        width: dimensions.width,
        height: dimensions.height,
        scale: options.scale,
        backgroundColor: options.backgroundColor ?? "#ffffff",
        useCORS: true,
        allowTaint: false,
        removeContainer: true,
        logging: false,
        scrollX: 0,
        scrollY: 0,
        windowWidth: dimensions.width,
        windowHeight: dimensions.height,
      });

      const blob = await this.canvasToBlob(canvas, options);
      const processingTime = performance.now() - startTime;

      return {
        success: true,
        data: blob,
        filename: `page-${pageNumber}.png`,
        metadata: {
          format: "PNG",
          size: blob.size,
          dimensions: { width: canvas.width, height: canvas.height },
          beatCount: 0,
          processingTime,
        },
      };
    } catch (error) {
      const processingTime = performance.now() - startTime;

      return {
        success: false,
        filename: `page-${pageNumber}.png`,
        error: error instanceof Error ? error.message : String(error),
        metadata: {
          format: "PNG",
          size: 0,
          dimensions: { width: 0, height: 0 },
          beatCount: 0,
          processingTime,
        },
      };
    }
  }

  private async pauseForUI(): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, 10));
  }
}

/**
 * ActService - Implementation for Write tab Act operations
 */
import { injectable } from "inversify";

import type { ActSummary, IActService } from "../contracts/IActService";

@injectable()
export class ActService implements IActService {
  private acts: Map<string, ActSummary> = new Map();
  private initialized = false;

  constructor() {
    console.log("üé≠ ActService initialized");
  }

  initialize(): void {
    if (this.initialized) return;

    console.log("üé≠ ActService: Initializing...");

    // TODO: Load acts from persistent storage
    // For now, create some sample acts
    const sampleActs: ActSummary[] = [
      {
        id: "act-1",
        name: "Sample Act 1",
        description: "A sample act for testing",
        sequences: [],
      },
      {
        id: "act-2",
        name: "Sample Act 2",
        description: "Another sample act",
        sequences: [],
      },
    ];

    for (const act of sampleActs) {
      this.acts.set(act.id, act);
    }

    this.initialized = true;
    console.log("‚úÖ ActService initialized with", this.acts.size, "acts");
  }

  cleanup(): void {
    console.log("üé≠ ActService: Cleaning up...");
    this.acts.clear();
    this.initialized = false;
  }

  async loadAct(idOrPath: string): Promise<ActSummary | null> {
    await this.ensureInitialized();

    const act = this.acts.get(idOrPath);
    if (act) {
      console.log("üé≠ ActService: Loaded act", act.name);
      return { ...act }; // Return a copy
    }

    console.warn("üé≠ ActService: Act not found:", idOrPath);
    return null;
  }

  async getAllActs(): Promise<ActSummary[]> {
    await this.ensureInitialized();

    const acts = Array.from(this.acts.values()).map((act) => ({ ...act }));
    console.log("üé≠ ActService: Retrieved", acts.length, "acts");
    return acts;
  }

  async addSequenceToAct(actId: string, sequenceId: string) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    if (!act.sequences) {
      act.sequences = [];
    }

    if (!act.sequences.includes(sequenceId)) {
      act.sequences.push(sequenceId);
      console.log("üé≠ ActService: Added sequence", sequenceId, "to act", actId);
    }
  }

  async removeSequenceFromAct(
    actId: string,
    sequenceId: string
  ) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    if (act.sequences) {
      const index = act.sequences.indexOf(sequenceId);
      if (index > -1) {
        act.sequences.splice(index, 1);
        console.log(
          "üé≠ ActService: Removed sequence",
          sequenceId,
          "from act",
          actId
        );
      }
    }
  }

  async exportAct(actId: string) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    // TODO: Implement export functionality
    console.log("üé≠ ActService: Exporting act", act.name);
    console.warn("üöß ActService: Export functionality not yet implemented");
  }

  private async ensureInitialized(): Promise<void> {
    if (!this.initialized) {
      this.initialize();
    }
  }
}

/**
 * MusicPlayerService - Implementation for music playback in Write tab
 */
import { injectable } from "inversify";

import type { IMusicPlayerService } from "../contracts/IMusicPlayerService";

@injectable()
export class MusicPlayerService implements IMusicPlayerService {
  private audioContext: AudioContext | null = null;
  private currentAudio: HTMLAudioElement | null = null;
  private initialized = false;

  constructor() {
    console.log("üéµ MusicPlayerService initialized");
  }

  async initialize(): Promise<void> {
    if (this.initialized) return;

    console.log("üéµ MusicPlayerService: Initializing audio context...");

    try {
      // Initialize Web Audio API context
      this.audioContext = new AudioContext();

      // Resume audio context if suspended (required for user interaction)
      if (this.audioContext.state === "suspended") {
        await this.audioContext.resume();
      }

      this.initialized = true;
      console.log("‚úÖ MusicPlayerService initialized with audio context");
    } catch (error) {
      console.error(
        "‚ùå MusicPlayerService: Failed to initialize audio context:",
        error
      );
      // Fallback to basic HTML audio without Web Audio API
      this.initialized = true;
      console.log("‚ö†Ô∏è MusicPlayerService: Fallback to basic HTML audio");
    }
  }

  cleanup(): void {
    console.log("üéµ MusicPlayerService: Cleaning up...");

    if (this.currentAudio) {
      this.currentAudio.pause();
      this.currentAudio.removeEventListener(
        "loadedmetadata",
        this.handleLoadedMetadata
      );
      this.currentAudio.removeEventListener(
        "timeupdate",
        this.handleTimeUpdate
      );
      this.currentAudio.removeEventListener("ended", this.handleEnded);
      this.currentAudio.removeEventListener("error", this.handleError);
      this.currentAudio = null;
    }

    if (this.audioContext?.state !== "closed") {
      void this.audioContext?.close();
      this.audioContext = null;
    }

    this.initialized = false;
  }

  async play(track: string): Promise<void> {
    await this.ensureInitialized();

    console.log("üéµ MusicPlayerService: Playing track:", track);

    try {
      // Stop current audio if playing
      if (this.currentAudio) {
        this.currentAudio.pause();
      }

      // Create new audio element
      this.currentAudio = new Audio(track);
      this.setupAudioEventListeners();

      // Start playback
      await this.currentAudio.play();
      console.log("‚úÖ MusicPlayerService: Track started playing");
    } catch (error) {
      console.error("‚ùå MusicPlayerService: Failed to play track:", error);
      throw new Error(`Failed to play track: ${track}`);
    }
  }

  pause(): void {
    if (!this.currentAudio) {
      console.warn("üéµ MusicPlayerService: No audio to pause");
      return;
    }

    this.currentAudio.pause();
    console.log("‚è∏Ô∏è MusicPlayerService: Playback paused");
  }

  stop(): void {
    if (!this.currentAudio) {
      console.warn("üéµ MusicPlayerService: No audio to stop");
      return;
    }

    this.currentAudio.pause();
    this.currentAudio.currentTime = 0;
    console.log("‚èπÔ∏è MusicPlayerService: Playback stopped");
  }

  private async ensureInitialized(): Promise<void> {
    if (!this.initialized) {
      await this.initialize();
    }
  }

  private setupAudioEventListeners(): void {
    if (!this.currentAudio) return;

    this.currentAudio.addEventListener(
      "loadedmetadata",
      this.handleLoadedMetadata
    );
    this.currentAudio.addEventListener("timeupdate", this.handleTimeUpdate);
    this.currentAudio.addEventListener("ended", this.handleEnded);
    this.currentAudio.addEventListener("error", this.handleError);
  }

  private handleLoadedMetadata = (): void => {
    if (this.currentAudio) {
      console.log(
        "üéµ MusicPlayerService: Audio metadata loaded, duration:",
        this.currentAudio.duration
      );
    }
  };

  private handleTimeUpdate = (): void => {
    if (this.currentAudio) {
      // Could emit events here for UI updates
      // console.log("üéµ Time update:", this.currentAudio.currentTime);
    }
  };

  private handleEnded = (): void => {
    console.log("üéµ MusicPlayerService: Track playback ended");
  };

  private handleError = (event: Event): void => {
    console.error("‚ùå MusicPlayerService: Audio error:", event);
  };
}

/**
 * Animation Service Contract
 *
 * Handles UI animations and transitions including fold transitions,
 * fade effects, and animation settings management.
 */

export interface FoldTransitionParams {
  duration?: number;
  direction?: "fold-in" | "fold-out";
  axis?: "x" | "y";
  easing?: (t: number) => number;
}

export interface SlideTransitionParams {
  direction?: "up" | "down" | "left" | "right";
  duration?: number;
  delay?: number;
}

export interface FadeTransitionParams {
  duration?: number;
  delay?: number;
}

export interface AnimationSettings {
  // Animations are always enabled, this interface kept for compatibility
}

export interface TransitionResult {
  duration: number;
  delay?: number;
  easing?: (t: number) => number;
  css?: (t: number) => string;
}

export interface IAnimationService {
  /**
   * Create a fold transition effect
   */
  createFoldTransition(params?: FoldTransitionParams): TransitionResult;

  /**
   * Create a slide transition effect
   */
  createSlideTransition(params?: SlideTransitionParams): TransitionResult;

  /**
   * Create a basic fade transition
   */
  createFadeTransition(params?: FadeTransitionParams): TransitionResult;

  /**
   * Create a fade out transition
   */
  createFadeOutTransition(
    params?: FadeTransitionParams & { settings?: AnimationSettings }
  ): TransitionResult;

  /**
   * Create a fade in transition with delay
   */
  createFadeInTransition(
    params?: FadeTransitionParams & {
      outDuration?: number;
      settings?: AnimationSettings;
    }
  ): TransitionResult;

  /**
   * Create a conditional fade that respects settings
   */
  createConditionalFade(
    params?: FadeTransitionParams & { settings?: AnimationSettings }
  ): TransitionResult;

  /**
   * Check if animations should be enabled based on user preferences
   */
  shouldAnimate(settings?: AnimationSettings): boolean;
}

import type { IPersistenceService, ISettingsService } from "$shared";

import { ensureContainerInitialized, resolve } from "../../inversify";
import { TYPES } from "../../inversify/types";

// Make isInitialized reactive so components using getSettings() will re-evaluate
let isInitialized = $state(false);
let settingsService: ISettingsService | null = null;
let persistenceService: IPersistenceService | null = null;

export async function initializeAppServices() {
  if (isInitialized) return;

  await ensureContainerInitialized();
  settingsService = await resolve(TYPES.ISettingsService);
  isInitialized = true;
}

export function clearAppServicesCache(): void {
  isInitialized = false;
  settingsService = null;
  persistenceService = null;
}

export function getSettingsServiceSync(): ISettingsService {
  if (!settingsService) {
    throw new Error(
      "Settings service not initialized. Call initializeAppServices first."
    );
  }
  return settingsService;
}

export function getSettingsService(): ISettingsService {
  if (!settingsService) {
    settingsService = resolve<ISettingsService>(TYPES.ISettingsService);
  }
  return settingsService;
}

export function getPersistenceService(): IPersistenceService {
  if (!persistenceService) {
    persistenceService = resolve<IPersistenceService>(TYPES.IPersistenceService);
  }
  return persistenceService;
}

export function areServicesInitialized(): boolean {
  return isInitialized;
}

import { browser } from "$app/environment";
import type { ModuleId } from "$shared";

import { authStore } from "../../../auth";
import { loadFeatureModule } from "../../../inversify/container";
import { getPersistenceService } from "../services.svelte";
import {
  getActiveModule,
  setActiveModule,
  setIsTransitioning,
} from "./ui-state.svelte";

const LOCAL_STORAGE_KEY = "tka-active-module-cache";
const TRANSITION_RESET_DELAY = 300;

/**
 * Check if a module is accessible to the current user
 */
function isModuleAccessible(moduleId: ModuleId): boolean {
  // Admin module requires admin permissions
  if (moduleId === "admin") {
    return authStore.isAdmin;
  }

  // For non-admin users, Create and Community modules are accessible
  // All other modules are disabled/coming soon
  if (!authStore.isAdmin && moduleId !== "create" && moduleId !== "community") {
    return false;
  }

  // Admin users have access to all modules
  return true;
}

/**
 * Re-validate current module and section after auth state changes
 * Called when auth initializes to restore any cached module that user now has access to
 * Also validates that current section is accessible (e.g., guided mode requires admin)
 */
export async function revalidateCurrentModule(): Promise<void> {
  const currentModule = getActiveModule();

  // Try to restore any cached module that user now has access to
  if (authStore.isAdmin) {
    try {
      // Check localStorage FIRST (most recent user intent, survives even if Firestore was overwritten)
      const cached = browser ? localStorage.getItem(LOCAL_STORAGE_KEY) : null;
      if (cached) {
        try {
          const parsed: { moduleId?: string } = JSON.parse(cached) as { moduleId?: string };
          const cachedModuleId: ModuleId | undefined = parsed.moduleId as ModuleId | undefined;

          // Restore ANY module the user has access to (not just admin)
          if (
            cachedModuleId &&
            isModuleAccessible(cachedModuleId) &&
            currentModule !== cachedModuleId
          ) {
            console.log(
              `üì¶ [module-state] Restoring cached module: ${cachedModuleId}`
            );

            // Load feature module BEFORE setting active module to ensure services are available
            await loadFeatureModule(cachedModuleId);

            setActiveModule(cachedModuleId);
            // Sync Firestore to match localStorage
            const persistence = getPersistenceService();
            await persistence.saveActiveTab(cachedModuleId);
            return;
          }
        } catch (e) {
          // Ignore parse errors
        }
      }

      // If localStorage doesn't have a valid module, check Firestore as fallback
      const persistence = getPersistenceService();
      const savedFromFirestore = await persistence.getActiveTab();

      // If Firestore has a module the user can access, restore it
      if (
        savedFromFirestore &&
        isModuleAccessible(savedFromFirestore as ModuleId) &&
        currentModule !== savedFromFirestore
      ) {
        console.log(
          `üì¶ [module-state] Restoring module from Firestore: ${savedFromFirestore}`
        );

        // Load feature module BEFORE setting active module to ensure services are available
        await loadFeatureModule(savedFromFirestore);

        setActiveModule(savedFromFirestore as ModuleId);
        // Update localStorage to match
        if (browser) {
          localStorage.setItem(
            LOCAL_STORAGE_KEY,
            JSON.stringify({ moduleId: savedFromFirestore })
          );
        }
        return;
      }
    } catch (error) {
      console.warn(`‚ö†Ô∏è [module-state] Failed to revalidate module:`, error);
    }
  }

  // Validate current section accessibility (e.g., guided mode requires admin)
  if (currentModule === "create" && !authStore.isAdmin) {
    try {
      // Dynamic import to avoid circular dependency
      const { navigationState } = await import(
        "../../../navigation/state/navigation-state.svelte"
      );
      const currentSection = navigationState.activeTab;

      // If non-admin user is on assembler mode, redirect to constructor
      if (currentSection === "assembler") {
        console.warn(
          "‚ö†Ô∏è [module-state] Non-admin user on assembler mode. Redirecting to constructor."
        );
        navigationState.setActiveTab("constructor");
      }
    } catch (error) {
      console.warn(`‚ö†Ô∏è [module-state] Failed to validate section:`, error);
    }
  }
}

export function getInitialModuleFromCache(): ModuleId {
  if (!browser) {
    return "create";
  }

  try {
    const savedModuleData = localStorage.getItem(LOCAL_STORAGE_KEY);
    if (savedModuleData) {
      const parsed: { moduleId?: string } = JSON.parse(savedModuleData) as { moduleId?: string };
      if (typeof parsed.moduleId === "string") {
        const moduleId: ModuleId = parsed.moduleId as ModuleId;
        // Return the cached module even if it's admin
        // If user doesn't have access, initializeModulePersistence will handle it
        return moduleId;
      }
    }
  } catch (error) {
    console.warn("‚ö†Ô∏è Failed to pre-load saved module from cache:", error);
  }

  return "create";
}

/**
 * Preload the cached module's services immediately to prevent UI flicker
 * This should be called as early as possible in the app lifecycle
 */
export function preloadCachedModuleServices(): void {
  if (!browser) return;

  try {
    const savedModuleData = localStorage.getItem(LOCAL_STORAGE_KEY);
    if (savedModuleData) {
      const parsed: { moduleId?: string } = JSON.parse(savedModuleData) as { moduleId?: string };
      if (typeof parsed.moduleId === "string") {
        const moduleId: ModuleId = parsed.moduleId as ModuleId;
        console.log(
          `‚ö° [module-state] Preloading services for cached module: ${moduleId}`
        );

        // Start loading feature module immediately (non-blocking)
        // This prevents the UI flicker where it shows "create" first
        loadFeatureModule(moduleId).catch((error) => {
          console.warn(
            `‚ö†Ô∏è Failed to preload module services for "${moduleId}":`,
            error
          );
        });
      }
    }
  } catch (error) {
    console.warn("‚ö†Ô∏è Failed to preload cached module services:", error);
  }
}

export async function switchModule(module: ModuleId): Promise<void> {
  if (getActiveModule() === module) {
    return;
  }

  // Check if user has access to the module
  if (!isModuleAccessible(module)) {
    console.warn(
      `‚ö†Ô∏è switchModule: User does not have access to module "${module}"`
    );
    setIsTransitioning(false);
    return;
  }

  setIsTransitioning(true);

  try {
    // ‚ö° PERFORMANCE: Load feature module on-demand (Tier 3)
    // Only loads the DI services needed for this specific tab
    await loadFeatureModule(module);

    setActiveModule(module);

    const persistence = getPersistenceService();
    await persistence.saveActiveTab(module);

    if (browser) {
      localStorage.setItem(
        LOCAL_STORAGE_KEY,
        JSON.stringify({ moduleId: module })
      );
    }
  } catch (error) {
    console.warn(
      "‚ö†Ô∏è switchModule: Failed to save module to persistence:",
      error
    );
  }

  setTimeout(() => {
    setIsTransitioning(false);
  }, TRANSITION_RESET_DELAY);
}

export function isModuleActive(module: string): boolean {
  return getActiveModule() === module;
}

export async function initializeModulePersistence(): Promise<void> {
  try {
    // Try localStorage FIRST (faster, synchronous)
    let savedModule: string | null = null;
    if (browser) {
      try {
        const cached = localStorage.getItem(LOCAL_STORAGE_KEY);
        if (cached) {
          const parsed: { moduleId?: string } = JSON.parse(cached) as { moduleId?: string };
          savedModule = parsed.moduleId ?? null;
        }
      } catch (e) {
        // Ignore parse errors
      }
    }

    // Fallback to Firestore if not in localStorage
    if (!savedModule) {
      const persistence = getPersistenceService();
      await persistence.initialize();
      savedModule = await persistence.getActiveTab();
    }

    if (savedModule) {
      // Cast to ModuleId since we're checking if it's a valid module
      const moduleId = savedModule as ModuleId;

      // IMPORTANT: During initial load, skip access check because auth might not be ready yet
      // revalidateCurrentModule() will correct this after auth loads if needed

      // ‚ö° CRITICAL: Load module services BEFORE setting activeModule
      // This prevents the UI from trying to render before services are ready
      await loadFeatureModule(moduleId);

      setActiveModule(moduleId);

      if (browser) {
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify({ moduleId }));
      }
    } else {
      // No saved module
      const defaultModule = "create" as ModuleId;

      // Load default module's DI services
      await loadFeatureModule(defaultModule);

      setActiveModule(defaultModule);

      const persistence = getPersistenceService();
      await persistence.initialize();
      await persistence.saveActiveTab(defaultModule);
      if (browser) {
        localStorage.setItem(
          LOCAL_STORAGE_KEY,
          JSON.stringify({ moduleId: defaultModule })
        );
      }
    }
  } catch (error) {
    console.warn("‚ö†Ô∏è Failed to initialize module persistence:", error);
    // Fallback to create module on error
    try {
      await loadFeatureModule("create");
      setActiveModule("create");
    } catch (fallbackError) {
      console.error("‚ùå Failed to load fallback create module:", fallbackError);
    }
  }
}

