  await clearAllFirebaseCache();
  console.log("üîÑ [Cache Clear] Reloading page in 1 second...");
  setTimeout(() => {
    window.location.reload();
  }, 1000);
}

/**
 * NUCLEAR CACHE CLEAR - Complete Firebase/Auth Storage Wipeout
 *
 * This utility COMPLETELY removes ALL Firebase-related storage.
 * Use this to fix auth issues caused by old cached data.
 */

export interface CacheDiagnostics {
  indexedDBDatabases: string[];
  localStorageKeys: string[];
  sessionStorageKeys: string[];
  cookies: string[];
}

/**
 * Diagnose what's currently in browser storage
 * This shows EXACTLY what databases and keys exist
 */
export async function diagnoseCacheState(): Promise<CacheDiagnostics> {
  const diagnostics: CacheDiagnostics = {
    indexedDBDatabases: [],
    localStorageKeys: [],
    sessionStorageKeys: [],
    cookies: [],
  };

  // 1. List ALL IndexedDB databases
  try {
    const databases = await window.indexedDB.databases();
    diagnostics.indexedDBDatabases = databases
      .map((db) => db.name ?? "unnamed")
      .filter(Boolean);

    console.log(
      "üì¶ [Cache Diagnostics] IndexedDB Databases:",
      diagnostics.indexedDBDatabases
    );
  } catch (error) {
    console.error("‚ùå [Cache Diagnostics] Failed to list IndexedDB:", error);
  }

  // 2. List ALL localStorage keys
  try {
    diagnostics.localStorageKeys = Object.keys(localStorage);
    console.log(
      "üóÑÔ∏è [Cache Diagnostics] localStorage Keys:",
      diagnostics.localStorageKeys
    );
  } catch (error) {
    console.error("‚ùå [Cache Diagnostics] Failed to list localStorage:", error);
  }

  // 3. List ALL sessionStorage keys
  try {
    diagnostics.sessionStorageKeys = Object.keys(sessionStorage);
    console.log(
      "üìã [Cache Diagnostics] sessionStorage Keys:",
      diagnostics.sessionStorageKeys
    );
  } catch (error) {
    console.error(
      "‚ùå [Cache Diagnostics] Failed to list sessionStorage:",
      error
    );
  }

  // 4. List ALL cookies
  try {
    diagnostics.cookies = document.cookie
      .split(";")
      .map((c) => c.trim().split("=")[0])
      .filter((name): name is string => !!name);
    console.log("üç™ [Cache Diagnostics] Cookies:", diagnostics.cookies);
  } catch (error) {
    console.error("‚ùå [Cache Diagnostics] Failed to list cookies:", error);
  }

  // CRITICAL: Check for old project references
  const oldProjectReferences = {
    indexedDB: diagnostics.indexedDBDatabases.filter((db) =>
      db.includes("the-kinetic-constructor")
    ),
    localStorage: diagnostics.localStorageKeys.filter((key) => {
      const value = localStorage.getItem(key);
      return value ? value.includes("the-kinetic-constructor") : false;
    }),
  };

  if (
    oldProjectReferences.indexedDB.length > 0 ||
    oldProjectReferences.localStorage.length > 0
  ) {
    console.error("üö® [Cache Diagnostics] OLD PROJECT DATA FOUND!");
    console.error("üö® IndexedDB:", oldProjectReferences.indexedDB);
    console.error("üö® localStorage:", oldProjectReferences.localStorage);
  }

  return diagnostics;
}

/**
 * NUCLEAR OPTION: Delete EVERYTHING Firebase/Auth related
 * This is the most aggressive cache clearing possible
 */
export async function nuclearCacheClear(): Promise<void> {
  console.log("üí£ [NUCLEAR] Starting complete cache wipeout...");

  const deletedItems: string[] = [];
  const failedItems: string[] = [];

  // ============================================================================
  // 1. DELETE ALL INDEXEDDB DATABASES (not just Firebase ones)
  // ============================================================================
  try {
    const databases = await window.indexedDB.databases();
    console.log(`üí£ [NUCLEAR] Found ${databases.length} IndexedDB databases`);

    for (const db of databases) {
      const dbName = db.name;
      if (!dbName) continue;

      // Delete ALL databases (Firebase, Firestore, everything)
      try {
        await new Promise<void>((resolve, reject) => {
          const deleteRequest = window.indexedDB.deleteDatabase(dbName);

          deleteRequest.onsuccess = () => {
            console.log(`‚úÖ [NUCLEAR] Deleted IndexedDB: ${dbName}`);
            deletedItems.push(`IndexedDB: ${dbName}`);
            resolve();
          };

          deleteRequest.onerror = () => {
            console.error(`‚ùå [NUCLEAR] Failed to delete IndexedDB: ${dbName}`);
            failedItems.push(`IndexedDB: ${dbName}`);
            reject(deleteRequest.error);
          };

          deleteRequest.onblocked = () => {
            console.warn(
              `‚ö†Ô∏è [NUCLEAR] Delete blocked (close other tabs): ${dbName}`
            );
            // Resolve anyway - we'll retry on next load
            resolve();
          };
        });
      } catch (error) {
        console.error(`‚ùå [NUCLEAR] Error deleting ${dbName}:`, error);
        failedItems.push(`IndexedDB: ${dbName}`);
      }
    }
  } catch (error) {
    console.error("‚ùå [NUCLEAR] Failed to list IndexedDB databases:", error);
  }

  // ============================================================================
  // 2. CLEAR ALL LOCALSTORAGE
  // ============================================================================
  try {
    const keysBefore = Object.keys(localStorage);
    console.log(
      `üí£ [NUCLEAR] Clearing ${keysBefore.length} localStorage items`
    );

    localStorage.clear();

    const keysAfter = Object.keys(localStorage);
    if (keysAfter.length === 0) {
      console.log("‚úÖ [NUCLEAR] localStorage completely cleared");
      deletedItems.push(`localStorage: ${keysBefore.length} items`);
    } else {
      console.error(
        `‚ö†Ô∏è [NUCLEAR] localStorage still has ${keysAfter.length} items`
      );
      failedItems.push(`localStorage: ${keysAfter.length} items remaining`);
    }
  } catch (error) {
    console.error("‚ùå [NUCLEAR] Failed to clear localStorage:", error);
  }

  // ============================================================================
  // 3. CLEAR ALL SESSIONSTORAGE
  // ============================================================================
  try {
    const keysBefore = Object.keys(sessionStorage);
    console.log(
      `üí£ [NUCLEAR] Clearing ${keysBefore.length} sessionStorage items`
    );

    sessionStorage.clear();

    const keysAfter = Object.keys(sessionStorage);
    if (keysAfter.length === 0) {
      console.log("‚úÖ [NUCLEAR] sessionStorage completely cleared");
      deletedItems.push(`sessionStorage: ${keysBefore.length} items`);
    } else {
      console.error(
        `‚ö†Ô∏è [NUCLEAR] sessionStorage still has ${keysAfter.length} items`
      );
      failedItems.push(`sessionStorage: ${keysAfter.length} items remaining`);
    }
  } catch (error) {
    console.error("‚ùå [NUCLEAR] Failed to clear sessionStorage:", error);
  }

  // ============================================================================
  // 4. DELETE ALL COOKIES
  // ============================================================================
  try {
    const cookies = document.cookie.split(";");
    console.log(`üí£ [NUCLEAR] Deleting ${cookies.length} cookies`);

    for (const cookie of cookies) {
      const cookieName = cookie.split("=")[0]?.trim();
      if (!cookieName) continue;
      // Delete for all possible domains and paths
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=${window.location.hostname};`;
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=.${window.location.hostname};`;
      deletedItems.push(`Cookie: ${cookieName}`);
    }
    console.log("‚úÖ [NUCLEAR] All cookies deleted");
  } catch (error) {
    console.error("‚ùå [NUCLEAR] Failed to delete cookies:", error);
  }

  // ============================================================================
  // 5. CLEAR CACHE STORAGE (Service Worker caches)
  // ============================================================================
  try {
    if ("caches" in window) {
      const cacheNames = await caches.keys();
      console.log(`üí£ [NUCLEAR] Deleting ${cacheNames.length} cache storages`);

      for (const cacheName of cacheNames) {
        await caches.delete(cacheName);
        console.log(`‚úÖ [NUCLEAR] Deleted cache: ${cacheName}`);
        deletedItems.push(`Cache: ${cacheName}`);
      }
    }
  } catch (error) {
    console.error("‚ùå [NUCLEAR] Failed to clear cache storage:", error);
  }

  // ============================================================================
  // SUMMARY
  // ============================================================================
  console.log("");
  console.log("üéâ [NUCLEAR] Cache wipeout complete!");
  console.log(`‚úÖ Successfully deleted: ${deletedItems.length} items`);
  console.log(`‚ùå Failed to delete: ${failedItems.length} items`);

  if (failedItems.length > 0) {
    console.error("‚ö†Ô∏è [NUCLEAR] Failed items:", failedItems);
    console.error(
      "‚ö†Ô∏è You may need to close other tabs or restart your browser"
    );
  }

  // Verify the clear worked
  console.log("");
  console.log("üîç [NUCLEAR] Verifying cache is clear...");
  const postClearDiagnostics = await diagnoseCacheState();

  const hasRemainingFirebaseData =
    postClearDiagnostics.indexedDBDatabases.some(
      (db) => db.includes("firebase") || db.includes("firestore")
    ) ||
    postClearDiagnostics.localStorageKeys.some(
      (key) => key.includes("firebase") || key.includes("firestore")
    );

  if (hasRemainingFirebaseData) {
    console.error(
      "üö® [NUCLEAR] WARNING: Firebase data still present after clear!"
    );
    console.error("üö® You MUST close all other tabs and restart the browser");
  } else {
    console.log("‚úÖ [NUCLEAR] Verification passed - cache is clean!");
  }
}

