import { Container, type ContainerModule } from "inversify";

// Export TYPES immediately to avoid circular dependency
export { TYPES } from "./types";

// Create container
const container = new Container();

// Export container and resolve function immediately
export { container };
export const inversifyContainer = container;

// Track initialization state
let isInitialized = false;
let initializationPromise: Promise<void> | null = null;
let isHMRRecovering = false; // Track HMR recovery state

// Track loaded modules to prevent duplicate loading
const loadedModules = new Set<string>();
let tier1Loaded = false;
let tier2Loaded = false;
let tier2Promise: Promise<void> | null = null;

// Browser detection utility
const isBrowser = typeof window !== "undefined";

// Handle HMR (Hot Module Replacement) - Full container rebuild
if (import.meta.hot) {
  import.meta.hot.accept(() => {
    console.log("üîÑ HMR: Rebuilding InversifyJS container...");
    isHMRRecovering = true;

    // Save the list of loaded feature modules BEFORE clearing
    const featureModulesToRestore = Array.from(loadedModules).filter(
      (module) =>
        ![
          "core",
          "navigation",
          "data",
          "keyboard",
          "render",
          "pictograph",
          "animator",
          "gamification",
        ].includes(module)
    );

    try {
      // Clear all existing bindings
      void container.unbindAll();

      // Reset initialization state
      isInitialized = false;
      initializationPromise = null;

      // Reset tier loading flags to prevent duplicate loading
      tier1Loaded = false;
      tier2Loaded = false;
      tier2Promise = null;
      loadedModules.clear();

      // Rebuild the container
      initializeContainer()
        .then(async () => {
          // Restore previously loaded feature modules
          console.log(
            `üîÑ HMR: Restoring feature modules: ${featureModulesToRestore.join(", ")}`
          );
          for (const module of featureModulesToRestore) {
            try {
              await loadFeatureModule(module);
              console.log(`‚úÖ HMR: Restored feature module "${module}"`);
            } catch (error) {
              console.error(
                `‚ùå HMR: Failed to restore feature module "${module}":`,
                error
              );
            }
          }

          isHMRRecovering = false;
          console.log("‚úÖ HMR: Container successfully rebuilt");
        })
        .catch((error) => {
          console.error("‚ùå HMR: Container rebuild failed:", error);
          isHMRRecovering = false;
        });
    } catch (error) {
      console.error("‚ùå HMR: Container unbind failed:", error);
      isHMRRecovering = false;
    }
  });

  // Clean up on module disposal
  import.meta.hot.dispose(() => {
    console.log("üßπ HMR: Disposing container...");
    try {
      void container.unbindAll();
    } catch (error) {
      console.error("‚ùå HMR: Container disposal failed:", error);
    }
    isInitialized = false;
    initializationPromise = null;
    tier1Loaded = false;
    tier2Loaded = false;
    tier2Promise = null;
    loadedModules.clear();
  });
}

// DEPRECATED: Sync resolve function - use async resolve instead
export function resolveSync<T>(serviceType: symbol): T {
  // Don't resolve services during SSR
  if (!isBrowser) {
    throw new Error(
      `Cannot resolve service ${String(serviceType)} during server-side rendering. Use tryResolve() or ensure this code only runs in browser.`
    );
  }

  // If we're in HMR recovery mode, wait a bit for auto-recovery
  if (isHMRRecovering) {
    // Small delay to allow HMR recovery to complete
    setTimeout(() => {}, 10);
  }

  if (!isInitialized) {
    // During HMR, try to auto-recover by re-initializing silently
    if (import.meta.hot && !isHMRRecovering) {
      isHMRRecovering = true;
      // Start initialization in background but throw for now - next call should work
      initializeContainer()
        .then(() => {
          isHMRRecovering = false;
        })
        .catch((error) => {
          console.error("‚ùå HMR: Container recovery failed:", error);
          isHMRRecovering = false;
        });
    }

    throw new Error(
      `Container not initialized. Service ${String(serviceType)} cannot be resolved before container initialization completes.`
    );
  }

  try {
    return container.get<T>(serviceType);
  } catch (error) {
    // During HMR, container might have stale bindings
    if (import.meta.hot && !isHMRRecovering) {
      isHMRRecovering = true;
      initializeContainer()
        .then(() => {
          isHMRRecovering = false;
        })
        .catch((err) => {
          console.error("HMR re-initialization failed:", err);
          isHMRRecovering = false;
        });
    }
    throw error;
  }
}

// Safe resolve function that returns null if container is not ready
export function tryResolve<T>(serviceType: symbol): T | null {
  // Return null during SSR
  if (!isBrowser) {
    return null;
  }

  if (!isInitialized) {
    return null;
  }
  try {
    return container.get<T>(serviceType);
  } catch (error) {
    console.warn(`Failed to resolve ${String(serviceType)}:`, error);
    return null;
  }
}

// Async resolve function for use during initialization
export async function resolve<T>(serviceType: symbol): Promise<T> {
  await ensureContainerInitialized();
  return container.get<T>(serviceType);
}

// Check if container is initialized
export function isContainerInitialized(): boolean {
  return isInitialized;
}

// Get container status for debugging
export function getContainerStatus() {
  return {
    isInitialized,
    hasInitializationPromise: initializationPromise !== null,
  };
}

// Ensure container is initialized
export async function ensureContainerInitialized(): Promise<void> {
  if (isInitialized) return;
  if (initializationPromise) {
    await initializationPromise;
    return;
  }
  await initializeContainer();
}

/**
 * TIER 1: Load critical infrastructure modules (auth, navigation, persistence)
 * ‚è±Ô∏è Target: <500ms - Essential for app shell to become interactive
 */
export async function loadCriticalModules(): Promise<void> {
  if (tier1Loaded) return;

  try {
    const modules = await import("./modules");

    const { coreModule, navigationModule, dataModule, keyboardModule } =
      modules;

    await container.load(
      coreModule,
      navigationModule,
      dataModule,
      keyboardModule
    );

    loadedModules.add("core");
    loadedModules.add("navigation");
    loadedModules.add("data");
    loadedModules.add("keyboard");
    tier1Loaded = true;
  } catch (error) {
    console.error("‚ùå Failed to load Tier 1 modules:", error);
    throw error;
  }
}

/**
 * TIER 2: Load shared service modules (rendering, animation, pictographs)
 * ‚è±Ô∏è Non-blocking - Loads in background while user reads content
 * These modules are used across ALL features (panels, rendering, etc.)
 */
export async function loadSharedModules(): Promise<void> {
  if (tier2Loaded) return;

  // If already loading, wait for the existing promise
  if (tier2Promise) {
    await tier2Promise;
    return;
  }

  // Start loading and cache the promise
  tier2Promise = (async () => {
    try {
      const modules = await import("./modules");
      const {
        renderModule,
        pictographModule,
        animatorModule, // Animation panels appear across all modules
        gamificationModule,
      } = modules;

      await container.load(
        renderModule,
        pictographModule,
        animatorModule,
        gamificationModule
      );

      loadedModules.add("render");
      loadedModules.add("pictograph");
      loadedModules.add("animator");
      loadedModules.add("gamification");
      tier2Loaded = true;
    } catch (error) {
      console.error("‚ùå Failed to load Tier 2 modules:", error);
      // Reset promise so it can be retried
      tier2Promise = null;
      // Non-critical, don't throw
    }
  })();

  await tier2Promise;
}

/**
 * TIER 3: Load feature modules on-demand (user tabs)
 * ‚è±Ô∏è Load when tab is clicked OR when user hovers >50ms (preloading)
 *
 * @param feature - Tab name: 'create', 'explore', 'learn', 'word_card', 'write', 'admin', 'share'
 */
export async function loadFeatureModule(feature: string): Promise<void> {
  // Check if already loaded
  if (loadedModules.has(feature)) {
    return;
  }

  try {
    const modules = await import("./modules");

    // Map feature names to their DI modules with dependency tracking
    const moduleMap: Record<string, Array<{ module: ContainerModule; name: string }>> = {
      create: [
        { module: modules.createModule, name: "create" },
        { module: modules.shareModule, name: "share" },
      ],
      explore: [{ module: modules.exploreModule, name: "explore" }],
      community: [
        { module: modules.exploreModule, name: "explore" },
        { module: modules.communityModule, name: "community" },
      ],
      learn: [{ module: modules.learnModule, name: "learn" }],
      animate: [{ module: modules.exploreModule, name: "explore" }],
      collect: [], // Collect/Library use shared services
      library: [], // Legacy alias for collect
      word_card: [
        { module: modules.wordCardModule, name: "word_card" },
        { module: modules.exploreModule, name: "explore" },
      ],
      write: [{ module: modules.writeModule, name: "write" }],
      admin: [{ module: modules.adminModule, name: "admin" }],
      share: [{ module: modules.shareModule, name: "share" }],
    };

    const moduleList = moduleMap[feature];
    if (!moduleList) {
      console.warn(`Unknown feature module: ${feature}`);
      return;
    }

    // Filter out already-loaded modules to prevent duplicate bindings
    const modulesToLoad = moduleList.filter(
      ({ name }) => !loadedModules.has(name)
    );

    if (modulesToLoad.length > 0) {
      await container.load(...modulesToLoad.map(({ module }) => module));

      // Mark all newly loaded modules
      modulesToLoad.forEach(({ name }) => {
        loadedModules.add(name);
      });
    }

    // Always mark the feature itself as loaded
    loadedModules.add(feature);
  } catch (error) {
    console.error(`‚ùå Failed to load feature module '${feature}':`, error);
    throw error;
  }
}

/**
 * Preload a feature module in the background (for hover-based preloading)
 * Non-blocking, errors are logged but don't throw
 */
export function preloadFeatureModule(feature: string): void {
  loadFeatureModule(feature).catch((error) => {
    console.warn(`Preload failed for '${feature}':`, error);
  });
}

/**
 * Initialize the DI container with three-tier loading strategy
 *
 * TIER 1: Critical modules (blocking) - Auth, navigation, persistence
 * TIER 2: Shared services (non-blocking background) - Rendering, animation, pictographs
 * TIER 3: Feature modules (on-demand) - User tabs loaded when accessed
 *
 * This reduces initial load time from 4-5s to ~1.5-2s
 */
function initializeContainer() {
  // Don't initialize during SSR
  if (!isBrowser) {
    return Promise.resolve();
  }

  if (initializationPromise) {
    return initializationPromise;
  }

  initializationPromise = (async () => {
    try {
      // TIER 1: Load critical infrastructure (BLOCKING - must complete)
      // This makes the app shell interactive quickly
      await loadCriticalModules();

      // TIER 2: Start loading shared services in background (NON-BLOCKING)
      // Animation, rendering, pictographs used across all features
      // Don't await - let it load in parallel while user explores
      tier2Promise = loadSharedModules();

      // TIER 3: Feature modules loaded on-demand when user clicks/hovers tabs
      // Preload the cached module to prevent UI flicker
      preloadCachedFeatureModule();

      isInitialized = true;
    } catch (error) {
      console.error("‚ùå TKA Container: Failed to load modules:", error);
      // Reset state so we can try again
      isInitialized = false;
      initializationPromise = null;
      tier1Loaded = false;
      tier2Loaded = false;
      loadedModules.clear();
      throw error;
    }
  })();

  return initializationPromise;
}

/**
 * Preload the cached feature module to prevent UI flicker
 * Reads from localStorage and starts loading the user's last active module
 */
function preloadCachedFeatureModule(): void {
  if (!isBrowser) return;

  try {
    const cached = localStorage.getItem("tka-active-module-cache");
    if (cached) {
      const parsed: unknown = JSON.parse(cached);
      if (parsed && typeof parsed === 'object' && 'moduleId' in parsed) {
        const parsedObj = parsed as Record<string, unknown>;
        if (typeof parsedObj.moduleId === "string") {
          const moduleId = parsedObj.moduleId;
          console.log(
            `‚ö° [container] Preloading cached feature module: ${moduleId}`
          );

          // Start loading in background (non-blocking)
          loadFeatureModule(moduleId).catch((error) => {
            console.warn(
              `‚ö†Ô∏è Failed to preload cached module "${moduleId}":`,
              error
            );
          });
        }
      }
    }
  } catch (error) {
    // Ignore errors - this is just an optimization
  }
}

// Initialize the container asynchronously without blocking exports (browser-only)
if (isBrowser) {
  initializeContainer().catch((error) => {
    console.error("üí• FATAL: Container initialization failed:", error);
  });
}

// Export module initialization function for testing or manual control
export { initializeContainer };

/**
 * Barrel export for InversifyJS dependency injection system.
 *
 * This provides a clean, centralized way to import all DI-related
 * functionality using the $inversify alias.
 *
 * PERFORMANCE FIX: All container access is now dynamic to enable proper code-splitting
 */

// Export type symbols (these are safe to import statically)
export { TYPES } from "./types";

// Re-export commonly used inversify decorators and types
export { Container, inject, injectable } from "inversify";
import type { Container as InversifyContainer } from "inversify";

import { debugHMR, debugHMRError } from "../utils/hmr-debug";

// ============================================================================
// HMR-SAFE CONTAINER MANAGEMENT
// ============================================================================

// Global container state that persists across HMR
declare global {

  var __TKA_CONTAINER__: InversifyContainer | undefined;

  var __TKA_CONTAINER_PROMISE__: Promise<InversifyContainer> | undefined;

  var __TKA_CONTAINER_INITIALIZED__: boolean | undefined;
}

// HMR-safe container state management
function getGlobalContainer(): InversifyContainer | null {
  if (globalThis.__TKA_CONTAINER__) {
    return globalThis.__TKA_CONTAINER__;
  }
  return null;
}

function setGlobalContainer(container: InversifyContainer | null): void {
  if (typeof globalThis !== "undefined") {
    if (container) {
      globalThis.__TKA_CONTAINER__ = container;
      globalThis.__TKA_CONTAINER_INITIALIZED__ = true;
    } else {
      globalThis.__TKA_CONTAINER__ = undefined;
      globalThis.__TKA_CONTAINER_INITIALIZED__ = false;
    }
  }
}

function getGlobalPromise(): Promise<InversifyContainer> | null {
  if (globalThis.__TKA_CONTAINER_PROMISE__) {
    return globalThis.__TKA_CONTAINER_PROMISE__;
  }
  return null;
}

function setGlobalPromise(promise: Promise<InversifyContainer> | null): void {
  if (typeof globalThis !== "undefined") {
    globalThis.__TKA_CONTAINER_PROMISE__ = promise ?? undefined;
  }
}

// Use global state to persist across HMR
let _cachedContainer: InversifyContainer | null = getGlobalContainer();
let _containerPromise: Promise<InversifyContainer> | null = getGlobalPromise();

// HMR support - preserve container across reloads
if (import.meta.hot) {
  import.meta.hot.accept(() => {
    console.log("üîÑ HMR: Preserving container state across reload");
    debugHMR("HMR accept triggered");

    // Restore from global state
    _cachedContainer = getGlobalContainer();
    _containerPromise = getGlobalPromise();

    if (_cachedContainer) {
      console.log("‚úÖ HMR: Container restored from global state");
      debugHMR("Container successfully restored");
    } else {
      console.log("‚ö†Ô∏è HMR: No container found in global state");
      debugHMR("No container found in global state");
    }
  });

  import.meta.hot.dispose(() => {
    console.log("üîÑ HMR: Saving container state to global");
    debugHMR("HMR dispose triggered");

    // Save to global state before disposal
    setGlobalContainer(_cachedContainer);
    setGlobalPromise(_containerPromise);

    if (_cachedContainer) {
      console.log("‚úÖ HMR: Container saved to global state");
      debugHMR("Container successfully saved to global state");
    } else {
      debugHMR("No container to save during dispose");
    }
  });
}

// ============================================================================
// HMR-SAFE SERVICE RESOLUTION
// ============================================================================

export function resolve<T>(serviceIdentifier: symbol): T {
  // Try to get container from cache first
  if (!_cachedContainer) {
    _cachedContainer = getGlobalContainer();
  }

  if (!_cachedContainer) {
    throw new Error(
      `Container not initialized. Call ensureContainerInitialized() before resolving services. This usually means a component is trying to resolve services before the app is fully loaded.`
    );
  }

  try {
    return _cachedContainer.get<T>(serviceIdentifier);
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error);

    console.error("‚ùå Service resolution failed:", {
      symbol: serviceIdentifier,
      symbolString: serviceIdentifier.toString(),
      error: error,
    });

    // Check if this is a "no bindings found" error (missing module)
    if (errorMessage.includes("No bindings found")) {
      console.error(
        `\nüí° HINT: The service "${serviceIdentifier.toString()}" is not bound in the container.\n` +
          `This usually means:\n` +
          `  1. The feature module containing this service hasn't been loaded yet\n` +
          `  2. An HMR update cleared the container but didn't restore the module\n` +
          `  3. The service binding is missing from the module configuration\n`
      );
    }

    // HMR recovery: try to reinitialize container
    console.warn(
      "üîÑ HMR: Service resolution failed, attempting container recovery"
    );
    debugHMRError(error as Error, "Service resolution failed");

    _cachedContainer = getGlobalContainer();

    if (_cachedContainer) {
      try {
        const result = _cachedContainer.get<T>(serviceIdentifier);
        debugHMR("Container recovery successful");
        return result;
      } catch (retryError) {
        console.error("‚ùå HMR: Container recovery failed", retryError);
        debugHMRError(retryError as Error, "Container recovery failed");
        throw retryError;
      }
    }

    debugHMRError(error as Error, "No container available for recovery");
    throw error;
  }
}

// HMR-safe resolve with fallback
export function resolveHMRSafe<T>(serviceIdentifier: symbol): T | null {
  try {
    return resolve<T>(serviceIdentifier);
  } catch (error) {
    console.warn("‚ö†Ô∏è HMR: Service resolution failed, returning null", error);
    return null;
  }
}

// Silent resolve - returns null if service not found, no error logging
// Useful for optional dependencies
export function tryResolve<T>(serviceIdentifier: symbol): T | null {
  if (!_cachedContainer) {
    _cachedContainer = getGlobalContainer();
  }

  if (!_cachedContainer) {
    return null;
  }

  try {
    return _cachedContainer.get<T>(serviceIdentifier);
  } catch {
    // Silent - no logging for optional services
    return null;
  }
}

// ============================================================================
// HMR-SAFE CONTAINER INITIALIZATION
// ============================================================================

export async function ensureContainerInitialized(): Promise<void> {
  // Check if we already have a container from global state
  if (!_cachedContainer) {
    _cachedContainer = getGlobalContainer();
  }

  if (!_cachedContainer) {
    if (!_containerPromise) {
      _containerPromise = getGlobalPromise();
    }

    if (!_containerPromise) {
      _containerPromise = import("./container").then(
        async ({ container, ensureContainerInitialized }) => {
          await ensureContainerInitialized();
          return container;
        }
      );
      // Save promise to global state
      setGlobalPromise(_containerPromise);
    }

    _cachedContainer = await _containerPromise;
    // Save container to global state
    setGlobalContainer(_cachedContainer);
  }
}

// Check if container is ready for synchronous access
export function isContainerReady(): boolean {
  return _cachedContainer !== null;
}

// HMR utility - reset container state
export function resetContainer(): void {
  _cachedContainer = null;
  _containerPromise = null;
  // Clear global state
  setGlobalContainer(null);
  setGlobalPromise(null);
}

// Legacy exports for backward compatibility
export const getContainer = async () => {
  // Use the HMR-safe initialization
  await ensureContainerInitialized();
  return _cachedContainer;
};

// Removed duplicate - using the one above

// DEPRECATED: Use resolve() instead - kept for backward compatibility
export const resolveSyncUnsafe = <T>(serviceIdentifier: symbol): T => {
  if (!_cachedContainer) {
    throw new Error("Container not initialized. Use async resolve() instead.");
  }
  return _cachedContainer.get<T>(serviceIdentifier);
};

// DEPRECATED: Use resolve() instead - kept for backward compatibility
export const resolveAsync = resolve;

/**
 * Register Command Palette Commands
 *
 * Registers all commands available in the command palette.
 *
 * Domain: Keyboard Shortcuts - Command Registration
 */

import { showSettingsDialog } from "$shared/application/state/ui/ui-state.svelte";
import { authStore } from "$shared/auth";
import type { ModuleId } from "$shared/navigation/domain/types";
import {
  getModuleDefinitions,
  handleModuleChange,
} from "$shared/navigation-coordinator/navigation-coordinator.svelte";

import type { ICommandPaletteService } from "../services/contracts";
import type { createKeyboardShortcutState } from "../state/keyboard-shortcut-state.svelte";

export function registerCommandPaletteCommands(
  service: ICommandPaletteService,
  state: ReturnType<typeof createKeyboardShortcutState>
) {
  // Get accessible modules
  const moduleDefinitions = getModuleDefinitions();
  const isAdmin = authStore.isAdmin;

  // Filter modules to only show accessible ones
  const accessibleModules = moduleDefinitions.filter((module) => {
    // Filter out admin module for non-admin users
    if (module.id === "admin" && !isAdmin) {
      return false;
    }
    // Filter out modules that aren't implemented yet
    const notImplemented = ["write", "word-card"];
    if (notImplemented.includes(module.id)) {
      return false;
    }
    return true;
  });

  // ==================== Navigation Commands ====================

  // Dynamically register commands for accessible modules
  accessibleModules.forEach((module, index) => {
    const shortcutNumber = index + 1;
    const shortcutKey = shortcutNumber <= 5 ? `${shortcutNumber}` : undefined;
    const moduleId = module.id;

    service.registerCommand({
      id: `navigate.${moduleId}`,
      label: `Go to ${module.label.toUpperCase()}`,
      description:
        module.description ?? `Navigate to the ${module.label} module`,
      icon: module.icon || "fa-circle",
      category: "Navigation",
      ...(shortcutKey !== undefined && { shortcut: shortcutKey }),
      keywords: [module.label.toLowerCase(), moduleId],
      available: true,
      action: async () => {
        await handleModuleChange(moduleId);
        state.closeCommandPalette();
      },
    });
  });

  // ==================== Settings Commands ====================

  service.registerCommand({
    id: "settings.open",
    label: "Open Settings",
    description: "Configure application settings",
    icon: "fa-cog",
    category: "Settings",
    shortcut: state.isMac ? "‚åò," : "Ctrl+Shift+,",
    keywords: ["settings", "preferences", "config", "options"],
    available: true,
    action: () => {
      showSettingsDialog();
      state.closeCommandPalette();
    },
  });

  // ==================== Help Commands ====================

  service.registerCommand({
    id: "help.shortcuts",
    label: "Show Keyboard Shortcuts",
    description: "View all available shortcuts",
    icon: "fa-keyboard",
    category: "Help",
    shortcut: state.isMac ? "‚åò/" : "Ctrl+/",
    keywords: ["help", "shortcuts", "keyboard", "hotkeys"],
    available: true,
    action: () => {
      state.openHelp();
      state.closeCommandPalette();
    },
  });
}

/**
 * Register Global Shortcuts
 *
 * Registers all global keyboard shortcuts that are available app-wide.
 *
 * Domain: Keyboard Shortcuts - Registration
 */

import { authStore } from "$shared/auth";
import type { ModuleId } from "$shared/navigation/domain/types";
import {
  getModuleDefinitions,
  handleModuleChange,
} from "$shared/navigation-coordinator/navigation-coordinator.svelte";

import type { IKeyboardShortcutService } from "../services/contracts";
import type { createKeyboardShortcutState } from "../state/keyboard-shortcut-state.svelte";

export function registerGlobalShortcuts(
  service: IKeyboardShortcutService,
  state: ReturnType<typeof createKeyboardShortcutState>
) {
  // Get accessible modules
  const moduleDefinitions = getModuleDefinitions();
  const isAdmin = authStore.isAdmin;

  // Filter modules to only show accessible ones
  const accessibleModules = moduleDefinitions.filter((module) => {
    // Filter out admin module for non-admin users
    if (module.id === "admin" && !isAdmin) {
      return false;
    }
    // Filter out modules that aren't implemented yet
    const notImplemented = ["write", "word-card"];
    if (notImplemented.includes(module.id)) {
      return false;
    }
    return true;
  });
  // ==================== TIER 1: Essential Global Shortcuts ====================
  // Using single-key shortcuts (Gmail/Notion style) since Chrome blocks most Ctrl combinations

  // ? - Show keyboard shortcuts help (Gmail standard)
  service.register({
    id: "global.shortcuts-help",
    label: "Show keyboard shortcuts",
    description: "Display all available shortcuts (press ? key)",
    key: "?",
    modifiers: [],
    context: "global",
    scope: "help",
    priority: "critical",
    condition: () => {
      // Only enable if settings allow single-key shortcuts
      return state.settings.enableSingleKeyShortcuts;
    },
    action: () => {
      state.toggleHelp();
    },
  });

  // Escape - Close current modal/panel
  service.register({
    id: "global.escape",
    label: "Close modal",
    description: "Close the current modal, panel, or dialog",
    key: "Escape",
    modifiers: [],
    context: "global",
    scope: "navigation",
    priority: "critical",
    action: () => {
      // Close command palette if open
      if (state.showCommandPalette) {
        state.closeCommandPalette();
        return;
      }

      // Close help if open
      if (state.showHelp) {
        state.closeHelp();
        return;
      }

      // Other escape handlers will be context-specific
    },
  });

  // ==================== Module Switching (Single Keys) ====================
  // Using single-key shortcuts (numbers 1-5) like Gmail/Notion
  // These work reliably in Chrome unlike Ctrl+1-9 or Alt+1-5

  // Map modules to number keys
  const moduleKeyMap = ["1", "2", "3", "4", "5"];

  accessibleModules.slice(0, 5).forEach((module, index) => {
    const key = moduleKeyMap[index];
    if (!key) return;
    const moduleId = module.id;

    service.register({
      id: `global.switch-to-${moduleId}`,
      label: `Switch to ${module.label.toUpperCase()} module`,
      description: `Navigate to the ${module.label} module (press ${key})`,
      key: key,
      modifiers: [],
      context: "global",
      scope: "navigation",
      priority: "high",
      condition: () => {
        // Only enable if settings allow single-key shortcuts
        return state.settings.enableSingleKeyShortcuts;
      },
      action: async () => {
        await handleModuleChange(moduleId);
      },
    });
  });
}

import { injectable } from "inversify";

import type { IMobileFullscreenService } from "../contracts/IMobileFullscreenService";

/**
 * Type definitions for vendor-prefixed fullscreen APIs
 */
interface VendorDocument extends Document {
  webkitFullscreenEnabled?: boolean;
  mozFullScreenEnabled?: boolean;
  msFullscreenEnabled?: boolean;
  webkitFullscreenElement?: Element;
  mozFullScreenElement?: Element;
  msFullscreenElement?: Element;
  webkitExitFullscreen?: () => Promise<void>;
  mozCancelFullScreen?: () => Promise<void>;
  msExitFullscreen?: () => Promise<void>;
}

interface VendorHTMLElement extends HTMLElement {
  webkitRequestFullscreen?: () => Promise<void>;
  mozRequestFullScreen?: () => Promise<void>;
  msRequestFullscreen?: () => Promise<void>;
}

interface VendorNavigator extends Navigator {
  standalone?: boolean;
}

interface BeforeInstallPromptEvent extends Event {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
}

/**
 * Mobile Fullscreen Service Implementation
 *
 * Provides comprehensive fullscreen management for mobile devices
 */
@injectable()
export class MobileFullscreenService implements IMobileFullscreenService {
  private fullscreenCallbacks: ((isFullscreen: boolean) => void)[] = [];
  private installCallbacks: ((canInstall: boolean) => void)[] = [];
  private deferredPrompt: BeforeInstallPromptEvent | null = null;

  constructor() {
    this.setupEventListeners();
  }

  private setupEventListeners(): void {
    if (typeof window === "undefined") return;

    // Listen for fullscreen changes
    const fullscreenEvents = [
      "fullscreenchange",
      "webkitfullscreenchange",
      "mozfullscreenchange",
      "msfullscreenchange",
    ];

    fullscreenEvents.forEach((event) => {
      document.addEventListener(event, () => {
        const isFullscreen = this.isFullscreen();
        this.fullscreenCallbacks.forEach((callback) => callback(isFullscreen));
      });
    });

    // Listen for PWA install prompt
    window.addEventListener("beforeinstallprompt", (e) => {
      e.preventDefault();
      this.deferredPrompt = e as BeforeInstallPromptEvent;
      this.installCallbacks.forEach((callback) => callback(true));
      console.log("üì± PWA install prompt available");
    });

    // Listen for PWA install completion
    window.addEventListener("appinstalled", () => {
      this.deferredPrompt = null;
      this.installCallbacks.forEach((callback) => callback(false));
      console.log("‚úÖ PWA installed successfully");
    });
  }

  isPWA(): boolean {
    if (typeof window === "undefined") return false;

    // Check if running in standalone mode (PWA)
    // Multiple display modes indicate PWA: standalone, fullscreen, minimal-ui
    const isStandalone = window.matchMedia(
      "(display-mode: standalone)"
    ).matches;
    const isFullscreenMode = window.matchMedia(
      "(display-mode: fullscreen)"
    ).matches;
    const isMinimalUI = window.matchMedia("(display-mode: minimal-ui)").matches;
    const iOSStandalone =
      (window.navigator as VendorNavigator).standalone === true;
    const isAndroidTWA = document.referrer.includes("android-app://");

    return (
      isStandalone ||
      isFullscreenMode ||
      isMinimalUI ||
      iOSStandalone ||
      isAndroidTWA
    );
  }

  canInstallPWA(): boolean {
    return this.deferredPrompt !== null;
  }

  async promptInstallPWA(): Promise<boolean> {
    if (!this.deferredPrompt) {
      console.warn("PWA install prompt not available");
      return false;
    }

    try {
      void this.deferredPrompt.prompt();
      const { outcome } = await this.deferredPrompt.userChoice;

      if (outcome === "accepted") {
        console.log("‚úÖ User accepted PWA install");
        return true;
      } else {
        console.log("‚ùå User dismissed PWA install");
        return false;
      }
    } catch (error) {
      console.error("PWA install prompt failed:", error);
      return false;
    } finally {
      this.deferredPrompt = null;
    }
  }

  isFullscreenSupported(): boolean {
    if (typeof document === "undefined") return false;

    const vendorDoc = document as VendorDocument;
    return !!(
      document.fullscreenEnabled ||
      vendorDoc.webkitFullscreenEnabled ||
      vendorDoc.mozFullScreenEnabled ||
      vendorDoc.msFullscreenEnabled
    );
  }

  isFullscreen(): boolean {
    if (typeof document === "undefined") return false;

    const vendorDoc = document as VendorDocument;
    return !!(
      document.fullscreenElement ||
      vendorDoc.webkitFullscreenElement ||
      vendorDoc.mozFullScreenElement ||
      vendorDoc.msFullscreenElement
    );
  }

  async requestFullscreen(): Promise<boolean> {
    if (!this.isFullscreenSupported()) {
      console.warn("Fullscreen API not supported");
      return false;
    }

    try {
      const element = document.documentElement as VendorHTMLElement;

      if (element.webkitRequestFullscreen) {
        await element.webkitRequestFullscreen();
      } else if (element.mozRequestFullScreen) {
        await element.mozRequestFullScreen();
      } else if (element.msRequestFullscreen) {
        await element.msRequestFullscreen();
      } else {
        await element.requestFullscreen();
      }

      return true;
    } catch (error) {
      console.error("Failed to enter fullscreen:", error);
      return false;
    }
  }

  async exitFullscreen(): Promise<boolean> {
    if (!this.isFullscreen()) {
      return true; // Already not in fullscreen
    }

    try {
      const vendorDoc = document as VendorDocument;
      if (vendorDoc.webkitExitFullscreen) {
        await vendorDoc.webkitExitFullscreen();
      } else if (vendorDoc.mozCancelFullScreen) {
        await vendorDoc.mozCancelFullScreen();
      } else if (vendorDoc.msExitFullscreen) {
        await vendorDoc.msExitFullscreen();
      } else {
        await document.exitFullscreen();
      }

      return true;
    } catch (error) {
      console.error("Failed to exit fullscreen:", error);
      return false;
    }
  }

  async toggleFullscreen(): Promise<boolean> {
    if (this.isFullscreen()) {
      return await this.exitFullscreen();
    } else {
      return await this.requestFullscreen();
    }
  }

  showFullscreenPrompt(): void {
    const strategy = this.getRecommendedStrategy();

    switch (strategy) {
      case "pwa":
        this.showPWAPrompt();
        break;
      case "fullscreen-api":
        this.showFullscreenAPIPrompt();
        break;
      case "viewport-only":
        this.showViewportPrompt();
        break;
      default:
        console.log("Fullscreen not supported on this device");
    }
  }

  private showPWAPrompt(): void {
    // This would show a custom UI prompt for PWA installation
    console.log("üí° Showing PWA install prompt");
    // Implementation would depend on your UI framework
  }

  private showFullscreenAPIPrompt(): void {
    // This would show a custom UI prompt for fullscreen API
    console.log("üí° Showing fullscreen API prompt");
    // Implementation would depend on your UI framework
  }

  private showViewportPrompt(): void {
    // This would show tips for manual fullscreen (browser menu)
    console.log("üí° Showing viewport optimization tips");
    // Implementation would depend on your UI framework
  }

  onFullscreenChange(callback: (isFullscreen: boolean) => void): () => void {
    this.fullscreenCallbacks.push(callback);

    return () => {
      const index = this.fullscreenCallbacks.indexOf(callback);
      if (index > -1) {
        this.fullscreenCallbacks.splice(index, 1);
      }
    };
  }

  onInstallPromptAvailable(
    callback: (canInstall: boolean) => void
  ): () => void {
    this.installCallbacks.push(callback);

    return () => {
      const index = this.installCallbacks.indexOf(callback);
      if (index > -1) {
        this.installCallbacks.splice(index, 1);
      }
    };
  }

  getRecommendedStrategy():
    | "pwa"
    | "fullscreen-api"
    | "viewport-only"
    | "not-supported" {
    if (this.isPWA()) {
      return "pwa"; // Already installed as PWA
    }

    if (this.canInstallPWA()) {
      return "pwa"; // Can be installed as PWA
    }

    if (this.isFullscreenSupported()) {
      return "fullscreen-api"; // Use Fullscreen API
    }

    // Check if mobile browser supports viewport optimization
    if (typeof window !== "undefined" && "ontouchstart" in window) {
      return "viewport-only"; // Mobile browser, suggest manual fullscreen
    }

    return "not-supported";
  }

  async handleInstallRequest(): Promise<boolean> {
    if (this.canInstallPWA()) {
      // Try native install
      try {
        const accepted = await this.promptInstallPWA();
        if (accepted) {
          return true;
        }
        // If user dismissed, fall back to showing guide
        this.showInstallGuide();
        return true;
      } catch (error) {
        console.error("Failed to show native install prompt:", error);
        // Fall back to showing guide
        this.showInstallGuide();
        return true;
      }
    } else {
      // No native prompt available, show guide
      this.showInstallGuide();
      return true;
    }
  }

  private showInstallGuide(): void {
    // Dispatch event for PWA install guide to be shown
    if (typeof window !== "undefined") {
      window.dispatchEvent(new CustomEvent("pwa:open-install-guide"));
    }
  }
}

/**
 * Compact Sequence URL Encoder/Decoder
 *
 * Compresses sequence data into ultra-compact URL-safe strings.
 * Format: startPosition|beat1|beat2|beat3|...
 * Each motion: startLoc(2)+endLoc(2)+startOrient(1)+endOrient(1)+rotDir(1)+turns(1+)+type(1)
 *
 * IMPORTANT: The first part is the START POSITION (where the user begins),
 * NOT a beat! Beats are numbered 1, 2, 3, ... The start position is beat 0
 * internally but should not be counted when reporting "total beats".
 *
 * Example: "sosoiix0s:nonoiix0s|soweiic0p:soweiuc0p" (start position + 1 beat)
 *
 * With LZString compression, URLs can be 60-70% smaller for longer sequences.
 *
 * Goal: Keep most sequences under 150 characters uncompressed, ~50-80 compressed
 */

import LZString from "lz-string";

import type { BeatData } from "$lib/modules/create/shared/domain/models/BeatData";
import type { SequenceData } from "$lib/shared/foundation/domain/models/SequenceData";
import { GridLocation } from "$lib/shared/pictograph/grid/domain/enums/grid-enums";
import {
  MotionType,
  Orientation,
  RotationDirection,
} from "$lib/shared/pictograph/shared/domain/enums/pictograph-enums";
import type { MotionData } from "$lib/shared/pictograph/shared/domain/models/MotionData";

// ============================================================================
// Character Code Mappings
// ============================================================================

// Use 2-character codes for all locations to avoid parsing ambiguity
const LOCATION_ENCODE: Record<GridLocation, string> = {
  [GridLocation.NORTH]: "no",
  [GridLocation.EAST]: "ea",
  [GridLocation.SOUTH]: "so",
  [GridLocation.WEST]: "we",
  [GridLocation.NORTHEAST]: "ne",
  [GridLocation.SOUTHEAST]: "se",
  [GridLocation.SOUTHWEST]: "sw",
  [GridLocation.NORTHWEST]: "nw",
};

const LOCATION_DECODE: Record<string, GridLocation> = Object.fromEntries(
  Object.entries(LOCATION_ENCODE).map(([k, v]) => [v, k as GridLocation])
);

const ORIENTATION_ENCODE: Record<Orientation, string> = {
  [Orientation.IN]: "i",
  [Orientation.OUT]: "o",
  [Orientation.CLOCK]: "k",
  [Orientation.COUNTER]: "t",
};

const ORIENTATION_DECODE: Record<string, Orientation> = Object.fromEntries(
  Object.entries(ORIENTATION_ENCODE).map(([k, v]) => [v, k as Orientation])
);

const ROTATION_ENCODE: Record<RotationDirection, string> = {
  [RotationDirection.CLOCKWISE]: "c",
  [RotationDirection.COUNTER_CLOCKWISE]: "u",
  [RotationDirection.NO_ROTATION]: "x",
};

const ROTATION_DECODE: Record<string, RotationDirection> = Object.fromEntries(
  Object.entries(ROTATION_ENCODE).map(([k, v]) => [v, k as RotationDirection])
);

const MOTION_TYPE_ENCODE: Record<MotionType, string> = {
  [MotionType.PRO]: "p",
  [MotionType.ANTI]: "a",
  [MotionType.FLOAT]: "l", // 'l' to avoid conflict with 'f' for float turns
  [MotionType.DASH]: "d",
  [MotionType.STATIC]: "s",
};

const MOTION_TYPE_DECODE: Record<string, MotionType> = Object.fromEntries(
  Object.entries(MOTION_TYPE_ENCODE).map(([k, v]) => [v, k as MotionType])
);

// ============================================================================
// Encoding Functions
// ============================================================================

/**
 * Encode a single motion into compact string format
 * Format: startLoc(2)+endLoc(2)+startOrient(1)+endOrient(1)+rotDir(1)+turns(1+)+type(1)
 * Example: "soweiic0p" = south‚Üíwest, in‚Üíin, clockwise, 0 turns, pro
 */
function encodeMotion(motion: MotionData | undefined): string {
  if (!motion) return ""; // Empty motion

  const startLoc = LOCATION_ENCODE[motion.startLocation];
  const endLoc = LOCATION_ENCODE[motion.endLocation];
  const startOrient = ORIENTATION_ENCODE[motion.startOrientation];
  const endOrient = ORIENTATION_ENCODE[motion.endOrientation];
  const rotation = ROTATION_ENCODE[motion.rotationDirection];
  const turns = motion.turns === "fl" ? "f" : String(motion.turns);
  const type = MOTION_TYPE_ENCODE[motion.motionType];

  return `${startLoc}${endLoc}${startOrient}${endOrient}${rotation}${turns}${type}`;
}

/**
 * Encode a pictograph's blue and red motions
 * Format: "blueMotion:redMotion"
 * Example: "soweiic0p:soweiuc0p"
 *
 * Used for both start positions and sequence beats.
 */
function encodeBeat(beat: BeatData): string {
  const blueMotion = encodeMotion(beat.motions.blue);
  const redMotion = encodeMotion(beat.motions.red);
  return `${blueMotion}:${redMotion}`;
}

/**
 * Encode entire sequence into compact URL string
 * Format: "startPosition|beat1|beat2|beat3..."
 * Example: "soweiic0p:noeaiic0p|wesouuc0p:nosouuc0p|..." (1 start position + 2 beats)
 *
 * IMPORTANT: The first part is the START POSITION (where performer begins),
 * NOT a beat. The actual sequence beats follow after the first pipe.
 */
export function encodeSequence(sequence: SequenceData): string {
  let startPositionBeat: BeatData;
  let actualBeats: readonly BeatData[];

  // Check if beat 0 exists in the beats array (Generator puts start position at beats[0])
  const beat0 = sequence.beats.find((b) => b.beatNumber === 0);

  if (beat0) {
    // Generator format: start position is in beats array at index 0
    startPositionBeat = beat0;
    actualBeats = sequence.beats.filter((b) => b.beatNumber !== 0);
  } else if (sequence.startingPositionBeat) {
    // Standard format: start position is in startingPositionBeat property
    startPositionBeat = sequence.startingPositionBeat;
    actualBeats = sequence.beats;
  } else {
    // No start position - create a blank one
    startPositionBeat = {
      beatNumber: 0,
      motions: { blue: undefined, red: undefined },
      duration: 1,
      blueReversal: false,
      redReversal: false,
      isBlank: true,
      id: crypto.randomUUID(),
      letter: null,
      startPosition: null,
      endPosition: null,
    };
    actualBeats = sequence.beats;
  }

  const encodedStartPosition = encodeBeat(startPositionBeat);
  const encodedBeats = actualBeats.map(encodeBeat);

  return `${encodedStartPosition}|${encodedBeats.join("|")}`;
}

// ============================================================================
// Decoding Functions
// ============================================================================

/**
 * Decode a single motion from compact string format
 * Format: startLoc(2)+endLoc(2)+startOrient(1)+endOrient(1)+rotDir(1)+turns(1+)+type(1)
 * Example: "soweii c0p" ‚Üí MotionData (south‚Üíwest, in‚Üíin, clockwise, 0 turns, pro)
 */
function decodeMotion(
  encoded: string,
  color: "blue" | "red"
): MotionData | undefined {
  if (!encoded || encoded.length < 9) return undefined; // Minimum: 2+2+1+1+1+1+1 = 9 chars

  let pos = 0;

  // Parse start location (always 2 chars)
  const startLocCode = encoded.slice(pos, pos + 2);
  pos += 2;

  // Parse end location (always 2 chars)
  const endLocCode = encoded.slice(pos, pos + 2);
  pos += 2;

  // Parse orientations (1 char each)
  const startOrientCode = encoded[pos++];
  const endOrientCode = encoded[pos++];

  // Parse rotation (1 char)
  const rotationCode = encoded[pos++];

  // Parse turns (1+ chars, until we hit motion type letter)
  let turnsCode = "";
  while (
    pos < encoded.length &&
    encoded[pos] &&
    !MOTION_TYPE_DECODE[encoded[pos]!]
  ) {
    turnsCode += encoded[pos++];
  }

  // Parse motion type (1 char)
  const typeCode = encoded[pos];

  // Decode values
  const startLocation = LOCATION_DECODE[startLocCode];
  const endLocation = LOCATION_DECODE[endLocCode];
  const startOrientation = ORIENTATION_DECODE[startOrientCode!];
  const endOrientation = ORIENTATION_DECODE[endOrientCode!];
  const rotationDirection = ROTATION_DECODE[rotationCode!];
  const turns = turnsCode === "f" ? ("fl" as const) : parseInt(turnsCode, 10);
  const motionType = MOTION_TYPE_DECODE[typeCode!];

  // Basic validation
  if (
    !startLocation ||
    !endLocation ||
    !startOrientation ||
    !endOrientation ||
    !rotationDirection ||
    !motionType
  ) {
    throw new Error(`Invalid motion encoding: ${encoded}`);
  }

  // Import MotionColor enum
  const MotionColor = { BLUE: "blue" as const, RED: "red" as const };
  const motionColor = color === "blue" ? MotionColor.BLUE : MotionColor.RED;

  // Create minimal MotionData (other fields will be populated by the pictograph system)
  return {
    motionType,
    rotationDirection,
    startLocation,
    endLocation,
    turns,
    startOrientation,
    endOrientation,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any, @typescript-eslint/no-unsafe-assignment
    color: motionColor as any,
    isVisible: true,
    // These fields will be populated by the pictograph system
    // eslint-disable-next-line @typescript-eslint/no-explicit-any, @typescript-eslint/no-unsafe-assignment
    propType: undefined as any,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any, @typescript-eslint/no-unsafe-assignment
    gridMode: undefined as any,
    arrowLocation: startLocation,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any, @typescript-eslint/no-unsafe-assignment
    arrowPlacementData: {} as any,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any, @typescript-eslint/no-unsafe-assignment
    propPlacementData: {} as any,
  };
}

/**
 * Decode a beat from "blueMotion:redMotion" format
 * NOTE: Letters are NOT decoded here - they will be derived later by the module
 * after the pictograph services are loaded
 */
function decodeBeat(encoded: string, beatNumber: number): BeatData {
  const [blueEncoded, redEncoded] = encoded.split(":");

  if (!blueEncoded || !redEncoded) {
    throw new Error(`Invalid beat encoding: ${encoded}`);
  }

  return {
    beatNumber,
    duration: 1,
    blueReversal: false,
    redReversal: false,
    isBlank: false,
    motions: {
      blue: decodeMotion(blueEncoded, "blue"),
      red: decodeMotion(redEncoded, "red"),
    },
    // Letter will be derived later by the module using MotionQueryHandler
    id: crypto.randomUUID(),
    letter: null,
    startPosition: null,
    endPosition: null,
  };
}

/**
 * Decode compact URL string into full SequenceData
 * Format (current): "startPosition|beat1|beat2|beat3..."
 * Format (legacy): "beatNumber|beat1|beat2|beat3..." - for backward compatibility
 *
 * IMPORTANT: The first part is the START POSITION (where performer begins),
 * NOT a beat. The actual sequence beats follow after the first pipe.
 *
 * Generator format: Start position is included in beats array with beatNumber=0
 * (internally we use beat 0 for start position, but it's not a "sequence beat")
 *
 * NOTE: Letters are not included - they will be derived later by the module
 */
export function decodeSequence(encoded: string): SequenceData {
  if (!encoded) {
    throw new Error("Cannot decode empty sequence");
  }

  const parts = encoded.split("|");
  if (parts.length < 1) {
    throw new Error("Invalid sequence encoding - missing data");
  }

  // Check if this is the old format (first part is just a number) or new format (encoded beat)
  const firstPart = parts[0];
  if (!firstPart) {
    throw new Error("Invalid sequence encoding - empty first part");
  }
  const isLegacyFormat = /^\d+$/.test(firstPart);

  let beats: BeatData[];

  if (isLegacyFormat) {
    // Legacy format: "beatNumber|beat1|beat2|..."
    const startBeat = parseInt(firstPart, 10);
    if (isNaN(startBeat)) {
      throw new Error("Invalid start beat number");
    }

    const beatEncodings = parts.slice(1).filter((e) => e && e.length > 0);
    if (beatEncodings.length === 0) {
      throw new Error("No beat data found in sequence");
    }

    // Create start position (internally beat 0, but not a "sequence beat")
    const startPositionBeat: BeatData = {
      beatNumber: 0, // Start position uses beat 0 internally
      motions: { blue: undefined, red: undefined },
      duration: 1,
      blueReversal: false,
      redReversal: false,
      isBlank: true,
      id: crypto.randomUUID(),
      letter: null,
      startPosition: null,
      endPosition: null,
    };

    const sequenceBeats = beatEncodings.map((encoding, index) =>
      decodeBeat(encoding, startBeat + index)
    );

    // Put start position at index 0, followed by sequence beats
    beats = [startPositionBeat, ...sequenceBeats];
  } else {
    // Current format: "startPosition|beat1|beat2|..."
    const startPositionEncoding = parts[0]!; // Already validated above
    const startingPositionBeat = decodeBeat(startPositionEncoding, 0);

    // Remaining parts are the actual sequence beats (numbered 1, 2, 3, ...)
    const beatEncodings = parts.slice(1).filter((e) => e && e.length > 0);

    beats = beatEncodings.map(
      (encoding, index) => decodeBeat(encoding, index + 1) // Beat numbers start at 1
    );

    return {
      id: crypto.randomUUID(),
      name: "Shared Sequence",
      word: "", // Will be derived from motion data
      beats,
      startingPositionBeat, // Separate field for start position
      thumbnails: [],
      isFavorite: false,
      isCircular: false,
      tags: [],
      metadata: {},
      sequenceLength: beats.length, // Count of sequence beats (NOT including start position)
    };
  }

  return {
    id: crypto.randomUUID(),
    name: "Shared Sequence",
    word: "", // Will be derived from motion data
    beats,
    thumbnails: [],
    isFavorite: false,
    isCircular: false,
    tags: [],
    metadata: {},
    sequenceLength: beats.length,
  };
}

// ============================================================================
// Compression Helpers
// ============================================================================

/**
 * Compress a string using LZString for URL safety
 */
function compressString(str: string): string {
  return LZString.compressToEncodedURIComponent(str);
}

/**
 * Decompress a LZString-compressed string
 */
function decompressString(compressed: string): string | null {
  return LZString.decompressFromEncodedURIComponent(compressed);
}

/**
 * Encode sequence with optional compression
 * Uses compression automatically if it results in shorter output
 */
export function encodeSequenceWithCompression(sequence: SequenceData): {
  encoded: string;
  compressed: boolean;
  originalLength: number;
  finalLength: number;
} {
  const rawEncoded = encodeSequence(sequence);
  const compressed = compressString(rawEncoded);

  // Use compression only if it's actually shorter
  if (compressed.length < rawEncoded.length) {
    return {
      encoded: `z:${compressed}`, // Prefix with 'z:' to indicate compression
      compressed: true,
      originalLength: rawEncoded.length,
      finalLength: compressed.length + 2, // +2 for 'z:' prefix
    };
  }

  return {
    encoded: rawEncoded,
    compressed: false,
    originalLength: rawEncoded.length,
    finalLength: rawEncoded.length,
  };
}

/**
 * Decode sequence, handling both compressed and uncompressed formats
 */
export function decodeSequenceWithCompression(encoded: string): SequenceData {
  // Check if compressed (prefixed with 'z:')
  if (encoded.startsWith("z:")) {
    const compressed = encoded.slice(2);
    const decompressed = decompressString(compressed);
    if (!decompressed) {
      throw new Error("Failed to decompress sequence data");
    }
    return decodeSequence(decompressed);
  }

  // Not compressed, decode directly
  return decodeSequence(encoded);
}

// ============================================================================
// URL Helpers
// ============================================================================

/**
 * Generate a shareable URL for a sequence in a specific module
 * @param sequence - The sequence data to encode
 * @param module - Target module: 'construct', 'animate', 'explore'
 * @param options - Encoding options
 * @returns Full URL with compact encoding and compression metadata
 */
export function generateShareURL(
  sequence: SequenceData,
  module: string,
  options: { compress?: boolean } = { compress: true }
): {
  url: string;
  length: number;
  compressed: boolean;
  savings: number; // Percentage saved if compressed
} {
  const baseUrl = typeof window !== "undefined" ? window.location.origin : "";

  if (options.compress) {
    const { encoded, compressed, originalLength, finalLength } =
      encodeSequenceWithCompression(sequence);
    const url = `${baseUrl}/?open=${module}:${encoded}`;
    const savings = compressed
      ? Math.round(((originalLength - finalLength) / originalLength) * 100)
      : 0;

    return {
      url,
      length: url.length,
      compressed,
      savings,
    };
  }

  // No compression
  const encoded = encodeSequence(sequence);
  const url = `${baseUrl}/?open=${module}:${encoded}`;

  return {
    url,
    length: url.length,
    compressed: false,
    savings: 0,
  };
}

/**
 * Parse a deep link URL and extract module + sequence data
 * Handles both compressed and uncompressed formats
 * @param url - URL or search params string
 * @returns { module: string, sequence: SequenceData } or null if invalid
 */
export function parseDeepLink(
  url: string
): { module: string; sequence: SequenceData } | null {
  try {
    const params = new URLSearchParams(
      url.includes("?") ? url.split("?")[1] : url
    );
    const openParam = params.get("open");

    if (!openParam) return null;

    const colonIndex = openParam.indexOf(":");
    if (colonIndex === -1) return null;

    const module = openParam.slice(0, colonIndex);
    const encoded = openParam.slice(colonIndex + 1);

    if (!module || !encoded) return null;

    const sequence = decodeSequenceWithCompression(encoded);
    return { module, sequence };
  } catch (error) {
    console.error("Failed to parse deep link:", error);
    return null;
  }
}

/**
 * Estimate URL length for a sequence
 * Useful for warning users about long URLs
 */
export function estimateURLLength(
  sequence: SequenceData,
  module: string,
  compress = true
): number {
  const result = generateShareURL(sequence, module, { compress });
  return result.length;
}

/**
 * Arrow Adjustment Calculator - Consolidated Service
 *
 * Consolidated service that combines ArrowAdjustmentCalculator and ArrowAdjustmentLookup
 * to eliminate pure delegation layer. Maintains exact same interface and behavior.
 *
 * CONSOLIDATION BENEFITS:
 * - Removes unnecessary delegation layer
 * - Maintains identical logic and results
 * - Preserves all existing interfaces and test compatibility
 * - Better TypeScript organization
 */

import { Point } from "fabric";
import { inject, injectable } from "inversify";

import type { MotionTypeType } from "$lib/modules/animate/utils/motion-utils";
import type {
  ArrowPlacementKeyService,
  GridLocation,
  IArrowAdjustmentCalculator,
} from "$shared";
import type { MotionData, PictographData } from "$shared";
import type {
  IAttributeKeyGenerator,
  IDefaultPlacementService,
  IDirectionalTupleProcessor,
  IGridModeDeriver,
  ISpecialPlacementOriKeyGenerator,
  ISpecialPlacementService,
  ITurnsTupleKeyGenerator,
} from "$shared";
import { GridMode } from "$shared";
import { TYPES } from "$shared/inversify/types";

@injectable()
export class ArrowAdjustmentCalculator implements IArrowAdjustmentCalculator {
  /**
   * Consolidated service combining lookup and calculation logic.
   * Eliminates the pure delegation layer while maintaining identical behavior.
   */

  // Lookup services (previously in ArrowAdjustmentLookup)
  private specialPlacementService: ISpecialPlacementService;
  private defaultPlacementService: IDefaultPlacementService;
  private orientationKeyService: ISpecialPlacementOriKeyGenerator;
  private placementKeyService: ArrowPlacementKeyService;
  private turnsTupleService: ITurnsTupleKeyGenerator;
  private attributeKeyService: IAttributeKeyGenerator;

  // Processing services
  private tupleProcessor: IDirectionalTupleProcessor;
  private gridModeService: IGridModeDeriver;

  constructor(
    @inject(TYPES.IGridModeDeriver) gridModeService: IGridModeDeriver,
    @inject(TYPES.ISpecialPlacementService)
    specialPlacementService: ISpecialPlacementService,
    @inject(TYPES.IDefaultPlacementService)
    defaultPlacementService: IDefaultPlacementService,
    @inject(TYPES.ISpecialPlacementOriKeyGenerator)
    orientationKeyService: ISpecialPlacementOriKeyGenerator,
    @inject(TYPES.IArrowPlacementKeyService)
    placementKeyService: ArrowPlacementKeyService,
    @inject(TYPES.ITurnsTupleKeyGenerator)
    turnsTupleService: ITurnsTupleKeyGenerator,
    @inject(TYPES.IAttributeKeyGenerator)
    attributeKeyService: IAttributeKeyGenerator,
    @inject(TYPES.IDirectionalTupleProcessor)
    tupleProcessor: IDirectionalTupleProcessor
  ) {
    // Store all injected services
    this.gridModeService = gridModeService;
    this.specialPlacementService = specialPlacementService;
    this.defaultPlacementService = defaultPlacementService;
    this.orientationKeyService = orientationKeyService;
    this.placementKeyService = placementKeyService;
    this.turnsTupleService = turnsTupleService;
    this.attributeKeyService = attributeKeyService;
    this.tupleProcessor = tupleProcessor;
  }

  async calculateAdjustment(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    location: GridLocation,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Calculate arrow position adjustment - IDENTICAL logic to original.
     */
    try {
      return await this.calculateAdjustmentResult(
        pictographData,
        motionData,
        letter,
        location,
        arrowColor
      );
    } catch (error) {
      console.error(`Adjustment calculation failed: ${String(error)}`);
      return new Point(0, 0);
    }
  }

  async calculateAdjustmentResult(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    location: GridLocation,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Calculate arrow position adjustment with proper error handling.
     * IDENTICAL logic to original ArrowAdjustmentCalculator.
     */
    try {
      // STEP 1: Look up base adjustment (special ‚Üí default)
      const baseAdjustment = await this.getBaseAdjustment(
        pictographData,
        motionData,
        letter,
        arrowColor
      );

      // STEP 2: Process directional tuples for ALL motion types
      // JSON values are reference adjustments for a specific location (e.g., North)
      // They must be rotated via directional tuple matrices for each quadrant
      // This applies to ALL arrow types: PRO/ANTI/FLOAT/STATIC/DASH
      const finalAdjustment = this.tupleProcessor.processDirectionalTuples(
        baseAdjustment,
        motionData,
        location
      );

      return new Point(finalAdjustment.x, finalAdjustment.y);
    } catch (error) {
      console.error(
        `Adjustment calculation failed for letter ${letter}: ${String(error)}`
      );
      throw new Error(`Arrow adjustment calculation failed: ${String(error)}`);
    }
  }

  // === PRIVATE METHODS - Consolidated from ArrowAdjustmentLookup ===

  private async getBaseAdjustment(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Get base adjustment using streamlined lookup logic.
     * IDENTICAL to ArrowAdjustmentLookup.getBaseAdjustment()
     */
    if (!letter) {
      throw new Error("Missing letter data for adjustment lookup");
    }

    try {
      // Generate required keys for special placement lookup
      const [, , attrKey] = this.generateLookupKeys(pictographData, motionData);

      // console.log("üéØ Base Adjustment Lookup:", {
      //   letter,
      //   oriKey,
      //   turnsTuple,
      //   attrKey,
      //   arrowColor
      // });

      try {
        const specialAdjustment = await this.lookupSpecialPlacement(
          motionData,
          pictographData,
          arrowColor,
          attrKey
        );

        if (specialAdjustment) {
          // console.log("‚úÖ Found special adjustment:", specialAdjustment);
          return specialAdjustment;
        } else {
          // console.log("‚ö†Ô∏è  No special adjustment found, falling back to default");
        }
      } catch (error) {
        console.warn(`Error in special placement lookup for ${letter}:`, error);
      }

      // STEP 2: Fall back to default calculation
      const defaultAdjustment = await this.calculateDefaultAdjustment(
        motionData,
        pictographData
      );
      return defaultAdjustment;
    } catch (error) {
      console.error("Error in base adjustment lookup:", error);
      throw new Error(`Arrow adjustment lookup failed: ${String(error)}`);
    }
  }

  private generateLookupKeys(
    pictographData: PictographData,
    motionData: MotionData
  ): [string, string, string] {
    /**Generate all required keys for special placement lookup.*/
    try {
      const oriKey = this.orientationKeyService.generateOrientationKey(
        motionData,
        pictographData
      );
      const turnsTuple =
        this.turnsTupleService.generateTurnsTuple(pictographData);

      const color = motionData.color;
      const tempArrow = {
        id: "temp",
        arrowLocation: null,
        positionX: 0,
        positionY: 0,
        rotationAngle: 0,
        coordinates: { x: 0, y: 0 },
        svgCenter: { x: 0, y: 0 },
        svgMirrored: false,
        isVisible: true,
        isSelected: false,
      };

      const attrKey = this.attributeKeyService.getKeyFromArrow(
        tempArrow,
        pictographData,
        color
      );

      return [oriKey, turnsTuple.join(","), attrKey];
    } catch (error) {
      console.error("Failed to generate lookup keys:", error);
      throw new Error(`Key generation failed: ${String(error)}`);
    }
  }

  private async lookupSpecialPlacement(
    motionData: MotionData,
    pictographData: PictographData,
    arrowColor?: string,
    attributeKey?: string
  ): Promise<Point | null> {
    /**
     * Look up special placement using exact legacy logic.
     * IDENTICAL to ArrowAdjustmentLookup.lookupSpecialPlacement()
     */
    try {
      const adjustment =
        await this.specialPlacementService.getSpecialAdjustment(
          motionData,
          pictographData,
          arrowColor,
          attributeKey
        );

      if (adjustment) {
        return new Point(adjustment.x, adjustment.y);
      }

      return null;
    } catch (error) {
      console.error("Error in special placement lookup:", error);
      return null;
    }
  }

  private async calculateDefaultAdjustment(
    motionData: MotionData,
    pictographData: PictographData
  ): Promise<Point> {
    /**
     * Calculate default adjustment - IDENTICAL to ArrowAdjustmentLookup.
     */
    try {
      // Use gridMode from motion data
      const gridMode = motionData.gridMode;

      // console.log("üîß Default Adjustment Calculation:", {
      //   letter: pictographData.letter,
      //   motionType: motionData.motionType,
      //   turns: motionData.turns,
      //   gridMode
      // });

      const keys = await this.defaultPlacementService.getAvailablePlacementKeys(
        motionData.motionType as MotionTypeType,
        gridMode
      );
      const defaultPlacements: Record<string, unknown> = Object.fromEntries(
        keys.map((k: string) => [k, true])
      );

      const availableKeys = Object.keys(defaultPlacements);
      // console.log("üìã Available placement keys:", availableKeys);

      const placementKey = this.placementKeyService.generatePlacementKey(
        motionData,
        pictographData,
        availableKeys
      );
      // console.log("üîë Generated placement key:", placementKey);

      const adjustmentPoint =
        await this.defaultPlacementService.getDefaultAdjustment(
          placementKey,
          motionData.turns,
          motionData.motionType as MotionTypeType,
          gridMode
        );

      // console.log("‚úÖ Default adjustment result:", adjustmentPoint);
      return new Point(adjustmentPoint.x, adjustmentPoint.y);
    } catch (error) {
      console.error("Error calculating default adjustment:", error);
      throw new Error(`Default adjustment calculation failed: ${String(error)}`);
    }
  }
}

/**
 * Arrow Placement Data Service
 *
 * Loads and manages arrow placement JSON data for positioning calculations.
 * Ports the exact functionality from desktop DefaultPlacementService.
 */

import { injectable } from "inversify";

import { GridMode, type MotionType } from "$shared";

import { jsonCache } from "../../../../../shared/services/implementations/SimpleJsonCache";
import type { AllPlacementData, JsonPlacementData } from "../../domain";
import type { IArrowPlacementService } from "../contracts";

@injectable()
export class ArrowPlacementService implements IArrowPlacementService {
  private allPlacements: AllPlacementData = {
    [GridMode.DIAMOND]: {},
    [GridMode.BOX]: {},
  };

  private isDataLoaded = false;

  // File mapping for placement data
  private readonly placementFiles: Record<string, Record<string, string>> = {
    [GridMode.DIAMOND]: {
      pro: "/data/arrow_placement/diamond/default/default_diamond_pro_placements.json",
      anti: "/data/arrow_placement/diamond/default/default_diamond_anti_placements.json",
      float:
        "/data/arrow_placement/diamond/default/default_diamond_float_placements.json",
      dash: "/data/arrow_placement/diamond/default/default_diamond_dash_placements.json",
      static:
        "/data/arrow_placement/diamond/default/default_diamond_static_placements.json",
    },
    [GridMode.BOX]: {
      pro: "/data/arrow_placement/box/default/default_box_pro_placements.json",
      anti: "/data/arrow_placement/box/default/default_box_anti_placements.json",
      float:
        "/data/arrow_placement/box/default/default_box_float_placements.json",
      dash: "/data/arrow_placement/box/default/default_box_dash_placements.json",
      static:
        "/data/arrow_placement/box/default/default_box_static_placements.json",
    },
    // SKEWED mode doesn't have separate files - it uses both diamond and box
  };

  /**
   * Load all placement data from JSON files
   */
  async loadPlacementData(): Promise<void> {
    if (this.isDataLoaded) {
      return;
    }

    try {
      // Load diamond placements
      await this.loadGridPlacements(GridMode.DIAMOND);

      // Load box placements (may not exist yet)
      await this.loadGridPlacements(GridMode.BOX);

      this.isDataLoaded = true;
    } catch (error) {
      console.error("‚ùå Failed to load arrow placement data:", error);
      throw new Error(
        `Placement data loading failed: ${error instanceof Error ? error.message : "Unknown error"}`
      );
    }
  }

  /**
   * Load placements for a specific grid mode
   */
  private async loadGridPlacements(gridMode: GridMode): Promise<void> {
    // For SKEWED mode, default to diamond files
    const actualGridMode =
      gridMode === GridMode.SKEWED ? GridMode.DIAMOND : gridMode;
    const files = this.placementFiles[actualGridMode];
    this.allPlacements[gridMode] = {};

    for (const [motionType, filePath] of Object.entries(files ?? {})) {
      try {
        const placementData = await this.loadJsonFile(filePath);
        // Filter out null values to match GridPlacementData type
        const filteredData: {
          [placementKey: string]: { [turns: string]: [number, number] };
        } = {};
        for (const [placementKey, turnsData] of Object.entries(placementData)) {
          filteredData[placementKey] = {};
          for (const [turns, coords] of Object.entries(turnsData || {})) {
            if (
              coords !== null &&
              Array.isArray(coords) &&
              coords.length === 2 &&
              typeof coords[0] === "number" &&
              typeof coords[1] === "number"
            ) {
              filteredData[placementKey][turns] = coords as unknown as [
                number,
                number,
              ];
            }
          }
        }
        this.allPlacements[gridMode][motionType] = filteredData;
      } catch (error) {
        console.warn(
          `Could not load ${motionType} placements for ${gridMode}: ${String(error)}`
        );
        this.allPlacements[gridMode][motionType] = {};
      }
    }
  }

  /**
   * Load JSON file with caching
   */
  private async loadJsonFile(path: string): Promise<JsonPlacementData> {
    try {
      const data = await jsonCache.get(path);
      return data as JsonPlacementData;
    } catch (error) {
      console.warn(`Failed to load placement data from ${path}:`, error);
      return {};
    }
  }

  /**
   * Get default adjustment using placement key and turns
   */
  async getDefaultAdjustment(
    motionType: MotionType,
    placementKey: string,
    turns: number | string,
    gridMode: GridMode = GridMode.DIAMOND
  ): Promise<{ x: number; y: number }> {
    await this.ensureDataLoaded();

    const gridPlacements = this.allPlacements[gridMode];
    const motionPlacements = gridPlacements?.[motionType];
    const placementData = motionPlacements?.[placementKey];

    // Convert turns to string format used in JSON
    const turnsStr = this.formatTurnsForLookup(turns);
    const adjustment = placementData?.[turnsStr];

    if (!adjustment) {
      return { x: 0, y: 0 };
    }

    const [x, y] = adjustment;
    return { x, y };
  }

  /**
   * Get available placement keys for a motion type
   */
  async getAvailablePlacementKeys(
    motionType: MotionType,
    gridMode: GridMode = GridMode.DIAMOND
  ): Promise<string[]> {
    await this.ensureDataLoaded();

    const motionPlacements = this.allPlacements[gridMode]?.[motionType];
    return Object.keys(motionPlacements ?? {});
  }

  /**
   * Check if data is loaded
   */
  isLoaded(): boolean {
    return this.isDataLoaded;
  }

  /**
   * Ensure data is loaded before operations
   */
  private async ensureDataLoaded(): Promise<void> {
    if (!this.isDataLoaded) {
      await this.loadPlacementData();
    }
  }

  /**
   * Format turns value for JSON lookup
   * Converts: 1.0 ‚Üí "1", 0.5 ‚Üí "0.5", etc.
   */
  private formatTurnsForLookup(turns: number | string): string {
    if (typeof turns === "string") {
      return turns; // Already formatted (e.g., "fl" for float)
    }

    // Convert numbers: remove .0 for whole numbers
    if (turns === Math.floor(turns)) {
      return Math.floor(turns).toString();
    }

    return turns.toString();
  }

  /**
   * Debug method to log available keys
   */
  async debugAvailableKeys(
    motionType: MotionType,
    gridMode: GridMode = GridMode.DIAMOND
  ): Promise<void> {
    const keys = await this.getAvailablePlacementKeys(motionType, gridMode);
    console.log(
      `Available placement keys for ${motionType} (${gridMode}):`,
      keys
    );
  }

  /**
   * Get raw placement data for debugging
   */
  async getPlacementData(
    motionType: MotionType,
    placementKey: string,
    gridMode: GridMode = GridMode.DIAMOND
  ): Promise<{ [turns: string]: [number, number] }> {
    await this.ensureDataLoaded();

    const motionPlacements = this.allPlacements[gridMode]?.[motionType];
    return motionPlacements?.[placementKey] ?? {};
  }
}

/**
 * Rotation Override Manager
 *
 * Manages user-triggered rotation angle overrides for DASH and STATIC arrows.
 * Stores overrides in browser localStorage, allowing users to customize rotation
 * angles for specific pictograph configurations.
 *
 * IMPORTANT: Overrides are stored per-user in localStorage and persist across sessions.
 * This is the web equivalent of the desktop app's special placement JSON modification.
 */

import { inject,injectable } from "inversify";

import type { MotionData, PictographData } from "$shared";
import { TYPES } from "$shared/inversify/types";

import type { IGridModeDeriver } from "../../../../../grid";
import { SpecialPlacementOriKeyGenerator } from "../../../key-generation";
import type { IRotationAngleOverrideKeyGenerator } from "../../../key-generation/services/implementations/RotationAngleOverrideKeyGenerator";
import type { ITurnsTupleGeneratorService } from "../contracts/ITurnsTupleGeneratorService";
const STORAGE_KEY = "tka_rotation_overrides";

interface RotationOverrideData {
  [gridMode: string]: {
    [oriKey: string]: {
      [letter: string]: {
        [turnsTuple: string]: {
          [rotationKey: string]: boolean;
        };
      };
    };
  };
}

export interface IRotationOverrideManager {
  /**
   * Toggle rotation override for the given motion and pictograph.
   * Returns true if override is now active, false if removed.
   */
  toggleRotationOverride(
    motion: MotionData,
    pictographData: PictographData
  ): boolean;

  /**
   * Check if rotation override is active for the given motion.
   */
  hasRotationOverride(
    motion: MotionData,
    pictographData: PictographData
  ): boolean;

  /**
   * Clear all rotation overrides (for testing/reset).
   */
  clearAllOverrides(): void;

  /**
   * Export overrides as JSON (for backup/sharing).
   */
  exportOverrides(): string;

  /**
   * Import overrides from JSON (for restore/sharing).
   */
  importOverrides(jsonData: string): void;
}

@injectable()
export class RotationOverrideManager implements IRotationOverrideManager {
  private oriKeyGenerator: SpecialPlacementOriKeyGenerator;

  constructor(
    @inject(TYPES.ITurnsTupleGeneratorService)
    private readonly tupleGenerator: ITurnsTupleGeneratorService,
    @inject(TYPES.IRotationAngleOverrideKeyGenerator)
    private readonly rotationKeyGenerator: IRotationAngleOverrideKeyGenerator,
    @inject(TYPES.IGridModeDeriver)
    private readonly gridModeService: IGridModeDeriver
  ) {
    this.oriKeyGenerator = new SpecialPlacementOriKeyGenerator();
  }

  toggleRotationOverride(
    motion: MotionData,
    pictographData: PictographData
  ): boolean {
    // Validate motion type - only DASH and STATIC can have rotation overrides
    const motionType = motion.motionType.toLowerCase();
    if (motionType !== "dash" && motionType !== "static") {
      console.warn(
        `Rotation override not allowed for motion type: ${motionType}`
      );
      return false;
    }

    if (!pictographData.letter) {
      console.warn("No letter found in pictograph data");
      return false;
    }

    // Generate keys for storage
    const oriKey = this.oriKeyGenerator.generateOrientationKey(
      motion,
      pictographData
    );
    const gridMode = this.getGridMode(pictographData);
    const turnsTuple = this.tupleGenerator.generateTurnsTuple(pictographData);
    const rotationKey =
      this.rotationKeyGenerator.generateRotationAngleOverrideKey(
        motion,
        pictographData
      );
    const letter = pictographData.letter;

    // Load current overrides
    const overrides = this.loadOverrides();

    // Ensure structure exists
    if (!overrides[gridMode]) overrides[gridMode] = {};
    if (!overrides[gridMode][oriKey]) overrides[gridMode][oriKey] = {};
    if (!overrides[gridMode][oriKey][letter])
      overrides[gridMode][oriKey][letter] = {};
    if (!overrides[gridMode][oriKey][letter][turnsTuple])
      overrides[gridMode][oriKey][letter][turnsTuple] = {};

    const turnsData = overrides[gridMode][oriKey][letter][turnsTuple];

    // Toggle override
    const isActive = turnsData[rotationKey] === true;
    if (isActive) {
      // Remove override
      delete turnsData[rotationKey];
    } else {
      // Add override
      turnsData[rotationKey] = true;
    }

    // Save updated overrides
    this.saveOverrides(overrides);

    // Return new state
    return !isActive;
  }

  hasRotationOverride(
    motion: MotionData,
    pictographData: PictographData
  ): boolean {
    // Validate motion type
    const motionType = motion.motionType.toLowerCase();
    if (motionType !== "dash" && motionType !== "static") {
      return false;
    }

    if (!pictographData.letter) {
      return false;
    }

    // Generate keys for lookup
    const oriKey = this.oriKeyGenerator.generateOrientationKey(
      motion,
      pictographData
    );
    const gridMode = this.getGridMode(pictographData);
    const turnsTuple = this.tupleGenerator.generateTurnsTuple(pictographData);
    const rotationKey =
      this.rotationKeyGenerator.generateRotationAngleOverrideKey(
        motion,
        pictographData
      );
    const letter = pictographData.letter;

    // Load overrides and check
    const overrides = this.loadOverrides();
    return (
      overrides[gridMode]?.[oriKey]?.[letter]?.[turnsTuple]?.[rotationKey] ===
      true
    );
  }

  clearAllOverrides(): void {
    if (typeof localStorage !== "undefined") {
      localStorage.removeItem(STORAGE_KEY);
    }
  }

  exportOverrides(): string {
    const overrides = this.loadOverrides();
    return JSON.stringify(overrides, null, 2);
  }

  importOverrides(jsonData: string): void {
    try {
      const overrides = JSON.parse(jsonData) as RotationOverrideData;
      this.saveOverrides(overrides);
    } catch (error) {
      console.error("Failed to import overrides:", error);
      throw new Error("Invalid override data format");
    }
  }

  private loadOverrides(): RotationOverrideData {
    if (typeof localStorage === "undefined") {
      return {};
    }

    try {
      const data = localStorage.getItem(STORAGE_KEY);
      if (!data) return {};
      return JSON.parse(data) as RotationOverrideData;
    } catch (error) {
      console.error("Failed to load rotation overrides:", error);
      return {};
    }
  }

  private saveOverrides(overrides: RotationOverrideData): void {
    if (typeof localStorage === "undefined") {
      return;
    }

    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(overrides));
    } catch (error) {
      console.error("Failed to save rotation overrides:", error);
    }
  }

  private getGridMode(pictographData: PictographData): string {
    if (pictographData.motions.blue && pictographData.motions.red) {
      return this.gridModeService.deriveGridMode(
        pictographData.motions.blue,
        pictographData.motions.red
      );
    }
    return "diamond";
  }
}

/**
 * Special Placement Data Service
 *
 * Handles loading and caching of special placement JSON data.
 * Uses Promise-based caching to prevent race conditions during concurrent loads.
 */

import { injectable } from "inversify";

import { jsonCache } from "$shared";

import type { ISpecialPlacementDataService } from "../contracts/ISpecialPlacementDataService";

@injectable()
export class SpecialPlacementDataService
  implements ISpecialPlacementDataService
{
  // Structure: [gridMode][oriKey][letter] -> Record<string, unknown>
  private cache: Record<
    string,
    Record<string, Record<string, Record<string, unknown>>>
  > = {
    diamond: {},
    box: {},
  };

  // Track in-flight loading operations to prevent race conditions
  private loadingPromises = new Map<string, Promise<void>>();

  // Manifest of which files actually exist (loaded lazily), keyed by gridMode
  private manifests = new Map<string, Record<string, string[]>>();
  private manifestLoadPromises = new Map<string, Promise<void>>();

  /**
   * Load the manifest file that tells us which placement files exist
   */
  private async loadManifest(gridMode: string): Promise<void> {
    // Return if already loaded for this gridMode
    if (this.manifests.has(gridMode)) return;

    // Check if loading is already in progress for this gridMode
    if (this.manifestLoadPromises.has(gridMode)) {
      await this.manifestLoadPromises.get(gridMode);
      return;
    }

    // Start loading
    const loadPromise = (async () => {
      try {
        const manifestPath = `/data/arrow_placement/${gridMode}/special/placement_manifest.json`;
        const manifest = (await jsonCache.get(manifestPath)) as Record<
          string,
          string[]
        >;
        this.manifests.set(gridMode, manifest);
      } catch (error) {
        // If manifest doesn't exist, cache empty object (no special placements for this gridMode)
        this.manifests.set(gridMode, {});
      } finally {
        // Clean up loading promise
        this.manifestLoadPromises.delete(gridMode);
      }
    })();

    this.manifestLoadPromises.set(gridMode, loadPromise);
    await loadPromise;
  }

  /**
   * Check if a placement file exists for the given letter
   */
  private async hasPlacementFile(
    gridMode: string,
    oriKey: string,
    letter: string
  ): Promise<boolean> {
    await this.loadManifest(gridMode);
    const manifest = this.manifests.get(gridMode);
    return manifest?.[oriKey]?.includes(letter) ?? false;
  }

  /**
   * Get special placement data for a specific letter.
   * Returns cached data if available, otherwise loads from JSON.
   */
  async getLetterData(
    gridMode: string,
    oriKey: string,
    letter: string
  ): Promise<Record<string, unknown>> {
    try {
      // Ensure cache structure exists
      this.ensureCacheStructure(gridMode, oriKey);

      // Return cached data if available
      if (this.cache[gridMode]?.[oriKey]?.[letter]) {
        return this.cache[gridMode][oriKey][letter];
      }

      // Check manifest to see if file exists before attempting to fetch
      const fileExists = await this.hasPlacementFile(gridMode, oriKey, letter);
      if (!fileExists) {
        // No placement file exists - cache and return empty object immediately
        if (this.cache[gridMode]?.[oriKey]) {
          this.cache[gridMode][oriKey][letter] = {};
        }
        return {};
      }

      const cacheKey = `${gridMode}:${oriKey}:${letter}`;

      // Check if loading is already in progress
      if (this.loadingPromises.has(cacheKey)) {
        await this.loadingPromises.get(cacheKey);
        return this.cache[gridMode]?.[oriKey]?.[letter] ?? {};
      }

      // Start new loading operation
      const loadingPromise = this.loadData(gridMode, oriKey, letter);
      this.loadingPromises.set(cacheKey, loadingPromise);

      try {
        await loadingPromise;
        return this.cache[gridMode]?.[oriKey]?.[letter] ?? {};
      } finally {
        // Clean up the promise from cache when done
        this.loadingPromises.delete(cacheKey);
      }
    } catch (error) {
      // Missing special placement files are expected - return empty object (interpreted as zero adjustment)
      return {};
    }
  }

  /**
   * Ensure cache structure exists for gridMode and oriKey
   */
  private ensureCacheStructure(gridMode: string, oriKey: string): void {
    if (!this.cache[gridMode]) {
      this.cache[gridMode] = {} as Record<
        string,
        Record<string, Record<string, unknown>>
      >;
    }
    if (!this.cache[gridMode][oriKey]) {
      this.cache[gridMode][oriKey] = {} as Record<
        string,
        Record<string, unknown>
      >;
    }
  }

  /**
   * Load data from JSON file and store in cache
   */
  private async loadData(
    gridMode: string,
    oriKey: string,
    letter: string
  ): Promise<void> {
    // Files are served under /data/... in the web app
    // Example path: /data/arrow_placement/diamond/special/from_layer1/A_placements.json
    const encodedLetter = encodeURIComponent(letter);
    const basePath = `/data/arrow_placement/${gridMode}/special/${oriKey}/${encodedLetter}_placements.json`;

    try {
      const data = (await jsonCache.get(basePath)) as Record<string, unknown>;
      if (this.cache[gridMode]?.[oriKey]) {
        this.cache[gridMode][oriKey][letter] = data;
      }
    } catch (error) {
      // If file doesn't exist, store empty object
      if (this.cache[gridMode]?.[oriKey]) {
        this.cache[gridMode][oriKey][letter] = {};
      }
    }
  }
}

/**
 * Special Placement Service for Modern Arrow Positioning
 *
 * Orchestrates special placement logic by coordinating specialized services.
 * Provides pixel-perfect special placement adjustments for specific pictograph configurations.
 *
 * ORCHESTRATES SPECIAL PLACEMENT PIPELINE:
 * - Data loading (SpecialPlacementDataService)
 * - Orientation key generation (SpecialPlacementOriKeyGenerator)
 * - Turns tuple generation (TurnsTupleGeneratorService)
 * - Placement lookup with fallback strategies (SpecialPlacementLookupService)
 *
 * Direct TypeScript mirror of reference/modern/application/services/positioning/arrows/placement/special_placement_service.py
 */

import type { Point } from "fabric";
import { inject, injectable } from "inversify";

import { resolve, TYPES } from "../../../../../../inversify";
import type { IGridModeDeriver } from "../../../../../grid";
import { GridMode } from "../../../../../grid";
import type { MotionData, PictographData } from "../../../../../shared";
import { SpecialPlacementOriKeyGenerator } from "../../../key-generation";
import type { ISpecialPlacementService } from "../contracts";
import type { ISpecialPlacementDataService } from "../contracts/ISpecialPlacementDataService";
import type { ISpecialPlacementLookupService } from "../contracts/ISpecialPlacementLookupService";
import type { ITurnsTupleGeneratorService } from "../contracts/ITurnsTupleGeneratorService";
@injectable()
export class SpecialPlacementService implements ISpecialPlacementService {
  private oriKeyGenerator: SpecialPlacementOriKeyGenerator;
  private gridModeService: IGridModeDeriver | null = null;

  constructor(
    @inject(TYPES.ISpecialPlacementDataService)
    private readonly dataService: ISpecialPlacementDataService,
    @inject(TYPES.ITurnsTupleGeneratorService)
    private readonly tupleGenerator: ITurnsTupleGeneratorService,
    @inject(TYPES.ISpecialPlacementLookupService)
    private readonly lookupService: ISpecialPlacementLookupService
  ) {
    this.oriKeyGenerator = new SpecialPlacementOriKeyGenerator();
  }

  /**
   * Get special adjustment for arrow based on special placement logic.
   *
   * Orchestrates the pipeline:
   * 1. Generate orientation key
   * 2. Determine grid mode
   * 3. Generate turns tuple
   * 4. Load letter data
   * 5. Lookup adjustment with fallback strategies
   *
   * @param motionData Motion data containing motion information
   * @param pictographData Pictograph data containing letter and context
   * @param arrowColor Color of the arrow ('red' or 'blue')
   * @param attributeKey Optional attribute key for precise lookup
   * @returns Point with special adjustment or null if no special placement found
   */
  async getSpecialAdjustment(
    motionData: MotionData,
    pictographData: PictographData,
    arrowColor?: string,
    attributeKey?: string
  ): Promise<Point | null> {
    if (!pictographData.letter) {
      return null;
    }

    const letter = pictographData.letter;

    // Step 1: Generate orientation key
    const oriKey = this.oriKeyGenerator.generateOrientationKey(
      motionData,
      pictographData
    );

    // Step 2: Determine grid mode
    const gridMode = this.getGridMode(pictographData);

    // Step 3: Generate turns tuple
    const turnsTuple = this.tupleGenerator.generateTurnsTuple(pictographData);

    // Step 4: Load letter data
    const letterData = await this.dataService.getLetterData(
      gridMode,
      oriKey,
      letter
    );

    if (Object.keys(letterData || {}).length === 0) {
      return null;
    }

    // Step 5: Lookup adjustment with fallback strategies
    return this.lookupService.lookupAdjustment(
      letterData,
      turnsTuple,
      motionData,
      pictographData,
      arrowColor,
      attributeKey
    );
  }

  /**
   * Check if rotation angle override exists for this motion.
   *
   * Orchestrates the pipeline for rotation override checking:
   * 1. Validate motion type (only DASH/STATIC)
   * 2. Check localStorage for user overrides (priority)
   * 3. Generate orientation key and turns tuple
   * 4. Load letter data
   * 5. Lookup rotation override flag in JSON data
   *
   * @param motionData Motion data containing motion information
   * @param pictographData Pictograph data containing letter and context
   * @param rotationOverrideKey Key generated by RotationAngleOverrideKeyGenerator
   * @returns true if rotation override flag is present in special placement data or localStorage
   */
  async hasRotationAngleOverride(
    motionData: MotionData,
    pictographData: PictographData,
    rotationOverrideKey: string
  ): Promise<boolean> {
    if (!pictographData.letter) {
      return false;
    }

    // Only DASH and STATIC motions can have rotation overrides
    const motionType = motionData.motionType.toLowerCase();
    if (motionType !== "dash" && motionType !== "static") {
      return false;
    }

    const letter = pictographData.letter;

    // Step 1: Generate orientation key
    const oriKey = this.oriKeyGenerator.generateOrientationKey(
      motionData,
      pictographData
    );

    // Step 2: Determine grid mode
    const gridMode = this.getGridMode(pictographData);

    // Step 3: Generate turns tuple
    const turnsTuple = this.tupleGenerator.generateTurnsTuple(pictographData);

    // Step 4: Check localStorage for user overrides (takes priority)
    const localStorageOverride = this.checkLocalStorageOverride(
      gridMode,
      oriKey,
      letter,
      turnsTuple,
      rotationOverrideKey
    );
    if (localStorageOverride !== null) {
      return localStorageOverride;
    }

    // Step 5: Load letter data
    const letterData = await this.dataService.getLetterData(
      gridMode,
      oriKey,
      letter
    );

    if (Object.keys(letterData || {}).length === 0) {
      return false;
    }

    // Step 6: Lookup rotation override in JSON data
    return this.lookupService.lookupRotationOverride(
      letterData,
      turnsTuple,
      rotationOverrideKey
    );
  }

  /**
   * Check localStorage for rotation override
   * Returns null if not found, true/false if found
   */
  private checkLocalStorageOverride(
    gridMode: string,
    oriKey: string,
    letter: string,
    turnsTuple: string,
    rotationOverrideKey: string
  ): boolean | null {
    if (typeof localStorage === "undefined") {
      return null;
    }

    try {
      const data = localStorage.getItem("tka_rotation_overrides");
      if (!data) return null;

      const overrides: unknown = JSON.parse(data);
      if (!overrides || typeof overrides !== 'object') return null;

      const gridModeData = (overrides as Record<string, unknown>)[gridMode];
      if (!gridModeData || typeof gridModeData !== 'object') return null;

      const oriKeyData = (gridModeData as Record<string, unknown>)[oriKey];
      if (!oriKeyData || typeof oriKeyData !== 'object') return null;

      const letterData = (oriKeyData as Record<string, unknown>)[letter];
      if (!letterData || typeof letterData !== 'object') return null;

      const turnsTupleData = (letterData as Record<string, unknown>)[turnsTuple];
      if (!turnsTupleData || typeof turnsTupleData !== 'object') return null;

      const override = (turnsTupleData as Record<string, unknown>)[rotationOverrideKey];

      if (override === true) return true;
      if (override === false) return false;
      return null;
    } catch (error) {
      return null;
    }
  }

  /**
   * Get grid mode from pictograph data
   */
  private getGridMode(pictographData: PictographData): string {
    if (pictographData.motions.blue && pictographData.motions.red) {
      return this.getGridModeService().deriveGridMode(
        pictographData.motions.blue,
        pictographData.motions.red
      );
    }
    return GridMode.DIAMOND;
  }

  /**
   * Lazy-load grid mode service
   */
  private getGridModeService(): IGridModeDeriver {
    if (!this.gridModeService) {
      this.gridModeService = resolve<IGridModeDeriver>(TYPES.IGridModeDeriver);
    }
    return this.gridModeService;
  }
}

/**
 * Turns Tuple Generator Service
 *
 * Generates turns tuple strings for looking up special placement data.
 * Handles all 6 letter types with exact logic from legacy desktop app.
 */

import { injectable } from "inversify";

import type { MotionData, PictographData } from "$shared";

import type { ITurnsTupleGeneratorService } from "../contracts/ITurnsTupleGeneratorService";
import { PropRotationStateService } from "./PropRotationStateService";

type LetterType =
  | "TYPE1_HYBRID"
  | "TYPE1_NON_HYBRID"
  | "TYPE2"
  | "TYPE3"
  | "TYPE4"
  | "TYPE5"
  | "TYPE6";

@injectable()
export class TurnsTupleGeneratorService implements ITurnsTupleGeneratorService {
  private propRotationService: PropRotationStateService;

  constructor() {
    this.propRotationService = new PropRotationStateService();
  }

  /**
   * Generate turns tuple string matching the legacy turns_tuple_generator logic.
   *
   * Formats:
   * - TYPE1 Hybrid: "(pro_turns, anti_turns)" or "(blue_turns, red_turns)" if has float
   * - TYPE1 Non-Hybrid: "(blue_turns, red_turns)"
   * - TYPE2: "(shift_turns, static_turns)" or "(direction, shift_turns, static_turns)"
   * - TYPE3: "(direction, shift_turns, dash_turns)"
   * - TYPE4: "(direction, dash_turns, static_turns)" or with prop rotation for Œõ
   * - TYPE5: "(direction, blue_turns, red_turns)" or with prop rotation for Œõ-
   * - TYPE6: "(direction, blue_turns, red_turns)" or with prop rotation for Œì
   */
  generateTurnsTuple(pictographData: PictographData): string {
    try {
      const blueMotion = pictographData.motions.blue;
      const redMotion = pictographData.motions.red;

      // console.log("üîç TurnsTupleGenerator - Input:", {
      //   letter: pictographData.letter,
      //   hasBlueMotion: !!blueMotion,
      //   hasRedMotion: !!redMotion,
      //   blueMotionData: blueMotion ? {
      //     motionType: blueMotion.motionType,
      //     turns: blueMotion.turns,
      //     rotationDirection: blueMotion.rotationDirection,
      //     startLocation: blueMotion.startLocation,
      //     endLocation: blueMotion.endLocation
      //   } : null,
      //   redMotionData: redMotion ? {
      //     motionType: redMotion.motionType,
      //     turns: redMotion.turns,
      //     rotationDirection: redMotion.rotationDirection,
      //     startLocation: redMotion.startLocation,
      //     endLocation: redMotion.endLocation
      //   } : null
      // });

      if (!blueMotion || !redMotion) {
        // console.log("‚ùå Returning (0, 0) - missing motion data");
        return "(0, 0)";
      }

      const letterType = this.determineLetterType(
        pictographData.letter ?? undefined
      );
      // console.log("üìù Letter type determined:", letterType);

      if (letterType === "TYPE1_HYBRID") {
        return this.generateType1HybridTuple(blueMotion, redMotion);
      }

      if (letterType === "TYPE1_NON_HYBRID") {
        return this.generateType1NonHybridTuple(blueMotion, redMotion);
      }

      if (letterType === "TYPE2") {
        return this.generateType2Tuple(blueMotion, redMotion);
      }

      if (letterType === "TYPE3") {
        return this.generateType3Tuple(blueMotion, redMotion);
      }

      if (letterType === "TYPE4") {
        return this.generateType4Tuple(
          blueMotion,
          redMotion,
          pictographData.letter ?? undefined
        );
      }

      if (letterType === "TYPE5") {
        return this.generateType5Tuple(
          blueMotion,
          redMotion,
          pictographData.letter ?? undefined
        );
      }

      if (letterType === "TYPE6") {
        return this.generateType6Tuple(
          blueMotion,
          redMotion,
          pictographData.letter || undefined
        );
      }

      // Fallback
      return this.generateType1NonHybridTuple(blueMotion, redMotion);
    } catch (error) {
      return "(0, 0)";
    }
  }

  /**
   * Determine letter type based on letter pattern
   */
  private determineLetterType(letter?: string): LetterType {
    if (!letter) {
      return "TYPE1_NON_HYBRID";
    }

    try {
      // TYPE5: Dash-Static letters with suffix (Œ¶-, Œ®-, Œõ-)
      if (["Œ¶-", "Œ®-", "Œõ-"].includes(letter)) {
        return "TYPE5";
      }

      // TYPE6: Beta letters (Œ±, Œ≤, Œì)
      if (["Œ±", "Œ≤", "Œì"].includes(letter)) {
        return "TYPE6";
      }

      // TYPE3: Cross-Shift letters (ending with '-' except TYPE5)
      if (letter.endsWith("-")) {
        return "TYPE3";
      }

      // TYPE4: Dash letters (Œ¶, Œ®, Œõ)
      if (["Œ¶", "Œ®", "Œõ"].includes(letter)) {
        return "TYPE4";
      }

      // TYPE2: Shift-only letters (W, X, Y, Z, Œ£, Œî, Œ∏, Œ©)
      if (["W", "X", "Y", "Z", "Œ£", "Œî", "Œ∏", "Œ©"].includes(letter)) {
        return "TYPE2";
      }

      // TYPE1 Hybrid: Specific shift-static letters
      if (["C", "F", "I", "L", "O", "R", "U", "V"].includes(letter)) {
        return "TYPE1_HYBRID";
      }

      // TYPE1 Non-Hybrid: All other standard letters
      return "TYPE1_NON_HYBRID";
    } catch {
      return "TYPE1_NON_HYBRID";
    }
  }

  /**
   * Generate TYPE1 Hybrid tuple: (pro_turns, anti_turns) or (blue_turns, red_turns)
   * Used for: C, F, I, L, O, R, U, V
   */
  private generateType1HybridTuple(
    blueMotion: MotionData,
    redMotion: MotionData
  ): string {
    // Check if one motion is float
    const hasFloat =
      blueMotion.motionType.toLowerCase() === "float" ||
      redMotion.motionType.toLowerCase() === "float";

    if (hasFloat) {
      // If has float, use blue/red ordering
      return `(${this.formatTurns(this.normalizeTurns(blueMotion))}, ${this.formatTurns(this.normalizeTurns(redMotion))})`;
    } else {
      // If no float, use pro/anti ordering
      const proMotion =
        blueMotion.motionType.toLowerCase() === "pro" ? blueMotion : redMotion;
      const antiMotion =
        blueMotion.motionType.toLowerCase() === "anti" ? blueMotion : redMotion;

      return `(${proMotion.turns}, ${antiMotion.turns})`;
    }
  }

  /**
   * Generate TYPE1 Non-Hybrid tuple: (blue_turns, red_turns)
   * Used for: A, B, D, E, G, H, J, K, M, N, P, Q, S, T
   */
  private generateType1NonHybridTuple(
    blueMotion: MotionData,
    redMotion: MotionData
  ): string {
    const blueTurns = this.normalizeTurns(blueMotion);
    const redTurns = this.normalizeTurns(redMotion);

    return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
  }

  /**
   * Generate TYPE2 tuple: (shift_turns, static_turns) or (direction, shift_turns, static_turns)
   * Used for: W, X, Y, Z, Œ£, Œî, Œ∏, Œ©
   */
  private generateType2Tuple(
    blueMotion: MotionData,
    redMotion: MotionData
  ): string {
    // Identify which is shift and which is static
    const isShift = (motion: MotionData) => {
      const motionType = motion.motionType.toLowerCase();
      return ["pro", "anti", "float"].includes(motionType || "");
    };

    const shiftMotion = isShift(blueMotion) ? blueMotion : redMotion;
    const staticMotion = isShift(blueMotion) ? redMotion : blueMotion;

    const shiftType = shiftMotion.motionType.toLowerCase();
    const shiftTurns = this.normalizeTurns(shiftMotion);
    const staticTurns = this.normalizeTurns(staticMotion);

    // Handle PRO/ANTI shift motions
    if (shiftType === "pro" || shiftType === "anti") {
      const staticHasTurnsAndRotation =
        typeof staticTurns === "number" &&
        staticTurns !== 0 &&
        staticMotion.rotationDirection.toLowerCase() !== "norotation";

      if (staticHasTurnsAndRotation) {
        const staticRotDir = staticMotion.rotationDirection.toLowerCase();
        const shiftRotDir = shiftMotion.rotationDirection.toLowerCase();
        const direction = staticRotDir === shiftRotDir ? "s" : "o";
        return `(${direction}, ${this.formatTurns(shiftTurns)}, ${this.formatTurns(staticTurns)})`;
      } else {
        return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(staticTurns)})`;
      }
    }

    // Handle FLOAT shift motions
    if (shiftType === "float") {
      const staticHasTurnsAndRotation =
        typeof staticTurns === "number" &&
        staticTurns !== 0 &&
        staticMotion.rotationDirection.toLowerCase() !== "norotation";

      if (staticHasTurnsAndRotation) {
        const staticRotDir = staticMotion.rotationDirection.toLowerCase();
        const prefloatRotDir =
          shiftMotion.prefloatRotationDirection?.toLowerCase() ?? "norotation";
        const direction = staticRotDir === prefloatRotDir ? "s" : "o";
        return `(${direction}, ${this.formatTurns(shiftTurns)}, ${this.formatTurns(staticTurns)})`;
      } else {
        return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(staticTurns)})`;
      }
    }

    // Fallback
    return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(staticTurns)})`;
  }

  /**
   * Generate TYPE3 tuple: (direction, shift_turns, dash_turns)
   * Used for Cross-Shift letters (W-, X-, Y-, Z-, Œ£-, Œî-, Œ∏-, Œ©-)
   */
  private generateType3Tuple(
    blueMotion: MotionData,
    redMotion: MotionData
  ): string {
    // Identify shift and dash motions
    const isDashBlue = blueMotion.motionType.toLowerCase() === "dash";
    const shiftMotion = isDashBlue ? redMotion : blueMotion;
    const dashMotion = isDashBlue ? blueMotion : redMotion;

    const shiftType = shiftMotion.motionType.toLowerCase();
    const shiftTurns = this.normalizeTurns(shiftMotion);
    const dashTurns = this.normalizeTurns(dashMotion);
    const dashRotDir =
      dashMotion.rotationDirection.toLowerCase() || "norotation";

    // Handle PRO/ANTI shift motions
    if (shiftType === "pro" || shiftType === "anti") {
      const shiftRotDir =
        shiftMotion.rotationDirection.toLowerCase() || "norotation";
      const direction = dashRotDir === shiftRotDir ? "s" : "o";

      if (typeof dashTurns === "number" && dashTurns > 0) {
        return `(${direction}, ${this.formatTurns(shiftTurns)}, ${this.formatTurns(dashTurns)})`;
      } else {
        return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(dashTurns)})`;
      }
    }

    // Handle FLOAT shift motions
    if (shiftType === "float") {
      if (
        typeof dashTurns === "number" &&
        dashTurns !== 0 &&
        dashRotDir !== "norotation"
      ) {
        const prefloatRotDir =
          shiftMotion.prefloatRotationDirection?.toLowerCase() ?? "norotation";
        const direction = dashRotDir === prefloatRotDir ? "s" : "o";
        return `(${direction}, ${this.formatTurns(shiftTurns)}, ${this.formatTurns(dashTurns)})`;
      } else {
        return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(dashTurns)})`;
      }
    }

    // Fallback
    return `(${this.formatTurns(shiftTurns)}, ${this.formatTurns(dashTurns)})`;
  }

  /**
   * Generate TYPE4 tuple: (direction, dash_turns, static_turns)
   * Used for Dash letters (Œ¶, Œ®, Œõ)
   *
   * Special case for Œõ (Lambda): includes prop rotation state (opening/closing)
   */
  private generateType4Tuple(
    blueMotion: MotionData,
    redMotion: MotionData,
    letter?: string
  ): string {
    // Identify dash and static motions
    const isDashBlue = blueMotion.motionType.toLowerCase() === "dash";
    const dashMotion = isDashBlue ? blueMotion : redMotion;
    const staticMotion = isDashBlue ? redMotion : blueMotion;

    const dashTurns = this.normalizeTurns(dashMotion);
    const staticTurns = this.normalizeTurns(staticMotion);

    // Lambda (Œõ) requires prop rotation state
    if (letter === "Œõ") {
      return this.generateLambdaTuple(
        dashMotion,
        staticMotion,
        dashTurns,
        staticTurns
      );
    }

    // Standard TYPE4 logic for Œ¶, Œ®
    if (dashTurns === 0 && staticTurns === 0) {
      return `(${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)})`;
    } else if (dashTurns === 0 || staticTurns === 0) {
      const turningMotion = dashTurns !== 0 ? dashMotion : staticMotion;
      const turningRotDir =
        turningMotion.rotationDirection.toLowerCase() || "cw";
      return `(${turningRotDir}, ${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)})`;
    } else {
      const dashRotDir =
        dashMotion.rotationDirection.toLowerCase() || "norotation";
      const staticRotDir =
        staticMotion.rotationDirection.toLowerCase() || "norotation";
      const direction = dashRotDir === staticRotDir ? "s" : "o";
      return `(${direction}, ${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)})`;
    }
  }

  /**
   * Generate Lambda (Œõ) specific tuple with prop rotation state.
   * Format: (direction, dash_turns, static_turns, dash_open_close, static_open_close)
   */
  private generateLambdaTuple(
    dashMotion: MotionData,
    staticMotion: MotionData,
    dashTurns: number | "fl",
    staticTurns: number | "fl"
  ): string {
    if (dashTurns === 0 && staticTurns === 0) {
      return `(${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)})`;
    } else if (
      dashTurns === 0 &&
      typeof staticTurns === "number" &&
      staticTurns > 0
    ) {
      const staticOpenClose = this.propRotationService.getStaticState(
        dashMotion.endLocation,
        staticMotion.endLocation,
        staticMotion.rotationDirection
      );
      return `(${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)}, ${staticOpenClose})`;
    } else if (
      typeof dashTurns === "number" &&
      dashTurns > 0 &&
      staticTurns === 0
    ) {
      const dashOpenClose = this.propRotationService.getDashState(
        dashMotion.endLocation,
        staticMotion.endLocation,
        dashMotion.rotationDirection
      );
      return `(${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)}, ${dashOpenClose})`;
    } else if (
      typeof staticTurns === "number" &&
      staticTurns > 0 &&
      typeof dashTurns === "number" &&
      dashTurns > 0
    ) {
      const staticOpenClose = this.propRotationService.getStaticState(
        dashMotion.endLocation,
        staticMotion.endLocation,
        staticMotion.rotationDirection
      );
      const dashOpenClose = this.propRotationService.getDashState(
        dashMotion.endLocation,
        staticMotion.endLocation,
        dashMotion.rotationDirection
      );
      const direction =
        staticMotion.rotationDirection === dashMotion.rotationDirection
          ? "s"
          : "o";
      return `(${direction}, ${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)}, ${dashOpenClose}, ${staticOpenClose})`;
    } else {
      return `(${this.formatTurns(dashTurns)}, ${this.formatTurns(staticTurns)})`;
    }
  }

  /**
   * Generate TYPE5 tuple: (direction, blue_turns, red_turns)
   * Used for Dash-Static letters with suffix (Œ¶-, Œ®-, Œõ-)
   *
   * Special case for Œõ- (Lambda Dash): includes prop rotation state (opening/closing)
   *
   * Logic from legacy Type56TurnsTupleGenerator:
   * - Both turns 0: (blue_turns, red_turns)
   * - One turn 0: (rotation_direction, blue_turns, red_turns)
   * - Both turns non-zero: (direction, blue_turns, red_turns) where direction = 's' if same, 'o' if opposite
   */
  private generateType5Tuple(
    blueMotion: MotionData,
    redMotion: MotionData,
    letter?: string
  ): string {
    const blueTurns = this.normalizeTurns(blueMotion);
    const redTurns = this.normalizeTurns(redMotion);

    // Lambda Dash (Œõ-) requires prop rotation state
    if (letter === "Œõ-") {
      return this.generateLambdaDashTuple(
        blueMotion,
        redMotion,
        blueTurns,
        redTurns
      );
    }

    // Standard TYPE5 logic for Œ¶-, Œ®-
    if (blueTurns === 0 && redTurns === 0) {
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    } else if (blueTurns === 0 || redTurns === 0) {
      const turningMotion = blueTurns !== 0 ? blueMotion : redMotion;
      const turningRotDir =
        turningMotion.rotationDirection.toLowerCase() || "cw";
      return `(${turningRotDir}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    } else {
      const blueRotDir =
        blueMotion.rotationDirection.toLowerCase() || "norotation";
      const redRotDir =
        redMotion.rotationDirection.toLowerCase() || "norotation";
      const direction = blueRotDir === redRotDir ? "s" : "o";
      return `(${direction}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    }
  }

  /**
   * Generate Lambda Dash (Œõ-) specific tuple with prop rotation state.
   * Format: (direction, blue_turns, red_turns, blue_open_close, red_open_close)
   */
  private generateLambdaDashTuple(
    blueMotion: MotionData,
    redMotion: MotionData,
    blueTurns: number | "fl",
    redTurns: number | "fl"
  ): string {
    if (blueTurns === 0 && redTurns === 0) {
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    } else if (
      blueTurns === 0 &&
      typeof redTurns === "number" &&
      redTurns > 0
    ) {
      const redOpenClose = this.propRotationService.getRedState(
        blueMotion.endLocation,
        redMotion.endLocation,
        redMotion.rotationDirection
      );
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${redOpenClose})`;
    } else if (
      typeof blueTurns === "number" &&
      blueTurns > 0 &&
      redTurns === 0
    ) {
      const blueOpenClose = this.propRotationService.getBlueState(
        blueMotion.endLocation,
        redMotion.endLocation,
        blueMotion.rotationDirection
      );
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${blueOpenClose})`;
    } else if (
      typeof redTurns === "number" &&
      redTurns > 0 &&
      typeof blueTurns === "number" &&
      blueTurns > 0
    ) {
      const redOpenClose = this.propRotationService.getRedState(
        blueMotion.endLocation,
        redMotion.endLocation,
        redMotion.rotationDirection
      );
      const blueOpenClose = this.propRotationService.getBlueState(
        blueMotion.endLocation,
        redMotion.endLocation,
        blueMotion.rotationDirection
      );
      const direction =
        blueMotion.rotationDirection === redMotion.rotationDirection
          ? "s"
          : "o";
      return `(${direction}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${blueOpenClose}, ${redOpenClose})`;
    } else {
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    }
  }

  /**
   * Generate TYPE6 tuple: (direction, blue_turns, red_turns)
   * Used for Beta letters (Œ±, Œ≤, Œì)
   *
   * Special case for Œì (Gamma): includes prop rotation state (opening/closing)
   * For Œ± and Œ≤: uses standard TYPE5 logic
   */
  private generateType6Tuple(
    blueMotion: MotionData,
    redMotion: MotionData,
    letter?: string
  ): string {
    const blueTurns = this.normalizeTurns(blueMotion);
    const redTurns = this.normalizeTurns(redMotion);

    // console.log("üéØ TYPE6 Tuple Generation:", {
    //   letter,
    //   blueTurns,
    //   redTurns,
    //   blueMotionTurns: blueMotion.turns,
    //   redMotionTurns: redMotion.turns,
    //   blueMotionType: blueMotion.motionType,
    //   redMotionType: redMotion.motionType
    // });

    // Gamma (Œì) requires prop rotation state
    if (letter === "Œì") {
      return this.generateGammaTuple(
        blueMotion,
        redMotion,
        blueTurns,
        redTurns
      );
    }

    // Standard TYPE5/TYPE6 logic for Œ±, Œ≤
    if (blueTurns === 0 && redTurns === 0) {
      const tuple = `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
      // console.log("‚úÖ TYPE6 tuple (both 0):", tuple);
      return tuple;
    } else if (blueTurns === 0 || redTurns === 0) {
      const turningMotion = blueTurns !== 0 ? blueMotion : redMotion;
      const turningRotDir =
        turningMotion.rotationDirection.toLowerCase() || "cw";
      const tuple = `(${turningRotDir}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
      // console.log("‚úÖ TYPE6 tuple (one motion turning):", tuple);
      return tuple;
    } else {
      const blueRotDir =
        blueMotion.rotationDirection.toLowerCase() || "norotation";
      const redRotDir =
        redMotion.rotationDirection.toLowerCase() || "norotation";
      const direction = blueRotDir === redRotDir ? "s" : "o";
      const tuple = `(${direction}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
      // console.log("‚úÖ TYPE6 tuple (both turning):", tuple);
      return tuple;
    }
  }

  /**
   * Generate Gamma (Œì) specific tuple with prop rotation state.
   * Format: (direction, blue_turns, red_turns, blue_open_close, red_open_close)
   */
  private generateGammaTuple(
    blueMotion: MotionData,
    redMotion: MotionData,
    blueTurns: number | "fl",
    redTurns: number | "fl"
  ): string {
    if (blueTurns === 0 && redTurns === 0) {
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    } else if (
      blueTurns === 0 &&
      typeof redTurns === "number" &&
      redTurns > 0
    ) {
      const redOpenClose = this.propRotationService.getRedState(
        blueMotion.endLocation,
        redMotion.endLocation,
        redMotion.rotationDirection
      );
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${redOpenClose})`;
    } else if (
      typeof blueTurns === "number" &&
      blueTurns > 0 &&
      redTurns === 0
    ) {
      const blueOpenClose = this.propRotationService.getBlueState(
        blueMotion.endLocation,
        redMotion.endLocation,
        blueMotion.rotationDirection
      );
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${blueOpenClose})`;
    } else if (
      typeof redTurns === "number" &&
      redTurns > 0 &&
      typeof blueTurns === "number" &&
      blueTurns > 0
    ) {
      const redOpenClose = this.propRotationService.getRedState(
        blueMotion.endLocation,
        redMotion.endLocation,
        redMotion.rotationDirection
      );
      const blueOpenClose = this.propRotationService.getBlueState(
        blueMotion.endLocation,
        redMotion.endLocation,
        blueMotion.rotationDirection
      );
      const direction =
        blueMotion.rotationDirection === redMotion.rotationDirection
          ? "s"
          : "o";
      return `(${direction}, ${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)}, ${blueOpenClose}, ${redOpenClose})`;
    } else {
      return `(${this.formatTurns(blueTurns)}, ${this.formatTurns(redTurns)})`;
    }
  }

  /**
   * Normalize turns value - exact port from legacy _normalize_turns()
   */
  private normalizeTurns(motion: MotionData): number | "fl" {
    const turns = motion.turns;
    const motionType = motion.motionType.toLowerCase();

    if (motionType === "float" || turns === "fl") {
      return "fl";
    }

    if (typeof turns === "number") {
      // Return int for whole numbers, float for half turns
      return turns === Math.floor(turns) ? Math.floor(turns) : turns;
    }

    return 0;
  }

  /**
   * Format turns value for string output
   */
  private formatTurns(turns: number | "fl"): string {
    if (typeof turns === "number") {
      return turns === Math.floor(turns)
        ? Math.floor(turns).toString()
        : turns.toString();
    }
    return turns; // Already a string ("fl")
  }
}

/**
 * Position Mapping Service - Correct position mapping based on hand combinations
 *
 * Maps between position names (alpha4, beta2, etc.) and hand location pairs.
 * A position represents the combination of (blue_hand_location, red_hand_location).
 */

import { injectable } from "inversify";

import type { IGridPositionDeriver } from "$shared";
import { GridLocation, GridPosition } from "$shared";

@injectable()
export class GridPositionDeriver implements IGridPositionDeriver {
  // Position mapping from (blue_location, red_location) to grid position
  // Using actual GridLocation enums as keys for type safety
  private readonly POSITIONS_MAP = new Map<string, GridPosition>([
    // Alpha positions - hands in opposite/complementary directions
    [`${GridLocation.SOUTH},${GridLocation.NORTH}`, GridPosition.ALPHA1],
    [
      `${GridLocation.SOUTHWEST},${GridLocation.NORTHEAST}`,
      GridPosition.ALPHA2,
    ],
    [`${GridLocation.WEST},${GridLocation.EAST}`, GridPosition.ALPHA3],
    [
      `${GridLocation.NORTHWEST},${GridLocation.SOUTHEAST}`,
      GridPosition.ALPHA4,
    ],
    [`${GridLocation.NORTH},${GridLocation.SOUTH}`, GridPosition.ALPHA5],
    [
      `${GridLocation.NORTHEAST},${GridLocation.SOUTHWEST}`,
      GridPosition.ALPHA6,
    ],
    [`${GridLocation.EAST},${GridLocation.WEST}`, GridPosition.ALPHA7],
    [
      `${GridLocation.SOUTHEAST},${GridLocation.NORTHWEST}`,
      GridPosition.ALPHA8,
    ],

    // Beta positions - both hands same direction
    [`${GridLocation.NORTH},${GridLocation.NORTH}`, GridPosition.BETA1],
    [`${GridLocation.NORTHEAST},${GridLocation.NORTHEAST}`, GridPosition.BETA2],
    [`${GridLocation.EAST},${GridLocation.EAST}`, GridPosition.BETA3],
    [`${GridLocation.SOUTHEAST},${GridLocation.SOUTHEAST}`, GridPosition.BETA4],
    [`${GridLocation.SOUTH},${GridLocation.SOUTH}`, GridPosition.BETA5],
    [`${GridLocation.SOUTHWEST},${GridLocation.SOUTHWEST}`, GridPosition.BETA6],
    [`${GridLocation.WEST},${GridLocation.WEST}`, GridPosition.BETA7],
    [`${GridLocation.NORTHWEST},${GridLocation.NORTHWEST}`, GridPosition.BETA8],

    // Gamma positions - mixed/varied combinations
    [`${GridLocation.WEST},${GridLocation.NORTH}`, GridPosition.GAMMA1],
    [
      `${GridLocation.NORTHWEST},${GridLocation.NORTHEAST}`,
      GridPosition.GAMMA2,
    ],
    [`${GridLocation.NORTH},${GridLocation.EAST}`, GridPosition.GAMMA3],
    [
      `${GridLocation.NORTHEAST},${GridLocation.SOUTHEAST}`,
      GridPosition.GAMMA4,
    ],
    [`${GridLocation.EAST},${GridLocation.SOUTH}`, GridPosition.GAMMA5],
    [
      `${GridLocation.SOUTHEAST},${GridLocation.SOUTHWEST}`,
      GridPosition.GAMMA6,
    ],
    [`${GridLocation.SOUTH},${GridLocation.WEST}`, GridPosition.GAMMA7],
    [
      `${GridLocation.SOUTHWEST},${GridLocation.NORTHWEST}`,
      GridPosition.GAMMA8,
    ],
    [`${GridLocation.EAST},${GridLocation.NORTH}`, GridPosition.GAMMA9],
    [
      `${GridLocation.SOUTHEAST},${GridLocation.NORTHEAST}`,
      GridPosition.GAMMA10,
    ],
    [`${GridLocation.SOUTH},${GridLocation.EAST}`, GridPosition.GAMMA11],
    [
      `${GridLocation.SOUTHWEST},${GridLocation.SOUTHEAST}`,
      GridPosition.GAMMA12,
    ],
    [`${GridLocation.WEST},${GridLocation.SOUTH}`, GridPosition.GAMMA13],
    [
      `${GridLocation.NORTHWEST},${GridLocation.SOUTHWEST}`,
      GridPosition.GAMMA14,
    ],
    [`${GridLocation.NORTH},${GridLocation.WEST}`, GridPosition.GAMMA15],
    [
      `${GridLocation.NORTHEAST},${GridLocation.NORTHWEST}`,
      GridPosition.GAMMA16,
    ],
  ]);

  // Reverse mapping from position to hand locations
  private readonly LOCATION_PAIRS_MAP: Record<
    GridPosition,
    [GridLocation, GridLocation]
  >;

  constructor() {
    // Build reverse mapping from the Map entries
    this.LOCATION_PAIRS_MAP = {} as Record<
      GridPosition,
      [GridLocation, GridLocation]
    >;

    this.POSITIONS_MAP.forEach((position, locationKey) => {
      const [blueLocationStr, redLocationStr] = locationKey.split(",");
      const blueLocation = blueLocationStr as GridLocation;
      const redLocation = redLocationStr as GridLocation;
      this.LOCATION_PAIRS_MAP[position] = [blueLocation, redLocation];
    });
  }

  /**
   * Get the hand location pair for a given position
   */
  getGridLocationsFromPosition(
    position: GridPosition
  ): [GridLocation, GridLocation] {
    const pair = this.LOCATION_PAIRS_MAP[position];
    if (!pair) {
      throw new Error(`No location pair found for position: ${position}`);
    }
    return pair;
  }

  /**
   * Get the position for a given hand location pair
   */
  getGridPositionFromLocations(
    blueLocation: GridLocation,
    redLocation: GridLocation
  ): GridPosition {
    const key = `${blueLocation},${redLocation}`;
    const position = this.POSITIONS_MAP.get(key);
    if (!position) {
      throw new Error(
        `No position found for locations: ${blueLocation}, ${redLocation}`
      );
    }
    return position;
  }
}

/**
 * Beta Prop Direction Calculator
 *
 * Lightweight orchestrator that delegates direction calculation to specialized handlers
 * based on letter type (I, G/H, Y/Z) and motion type (SHIFT vs STATIC/DASH).
 *
 * @see Legacy: legacy_web/BetaPropDirectionCalculator.ts
 */

import type { VectorDirection } from "$shared";
import { type MotionData, MotionType, type PropPlacementData } from "$shared";

import { LetterGHHandler } from "./LetterGHHandler";
import { LetterIHandler } from "./LetterIHandler";
import { LetterYZHandler } from "./LetterYZHandler";
import { OrientationChecker } from "./OrientationChecker";
import { ShiftMotionHandler } from "./ShiftMotionHandler";
import { StaticDashMotionHandler } from "./StaticDashMotionHandler";

export class BetaPropDirectionCalculator {
  private orientationChecker: OrientationChecker;
  private staticDashHandler: StaticDashMotionHandler;
  private shiftHandler: ShiftMotionHandler;
  private letterIHandler: LetterIHandler;
  private letterGHHandler: LetterGHHandler;
  private letterYZHandler: LetterYZHandler;

  constructor(
    private motionDataSet: { red: MotionData; blue: MotionData },
    private pictographLetter?: string
  ) {
    this.orientationChecker = new OrientationChecker(motionDataSet);
    this.staticDashHandler = new StaticDashMotionHandler(
      this.orientationChecker
    );
    this.shiftHandler = new ShiftMotionHandler(this.orientationChecker);
    this.letterIHandler = new LetterIHandler(this.orientationChecker);
    this.letterGHHandler = new LetterGHHandler(this.orientationChecker);
    this.letterYZHandler = new LetterYZHandler(
      motionDataSet,
      this.shiftHandler
    );
  }

  /** @deprecated Use getDirectionForMotionData() directly with the specific motion. */
  getDirection(_prop: PropPlacementData): VectorDirection | null {
    const motionData = this.motionDataSet.blue ?? this.motionDataSet.red;
    return this.getDirectionForMotionData(motionData);
  }

  getDirectionForMotionData(motionData: MotionData): VectorDirection | null {

    if (this.isYOrZLetter()) {
      return this.letterYZHandler.calculate(motionData);
    }

    if (this.isShiftMotion(motionData)) {
      if (this.isGOrHLetter()) {
        return this.letterGHHandler.calculate(motionData);
      }
      if (this.pictographLetter === "I") {
        return this.letterIHandler.calculate(motionData);
      }
      return this.shiftHandler.calculate(motionData);
    }

    return this.staticDashHandler.calculate(motionData);
  }

  private isYOrZLetter(): boolean {
    return (
      this.pictographLetter === "Y" ||
      this.pictographLetter === "Z" ||
      this.pictographLetter === "Y-" ||
      this.pictographLetter === "Z-"
    );
  }

  private isGOrHLetter(): boolean {
    return this.pictographLetter === "G" || this.pictographLetter === "H";
  }

  private isShiftMotion(motion: MotionData): boolean {
    return [MotionType.PRO, MotionType.ANTI, MotionType.FLOAT].includes(
      motion.motionType
    );
  }
}

import type { GridLocation, GridMode } from "$shared";
import { createGridPointData,type GridPointData } from "$shared";

/**
 * DefaultPropPositioner - Calculates default prop positions using grid coordinates
 * Ported from legacy web app to ensure positioning parity
 */
export class DefaultPropPositioner {
  private fallbackCoordinates: Record<string, { x: number; y: number }> = {
    // Default positions if grid points aren't found - matching legacy fallbacks
    n: { x: 475, y: 330 },
    e: { x: 620, y: 475 },
    s: { x: 475, y: 620 },
    w: { x: 330, y: 475 },
    ne: { x: 620, y: 330 },
    se: { x: 620, y: 620 },
    sw: { x: 330, y: 620 },
    nw: { x: 330, y: 330 },
  };

  constructor(
    private gridData: GridPointData,
    private gridMode: GridMode
  ) {
    // Grid data is always provided and valid - no validation needed
  }

  /**
   * Calculate coordinates for a prop based on its location
   */
  public calculateCoordinates(location: GridLocation | string): {
    x: number;
    y: number;
  } {
    // Normalize location to lowercase to match grid coordinate keys
    const normalizedLocation = (
      typeof location === "string" ? location : location
    ).toLowerCase();
    const pointName = `${normalizedLocation}_${this.gridMode.valueOf()}_hand_point`;
    const gridPoint = this.getGridPoint(pointName);

    if (gridPoint?.coordinates) {
      return gridPoint.coordinates;
    } else {
      const fallback = this.getFallbackCoordinates(normalizedLocation);
      return fallback;
    }
  }

  /**
   * Get grid point by name from grid data
   */
  private getGridPoint(
    pointName: string
  ): { coordinates: { x: number; y: number } } | null {
    // Try to find the point in allHandPointsNormal
    if (this.gridData.allHandPointsNormal[pointName]) {
      const point = this.gridData.allHandPointsNormal[pointName];
      if (point.coordinates) {
        return { coordinates: point.coordinates };
      }
    }

    // Try alternative naming patterns
    const alternativeNames = [
      pointName,
      pointName.replace("_hand_point", ""),
      `${pointName}_normal`,
      `hand_${pointName}`,
    ];

    for (const altName of alternativeNames) {
      if (this.gridData.allHandPointsNormal[altName]) {
        const point = this.gridData.allHandPointsNormal[altName];
        if (point.coordinates) {
          return { coordinates: point.coordinates };
        }
      }
    }

    return null;
  }

  /**
   * Get fallback coordinates for a location
   */
  private getFallbackCoordinates(location: string): { x: number; y: number } {
    return this.fallbackCoordinates[location] || { x: 475, y: 475 }; // Center fallback
  }

  /**
   * Static helper method for quick coordinate calculation
   */
  static calculatePosition(
    location: GridLocation,
    gridMode: GridMode
  ): { x: number; y: number } {
    try {
      const gridPointData = createGridPointData(gridMode);
      const positioner = new DefaultPropPositioner(gridPointData, gridMode);
      const result = positioner.calculateCoordinates(location);
      return result;
    } catch (error) {
      console.error("Error calculating position:", error);
      // Return center as ultimate fallback
      return { x: 475, y: 475 };
    }
  }
}

export default DefaultPropPositioner;

/**
 * Letter G/H Handler
 *
 * Letters G and H use override logic:
 * - Red gets the base direction
 * - Blue gets the opposite direction
 */

import {
  GridLocation,
  MotionColor,
  type MotionData,
  VectorDirection,
} from "$shared";

import type { DiamondLoc } from "../../domain/direction/DirectionMaps";
import {
  DIAMOND_NON_RADIAL_MAP,
  DIAMOND_RADIAL_MAP,
} from "../../domain/direction/DirectionMaps";
import type { IDirectionCalculator } from "../contracts/IDirectionCalculator";
import type { IOrientationChecker } from "../contracts/IOrientationChecker";
import { getEndLocation, getOppositeDirection } from "./DirectionUtils";

export class LetterGHHandler implements IDirectionCalculator {
  constructor(private orientationChecker: IOrientationChecker) {}

  /**
   * Calculate direction using G/H override logic.
   *
   * Red gets base direction, blue gets opposite.
   */
  calculate(motionData: MotionData): VectorDirection | null {
    const isRadial = this.orientationChecker.isRadial();
    const endLocation = getEndLocation(motionData);

    const baseDirection = this.getBaseDirection(isRadial, endLocation);
    if (!baseDirection) {
      return null;
    }

    return motionData.color === MotionColor.RED
      ? baseDirection
      : getOppositeDirection(baseDirection);
  }

  /**
   * Get base motion direction for G/H letters.
   */
  private getBaseDirection(
    isRadial: boolean,
    endLocation: string
  ): VectorDirection | null {
    // Special case: South location always returns RIGHT
    if (endLocation === "s") {
      return VectorDirection.RIGHT;
    }

    // Use red prop's direction from the map as base
    const map = isRadial ? DIAMOND_RADIAL_MAP : DIAMOND_NON_RADIAL_MAP;
    return map[endLocation as DiamondLoc][MotionColor.RED] ?? null;
  }
}

/**
 * Letter I Handler
 *
 * Letter I has unique direction rules that use special lookup tables.
 */

import type {
  MotionColor,
  VectorDirection,
} from "../../../shared/domain/enums/pictograph-enums";
import type { MotionData } from "../../../shared/domain/models/MotionData";
import type { Loc } from "../../domain/direction/DirectionMaps";
import {
  LETTER_I_NON_RADIAL_MAP,
  LETTER_I_RADIAL_MAP,
} from "../../domain/direction/DirectionMaps";
import type { IDirectionCalculator } from "../contracts/IDirectionCalculator";
import type { IOrientationChecker } from "../contracts/IOrientationChecker";
import { getEndLocation } from "./DirectionUtils";

export class LetterIHandler implements IDirectionCalculator {
  constructor(private orientationChecker: IOrientationChecker) {}

  /**
   * Calculate direction using Letter I's special maps.
   */
  calculate(motionData: MotionData): VectorDirection | null {
    const isRadial = this.orientationChecker.isRadial();
    const endLocation = getEndLocation(motionData);

    const map = isRadial ? LETTER_I_RADIAL_MAP : LETTER_I_NON_RADIAL_MAP;
    return map[endLocation as Loc][motionData.color] ?? null;
  }
}

/**
 * Prop SVG Loader Service - OPTIMIZED (2025 Best Practices)
 *
 * Fast, direct SVG loading for props with aggressive caching.
 *
 * Key optimizations:
 * - Multi-level caching (raw SVG + transformed SVG by color)
 * - Request deduplication (prevents duplicate concurrent fetches)
 * - Cached metadata parsing (viewBox, center)
 * - Performance monitoring (cache hit/miss tracking)
 */

import { injectable } from "inversify";

import { MotionColor } from "../../../shared/domain/enums/pictograph-enums";
import type { MotionData } from "../../../shared/domain/models/MotionData";
import type { PropPlacementData } from "../../domain/models/PropPlacementData";
import type { PropRenderData } from "../../domain/models/PropRenderData";
import type { IPropSvgLoader } from "../contracts/IPropSvgLoader";

@injectable()
export class PropSvgLoader implements IPropSvgLoader {
  // üöÄ OPTIMIZATION: Multi-level caching
  private rawSvgCache = new Map<string, string>(); // path -> raw SVG text
  private transformedSvgCache = new Map<string, PropRenderData>(); // path:color -> transformed data
  private loadingPromises = new Map<string, Promise<string>>(); // path -> loading promise (deduplication)
  private metadataCache = new Map<
    string,
    {
      viewBox: { width: number; height: number };
      center: { x: number; y: number };
    }
  >();

  // Performance monitoring
  private cacheHits = 0;
  private cacheMisses = 0;
  /**
   * Load prop SVG data with color transformation
   * üöÄ OPTIMIZED: Checks transformed cache first, then raw cache, then fetches
   * @param propData - Prop placement data
   * @param motionData - Motion data including prop type
   * @param useAnimatedVersion - If true, loads {propType}_animated.svg for animation canvas (300px width)
   */
  async loadPropSvg(
    propData: PropPlacementData,
    motionData: MotionData,
    useAnimatedVersion: boolean = false
  ): Promise<PropRenderData> {
    try {
      // Get prop type and color
      const propType = motionData.propType ?? "staff";
      const color = motionData.color ?? MotionColor.BLUE;

      // Create cache key including color for transformed prop cache
      // Use _animated version for animation canvas (scaled to 300px width)
      const suffix = useAnimatedVersion ? "_animated" : "";
      const path = `/images/props/${propType}${suffix}.svg`;
      const transformedCacheKey = `${path}:${color}`;

      // üöÄ OPTIMIZATION: Check transformed cache first (fastest path)
      if (this.transformedSvgCache.has(transformedCacheKey)) {
        this.cacheHits++;
        const cached = this.transformedSvgCache.get(transformedCacheKey)!;
        // Return with updated position/rotation (these are per-instance)
        return {
          ...cached,
          position: { x: propData.positionX, y: propData.positionY },
          rotation: propData.rotationAngle,
        };
      }

      this.cacheMisses++;

      // Fetch raw SVG (uses raw cache + deduplication)
      const originalSvgText = await this.fetchSvgContentCached(path);

      // Parse SVG for viewBox and center (uses metadata cache)
      const { viewBox, center } = this.parsePropSvgCached(
        originalSvgText,
        path
      );

      // Apply color transformation
      const coloredSvgText = this.applyColorToSvg(originalSvgText, color);

      // Extract SVG content
      const svgContent = this.extractSvgContent(coloredSvgText);

      const result: PropRenderData = {
        position: { x: propData.positionX, y: propData.positionY },
        rotation: propData.rotationAngle,
        svgData: {
          svgContent,
          viewBox,
          center,
        },
        loaded: true,
        error: null,
      };

      // üöÄ OPTIMIZATION: Cache transformed result
      this.transformedSvgCache.set(transformedCacheKey, result);

      return result;
    } catch (error) {
      console.error("‚ùå PropSvgLoader: Error loading prop SVG:", error);
      return {
        position: { x: 475, y: 475 },
        rotation: 0,
        svgData: null,
        loaded: false,
        error: error instanceof Error ? error.message : "Unknown error",
      };
    }
  }

  /**
   * üöÄ NEW: Fetch SVG content with caching and deduplication
   */
  private async fetchSvgContentCached(path: string): Promise<string> {
    // Check raw SVG cache
    if (this.rawSvgCache.has(path)) {
      return this.rawSvgCache.get(path)!;
    }

    // Check if already loading (prevents duplicate concurrent requests)
    if (this.loadingPromises.has(path)) {
      return this.loadingPromises.get(path)!;
    }

    // Create loading promise
    const loadingPromise = this.fetchSvgContent(path);
    this.loadingPromises.set(path, loadingPromise);

    try {
      const svgText = await loadingPromise;

      // Cache the raw SVG
      this.rawSvgCache.set(path, svgText);

      // Clean up loading promise
      this.loadingPromises.delete(path);

      return svgText;
    } catch (error) {
      // Clean up on error
      this.loadingPromises.delete(path);
      throw error;
    }
  }

  /**
   * Fetch SVG content from a given path - direct fetch
   */
  async fetchSvgContent(path: string): Promise<string> {
    const response = await fetch(path);
    if (!response.ok) {
      throw new Error(`Failed to fetch SVG: ${response.status}`);
    }
    return await response.text();
  }

  /**
   * üöÄ NEW: Parse prop SVG with caching
   */
  private parsePropSvgCached(
    svgText: string,
    cacheKey: string
  ): {
    viewBox: { width: number; height: number };
    center: { x: number; y: number };
  } {
    // Check metadata cache
    if (this.metadataCache.has(cacheKey)) {
      return this.metadataCache.get(cacheKey)!;
    }

    // Parse and cache
    const result = this.parsePropSvg(svgText);
    this.metadataCache.set(cacheKey, result);
    return result;
  }

  /**
   * Parse prop SVG to extract viewBox and center
   */
  private parsePropSvg(svgText: string): {
    viewBox: { width: number; height: number };
    center: { x: number; y: number };
  } {
    const parser = new DOMParser();
    const doc = parser.parseFromString(svgText, "image/svg+xml");
    const svgElement = doc.querySelector("svg");

    if (!svgElement) {
      throw new Error("Invalid SVG: No SVG element found");
    }

    // Extract viewBox
    const viewBoxAttr = svgElement.getAttribute("viewBox");
    let width = 100,
      height = 100;

    if (viewBoxAttr) {
      const [, , w, h] = viewBoxAttr.split(" ").map(Number);
      width = w || 100;
      height = h || 100;
    }

    return {
      viewBox: { width, height },
      center: { x: width / 2, y: height / 2 },
    };
  }

  /**
   * Apply color transformation to SVG - sophisticated approach matching arrows
   * Simple and correct: props are blue by default, change to red when needed
   * Also makes CSS class names unique to prevent conflicts between different colored props
   * Preserves accent colors like tan/gold for special prop features (e.g., minihoop grip)
   */
  private applyColorToSvg(svgText: string, color: MotionColor): string {
    const colorMap: Record<MotionColor, string> = {
      [MotionColor.BLUE]: "#2E3192",
      [MotionColor.RED]: "#ED1C24",
    };

    const targetColor = colorMap[color] || colorMap[MotionColor.BLUE];

    // Accent colors to preserve (like minihoop's gold/tan grip)
    const ACCENT_COLORS_TO_PRESERVE = [
      "#c9ac68", // Gold/tan color used for minihoop grip
    ];

    // Replace fill colors in both attribute and CSS style formats
    // BUT preserve accent colors and transparent fills
    let coloredSvg = svgText.replace(
      /fill="(#[0-9A-Fa-f]{3,6})"/gi,
      (match: string, capturedColor: string) => {
        const colorLower = capturedColor.toLowerCase();
        // Preserve accent colors
        if (
          ACCENT_COLORS_TO_PRESERVE.some(
            (accent) => accent.toLowerCase() === colorLower
          )
        ) {
          return match;
        }
        return `fill="${targetColor}"`;
      }
    );

    coloredSvg = coloredSvg.replace(
      /fill:\s*(#[0-9A-Fa-f]{3,6})/gi,
      (match: string, capturedColor: string) => {
        const colorLower = capturedColor.toLowerCase();
        // Preserve accent colors
        if (
          ACCENT_COLORS_TO_PRESERVE.some(
            (accent) => accent.toLowerCase() === colorLower
          )
        ) {
          return match;
        }
        return `fill:${targetColor}`;
      }
    );

    // Make CSS class names unique for each color to prevent conflicts
    // Replace .st0, .st1, etc. with .st0-blue, .st1-blue, etc.
    const colorSuffix = color.toLowerCase();
    coloredSvg = coloredSvg.replace(/\.st(\d+)/g, `.st$1-${colorSuffix}`);

    // Also update class references in elements
    coloredSvg = coloredSvg.replace(
      /class="st(\d+)"/g,
      `class="st$1-${colorSuffix}"`
    );

    // Remove the centerPoint circle entirely to prevent unwanted visual elements
    coloredSvg = coloredSvg.replace(
      /<circle[^>]*id="centerPoint"[^>]*\/?>/,
      ""
    );

    return coloredSvg;
  }

  /**
   * Extract SVG content (remove outer SVG wrapper)
   */
  private extractSvgContent(svgText: string): string {
    const parser = new DOMParser();
    const doc = parser.parseFromString(svgText, "image/svg+xml");
    const svgElement = doc.querySelector("svg");

    if (!svgElement) {
      return svgText;
    }

    return svgElement.innerHTML;
  }

  /**
   * üöÄ NEW: Clear caches (useful for testing or memory management)
   */
  clearCache(): void {
    this.rawSvgCache.clear();
    this.transformedSvgCache.clear();
    this.loadingPromises.clear();
    this.metadataCache.clear();
    this.cacheHits = 0;
    this.cacheMisses = 0;
  }

  /**
   * üöÄ NEW: Get cache statistics for performance monitoring
   */
  getCacheStats() {
    return {
      rawCacheSize: this.rawSvgCache.size,
      transformedCacheSize: this.transformedSvgCache.size,
      metadataCacheSize: this.metadataCache.size,
      cacheHits: this.cacheHits,
      cacheMisses: this.cacheMisses,
      hitRate:
        this.cacheHits + this.cacheMisses > 0
          ? (
              (this.cacheHits / (this.cacheHits + this.cacheMisses)) *
              100
            ).toFixed(2) + "%"
          : "0%",
    };
  }
}

/**
 * Static/Dash Motion Handler Implementation
 */

import type { MotionColor, VectorDirection } from "$shared";
import { GridLocation, GridMode, type MotionData } from "$shared";

import type { BoxLoc, DiamondLoc } from "../../domain/direction/DirectionMaps";
import {
  BOX_NON_RADIAL_MAP,
  BOX_RADIAL_MAP,
  DIAMOND_NON_RADIAL_MAP,
  DIAMOND_RADIAL_MAP,
} from "../../domain/direction/DirectionMaps";
import type { IDirectionCalculator } from "../contracts/IDirectionCalculator";
import type { IOrientationChecker } from "../contracts/IOrientationChecker";

export class StaticDashMotionHandler implements IDirectionCalculator {
  constructor(private orientationChecker: IOrientationChecker) {}

  calculate(motionData: MotionData): VectorDirection | null {
    const location = motionData.endLocation;
    const gridMode = this.getGridMode(location);
    const isRadial = this.orientationChecker.isRadial();

    if (gridMode === GridMode.DIAMOND) {
      const map = isRadial ? DIAMOND_RADIAL_MAP : DIAMOND_NON_RADIAL_MAP;
      return (
        map[location as DiamondLoc][motionData.color] ?? null
      );
    }

    const map = isRadial ? BOX_RADIAL_MAP : BOX_NON_RADIAL_MAP;
    return map[location as BoxLoc][motionData.color] ?? null;
  }

  private getGridMode(location: string): GridMode {
    const cardinalLocations = [
      GridLocation.NORTH,
      GridLocation.SOUTH,
      GridLocation.EAST,
      GridLocation.WEST,
    ] as string[];
    return cardinalLocations.includes(location)
      ? GridMode.DIAMOND
      : GridMode.BOX;
  }
}

/**
 * Pictograph Prop State
 *
 * Manages prop positioning, assets, and lifecycle with user-selected prop types.
 * Independent sub-state - no dependencies on arrow state.
 */

import type { MotionData,PictographData, PropType } from "$shared";

import type { PropAssets, PropPosition } from "../domain/models";
import type { IPropPlacementService } from "../services/contracts/IPropPlacementService";
import type { IPropSvgLoader } from "../services/contracts/IPropSvgLoader";
import type { IPropTypeConfigurationService } from "../services/contracts/IPropTypeConfigurationService";

export interface PropState {
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;
  calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void>;
}

export function createPropState(
  propSvgLoader: IPropSvgLoader,
  propPlacementService: IPropPlacementService,
  propTypeConfigService: IPropTypeConfigurationService
): PropState {
  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  async function calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void> {
    if (!pictographData?.motions) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Use the prop type configuration service to map UI type to filename
      const propTypeFilename =
        propTypeConfigService.mapPropTypeToFilename(userPropType);

      // Use the service to create pictograph data with all props using user's selected type
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData =
        propTypeConfigService.applyPropTypeToPictographData(
          pictographData,
          userPropType
        );

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(pictographData.motions).map(
        async ([color, motionData]) => {
          try {
            if (!motionData) {
              throw new Error("Motion data is undefined");
            }
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // This ensures all props render as the user's chosen type
            const motionDataWithUserProp: MotionData = {
              motionType: motionData.motionType,
              rotationDirection: motionData.rotationDirection,
              startLocation: motionData.startLocation,
              endLocation: motionData.endLocation,
              turns: motionData.turns,
              startOrientation: motionData.startOrientation,
              endOrientation: motionData.endOrientation,
              isVisible: motionData.isVisible,
              propType: propTypeFilename as PropType,
              arrowLocation: motionData.arrowLocation,
              color: motionData.color,
              gridMode: motionData.gridMode,
              arrowPlacementData: motionData.arrowPlacementData,
              propPlacementData: motionData.propPlacementData,
              ...(motionData.prefloatMotionType !== undefined && {
                prefloatMotionType: motionData.prefloatMotionType,
              }),
              ...(motionData.prefloatRotationDirection !== undefined && {
                prefloatRotationDirection: motionData.prefloatRotationDirection,
              }),
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp
              ),
              propPlacementService.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssetsData = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssetsData;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        }
      );

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("‚ö†Ô∏è Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("‚ùå Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  return {
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },
    calculatePropPositions,
  };
}

import type { PictographData } from "../models";

export function createPictographData(
  data: Partial<PictographData> = {}
): PictographData {
  // Build object conditionally using object spread to avoid undefined assignments
  return {
    id: data.id ?? crypto.randomUUID(),
    motions: data.motions ?? {},
    // Only include optional properties if they are not undefined
    ...(data.letter !== undefined && { letter: data.letter }),
    ...(data.startPosition !== undefined && {
      startPosition: data.startPosition,
    }),
    ...(data.endPosition !== undefined && { endPosition: data.endPosition }),
  };
}

/**
 * Pictograph Zod Schemas
 *
 * Runtime validation schemas for pictograph-specific data boundaries:
 * - Motion data validation
 * - Pictograph data validation
 * - Arrow and prop placement validation
 *
 * ‚ö†Ô∏è  USAGE POLICY: Only use for data boundaries with external sources!
 *     Don't use for internal service constructors or component props.
 */

import { z } from "zod";

// Use barrel exports for consistency
import { Letter } from "../../../../foundation/domain/models/Letter";
import { GridLocation, GridPosition } from "../../../grid/domain";
import { PropType } from "../../../prop/domain";
import {
  MotionColor,
  MotionType,
  Orientation,
  RotationDirection,
} from "../enums/pictograph-enums";

// ============================================================================
// COORDINATE AND PLACEMENT SCHEMAS
// ============================================================================

const CoordinateSchema = z
  .object({
    x: z.number(),
    y: z.number(),
  })
  .nullable();

const ArrowPlacementDataSchema = z.object({
  positionX: z.number().default(0.0),
  positionY: z.number().default(0.0),
  rotationAngle: z.number().default(0.0),
  coordinates: CoordinateSchema.default(null),
  svgCenter: CoordinateSchema.default(null),
  svgMirrored: z.boolean().default(false),
});

const PropPlacementDataSchema = z.object({
  positionX: z.number().default(0.0),
  positionY: z.number().default(0.0),
  rotationAngle: z.number().default(0.0),
  coordinates: CoordinateSchema.default(null),
  svgCenter: CoordinateSchema.default(null),
});

// ============================================================================
// MOTION AND PICTOGRAPH SCHEMAS
// ============================================================================

const MotionDataSchema = z.object({
  motionType: z.nativeEnum(MotionType).default(MotionType.STATIC),
  rotationDirection: z
    .nativeEnum(RotationDirection)
    .default(RotationDirection.NO_ROTATION),
  startLocation: z.nativeEnum(GridLocation).default(GridLocation.NORTH),
  endLocation: z.nativeEnum(GridLocation).default(GridLocation.NORTH),
  turns: z.union([z.number(), z.literal("fl")]).default(0.0),
  startOrientation: z.nativeEnum(Orientation).default(Orientation.IN),
  endOrientation: z.nativeEnum(Orientation).default(Orientation.IN),
  isVisible: z.boolean().default(true),
  propType: z.nativeEnum(PropType).default(PropType.STAFF),
  arrowLocation: z.nativeEnum(GridLocation).default(GridLocation.NORTH),
  color: z.nativeEnum(MotionColor).default(MotionColor.BLUE),
  arrowPlacementData: ArrowPlacementDataSchema.default({}),
  propPlacementData: PropPlacementDataSchema.default({}),
  prefloatMotionType: z.nativeEnum(MotionType).nullable().default(null),
  prefloatRotationDirection: z
    .nativeEnum(RotationDirection)
    .nullable()
    .default(null),
});

const PictographDataSchema = z.object({
  id: z
    .string()
    .min(1)
    .default(() => crypto.randomUUID()),
  letter: z.nativeEnum(Letter).nullable().default(null),
  startPosition: z.nativeEnum(GridPosition).nullable().default(null),
  endPosition: z.nativeEnum(GridPosition).nullable().default(null),
  motions: z.record(z.nativeEnum(MotionColor), MotionDataSchema).default({}),
});

// ============================================================================
// EXPORTS
// ============================================================================

export {
  ArrowPlacementDataSchema,
  CoordinateSchema,
  MotionDataSchema,
  PictographDataSchema,
  PropPlacementDataSchema,
};

/**
 * Data Transformation Service
 *
 * Handles data transformation and adaptation between different formats.
 * Extracted from PictographRenderingService.
 */

import { injectable } from "inversify";

import type { BeatData } from "../../../../../modules/create/shared/domain/models/BeatData";
import type { GridMode } from "../../../grid/domain/enums/grid-enums";
import type {
  GridData,
  GridPointData as RawGridData,
} from "../../../grid/domain/models/grid-models";
import { createPictographData } from "../../domain/factories/createPictographData";
import type { MotionData, PictographData } from "../../domain/models";
import type { IDataTransformer } from "../contracts/IDataTransformer";

// Interface moved to contracts/IDataTransformer.ts

@injectable()
export class DataTransformer implements IDataTransformer {
  /**
   * Convert beat data to pictograph data
   */
  beatToPictographData(beat: BeatData): PictographData {
    const motions: Record<string, MotionData> = {};
    if (beat.motions["blue"]) motions["blue"] = beat.motions["blue"];
    if (beat.motions["red"]) motions["red"] = beat.motions["red"];
    return createPictographData({
      id: `beat-${beat.beatNumber}`,
      motions,
      letter: beat.letter ?? null,
    });
  }

  /**
   * Adapt raw grid data to match the interface requirements
   */
  adaptGridData(rawGridData: RawGridData, mode: GridMode): GridData {
    // Filter out null coordinates and adapt to interface
    const adaptPoints = (
      points: Record<string, { coordinates: { x: number; y: number } | null }>
    ) => {
      const adapted: Record<string, { coordinates: { x: number; y: number } }> =
        {};
      for (const [key, point] of Object.entries(points)) {
        if (point.coordinates) {
          adapted[key] = { coordinates: point.coordinates };
        }
      }
      return adapted;
    };

    return {
      gridMode: mode,
      centerX: 0.0,
      centerY: 0.0,
      radius: 100.0,
      gridPointData: {
        allLayer2PointsNormal: adaptPoints(
          rawGridData.allLayer2PointsNormal ?? {}
        ),
        allHandPointsNormal: adaptPoints(rawGridData.allHandPointsNormal ?? {}),
        allHandPointsStrict: {},
        allLayer2PointsStrict: {},
        allOuterPoints: {},
        centerPoint: { coordinates: { x: 475, y: 475 } },
      },
    };
  }
}

/**
 * Pictograph State Management - Svelte 5 runes
 *
 * Reactive state management for pictograph rendering and arrow positioning.
 * Wraps pictograph services with runes for UI reactivity.
 * Follows TKA architecture: services handle business logic, runes handle reactivity.
 */

import type {
  IComponentManagementService,
  IDataTransformationService,
  MotionColor,
  MotionData,
  PictographData,
} from "$shared";
import { PropType,resolve } from "$shared";
import { loadSharedModules } from "$shared/inversify/container";
import { TYPES } from "$shared/inversify/types";

import { getSettings } from "../../../application/state/app-state.svelte";
import type { ArrowAssets } from "../../arrow/orchestration/domain/arrow-models";
import type { IArrowLifecycleManager } from "../../arrow/orchestration/services/contracts/IArrowLifecycleManager";
import type { PropAssets, PropPosition } from "../../prop/domain/models";
import type { IPropPlacementService } from "../../prop/services/contracts/IPropPlacementService";
import type { IPropSvgLoader } from "../../prop/services/contracts/IPropSvgLoader";

export interface PictographState {
  // Data state
  readonly effectivePictographData: PictographData | null;
  readonly hasValidData: boolean;
  readonly displayLetter: string | null;
  readonly motionsToRender: Array<{
    color: MotionColor;
    motionData: MotionData;
  }>;
  readonly requiredComponents: string[];

  // Arrow positioning state
  readonly arrowPositions: Record<
    string,
    { x: number; y: number; rotation: number }
  >;
  readonly arrowMirroring: Record<string, boolean>;
  readonly arrowAssets: Record<string, ArrowAssets>;
  readonly showArrows: boolean;

  // Prop positioning state
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;

  // Loading state
  readonly isLoading: boolean;
  readonly isLoaded: boolean;
  readonly errorMessage: string | null;
  readonly loadedComponents: Set<string>;
  readonly allComponentsLoaded: boolean;

  // Actions
  calculateArrowPositions(): Promise<void>;
  calculatePropPositions(): Promise<void>;
  handleComponentLoaded(componentName: string): void;
  handleComponentError(componentName: string, error: string): void;
  clearLoadingState(): void;
  updatePictographData(newData: PictographData | null): void;
}

/**
 * Creates reactive state for pictograph rendering and management
 */
export function createPictographState(
  initialPictographData: PictographData | null = null
): PictographState {
  // Services will be resolved asynchronously to avoid container initialization errors
  let dataTransformationService: IDataTransformationService | null = null;
  let componentManagementService: IComponentManagementService | null = null;
  let arrowLifecycleManager: IArrowLifecycleManager | null = null;
  let propSvgLoader: IPropSvgLoader | null = null;
  let propPlacementService: IPropPlacementService | null = null;
  let servicesInitialized = $state(false);

  // Initialize services asynchronously
  async function initializeServices() {
    // Skip if already initialized or currently initializing
    if (servicesInitialized || initializationPromise) {
      await initializationPromise;
      return;
    }

    initializationPromise = (async () => {
      try {
        // CRITICAL: Ensure Tier 2 (pictograph module) is fully loaded before resolving services
        // This prevents race conditions where services are accessed before bindings exist
        await loadSharedModules();

        dataTransformationService = resolve<IDataTransformationService>(
          TYPES.IDataTransformationService
        );
        componentManagementService = resolve<IComponentManagementService>(
          TYPES.IComponentManagementService
        );
        arrowLifecycleManager = resolve<IArrowLifecycleManager>(TYPES.IArrowLifecycleManager);
        propSvgLoader = resolve<IPropSvgLoader>(TYPES.IPropSvgLoader);
        propPlacementService = resolve<IPropPlacementService>(TYPES.IPropPlacementService);
        servicesInitialized = true;
      } catch (error) {
        console.error("Failed to initialize pictograph services:", error);
        errorMessage = `Service initialization failed: ${String(error)}`;
        initializationPromise = null; // Reset so we can retry
        throw error;
      }
    })();

    await initializationPromise;
  }

  // Track initialization promise to prevent duplicate initialization
  let initializationPromise: Promise<void> | null = null;

  // Helper to ensure services are initialized before use
  async function ensureServicesInitialized() {
    if (!servicesInitialized) {
      await initializeServices();
    }
  }

  // Input data state
  let pictographData = $state<PictographData | null>(initialPictographData);

  // Component loading state
  let errorMessage = $state<string | null>(null);
  let loadedComponents = $state(new Set<string>());

  // Arrow positioning state
  let arrowPositions = $state<
    Record<string, { x: number; y: number; rotation: number }>
  >({});
  let arrowMirroring = $state<Record<string, boolean>>({});
  let arrowAssets = $state<Record<string, ArrowAssets>>({});
  let showArrows = $state(false);

  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  // Global prop type from settings - reactive to settings changes
  let currentPropType = $state<string | undefined>(undefined);

  // Watch for prop type changes in settings and trigger re-render
  $effect(() => {
    const settings = getSettings();
    const newPropType = settings.propType;

    // Only trigger recalculation if prop type actually changed and we have valid data
    if (newPropType !== currentPropType && dataState.hasValidData) {
      currentPropType = newPropType;
      void calculatePropPositions();
    } else if (currentPropType === undefined) {
      // Initialize on first run
      currentPropType = newPropType;
    }
  });

  // Derived data transformation state - only when services are ready
  const dataState = $derived.by(() => {
    if (!servicesInitialized || !dataTransformationService) {
      return {
        hasValidData: false,
        effectivePictographData: null,
        transformedData: null,
      };
    }
    return dataTransformationService.transformPictographData(pictographData);
  });

  // Derived component requirements - only when services are ready
  const requiredComponents = $derived.by(() => {
    if (!servicesInitialized || !componentManagementService) {
      return [];
    }
    return componentManagementService.getRequiredComponents(pictographData);
  });

  // Derived loading states
  const allComponentsLoaded = $derived.by(() => {
    return requiredComponents.every((component: string) =>
      loadedComponents.has(component)
    );
  });

  const isLoading = $derived.by(() => {
    return dataState.hasValidData && !allComponentsLoaded;
  });

  const isLoaded = $derived.by(() => {
    return dataState.hasValidData && allComponentsLoaded;
  });

  // Effect to recalculate positions when data changes
  $effect(() => {
    const currentData = dataState.effectivePictographData;
    if (currentData) {
      errorMessage = null;

      // Explicitly track motion data properties that affect positioning
      // This ensures recalculation when turns/orientations change (e.g., via edit panel)
      const redMotion = currentData.motions.red;
      const blueMotion = currentData.motions.blue;

      // Track key properties that affect beta offset calculations:
      // - endOrientation (radial vs non-radial determines if offset is applied)
      // - endLocation (both props must end at same location for beta offset)
      // - turns (changes orientation, which affects offset logic)
      if (redMotion) {
        void redMotion.endOrientation;
        void redMotion.endLocation;
        void redMotion.turns;
      }
      if (blueMotion) {
        void blueMotion.endOrientation;
        void blueMotion.endLocation;
        void blueMotion.turns;
      }

      // Don't clear loadedComponents - keep elements visible during transitions
      // Recalculate arrow and prop positions when data changes
      void calculateArrowPositions();
      void calculatePropPositions();
    }
  });

  // Actions
  async function calculateArrowPositions(): Promise<void> {
    // Ensure services are initialized before attempting calculations
    await ensureServicesInitialized();

    const currentData = dataState.effectivePictographData;

    if (
      !currentData?.motions ||
      !servicesInitialized ||
      !arrowLifecycleManager
    ) {
      // Only clear if we don't have valid data - don't clear during transitions
      arrowPositions = {};
      arrowMirroring = {};
      arrowAssets = {};
      showArrows = true;
      return;
    }

    try {
      // Use the arrow lifecycle manager to coordinate complete arrow loading
      const arrowLifecycleResult =
        await arrowLifecycleManager.coordinateArrowLifecycle(currentData);

      // Only update state after async loading completes - keeps old data visible during transitions
      arrowPositions = arrowLifecycleResult.positions;
      arrowMirroring = arrowLifecycleResult.mirroring;
      arrowAssets = arrowLifecycleResult.assets;
      showArrows =
        arrowLifecycleResult.allReady &&
        Object.keys(arrowLifecycleResult.positions).length > 0;

      // Log any errors
      if (Object.keys(arrowLifecycleResult.errors).length > 0) {
        console.warn(
          "‚ö†Ô∏è Arrow lifecycle had errors:",
          arrowLifecycleResult.errors
        );
      }
    } catch (error) {
      console.error("‚ùå Arrow lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      arrowPositions = {};
      arrowMirroring = {};
      arrowAssets = {};
      showArrows = false;
    }
  }

  async function calculatePropPositions(): Promise<void> {
    // Ensure services are initialized before attempting calculations
    await ensureServicesInitialized();

    const currentData = dataState.effectivePictographData;

    if (
      !currentData?.motions ||
      !servicesInitialized ||
      !propSvgLoader ||
      !propPlacementService
    ) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Get the user's selected prop type from settings
      const settings = getSettings();
      const selectedPropType = settings.propType ?? "Staff";

      // Map PropTypeTab IDs to actual filenames
      // PropTypeTab uses capitalized IDs, but filenames have specific formats
      const propTypeMapping: Record<string, string> = {
        Staff: "staff",
        Simplestaff: "simple_staff",
        Club: "club",
        Fan: "fan",
        Triad: "triad",
        Minihoop: "minihoop",
        Buugeng: "buugeng",
        Triquetra: "triquetra",
        Sword: "sword",
        Chicken: "chicken",
        Hand: "hand",
        Guitar: "guitar",
        Ukulele: "ukulele",
      };

      const userPropType =
        propTypeMapping[selectedPropType] || selectedPropType.toLowerCase();

      // Create an updated pictographData with all props set to user's selected type
      // UNLESS they're already set to "hand" (from hand path assembly mode)
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData = {
        ...currentData,
        motions: {
          red: currentData.motions.red
            ? {
                ...currentData.motions.red,
                propType: (currentData.motions.red.propType === PropType.HAND
                  ? PropType.HAND
                  : userPropType) as PropType,
              }
            : currentData.motions.red,
          blue: currentData.motions.blue
            ? {
                ...currentData.motions.blue,
                propType: (currentData.motions.blue.propType === PropType.HAND
                  ? PropType.HAND
                  : userPropType) as PropType,
              }
            : currentData.motions.blue,
        },
      };

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(currentData.motions)
        .filter(
          (entry): entry is [string, MotionData] => entry[1] !== undefined
        )
        .map(async ([color, motionData]: [string, MotionData]) => {
          try {
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // UNLESS it's already set to "hand" (from hand path assembly mode)
            // This ensures hand path assembly always uses hands regardless of user settings
            // Cast to PropType - PropSvgLoader uses it as a string for the path anyway
            const motionDataWithUserProp: MotionData = {
              ...motionData,
              propType: (motionData.propType === PropType.HAND
                ? PropType.HAND
                : userPropType) as PropType,
              motionType: motionData.motionType,
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader!.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp
              ),
              propPlacementService!.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssets = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssets;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        });

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("‚ö†Ô∏è Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("‚ùå Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  function handleComponentLoaded(componentName: string): void {
    loadedComponents.add(componentName);
    loadedComponents = new Set(loadedComponents); // Trigger reactivity
  }

  function handleComponentError(componentName: string, error: string): void {
    console.error(`‚ùå Component ${componentName} failed to load:`, error);
    errorMessage = `Failed to load ${componentName}: ${error}`;
  }

  function clearLoadingState(): void {
    errorMessage = null;
    loadedComponents.clear();
    loadedComponents = new Set(); // Trigger reactivity
  }

  // Update pictograph data (for external updates)
  function updatePictographData(newData: PictographData | null): void {
    pictographData = newData;
  }

  return {
    // Data state (derived)
    get effectivePictographData() {
      return dataState.effectivePictographData;
    },
    get hasValidData() {
      return dataState.hasValidData;
    },
    get displayLetter() {
      const data = dataState;
      return "displayLetter" in data ? data.displayLetter : null;
    },
    get motionsToRender() {
      const data = dataState;
      return "motionsToRender" in data ? data.motionsToRender : [];
    },
    get requiredComponents() {
      return requiredComponents;
    },

    // Arrow positioning state
    get arrowPositions() {
      return arrowPositions;
    },
    get arrowMirroring() {
      return arrowMirroring;
    },
    get arrowAssets() {
      return arrowAssets;
    },
    get showArrows() {
      return showArrows;
    },

    // Prop positioning state
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },

    // Loading state
    get isLoading() {
      return isLoading;
    },
    get isLoaded() {
      return isLoaded;
    },
    get errorMessage() {
      return errorMessage;
    },
    get loadedComponents() {
      return loadedComponents;
    },
    get allComponentsLoaded() {
      return allComponentsLoaded;
    },

    // Actions
    calculateArrowPositions,
    calculatePropPositions,
    handleComponentLoaded,
    handleComponentError,
    clearLoadingState,
    updatePictographData,
  };
}

/**
 * Pictograph Prop State
 *
 * Manages prop positioning, assets, and lifecycle with user-selected prop types.
 * Independent sub-state - no dependencies on arrow state.
 */

import type { MotionData,PictographData, PropType } from "$shared";

import type { PropAssets, PropPosition } from "../../../prop/domain/models";
import type { IPropPlacementService } from "../../../prop/services/contracts/IPropPlacementService";
import type { IPropSvgLoader } from "../../../prop/services/contracts/IPropSvgLoader";
import type { IPropTypeConfigurationService } from "../../../prop/services/contracts/IPropTypeConfigurationService";

export interface PictographPropState {
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;
  calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void>;
}

export function createPictographPropState(
  propSvgLoader: IPropSvgLoader,
  propPlacementService: IPropPlacementService,
  propTypeConfigService: IPropTypeConfigurationService,
  useAnimatedProps: boolean = false
): PictographPropState {
  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  async function calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void> {
    if (!pictographData?.motions) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Use the prop type configuration service to map UI type to filename
      const propTypeFilename =
        propTypeConfigService.mapPropTypeToFilename(userPropType);

      // Use the service to create pictograph data with all props using user's selected type
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData =
        propTypeConfigService.applyPropTypeToPictographData(
          pictographData,
          userPropType
        );

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(pictographData.motions).map(
        async ([color, motionData]) => {
          try {
            if (!motionData) {
              throw new Error("Motion data is undefined");
            }
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // This ensures all props render as the user's chosen type
            const motionDataWithUserProp: MotionData = {
              motionType: motionData.motionType,
              rotationDirection: motionData.rotationDirection,
              startLocation: motionData.startLocation,
              endLocation: motionData.endLocation,
              turns: motionData.turns,
              startOrientation: motionData.startOrientation,
              endOrientation: motionData.endOrientation,
              isVisible: motionData.isVisible,
              propType: propTypeFilename as PropType,
              arrowLocation: motionData.arrowLocation,
              color: motionData.color,
              gridMode: motionData.gridMode,
              arrowPlacementData: motionData.arrowPlacementData,
              propPlacementData: motionData.propPlacementData,
              ...(motionData.prefloatMotionType !== undefined && {
                prefloatMotionType: motionData.prefloatMotionType,
              }),
              ...(motionData.prefloatRotationDirection !== undefined && {
                prefloatRotationDirection: motionData.prefloatRotationDirection,
              }),
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp,
                useAnimatedProps
              ),
              propPlacementService.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssetsData = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssetsData;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        }
      );

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("‚ö†Ô∏è Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("‚ùå Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  return {
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },
    calculatePropPositions,
  };
}

/**
 * Visibility State Manager - Modern Web Implementation
 *
 * Replicates the sophisticated visibility system from the legacy desktop app.
 * Manages complex dependencies between motion visibility and dependent glyphs.
 */

import type { AppSettings } from "$shared";
import { MotionColor } from "$shared";

type VisibilityObserver = () => void;

type VisibilityCategory = "glyph" | "motion" | "non_radial" | "all" | "buttons";

interface VisibilitySettings {
  // Motion visibility (independent)
  red_motion: boolean;
  blue_motion: boolean;

  // Independent glyphs
  Reversals: boolean;

  // Dependent glyphs (only available when both motions are visible)
  TKA: boolean;
  VTG: boolean;
  Elemental: boolean;
  Positions: boolean;

  // TKA sub-elements (dependent on TKA glyph)
  TurnNumbers: boolean;

  // Grid elements
  nonRadialPoints: boolean;
}

export class VisibilityStateManager {
  private settings: VisibilitySettings;
  private observers: Map<VisibilityCategory, Set<VisibilityObserver>> =
    new Map();

  // Dependent glyphs that require both motions to be visible
  private readonly DEPENDENT_GLYPHS = ["TKA", "VTG", "Elemental", "Positions"];

  // Sub-elements that depend on their parent glyph
  private readonly TKA_SUB_ELEMENTS = ["TurnNumbers"];

  constructor(initialSettings?: Partial<AppSettings>) {
    // Initialize with defaults matching desktop app
    this.settings = {
      // Motion defaults - both visible
      red_motion: true,
      blue_motion: true,

      // Independent glyph defaults
      Reversals: true,

      // Dependent glyph defaults
      TKA: true,
      VTG: false,
      Elemental: false,
      Positions: false,

      // TKA sub-element defaults
      TurnNumbers: true,

      // Grid defaults
      nonRadialPoints: false,

      // Override with any provided settings
      ...this.convertAppSettingsToVisibility(initialSettings),
    };

    // Initialize observer categories
    this.observers.set("glyph", new Set());
    this.observers.set("motion", new Set());
    this.observers.set("non_radial", new Set());
    this.observers.set("all", new Set());
    this.observers.set("buttons", new Set());
  }

  /**
   * Convert AppSettings visibility format to internal format
   */
  private convertAppSettingsToVisibility(
    _appSettings?: Partial<AppSettings>
  ): Partial<VisibilitySettings> {
    // Visibility settings are no longer in AppSettings, return defaults
    return {};
  }

  /**
   * Convert internal visibility format to AppSettings format
   * Note: Visibility settings are no longer part of AppSettings
   */
  public toAppSettings(): Record<string, boolean> {
    return {
      TKA: this.settings.TKA,
      Reversals: this.settings.Reversals,
      VTG: this.settings.VTG,
      Elemental: this.settings.Elemental,
      Positions: this.settings.Positions,
      TurnNumbers: this.settings.TurnNumbers,
      nonRadialPoints: this.settings.nonRadialPoints,
    };
  }

  /**
   * Register an observer for visibility changes
   */
  registerObserver(
    callback: VisibilityObserver,
    categories: VisibilityCategory[] = ["all"]
  ): void {
    categories.forEach((category) => {
      if (!this.observers.has(category)) {
        this.observers.set(category, new Set());
      }
      const categoryObservers = this.observers.get(category);
      if (categoryObservers) {
        categoryObservers.add(callback);
      }
    });
  }

  /**
   * Unregister an observer
   */
  unregisterObserver(callback: VisibilityObserver): void {
    this.observers.forEach((observerSet) => {
      observerSet.delete(callback);
    });
  }

  /**
   * Notify observers of changes
   */
  private notifyObservers(categories: VisibilityCategory[]): void {
    const callbacksToNotify = new Set<VisibilityObserver>();

    // Collect callbacks from specific categories
    categories.forEach((category) => {
      const observers = this.observers.get(category);
      if (observers) {
        console.log(
          `üì¢ [VisibilityManager] Notifying ${observers.size} observers for category: ${category}`
        );
        observers.forEach((callback) => callbacksToNotify.add(callback));
      }
    });

    // Always notify "all" observers
    const allObservers = this.observers.get("all");
    if (allObservers) {
      console.log(
        `üì¢ [VisibilityManager] Notifying ${allObservers.size} "all" observers`
      );
      allObservers.forEach((callback) => callbacksToNotify.add(callback));
    }

    console.log(
      `üì¢ [VisibilityManager] Total callbacks to execute: ${callbacksToNotify.size}`
    );

    // Execute callbacks
    callbacksToNotify.forEach((callback) => {
      try {
        callback();
      } catch (error) {
        console.error("Error in visibility observer:", error);
      }
    });
  }

  // ============================================================================
  // MOTION VISIBILITY
  // ============================================================================

  /**
   * Get motion visibility for a specific color
   */
  getMotionVisibility(color: MotionColor): boolean {
    return this.settings[
      `${color}_motion` as keyof VisibilitySettings
    ];
  }

  /**
   * Set motion visibility with constraint enforcement
   */
  setMotionVisibility(color: MotionColor, visible: boolean): void {
    const otherColor =
      color === MotionColor.RED ? MotionColor.BLUE : MotionColor.RED;

    const colorMotionKey = `${color}_motion` as keyof VisibilitySettings;
    const otherColorMotionKey =
      `${otherColor}_motion` as keyof VisibilitySettings;

    // Enforce constraint: at least one motion must remain visible
    if (!visible && !this.settings[otherColorMotionKey]) {
      // If trying to turn off the last visible motion, turn on the other one
      this.settings[colorMotionKey] = false;
      this.settings[otherColorMotionKey] = true;
      this.notifyObservers(["motion", "glyph", "buttons"]);
      return;
    }

    // Normal case
    this.settings[colorMotionKey] = visible;
    this.notifyObservers(["motion", "glyph", "buttons"]);
  }

  /**
   * Check if all motions are visible
   */
  areAllMotionsVisible(): boolean {
    return this.settings.red_motion && this.settings.blue_motion;
  }

  /**
   * Check if any motion is visible
   */
  isAnyMotionVisible(): boolean {
    return this.settings.red_motion || this.settings.blue_motion;
  }

  /**
   * Save current motion visibility state (for temporary overrides)
   */
  saveMotionVisibilityState(): { blue: boolean; red: boolean } {
    return {
      blue: this.settings.blue_motion,
      red: this.settings.red_motion,
    };
  }

  /**
   * Restore saved motion visibility state (after temporary overrides)
   */
  restoreMotionVisibilityState(savedState: {
    blue: boolean;
    red: boolean;
  }): void {
    this.settings.blue_motion = savedState.blue;
    this.settings.red_motion = savedState.red;
    this.notifyObservers(["motion", "glyph", "buttons"]);
  }

  // ============================================================================
  // GLYPH VISIBILITY
  // ============================================================================

  /**
   * Get glyph visibility considering dependencies
   */
  getGlyphVisibility(glyphType: string): boolean {
    const baseVisibility =
      (this.settings[glyphType as keyof VisibilitySettings]) ||
      false;

    // For TKA sub-elements, also check if TKA glyph is visible
    if (this.TKA_SUB_ELEMENTS.includes(glyphType)) {
      return baseVisibility && this.getGlyphVisibility("TKA");
    }

    // For dependent glyphs, also check if both motions are visible
    if (this.DEPENDENT_GLYPHS.includes(glyphType)) {
      return baseVisibility && this.areAllMotionsVisible();
    }

    // For independent glyphs, return direct visibility
    return baseVisibility;
  }

  /**
   * Set glyph visibility
   */
  setGlyphVisibility(glyphType: string, visible: boolean): void {
    if (glyphType in this.settings) {
      console.log(`üîß [VisibilityManager] Setting ${glyphType} to ${visible}`);
      (this.settings as unknown as Record<string, boolean>)[glyphType] =
        visible;
      console.log(
        "üì¢ [VisibilityManager] Notifying observers for glyph change"
      );
      this.notifyObservers(["glyph"]);
    }
  }

  /**
   * Get raw glyph visibility (user preference, ignoring dependencies)
   */
  getRawGlyphVisibility(glyphType: string): boolean {
    return (
      (this.settings[glyphType as keyof VisibilitySettings]) || false
    );
  }

  /**
   * Check if glyph is dependent on motion visibility
   */
  isGlyphDependent(glyphType: string): boolean {
    return this.DEPENDENT_GLYPHS.includes(glyphType);
  }

  // ============================================================================
  // NON-RADIAL POINTS
  // ============================================================================

  /**
   * Get non-radial points visibility
   */
  getNonRadialVisibility(): boolean {
    return this.settings.nonRadialPoints;
  }

  /**
   * Set non-radial points visibility
   */
  setNonRadialVisibility(visible: boolean): void {
    this.settings.nonRadialPoints = visible;
    this.notifyObservers(["non_radial"]);
  }

  // ============================================================================
  // UTILITY METHODS
  // ============================================================================

  /**
   * Get all visible glyph types
   */
  getVisibleGlyphs(): string[] {
    return ["TKA", "Reversals", "VTG", "Elemental", "Positions"].filter(
      (glyph) => this.getGlyphVisibility(glyph)
    );
  }

  /**
   * Get all enabled dependent glyphs (considering motion constraints)
   */
  getAvailableDependentGlyphs(): string[] {
    if (!this.areAllMotionsVisible()) {
      return [];
    }
    return this.DEPENDENT_GLYPHS.filter((glyph) =>
      this.getRawGlyphVisibility(glyph)
    );
  }

  /**
   * Update from external AppSettings
   */
  updateFromAppSettings(appSettings: AppSettings): void {
    const visibilityUpdate = this.convertAppSettingsToVisibility(appSettings);
    Object.assign(this.settings, visibilityUpdate);
    this.notifyObservers(["all"]);
  }

  /**
   * Get complete visibility state for debugging
   */
  getState(): VisibilitySettings {
    return { ...this.settings };
  }
}

// Global instance for the application
let globalVisibilityStateManager: VisibilityStateManager | null = null;

/**
 * Get or create the global visibility state manager
 */
export function getVisibilityStateManager(
  initialSettings?: Partial<AppSettings>
): VisibilityStateManager {
  if (!globalVisibilityStateManager) {
    globalVisibilityStateManager = new VisibilityStateManager(initialSettings);
  }
  return globalVisibilityStateManager;
}

/**
 * Reset the global visibility state manager (useful for testing)
 */
export function resetVisibilityStateManager(): void {
  globalVisibilityStateManager = null;
}

/**
 * Letter Border Colors
 *
 * Clean utility for getting letter border colors based on the desktop app's
 * PictographBorderManager dual border system.
 */

import type { Letter } from "$shared";
import { getLetterType } from "$shared";

import { LETTER_TYPE_COLORS } from "../domain/constants/pictograph-constants";

/**
 * Letter type border color mapping from desktop app
 * Format: [primary, secondary] for outer and inner borders
 */

/**
 * Get border colors for a letter
 */
export function getLetterBorderColors(letter: Letter | null | undefined): { primary: string; secondary: string } {
  if (letter === null || letter === undefined) {
    return { primary: "#000000", secondary: "#000000" };
  }

  const letterType = getLetterType(letter);
  const [primary, secondary] = LETTER_TYPE_COLORS[letterType] ?? [
    "#000000",
    "#000000",
  ];

  return { primary, secondary };
}

/**
 * Get primary border color for a letter
 */
export function getLetterBorderColor(
  letter: Letter | null | undefined
): string {
  return getLetterBorderColors(letter).primary;
}

// Legacy aliases for backward compatibility
export const getLetterBorderColorsSafe = getLetterBorderColors;
export const getLetterBorderColorSafe = getLetterBorderColor;

/**
 * Pictograph Rendering Utilities
 *
 * Direct composition functions for rendering pictographs without orchestration service.
 * This replaces the PictographRenderingService with explicit composition.
 */

import { Point } from "fabric";

import type { MotionColor } from "$shared";
import {
  GridMode,
  type IArrowPositioningOrchestrator,
  type IGridModeDeriver,
  type PictographData,
  resolve,
} from "$shared";
import { TYPES } from "$shared/inversify/types";

// TODO: These services have been archived - need to refactor this file
// import type { IOverlayRenderer } from "../../../../modules/animator/services/contracts/IOverlayRenderer";
// import type { ISvgUtilityService } from "../../../../modules/animator/services/implementations/SvgUtilityService";
import type { ArrowPosition } from "../../arrow";
import type { IArrowRenderer } from "../../arrow/rendering/services/contracts";
import type { IGridRenderingService } from "../../grid";

// TODO: These services are archived - defining minimal interfaces for compilation
interface ISvgUtilityService {
  createBaseSVG(): SVGElement;
  createErrorSVG(message: string): SVGElement;
}

interface IOverlayRenderer {
  renderOverlays(svg: SVGElement, data: PictographData): Promise<void>;
  renderIdLabel(svg: SVGElement, data: PictographData): void;
  renderDebugInfo(
    svg: SVGElement,
    data: PictographData,
    positions: Map<string, ArrowPosition>
  ): void;
}

export async function renderPictograph(
  data: PictographData
): Promise<SVGElement> {
  console.log("üé® renderPictograph called for letter:", data.letter);

  try {
    console.log("üé® Rendering pictograph:", data.letter);

    // Resolve required services directly
    const svgUtility = resolve<ISvgUtilityService>(TYPES.ISvgUtilityService);
    const gridRendering = resolve<IGridRenderingService>(
      TYPES.IGridRenderingService
    );
    const arrowRendering = resolve<IArrowRenderer>(TYPES.IArrowRenderer);
    const overlayRendering = resolve<IOverlayRenderer>(TYPES.IOverlayRenderer);
    const gridModeService = resolve<IGridModeDeriver>(TYPES.IGridModeDeriver);

    // Get arrow positioning from InversifyJS container
    const arrowPositioning: IArrowPositioningOrchestrator = resolve(
      TYPES.IArrowPositioningOrchestrator
    );

    // Create base SVG
    const svg = svgUtility.createBaseSVG();

    // Determine grid mode
    const blueMotion = data.motions.blue;
    const redMotion = data.motions.red;
    const gridMode: GridMode =
      blueMotion && redMotion
        ? gridModeService.deriveGridMode(blueMotion, redMotion)
        : GridMode.DIAMOND;

    // Render grid
    await gridRendering.renderGrid(svg, gridMode);

    // Calculate arrow positions
    const updatedPictographData =
      await arrowPositioning.calculateAllArrowPoints(data);

    // Render arrows
    for (const [color, motionData] of Object.entries(
      updatedPictographData.motions
    )) {
      if (motionData === undefined) continue;
      if (motionData.isVisible && motionData.arrowPlacementData) {
        const position = Object.assign(
          new Point(
            motionData.arrowPlacementData.positionX,
            motionData.arrowPlacementData.positionY
          ),
          { rotation: motionData.arrowPlacementData.rotationAngle }
        );
        await arrowRendering.renderArrowAtPosition(
          svg,
          color as MotionColor,
          position,
          motionData
        );
      }
    }

    // Render overlays
    await overlayRendering.renderOverlays(svg, data);
    overlayRendering.renderIdLabel(svg, data);

    // Render debug info
    const arrowPositions = new Map<string, ArrowPosition>();
    for (const [color, motionData] of Object.entries(
      updatedPictographData.motions
    )) {
      if (motionData === undefined) continue;
      if (motionData.isVisible && motionData.arrowPlacementData) {
        const arrowPosition = Object.assign(
          new Point(
            motionData.arrowPlacementData.positionX,
            motionData.arrowPlacementData.positionY
          ),
          { rotation: motionData.arrowPlacementData.rotationAngle }
        );
        arrowPositions.set(color, arrowPosition);
      }
    }
    overlayRendering.renderDebugInfo(svg, data, arrowPositions);

    return svg;
  } catch (error) {
    console.error("‚ùå Error rendering pictograph:", error);
    const errorMessage =
      error instanceof Error ? error.message : "Unknown error";
    const svgUtility = resolve<ISvgUtilityService>(TYPES.ISvgUtilityService);
    return svgUtility.createErrorSVG(errorMessage);
  }
}

/**
 * Batch render multiple pictographs
 */
export async function renderPictographs(
  dataArray: PictographData[]
): Promise<SVGElement[]> {
  const promises = dataArray.map((data) => renderPictograph(data));
  return Promise.all(promises);
}

/**
 * Unified PNG Metadata Extractor for TKA Sequences
 *
 * This class extracts ALL sequence metadata from the unified JSON structure
 * stored in the "metadata" tEXt chunk of PNG files. This includes:
 * - Sequence information (author, level, start position, etc.)
 * - Beat data (letters, motion types, attributes)
 * - All other metadata fields
 *
 * We use ONE consistent system - JSON metadata only.
 * No separate tEXt chunks for individual fields.
 */

export class PngMetadataExtractor {
  /**
   * Extract complete JSON metadata from a PNG file
   * @param filePath - Path to the PNG file (relative to static directory)
   * @returns Promise<Record<string, unknown>[]> - The complete sequence metadata as JSON array
   */
  static async extractMetadata(
    filePath: string
  ): Promise<Record<string, unknown>[]> {
    try {
      // Fetch the PNG file
      const response = await fetch(filePath);
      if (!response.ok) {
        throw new Error(
          `Failed to fetch PNG file: ${response.status} ${response.statusText}`
        );
      }

      const arrayBuffer = await response.arrayBuffer();
      const uint8Array = new Uint8Array(arrayBuffer);

      // Extract the unified JSON metadata from the "metadata" tEXt chunk
      const metadataJson = this.findTextChunk(uint8Array, "metadata");

      if (!metadataJson) {
        throw new Error("No unified JSON metadata found in PNG file");
      }

      // Parse and return the complete metadata structure
      const parsed = JSON.parse(metadataJson) as Record<string, unknown>;
      const sequence = parsed.sequence as Record<string, unknown>[] | undefined;
      return sequence ?? [parsed];
    } catch (error) {
      console.error("Error extracting PNG metadata:", error);
      throw error;
    }
  }

  /**
   * Find the unified JSON metadata tEXt chunk in PNG data
   *
   * We only look for the "metadata" keyword which contains the complete
   * JSON structure with all sequence information. This is our single
   * source of truth for all metadata fields.
   *
   * @param data - PNG file data as Uint8Array
   * @param keyword - Should always be "metadata" for TKA sequences
   * @returns string | null - The JSON metadata string or null if not found
   */
  private static findTextChunk(
    data: Uint8Array,
    keyword: string
  ): string | null {
    let offset = 8; // Skip PNG signature

    while (offset < data.length) {
      // Read chunk length (4 bytes, big-endian)
      const length =
        (data[offset]! << 24) |
        (data[offset + 1]! << 16) |
        (data[offset + 2]! << 8) |
        data[offset + 3]!;
      offset += 4;

      // Read chunk type (4 bytes)
      const type = String.fromCharCode(
        data[offset]!,
        data[offset + 1]!,
        data[offset + 2]!,
        data[offset + 3]!
      );
      offset += 4;

      // If this is a tEXt chunk, check if it contains our keyword
      if (type === "tEXt") {
        const chunkData = data.slice(offset, offset + length);
        const text = new TextDecoder("latin1").decode(chunkData);

        // Find the null separator between keyword and text
        const nullIndex = text.indexOf("\0");
        if (nullIndex !== -1) {
          const chunkKeyword = text.substring(0, nullIndex);
          if (chunkKeyword === keyword) {
            return text.substring(nullIndex + 1);
          }
        }
      }

      // Skip chunk data and CRC (4 bytes)
      offset += length + 4;
    }

    return null;
  }

  /**
   * Extract metadata for a specific sequence by name
   * @param sequenceName - Name of the sequence (e.g., "DKIIEJII")
   * @returns Promise<Record<string, unknown>[]> - The extracted metadata
   */
  static async extractSequenceMetadata(
    sequenceName: string
  ): Promise<Record<string, unknown>[]> {
    // URL-encode the sequence name for proper file path construction
    const encodedSequenceName = encodeURIComponent(sequenceName);

    // Try .meta.json sidecar files first (new system)
    const versionsToTry = [2, 1, 3];
    for (const version of versionsToTry) {
      const jsonPath = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver${version}.meta.json`;
      try {
        const response = await fetch(jsonPath);
        if (response.ok) {
          const jsonData = (await response.json()) as Record<string, unknown>;
          // Extract sequence array from the sidecar JSON structure
          const metadata = jsonData.metadata as Record<string, unknown> | undefined;
          const metadataSequence = metadata?.sequence as Record<string, unknown>[] | undefined;
          const directSequence = jsonData.sequence as Record<string, unknown>[] | undefined;
          return metadataSequence ?? directSequence ?? [];
        }
      } catch {
        // Continue to next version
        continue;
      }
    }

    // Fallback to PNG extraction (legacy system)
    const filePathV1 = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver1.png`;
    const filePathV2 = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver2.png`;

    try {
      return await this.extractMetadata(filePathV1);
    } catch {
      // Try version 2 if version 1 doesn't exist
      return this.extractMetadata(filePathV2);
    }
  }

  /**
   * Extract complete metadata structure for a specific sequence by name
   * @param sequenceName - Name of the sequence (e.g., "DKIIEJII")
   * @param thumbnailPath - Optional thumbnail path from sequence index to determine version
   * @returns Promise<{sequence: Record<string, unknown>[], date_added?: string, is_favorite?: boolean}> - The complete metadata structure
   */
  static async extractCompleteMetadata(
    sequenceName: string,
    thumbnailPath?: string
  ): Promise<{
    sequence: Record<string, unknown>[];
    date_added?: string;
    is_favorite?: boolean;
  }> {
    // URL-encode the sequence name for proper file path construction
    const encodedSequenceName = encodeURIComponent(sequenceName);

    // Try .meta.json sidecar files first (new system)
    // If we have thumbnail path, extract the version from it
    if (thumbnailPath) {
      const versionMatch = thumbnailPath.match(/_ver(\d+)\.webp$/);
      if (versionMatch) {
        const version = versionMatch[1];
        const jsonPath = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver${version}.meta.json`;
        try {
          const response = await fetch(jsonPath);
          if (response.ok) {
            const jsonData = (await response.json()) as Record<string, unknown>;
            const metadata = jsonData.metadata as {
              sequence: Record<string, unknown>[];
              date_added?: string;
              is_favorite?: boolean;
            } | undefined;
            return metadata ?? jsonData;
          }
        } catch (error) {
          // Fall back to version guessing if the specific version fails
        }
      }
    }

    // Try common versions for .meta.json files
    const versionsToTry = [2, 1, 3]; // Most sequences are ver2, some are ver1, rarely ver3+
    for (const version of versionsToTry) {
      const jsonPath = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver${version}.meta.json`;
      try {
        const response = await fetch(jsonPath);
        if (response.ok) {
          const jsonData = (await response.json()) as Record<string, unknown>;
          const metadata = jsonData.metadata as {
            sequence: Record<string, unknown>[];
            date_added?: string;
            is_favorite?: boolean;
          } | undefined;
          return metadata ?? jsonData;
        }
      } catch (error) {
        // Continue to next version silently
        continue;
      }
    }

    // Fallback to PNG extraction (legacy system)
    let response: Response | null = null;

    // Try PNG files if .meta.json not found
    if (thumbnailPath) {
      const versionMatch = thumbnailPath.match(/_ver(\d+)\.webp$/);
      if (versionMatch) {
        const version = versionMatch[1];
        const filePath = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver${version}.png`;
        try {
          response = await fetch(filePath);
          if (!response.ok) response = null;
        } catch (error) {
          // Continue to try other versions
        }
      }
    }

    // If we still don't have a response, try common versions for PNG
    if (!response?.ok) {
      for (const version of versionsToTry) {
        const filePath = `/gallery/${encodedSequenceName}/${encodedSequenceName}_ver${version}.png`;
        try {
          response = await fetch(filePath);
          if (response.ok) {
            break;
          }
        } catch (error) {
          // Continue to next version silently
          continue;
        }
      }
    }

    if (!response?.ok) {
      throw new Error(
        `Failed to fetch metadata for ${sequenceName}: No valid version found (tried both .meta.json and .png)`
      );
    }

    const arrayBuffer = await response.arrayBuffer();
    const uint8Array = new Uint8Array(arrayBuffer);

    // Extract the unified JSON metadata from the "metadata" tEXt chunk
    const metadataJson = this.findTextChunk(uint8Array, "metadata");

    if (!metadataJson) {
      throw new Error(
        `PNG file exists but contains no metadata tEXt chunk. File may have been created without sequence data.`
      );
    }

    // Parse and return the COMPLETE metadata structure (including top-level fields)
    return JSON.parse(metadataJson) as {
      sequence: Record<string, unknown>[];
      date_added?: string;
      is_favorite?: boolean;
    };
  }

  /**
   * Debug method to display complete unified metadata for a sequence
   *
   * This shows the entire JSON metadata structure including:
