/**
 * HMR State Backup Utility
 *
 * Provides automatic state backup to localStorage during development
 * to preserve application state across HMR reloads.
 */

// ============================================================================
// TYPES
// ============================================================================

export interface HMRStateBackupOptions {
  key: string;
  enabled?: boolean;
  debounceMs?: number;
}

export interface BackupState<T = unknown> {
  timestamp: number;
  data: T;
  version: string;
}

// ============================================================================
// HMR STATE BACKUP CLASS
// ============================================================================

export class HMRStateBackup<T = unknown> {
  private key: string;
  private enabled: boolean;
  private debounceMs: number;
  private debounceTimer: number | null = null;
  private version: string = "1.0.0";

  constructor(options: HMRStateBackupOptions) {
    this.key = `hmr-backup-${options.key}`;
    this.enabled = options.enabled !== undefined ? options.enabled : (import.meta.env.DEV || false);
    this.debounceMs = options.debounceMs ?? 500;
  }

  /**
   * Save state to localStorage with debouncing
   */
  save(state: T): void {
    if (!this.enabled || typeof localStorage === "undefined") {
      return;
    }

    // Clear existing timer
    if (this.debounceTimer) {
      clearTimeout(this.debounceTimer);
    }

    // Debounce the save operation
    this.debounceTimer = setTimeout(() => {
      try {
        const backupState: BackupState<T> = {
          timestamp: Date.now(),
          data: state,
          version: this.version,
        };

        localStorage.setItem(this.key, JSON.stringify(backupState));
        console.log(`üíæ HMR: State backed up for key: ${this.key}`);
      } catch (error) {
        console.warn(
          `‚ö†Ô∏è HMR: Failed to backup state for key: ${this.key}`,
          error
        );
      }
    }, this.debounceMs) as unknown as number;
  }

  /**
   * Load state from localStorage
   */
  load(): T | null {
    if (!this.enabled || typeof localStorage === "undefined") {
      return null;
    }

    try {
      const stored = localStorage.getItem(this.key);
      if (!stored) {
        return null;
      }

      const backupState = JSON.parse(stored) as BackupState;

      // Check if backup is recent (within last hour)
      const hourAgo = Date.now() - 60 * 60 * 1000;
      if (backupState.timestamp < hourAgo) {
        console.log(`üóëÔ∏è HMR: Removing stale backup for key: ${this.key}`);
        this.clear();
        return null;
      }

      console.log(`üìÇ HMR: State restored from backup for key: ${this.key}`);
      return backupState.data as T | null;
    } catch (error) {
      console.warn(
        `‚ö†Ô∏è HMR: Failed to load backup state for key: ${this.key}`,
        error
      );
      return null;
    }
  }

  /**
   * Clear backup from localStorage
   */
  clear(): void {
    if (typeof localStorage === "undefined") {
      return;
    }

    try {
      localStorage.removeItem(this.key);
      console.log(`üóëÔ∏è HMR: Backup cleared for key: ${this.key}`);
    } catch (error) {
      console.warn(
        `‚ö†Ô∏è HMR: Failed to clear backup for key: ${this.key}`,
        error
      );
    }
  }

  /**
   * Check if backup exists
   */
  hasBackup(): boolean {
    if (!this.enabled || typeof localStorage === "undefined") {
      return false;
    }

    return localStorage.getItem(this.key) !== null;
  }
}

// ============================================================================
// CONVENIENCE FUNCTIONS
// ============================================================================

/**
 * Create a simple HMR state backup instance
 */
export function createHMRBackup<T>(
  key: string,
  options?: Partial<HMRStateBackupOptions>
): HMRStateBackup<T> {
  return new HMRStateBackup<T>({
    key,
    ...options,
  });
}

/**
 * Create a reactive state with automatic HMR backup
 */
export function createBackedUpState<T>(
  key: string,
  initialValue: T,
  options?: Partial<HMRStateBackupOptions>
): {
  backup: HMRStateBackup<T>;
  getInitialValue: () => T;
  save: (value: T) => void;
  load: () => T | null;
  clear: () => void;
} {
  const backup = createHMRBackup<T>(key, options);

  return {
    backup,
    getInitialValue: () => backup.load() ?? initialValue,
    save: (value: T) => backup.save(value),
    load: () => backup.load(),
    clear: () => backup.clear(),
  };
}

// ============================================================================
// SVELTE INTEGRATION HELPERS
// ============================================================================

/**
 * Create a Svelte rune state with HMR backup
 * Usage in component:
 *
 * const { state, saveState } = createHMRState('my-form', { step: 1, data: {} });
 *
 * $effect(() => {
 *   saveState(state);
 * });
 */
export function createHMRState<T>(
  key: string,
  initialValue: T,
  options?: Partial<HMRStateBackupOptions>
) {
  const backup = createHMRBackup<T>(key, options);
  const restoredValue = backup.load() ?? initialValue;

  return {
    initialValue: restoredValue,
    saveState: (value: T) => backup.save(value),
    clearBackup: () => backup.clear(),
    hasBackup: () => backup.hasBackup(),
  };
}

/**
 * Clear all HMR backups (useful for cleanup)
 */
export function clearAllHMRBackups(): void {
  if (typeof localStorage === "undefined") {
    return;
  }

  const keys = Object.keys(localStorage);
  const hmrKeys = keys.filter((key) => key.startsWith("hmr-backup-"));

  hmrKeys.forEach((key) => {
    localStorage.removeItem(key);
  });

  console.log(`üóëÔ∏è HMR: Cleared ${hmrKeys.length} backup entries`);
}

/**
 * Instagram Media Upload API Endpoint
 *
 * Accepts media files (images/videos) and uploads them to Firebase Storage.
 * Returns a publicly accessible URL that Instagram Graph API can use.
 *
 * This endpoint is CRITICAL for production Instagram posting since Instagram
 * requires media to be hosted on a publicly accessible URL with appropriate CORS headers.
 */

import { error,json } from "@sveltejs/kit";
import { getDownloadURL,getStorage, ref, uploadBytes } from "firebase/storage";

import { app } from "$shared/auth/firebase";

import type { RequestHandler } from "./$types";

// Initialize Firebase Storage
const storage = getStorage(app);

// Maximum file sizes (Instagram limits)
const MAX_IMAGE_SIZE = 8 * 1024 * 1024; // 8MB
const MAX_VIDEO_SIZE = 100 * 1024 * 1024; // 100MB

// Allowed MIME types
const ALLOWED_IMAGE_TYPES = ["image/jpeg", "image/png", "image/gif"];
const ALLOWED_VIDEO_TYPES = ["video/mp4", "video/quicktime", "video/x-m4v"];

/**
 * POST /api/instagram/upload-media
 *
 * Uploads a media file to Firebase Storage and returns the public URL.
 *
 * Request Body (multipart/form-data):
 * - file: The media file to upload
 *
 * Response:
 * - url: Public URL to the uploaded file
 * - path: Storage path (for cleanup)
 * - expiresAt: When the file will be automatically deleted (24 hours)
 */
export const POST: RequestHandler = async ({ request }) => {
  try {
    // Parse multipart form data
    const formData = await request.formData();
    const file = formData.get("file");

    // Validate file exists
    if (!file || !(file instanceof File)) {
      throw error(400, "No file provided");
    }

    // Validate file type
    const isImage = ALLOWED_IMAGE_TYPES.includes(file.type);
    const isVideo = ALLOWED_VIDEO_TYPES.includes(file.type);

    if (!isImage && !isVideo) {
      throw error(400, `Unsupported file type: ${file.type}`);
    }

    // Validate file size
    const maxSize = isImage ? MAX_IMAGE_SIZE : MAX_VIDEO_SIZE;
    if (file.size > maxSize) {
      const maxSizeMB = maxSize / (1024 * 1024);
      throw error(400, `File too large. Max size: ${maxSizeMB}MB`);
    }

    // Generate unique filename with timestamp
    const timestamp = Date.now();
    const randomString = Math.random().toString(36).substring(7);
    const fileExtension = file.name.split(".").pop() ?? "jpg";
    const filename = `${timestamp}-${randomString}.${fileExtension}`;

    // Create storage path (files will be in instagram-uploads folder)
    const storagePath = `instagram-uploads/${filename}`;
    const storageRef = ref(storage, storagePath);

    // Convert File to ArrayBuffer for upload
    const arrayBuffer = await file.arrayBuffer();
    const buffer = new Uint8Array(arrayBuffer);

    // Upload to Firebase Storage with metadata
    const metadata = {
      contentType: file.type,
      customMetadata: {
        originalName: file.name,
        uploadedAt: new Date().toISOString(),
        // Auto-delete after 24 hours (handled by Firebase Storage lifecycle rules)
        expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(),
      },
    };

    await uploadBytes(storageRef, buffer, metadata);

    // Get public download URL
    const downloadURL = await getDownloadURL(storageRef);

    // Calculate expiration (24 hours from now)
    const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000);

    return json({
      url: downloadURL,
      path: storagePath,
      expiresAt: expiresAt.toISOString(),
      size: file.size,
      type: file.type,
    });
  } catch (err: unknown) {
    console.error("Instagram media upload error:", err);

    // Handle known errors
    if (err && typeof err === "object" && "status" in err) {
      throw err; // Re-throw SvelteKit errors
    }

    // Generic error
    const message = err instanceof Error ? err.message : "Unknown error";
    throw error(500, `Upload failed: ${message}`);
  }
};

/**
 * DELETE /api/instagram/upload-media
 *
 * Deletes a previously uploaded media file from Firebase Storage.
 *
 * Request Body (JSON):
 * - path: Storage path of the file to delete
 *
 * Response:
 * - success: true if deleted successfully
 */
export const DELETE: RequestHandler = async ({ request }) => {
  try {
    const body = await request.json() as { path?: unknown };
    const { path } = body;

    if (!path || typeof path !== "string") {
      throw error(400, "Storage path is required");
    }

    // Only allow deletion of files in instagram-uploads folder
    if (!path.startsWith("instagram-uploads/")) {
      throw error(403, "Can only delete files from instagram-uploads folder");
    }

    const storageRef = ref(storage, path);

    // Delete the file
    const { deleteObject } = await import("firebase/storage");
    await deleteObject(storageRef);

    return json({
      success: true,
      path,
    });
  } catch (err: unknown) {
    console.error("Instagram media deletion error:", err);

    if (err && typeof err === "object" && "status" in err) {
      throw err;
    }

    const message = err instanceof Error ? err.message : "Unknown error";
    throw error(500, `Deletion failed: ${message}`);
  }
};

/**
 * API endpoint to list available sequence PNG files
 */
import { json } from "@sveltejs/kit";
import { readdir } from "fs/promises";
import { join } from "path";
import { fileURLToPath } from "url";

const __dirname = fileURLToPath(new URL(".", import.meta.url));

export async function GET() {
  try {
    const staticDir = join(__dirname, "../../../../static");
    const dictionaryDir = join(staticDir, "Explore");

    // Read all directories in the Explore
    const sequenceDirectories = await readdir(dictionaryDir, {
      withFileTypes: true,
    });

    const sequences: Array<{
      name: string;
      path: string;
      word: string;
    }> = [];

    // Check each directory for PNG files
    for (const dirent of sequenceDirectories) {
      if (dirent.isDirectory()) {
        const sequenceName = dirent.name;

        // Skip test sequences and invalid entries
        if (
          sequenceName === "A_A" ||
          sequenceName.length < 2 ||
          sequenceName.includes("test")
        ) {
          continue;
        }

        const sequenceDir = join(dictionaryDir, sequenceName);

        try {
          const files = await readdir(sequenceDir);
          // Try version 1 first, then version 2
          let pngFile = files.find((file) => file.endsWith("_ver1.png"));
          if (!pngFile) {
            pngFile = files.find((file) => file.endsWith("_ver2.png"));
          }

          if (pngFile) {
            sequences.push({
              name: pngFile,
              path: `/gallery/${sequenceName}/${pngFile}`,
              word: sequenceName,
            });
          }
        } catch (error) {
          // Skip directories we can't read
          console.warn(`Could not read directory ${sequenceName}:`, error);
        }
      }
    }

    // Note: Removed thumbnails directory scanning as it was including fake/test sequences
    // Only use real sequences from Explore directories

    // Sort by word name
    sequences.sort((a, b) => a.word.localeCompare(b.word));

    return json({
      success: true,
      sequences,
      count: sequences.length,
    });
  } catch (error) {
    console.error("Error listing sequences:", error);
    return json(
      {
        success: false,
        error: "Failed to list sequences",
        sequences: [],
        count: 0,
      },
      { status: 500 }
    );
  }
}

/**
 * Sequence Count API - Fast Total Count
 *
 * Returns just the total count of sequences without loading all data.
 * Used for pagination calculations and progress indicators.
 */

import { json } from "@sveltejs/kit";
import { readdir } from "fs/promises";
import { join } from "path";
import { fileURLToPath } from "url";

import type { RequestHandler } from "./$types";

const __dirname = fileURLToPath(new URL(".", import.meta.url));

let cachedCount: number | null = null;
let cacheTimestamp: number | null = null;
const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

export const GET: RequestHandler = async () => {
  try {
    // Check cache first
    const now = Date.now();
    if (
      cachedCount !== null &&
      cacheTimestamp !== null &&
      now - cacheTimestamp < CACHE_DURATION
    ) {
      console.log(`üìä Count API: Returning cached count: ${cachedCount}`);
      return json({
        success: true,
        count: cachedCount,
        cached: true,
      });
    }

    console.log("üîÑ Count API: Calculating sequence count...");

    const staticDir = join(__dirname, "../../../../../static");
    const ExploreDir = join(staticDir, "Explore");

    const sequenceDirectories = await readdir(ExploreDir, {
      withFileTypes: true,
    });
    let count = 0;

    for (const dirent of sequenceDirectories) {
      if (!dirent.isDirectory()) continue;

      const sequenceName = dirent.name;

      // Skip invalid sequences (same logic as paginated endpoint)
      if (
        sequenceName === "A_A" ||
        sequenceName.length < 2 ||
        sequenceName.includes("test") ||
        sequenceName.includes("Test")
      ) {
        continue;
      }

      // Quick check - just verify directory has content
      try {
        const sequenceDir = join(ExploreDir, sequenceName);
        const files = await readdir(sequenceDir);

        // Check if it has any PNG files
        const hasPng = files.some((file) => file.endsWith(".png"));
        if (hasPng) {
          count++;
        }
      } catch (error) {
        // Skip directories we can't read
        continue;
      }
    }

    // Cache the result
    cachedCount = count;
    cacheTimestamp = now;

    console.log(`‚úÖ Count API: Found ${count} valid sequences`);

    return json({
      success: true,
      count,
      cached: false,
    });
  } catch (error) {
    console.error("‚ùå Count API: Error:", error);
    return json(
      {
        success: false,
        error: "Failed to count sequences",
        count: 0,
      },
      { status: 500 }
    );
  }
};

/**
 * Paginated Sequences API - Mobile Optimized (Manifest-Based)
 *
 * Returns sequences in small batches for progressive loading.
 * Uses pre-generated manifest for instant loading (20-50ms vs 1500-2500ms).
 *
 * Performance: Eliminates filesystem scanning on every request.
 */

import { json } from "@sveltejs/kit";
import { readFile } from "fs/promises";
import { join } from "path";
import { fileURLToPath } from "url";
import { z } from "zod";

import type { RequestHandler } from "./$types";

const __dirname = fileURLToPath(new URL(".", import.meta.url));

interface ManifestSequence {
  id: string;
  word: string;
  thumbnailPath: string;
  webpPath: string | null;
  width: number;
  height: number;
  length: number;
  hasImage: boolean;
  hasWebP: boolean;
}

interface ExploreManifest {
  version: string;
  generatedAt: string;
  totalCount: number;
  sequences: ManifestSequence[];
}

interface SequenceMetadata {
  id: string;
  word: string;
  thumbnailUrl: string;
  webpThumbnailUrl?: string;
  width: number;
  height: number;
  length: number;
  hasImage: boolean;
  priority: boolean;
}

let cachedSequences: SequenceMetadata[] | null = null;

// Validation schema for request parameters
const paginationSchema = z.object({
  page: z.coerce.number().int().positive().max(10000).default(1),
  limit: z.coerce.number().int().positive().min(1).max(100).default(20),
  priority: z.coerce.boolean().default(false),
});

export const GET: RequestHandler = async ({ url }) => {
  try {
    // Validate and parse query parameters
    const rawParams = {
      page: url.searchParams.get("page") ?? "1",
      limit: url.searchParams.get("limit") ?? "20",
      priority: url.searchParams.get("priority") ?? "false",
    };

    const validationResult = paginationSchema.safeParse(rawParams);

    if (!validationResult.success) {
      console.error(
        "‚ùå Invalid pagination parameters:",
        validationResult.error
      );
      return json(
        {
          success: false,
          error: "Invalid parameters",
          details: validationResult.error.errors,
        },
        { status: 400 }
      );
    }

    const { page, limit, priority } = validationResult.data;

    const startTime = performance.now();

    console.log(
      `üìÑ Paginated API: Loading page ${page}, limit ${limit}, priority ${priority}`
    );

    // Load sequences from manifest (cached after first load)
    if (!cachedSequences) {
      cachedSequences = await loadSequencesFromManifest();
    }

    // Calculate pagination
    const startIndex = (page - 1) * limit;
    const endIndex = startIndex + limit;
    const totalCount = cachedSequences.length;

    // Get page of sequences
    let pageSequences = cachedSequences.slice(startIndex, endIndex);

    // Mark priority sequences (first page gets priority loading)
    if (priority || page === 1) {
      pageSequences = pageSequences.map((seq, index) => ({
        ...seq,
        priority: index < 10, // First 10 are priority
      }));
    }

    const hasMore = endIndex < totalCount;
    const duration = Math.round(performance.now() - startTime);

    console.log(
      `‚úÖ Paginated API: Returning ${pageSequences.length} sequences (${startIndex + 1}-${Math.min(endIndex, totalCount)} of ${totalCount}) in ${duration}ms`
    );

    return json({
      success: true,
      sequences: pageSequences,
      totalCount,
      hasMore,
      page,
      limit,
      totalPages: Math.ceil(totalCount / limit),
    });
  } catch (error) {
    console.error("‚ùå Paginated API: Error:", error);
    return json(
      {
        success: false,
        error: "Failed to load sequences",
        sequences: [],
        totalCount: 0,
        hasMore: false,
      },
      { status: 500 }
    );
  }
};

/**
 * Load sequences from pre-generated manifest
 * This is 50-100x faster than scanning filesystem!
 */
async function loadSequencesFromManifest(): Promise<SequenceMetadata[]> {
  const startTime = performance.now();
  console.log("üöÄ Loading sequences from manifest...");

  try {
    const staticDir = join(__dirname, "../../../../../static");
    const manifestPath = join(staticDir, "Explore-manifest.json");

    // Read manifest file
    const manifestContent = await readFile(manifestPath, "utf-8");
    const manifest = JSON.parse(manifestContent) as ExploreManifest;

    const duration = Math.round(performance.now() - startTime);

    console.log(
      `‚úÖ Loaded ${manifest.sequences.length} sequences from manifest in ${duration}ms`
    );
    console.log(`üìä Manifest generated at: ${manifest.generatedAt}`);

    // Convert manifest format to API format
    const sequences: SequenceMetadata[] = manifest.sequences.map((seq) => ({
      id: seq.id,
      word: seq.word,
      thumbnailUrl: seq.webpPath ?? seq.thumbnailPath, // Prefer WebP
      ...(seq.webpPath ? { webpThumbnailUrl: seq.webpPath } : {}),
      width: seq.width,
      height: seq.height,
      length: seq.length,
      hasImage: seq.hasImage,
      priority: false, // Will be set during pagination
    }));

    return sequences;
  } catch (error) {
    console.error(
      "‚ùå Failed to load manifest, falling back to filesystem scan:",
      error
    );

    // Fallback to old method if manifest doesn't exist
    return await loadAllSequenceMetadataFallback();
  }
}

/**
 * Fallback: Original filesystem scanning method
 * Only used if manifest file doesn't exist
 */
async function loadAllSequenceMetadataFallback(): Promise<SequenceMetadata[]> {
  console.log("‚ö†Ô∏è  Using fallback filesystem scan (slow)");
  console.log("üí° Tip: Run 'npm run build:manifest' to generate manifest");

  const { readdir } = await import("fs/promises");
  const staticDir = join(__dirname, "../../../../../static");
  const ExploreDir = join(staticDir, "Explore");

  try {
    const sequenceDirectories = await readdir(ExploreDir, {
      withFileTypes: true,
    });
    const sequences: SequenceMetadata[] = [];

    for (const dirent of sequenceDirectories) {
      if (!dirent.isDirectory()) continue;

      const sequenceName = dirent.name;

      // Skip invalid sequences
      if (
        sequenceName === "A_A" ||
        sequenceName.length < 2 ||
        sequenceName.includes("test") ||
        sequenceName.includes("Test")
      ) {
        continue;
      }

      const sequenceDir = join(ExploreDir, sequenceName);

      try {
        const files = await readdir(sequenceDir);

        // Look for image files
        let imageFile = files.find((file) => file.endsWith("_ver1.png"));
        if (!imageFile) {
          imageFile = files.find((file) => file.endsWith("_ver2.png"));
        }
        if (!imageFile) {
          imageFile = files.find((file) => file.endsWith(".png"));
        }

        if (imageFile) {
          // Check if WebP version exists
          const webpFile = imageFile.replace(".png", ".webp");
          const hasWebP = files.includes(webpFile);

          sequences.push({
            id: sequenceName,
            word: sequenceName,
            thumbnailUrl: `/gallery/${sequenceName}/${imageFile}`,
            ...(hasWebP
              ? { webpThumbnailUrl: `/gallery/${sequenceName}/${webpFile}` }
              : {}),
            width: 400, // Default dimensions when using fallback
            height: 400,
            length: calculateSequenceLength(sequenceName),
            hasImage: true,
            priority: false,
          });
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è Could not process sequence ${sequenceName}:`, error);
      }
    }

    // Sort alphabetically for consistent pagination
    sequences.sort((a, b) => a.word.localeCompare(b.word));

    console.log(
      `‚úÖ Loaded metadata for ${sequences.length} sequences (fallback)`
    );
    return sequences;
  } catch (error) {
    console.error("‚ùå Failed to load sequence metadata:", error);
    return [];
  }
}

function calculateSequenceLength(sequenceName: string): number {
  // Simple heuristic - in a real app you'd parse the sequence data
  // For now, estimate based on word length
  return Math.max(4, Math.min(16, sequenceName.length));
}
