/**
 * Admin Challenge Service Implementation
 *
 * Handles admin operations for daily challenges
 */

import { injectable } from "inversify";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  deleteDoc,
  Timestamp,
} from "firebase/firestore";
import { firestore } from "$shared/auth/firebase";
import { db } from "$shared/persistence";
import type { DailyChallenge } from "$shared/gamification/domain/models";
import type { SequenceData } from "$shared";
import type {
  ChallengeScheduleEntry,
  ChallengeFormData,
} from "../../domain/models";
import type { IAdminChallengeService } from "../contracts";

@injectable()
export class AdminChallengeService implements IAdminChallengeService {
  /**
   * Get all scheduled challenges for a date range
   */
  async getScheduledChallenges(
    startDate: Date,
    endDate: Date
  ): Promise<ChallengeScheduleEntry[]> {
    const entries: ChallengeScheduleEntry[] = [];
    const currentDate = new Date(startDate);

    while (currentDate <= endDate) {
      const dateStr = currentDate.toISOString().split("T")[0]!; // ISO string always has T separator
      const challenge = await this.getChallengeByDate(dateStr);

      entries.push({
        date: dateStr,
        challenge,
        isScheduled: challenge !== null,
      });

      currentDate.setDate(currentDate.getDate() + 1);
    }

    return entries;
  }

  /**
   * Create a new daily challenge
   */
  async createChallenge(formData: ChallengeFormData): Promise<DailyChallenge> {
    const challengeId = `challenge_${formData.date}`;

    // Create end-of-day expiration
    const expiresAt = new Date(formData.date);
    expiresAt.setHours(23, 59, 59, 999);

    const challenge: DailyChallenge = {
      id: challengeId,
      date: formData.date,
      type: formData.type,
      difficulty: formData.difficulty,
      title: formData.title,
      description: formData.description,
      xpReward: formData.xpReward,
      requirement: {
        type: formData.type,
        target: formData.target,
        ...(formData.metadata && { metadata: formData.metadata }),
      },
      expiresAt,
    };

    // Save to Firestore
    const challengeDocRef = doc(firestore, `dailyChallenges/${challengeId}`);
    await setDoc(challengeDocRef, {
      ...challenge,
      expiresAt: Timestamp.fromDate(expiresAt),
    });

    console.log(`✅ [Admin] Created daily challenge: ${challenge.title}`);

    return challenge;
  }

  /**
   * Update an existing daily challenge
   */
  async updateChallenge(
    challengeId: string,
    formData: Partial<ChallengeFormData>
  ): Promise<DailyChallenge> {
    const challengeDocRef = doc(firestore, `dailyChallenges/${challengeId}`);
    const challengeDoc = await getDoc(challengeDocRef);

    if (!challengeDoc.exists()) {
      throw new Error(`Challenge ${challengeId} not found`);
    }

    const existingChallenge = challengeDoc.data() as DailyChallenge;

    // Build update object
    const updates: Partial<DailyChallenge> = {};

    if ('title' in formData && formData.title) updates.title = formData.title;
    if ('description' in formData && formData.description) updates.description = formData.description;
    if ('difficulty' in formData && formData.difficulty) updates.difficulty = formData.difficulty;
    if ('xpReward' in formData && formData.xpReward !== undefined) updates.xpReward = formData.xpReward;
    if ('type' in formData && formData.type) updates.type = formData.type;

    if ('target' in formData && formData.target !== undefined || 'metadata' in formData && formData.metadata !== undefined) {
      updates.requirement = {
        ...existingChallenge.requirement,
        ...('target' in formData && formData.target !== undefined && { target: formData.target }),
        ...('metadata' in formData && formData.metadata && { metadata: formData.metadata }),
      };
    }

    await updateDoc(challengeDocRef, updates);

    console.log(`✅ [Admin] Updated daily challenge: ${challengeId}`);

    // Fetch and return updated challenge
    const updatedDoc = await getDoc(challengeDocRef);
    return updatedDoc.data() as DailyChallenge;
  }

  /**
   * Delete a daily challenge
   */
  async deleteChallenge(challengeId: string): Promise<void> {
    const challengeDocRef = doc(firestore, `dailyChallenges/${challengeId}`);
    await deleteDoc(challengeDocRef);

    console.log(`✅ [Admin] Deleted daily challenge: ${challengeId}`);
  }

  /**
   * Get user's saved sequences (for selection)
   */
  async getUserSequences(): Promise<SequenceData[]> {
    try {
      const sequences = await db.sequences.toArray();
      return sequences;
    } catch (error) {
      console.error("❌ [Admin] Failed to fetch user sequences:", error);
      return [];
    }
  }

  /**
   * Get a specific challenge by date
   */
  async getChallengeByDate(date: string): Promise<DailyChallenge | null> {
    const challengeId = `challenge_${date}`;
    const challengeDocRef = doc(firestore, `dailyChallenges/${challengeId}`);

    try {
      const challengeDoc = await getDoc(challengeDocRef);

      if (challengeDoc.exists()) {
        return challengeDoc.data() as DailyChallenge;
      }

      return null;
    } catch (error) {
      console.error(`❌ [Admin] Failed to fetch challenge for ${date}:`, error);
      return null;
    }
  }
}

/**
 * Angle Calculation Service
 *
 * Handles all angle-related calculations including normalization,
 * position mapping, and orientation mapping.
 */

import type { GridLocation } from "$shared";
import { Orientation, RotationDirection } from "$shared";
import { injectable } from "inversify";
import {
  HALF_PI,
  LOCATION_ANGLES,
  PI,
  TWO_PI,
} from "../../domain/math-constants.js";
import type { IAngleCalculator } from "../contracts/IAngleCalculator";

// ============================================================================
// Standalone utility functions (exported for use by domain layer)
// ============================================================================

export function normalizeAnglePositive(angle: number): number {
  const norm: number = angle % TWO_PI;
  return (norm < 0 ? norm + TWO_PI : norm) as number;
}

export function normalizeAngleSigned(angle: number): number {
  const norm = normalizeAnglePositive(angle);
  return norm > PI ? norm - TWO_PI : norm;
}

export function mapPositionToAngle(loc: GridLocation): number {
  // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
  const angle = LOCATION_ANGLES[loc] as number | undefined;
  if (angle === undefined) {
    throw new Error(`No angle defined for grid location: ${loc}`);
  }
  return angle;
}

export function mapOrientationToAngle(
  ori: Orientation,
  centerPathAngle: number
): number {
  if (ori === Orientation.IN) {
    return normalizeAnglePositive(centerPathAngle + PI);
  }

  if (ori === Orientation.OUT) {
    return normalizeAnglePositive(centerPathAngle);
  }

  if (ori === Orientation.CLOCK) {
    return normalizeAnglePositive(centerPathAngle + HALF_PI);
  }

  // Must be COUNTER (exhaustive check)
  return normalizeAnglePositive(centerPathAngle - HALF_PI);
}

// ============================================================================
// Service class (uses standalone functions internally)
// ============================================================================

@injectable()
export class AngleCalculator implements IAngleCalculator {
  /**
   * Normalize angle to positive range [0, 2π)
   */
  normalizeAnglePositive(angle: number): number {
    return normalizeAnglePositive(angle);
  }

  /**
   * Normalize angle to signed range (-π, π]
   */
  normalizeAngleSigned(angle: number): number {
    return normalizeAngleSigned(angle);
  }

  /**
   * Map grid position to angle using centralized enums
   */
  mapPositionToAngle(loc: GridLocation): number {
    return mapPositionToAngle(loc);
  }

  /**
   * Map orientation to staff angle using centralized enums
   */
  mapOrientationToAngle(ori: Orientation, centerPathAngle: number): number {
    return mapOrientationToAngle(ori, centerPathAngle);
  }

  /**
   * Linear interpolation between two values
   */
  lerp(a: number, b: number, t: number): number {
    return a * (1 - t) + b * t;
  }

  /**
   * Angular interpolation (handles wraparound) - ALWAYS TAKES SHORTEST PATH
   * ⚠️ This ignores rotation direction! Use lerpAngleDirectional for explicit direction control.
   */
  lerpAngle(a: number, b: number, t: number): number {
    const d = this.normalizeAngleSigned(b - a);
    return this.normalizeAnglePositive(a + d * t);
  }

  /**
   * Directional angular interpolation - respects explicit rotation direction from sequence data
   * This is NOT over-engineered - it directly follows the sequence data instructions!
   *
   * @param startAngle - Starting angle in radians
   * @param endAngle - Target angle in radians
   * @param direction - Explicit rotation direction from sequence data (CW, CCW, or noRotation)
   * @param progress - Interpolation progress (0 to 1)
   * @returns Interpolated angle that follows the specified direction
   */
  lerpAngleDirectional(
    startAngle: number,
    endAngle: number,
    direction: RotationDirection,
    progress: number
  ): number {
    // For noRotation (STATIC/DASH), use shortest path
    if (direction === RotationDirection.NO_ROTATION) {
      return this.lerpAngle(startAngle, endAngle, progress);
    }

    // Normalize angles to [0, 2π) range
    const start = this.normalizeAnglePositive(startAngle);
    const end = this.normalizeAnglePositive(endAngle);

    // Calculate the raw difference
    let delta = end - start;

    // Force the direction specified in the sequence data
    if (direction === RotationDirection.CLOCKWISE) {
      // Clockwise = negative rotation (in standard math coords)
      // If delta is positive, we need to go the long way (subtract 2π)
      if (delta > 0) {
        delta -= TWO_PI;
      }
      // If delta is 0, we're at the same angle - no movement needed
    } else {
      // Counter-clockwise = positive rotation
      // If delta is negative, we need to go the long way (add 2π)
      if (delta < 0) {
        delta += TWO_PI;
      }
      // If delta is 0, we're at the same angle - no movement needed
    }

    // Apply the forced direction
    return this.normalizeAnglePositive(start + delta * progress);
  }
}

import { injectable } from "inversify";
import type {
  IAnimatedImageTranscoder,
  WebpTranscodeOptions,
} from "../contracts/IAnimatedImageTranscoder";

interface WebPEncoderModule {
  encodeGifImageData(
    gifBuffer: Uint8Array,
    length: number,
    lossless: number
  ): { buffer: ArrayBuffer };
  api?: {
    allocateMemory?: () => void;
    encodeGif?: () => void;
  };
}

@injectable()
export class AnimatedImageTranscoder implements IAnimatedImageTranscoder {
  private encoderReadyPromise: Promise<void> | null = null;
  private WebPEncoder: WebPEncoderModule | null = null;

  async convertGifToWebp(
    blob: Blob,
    options: WebpTranscodeOptions = {}
  ): Promise<Blob> {
    if (typeof window === "undefined") {
      throw new Error("WebP transcoding is only available in the browser");
    }

    // Dynamically import webp-encoder only in browser context to avoid SSR issues
    if (!this.WebPEncoder) {
      const module = await import("webp-encoder");
      this.WebPEncoder = module.default as unknown as WebPEncoderModule;
    }

    await this.ensureEncoderReady();

    const gifBuffer = new Uint8Array(await blob.arrayBuffer());
    const losslessFlag = options.lossless ? 1 : 0;
    const encodedBuffer = this.WebPEncoder.encodeGifImageData(
      gifBuffer,
      gifBuffer.length,
      losslessFlag
    );

    return new Blob([encodedBuffer.buffer as ArrayBuffer], {
      type: "image/webp",
    });
  }

  private ensureEncoderReady(): Promise<void> {
    if (this.encoderReadyPromise) {
      return this.encoderReadyPromise;
    }

    this.encoderReadyPromise = new Promise((resolve, reject) => {
      let attempts = 0;
      const maxAttempts = 200; // ~3s

      const schedule = (fn: () => void) => {
        if (
          typeof window !== "undefined" &&
          typeof window.requestAnimationFrame === "function"
        ) {
          window.requestAnimationFrame(fn);
        } else {
          setTimeout(fn, 16);
        }
      };

      const check = () => {
        attempts += 1;

        if (this.hasEncoderApi()) {
          resolve();
          return;
        }

        if (attempts > maxAttempts) {
          reject(new Error("WebP encoder runtime failed to initialize"));
          return;
        }

        schedule(check);
      };

      check();
    });

    return this.encoderReadyPromise;
  }

  private hasEncoderApi(): boolean {
    if (!this.WebPEncoder) {
      return false;
    }
    const api = this.WebPEncoder.api;
    return Boolean(
      api &&
        typeof api.allocateMemory === "function" &&
        typeof api.encodeGif === "function"
    );
  }
}

/**
 * Motion Calculation Service
 *
 * Handles calculations for different motion types including
 * pro, anti, static, dash, and float motions.
 */

import { Orientation, RotationDirection } from "$shared";
import { injectable } from "inversify";
import { PI } from "../../domain/math-constants.js";
import type { IMotionCalculator } from "../contracts/IMotionCalculator";
import {
  mapOrientationToAngle,
  normalizeAnglePositive,
  normalizeAngleSigned,
} from "./AngleCalculator";

// ============================================================================
// Standalone utility functions (exported for use by domain layer)
// ============================================================================

export function calculateProIsolationStaffAngle(
  centerPathAngle: number,
  _propRotDir: RotationDirection
): number {
  return normalizeAnglePositive(centerPathAngle + PI);
}

export function calculateProTargetAngle(
  startCenterAngle: number,
  targetCenterAngle: number,
  startStaffAngle: number,
  turns: number,
  rotationDirection: RotationDirection
): number {
  const centerMovement = normalizeAngleSigned(
    targetCenterAngle - startCenterAngle
  );
  const dir =
    rotationDirection === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
  const propRotation = dir * turns * PI; // 1 turn = 180°, not 360°
  const staffMovement = centerMovement; // PRO: same direction as grid movement
  const targetStaffAngle = startStaffAngle + staffMovement + propRotation;
  return normalizeAnglePositive(targetStaffAngle);
}

export function calculateAntispinTargetAngle(
  startCenterAngle: number,
  targetCenterAngle: number,
  startStaffAngle: number,
  turns: number,
  rotationDirection: RotationDirection
): number {
  const centerMovement = normalizeAngleSigned(
    targetCenterAngle - startCenterAngle
  );
  const dir =
    rotationDirection === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
  const propRotation = dir * turns * PI; // 1 turn = 180°, not 360°
  const staffMovement = -centerMovement; // ANTI: opposite direction to grid movement
  const targetStaffAngle = startStaffAngle + staffMovement + propRotation;
  return normalizeAnglePositive(targetStaffAngle);
}

export function calculateStaticStaffAngle(
  startStaffAngle: number,
  endOrientation: Orientation,
  targetCenterAngle: number
): number {
  const endOriAngle = mapOrientationToAngle(endOrientation, targetCenterAngle);
  const angleDiff = normalizeAngleSigned(endOriAngle - startStaffAngle);

  return Math.abs(angleDiff) > 0.1 ? endOriAngle : startStaffAngle;
}

export function calculateDashTargetAngle(
  startStaffAngle: number,
  endOrientation: Orientation,
  targetCenterAngle: number,
  turns: number,
  rotationDirection: RotationDirection
): number {
  // Calculate base orientation angle
  const baseAngle = (
    endOrientation === Orientation.IN
      ? targetCenterAngle + PI
      : endOrientation === Orientation.OUT
        ? targetCenterAngle
        : startStaffAngle
  ) as number;

  // Add prop rotation: 1 turn = 180° (π) for all motion types
  const dir =
    rotationDirection === RotationDirection.COUNTER_CLOCKWISE ? -1 : 1;
  const propRotation = dir * turns * PI;
  const targetStaffAngle: number = baseAngle + propRotation;

  return normalizeAnglePositive(targetStaffAngle);
}

export function calculateFloatStaffAngle(startStaffAngle: number): number {
  return startStaffAngle;
}

// ============================================================================
// Service class (delegates to standalone functions)
// ============================================================================

@injectable()
export class MotionCalculator implements IMotionCalculator {
  constructor() {}

  /**
   * Calculate Pro Isolation staff angle using centralized enums
   */
  calculateProIsolationStaffAngle(
    centerPathAngle: number,
    propRotDir: RotationDirection
  ): number {
    return calculateProIsolationStaffAngle(centerPathAngle, propRotDir);
  }

  /**
   * Calculate Pro motion target angle with turns support
   */
  calculateProTargetAngle(
    startCenterAngle: number,
    targetCenterAngle: number,
    startStaffAngle: number,
    turns: number,
    rotationDirection: RotationDirection
  ): number {
    return calculateProTargetAngle(
      startCenterAngle,
      targetCenterAngle,
      startStaffAngle,
      turns,
      rotationDirection
    );
  }

  /**
   * Calculate Antispin target angle using centralized enums
   */
  calculateAntispinTargetAngle(
    startCenterAngle: number,
    targetCenterAngle: number,
    startStaffAngle: number,
    turns: number,
    rotationDirection: RotationDirection
  ): number {
    return calculateAntispinTargetAngle(
      startCenterAngle,
      targetCenterAngle,
      startStaffAngle,
      turns,
      rotationDirection
    );
  }

  /**
   * Calculate static motion staff angle
   */
  calculateStaticStaffAngle(
    startStaffAngle: number,
    endOrientation: Orientation,
    targetCenterAngle: number
  ): number {
    return calculateStaticStaffAngle(
      startStaffAngle,
      endOrientation,
      targetCenterAngle
    );
  }

  /**
   * Calculate dash target angle with turns support
   * Note: 1 turn = 180° (π) for all motion types
   */
  calculateDashTargetAngle(
    startStaffAngle: number,
    endOrientation: Orientation,
    targetCenterAngle: number,
    turns: number,
    rotationDirection: RotationDirection
  ): number {
    return calculateDashTargetAngle(
      startStaffAngle,
      endOrientation,
      targetCenterAngle,
      turns,
      rotationDirection
    );
  }

  /**
   * Calculate float motion staff angle (no change)
   */
  calculateFloatStaffAngle(startStaffAngle: number): number {
    return calculateFloatStaffAngle(startStaffAngle);
  }
}

/**
 * PixiJS Texture Loader
 *
 * Handles texture creation and caching:
 * - SVG to texture conversion
 * - Prop texture loading
 * - Grid texture loading
 * - Glyph texture loading
 * - Texture lifecycle management
 *
 * Single Responsibility: Texture loading and management
 */

import { Texture } from "pixi.js";
import type { ISVGGenerator, PropSvgData } from "$modules/animate/services/contracts/ISVGGenerator";

const VIEWBOX_SIZE = 950;

export class PixiTextureLoader {
  // Texture cache
  private bluePropTexture: Texture | null = null;
  private redPropTexture: Texture | null = null;
  private gridTexture: Texture | null = null;
  private glyphTexture: Texture | null = null;
  private previousGlyphTexture: Texture | null = null;

  async loadPropTextures(propType: string): Promise<{
    blue: Texture;
    red: Texture;
  }> {
    try {
      // Import SVGGenerator to generate prop SVGs
      const { TYPES } = await import("$shared/inversify/types");
      const { resolve } = await import("$shared");
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      const svgGenerator = resolve<ISVGGenerator>(TYPES.ISVGGenerator);

      // Generate blue and red prop SVGs
      /* eslint-disable @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call */
      const [bluePropData, redPropData] = await Promise.all([
        svgGenerator.generateBluePropSvg(propType),
        svgGenerator.generateRedPropSvg(propType),
      ]);

      // Load textures from SVG strings
      this.bluePropTexture = await this.createTextureFromSVG(
        bluePropData.svg,
        bluePropData.width,
        bluePropData.height
      );
      this.redPropTexture = await this.createTextureFromSVG(
        redPropData.svg,
        redPropData.width,
        redPropData.height
      );
      /* eslint-enable @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call */

      console.log(`[PixiTextureLoader] Loaded prop textures for ${propType}`);

      return {
        blue: this.bluePropTexture,
        red: this.redPropTexture,
      };
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load prop textures:", error);
      throw error;
    }
  }

  async loadGridTexture(
    gridMode: string,
    canvasSize: number
  ): Promise<Texture> {
    try {
      const { TYPES } = await import("$shared/inversify/types");
      const { resolve, GridMode } = await import("$shared");
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      const svgGenerator = resolve<ISVGGenerator>(TYPES.ISVGGenerator);

      // Convert gridMode string to GridMode enum
      const gridModeEnum =
        GridMode[gridMode.toUpperCase() as keyof typeof GridMode] ||
        GridMode.DIAMOND;

      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
      const gridSvg = svgGenerator.generateGridSvg(gridModeEnum);

      // Load texture at current canvas size for optimal quality
      this.gridTexture = await this.createTextureFromSVG(
        gridSvg,
        canvasSize,
        canvasSize
      );

      console.log(`[PixiTextureLoader] Loaded grid texture for ${gridMode}`);

      return this.gridTexture;
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load grid texture:", error);
      throw error;
    }
  }

  async loadGlyphTexture(
    svgString: string,
    _width: number,
    _height: number
  ): Promise<{
    current: Texture;
    previous: Texture | null;
  }> {
    try {
      // Save previous glyph for fade transition
      if (this.glyphTexture) {
        this.previousGlyphTexture = this.glyphTexture;
      }

      // Create texture from SVG (full 950x950 viewBox)
      this.glyphTexture = await this.createTextureFromSVG(
        svgString,
        VIEWBOX_SIZE,
        VIEWBOX_SIZE
      );

      console.log("[PixiTextureLoader] Loaded glyph texture");

      return {
        current: this.glyphTexture,
        previous: this.previousGlyphTexture,
      };
    } catch (error) {
      console.error("[PixiTextureLoader] Failed to load glyph texture:", error);
      throw error;
    }
  }

  clearPreviousGlyphTexture(): void {
    if (this.previousGlyphTexture) {
      this.previousGlyphTexture.destroy();
      this.previousGlyphTexture = null;
    }
  }

  private async createTextureFromSVG(
    svgString: string,
    width: number,
    height: number
  ): Promise<Texture> {
    // Convert SVG to data URL
    const base64 = btoa(unescape(encodeURIComponent(svgString)));
    const dataUrl = `data:image/svg+xml;base64,${base64}`;

    // Scale by devicePixelRatio for crisp rendering on high-DPI displays
    const scale =
      typeof window !== "undefined" ? window.devicePixelRatio || 1 : 1;
    const targetWidth = width * scale;
    const targetHeight = height * scale;

    return new Promise((resolve, reject) => {
      const img = new Image();

      img.onload = () => {
        try {
          // Render SVG to canvas at high resolution for crisp textures
          const canvas = document.createElement("canvas");
          canvas.width = targetWidth;
          canvas.height = targetHeight;

          const ctx = canvas.getContext("2d");
          if (!ctx) {
            reject(new Error("Failed to get 2D context"));
            return;
          }

          // Draw the SVG image to canvas at target size
          ctx.drawImage(img, 0, 0, targetWidth, targetHeight);

          // Create texture from canvas
          const texture = Texture.from(canvas);
          resolve(texture);
        } catch (error) {
          console.error("Texture creation error:", error);
          reject(error);
        }
      };

      img.onerror = (error) => {
        console.error("Image load error:", error);
        reject(new Error("Failed to load SVG image"));
      };

      img.src = dataUrl;
    });
  }

  getBluePropTexture(): Texture | null {
    return this.bluePropTexture;
  }

  getRedPropTexture(): Texture | null {
    return this.redPropTexture;
  }

  getGridTexture(): Texture | null {
    return this.gridTexture;
  }

  getGlyphTexture(): Texture | null {
    return this.glyphTexture;
  }

  destroy(): void {
    try {
      this.bluePropTexture?.destroy(true);
      this.redPropTexture?.destroy(true);
      this.gridTexture?.destroy(true);
      this.glyphTexture?.destroy(true);
      this.previousGlyphTexture?.destroy(true);
    } catch (e) {
      // Ignore texture destroy errors
    }

    this.bluePropTexture = null;
    this.redPropTexture = null;
    this.gridTexture = null;
    this.glyphTexture = null;
    this.previousGlyphTexture = null;
  }
}

import type { IReversalDetectionService } from "$create/shared/services/contracts";
import type { BeatData, SequenceData } from "$shared";
import { inject, injectable } from "inversify";
// Import TYPES directly from inversify/types to avoid HMR issues with re-exports
import { TYPES } from "$shared/inversify/types";
import type { ICAPEndPositionSelector } from "../../../circular/services/contracts/ICAPEndPositionSelector";
import type { ICAPExecutorSelector } from "../../../circular/services/contracts/ICAPExecutorSelector";
import type { IPartialSequenceGenerator } from "../../../circular/services/contracts/IPartialSequenceGenerator";
import type { IRotationDirectionService } from "../../../circular/services/contracts/IRotationDirectionService";
import type { GenerationOptions } from "../../domain";
import { GenerationMode, PropContinuity } from "../../domain";
import type { IBeatGenerationOrchestrator } from "../contracts/IBeatGenerationOrchestrator";
import type { BeatGenerationOptions } from "../contracts/IBeatGenerationOrchestrator";
import type { IGenerationOrchestrationService } from "../contracts/IGenerationOrchestrationService";
import type { ISequenceMetadataService } from "../contracts/ISequenceMetadataService";
import type { IStartPositionSelector } from "../contracts/IStartPositionSelector";
import type { ITurnAllocator } from "../contracts/ITurnAllocator";
/**
 * Service orchestrating the complete sequence generation pipeline
 *
 * Extracted from generate-actions.svelte.ts to separate orchestration logic
 * from state management. This service composes multiple focused services to
 * build complete sequences for both freeform and circular modes.
 */
@injectable()
export class GenerationOrchestrationService
  implements IGenerationOrchestrationService
{
  constructor(
    @inject(TYPES.IStartPositionSelector)
    private readonly startPositionSelector: IStartPositionSelector,

    @inject(TYPES.IRotationDirectionService)
    private readonly rotationDirectionService: IRotationDirectionService,

    @inject(TYPES.ITurnAllocationCalculator)
    private readonly turnAllocationCalculator: ITurnAllocator,

    @inject(TYPES.IBeatGenerationOrchestrator)
    private readonly beatGenerationOrchestrator: IBeatGenerationOrchestrator,

    @inject(TYPES.ISequenceMetadataService)
    private readonly metadataService: ISequenceMetadataService,

    @inject(TYPES.IReversalDetectionService)
    private readonly reversalDetectionService: IReversalDetectionService,

    @inject(TYPES.IPartialSequenceGenerator)
    private readonly partialSequenceGenerator: IPartialSequenceGenerator,

    @inject(TYPES.ICAPEndPositionSelector)
    private readonly capEndPositionSelector: ICAPEndPositionSelector,

    @inject(TYPES.ICAPExecutorSelector)
    private readonly capExecutorSelector: ICAPExecutorSelector
  ) {}

  /**
   * Generate complete sequence - routes to appropriate mode
   */
  async generateSequence(options: GenerationOptions): Promise<SequenceData> {
    // Route to appropriate generation mode
    if (options.mode === GenerationMode.CIRCULAR) {
      return this.generateCircularSequence(options);
    }

    return this.generateFreeformSequence(options);
  }

  /**
   * Generate freeform sequence using focused service composition
   * EXACT ORIGINAL LOGIC from SequenceGenerationService.generateSequence
   */
  private async generateFreeformSequence(
    options: GenerationOptions
  ): Promise<SequenceData> {
    // Step 1: Get random start position
    const startPosition = await this.startPositionSelector.selectStartPosition(
      options.gridMode
    );
    const sequence: BeatData[] = [startPosition];

    // Step 2: Determine rotation directions
    const rotationDirections =
      this.rotationDirectionService.determineRotationDirections(
        options.propContinuity
      );

    // Step 3: Calculate turn allocation
    const level = this.metadataService.mapDifficultyToLevel(options.difficulty);
    const turnIntensity = options.turnIntensity ?? 1;
    const turnAllocation = await this.turnAllocationCalculator.allocateTurns(
      options.length,
      level,
      turnIntensity
    );

    // Step 4: Generate beats
    const beatGenOptions: BeatGenerationOptions = {
      level,
      turnAllocation,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: rotationDirections.blueRotationDirection,
      redRotationDirection: rotationDirections.redRotationDirection,
      gridMode: options.gridMode,
    };

    const generatedBeats = await this.beatGenerationOrchestrator.generateBeats(
      sequence,
      options.length,
      beatGenOptions
    );

    // Step 5: Build sequence data structure
    const word = this.metadataService.calculateWordFromBeats(generatedBeats);
    const metadata = this.metadataService.createGenerationMetadata({
      beatsGenerated: generatedBeats.length,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: rotationDirections.blueRotationDirection,
      redRotationDirection: rotationDirections.redRotationDirection,
      turnIntensity,
      level,
    });

    // Import shared utilities dynamically to avoid circular dependencies
    const { createSequenceData } = await import("$shared");

    const sequenceData = createSequenceData({
      name: word || `Sequence ${Date.now()}`,
      word,
      beats: generatedBeats,
      startingPositionBeat: startPosition,
      startPosition,
      gridMode: options.gridMode,
      propType: options.propType,
      difficultyLevel: options.difficulty,
      isFavorite: false,
      isCircular: false,
      tags: ["generated", "freeform"],
      metadata,
    });

    // Step 6: Apply reversal detection
    const finalSequence =
      this.reversalDetectionService.processReversals(sequenceData);

    return finalSequence;
  }

  /**
   * Generate circular sequence using CAP executor
   * EXACT ORIGINAL LOGIC from SequenceGenerationService.generatePatternSequence
   */
  private async generateCircularSequence(
    options: GenerationOptions
  ): Promise<SequenceData> {
    // Import circular-specific models
    const { CAPType, SliceSize } = await import(
      "../../../circular/domain/models/circular-models"
    );

    // Use constructor-injected services to avoid HMR issues
    // Determine which CAP executor to use based on capType option
    const capTypeValue = options.capType ?? CAPType.STRICT_ROTATED;
    const isValidCAPType = (value: string | undefined): value is CAPType => {
      return Object.values(CAPType).includes(value as CAPType);
    };
    const capType: CAPType = isValidCAPType(capTypeValue)
      ? capTypeValue
      : CAPType.STRICT_ROTATED;
    const capExecutor = this.capExecutorSelector.getExecutor(capType);

    // Get slice size
    const sliceSizeValue = options.sliceSize ?? SliceSize.HALVED;
    const isValidSliceSize = (value: string | undefined): value is SliceSize => {
      return Object.values(SliceSize).includes(value as SliceSize);
    };
    const sliceSize: SliceSize = isValidSliceSize(sliceSizeValue)
      ? sliceSizeValue
      : SliceSize.HALVED;

    // Determine start and required end positions
    const { GridMode, GridPosition } = await import("$shared");
    const basicStartPositions =
      options.gridMode === GridMode.DIAMOND
        ? [GridPosition.ALPHA1, GridPosition.BETA5, GridPosition.GAMMA11]
        : [GridPosition.ALPHA2, GridPosition.BETA4, GridPosition.GAMMA12];

    const startPos =
      basicStartPositions[
        Math.floor(Math.random() * basicStartPositions.length)
      ];
    // Use CAP-specific end position selector (different end positions for rotated/mirrored/swapped/complementary)
    const requiredEndPos = this.capEndPositionSelector.determineEndPosition(
      capType,
      startPos!,
      sliceSize
    );

    // Generate partial sequence ending at required position
    const partialSequence =
      await this.partialSequenceGenerator.generatePartialSequence(
        startPos,
        requiredEndPos,
        sliceSize,
        options
      );

    // Execute CAP to complete the circle
    const circularBeats = capExecutor.executeCAP(partialSequence, sliceSize);

    // Build sequence data
    const word = this.metadataService.calculateWordFromBeats(
      circularBeats.slice(1)
    ); // Exclude start position
    const metadata = this.metadataService.createGenerationMetadata({
      beatsGenerated: circularBeats.length - 1,
      propContinuity: options.propContinuity ?? PropContinuity.CONTINUOUS,
      blueRotationDirection: "",
      redRotationDirection: "",
      turnIntensity: options.turnIntensity ?? 1,
      level: this.metadataService.mapDifficultyToLevel(options.difficulty),
    });

    const { createSequenceData } = await import("$shared");
    const capTypeTag = typeof capType === 'string' ? capType.replace("_", "-") : String(capType);
    const sequence = createSequenceData({
      name: `Circular ${word}`,
      word,
      beats: circularBeats.slice(1), // Exclude start position beat
      ...(circularBeats[0] && { startingPositionBeat: circularBeats[0] }),
      ...(circularBeats[0] && { startPosition: circularBeats[0] }),
      gridMode: options.gridMode,
      propType: options.propType,
      difficultyLevel: options.difficulty,
      isFavorite: false,
      isCircular: true,
      tags: ["circular", "cap", capTypeTag],
      metadata,
    });

    return this.reversalDetectionService.processReversals(sequence);
  }
}

/**
 * Generation Validation State - Reactive validation for generation configuration
 *
 * Provides real-time validation of generation settings before orchestration.
 * Prevents invalid configurations from reaching the generation pipeline.
 */

import { DifficultyLevel, GenerationMode } from "../shared/domain/models/generate-models";
import type { UIGenerationConfig } from "./generate-config.svelte";

export interface ValidationError {
  field: keyof UIGenerationConfig;
  message: string;
  severity: "error" | "warning";
}

export interface ValidationResult {
  isValid: boolean;
  errors: ValidationError[];
  warnings: ValidationError[];
}

/**
 * Validation rules for generation configuration
 */
const VALIDATION_RULES = {
  length: {
    min: 1,
    max: 64,
    recommended: { min: 4, max: 32 },
  },
  turnIntensity: {
    beginner: { min: 1, max: 3 },
    intermediate: { min: 1, max: 5 },
    advanced: { min: 1, max: 7 },
  },
} as const;

/**
 * Create validation state for generation configuration
 */
export function createGenerationValidationState() {
  let validationErrors = $state<ValidationError[]>([]);
  let validationWarnings = $state<ValidationError[]>([]);
  let lastValidatedConfig = $state<UIGenerationConfig | null>(null);

  /**
   * Validate complete generation configuration
   */
  function validateConfig(config: UIGenerationConfig): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationError[] = [];

    // Validate length
    const lengthValidation = validateLength(config.length);
    errors.push(...lengthValidation.errors);
    warnings.push(...lengthValidation.warnings);

    // Validate turn intensity for level
    const turnValidation = validateTurnIntensity(
      config.turnIntensity,
      config.level
    );
    errors.push(...turnValidation.errors);
    warnings.push(...turnValidation.warnings);

    // Validate CAP configuration (circular mode only)
    if (config.mode === GenerationMode.CIRCULAR) {
      const capValidation = validateCAPConfiguration(config);
      errors.push(...capValidation.errors);
      warnings.push(...capValidation.warnings);
    }

    // Update reactive state
    validationErrors = errors;
    validationWarnings = warnings;
    lastValidatedConfig = config;

    return {
      isValid: errors.length === 0,
      errors,
      warnings,
    };
  }

  /**
   * Validate sequence length
   */
  function validateLength(length: number): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationError[] = [];

    if (length < VALIDATION_RULES.length.min) {
      errors.push({
        field: "length",
        message: `Length must be at least ${VALIDATION_RULES.length.min}`,
        severity: "error",
      });
    }

    if (length > VALIDATION_RULES.length.max) {
      errors.push({
        field: "length",
        message: `Length cannot exceed ${VALIDATION_RULES.length.max}`,
        severity: "error",
      });
    }

    // Warnings for non-optimal lengths
    if (
      length < VALIDATION_RULES.length.recommended.min &&
      length >= VALIDATION_RULES.length.min
    ) {
      warnings.push({
        field: "length",
        message: `Sequences shorter than ${VALIDATION_RULES.length.recommended.min} may lack complexity`,
        severity: "warning",
      });
    }

    if (
      length > VALIDATION_RULES.length.recommended.max &&
      length <= VALIDATION_RULES.length.max
    ) {
      warnings.push({
        field: "length",
        message: `Sequences longer than ${VALIDATION_RULES.length.recommended.max} may be difficult to perform`,
        severity: "warning",
      });
    }

    return { isValid: errors.length === 0, errors, warnings };
  }

  /**
   * Validate turn intensity for difficulty level
   */
  function validateTurnIntensity(
    intensity: number,
    level: number
  ): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationError[] = [];

    // Map level number to difficulty
    const difficulty: DifficultyLevel =
      level === 1
        ? DifficultyLevel.BEGINNER
        : level === 2
          ? DifficultyLevel.INTERMEDIATE
          : DifficultyLevel.ADVANCED;

    const rules = VALIDATION_RULES.turnIntensity[difficulty];

    if (intensity < rules.min) {
      errors.push({
        field: "turnIntensity",
        message: `Turn intensity must be at least ${rules.min} for ${difficulty} level`,
        severity: "error",
      });
    }

    if (intensity > rules.max) {
      errors.push({
        field: "turnIntensity",
        message: `Turn intensity cannot exceed ${rules.max} for ${difficulty} level`,
        severity: "error",
      });
    }

    // Warning if intensity seems too high for level
    if (difficulty === DifficultyLevel.BEGINNER && intensity > 2) {
      warnings.push({
        field: "turnIntensity",
        message: "High turn intensity may be challenging for beginners",
        severity: "warning",
      });
    }

    return { isValid: errors.length === 0, errors, warnings };
  }

  /**
   * Validate CAP configuration for circular mode
   */
  function validateCAPConfiguration(
    config: UIGenerationConfig
  ): ValidationResult {
    const errors: ValidationError[] = [];
    const warnings: ValidationError[] = [];

    // CAP type must be selected
    if (!config.capType) {
      errors.push({
        field: "capType",
        message: "CAP type must be selected for circular mode",
        severity: "error",
      });
    }

    // Slice size must be selected
    if (!config.sliceSize) {
      errors.push({
        field: "sliceSize",
        message: "Slice size must be selected for circular mode",
        severity: "error",
      });
    }

    // Warn about complex CAP types for beginners
    if (
      config.level === 1 &&
      config.capType &&
      config.capType.includes("COMPLEMENTARY")
    ) {
      warnings.push({
        field: "capType",
        message: "Complementary CAP types may be challenging for beginners",
        severity: "warning",
      });
    }

    return { isValid: errors.length === 0, errors, warnings };
  }

  /**
   * Clear all validation errors and warnings
   */
  function clearValidation() {
    validationErrors = [];
    validationWarnings = [];
    lastValidatedConfig = null;
  }

  /**
   * Get validation errors for a specific field
   */
  function getFieldErrors(field: keyof UIGenerationConfig): ValidationError[] {
    return validationErrors.filter((error) => error.field === field);
  }

  /**
   * Get validation warnings for a specific field
   */
  function getFieldWarnings(
    field: keyof UIGenerationConfig
  ): ValidationError[] {
    return validationWarnings.filter((warning) => warning.field === field);
  }

  /**
   * Check if a specific field has errors
   */
  function hasFieldErrors(field: keyof UIGenerationConfig): boolean {
    return validationErrors.some((error) => error.field === field);
  }

  return {
    // Reactive state
    get errors() {
      return validationErrors;
    },
    get warnings() {
      return validationWarnings;
    },
    get hasErrors() {
      return validationErrors.length > 0;
    },
    get hasWarnings() {
      return validationWarnings.length > 0;
    },
    get lastValidated() {
      return lastValidatedConfig;
    },

    // Validation methods
    validateConfig,
    validateLength,
    validateTurnIntensity,
    validateCAPConfiguration,
    clearValidation,
    getFieldErrors,
    getFieldWarnings,
    hasFieldErrors,
  };
}

/**
 * Type guard for validation errors
 */
export function isValidationError(error: unknown): error is ValidationError {
  return (
    typeof error === "object" &&
    error !== null &&
    "field" in error &&
    "message" in error &&
    "severity" in error
  );
}

/**
 * Construct Tab Coordination Service - Implementation
 *
 * Coordinates between construct tab components (start position picker, option picker).
 * Based on desktop ConstructTabCoordinationService but simplified for web with runes.
 *
 * FIXED: Added proper state synchronization to resolve start position selection getting stuck
 */

import type { SequenceData } from "$shared";
import { injectable } from "inversify";
import type { BeatData } from "../../domain/models/BeatData";
import type { IBuildConstructSectionCoordinator as IConstructCoordinator } from "../contracts/IConstructCoordinator";

// Note: This service will need to be updated to use the new DI pattern
// For now, we'll comment out the direct import to avoid build errors
// import { sequenceStateService } from "../../SequenceStateService.svelte";

interface ComponentWithEventHandler {
  handleEvent?: (eventType: string, data: unknown) => void;
}

interface StartPositionSelectedDetail {
  startPosition: BeatData;
}

interface OptionSelectedDetail {
  beatData: BeatData;
}

interface SequenceModifiedDetail {
  sequence: SequenceData;
}

@injectable()
export class ConstructCoordinator implements IConstructCoordinator {
  private components: Record<string, ComponentWithEventHandler> = {};
  private isHandlingSequenceModification = false;
  private eventListenersSetup = false;
  private boundEventHandlers: {
    startPositionSelected: (event: CustomEvent<StartPositionSelectedDetail>) => void;
    optionSelected: (event: CustomEvent<OptionSelectedDetail>) => void;
    sequenceModified: (event: CustomEvent<SequenceModifiedDetail>) => void;
  } | null = null;

  constructor() {}

  /**
   * Clean up resources when service is destroyed
   */
  destroy(): void {
    this.disconnectComponentSignals();
    this.components = {};
  }

  setupComponentCoordination(
    components: Record<string, ComponentWithEventHandler>
  ): void {
    this.components = components;

    // Set up any cross-component communication here (only once)
    if (!this.eventListenersSetup) {
      this.connectComponentSignals();
      this.eventListenersSetup = true;
    }
  }

  async handleSequenceModified(sequence: SequenceData): Promise<void> {
    if (this.isHandlingSequenceModification) {
      return;
    }

    try {
      this.isHandlingSequenceModification = true;

      // State management is handled by individual components via DI

      // Update UI based on sequence state
      await this.updateUIBasedOnSequence(sequence);

      // Notify components about sequence change
      this.notifyComponents("sequence_modified", { sequence });
    } catch (error) {
      console.error("❌ Error handling sequence modification:", error);
    } finally {
      this.isHandlingSequenceModification = false;
    }
  }

  handleStartPositionSet(_startPosition: BeatData): void {
    try {
      return;
    } catch (error) {
      console.error("❌ Error handling start position set:", error);
      // Error handling is managed by individual components
    }
  }

  handleBeatAdded(beatData: BeatData): void {
    try {
      // Beat addition is handled by workbench components directly
      // This coordinator just notifies other components of the change
      this.notifyComponents("beat_added", { beatData });
    } catch (error) {
      console.error("❌ Error handling beat added:", error);
    }
  }

  handleGenerationRequest(
    config: Record<string, unknown>
  ): void {
    try {
      // Generation is handled by the generate module components
      // This coordinator just facilitates communication between components
      this.notifyComponents("generation_requested", { config });

      // Simulate generation completion
      setTimeout(() => {
        this.notifyComponents("generation_completed", {
          success: true,
          message: "Generation completed",
        });
      }, 1000);
    } catch (error) {
      console.error("❌ Error handling generation request:", error);
    }
  }

  handleUITransitionRequest(targetPanel: string): void {
    try {
      // Emit custom events for UI transitions (similar to legacy implementation)
      const transitionEvent = new CustomEvent("construct-tab-transition", {
        detail: { targetPanel },
        bubbles: true,
      });

      if (typeof window !== "undefined") {
        document.dispatchEvent(transitionEvent);
      }

      // Notify components about the transition
      this.notifyComponents("ui_transition", { targetPanel });
    } catch (error) {
      console.error("❌ Error handling UI transition:", error);
    }
  }

  private connectComponentSignals(): void {
    // Set up event listeners for component coordination
    if (typeof window !== "undefined") {
      // Create bound event handlers to allow proper cleanup
      this.boundEventHandlers = {
        startPositionSelected: ((event: CustomEvent<StartPositionSelectedDetail>) => {
          void this.handleStartPositionSet(event.detail.startPosition);
        }) as (event: CustomEvent<StartPositionSelectedDetail>) => void,

        optionSelected: ((event: CustomEvent<OptionSelectedDetail>) => {
          void this.handleBeatAdded(event.detail.beatData);
        }) as (event: CustomEvent<OptionSelectedDetail>) => void,

        sequenceModified: ((event: CustomEvent<SequenceModifiedDetail>) => {
          void this.handleSequenceModified(event.detail.sequence);
        }) as (event: CustomEvent<SequenceModifiedDetail>) => void,
      };

      // Add event listeners
      document.addEventListener(
        "start-position-selected",
        this.boundEventHandlers.startPositionSelected as EventListener
      );
      document.addEventListener(
        "option-selected",
        this.boundEventHandlers.optionSelected as EventListener
      );
      document.addEventListener(
        "sequence-modified",
        this.boundEventHandlers.sequenceModified as EventListener
      );
    }
  }

  /**
   * Clean up event listeners to prevent memory leaks and duplicate handlers
   */
  private disconnectComponentSignals(): void {
    if (typeof window !== "undefined" && this.boundEventHandlers) {
      document.removeEventListener(
        "start-position-selected",
        this.boundEventHandlers.startPositionSelected as EventListener
      );
      document.removeEventListener(
        "option-selected",
        this.boundEventHandlers.optionSelected as EventListener
      );
      document.removeEventListener(
        "sequence-modified",
        this.boundEventHandlers.sequenceModified as EventListener
      );
      this.boundEventHandlers = null;
      this.eventListenersSetup = false;
    }
  }

  private updateUIBasedOnSequence(sequence: SequenceData): void {
    try {
      // Determine which panel to show based on sequence state
      const hasStartPosition = sequence.startingPositionBeat !== null && sequence.startingPositionBeat !== undefined;
      const hasBeats = sequence.beats.length > 0;

      let targetPanel: string;

      if (hasStartPosition || hasBeats) {
        targetPanel = "option_picker";
      } else {
        targetPanel = "start_position_picker";
      }

      // Transition to appropriate panel
      this.handleUITransitionRequest(targetPanel);
    } catch (error) {
      console.error("❌ Error updating UI based on sequence:", error);
    }
  }

  private notifyComponents(eventType: string, data: unknown): void {
    // Notify individual components if they have handlers
    Object.entries(this.components).forEach(([name, component]) => {
      if (component && typeof component.handleEvent === "function") {
        try {
          component.handleEvent(eventType, data);
        } catch (error) {
          console.error(`❌ Error notifying component ${name}:`, error);
        }
      }
    });

    // Emit global event for any listeners
    if (typeof window !== "undefined") {
      const event = new CustomEvent(`construct-coordination-${eventType}`, {
        detail: data,
        bubbles: true,
      });
      document.dispatchEvent(event);
    }
  }
}

/**
 * ConstructTab Event Service
 *
 * Centralized event handling for the ConstructTab component.
 * This service handles all the event coordination between different child components
 * that was previously scattered throughout the massive ConstructTab component.
 */

import {
  createBeatData,
  resolve,
  type BeatData,
  type IOrientationCalculationService,
  type PictographData,
  type SequenceData,
} from "$shared";
import { TYPES } from "$shared/inversify/types";
import { injectable } from "inversify";
import type {
  ICreateModuleEventService,
  IBuildConstructSectionCoordinator,
} from "../contracts";

@injectable()
export class CreateModuleEventService implements ICreateModuleEventService {
  private constructCoordinator: IBuildConstructSectionCoordinator | null = null;
  private orientationCalculationService: IOrientationCalculationService | null =
    null;
  private initialized = false;

  // Callback to access current sequence from component state
  private getCurrentSequenceCallback: (() => SequenceData | null) | null = null;
  private updateSequenceCallback: ((sequence: SequenceData) => void) | null =
    null;

  // Callback to add option to history
  private addOptionToHistoryCallback:
    | ((beatIndex: number, beatData: BeatData) => void)
    | null = null;

  // Callback to push undo snapshot
  private pushUndoSnapshotCallback:
    | ((type: "ADD_BEAT", metadata?: Record<string, unknown>) => void)
    | null = null;

  constructor() {
    // Don't initialize services in constructor - wait for lazy initialization
  }

  private initializeServices() {
    if (this.initialized) {
      return; // Already initialized
    }

    try {
      this.constructCoordinator = resolve<IBuildConstructSectionCoordinator>(
        TYPES.IBuildConstructTabCoordinator
      );
      this.orientationCalculationService =
        resolve<IOrientationCalculationService>(
          TYPES.IOrientationCalculationService
        );
      this.initialized = true;
    } catch (error) {
      // This is expected during SSR - services will be resolved once client-side DI container is ready
      // Services will remain null and methods will handle gracefully
    }
  }

  /**
   * Set callbacks to access sequence state from component
   */
  setSequenceStateCallbacks(
    getCurrentSequence: () => SequenceData | null,
    updateSequence: (sequence: SequenceData) => void
  ): void {
    this.getCurrentSequenceCallback = getCurrentSequence;
    this.updateSequenceCallback = updateSequence;
  }

  /**
   * Set callback to add option to history
   */
  setAddOptionToHistoryCallback(
    addOptionToHistory: (beatIndex: number, beatData: BeatData) => void
  ): void {
    this.addOptionToHistoryCallback = addOptionToHistory;
  }

  /**
   * Set callback to push undo snapshot
   */
  setPushUndoSnapshotCallback(
    pushUndoSnapshot: (type: "ADD_BEAT", metadata?: Record<string, unknown>) => void
  ): void {
    this.pushUndoSnapshotCallback = pushUndoSnapshot;
  }

  private ensureInitialized() {
    if (!this.initialized) {
      this.initializeServices();
    }
  }

  /**
   * Handle option selection in the Create module
   * OPTIMIZED: Add to sequence immediately for responsive UX, then process in background
   */
  handleOptionSelected(option: PictographData): void {
    try {
      performance.mark("event-service-start");
      this.ensureInitialized();

      // Get current sequence from component state
      const currentSequence = this.getCurrentSequenceCallback?.();
      if (!currentSequence) {
        throw new Error("No current sequence available");
      }

      performance.mark("initialization-complete");

      // Calculate correct beat number based on current sequence length
      const nextBeatNumber = currentSequence.beats.length + 1;

      // 📸 PUSH UNDO SNAPSHOT: Save state BEFORE adding beat (now deferred via queueMicrotask)
      this.pushUndoSnapshotCallback?.("ADD_BEAT", {
        beatNumber: nextBeatNumber,
        description: `Add beat ${nextBeatNumber}`,
      });

      // Create initial beat data from option with correct beat number
      let beatData = createBeatData({
        ...option, // Spread PictographData properties since BeatData extends PictographData
        beatNumber: nextBeatNumber,
        isBlank: false, // This is a real beat with pictograph data
      });

      performance.mark("beat-data-created");

      // 🔄 OPTIMIZATION: Calculate orientations BEFORE UI update to batch into single update
      if (
        currentSequence.beats.length > 0 &&
        this.orientationCalculationService
      ) {
        const lastBeat =
          currentSequence.beats[currentSequence.beats.length - 1];

        // Only apply orientation calculations if both beats have motion data
        if (lastBeat && !lastBeat.isBlank && !beatData.isBlank) {
          try {
            // Update start orientations from the last beat's end orientations
            beatData =
              this.orientationCalculationService.updateStartOrientations(
                beatData,
                lastBeat
              );
            performance.mark("start-orientations-complete");

            // Update end orientations based on the motion calculations
            beatData =
              this.orientationCalculationService.updateEndOrientations(
                beatData
              );
            performance.mark("end-orientations-complete");
          } catch (orientationError) {
            console.warn(
              `⚠️ CreateModuleEventService: Failed to calculate orientations for beat ${nextBeatNumber}:`,
              orientationError
            );
            // Continue without orientation updates rather than failing completely
          }
        }
      }
      performance.mark("orientation-processing-complete");

      // 🚀 SINGLE UI UPDATE: Add beat with orientations already calculated
      const finalSequence = {
        ...currentSequence,
        beats: [...currentSequence.beats, beatData],
      };
      performance.mark("sequence-updated");

      this.updateSequenceCallback?.(finalSequence);
      performance.mark("ui-callback-complete");

      // 📝 ADD TO HISTORY: Track this option addition for undo functionality
      this.addOptionToHistoryCallback?.(nextBeatNumber - 1, beatData); // beatIndex is 0-based
      performance.mark("history-updated");

      // 📡 COORDINATION: Notify other components (async, non-blocking)
      performance.mark("coordination-start");
      if (this.constructCoordinator) {
        this.constructCoordinator.handleBeatAdded(beatData).catch((error) => {
          console.warn(
            "⚠️ CreateModuleEventService: Coordination service error:",
            error
          );
        });
      }
      performance.mark("coordination-complete");
    } catch (error) {
      console.error("❌ Error handling option selection:", error);
      throw error;
    }
  }

  /**
   * Handle beat modification from the Graph Editor
   */
  handleBeatModified(_beatIndex: number, _beatData: BeatData): void {
