// Lazy singleton instance
let _CreateModuleEventService: CreateModuleEventService | null = null;

/**
 * Get the singleton instance of ConstructTabEventService
 * Creates the instance only when first accessed, ensuring DI container is ready
 */
export function getCreateModuleEventService(): CreateModuleEventService {
  if (!_CreateModuleEventService) {
    _CreateModuleEventService = new CreateModuleEventService();
  }
  return _CreateModuleEventService;
}

// Export the getter function directly for backward compatibility
export const constructTabEventService = getCreateModuleEventService;

/**
 * Create Module Initialization Service Implementation
 *
 * Manages complete initialization sequence for CreateModule's construction interface.
 * Resolves services, creates state, configures callbacks for sequence building workflow.
 *
 * Domain: Create module - Sequence Construction Interface
 * Extracted from CreateModule.svelte onMount monolith.
 */

import { GridMode, TYPES, resolve, type SequenceData } from "$shared";
import { injectable } from "inversify";
import type { IStartPositionService } from "../../../construct/start-position-picker/services/contracts";
import {
  createCreateModuleState,
  createConstructTabState,
  createAssemblerTabState,
  createGeneratorTabState,
} from "../../state";
import type { PanelCoordinationState } from "../../state/panel-coordination-state.svelte";
import type {
  IBeatOperationsService,
  ICreateModuleService,
  INavigationSyncService,
  IResponsiveLayoutService,
  ISequencePersistenceService,
  ISequenceService,
} from "../contracts";
import type {
  CreateModuleInitializationResult,
  ICreateModuleInitializationService,
} from "../contracts/ICreateModuleInitializationService";
import { getCreateModuleEventService } from "./CreateModuleEventService";

@injectable()
export class CreateModuleInitializationService
  implements ICreateModuleInitializationService
{
  private sequenceService: ISequenceService | null = null;
  private sequencePersistenceService: ISequencePersistenceService | null = null;
  private startPositionService: IStartPositionService | null = null;
  private CreateModuleService: ICreateModuleService | null = null;
  private layoutService: IResponsiveLayoutService | null = null;
  private navigationSyncService: INavigationSyncService | null = null;
  private beatOperationsService: IBeatOperationsService | null = null;

  async initialize(): Promise<CreateModuleInitializationResult> {
    // Resolve all required services
    this.sequenceService = resolve(TYPES.ISequenceService);
    this.sequencePersistenceService = resolve(
      TYPES.ISequencePersistenceService
    );
    this.startPositionService = resolve(TYPES.IStartPositionService);
    this.CreateModuleService = resolve(TYPES.ICreateModuleService);
    this.layoutService = resolve(TYPES.IResponsiveLayoutService);
    this.navigationSyncService = resolve(TYPES.INavigationSyncService);
    this.beatOperationsService = resolve(TYPES.IBeatOperationsService);

    // Wait a tick to ensure component context is fully established
    await new Promise((resolve) => setTimeout(resolve, 0));

    // Create state objects
    const CreateModuleState = createCreateModuleState(
      this.sequenceService,
      this.sequencePersistenceService
    );

    // Create tab-specific states - each tab has its own independent sequence state
    const constructTabState = createConstructTabState(
      this.CreateModuleService,
      CreateModuleState.sequenceState,
      this.sequencePersistenceService,
      CreateModuleState,
      undefined
    );

    const assemblerTabState = createAssemblerTabState(
      this.sequenceService,
      this.sequencePersistenceService
    );

    const generatorTabState = createGeneratorTabState(
      this.sequenceService,
      this.sequencePersistenceService
    );

    // Attach tab states to CreateModuleState for easy access
    (CreateModuleState as Record<string, unknown>).constructTabState = constructTabState;
    (CreateModuleState as Record<string, unknown>).assemblerTabState = assemblerTabState;
    (CreateModuleState as Record<string, unknown>).generatorTabState = generatorTabState;

    // Initialize services
    this.CreateModuleService.initialize();

    // Initialize state with persistence
    // Note: Deep link check happens in CreateModule.svelte onMount
    // Persistence will be skipped there if deep link is present
    if (typeof CreateModuleState.initializeWithPersistence === 'function') {
      await CreateModuleState.initializeWithPersistence();
    }
    await constructTabState.initializeConstructTab();
    await assemblerTabState.initializeAssemblerTab();
    await generatorTabState.initializeGeneratorTab();

    // Note: Event callbacks configured separately via configureEventCallbacks()
    // after component has created panelState

    // Load start positions
    await this.loadStartPositions(GridMode.DIAMOND);

    return {
      sequenceService: this.sequenceService,
      sequencePersistenceService: this.sequencePersistenceService,
      startPositionService: this.startPositionService,
      CreateModuleService: this.CreateModuleService,
      CreateModuleState,
      constructTabState,
      assemblerTabState,
      generatorTabState,
      layoutService: this.layoutService,
      navigationSyncService: this.navigationSyncService,
      beatOperationsService: this.beatOperationsService,
    };
  }

  configureEventCallbacks(
    CreateModuleState: ReturnType<typeof createCreateModuleState>,
    panelState: PanelCoordinationState
  ): void {
    const CreateModuleEventService = getCreateModuleEventService();

    // Set up sequence state callbacks for CreateModuleEventService
    CreateModuleEventService.setSequenceStateCallbacks(
      () => CreateModuleState.sequenceState.getCurrentSequence(),
      (sequence) => CreateModuleState.sequenceState.setCurrentSequence(sequence)
    );

    // Set up option history callback
    CreateModuleEventService.setAddOptionToHistoryCallback(
      (beatIndex, beatData) => {
        CreateModuleState.addOptionToHistory(beatIndex, beatData);
      }
    );

    // Set up undo snapshot callback
    CreateModuleEventService.setPushUndoSnapshotCallback((type, metadata) => {
      CreateModuleState.pushUndoSnapshot(type, metadata);
    });

    // Configure panel state callbacks on sequenceState
    CreateModuleState.sequenceState.onEditPanelOpen = (
      beatIndex: number,
      beatData: BeatData,
      beatsData: BeatData[]
    ) => {
      if (beatsData && beatsData.length > 0) {
        panelState.openBatchEditPanel(beatsData);
      } else {
        panelState.openEditPanel(beatIndex, beatData);
      }
    };

    CreateModuleState.sequenceState.onEditPanelClose = () => {
      panelState.closeEditPanel();
    };

    CreateModuleState.sequenceState.onAnimationStart = () => {
      panelState.setAnimating(true);
    };

    CreateModuleState.sequenceState.onAnimationEnd = () => {
      panelState.setAnimating(false);
    };
  }

  loadStartPositions(gridMode: GridMode): void {
    if (!this.startPositionService) {
      throw new Error("Start position service not initialized");
    }

    this.startPositionService.getDefaultStartPositions(gridMode);
  }
}

/**
 * Reversal Detection Service Implementation
 *
 * Detects reversals between beats in sequences based on prop rotation direction changes.
 * Ported from desktop app's ReversalDetector logic.
 */

import type { BeatData, PictographData, SequenceData } from "$shared";
import { MotionColor } from "$shared";
import { injectable } from "inversify";
import { createBeatData } from "../../domain/factories/createBeatData";
import type {
  IReversalDetectionService,
  PictographWithReversals,
  ReversalInfo,
} from "../contracts/IReversalDetectionService";

@injectable()
export class ReversalDetectionService implements IReversalDetectionService {
  /**
   * Process reversals for an entire sequence
   */
  processReversals(sequence: SequenceData): SequenceData {
    const processedBeats: BeatData[] = [];

    for (let i = 0; i < sequence.beats.length; i++) {
      const currentBeat = sequence.beats[i]!;
      const previousBeats = sequence.beats.slice(0, i);

      // Detect reversals for this beat
      const reversalInfo = this.detectReversal(previousBeats, currentBeat);

      // Apply reversal symbols to the beat
      const processedBeat = this.applyReversalSymbols(
        currentBeat,
        reversalInfo
      );

      processedBeats.push(processedBeat);
    }

    return {
      ...sequence,
      beats: processedBeats,
    };
  }

  /**
   * Detect reversal for a single beat based on previous beats
   */
  detectReversal(
    previousBeats: BeatData[],
    currentBeat: BeatData
  ): ReversalInfo {
    const reversalInfo: ReversalInfo = {
      blueReversal: false,
      redReversal: false,
    };

    if (currentBeat.isBlank) {
      return reversalInfo;
    }

    // Check blue motion reversal
    const lastBluePropRotDir = this._getLastValidPropRotDir(
      previousBeats,
      "blue"
    );
    const currentBluePropRotDir = this._getPropRotDir(currentBeat, "blue");

    if (this._isReversal(lastBluePropRotDir, currentBluePropRotDir)) {
      reversalInfo.blueReversal = true;
    }

    // Check red motion reversal
    const lastRedPropRotDir = this._getLastValidPropRotDir(
      previousBeats,
      "red"
    );
    const currentRedPropRotDir = this._getPropRotDir(currentBeat, "red");

    if (this._isReversal(lastRedPropRotDir, currentRedPropRotDir)) {
      reversalInfo.redReversal = true;
    }

    return reversalInfo;
  }

  /**
   * Apply reversal symbols to a beat
   */
  applyReversalSymbols(
    beatData: BeatData,
    reversalInfo: ReversalInfo
  ): BeatData {
    return createBeatData({
      ...beatData,
      blueReversal: reversalInfo.blueReversal,
      redReversal: reversalInfo.redReversal,
    });
  }

  /**
   * Detect reversal for an option preview based on current sequence
   * This is used to show reversal indicators on options before they're selected
   */
  detectReversalForOption(
    currentSequence: BeatData[],
    optionPictographData: PictographData
  ): ReversalInfo {
    const reversalInfo: ReversalInfo = {
      blueReversal: false,
      redReversal: false,
    };

    if (!optionPictographData.motions) {
      return reversalInfo;
    }

    // If sequence is empty, no reversals possible
    if (currentSequence.length === 0) {
      return reversalInfo;
    }

    // Get the last valid prop rotation directions from the current sequence
    const lastBluePropRotDir = this._getLastValidPropRotDirFromSequence(
      currentSequence,
      "blue"
    );
    const lastRedPropRotDir = this._getLastValidPropRotDirFromSequence(
      currentSequence,
      "red"
    );

    // Get the prop rotation directions from the option's motion data
    const optionBluePropRotDir = this._getPropRotDirFromPictographData(
      optionPictographData,
      "blue"
    );
    const optionRedPropRotDir = this._getPropRotDirFromPictographData(
      optionPictographData,
      "red"
    );

    // Check for reversals
    if (this._isReversal(lastBluePropRotDir, optionBluePropRotDir)) {
      reversalInfo.blueReversal = true;
    }

    if (this._isReversal(lastRedPropRotDir, optionRedPropRotDir)) {
      reversalInfo.redReversal = true;
    }

    return reversalInfo;
  }

  /**
   * Get the last valid prop rotation direction for a color from previous beats
   */
  private _getLastValidPropRotDir(
    beats: BeatData[],
    color: "blue" | "red"
  ): string | null {
    for (let i = beats.length - 1; i >= 0; i--) {
      const beat = beats[i]!;
      const propRotDir = this._getPropRotDir(beat, color);

      if (propRotDir && propRotDir !== "noRotation") {
        return propRotDir;
      }
    }
    return null;
  }

  /**
   * Get prop rotation direction for a specific color from a beat
   */
  private _getPropRotDir(beat: BeatData, color: "blue" | "red"): string | null {
    if (beat.isBlank) {
      return null;
    }

    // Use current data structure: motions[MotionColor]
    const motionColor = color === "blue" ? MotionColor.BLUE : MotionColor.RED;
    const motionData = beat.motions[motionColor];

    if (!motionData) {
      console.log(
        `⚠️ ReversalDetectionService: No motion data found for ${color} motion`
      );
      return null;
    }

    // Use rotationDirection property (current structure) instead of propRotDir (legacy)
    const rotationDirection = motionData.rotationDirection;

    return rotationDirection || null;
  }

  /**
   * Check if there's a reversal between two prop rotation directions
   */
  private _isReversal(
    lastPropRotDir: string | null,
    currentPropRotDir: string | null
  ): boolean {
    // If either is null or noRotation, no reversal
    if (
      !lastPropRotDir ||
      !currentPropRotDir ||
      lastPropRotDir === "noRotation" ||
      currentPropRotDir === "noRotation"
    ) {
      return false;
    }

    // If directions are different, it's a reversal
    return lastPropRotDir !== currentPropRotDir;
  }

  /**
   * Get the last valid prop rotation direction from a sequence of beats
   */
  private _getLastValidPropRotDirFromSequence(
    beats: BeatData[],
    color: "blue" | "red"
  ): string | null {
    // Iterate backwards through the beats to find the last valid rotation direction
    for (let i = beats.length - 1; i >= 0; i--) {
      const beat = beats[i]!;
      if (!beat.isBlank) {
        const propRotDir = this._getPropRotDir(beat, color);
        if (propRotDir && propRotDir !== "noRotation") {
          return propRotDir;
        }
      }
    }
    return null;
  }

  /**
   * Get prop rotation direction from PictographData (for option previews)
   */
  private _getPropRotDirFromPictographData(
    pictographData: PictographData,
    color: "blue" | "red"
  ): string | null {
    // Use same MotionColor enum conversion as _getPropRotDir for consistency
    const motionColor = color === "blue" ? MotionColor.BLUE : MotionColor.RED;
    const motionData = pictographData.motions[motionColor];

    if (!motionData) {
      return null;
    }

    // Use rotationDirection property from the motion data
    const rotationDirection = motionData.rotationDirection;
    return rotationDirection || null;
  }

  /**
   * Detect reversals for multiple option pictographs at once
   * This is optimized for option picker display where we need to show reversals for all options
   */
  detectReversalsForOptions(
    currentSequence: PictographData[],
    options: PictographData[]
  ): PictographWithReversals[] {
    // If sequence is empty, no reversals possible
    if (currentSequence.length === 0) {
      return options.map((option) => ({
        ...option,
        blueReversal: false,
        redReversal: false,
      }));
    }

    // Get the last valid prop rotation directions from the current sequence
    const lastBluePropRotDir = this._getLastValidPropRotDirFromPictographs(
      currentSequence,
      "blue"
    );
    const lastRedPropRotDir = this._getLastValidPropRotDirFromPictographs(
      currentSequence,
      "red"
    );

    // Process each option and add reversal information
    return options.map((option) => {
      const reversalInfo: ReversalInfo = {
        blueReversal: false,
        redReversal: false,
      };

      if (!option.motions) {
        return { ...option, ...reversalInfo };
      }

      // Get the prop rotation directions from the option's motion data
      const optionBluePropRotDir = this._getPropRotDirFromPictographData(
        option,
        "blue"
      );
      const optionRedPropRotDir = this._getPropRotDirFromPictographData(
        option,
        "red"
      );

      // Check for reversals
      if (this._isReversal(lastBluePropRotDir, optionBluePropRotDir)) {
        reversalInfo.blueReversal = true;
      }

      if (this._isReversal(lastRedPropRotDir, optionRedPropRotDir)) {
        reversalInfo.redReversal = true;
      }

      return {
        ...option,
        ...reversalInfo,
      };
    });
  }

  /**
   * Get the last valid prop rotation direction from a sequence of pictographs
   * Similar to _getLastValidPropRotDirFromSequence but works with PictographData
   */
  private _getLastValidPropRotDirFromPictographs(
    pictographs: PictographData[],
    color: "blue" | "red"
  ): string | null {
    // Iterate backwards through the pictographs to find the last valid rotation direction
    for (let i = pictographs.length - 1; i >= 0; i--) {
      const pictograph = pictographs[i]!;
      if (pictograph.motions) {
        const propRotDir = this._getPropRotDirFromPictographData(
          pictograph,
          color
        );
        if (propRotDir && propRotDir !== "noRotation") {
          return propRotDir;
        }
      }
    }
    return null;
  }
}

import { inject, injectable } from "inversify";
import { TYPES } from "$shared/inversify/types";
import type { GridPosition } from "$shared/pictograph/grid/domain/enums/grid-enums";
import type { BeatData } from "../../domain/models/BeatData";
import type { SequenceData } from "$shared/foundation/domain/models/SequenceData";
import type { IBetaDetectionService } from "$shared/pictograph/prop/services/contracts/IBetaDetectionService";
import type {
  ISequenceAnalysisService,
  CircularityAnalysis,
  CircularType,
  StrictCapType,
} from "../contracts/ISequenceAnalysisService";
import {
  QUARTERED_CAPS,
  HALVED_CAPS,
} from "$create/generate/circular/domain/constants/circular-position-maps";
import {
  VERTICAL_MIRROR_POSITION_MAP,
  SWAPPED_POSITION_MAP,
} from "$create/generate/circular/domain/constants/strict-cap-position-maps";

/**
 * Sequence Analysis Service Implementation
 *
 * Analyzes sequences to detect circular patterns and CAP (Continuous Assembly Pattern) potential.
 *
 * Key Concepts:
 * - Circular sequences can be "autocompleted" by applying CAP transformations
 * - The start→end position relationship determines which CAP types are possible
 * - Uses predefined position maps (quartered, halved, mirrored, swapped, complementary)
 * - Intermediate pictographs are irrelevant - only start/end positions matter
 */
@injectable()
export class SequenceAnalysisService implements ISequenceAnalysisService {
  constructor(
    @inject(TYPES.IBetaDetectionService)
    private readonly betaDetectionService: IBetaDetectionService
  ) {}

  /**
   * Analyze a sequence for circular properties
   */
  analyzeCircularity(sequence: SequenceData): CircularityAnalysis {
    // Get start and end beats
    const startBeat = this.getStartBeat(sequence);
    const endBeat = this.getEndBeat(sequence);

    // Default non-circular result
    const defaultResult: CircularityAnalysis = {
      isCircular: false,
      circularType: null,
      startPosition: null,
      endPosition: null,
      startIsBeta: false,
      endIsBeta: false,
      possibleCapTypes: [],
      description: "Not circular",
    };

    // Check if we have valid start and end beats
    if (!startBeat || !endBeat) {
      return defaultResult;
    }

    // Get start and end positions
    const startPosition = startBeat.startPosition;
    const endPosition = endBeat.endPosition;

    if (!startPosition || !endPosition) {
      return defaultResult;
    }

    // Check if both positions are in the same position group
    const sameGroup = this.areSamePositionGroup(startPosition, endPosition);
    const startIsBeta = this.isBetaPosition(startPosition);
    const endIsBeta = this.isBetaPosition(endPosition);

    if (!sameGroup) {
      return {
        ...defaultResult,
        startPosition,
        endPosition,
        startIsBeta,
        endIsBeta,
        description: "Positions are not in the same position group",
      };
    }

    // Determine circular type
    const circularType = this.getCircularType(startPosition, endPosition);

    if (!circularType) {
      return {
        ...defaultResult,
        startPosition,
        endPosition,
        startIsBeta,
        endIsBeta,
        description: "Invalid circular relationship",
      };
    }

    // Get possible CAP types based on circular type
    const possibleCapTypes =
      this.getPossibleCapTypesForCircularType(circularType);

    return {
      isCircular: true,
      circularType,
      startPosition,
      endPosition,
      startIsBeta,
      endIsBeta,
      possibleCapTypes,
      description: this.buildCircularDescription(
        startPosition,
        endPosition,
        circularType
      ),
    };
  }

  /**
   * Check if a sequence is circular-capable (simple boolean check)
   */
  isCircularCapable(sequence: SequenceData): boolean {
    const analysis = this.analyzeCircularity(sequence);
    return analysis.isCircular;
  }

  /**
   * Get possible CAP types for a circular sequence
   */
  getPossibleCapTypes(sequence: SequenceData): readonly StrictCapType[] {
    const analysis = this.analyzeCircularity(sequence);
    return analysis.possibleCapTypes;
  }

  /**
   * Determine the circular relationship between two positions
   *
   * Uses the predefined transformation maps to check if the start→end pair
   * exists in any of the CAP validation sets:
   * - Same position → 'same' (complementary, mirrored, swapped)
   * - Quartered map → 'quartered' (90° rotation)
   * - Halved map → 'halved' (180° rotation)
   */
  getCircularType(
    startPosition: GridPosition,
    endPosition: GridPosition
  ): CircularType | null {
    const positionKey = `${startPosition},${endPosition}`;

    // Check if same position (complementary CAP)
    if (startPosition === endPosition) {
      return "same";
    }

    // Check quartered CAPs (90° rotation)
    if (QUARTERED_CAPS.has(positionKey)) {
      return "quartered";
    }

    // Check halved CAPs (180° rotation)
    if (HALVED_CAPS.has(positionKey)) {
      return "halved";
    }

    // Check mirrored positions (also 'same' type)
    if (VERTICAL_MIRROR_POSITION_MAP[startPosition] === endPosition) {
      return "same";
    }

    // Check swapped positions (also 'halved' type since alpha1→alpha5 is both)
    if (SWAPPED_POSITION_MAP[startPosition] === endPosition) {
      return "halved";
    }

    return null;
  }

  /**
   * Check if a position is a beta position
   */
  isBetaPosition(position: GridPosition): boolean {
    return this.betaDetectionService.isBetaPosition(position);
  }

  /**
   * Check if both positions are in the same position group
   */
  private areSamePositionGroup(
    pos1: GridPosition,
    pos2: GridPosition
  ): boolean {
    const info1 = this.extractPositionInfo(pos1);
    const info2 = this.extractPositionInfo(pos2);

    if (!info1 || !info2) return false;

    return info1.group === info2.group;
  }

  /**
   * Get the first beat with valid pictograph data (start beat)
   */
  getStartBeat(sequence: SequenceData): BeatData | null {
    if (!sequence.beats || sequence.beats.length === 0) {
      return null;
    }

    // Find first beat with a start position
    for (const beat of sequence.beats) {
      if (beat.startPosition && !beat.isBlank) {
        return beat;
      }
    }

    return null;
  }

  /**
   * Get the last beat with valid pictograph data (end beat)
   */
  getEndBeat(sequence: SequenceData): BeatData | null {
    if (!sequence.beats || sequence.beats.length === 0) {
      return null;
    }

    // Find last beat with an end position (iterate backwards)
    for (let i = sequence.beats.length - 1; i >= 0; i--) {
      const beat = sequence.beats[i]!;
      if (beat.endPosition && !beat.isBlank) {
        return beat;
      }
    }

    return null;
  }

  /**
   * Get a human-readable description of the circular relationship
   */
  getCircularDescription(analysis: CircularityAnalysis): string {
    return analysis.description;
  }

  /**
   * Detect the actual CAP type of a COMPLETED sequence
   *
   * Analyzes ALL consecutive beat transformations to determine what type
   * of completed CAP pattern the sequence represents.
   */
  detectCompletedCapTypes(sequence: SequenceData): readonly StrictCapType[] {
    if (!sequence.beats || sequence.beats.length === 0) {
      return [];
    }

    // Filter out blank beats
    const validBeats = sequence.beats.filter(
      (beat) => !beat.isBlank && beat.endPosition
    );

    if (validBeats.length === 0) {
      return [];
    }

    // Check 1: Static CAP - all beats at the same position
    const allSamePosition = validBeats.every(
      (beat) =>
        beat.startPosition === validBeats[0]!.startPosition &&
        beat.endPosition === validBeats[0]!.endPosition
    );

    if (allSamePosition) {
      return ["static"] as const;
    }

    // Build consecutive pairs: each beat's end → next beat's start
    const consecutivePairs: Array<{ from: GridPosition; to: GridPosition }> =
      [];

    for (let i = 0; i < validBeats.length; i++) {
      const currentBeat = validBeats[i]!;
      const nextBeat = validBeats[(i + 1) % validBeats.length]!; // Wrap around to first beat

      if (
        currentBeat.endPosition &&
        nextBeat.startPosition
      ) {
        consecutivePairs.push({
          from: currentBeat.endPosition,
          to: nextBeat.startPosition,
        });
      }
    }

    if (consecutivePairs.length === 0) {
      return [];
    }

    // Check 2: Rotated CAP - all consecutive pairs show 90° rotation
    const allQuartered = consecutivePairs.every((pair) => {
      const key = `${pair.from},${pair.to}`;
      return QUARTERED_CAPS.has(key);
    });

    if (allQuartered) {
      return ["rotated"] as const;
    }

    // Check 3: Mirrored CAP - all consecutive pairs show mirroring
    const allMirrored = consecutivePairs.every((pair) => {
      const key = `${pair.from},${pair.to}`;

      // Check halved caps (180° mirroring)
      if (HALVED_CAPS.has(key)) {
        return true;
      }

      // Check vertical mirror map
      if (VERTICAL_MIRROR_POSITION_MAP[pair.from] === pair.to) {
        return true;
      }

      // Check swapped positions
      if (SWAPPED_POSITION_MAP[pair.from] === pair.to) {
        return true;
      }

      // Check if same position (like alpha1 → alpha1 with mirrored turns)
      if (pair.from === pair.to) {
        return true;
      }

      return false;
    });

    if (allMirrored) {
      return ["mirrored"] as const;
    }

    // If none of the patterns match, return empty
    return [];
  }

  /**
   * Get possible CAP types based on circular type
   *
   * Mapping:
   * - 'same' → ['static']
   * - 'halved' → ['mirrored']
   * - 'quartered' → ['rotated']
   */
  private getPossibleCapTypesForCircularType(
    circularType: CircularType
  ): readonly StrictCapType[] {
    switch (circularType) {
      case "same":
        return ["static"] as const;
      case "halved":
        return ["mirrored"] as const;
      case "quartered":
        return ["rotated"] as const;
    }
  }

  /**
   * Build a human-readable description
   */
  private buildCircularDescription(
    startPosition: GridPosition,
    endPosition: GridPosition,
    circularType: CircularType
  ): string {
    const typeDescriptions: Record<CircularType, string> = {
      same: "Same position",
      halved: "Opposite/halved position (180°)",
      quartered: "Adjacent/quartered position (90°)",
    };

    const typeDesc = typeDescriptions[circularType];
    return `${typeDesc}: ${startPosition} → ${endPosition}`;
  }

  /**
   * Extract position group and number from a GridPosition
   */
  private extractPositionInfo(
    position: GridPosition
  ): { group: string; number: number; groupSize: number } | null {
    const positionStr = position.toString().toLowerCase();
    const match = positionStr.match(/^(alpha|beta|gamma)(\d+)$/);
    if (!match) return null;

    const group = match[1]!;
    const num = parseInt(match[2]!, 10);

    let groupSize: number;
    let maxNum: number;

    if (group === "alpha" || group === "beta") {
      groupSize = 8;
      maxNum = 8;
    } else if (group === "gamma") {
      groupSize = 16;
      maxNum = 16;
    } else {
      return null;
    }

    if (num < 1 || num > maxNum) {
      return null;
    }

    return { group, number: num, groupSize };
  }
}

/**
 * Sequence Domain Service - REAL Business Logic from Desktop
 *
 * Ported from desktop.modern.application.services.sequence.beat_sequence_service
 * and desktop.modern.domain.models for actual validation and business rules.
 */

import type {
  BeatData,
  SequenceData,
  ValidationErrorInfo,
  ValidationResult,
} from "$shared";
import { GridMode } from "$shared";
// Domain types
// import type { SequenceCreateRequest } from "$shared";

// Behavioral contracts
import { injectable } from "inversify";
// import type { ISequenceDomainService} from "../contracts";

interface SequenceCreateRequest {
  name: string;
  length?: number;
  gridMode?: GridMode;
}

@injectable()
export class SequenceDomainService {
  validateSequence(_sequence: unknown): boolean {
    // TODO: Implement sequence validation
    return true;
  }

  transformSequence(sequence: unknown): unknown {
    // TODO: Implement sequence transformation
    return sequence;
  }

  // Duplicate methods removed - using the detailed implementations below

  /**
   * Validate sequence creation request - REAL validation from desktop
   */
  validateCreateRequest(request: SequenceCreateRequest): ValidationResult {
    const errors: ValidationErrorInfo[] = [];

    // Validation from desktop SequenceData.__post_init__
    if (!request.name || request.name.trim().length === 0) {
      errors.push({
        code: "MISSING_NAME",
        message: "Sequence name is required",
        field: "name",
        severity: "error",
      });
    }

    if (request.name && request.name.length > 100) {
      errors.push({
        code: "NAME_TOO_LONG",
        message: "Sequence name must be less than 100 characters",
        field: "name",
        severity: "error",
      });
    }

    // Length validation from desktop domain models
    // Allow 0 length for progressive creation (start position only)
    if (
      request.length !== undefined &&
      (request.length < 0 || request.length > 64)
    ) {
      errors.push({
        code: "INVALID_LENGTH",
        message: "Sequence length must be between 0 and 64",
        field: "length",
        severity: "error",
      });
    }

    // Grid mode validation from desktop enums
    if (
      request.gridMode &&
      ![GridMode.DIAMOND, GridMode.BOX].includes(request.gridMode)
    ) {
      errors.push({
        code: "INVALID_GRID_MODE",
        message: "Grid mode must be either GridMode.DIAMOND or GridMode.BOX",
        field: "gridMode",
        severity: "error",
      });
    }

    return {
      isValid: errors.length === 0,
      errors,
      warnings: [],
    };
  }

  /**
   * Create sequence with proper beat numbering - from desktop SequenceData
   */
  createSequence(request: SequenceCreateRequest): SequenceData {
    const validation = this.validateCreateRequest(request);
    if (!validation.isValid) {
      throw new Error(
        `Invalid sequence request: ${validation.errors.join(", ")}`
      );
    }

    // Create beats with proper numbering (desktop logic)
    const beats: BeatData[] = [];
    const length = request.length || 0;
    for (let i = 1; i <= length; i++) {
      beats.push(this.createEmptyBeat(i));
    }

    // Create sequence following desktop SequenceData structure
    const sequence: SequenceData = {
      id: this.generateId(),
      name: request.name.trim(),
      word: "",
      beats,
      thumbnails: [],
      isFavorite: false,
      isCircular: false,
      tags: [],
      metadata: { length: request.length },
    };

    return sequence;
  }

  /**
   * Update beat with proper validation - from desktop BeatSequenceService
   */
  updateBeat(
    sequence: SequenceData,
    beatIndex: number,
    beatData: BeatData
  ): SequenceData {
    // Validation from desktop BeatSequenceService
    if (beatIndex < 0 || beatIndex >= sequence.beats.length) {
      throw new Error(`Invalid beat index: ${beatIndex}`);
    }

    // Validate beat data
    if (beatData.duration && beatData.duration < 0) {
      throw new Error("Beat duration must be positive");
    }

    // Legacy field guard (beatNumber) for migrated data
    if (
      typeof (beatData as unknown as { beatNumber?: number }).beatNumber ===
      "number"
    ) {
      if ((beatData as unknown as { beatNumber: number }).beatNumber < 0) {
        throw new Error("Beat number must be non-negative");
      }
    }

    // Create new beats array with updated beat
    const newBeats = [...sequence.beats];
    newBeats[beatIndex] = { ...beatData };

    return { ...sequence, beats: newBeats } as SequenceData;
  }

  /**
   * Calculate sequence word - from desktop SequenceWordCalculator
   */
  calculateSequenceWord(sequence: SequenceData): string {
    if (sequence.beats.length === 0) {
      return "";
    }

    // Extract letters from beats (desktop logic)
    const word = sequence.beats.map((beat) => (beat as { letter?: string }).letter ?? "").join("");

    // Apply word simplification for circular sequences (desktop logic)
    return this.simplifyRepeatedWord(word);
  }

  /**
   * Simplify repeated patterns - from desktop WordSimplifier
   */
  private simplifyRepeatedWord(word: string): string {
    if (!word) return word;

    const canFormByRepeating = (s: string, pattern: string): boolean => {
      const patternLen = pattern.length;
      for (let i = 0; i < s.length; i += patternLen) {
        if (s.slice(i, i + patternLen) !== pattern) {
          return false;
        }
      }
      return true;
    };

    const n = word.length;

    // Try each possible pattern length from smallest to largest
    for (let i = 1; i <= Math.floor(n / 2); i++) {
      const pattern = word.slice(0, i);
      if (n % i === 0 && canFormByRepeating(word, pattern)) {
        return pattern;
      }
    }

    return word;
  }

  /**
   * Create empty beat - from desktop BeatData structure
   */
  private createEmptyBeat(beatNumber: number): BeatData {
    return {
      id: crypto.randomUUID(),
      beatNumber: beatNumber,
      duration: 1.0,
      blueReversal: false,
      redReversal: false,
      isBlank: true,
      // PictographData properties (since BeatData extends PictographData)
      letter: null,
      startPosition: null,
      endPosition: null,
      motions: {},
    };
  }

  /**
   * Generate unique ID - following desktop pattern
   */
  private generateId(): string {
    return `seq_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }
}

