import type { SequenceData } from "$shared";
import { injectable } from "inversify";
import type { ISequenceIndexService } from "../contracts/ISequenceIndexService";

interface SearchIndex {
  wordIndex: Map<string, Set<string>>; // word -> sequence IDs
  authorIndex: Map<string, Set<string>>; // author -> sequence IDs
  tagIndex: Map<string, Set<string>>; // tag -> sequence IDs
  metadataIndex: Map<string, Set<string>>; // combined metadata -> sequence IDs
}

@injectable()
export class SequenceIndexService implements ISequenceIndexService {
  buildIndex(sequences: SequenceData[]): void {
    // Build search index for sequences - placeholder implementation
    console.log(`Building index for ${sequences.length} sequences`);
    // TODO: Implement full-text search indexing when needed
  }

  getSequencesByTag(tag: string): SequenceData[] {
    // Get sequences by tag - placeholder implementation
    console.log(`Searching for sequences with tag: ${tag}`);
    return [];
  }

  updateIndex(sequence: SequenceData): void {
    // Update index with new/modified sequence - placeholder implementation
    console.log(`Updating index for sequence: ${sequence.id}`);
    // TODO: Implement incremental index updates when needed
  }

  removeFromIndex(sequenceId: string): void {
    // Remove sequence from search index - placeholder implementation
    console.log(`Removing sequence from index: ${sequenceId}`);
    // TODO: Implement index cleanup when needed
  }
  private sequenceIndex: SequenceData[] | null = null;
  private searchIndex: SearchIndex | null = null;
  private sequenceMap = new Map<string, SequenceData>();

  loadSequenceIndex(): SequenceData[] {
    if (this.sequenceIndex !== null) {
      return this.sequenceIndex;
    }

    try {
      // Load sequences if not already loaded
      this.sequenceIndex = this.scanSequenceDirectory();

      // Build search index
      this.buildSearchIndex(this.sequenceIndex);

      return this.sequenceIndex;
    } catch (error) {
      console.error("Failed to load sequence index:", error);
      // Return empty array as fallback
      this.sequenceIndex = [];
      return [];
    }
  }

  scanSequenceDirectory(): SequenceData[] {
    // Placeholder implementation - would scan for sequence files
    console.log("Scanning sequence directory...");
    return [];
  }

  buildSearchIndex(sequences: SequenceData[]): void {
    this.searchIndex = {
      wordIndex: new Map(),
      authorIndex: new Map(),
      tagIndex: new Map(),
      metadataIndex: new Map(),
    };

    this.sequenceMap.clear();

    for (const sequence of sequences) {
      this.sequenceMap.set(sequence.id, sequence);

      // Index by word/name
      this.addToIndex(
        this.searchIndex.wordIndex,
        sequence.word.toLowerCase(),
        sequence.id
      );
      this.addToIndex(
        this.searchIndex.wordIndex,
        sequence.name.toLowerCase(),
        sequence.id
      );

      // Index by author
      if (sequence.author) {
        this.addToIndex(
          this.searchIndex.authorIndex,
          sequence.author.toLowerCase(),
          sequence.id
        );
      }

      // Index by tags
      for (const tag of sequence.tags) {
        this.addToIndex(
          this.searchIndex.tagIndex,
          tag.toLowerCase(),
          sequence.id
        );
      }

      // Index by metadata (combined searchable text)
      const searchableText = this.buildSearchableText(sequence);
      for (const term of searchableText.split(/\s+/)) {
        if (term.length > 2) {
          // Only index terms longer than 2 characters
          this.addToIndex(
            this.searchIndex.metadataIndex,
            term.toLowerCase(),
            sequence.id
          );
        }
      }
    }
  }

  private addToIndex(
    index: Map<string, Set<string>>,
    key: string,
    value: string
  ): void {
    if (!index.has(key)) {
      index.set(key, new Set());
    }
    index.get(key)!.add(value);
  }

  private buildSearchableText(sequence: SequenceData): string {
    const parts = [
      sequence.name,
      sequence.word,
      sequence.author ?? "",
      ...sequence.tags,
      sequence.difficultyLevel ?? "",
    ];
    return parts.join(" ");
  }

  searchSequences(query: string): SequenceData[] {
    if (!this.searchIndex || !query.trim()) {
      return this.sequenceIndex || [];
    }

    const searchTerms = query
      .toLowerCase()
      .split(/\s+/)
      .filter((term) => term.length > 0);
    const resultIds = new Set<string>();

    for (const term of searchTerms) {
      const matchingIds = this.searchTerm(term);

      // For multiple terms, use intersection (AND logic)
      if (resultIds.size === 0) {
        matchingIds.forEach((id) => resultIds.add(id));
      } else {
        const currentIds = new Set(resultIds);
        resultIds.clear();
        matchingIds.forEach((id) => {
          if (currentIds.has(id)) {
            resultIds.add(id);
          }
        });
      }
    }

    // Convert IDs back to sequences and sort by relevance
    const results = Array.from(resultIds)
      .map((id) => this.sequenceMap.get(id))
      .filter((seq): seq is SequenceData => seq !== undefined);

    return this.sortByRelevance(results, query);
  }

  private searchTerm(term: string): Set<string> {
    const results = new Set<string>();
    if (!this.searchIndex) return results;

    // Search in word index
    const wordMatches = this.searchIndex.wordIndex.get(term) || new Set();
    wordMatches.forEach((id: string) => results.add(id));

    // Search in author index
    const authorMatches = this.searchIndex.authorIndex.get(term) || new Set();
    authorMatches.forEach((id: string) => results.add(id));

    // Search in tag index
    const tagMatches = this.searchIndex.tagIndex.get(term) || new Set();
    tagMatches.forEach((id: string) => results.add(id));

    // Search in metadata index
    const metadataMatches =
      this.searchIndex.metadataIndex.get(term) || new Set();
    metadataMatches.forEach((id: string) => results.add(id));

    return results;
  }

  private sortByRelevance(
    sequences: SequenceData[],
    query: string
  ): SequenceData[] {
    const queryLower = query.toLowerCase();
    return sequences.sort((a, b) => {
      const scoreA = this.calculateRelevanceScore(a, queryLower);
      const scoreB = this.calculateRelevanceScore(b, queryLower);
      return scoreB - scoreA;
    });
  }

  private calculateRelevanceScore(
    sequence: SequenceData,
    query: string
  ): number {
    let score = 0;

    // Exact word match gets highest score
    if (sequence.word.toLowerCase() === query) {
      score += 100;
    }

    // Word starts with query
    if (sequence.word.toLowerCase().startsWith(query)) {
      score += 50;
    }

    // Word contains query
    if (sequence.word.toLowerCase().includes(query)) {
      score += 25;
    }

    // Name matches
    if (sequence.name.toLowerCase().includes(query)) {
      score += 15;
    }

    // Author matches
    if (sequence.author?.toLowerCase().includes(query)) {
      score += 10;
    }

    // Tag matches
    for (const tag of sequence.tags) {
      if (tag.toLowerCase().includes(query)) {
        score += 5;
      }
    }

    return score;
  }

  refreshIndex(): void {
    this.sequenceIndex = null;
    this.searchIndex = null;
    this.sequenceMap.clear();
    this.loadSequenceIndex();
  }

  // Additional utility methods
  getIndexStats(): {
    totalSequences: number;
    indexedWords: number;
    indexedAuthors: number;
    indexedTags: number;
    indexedMetadata: number;
  } {
    return {
      totalSequences: this.sequenceMap.size,
      indexedWords: this.searchIndex?.wordIndex.size || 0,
      indexedAuthors: this.searchIndex?.authorIndex.size || 0,
      indexedTags: this.searchIndex?.tagIndex.size || 0,
      indexedMetadata: this.searchIndex?.metadataIndex.size || 0,
    };
  }

  getSequenceById(id: string): SequenceData | null {
    if (!this.sequenceMap.has(id)) {
      this.loadSequenceIndex();
    }
    return this.sequenceMap.get(id) || null;
  }

  getSuggestions(
    partialQuery: string,
    maxSuggestions = 10
  ): string[] {
    if (!this.searchIndex || partialQuery.length < 2) {
      return [];
    }

    const suggestions = new Set<string>();
    const query = partialQuery.toLowerCase();

    // Search in words/names
    for (const [word] of this.searchIndex.wordIndex) {
      if (word.includes(query)) {
        suggestions.add(word);
        if (suggestions.size >= maxSuggestions) break;
      }
    }

    // Search in authors if not enough suggestions
    if (suggestions.size < maxSuggestions) {
      for (const [author] of this.searchIndex.authorIndex) {
        if (author.includes(query)) {
          suggestions.add(author);
          if (suggestions.size >= maxSuggestions) break;
        }
      }
    }

    return Array.from(suggestions).slice(0, maxSuggestions);
  }
}

/**
 * Sequence Transformation Service
 *
 * Pure transformation functions for sequences.
 * All functions return new sequences without mutating inputs.
 *
 * Based on legacy desktop app implementation with modern TypeScript patterns.
 */

import type { BeatData, SequenceData, IGridPositionDeriver } from "$shared";
import type { IMotionQueryHandler } from "$shared";
import {
  createSequenceData,
  updateSequenceData,
  GridMode,
  Letter,
  MotionColor,
  MotionType,
  RotationDirection,
  TYPES,
  resolve,
  createMotionData,
} from "$shared";
import { inject, injectable } from "inversify";
import { createBeatData } from "../../domain/factories/createBeatData";
import type { ISequenceTransformationService } from "../contracts/ISequenceTransformationService";
import {
  LOCATION_MAP_EIGHTH_CW,
  VERTICAL_MIRROR_POSITION_MAP,
  VERTICAL_MIRROR_LOCATION_MAP,
  SWAPPED_POSITION_MAP,
} from "$create/generate/circular/domain/constants";

@injectable()
export class SequenceTransformationService
  implements ISequenceTransformationService
{
  constructor(
    @inject(TYPES.IMotionQueryHandler)
    private readonly motionQueryHandler: IMotionQueryHandler
  ) {}
  /**
   * Clear all beats in a sequence (make them blank)
   */
  clearSequence(sequence: SequenceData): SequenceData {
    const clearedBeats = sequence.beats.map((beat) => ({
      ...beat,
      isBlank: true,
      pictographData: null,
      blueReversal: false,
      redReversal: false,
    }));

    return updateSequenceData(sequence, {
      beats: clearedBeats,
    });
  }

  /**
   * Duplicate a sequence with new IDs
   */
  duplicateSequence(sequence: SequenceData, newName?: string): SequenceData {
    return createSequenceData({
      ...sequence,
      id: crypto.randomUUID(),
      name: newName ?? `${sequence.name} (Copy)`,
      beats: sequence.beats.map((beat) => ({
        ...beat,
        id: crypto.randomUUID(),
      })),
    });
  }

  /**
   * Mirror sequence vertically
   * - Mirrors all positions using vertical mirror map
   * - Mirrors all locations (flips east/west)
   * - Reverses rotation directions (cw ↔ ccw)
   * - Grid mode stays the same
   */
  mirrorSequence(sequence: SequenceData): SequenceData {
    const mirroredBeats = sequence.beats.map((beat) => this.mirrorBeat(beat));

    // Also mirror the start position if it exists (both fields for compatibility)
    const mirroredStartPosition = sequence.startPosition
      ? this.mirrorBeat(sequence.startPosition)
      : undefined;
    const mirroredStartingPositionBeat = sequence.startingPositionBeat
      ? this.mirrorBeat(sequence.startingPositionBeat)
      : undefined;

    return updateSequenceData(sequence, {
      beats: mirroredBeats,
      ...(mirroredStartPosition && { startPosition: mirroredStartPosition }),
      ...(mirroredStartingPositionBeat && {
        startingPositionBeat: mirroredStartingPositionBeat,
      }),
    });
  }

  /**
   * Mirror a single beat vertically
   */
  private mirrorBeat(beat: BeatData): BeatData {
    if (beat.isBlank) {
      return beat;
    }

    // Mirror positions (handle undefined as null)
    const mirroredStartPosition = beat.startPosition
      ? VERTICAL_MIRROR_POSITION_MAP[beat.startPosition]
      : null;
    const mirroredEndPosition = beat.endPosition
      ? VERTICAL_MIRROR_POSITION_MAP[beat.endPosition]
      : null;

    // Mirror motions
    const mirroredMotions = { ...beat.motions };

    // Mirror blue motion
    if (beat.motions[MotionColor.BLUE]) {
      const blueMotion = beat.motions[MotionColor.BLUE];
      mirroredMotions[MotionColor.BLUE] = {
        ...blueMotion,
        startLocation: VERTICAL_MIRROR_LOCATION_MAP[blueMotion.startLocation],
        endLocation: VERTICAL_MIRROR_LOCATION_MAP[blueMotion.endLocation],
        arrowLocation: VERTICAL_MIRROR_LOCATION_MAP[blueMotion.arrowLocation],
        rotationDirection: this.reverseRotationDirection(
          blueMotion.rotationDirection
        ),
      };
    }

    // Mirror red motion
    if (beat.motions[MotionColor.RED]) {
      const redMotion = beat.motions[MotionColor.RED];
      mirroredMotions[MotionColor.RED] = {
        ...redMotion,
        startLocation: VERTICAL_MIRROR_LOCATION_MAP[redMotion.startLocation],
        endLocation: VERTICAL_MIRROR_LOCATION_MAP[redMotion.endLocation],
        arrowLocation: VERTICAL_MIRROR_LOCATION_MAP[redMotion.arrowLocation],
        rotationDirection: this.reverseRotationDirection(
          redMotion.rotationDirection
        ),
      };
    }

    return createBeatData({
      ...beat,
      startPosition: mirroredStartPosition,
      endPosition: mirroredEndPosition,
      motions: mirroredMotions,
    });
  }

  /**
   * Reverse rotation direction (cw ↔ ccw, others stay same)
   */
  private reverseRotationDirection(
    direction: RotationDirection
  ): RotationDirection {
    if (direction === RotationDirection.CLOCKWISE) {
      return RotationDirection.COUNTER_CLOCKWISE;
    } else if (direction === RotationDirection.COUNTER_CLOCKWISE) {
      return RotationDirection.CLOCKWISE;
    }
    return direction; // NO_ROTATION stays the same
  }

  /**
   * Swap colors (blue ↔ red)
   * - Swaps entire blue and red motion data
   * - Swaps blue and red reversal states
   * - Updates positions based on swapped locations
   */
  swapColors(sequence: SequenceData): SequenceData {
    const swappedBeats = sequence.beats.map((beat) => this.colorSwapBeat(beat));

    // Also swap colors for the start position if it exists (both fields for compatibility)
    const swappedStartPosition = sequence.startPosition
      ? this.colorSwapBeat(sequence.startPosition)
      : undefined;
    const swappedStartingPositionBeat = sequence.startingPositionBeat
      ? this.colorSwapBeat(sequence.startingPositionBeat)
      : undefined;

    return updateSequenceData(sequence, {
      beats: swappedBeats,
      ...(swappedStartPosition && { startPosition: swappedStartPosition }),
      ...(swappedStartingPositionBeat && {
        startingPositionBeat: swappedStartingPositionBeat,
      }),
    });
  }

  /**
   * Color swap a single beat
   */
  private colorSwapBeat(beat: BeatData): BeatData {
    if (beat.isBlank) {
      return beat;
    }

    // Swap positions using swap position map (handle undefined as null)
    const swappedStartPosition = beat.startPosition
      ? SWAPPED_POSITION_MAP[beat.startPosition]
      : null;
    const swappedEndPosition = beat.endPosition
      ? SWAPPED_POSITION_MAP[beat.endPosition]
      : null;

    // Swap the motions
    const swappedMotions = {
      [MotionColor.BLUE]: beat.motions[MotionColor.RED],
      [MotionColor.RED]: beat.motions[MotionColor.BLUE],
    };

    // Update the color property in each motion to match the new color
    if (swappedMotions[MotionColor.BLUE]) {
      swappedMotions[MotionColor.BLUE] = {
        ...swappedMotions[MotionColor.BLUE],
        color: MotionColor.BLUE,
      };
    }
    if (swappedMotions[MotionColor.RED]) {
      swappedMotions[MotionColor.RED] = {
        ...swappedMotions[MotionColor.RED],
        color: MotionColor.RED,
      };
    }

    return createBeatData({
      ...beat,
      startPosition: swappedStartPosition,
      endPosition: swappedEndPosition,
      motions: swappedMotions,
      blueReversal: beat.redReversal,
      redReversal: beat.blueReversal,
    });
  }

  /**
   * Rotate sequence 45° clockwise
   * - Rotates all locations by 45° (one step: N → NE → E → SE → S → SW → W → NW → N)
   * - Derives new positions from rotated locations
   * - Toggles grid mode (DIAMOND ↔ BOX)
   *
   * Based on legacy desktop app sequence_rotater.py implementation
   */
  rotateSequence(
    sequence: SequenceData,
    _rotationAmount: number
  ): SequenceData {
    const rotatedBeats = sequence.beats.map((beat) => this.rotateBeat(beat));

    // Also rotate the start position if it exists (both fields for compatibility)
    const rotatedStartPosition = sequence.startPosition
      ? this.rotateBeat(sequence.startPosition)
      : undefined;
    const rotatedStartingPositionBeat = sequence.startingPositionBeat
      ? this.rotateBeat(sequence.startingPositionBeat)
      : undefined;

    // Toggle grid mode (DIAMOND ↔ BOX)
    const newGridMode =
      sequence.gridMode === GridMode.DIAMOND ? GridMode.BOX : GridMode.DIAMOND;

    return updateSequenceData(sequence, {
      beats: rotatedBeats,
      ...(rotatedStartPosition && { startPosition: rotatedStartPosition }),
      ...(rotatedStartingPositionBeat && {
        startingPositionBeat: rotatedStartingPositionBeat,
      }),
      gridMode: newGridMode,
    });
  }

  /**
   * Rotate a single beat 45° clockwise
   * - Rotates each location by 45° (one position in cw order)
   * - Derives new positions from rotated blue/red location pairs
   * - Creates fresh motion data with new placement data
   * - Toggles grid mode for each motion
   */
  private rotateBeat(beat: BeatData): BeatData {
    if (beat.isBlank) {
      return beat;
    }

    // Get position deriver service
    const positionDeriver = resolve<IGridPositionDeriver>(
      TYPES.IGridPositionDeriver
    );

    // Determine new grid mode (toggle DIAMOND ↔ BOX)
    const currentGridMode =
      beat.motions[MotionColor.BLUE]!.gridMode || GridMode.DIAMOND;
    const newGridMode =
      currentGridMode === GridMode.DIAMOND ? GridMode.BOX : GridMode.DIAMOND;

    // Rotate motions and locations (keep orientations - they're relative)
    const rotatedMotions = { ...beat.motions };

    // Rotate blue motion locations
    // Use createMotionData to ensure fresh placement data with new grid mode
    if (beat.motions[MotionColor.BLUE]) {
      const blueMotion = beat.motions[MotionColor.BLUE];
      // Destructure to exclude old placement data - force regeneration
      const {
        arrowPlacementData,
        propPlacementData,
        ...motionWithoutPlacement
      } = blueMotion;
      rotatedMotions[MotionColor.BLUE] = createMotionData({
        ...motionWithoutPlacement,
        startLocation: LOCATION_MAP_EIGHTH_CW[blueMotion.startLocation],
        endLocation: LOCATION_MAP_EIGHTH_CW[blueMotion.endLocation],
        arrowLocation: LOCATION_MAP_EIGHTH_CW[blueMotion.arrowLocation],
        gridMode: newGridMode,
      });
    }

    // Rotate red motion locations
    // Use createMotionData to ensure fresh placement data with new grid mode
    if (beat.motions[MotionColor.RED]) {
      const redMotion = beat.motions[MotionColor.RED];
      // Destructure to exclude old placement data - force regeneration
      const {
        arrowPlacementData,
        propPlacementData,
        ...motionWithoutPlacement
      } = redMotion;
      rotatedMotions[MotionColor.RED] = createMotionData({
        ...motionWithoutPlacement,
        startLocation: LOCATION_MAP_EIGHTH_CW[redMotion.startLocation],
        endLocation: LOCATION_MAP_EIGHTH_CW[redMotion.endLocation],
        arrowLocation: LOCATION_MAP_EIGHTH_CW[redMotion.arrowLocation],
        gridMode: newGridMode,
      });
    }

    // Derive new positions from rotated locations
    const rotatedBlueMotion = rotatedMotions[MotionColor.BLUE];
    const rotatedRedMotion = rotatedMotions[MotionColor.RED];

    let rotatedStartPosition = beat.startPosition || null;
    let rotatedEndPosition = beat.endPosition || null;

    // Derive start position from rotated start locations
    if (rotatedBlueMotion && rotatedRedMotion) {
      rotatedStartPosition = positionDeriver.getGridPositionFromLocations(
        rotatedBlueMotion.startLocation,
        rotatedRedMotion.startLocation
      );
      rotatedEndPosition = positionDeriver.getGridPositionFromLocations(
        rotatedBlueMotion.endLocation,
        rotatedRedMotion.endLocation
      );
    }

    return createBeatData({
      ...beat,
      startPosition: rotatedStartPosition,
      endPosition: rotatedEndPosition,
      motions: rotatedMotions,
    });
  }

  /**
   * Reverse sequence (NOT just reverse beat order!)
   * - Creates new start position from final beat's end position/orientations
   * - Reverses beat order
   * - Each beat: swaps positions/locations/orientations, flips rotation direction
   * - Letter is looked up from dataset based on new motion configuration
   *
   * This is a complex transformation that requires pictograph dataset lookup.
   */
  async reverseSequence(sequence: SequenceData): Promise<SequenceData> {
    if (sequence.beats.length === 0) {
      return sequence;
    }

    // Step 1: Create new start position from final beat's end position
    const finalBeat = sequence.beats[sequence.beats.length - 1]!;
    const newStartPosition = this.createStartPositionFromBeatEnd(finalBeat);

    // Step 2: Reverse and transform each beat with letter lookup
    const reversedBeats: BeatData[] = [];
    const reversedBeatArray = [...sequence.beats].reverse();
    const gridMode = sequence.gridMode || GridMode.DIAMOND; // Default to DIAMOND if undefined

    for (let index = 0; index < reversedBeatArray.length; index++) {
      const beat = reversedBeatArray[index]!;
      const reversedBeat = await this.reverseBeat(beat, index + 1, gridMode);
      reversedBeats.push(reversedBeat);
    }

    return updateSequenceData(sequence, {
      beats: reversedBeats,
      startPosition: newStartPosition,
      startingPositionBeat: newStartPosition,
      name: `${sequence.name} (Reversed)`,
    });
  }

  /**
   * Create a new start position pictograph from a beat's end state
   */
  private createStartPositionFromBeatEnd(beat: BeatData): BeatData {
    const blueMotion = beat.motions[MotionColor.BLUE];
    const redMotion = beat.motions[MotionColor.RED];

    return createBeatData({
      id: `beat-${Date.now()}`,
      letter: Letter.ALPHA, // Use Greek alpha as placeholder - TODO: lookup from dataset
      startPosition: beat.endPosition || null,
      endPosition: beat.endPosition || null,
      beatNumber: 0,
      duration: 1000,
      blueReversal: false,
      redReversal: false,
      isBlank: false,
      motions: {
        [MotionColor.BLUE]: blueMotion
          ? {
              ...blueMotion,
              motionType: MotionType.STATIC,
              rotationDirection: RotationDirection.NO_ROTATION,
              startLocation: blueMotion.endLocation,
              endLocation: blueMotion.endLocation,
              arrowLocation: blueMotion.endLocation,
              startOrientation: blueMotion.endOrientation,
              endOrientation: blueMotion.endOrientation,
              turns: 0,
            }
          : undefined,
        [MotionColor.RED]: redMotion
          ? {
              ...redMotion,
              motionType: MotionType.STATIC,
              rotationDirection: RotationDirection.NO_ROTATION,
              startLocation: redMotion.endLocation,
              endLocation: redMotion.endLocation,
              arrowLocation: redMotion.endLocation,
              startOrientation: redMotion.endOrientation,
              endOrientation: redMotion.endOrientation,
              turns: 0,
            }
          : undefined,
      },
    });
  }

  /**
   * Reverse a single beat (for playing sequence backward)
   * - Swaps start/end positions
   * - Swaps start/end locations
   * - Swaps start/end orientations
   * - Flips rotation direction (CW ↔ CCW, noRotation stays same)
   * - Keeps motionType and turns the same
   * - Looks up correct letter from pictograph dataset based on new motion configuration
   */
  private async reverseBeat(
    beat: BeatData,
    newBeatNumber: number,
    gridMode: GridMode
  ): Promise<BeatData> {
    if (beat.isBlank) {
      return { ...beat, beatNumber: newBeatNumber };
    }

    // Swap positions
    const swappedStartPosition = beat.endPosition || null;
    const swappedEndPosition = beat.startPosition || null;

    // Reverse motions
    const reversedMotions = { ...beat.motions };

    // Reverse blue motion
    if (beat.motions[MotionColor.BLUE]) {
      const blueMotion = beat.motions[MotionColor.BLUE];
      reversedMotions[MotionColor.BLUE] = {
        ...blueMotion,
        startLocation: blueMotion.endLocation,
        endLocation: blueMotion.startLocation,
        startOrientation: blueMotion.endOrientation,
        endOrientation: blueMotion.startOrientation,
        rotationDirection: this.reverseRotationDirection(
          blueMotion.rotationDirection
        ),
        // motionType and turns stay the same
      };
    }

    // Reverse red motion
    if (beat.motions[MotionColor.RED]) {
      const redMotion = beat.motions[MotionColor.RED];
      reversedMotions[MotionColor.RED] = {
        ...redMotion,
        startLocation: redMotion.endLocation,
        endLocation: redMotion.startLocation,
        startOrientation: redMotion.endOrientation,
        endOrientation: redMotion.startOrientation,
        rotationDirection: this.reverseRotationDirection(
          redMotion.rotationDirection
        ),
        // motionType and turns stay the same
      };
    }

    // Look up the correct letter from the pictograph dataset
    let correctLetter: Letter | null = beat.letter || null; // Default to original letter as fallback
    if (reversedMotions[MotionColor.BLUE] && reversedMotions[MotionColor.RED]) {
      try {
        const foundLetter =
          await this.motionQueryHandler.findLetterByMotionConfiguration(
            reversedMotions[MotionColor.BLUE],
            reversedMotions[MotionColor.RED],
            gridMode
          );
        if (foundLetter) {
          correctLetter = foundLetter as Letter;
          console.log(
            `✅ Reverse: Found letter "${correctLetter}" for reversed beat (was "${beat.letter}")`
          );
        } else {
          console.warn(
            `⚠️ Reverse: No letter found for reversed beat, keeping original letter "${beat.letter}"`
          );
        }
      } catch (error) {
        console.error(
          `❌ Reverse: Error looking up letter for reversed beat:`,
          error
        );
      }
    }

    return createBeatData({
      ...beat,
      beatNumber: newBeatNumber,
      startPosition: swappedStartPosition,
      endPosition: swappedEndPosition,
      motions: reversedMotions,
      letter: correctLetter,
    });
  }
}

/**
 * Persistence Controller
 *
 * Centralizes initialization and cross-tab workspace persistence logic for the
 * create module. The navigation controller delegates to this module whenever a
 * mode transition requires saving or loading workspace data.
 */

import type { BuildModeId } from "$shared";
import type { SequenceState } from "../SequenceStateOrchestrator.svelte";
import type { ISequencePersistenceService } from "../../services/contracts";
import type { IUndoService } from "../../services/contracts/IUndoService";
import type { OptionHistoryManager } from "./option-history-manager.svelte";

type ConstructTabState =
  | {
      setShowStartPositionPicker: (show: boolean) => void;
      setSelectedStartPosition: (value: unknown) => void;
    }
  | null
  | undefined;

type PersistenceControllerDeps = {
  sequenceState: SequenceState;
  sequencePersistenceService?: ISequencePersistenceService;
  handPathCoordinator: { initializeServices: () => void };
  optionHistoryManager: OptionHistoryManager;
  undoService: IUndoService;
};

export function createCreateModulePersistenceController({
  sequenceState,
  sequencePersistenceService,
  handPathCoordinator,
  optionHistoryManager,
  undoService,
}: PersistenceControllerDeps) {
  let isPersistenceInitialized = $state(false);

  async function initialize(): Promise<BuildModeId> {
    handPathCoordinator.initializeServices();
    let modeToLoad: BuildModeId = "constructor";

    try {
      if (sequencePersistenceService) {
        const lastActiveState =
          await sequencePersistenceService.loadCurrentState();
        if (lastActiveState?.activeBuildSection) {
          modeToLoad = lastActiveState.activeBuildSection;
        }
      }

      await sequenceState.initializeWithPersistence();

      if (sequencePersistenceService) {
        await restoreWorkspaceForMode(modeToLoad, null, {
          bypassInitializationGuard: true,
        });
      }

      optionHistoryManager.rebuildFromSequence();
      await undoService.loadHistory();
    } catch (error) {
      console.error(
        "? CreateModuleState: Failed to initialize persistence:",
        error
      );
    } finally {
      isPersistenceInitialized = true;
    }

    return modeToLoad;
  }

  async function saveCurrentState(activeSection: BuildModeId): Promise<void> {
    if (!isPersistenceInitialized) {
      return;
    }

    try {
      await sequenceState.saveCurrentState(activeSection);
    } catch (error) {
      console.error(
        "? CreateModuleState: Failed to save current state:",
        error
      );
    }
  }

  async function restoreWorkspaceForMode(
    panel: BuildModeId,
    constructTabState: ConstructTabState,
    options?: { bypassInitializationGuard?: boolean }
  ): Promise<void> {
    if (!sequencePersistenceService) {
      return;
    }

    if (!isPersistenceInitialized && !options?.bypassInitializationGuard) {
      return;
    }

    sequenceState.updateCachedActiveTab(panel);

    // Check if there's a pending deep link - if so, skip restoring saved state
    // This prevents overwriting deep link sequences with empty/old persisted state
    const { deepLinkStore } = await import(
      "$shared/navigation/utils/deep-link-store.svelte"
    );
    const hasDeepLink = deepLinkStore.has("create");

    if (hasDeepLink) {
      console.log(
        `🚫 Skipping workspace restoration for ${panel} - deep link present`
      );
      return;
    }

    // Check if there's already a sequence loaded (might be from a deep link that was just consumed)
    // Don't overwrite it with empty/null state
    const currentBeats = sequenceState.getCurrentBeats();
    const hasExistingSequence = currentBeats.length > 0;

    try {
      const savedState =
        await sequencePersistenceService.loadCurrentState(panel);

      if (savedState && !hasExistingSequence) {
        // Only load saved state if there's NO existing sequence
        // This prevents overwriting deep link sequences with old saved state
        sequenceState.setCurrentSequence(savedState.currentSequence);
        sequenceState.setSelectedStartPosition(
          savedState.selectedStartPosition ?? null
        );

        syncConstructTabState(
          constructTabState,
          savedState.hasStartPosition,
          savedState.selectedStartPosition
        );
      } else if (savedState && hasExistingSequence) {
        // Saved state exists but we already have a sequence loaded (e.g., from deep link)
        // Preserve the existing sequence instead of overwriting it
        console.log(
          `🔒 Preserving existing sequence (${currentBeats.length} beats) - ignoring saved state for ${panel}`
        );
      } else if (!hasExistingSequence) {
        // Only clear if there's no existing sequence (prevents clearing deep link sequences)
        sequenceState.setCurrentSequence(null);
        sequenceState.setSelectedStartPosition(null);
        syncConstructTabState(constructTabState, false, null);
      }
    } catch (error) {
      console.error(`? Failed to load ${panel} state:`, error);
    }
  }

  function syncConstructTabState(
    constructTabState: ConstructTabState,
    hasStartPosition: boolean,
    selectedStartPosition: unknown
  ) {
    if (!constructTabState) {
      return;
    }

    if (hasStartPosition && selectedStartPosition) {
      constructTabState.setShowStartPositionPicker(false);
      constructTabState.setSelectedStartPosition(selectedStartPosition);
    } else {
      constructTabState.setShowStartPositionPicker(true);
      constructTabState.setSelectedStartPosition(null);
    }
  }

  return {
    get isInitialized() {
      return isPersistenceInitialized;
    },
    initialize,
    saveCurrentState,
    restoreWorkspaceForMode,
  };
}

export type CreateModulePersistenceController = ReturnType<
  typeof createCreateModulePersistenceController
>;

/**
 * Explore Loader Service
 *
 * Orchestrates loading of gallery sequences from the sequence index.
 * Handles validation, metadata extraction, and data normalization.
 */

import type { GridPositionGroup } from "$shared";
import type { SequenceData } from "$shared";
import { createSequenceData } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { GridMode } from "$shared/pictograph/grid/domain/enums/grid-enums";
import { PropType } from "$shared/pictograph/prop/domain/enums/PropType";
import { inject, injectable } from "inversify";
import type { IExploreLoader } from "../contracts/IExploreLoader";
import type { IExploreMetadataExtractor } from "../contracts/IExploreMetadataExtractor";
// Constants for validation
const MAX_WORD_LENGTH = 200;
const SEQUENCE_INDEX_URL = "/sequence-index.json";

interface RawSequenceData {
  word?: string;
  name?: string;
  id?: string;
  thumbnails?: unknown;
  isFavorite?: unknown;
  isCircular?: unknown;
  tags?: unknown;
  metadata?: unknown;
  author?: string;
  gridMode?: unknown;
  difficultyLevel?: unknown;
  sequenceLength?: unknown;
  level?: unknown;
  dateAdded?: unknown;
  propType?: unknown;
  startingPosition?: unknown;
  fullMetadata?: unknown; // Bundled metadata from build script
  metadataBundled?: boolean; // Flag indicating metadata is pre-bundled
}

@injectable()
export class ExploreLoader implements IExploreLoader {
  constructor(
    @inject(TYPES.IExploreMetadataExtractor)
    private metadataExtractor: IExploreMetadataExtractor
  ) {}

  async loadSequenceMetadata(): Promise<SequenceData[]> {
    try {
      const rawSequences = await this.fetchSequenceIndex();
      const validSequences = this.processRawSequences(rawSequences);
      return validSequences;
    } catch (error) {
      console.error("❌ Failed to load sequence metadata:", error);
      throw error;
    }
  }

  /**
   * Lazy-load full sequence data including beats (only called when user opens a sequence)
   * This prevents the N+1 query problem during initial gallery load
   *
   * OPTIMIZATION: If metadata was bundled via build script, use it directly.
   * Otherwise, fetch from .meta.json file (fallback for development).
   */
  async loadFullSequenceData(
    sequenceName: string
  ): Promise<SequenceData | null> {
    try {
      // Check if we have bundled metadata in the sequence index cache
      // This would be populated if you run: npm run bundle:metadata
      const cachedSequence = this.sequenceCache.get(sequenceName);

      if (cachedSequence?.fullMetadata) {
        console.log(`⚡ Using bundled metadata for ${sequenceName}`);
        return this.createSequenceFromBundledMetadata(cachedSequence);
      }

      // Fallback: Fetch metadata from .meta.json file (slower, but works in development)
      console.log(`🔄 Fetching metadata for ${sequenceName} from .meta.json`);
      const thumbnailPath = `/gallery/${sequenceName}/${sequenceName}.webp`;
      const metadata = await this.metadataExtractor.extractMetadata(
        sequenceName,
        thumbnailPath
      );

      const gridMode = metadata.gridMode ?? GridMode.BOX;
      const dateAdded = metadata.dateAdded ?? new Date();
      const difficultyLevel = metadata.difficultyLevel ?? "beginner";
      const calculatedLevel = this.difficultyStringToLevel(difficultyLevel);

      return createSequenceData({
        id: sequenceName,
        name: this.cleanSequenceName(sequenceName),
        word: sequenceName,
        beats: metadata.beats,
        thumbnails: [thumbnailPath],
        isFavorite: false,
        isCircular: metadata.isCircular ?? false,
        tags: ["flow", "practice"],
        metadata: { source: "tka_dictionary" },
        author: metadata.author ?? "Unknown",
        gridMode,
        difficultyLevel,
        sequenceLength: metadata.sequenceLength,
        level: calculatedLevel,
        dateAdded,
        propType: metadata.propType ?? PropType.STAFF,
        startingPositionGroup: (metadata.startingPosition ??
          "alpha") as GridPositionGroup,
      });
    } catch (error) {
      console.error(
        `❌ Failed to load full sequence data for ${sequenceName}:`,
        error
      );
      return null;
    }
  }

  /**
   * Cache to store raw sequences for quick lookup during lazy loading
   */
  private sequenceCache = new Map<string, RawSequenceData>();

  /**
   * Create SequenceData from bundled metadata (instant, no HTTP request!)
   */
  private createSequenceFromBundledMetadata(
    rawSeq: RawSequenceData
  ): SequenceData | null {
    try {
      const fullMetadata = rawSeq.fullMetadata as Record<string, unknown> | undefined;
      if (!fullMetadata || typeof fullMetadata !== 'object') {
        throw new Error('Invalid fullMetadata');
      }
      const sequence = Array.isArray(fullMetadata.sequence) ? fullMetadata.sequence : [];

      // Parse beats from bundled metadata
      const beats = this.parseBundledBeats(sequence);

      const word = rawSeq.word ?? rawSeq.id ?? "";
      const gridMode = this.parseGridMode(rawSeq.gridMode) || GridMode.BOX;
      const dateAdded = this.parseDate(rawSeq.dateAdded) || new Date();
      const difficultyLevel = this.parseDifficulty(rawSeq.difficultyLevel);
      const calculatedLevel = this.difficultyStringToLevel(difficultyLevel);

      return createSequenceData({
        id: word,
        name: this.cleanSequenceName(String(rawSeq.name ?? word)),
        word,
        beats,
        thumbnails: this.parseThumbnails(rawSeq.thumbnails),
        isFavorite: Boolean(rawSeq.isFavorite),
        isCircular: Boolean(rawSeq.isCircular),
        tags: this.parseTags(rawSeq.tags),
        metadata: this.parseMetadata(rawSeq.metadata),
        author: String(rawSeq.author ?? "Unknown"),
        gridMode,
        difficultyLevel,
        sequenceLength: beats.length,
        level: calculatedLevel,
        dateAdded,
        propType: (rawSeq.propType || "Staff") as PropType,
        startingPositionGroup: (rawSeq.startingPosition ||
          "alpha") as GridPositionGroup,
      });
    } catch (error) {
      console.error(`❌ Failed to parse bundled metadata:`, error);
      return null;
    }
  }

  /**
   * Parse beats from bundled metadata (simplified version)
   * Full parsing is handled by ExploreMetadataExtractor when needed
   */
  private parseBundledBeats(sequence: unknown[]): unknown[] {
    // Handle two different metadata formats:
    // Format 1: Has explicit 'beat' field (newer format)
    // Format 2: No 'beat' field, just 'letter' field (older format)

    const hasBeatNumbers = sequence.some(
      (item) => typeof item === 'object' && item !== null && 'beat' in item && typeof (item as Record<string, unknown>).beat === "number"
    );

    if (hasBeatNumbers) {
      // Format 1: Filter out the start position (beat 0) - only return beats >= 1
      return sequence.filter((item) => {
        if (typeof item !== 'object' || item === null || !('beat' in item)) return false;
        const beat = (item as Record<string, unknown>).beat;
        return typeof beat === "number" && beat >= 1;
      });
    } else {
      // Format 2: Count items with 'letter' field
      // Exclude: sequence metadata (has 'word' field) and start position (has 'sequence_start_position')
      return sequence.filter((item) => {
        if (typeof item !== 'object' || item === null) return false;
        const obj = item as Record<string, unknown>;
        return 'letter' in obj && obj.letter && !('word' in obj) && !('sequence_start_position' in obj);
      });
    }
  }

  // ============================================================================
  // Data Loading
  // ============================================================================

  private async fetchSequenceIndex(): Promise<RawSequenceData[]> {
    // Add cache-busting parameter to force fresh load after difficulty calculator changes
    const cacheBuster = Date.now();
    const url = `${SEQUENCE_INDEX_URL}?v=${cacheBuster}`;

    const response = await fetch(url, {
      cache: "no-store", // Prevent browser caching
    });

    if (!response.ok) {
      throw new Error(`Failed to load sequence index: ${response.status}`);
    }

    const data = (await response.json()) as { sequences?: RawSequenceData[] };
    return data.sequences || [];
  }

  // ============================================================================
  // Data Processing
  // ============================================================================

  private processRawSequences(
    rawSequences: RawSequenceData[]
  ): SequenceData[] {
    const sequences: SequenceData[] = [];

    for (const rawSeq of rawSequences) {
      const word = this.extractWord(rawSeq);

      if (!this.isValidWord(word)) {
        console.warn(`🚫 Skipping invalid sequence: ${word}`);
        continue;
      }

      // Cache the raw sequence for later lazy loading
      this.sequenceCache.set(word, rawSeq);

      try {
        const sequence = this.createSequenceFromRaw(rawSeq, word);
        sequences.push(sequence);
      } catch (error) {
        const shouldLog = !this.isRoutineError(error);
        if (shouldLog) {
          console.warn(`⚠️ Failed to process ${word}:`, error);
        }

        // Use fallback sequence creation
        const fallbackSequence = this.createFallbackSequence(rawSeq, word);
        sequences.push(fallbackSequence);
      }
    }

    return sequences;
  }

  // ============================================================================
  // Sequence Creation
  // ============================================================================

  private createSequenceFromRaw(
    rawSeq: RawSequenceData,
    word: string
  ): SequenceData {
    // ⚡ PERFORMANCE FIX: Skip expensive metadata extraction during initial load
    // The sequence-index.json already has all the data we need for the gallery view
    // Full beat data will be loaded lazily when user clicks on a sequence

    const gridMode = this.parseGridMode(rawSeq.gridMode) || GridMode.BOX;
    const dateAdded = this.parseDate(rawSeq.dateAdded) || new Date();

    // Get difficulty from sequence-index.json (no need to extract from .meta.json)
    const difficultyLevel = this.parseDifficulty(rawSeq.difficultyLevel);

    // Calculate numeric level from difficulty string
    const calculatedLevel = this.difficultyStringToLevel(difficultyLevel);

    return createSequenceData({
      id: word,
      name: this.cleanSequenceName(
        String(rawSeq.name ?? word ?? "Unnamed Sequence")
      ),
      word,
      beats: [], // Empty - will be loaded lazily via loadFullSequenceData()
      thumbnails: this.parseThumbnails(rawSeq.thumbnails),
      isFavorite: Boolean(rawSeq.isFavorite),
      isCircular: Boolean(rawSeq.isCircular),
      tags: this.parseTags(rawSeq.tags),
      metadata: this.parseMetadata(rawSeq.metadata),
      author: String(rawSeq.author ?? "Unknown"),
      gridMode,
      difficultyLevel,
      sequenceLength: this.parseSequenceLength(rawSeq.sequenceLength),
      level: calculatedLevel,
      dateAdded,
      propType: (rawSeq.propType || "Staff") as PropType,
      startingPositionGroup: (rawSeq.startingPosition ||
        "alpha") as GridPositionGroup,
    });
  }

  private createFallbackSequence(
    rawSeq: RawSequenceData,
    word: string
  ): SequenceData {
    const gridMode = this.parseGridMode(rawSeq.gridMode) || GridMode.BOX;
    const dateAdded = this.parseDate(rawSeq.dateAdded) || new Date();

    // Get difficulty and calculate numeric level
    const difficultyLevel = this.parseDifficulty(rawSeq.difficultyLevel);
    const calculatedLevel = this.difficultyStringToLevel(difficultyLevel);

    return createSequenceData({
      id: word,
      name: this.cleanSequenceName(
        String(rawSeq.name ?? word ?? "Unnamed Sequence")
      ),
      word,
      beats: [],
      thumbnails: this.parseThumbnails(rawSeq.thumbnails),
      isFavorite: Boolean(rawSeq.isFavorite),
      isCircular: Boolean(rawSeq.isCircular),
      tags: this.parseTags(rawSeq.tags),
      metadata: this.parseMetadata(rawSeq.metadata),
      author: String(rawSeq.author ?? "Unknown"),
      gridMode,
      difficultyLevel,
      sequenceLength: this.parseSequenceLength(rawSeq.sequenceLength),
      level: calculatedLevel, // Use calculated level instead of old stored value
      dateAdded,
      propType: (rawSeq.propType || "Staff") as PropType,
      startingPositionGroup: (rawSeq.startingPosition ||
        "alpha") as GridPositionGroup,
    });
  }

  // ============================================================================
  // Validation
  // ============================================================================

  private extractWord(rawSeq: RawSequenceData): string {
    return rawSeq.word ?? rawSeq.name ?? rawSeq.id ?? "";
  }

  private isValidWord(word: string): boolean {
    return (
      word.length > 0 &&
      word.length <= MAX_WORD_LENGTH &&
      !word.toLowerCase().includes("test")
    );
  }

  private isRoutineError(error: unknown): boolean {
    const errorMessage = error instanceof Error ? error.message : String(error);
    return errorMessage.includes("No valid version found");
  }

  // ============================================================================
  // Data Parsing Helpers
  // ============================================================================

  private parseThumbnails(value: unknown): string[] {
    return Array.isArray(value) ? (value as string[]) : [];
  }

  private parseTags(value: unknown): string[] {
    return Array.isArray(value) ? (value as string[]) : ["flow", "practice"];
  }

  private parseMetadata(value: unknown): Record<string, unknown> {
    return typeof value === "object" && value !== null
      ? (value as Record<string, unknown>)
      : { source: "tka_dictionary" };
  }

  private parseGridMode(value: unknown): GridMode | null {
    if (!value) return null;

    if (typeof value === "string") {
      const normalized = value.toLowerCase();
      if (normalized === "gridmode.diamond" || normalized === "diamond") {
        return GridMode.DIAMOND;
      }
      if (normalized === "gridmode.box" || normalized === "box") {
        return GridMode.BOX;
      }
    }

    return value as GridMode;
  }

  private parseDifficulty(value: unknown): string {
    return typeof value === "string" ? value : "beginner";
  }

  private parseSequenceLength(value: unknown): number {
    return typeof value === "number" ? value : 0;
  }

  /**
   * Convert difficulty string to numeric level for SequenceCard styling
   * This ensures the card uses the NEW calculated difficulty, not old stored values
   */
  private difficultyStringToLevel(difficulty: string): number {
    const normalized = difficulty.toLowerCase();
    switch (normalized) {
      case "beginner":
        return 1;
      case "intermediate":
        return 2;
      case "advanced":
        return 3;
      case "mythic":
        return 4;
      case "legendary":
        return 5;
      default:
        return 1; // Default to beginner
    }
  }

  private parseDate(value: unknown): Date | null {
    if (!value) return null;

    if (value instanceof Date) {
      return value;
    }

    if (typeof value === "string") {
      const date = new Date(value);
      return isNaN(date.getTime()) ? null : date;
    }

    return null;
  }

  /**
   * Remove " Sequence" suffix from sequence names
   */
  private cleanSequenceName(name: string): string {
    return name.replace(/\s+Sequence$/i, "");
  }
}

/**
 * Explore Metadata Extractor Service
 *
 * Extracts metadata from sequence files (PNG, WebP, or JSON sidecars)
 * with proper error handling and type safety.
 */

import type { BeatData } from "$shared";
import {
  createMotionData,
  GridLocation,
  GridMode,
  MotionColor,
  MotionType,
  Orientation,
  RotationDirection,
} from "$shared";
import { TYPES } from "$shared/inversify/types";
import { PropType } from "$shared/pictograph/prop/domain/enums/PropType";
import { UniversalMetadataExtractor } from "$shared/services/UniversalMetadataExtractor";
import { inject, injectable } from "inversify";
import type {
  IExploreMetadataExtractor,
  SequenceMetadata,
} from "../contracts/IExploreMetadataExtractor";
import type { ISequenceDifficultyCalculator } from "../contracts/ISequenceDifficultyCalculator";
// Constants for metadata extraction
// Using function to avoid module-level enum reference (fixes test initialization)
const getDefaultMetadata = (): SequenceMetadata => ({
  beats: [],
  author: "Unknown",
  difficultyLevel: "beginner",
  dateAdded: new Date(),
  gridMode: GridMode.DIAMOND,
  isCircular: false,
  propType: "Staff" as PropType,
  sequenceLength: 0,
  startingPosition: "alpha",
});

const DATE_FIELD_NAMES = [
  "date_added",
  "dateAdded",
  "date",
  "created_date",
  "timestamp",
] as const;

@injectable()
export class ExploreMetadataExtractor implements IExploreMetadataExtractor {
  constructor(
    @inject(TYPES.ISequenceDifficultyCalculator)
    private readonly difficultyCalculator: ISequenceDifficultyCalculator
  ) {}

  async extractMetadata(
    sequenceName: string,
    thumbnailPath?: string
  ): Promise<SequenceMetadata> {
    try {
      const sequenceWithVersion = this.determineSequenceVersion(
        sequenceName,
        thumbnailPath
      );

      const result =
        await UniversalMetadataExtractor.extractMetadata(sequenceWithVersion);

      if (!result.success || !result.data) {
        return getDefaultMetadata();
      }

      return this.parseMetadataResult(sequenceName, result.data);
    } catch (error) {
      const errorMessage =
        error instanceof Error ? error.message : String(error);

      // Only log non-routine errors
      if (!errorMessage.includes("No valid version found")) {
        console.warn(
          `⚠️ Failed to extract metadata for ${sequenceName}:`,
          error
        );
      }

      throw error;
    }
  }

  /**
   * Determine the sequence version from thumbnail path or default to ver1
   */
  private determineSequenceVersion(
    sequenceName: string,
    thumbnailPath?: string
  ): string {
    if (!thumbnailPath) {
      return `${sequenceName}_ver1`;
    }

    const versionMatch = thumbnailPath.match(/_ver(\d+)\.webp$/);
    if (versionMatch) {
      const version = versionMatch[1];
      return `${sequenceName}_ver${version}`;
    }

    return `${sequenceName}_ver1`;
  }

  /**
   * Parse raw metadata into typed SequenceMetadata
   */
  private parseMetadataResult(
    sequenceName: string,
    rawData: Record<string, unknown>
  ): SequenceMetadata {
    const beats = this.parseBeats(sequenceName, rawData["sequence"]);
    const gridMode = this.parseGridMode(rawData["grid_mode"]);

    // Calculate difficulty from actual sequence data instead of stored level
    const difficultyLevel = this.calculateDifficultyLevel(beats);

    const dateAdded = this.parseDateAdded(rawData);
    const startingPosition = this.parseStartingPosition(beats);

    return {
      beats,
      author: String(rawData["author"] || "Unknown"),
      difficultyLevel,
      dateAdded,
      gridMode,
      isCircular: Boolean(rawData["is_circular"]),
      propType: String(rawData["prop_type"] || "Staff") as PropType,
      sequenceLength: beats.length,
      startingPosition,
    };
  }

  /**
   * Parse beat data from sequence array with full motion parsing
   */
  private parseBeats(sequenceName: string, sequence: unknown): BeatData[] {
    if (!Array.isArray(sequence)) {
      return [];
    }

    return sequence.map((step: unknown, index: number) => {
      const stepData = step as Record<string, unknown>;
      const blueAttrs = stepData["blue_attributes"] as Record<string, unknown>;
      const redAttrs = stepData["red_attributes"] as Record<string, unknown>;

      return {
        // PictographData properties
        id: `beat-${sequenceName}-${index + 1}`,
        letter: String(stepData["letter"] || ""),
        startPosition: null,
        endPosition: null,
        motions: {
          [MotionColor.BLUE]: blueAttrs &&
            typeof blueAttrs === "object" &&
            blueAttrs !== null
            ? createMotionData({
                color: MotionColor.BLUE,
                motionType: this.parseMotionType(blueAttrs["motion_type"]),
                startLocation: this.parseLocation(blueAttrs["start_loc"]),
                endLocation: this.parseLocation(blueAttrs["end_loc"]),
                startOrientation: this.parseOrientation(blueAttrs["start_ori"]),
                endOrientation: this.parseOrientation(blueAttrs["end_ori"]),
                rotationDirection: this.parseRotationDirection(
                  blueAttrs["prop_rot_dir"]
                ),
                turns: this.parseTurns(blueAttrs["turns"]),
                isVisible: true,
                propType: PropType.STAFF,
                arrowLocation: this.parseLocation(blueAttrs["start_loc"]),
                gridMode: GridMode.DIAMOND,
              })
            : undefined,
          [MotionColor.RED]: redAttrs &&
            typeof redAttrs === "object" &&
            redAttrs !== null
            ? createMotionData({
                color: MotionColor.RED,
                motionType: this.parseMotionType(redAttrs["motion_type"]),
                startLocation: this.parseLocation(redAttrs["start_loc"]),
                endLocation: this.parseLocation(redAttrs["end_loc"]),
                startOrientation: this.parseOrientation(redAttrs["start_ori"]),
                endOrientation: this.parseOrientation(redAttrs["end_ori"]),
                rotationDirection: this.parseRotationDirection(
                  redAttrs["prop_rot_dir"]
                ),
                turns: this.parseTurns(redAttrs["turns"]),
                isVisible: true,
                propType: PropType.STAFF,
                arrowLocation: this.parseLocation(redAttrs["start_loc"]),
                gridMode: GridMode.DIAMOND,
              })
            : undefined,
        },
        // Beat context properties
        beatNumber: Number(stepData["beat"] || index + 1),
        duration: 1.0,
        blueReversal: false,
        redReversal: false,
        isBlank: false,
      } as BeatData;
    });
  }

  /**
   * Parse grid mode with fallback
   */
  private parseGridMode(gridModeValue: unknown): GridMode {
    if (gridModeValue === "diamond") {
      return GridMode.DIAMOND;
    }
    if (gridModeValue === "box") {
      return GridMode.BOX;
    }
    return GridMode.DIAMOND;
  }

  /**
   * Calculate difficulty level from actual beat data
   * Replaces the old parseDifficultyLevel that just read a stored value
   */
  private calculateDifficultyLevel(beats: BeatData[]): string {
    const numericLevel =
      this.difficultyCalculator.calculateDifficultyLevel(beats);
    return this.difficultyCalculator.levelToString(numericLevel);
  }

  /**
   * Parse motion type with fallback
   */
  private parseMotionType(value: unknown): MotionType {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "pro":
        return MotionType.PRO;
      case "anti":
        return MotionType.ANTI;
      case "float":
        return MotionType.FLOAT;
      case "dash":
        return MotionType.DASH;
      case "static":
        return MotionType.STATIC;
      default:
        return MotionType.STATIC;
    }
  }

  /**
   * Parse grid location with fallback
   */
  private parseLocation(value: unknown): GridLocation {
    const str = String(value || "").toUpperCase();
    // Map common location strings to GridLocation enum values
    const locationMap: Record<string, GridLocation> = {
      N: GridLocation.NORTH,
      NORTH: GridLocation.NORTH,
      E: GridLocation.EAST,
      EAST: GridLocation.EAST,
      S: GridLocation.SOUTH,
      SOUTH: GridLocation.SOUTH,
      W: GridLocation.WEST,
      WEST: GridLocation.WEST,
      NE: GridLocation.NORTHEAST,
      NORTHEAST: GridLocation.NORTHEAST,
      SE: GridLocation.SOUTHEAST,
      SOUTHEAST: GridLocation.SOUTHEAST,
      SW: GridLocation.SOUTHWEST,
      SOUTHWEST: GridLocation.SOUTHWEST,
      NW: GridLocation.NORTHWEST,
      NORTHWEST: GridLocation.NORTHWEST,
    };
    return locationMap[str] || GridLocation.NORTH;
  }

  /**
   * Parse orientation with fallback
   */
  private parseOrientation(value: unknown): Orientation {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "in":
        return Orientation.IN;
      case "out":
        return Orientation.OUT;
      case "clock":
      case "clockwise":
        return Orientation.CLOCK;
      case "counter":
      case "counterclockwise":
        return Orientation.COUNTER;
      default:
        return Orientation.IN;
    }
  }

  /**
   * Parse rotation direction with fallback
   */
  private parseRotationDirection(value: unknown): RotationDirection {
    const str = String(value || "").toLowerCase();
    switch (str) {
      case "cw":
      case "clockwise":
        return RotationDirection.CLOCKWISE;
      case "ccw":
      case "counterclockwise":
      case "counter_clockwise":
        return RotationDirection.COUNTER_CLOCKWISE;
      case "no_rotation":
      case "norotation":
        return RotationDirection.NO_ROTATION;
      default:
        return RotationDirection.NO_ROTATION;
    }
  }

  /**
   * Parse turns value (can be number or "fl" for float)
   */
  private parseTurns(value: unknown): number | "fl" {
    if (value === "fl" || value === "float") {
      return "fl";
    }
    const num = Number(value);
    return isNaN(num) ? 0 : num;
  }

  /**
   * Parse date from various possible field names
   */
  private parseDateAdded(rawData: Record<string, unknown>): Date {
    for (const fieldName of DATE_FIELD_NAMES) {
      const fieldValue = rawData[fieldName];
      if (fieldValue) {
        try {
          const date = new Date(String(fieldValue));
          if (!isNaN(date.getTime())) {
            return date;
          }
        } catch {
          // Continue to next field
        }
      }
    }

    return new Date();
  }

  /**
   * Extract starting position from first beat
   */
  private parseStartingPosition(beats: BeatData[]): string {
    if (beats.length > 0) {
      const firstLetter = beats[0]?.letter;
      if (firstLetter) {
        return firstLetter;
      }
    }
    return "alpha";
  }
}

/**
 * Sequence Difficulty Calculator Implementation
 *
 * Analyzes sequence beat data to determine difficulty level based on:
 * - Turn values (0, whole numbers, half values, floats)
 * - Orientation types (radial IN/OUT vs non-radial CLOCK/COUNTER)
 *
 * This replaces storing difficulty in metadata and calculates it on-the-fly.
 */

import { injectable } from "inversify";
import type { BeatData } from "$shared";
import {
  Orientation,
  MotionColor,
} from "$shared/pictograph/shared/domain/enums/pictograph-enums";
import type { ISequenceDifficultyCalculator } from "../contracts/ISequenceDifficultyCalculator";

@injectable()
export class SequenceDifficultyCalculator
  implements ISequenceDifficultyCalculator
{
  /**
   * Calculate difficulty level by analyzing all beats in the sequence
   */
  calculateDifficultyLevel(beats: BeatData[]): number {
    if (beats.length === 0) {
      return 1; // Default to beginner for empty sequences
    }

    let hasNonRadialOrientation = false;
    let hasTurns = false;

    // Analyze all beats
    for (const beat of beats) {

      // Check both blue and red motions
      const blueMotion = beat.motions[MotionColor.BLUE];
      const redMotion = beat.motions[MotionColor.RED];

      // Check for non-radial orientations
      if (this.hasNonRadialOrientation(blueMotion, redMotion)) {
        hasNonRadialOrientation = true;
      }

      // Check for turns
      if (this.hasTurns(blueMotion, redMotion)) {
        hasTurns = true;
      }
    }

    // Determine level based on findings
    if (hasNonRadialOrientation) {
      return 3; // Level 3: Contains non-radial orientations
    } else if (hasTurns) {
      return 2; // Level 2: Contains turns with radial orientations only
    } else {
      return 1; // Level 1: No turns, only radial orientations
    }
  }

  /**
   * Convert numeric level to difficulty string
   */
  levelToString(level: number): string {
    switch (level) {
      case 1:
        return "beginner";
      case 2:
        return "intermediate";
      case 3:
        return "advanced";
      default:
        return "beginner";
    }
  }

  /**
   * Check if any motion has non-radial orientations (CLOCK or COUNTER)
   */
  private hasNonRadialOrientation(blueMotion: MotionData | null, redMotion: MotionData | null): boolean {
    /* eslint-disable @typescript-eslint/no-unsafe-member-access */
    const orientationsToCheck = [
      blueMotion?.startOrientation,
      blueMotion?.endOrientation,
      redMotion?.startOrientation,
      redMotion?.endOrientation,
    ].filter((o): o is Orientation => o !== undefined && o !== null);
    /* eslint-enable @typescript-eslint/no-unsafe-member-access */

    return orientationsToCheck.some(
      (orientation) =>
        orientation === Orientation.CLOCK || orientation === Orientation.COUNTER
    );
  }

  /**
   * Check if any motion has turns greater than 0
   * Handles both numeric turns and "fl" (float) values
   */
  private hasTurns(blueMotion: MotionData | null, redMotion: MotionData | null): boolean {
    return this.motionHasTurns(blueMotion) || this.motionHasTurns(redMotion);
  }

  /**
   * Check if a single motion has turns
   */
  private motionHasTurns(motion: MotionData | null): boolean {
    /* eslint-disable @typescript-eslint/no-unsafe-member-access */
    if (!motion) {
      return false;
    }

    // Handle "fl" (float) case - this is considered a turn
    if (motion.turns === "fl") {
      return true;
    }

    // Handle numeric turns
    if (typeof motion.turns === "number") {
      return motion.turns > 0;
    }
    /* eslint-enable @typescript-eslint/no-unsafe-member-access */

    return false;
  }
}

import type { SequenceData } from "$shared";
import type { IExploreThumbnailService } from "../../gallery/display/services/contracts";
import { openSpotlightViewer } from "../../../../shared/application/state/app-state.svelte";
import { navigationState } from "../../../../shared/navigation/state/navigation-state.svelte";
import { galleryPanelManager } from "../state/gallery-panel-state.svelte";

interface GalleryState {
  selectSequence(sequence: SequenceData): void;
  toggleFavorite(sequenceId: string): Promise<void>;
  openAnimationModal(sequence: SequenceData): void;
  loadAllSequences(): Promise<void>;
}

interface DeleteConfirmationData {
  sequence?: SequenceData;
}

interface ExploreHandlersParams {
  galleryState: GalleryState;
  setSelectedSequence: (sequence: SequenceData | null) => void;
  setDeleteConfirmationData: (data: DeleteConfirmationData | null) => void;
  setError: (error: string | null) => void;
  thumbnailService: IExploreThumbnailService;
}

export function useExploreHandlers({
  galleryState,
  setSelectedSequence,
  setDeleteConfirmationData,
  setError,
  thumbnailService,
}: ExploreHandlersParams) {
  function handleSequenceSelect(sequence: SequenceData) {
    setSelectedSequence(sequence);
    galleryState.selectSequence(sequence);
  }

  async function handleSequenceAction(action: string, sequence: SequenceData) {
    try {
      switch (action) {
        case "select":
          handleSequenceSelect(sequence);
          break;
        case "view-detail":
          handleViewDetail(sequence);
          break;
        case "delete":
          handleSequenceDelete(sequence);
          break;
        case "favorite":
          await galleryState.toggleFavorite(sequence.id);
          break;
        case "fullscreen":
          handleSpotlightView(sequence);
          break;
        case "animate":
          galleryState.openAnimationModal(sequence);
          break;
        default:
          console.warn("⚠️ Unknown action:", action);
      }
    } catch (err) {
      console.error("❌ Action failed:", err);
      setError(
        err instanceof Error ? err.message : `Failed to ${action} sequence`
      );
    }
  }

  function handleViewDetail(sequence: SequenceData) {
    galleryPanelManager.openDetail(sequence);
  }

  function handleCloseDetailPanel() {
    galleryPanelManager.close();
  }

  function handleEditSequence(sequence: SequenceData) {
    try {
      // Store the sequence data in localStorage for the Create module to pick up
      localStorage.setItem(
        "tka-pending-edit-sequence",
        JSON.stringify(sequence)
      );

      // Close the detail panel if open
      handleCloseDetailPanel();

      // Navigate to Create module's construct tab
      navigationState.setCurrentModule("create");
      navigationState.setCurrentSection("construct");

      console.log("🖊️ Navigating to edit sequence:", sequence.id);
    } catch (err) {
      console.error("❌ Failed to initiate edit:", err);
      setError(
        err instanceof Error
          ? err.message
          : "Failed to open sequence for editing"
      );
    }
  }

  async function handleDetailPanelAction(
    action: string,
    sequence: SequenceData
  ) {
    // Handle actions from the detail panel
    switch (action) {
      case "play":
      case "animate":
        galleryState.openAnimationModal(sequence);
        break;
      case "fullscreen":
        handleSpotlightView(sequence);
        break;
      case "favorite":
        await galleryState.toggleFavorite(sequence.id);
        break;
      case "edit":
        handleEditSequence(sequence);
        break;
      case "delete":
        handleSequenceDelete(sequence);
        handleCloseDetailPanel(); // Close panel before showing delete dialog
        break;
      default:
        console.warn("⚠️ Unknown detail panel action:", action);
    }
  }

  function handleSequenceDelete(sequence: SequenceData) {
    setDeleteConfirmationData({
      sequence: sequence,
      relatedSequences: [],
      totalCount: 1,
    });
  }

  function handleSpotlightView(sequence: SequenceData) {
    openSpotlightViewer(sequence, thumbnailService);

    // Also update URL for sharing/bookmarking
    void import("$shared/navigation/utils/sheet-router").then(
      ({ openSpotlight }) => {
        openSpotlight(sequence.id);
      }
    );
  }

  async function handleDeleteConfirm(deleteConfirmationData: DeleteConfirmationData | null) {
    if (!deleteConfirmationData?.sequence) return;

    try {
      // TODO: Implement actual delete logic
      console.log("🗑️ Deleting sequence:", deleteConfirmationData.sequence.id);
      setDeleteConfirmationData(null);
      // Refresh the sequence list
      await galleryState.loadAllSequences();
    } catch (err) {
      console.error("❌ Delete failed:", err);
      setError(
        err instanceof Error ? err.message : "Failed to delete sequence"
      );
    }
  }

  function handleDeleteCancel() {
    setDeleteConfirmationData(null);
  }

  function handleErrorDismiss() {
    setError(null);
  }

  function handleRetry() {
    setError(null);
    galleryState.loadAllSequences();
  }

  return {
    handleSequenceSelect,
    handleSequenceAction,
    handleViewDetail,
    handleCloseDetailPanel,
    handleEditSequence,
    handleDetailPanelAction,
    handleSequenceDelete,
    handleSpotlightView,
    handleDeleteConfirm,
    handleDeleteCancel,
    handleErrorDismiss,
    handleRetry,
  };
}

/**
 * Lesson Config Service
 *
 * Manages lesson configurations, validation, and provides utilities
 * for working with different lesson types and quiz modes.
 */

import {
  LESSON_CONFIGS,
  LESSON_INFO,
  LESSON_TYPE_NAMES,
  QUIZ_DEFAULTS,
  QUIZ_MODE_NAMES,
  QuizAnswerFormat,
  QuizMode,
  QuizQuestionFormat,
  QuizType,
  type QuizConfig,
  type QuizInfo,
} from "../../domain";

export class QuizConfigurator {
  /**
   * Get configuration for a specific lesson type.
   */
  static getQuizConfig(lessonType: QuizType): QuizConfig {
    const config = LESSON_CONFIGS[lessonType];
    // Convert to domain QuizConfig format
    return {
      id: config.type,
      type: config.type,
      name: config.quizDescription,
      description: config.questionPrompt || "",
      includedCategories: [], // Default empty array
      lessonType: config.lessonType,
      questionFormat: config.questionFormat as QuizQuestionFormat,
      answerFormat: config.answerFormat as QuizAnswerFormat,
      quizDescription: config.quizDescription,
      questionPrompt: config.questionPrompt || "",
    };
  }

  /**
   * Get lesson information for display.
   */
  static getQuizInfo(lessonType: QuizType): QuizInfo {
    const info = LESSON_INFO.find((lesson) => lesson.lessonType === lessonType);
    if (!info) {
      throw new Error(`No lesson info found for lesson type: ${lessonType}`);
    }
    return info;
  }

  /**
   * Get all available lesson types.
   */
  static getAvailableQuizTypes(): QuizType[] {
    return Object.values(QuizType);
  }

  /**
   * Get all available quiz modes.
   */
  static getAvailableQuizModes(): QuizMode[] {
    return Object.values(QuizMode);
  }

  /**
   * Get display name for a lesson type.
   */
  static getQuizTypeName(lessonType: QuizType): string {
    return LESSON_TYPE_NAMES[lessonType] || lessonType;
  }

  /**
   * Get display name for a quiz mode.
   */
  static getQuizModeName(quizMode: QuizMode): string {
    return QUIZ_MODE_NAMES[quizMode] || quizMode;
  }

  /**
   * Get total questions for a quiz mode.
   */
  static getTotalQuestions(quizMode: QuizMode): number {
    switch (quizMode) {
      case QuizMode.FIXED_QUESTION:
        return QUIZ_DEFAULTS.FIXED_QUESTION_COUNT;
      case QuizMode.COUNTDOWN:
        return 0; // Unlimited questions in countdown mode
      default:
        return QUIZ_DEFAULTS.FIXED_QUESTION_COUNT;
    }
  }

  /**
   * Get quiz time for a quiz mode.
   */
  static getQuizTime(quizMode: QuizMode): number {
    switch (quizMode) {
      case QuizMode.COUNTDOWN:
        return QUIZ_DEFAULTS.COUNTDOWN_TIME_SECONDS;
      case QuizMode.FIXED_QUESTION:
        return 0; // No time limit for fixed question mode
      default: // QuizMode.FIXED_QUESTION
        return QUIZ_DEFAULTS.COUNTDOWN_TIME_SECONDS;
    }
  }

  /**
   * Validate lesson configuration.
   */
  static validateQuizConfig(config: QuizConfig): boolean {
    return (
      Object.values(QuizType).includes(config.lessonType) &&
      Object.values(QuizQuestionFormat).includes(config.questionFormat) &&
      Object.values(QuizAnswerFormat).includes(config.answerFormat) &&
      typeof config.quizDescription === "string" &&
      typeof config.questionPrompt === "string"
    );
  }

  /**
   * Check if a lesson type supports a specific question format.
   */
  static supportsQuestionFormat(
    lessonType: QuizType,
    format: QuizQuestionFormat
  ): boolean {
    const config = this.getQuizConfig(lessonType);
    return config.questionFormat === format;
  }

  /**
   * Check if a lesson type supports a specific answer format.
   */
  static supportsAnswerFormat(
    lessonType: QuizType,
    format: QuizAnswerFormat
  ): boolean {
    const config = this.getQuizConfig(lessonType);
    return config.answerFormat === format;
  }

  /**
   * Get quiz number from quiz type (for display purposes).
   */
  static getQuizNumber(quizType: QuizType): number {
    switch (quizType) {
      case QuizType.PICTOGRAPH_TO_LETTER:
        return 1;
      case QuizType.LETTER_TO_PICTOGRAPH:
        return 2;
      case QuizType.VALID_NEXT_PICTOGRAPH:
        return 3;
      default:
        return 0;
    }
  }

  /**
   * Get quiz type from quiz number.
   */
  static getQuizTypeFromNumber(quizNumber: number): QuizType | null {
    switch (quizNumber) {
      case 1:
        return QuizType.PICTOGRAPH_TO_LETTER;
      case 2:
        return QuizType.LETTER_TO_PICTOGRAPH;
      case 3:
        return QuizType.VALID_NEXT_PICTOGRAPH;
      default:
        return null;
    }
  }

  /**
   * Get formatted quiz title for display.
   */
  static getFormattedQuizTitle(quizType: QuizType): string {
    const quizNumber = this.getQuizNumber(quizType);
    const quizName = this.getQuizTypeName(quizType);
    return `Quiz ${quizNumber}: ${quizName}`;
  }

  /**
   * Get quiz description for display.
   */
  static getQuizDescription(quizType: QuizType): string {
    const info = this.getQuizInfo(quizType);
    return info.description;
  }

  /**
   * Check if a quiz is available (for future use with unlocking system).
   */
  static isQuizAvailable(_quizType: QuizType): boolean {
    // For now, all quizzes are available
    // This can be extended to support quiz unlocking logic
    return true;
  }

  /**
   * Get recommended quiz mode for a quiz type.
   */
  static getRecommendedQuizMode(quizType: QuizType): QuizMode {
    // For beginners, start with fixed questions
    // More advanced quizzes could default to countdown
    switch (quizType) {
      case QuizType.PICTOGRAPH_TO_LETTER:
        return QuizMode.FIXED_QUESTION;
      case QuizType.LETTER_TO_PICTOGRAPH:
        return QuizMode.FIXED_QUESTION;
      case QuizType.VALID_NEXT_PICTOGRAPH:
        return QuizMode.COUNTDOWN; // More challenging quiz
      default:
        return QuizMode.FIXED_QUESTION;
    }
  }

  /**
   * Get difficulty level for a quiz type (1-5 scale).
   */
  static getDifficultyLevel(quizType: QuizType): number {
    switch (quizType) {
      case QuizType.PICTOGRAPH_TO_LETTER:
        return 1; // Easiest - just matching pictograph to letter
      case QuizType.LETTER_TO_PICTOGRAPH:
        return 2; // Medium - requires understanding pictograph structure
      case QuizType.VALID_NEXT_PICTOGRAPH:
        return 4; // Hardest - requires understanding flow and transitions
      default:
        return 1;
    }
  }

  /**
   * Get estimated completion time for a quiz (in minutes).
   */
  static getEstimatedCompletionTime(
    quizType: QuizType,
    quizMode: QuizMode
  ): number {
    const baseTime = this.getDifficultyLevel(quizType) * 2; // 2 minutes per difficulty level

    switch (quizMode) {
      case QuizMode.FIXED_QUESTION:
        return baseTime + 5; // Add 5 minutes for fixed questions
      case QuizMode.COUNTDOWN:
        return 2; // Countdown mode is always 2 minutes
      default:
        return baseTime;
    }
  }
}

/**
 * Lesson Repo Implementation
 *
 * Service implementation for lesson configuration management and retrieval.
 * Handles lesson types, configurations, and category management.
 */

import { inject, injectable } from "inversify";
import { TYPES } from "$shared/inversify/types";
import type { ICodexLetterMappingRepo } from "../../../codex";
import type { LetterCategory } from "../../../codex";
import type { QuizConfig } from "../../domain";
import { QuizAnswerFormat, QuizQuestionFormat, QuizType } from "../../domain";
import type { IQuizRepoManager } from "../contracts";

@injectable()
export class QuizRepoManager implements IQuizRepoManager {
  private configurations: Map<string, QuizConfig> = new Map();
  private initialized = false;

  constructor(
    @inject(TYPES.ICodexLetterMappingRepo)
    private letterMappingRepo: ICodexLetterMappingRepo
  ) {}

  async initialize(): Promise<void> {
    if (this.initialized) {
      return;
    }

    try {
      // Initialize letter mapping repository first
      if (this.letterMappingRepo.initialize) {
        await this.letterMappingRepo.initialize();
      }

      // Load lesson configurations
      await this.loadQuizConfigs();

      this.initialized = true;
    } catch (error) {
      console.error("Failed to initialize QuizRepoManager:", error);
      throw error;
    }
  }

  getQuizConfig(lessonType: string): QuizConfig | null {
    if (!this.initialized) {
      console.warn("QuizRepoManager not initialized. Call initialize() first.");
      return null;
    }

    return this.configurations.get(lessonType) ?? null;
  }

  getAllQuizTypes(): string[] {
    if (!this.initialized) {
      console.warn("QuizRepoManager not initialized. Call initialize() first.");
      return [];
    }

    return Array.from(this.configurations.keys());
  }

  getLettersForLesson(lessonType: string): string[] {
    const config = this.getQuizConfig(lessonType);
    if (!config) {
      return [];
    }

    // Extract letters from lesson configuration
    return config.includedLetters ?? [];
  }

  getLessonCategories(lessonType: string): LetterCategory[] {
    const config = this.getQuizConfig(lessonType);
    if (!config) {
      return [];
    }

    // Extract categories from lesson configuration
    return config.categories ?? [];
  }

  private loadQuizConfigs(): void {
    try {
      // TODO: Load from actual lesson configuration files
      // For now, create some default configurations
      const defaultLessons: QuizConfig[] = [
        {
          id: "basic-letters",
          type: "basic-letters",
          name: "Basic Letters",
          description: "Learn the basic letter formations",
          includedCategories: ["basic" as LetterCategory],
          letters: ["A", "B", "C", "D", "E"],
          categories: ["basic" as LetterCategory],
          difficulty: 1,
          lessonType: QuizType.PICTOGRAPH_TO_LETTER,
          questionFormat: QuizQuestionFormat.PICTOGRAPH,
          answerFormat: QuizAnswerFormat.BUTTON,
          quizDescription: "",
          questionPrompt: "",
        },
        {
          id: "advanced-letters",
          type: "advanced-letters",
          name: "Advanced Letters",
          description: "Master advanced letter combinations",
          includedCategories: ["extended" as LetterCategory],
          letters: ["F", "G", "H", "I", "J"],
          categories: ["extended" as LetterCategory],
          difficulty: 2,
          lessonType: QuizType.PICTOGRAPH_TO_LETTER,
          questionFormat: QuizQuestionFormat.PICTOGRAPH,
          answerFormat: QuizAnswerFormat.BUTTON,
          quizDescription: "",
          questionPrompt: "",
        },
      ];

      // Store configurations
      for (const lesson of defaultLessons) {
        if (lesson.id) {
          this.configurations.set(lesson.id, lesson);
        }
      }
    } catch (error) {
      console.error("Failed to load lesson configurations:", error);
      throw error;
    }
  }
