/**
 * ConceptProgressService
 *
 * Manages user progress through the TKA learning path.
 * Handles concept unlocking, progress tracking, and persistence.
 */

import {
  TKA_CONCEPTS,
  isConceptUnlocked,
  type ConceptProgress,
  type ConceptStatus,
  type LearningProgress,
} from "../domain";

const STORAGE_KEY = "tka_learning_progress";

export class ConceptProgressService {
  private progress: LearningProgress;
  private subscribers: Set<(progress: LearningProgress) => void> = new Set();

  constructor() {
    this.progress = this.loadProgress();
  }

  /**
   * Load progress from localStorage or create new
   */
  private loadProgress(): LearningProgress {
    try {
      const stored = localStorage.getItem(STORAGE_KEY);
      if (stored) {
        const data = JSON.parse(stored) as Partial<LearningProgress> & { concepts?: Record<string, unknown>; completedConcepts?: string[]; lastUpdated?: string };
        return {
          ...(data as Partial<LearningProgress>),
          concepts: new Map(Object.entries(data.concepts || {})),
          completedConcepts: new Set(data.completedConcepts || []),
          lastUpdated: new Date(data.lastUpdated || Date.now()),
        };
      }
    } catch (error) {
      console.warn("Failed to load learning progress:", error);
    }

    // Create fresh progress
    return {
      concepts: new Map(),
      completedConcepts: new Set(),
      overallProgress: 0,
      totalCorrect: 0,
      totalTimeSpent: 0,
      badges: [],
      lastUpdated: new Date(),
    };
  }

  /**
   * Save progress to localStorage
   */
  private saveProgress(): void {
    try {
      const data = {
        ...this.progress,
        concepts: Object.fromEntries(this.progress.concepts),
        completedConcepts: Array.from(this.progress.completedConcepts),
        lastUpdated: this.progress.lastUpdated.toISOString(),
      };
      localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
      this.notifySubscribers();
    } catch (error) {
      console.error("Failed to save learning progress:", error);
    }
  }

  /**
   * Subscribe to progress changes
   */
  subscribe(callback: (progress: LearningProgress) => void): () => void {
    this.subscribers.add(callback);
    return () => this.subscribers.delete(callback);
  }

  /**
   * Notify all subscribers of progress changes
   */
  private notifySubscribers(): void {
    this.subscribers.forEach((callback) => callback(this.progress));
  }

  /**
   * Get current progress state
   */
  getProgress(): LearningProgress {
    return { ...this.progress };
  }

  /**
   * Get status of a specific concept
   */
  getConceptStatus(conceptId: string): ConceptStatus {
    const progress = this.progress.concepts.get(conceptId);
    if (progress) return progress.status;

    // Check if unlocked
    if (isConceptUnlocked(conceptId, this.progress.completedConcepts)) {
      return "available";
    }

    return "locked";
  }

  /**
   * Get progress for a specific concept
   */
  getConceptProgress(conceptId: string): ConceptProgress {
    const existing = this.progress.concepts.get(conceptId);
    if (existing) return existing;

    // Create new progress entry
    const status = this.getConceptStatus(conceptId);
    return {
      conceptId,
      status,
      percentComplete: 0,
      correctAnswers: 0,
      incorrectAnswers: 0,
      totalAttempts: 0,
      accuracy: 0,
      currentStreak: 0,
      bestStreak: 0,
      timeSpentSeconds: 0,
    };
  }

  /**
   * Start learning a concept
   */
  startConcept(conceptId: string): void {
    const status = this.getConceptStatus(conceptId);
    if (status === "locked") {
      throw new Error(`Concept ${conceptId} is locked`);
    }

    const progress = this.getConceptProgress(conceptId);

    if (progress.status === "available") {
      progress.status = "in-progress";
      progress.startedAt = new Date();
    }

    this.progress.concepts.set(conceptId, progress);
    this.progress.currentConceptId = conceptId;
    this.progress.lastUpdated = new Date();

    this.saveProgress();
  }

  /**
   * Record a practice attempt
   */
  recordPracticeAttempt(
    conceptId: string,
    correct: boolean,
    timeSpentSeconds: number = 0
  ): void {
    const progress = this.getConceptProgress(conceptId);

    progress.totalAttempts++;
    progress.timeSpentSeconds += timeSpentSeconds;

    if (correct) {
      progress.correctAnswers++;
      progress.currentStreak++;
      progress.bestStreak = Math.max(
        progress.bestStreak,
        progress.currentStreak
      );
      this.progress.totalCorrect++;
    } else {
      progress.incorrectAnswers++;
      progress.currentStreak = 0;
    }

    // Calculate accuracy
    progress.accuracy =
      (progress.correctAnswers / progress.totalAttempts) * 100;

    // Calculate progress percentage (example: 10 correct answers = 100%)
    progress.percentComplete = Math.min(
      (progress.correctAnswers / 10) * 100,
      100
    );

    // Update practice timing
    progress.lastPracticedAt = new Date();
    progress.nextPracticeAt = this.calculateNextPracticeDate(progress);

    // Check if concept is completed
    if (
      progress.percentComplete >= 100 &&
      progress.accuracy >= 80 &&
      progress.status !== "completed"
    ) {
      this.completeConcept(conceptId);
    } else {
      this.progress.concepts.set(conceptId, progress);
    }

    this.progress.totalTimeSpent += timeSpentSeconds;
    this.progress.lastUpdated = new Date();

    this.saveProgress();
  }

  /**
   * Mark a concept as completed
   */
  completeConcept(conceptId: string): void {
    const progress = this.getConceptProgress(conceptId);

    progress.status = "completed";
    progress.completedAt = new Date();
    progress.percentComplete = 100;

    this.progress.concepts.set(conceptId, progress);
    this.progress.completedConcepts.add(conceptId);

    // Calculate overall progress
    this.updateOverallProgress();

    // Check for badge achievements
    this.checkBadges();

    this.progress.lastUpdated = new Date();
    this.saveProgress();
  }

  /**
   * Calculate next practice date (spaced repetition)
   */
  private calculateNextPracticeDate(progress: ConceptProgress): Date {
    const intervals = [1, 3, 7, 14, 30]; // days
    const reviewCount = Math.min(
      Math.floor(progress.correctAnswers / 5),
      intervals.length - 1
    );

    const daysToAdd = intervals[reviewCount] ?? 1;
    const nextDate = new Date();
    nextDate.setDate(nextDate.getDate() + daysToAdd);

    return nextDate;
  }

  /**
   * Update overall progress percentage
   */
  private updateOverallProgress(): void {
    const totalConcepts = TKA_CONCEPTS.length;
    const completedCount = this.progress.completedConcepts.size;
    this.progress.overallProgress = (completedCount / totalConcepts) * 100;
  }

  /**
   * Check and award badges
   */
  private checkBadges(): void {
    const badges = new Set(this.progress.badges);
    const completed = this.progress.completedConcepts.size;

    // Category completion badges
    if (this.isCategoryComplete("foundation")) {
      badges.add("foundation-master");
    }
    if (this.isCategoryComplete("letters")) {
      badges.add("letter-master");
    }
    if (this.isCategoryComplete("combinations")) {
      badges.add("combination-master");
    }
    if (this.isCategoryComplete("advanced")) {
      badges.add("advanced-master");
    }

    // Milestone badges
    if (completed >= 5) badges.add("first-five");
    if (completed >= 10) badges.add("halfway-there");
    if (completed >= 20) badges.add("almost-there");
    if (completed >= 28) badges.add("tka-master");

    // Streak badges
    const maxStreak = Math.max(
      ...Array.from(this.progress.concepts.values()).map((p) => p.bestStreak)
    );
    if (maxStreak >= 10) badges.add("streak-10");
    if (maxStreak >= 25) badges.add("streak-25");
    if (maxStreak >= 50) badges.add("streak-50");

    this.progress.badges = Array.from(badges);
  }

  /**
   * Check if all concepts in a category are completed
   */
  private isCategoryComplete(category: string): boolean {
    const categoryConcepts = TKA_CONCEPTS.filter(
      (c) => c.category === category
    );
    return categoryConcepts.every((c) =>
      this.progress.completedConcepts.has(c.id)
    );
  }

  /**
   * Get concepts that are ready for review (spaced repetition)
   */
  getConceptsDueForReview(): string[] {
    const now = new Date();
    const due: string[] = [];

    this.progress.concepts.forEach((progress, conceptId) => {
      if (
        progress.status === "completed" &&
        progress.nextPracticeAt &&
        new Date(progress.nextPracticeAt) <= now
      ) {
        due.push(conceptId);
      }
    });

    return due;
  }

  /**
   * Reset all progress (for testing/development)
   */
  resetProgress(): void {
    this.progress = {
      concepts: new Map(),
      completedConcepts: new Set(),
      overallProgress: 0,
      totalCorrect: 0,
      totalTimeSpent: 0,
      badges: [],
      lastUpdated: new Date(),
    };
    this.saveProgress();
  }

  /**
   * Export progress as JSON (for backup/transfer)
   */
  exportProgress(): string {
    return JSON.stringify(
      {
        concepts: Object.fromEntries(this.progress.concepts),
        completedConcepts: Array.from(this.progress.completedConcepts),
        currentConceptId: this.progress.currentConceptId,
        overallProgress: this.progress.overallProgress,
        totalCorrect: this.progress.totalCorrect,
        totalTimeSpent: this.progress.totalTimeSpent,
        badges: this.progress.badges,
        lastUpdated: this.progress.lastUpdated.toISOString(),
      },
      null,
      2
    );
  }

  /**
   * Import progress from JSON
   */
  importProgress(json: string): void {
    try {
      const data = JSON.parse(json) as Partial<LearningProgress> & {
        concepts?: Record<string, ConceptProgress>;
        completedConcepts?: string[];
        lastUpdated?: string | number;
      };
      this.progress = {
        concepts: new Map(Object.entries(data.concepts || {})),
        completedConcepts: new Set(data.completedConcepts || []),
        currentConceptId: data.currentConceptId,
        overallProgress: data.overallProgress || 0,
        totalCorrect: data.totalCorrect || 0,
        totalTimeSpent: data.totalTimeSpent || 0,
        badges: data.badges || [],
        lastUpdated: new Date(data.lastUpdated || Date.now()),
      };
      this.saveProgress();
    } catch (error) {
      console.error("Failed to import progress:", error);
      throw new Error("Invalid progress data");
    }
  }
}

// Singleton instance
export const conceptProgressService = new ConceptProgressService();

/**
 * Printable Page Layout Service Implementation
 *
 * Handles calculations for printable page layouts including paper sizes,
 * margins, grid configurations, and measurement conversions.
 *
 * Based on desktop application's printable_layout.py functionality.
 */

import type {
  DPIConfiguration,
  GridCalculationOptions,
  LayoutCalculationRequest,
  LayoutCalculationResult,
  LayoutValidationError,
  LayoutValidationResult,
  LayoutValidationWarning,
  PageDimensions,
  PageLayoutConfig,
  PageMargins,
  PageOrientation,
  Rectangle,
  WordCardGridConfig,
  WordCardPaperSize,
} from "$shared";
import { injectable } from "inversify";
import type { IPrintablePageLayoutService } from "../contracts";

@injectable()
export class PrintablePageLayoutService implements IPrintablePageLayoutService {
  private readonly paperSizes = {
    A4: { width: 595, height: 842 },
    A3: { width: 842, height: 1191 },
    Letter: { width: 612, height: 792 },
    Legal: { width: 612, height: 1008 },
    Tabloid: { width: 792, height: 1224 },
  };

  private readonly defaultMargins: PageMargins = {
    top: 36, // 0.5 inch
    right: 18, // 0.25 inch
    bottom: 36, // 0.5 inch
    left: 18, // 0.25 inch
  };

  private readonly dpiConfig: DPIConfiguration = {
    screenDPI: 96,
    printDPI: 300,
    scaleFactor: 1.0,
  };

  calculatePageDimensions(
    paperSize: WordCardPaperSize,
    orientation: PageOrientation
  ): PageDimensions {
    const dimensions = this.paperSizes[paperSize];

    if (orientation === "landscape") {
      return {
        width: dimensions.height,
        height: dimensions.width,
      };
    }

    return {
      width: dimensions.width,
      height: dimensions.height,
    };
  }

  calculateMargins(_paperSize: WordCardPaperSize): PageMargins {
    // For now, use default margins for all paper sizes
    // Could be extended to have paper-specific margins
    return { ...this.defaultMargins };
  }

  calculateContentArea(
    pageSize: PageDimensions,
    margins: PageMargins
  ): Rectangle {
    return {
      x: margins.left,
      y: margins.top,
      width: pageSize.width - margins.left - margins.right,
      height: pageSize.height - margins.top - margins.bottom,
    };
  }

  calculateOptimalGrid(
    cardAspectRatio: number,
    contentArea: Rectangle,
    options: Partial<GridCalculationOptions> = {}
  ): WordCardGridConfig {
    const opts: GridCalculationOptions = {
      minCardsPerPage: 2,
      maxCardsPerPage: 12,
      preferSquareLayout: false,
      prioritizeCardSize: true,
      allowPartialLastPage: true,
      ...options,
    };

    let bestGrid: WordCardGridConfig | null = null;
    let bestScore = 0;

    // Try different grid configurations
    for (
      let totalCards = opts.minCardsPerPage;
      totalCards <= opts.maxCardsPerPage;
      totalCards++
    ) {
      for (let rows = 1; rows <= Math.ceil(Math.sqrt(totalCards)); rows++) {
        const cols = Math.ceil(totalCards / rows);

        if (rows * cols < totalCards && !opts.allowPartialLastPage) {
          continue;
        }

        const cellWidth = contentArea.width / cols;
        const cellHeight = contentArea.height / rows;

        // Calculate card dimensions maintaining aspect ratio
        let cardWidth = cellWidth * 0.9; // Leave some spacing
        let cardHeight = cardWidth / cardAspectRatio;

        if (cardHeight > cellHeight * 0.9) {
          cardHeight = cellHeight * 0.9;
          cardWidth = cardHeight * cardAspectRatio;
        }

        // Calculate score based on card size and layout efficiency
        const cardArea = cardWidth * cardHeight;
        const totalUsedArea = totalCards * cardArea;
        const totalAvailableArea = contentArea.width * contentArea.height;
        const utilization = totalUsedArea / totalAvailableArea;

        // Prefer layouts that maximize card size if prioritizeCardSize is true
        const cardSizeScore = opts.prioritizeCardSize
          ? cardArea / (contentArea.width * contentArea.height)
          : 0.5;
        const utilizationScore = Math.min(utilization, 1.0);
        const layoutScore = opts.preferSquareLayout
          ? 1 - Math.abs(rows - cols) / Math.max(rows, cols)
          : 0.5;

        const score =
          cardSizeScore * 0.4 + utilizationScore * 0.4 + layoutScore * 0.2;

        if (score > bestScore) {
          bestScore = score;
          bestGrid = {
            rows,
            columns: cols,
            spacing: Math.min(
              (cellWidth - cardWidth) / 2,
              (cellHeight - cardHeight) / 2
            ),
            cardWidth,
            cardHeight,
          };
        }
      }
    }

    // Fallback to simple 2x2 if no optimal grid found
    if (!bestGrid) {
      const cellWidth = contentArea.width / 2;
      const cellHeight = contentArea.height / 2;
      const cardWidth = cellWidth * 0.9;
      const cardHeight = cardWidth / cardAspectRatio;

      bestGrid = {
        rows: 2,
        columns: 2,
        spacing: Math.min(
          (cellWidth - cardWidth) / 2,
          (cellHeight - cardHeight) / 2
        ),
        cardWidth,
        cardHeight,
      };
    }

    return bestGrid;
  }

  getPageSizeInPixels(
    paperSize: WordCardPaperSize,
    orientation: PageOrientation,
    dpi: number = this.dpiConfig.screenDPI
  ): PageDimensions {
    const pointDimensions = this.calculatePageDimensions(
      paperSize,
      orientation
    );
    const scaleFactor = dpi / 72; // Convert from points to pixels

    return {
      width: Math.round(pointDimensions.width * scaleFactor),
      height: Math.round(pointDimensions.height * scaleFactor),
    };
  }

  calculateLayout(request: LayoutCalculationRequest): LayoutCalculationResult {
    const pageDimensions = this.calculatePageDimensions(
      request.paperSize,
      request.orientation
    );
    const margins = request.margins ?? this.calculateMargins(request.paperSize);
    const contentArea = this.calculateContentArea(pageDimensions, margins);

    const gridOptions: GridCalculationOptions = {
      minCardsPerPage: 2,
      maxCardsPerPage: request.preferredCardsPerPage ?? 12,
      preferSquareLayout: false,
      prioritizeCardSize: true,
      allowPartialLastPage: true,
    };

    const gridConfig = this.calculateOptimalGrid(
      request.cardAspectRatio || 0.7,
      contentArea,
      gridOptions
    );

    const cardsPerPage = gridConfig.rows * gridConfig.columns;

    // Calculate utilization
    const cardArea = gridConfig.cardWidth * gridConfig.cardHeight;
    const totalCardArea = cardsPerPage * cardArea;
    const contentAreaSize = contentArea.width * contentArea.height;
    const utilization = Math.min(totalCardArea / contentAreaSize, 1.0);

    // Consider layout optimal if utilization > 0.6 and card size is reasonable
    const minCardSize = Math.min(contentArea.width, contentArea.height) * 0.2;
    const isOptimal =
      utilization > 0.6 &&
      Math.min(gridConfig.cardWidth, gridConfig.cardHeight) > minCardSize;

    return {
      isOptimal,
      gridConfig: {
        rows: gridConfig.rows,
        columns: gridConfig.columns,
        cardWidth: gridConfig.cardWidth,
        cardHeight: gridConfig.cardHeight,
        spacing: gridConfig.spacing,
      },
      pageConfig: {
        printConfig: {
          paperSize: request.paperSize,
          orientation: request.orientation,
          margins,
          dpi: this.dpiConfig.printDPI,
          enablePageNumbers: false,
          enableHeader: false,
          enableFooter: false,
        },
        gridOptions: {
          minCardsPerPage: 1,
          maxCardsPerPage: 12,
          preferSquareLayout: false,
          prioritizeCardSize: true,
          allowPartialLastPage: true,
        },
        sequencesPerPage: gridConfig.rows * gridConfig.columns,
        enableOptimization: true,
      },
      utilization,
    };
  }

  validateLayout(config: PageLayoutConfig): LayoutValidationResult {
    const errors: LayoutValidationError[] = [];
    const warnings: LayoutValidationWarning[] = [];

    // Validate margins
    const margins = config.printConfig.margins;
    if (
      margins.top < 0 ||
      margins.right < 0 ||
      margins.bottom < 0 ||
      margins.left < 0
    ) {
      errors.push({
        code: "NEGATIVE_MARGINS",
        message: "Margins cannot be negative",
        field: "margins",
        severity: "error" as const,
      });
    }

    // Validate sequences per page
    if (config.sequencesPerPage < 1) {
      errors.push({
        code: "INVALID_SEQUENCES_PER_PAGE",
        message: "Sequences per page must be at least 1",
        field: "sequencesPerPage",
        severity: "error" as const,
      });
    }

    if (config.sequencesPerPage > 20) {
      warnings.push({
        code: "HIGH_SEQUENCES_PER_PAGE",
        message:
          "High number of sequences per page may result in very small cards",
        severity: "warning",
        suggestion:
          "Consider reducing sequences per page for better readability",
      });
    }

    // Check if margins are too large
    const pageDimensions = this.calculatePageDimensions(
      config.printConfig.paperSize,
      config.printConfig.orientation
    );
    const totalMarginWidth = margins.left + margins.right;
    const totalMarginHeight = margins.top + margins.bottom;

    if (totalMarginWidth > pageDimensions.width * 0.5) {
      warnings.push({
        code: "LARGE_HORIZONTAL_MARGINS",
        message: "Horizontal margins are very large",
        severity: "warning",
        suggestion: "Consider reducing left and right margins",
      });
    }

    if (totalMarginHeight > pageDimensions.height * 0.5) {
      warnings.push({
        code: "LARGE_VERTICAL_MARGINS",
        message: "Vertical margins are very large",
        severity: "warning",
        suggestion: "Consider reducing top and bottom margins",
      });
    }

    return {
      isValid: errors.length === 0,
      errors: errors.map((e) => e.message),
      warnings: warnings.map((w) => w.message),
    };
  }

  getDPIConfiguration(): DPIConfiguration {
    return { ...this.dpiConfig };
  }

  convertMeasurement(value: number, fromUnit: string, toUnit: string): number {
    const units = {
      points: 1,
      inches: 72,
      millimeters: 72 / 25.4,
      centimeters: 72 / 2.54,
    } as const;

    const fromPoints = units[fromUnit as keyof typeof units] as number | undefined;
    const toPoints = units[toUnit as keyof typeof units] as number | undefined;

    if (fromPoints === undefined || toPoints === undefined) {
      throw new Error(
        `Invalid measurement unit. Supported: ${Object.keys(units).join(", ")}`
      );
    }

    // Convert to points, then to target unit
    const valueInPoints = value * fromPoints;
    return valueInPoints / toPoints;
  }
}

/**
 * Word Card Export Integration Service Implementation
 *
 * Bridges the gap between UI components and export services.
 * Handles DOM element selection, export orchestration, and file downloads.
 */

import type { IFileDownloadService } from "$shared";
import type { DownloadResult, ExportResult, SequenceData } from "$shared";
import { inject, injectable } from "inversify";
import { TYPES } from "$shared/inversify/types";
import type {
  BatchExportResult,
  WordCardExportOptions,
} from "../../domain/models/word-card-export";

// Local type definitions

// File download functionality provided by FileDownloadService

function generateTimestampedFilename(
  prefix: string,
  extension: string,
  includeTime: boolean = true
): string {
  const now = new Date();
  const date = now.toISOString().split("T")[0];
  const time = includeTime
    ? (now.toTimeString().split(" ")[0] ?? "").replace(/:/g, "-")
    : "";
  return `${prefix}_${date}${time ? "_" + time : ""}.${extension}`;
}

function sanitizeFilename(filename: string): string {
  return filename.replace(/[^a-z0-9]/gi, "_").toLowerCase();
}

function supportsFileDownload(): boolean {
  return typeof window !== "undefined" && "document" in window;
}
// import type {
//   IPageImageExportService,
//   IWordCardExportIntegrationService,
// } from "../contracts";

interface IPageImageExportService {
  exportPagesAsImages?(
    pageElements: HTMLElement[],
    options: WordCardExportOptions
  ): Promise<BatchExportResult>;
  cancelExport?(): Promise<void>;
}

interface IWordCardExportIntegrationService {
  exportWordCards(
    sequences: SequenceData[],
    options: WordCardExportOptions
  ): Promise<ExportResult[]>;
  cancelExport(): Promise<void>;
}

@injectable()
export class WordCardExportIntegrationService
  implements IWordCardExportIntegrationService
{
  private isExporting = false;
  private abortController: AbortController | null = null;

  constructor(
    @inject(TYPES.IFileDownloadService)
    private fileDownloadService: IFileDownloadService,
    private readonly pageImageExportService: IPageImageExportService
  ) {}

  async exportWordCards(
    _sequences: SequenceData[],
    options: WordCardExportOptions
  ): Promise<ExportResult[]> {
    // Filter options to only include supported formats
    const filteredOptions = {
      format:
        options.format === "PDF"
          ? "PNG"
          : (options.format as "PNG" | "JPEG" | "WebP"),
      quality: options.quality,
      scale: options.scale,
      ...(options.filename && { filenamePrefix: options.filename }),
    };

    // Delegate to the main export method
    const result = await this.exportPrintablePagesAsImages(filteredOptions);

    // Convert result to ExportResult format
    const exportResult: ExportResult = {
      success: result.successCount > 0,
      filename: "word-cards-export",
      ...(result.errors.length > 0 && {
        error: result.errors[0]?.message || "Unknown error",
      }),
      metadata: {
        format: "PNG", // Default format for word cards
        size: 0, // Will be set when actual export happens
        dimensions: { width: 800, height: 600 }, // Default dimensions
        beatCount: 0, // Word cards don't have beats
        processingTime: 0, // Will be measured during export
        successCount: result.successCount,
        failureCount: result.failureCount,
        totalErrors: result.errors.length,
      },
    };

    return [exportResult]; // Return as array to match interface
  }

  async exportPrintablePagesAsImages(
    options: {
      format?: "PNG" | "JPEG" | "WebP";
      quality?: number;
      scale?: number;
      filenamePrefix?: string;
    } = {},
    onProgress?: (current: number, total: number, message: string) => void
  ): Promise<{ successCount: number; failureCount: number; errors: Error[] }> {
    console.log("🖼️ Starting export of all printable pages as images");

    // Validate export capability
    const validation = this.validateExportCapability();
    if (!validation.canExport) {
      const error = new Error(
        `Export not possible: ${validation.issues.join(", ")}`
      );
      console.error("❌ Export validation failed:", error);
      return { successCount: 0, failureCount: 1, errors: [error] };
    }

    // Get page elements
    const pageElements = this.getPrintablePageElements();
    if (pageElements.length === 0) {
      const error = new Error("No printable page elements found in DOM");
      console.error("❌ No page elements found:", error);
      return { successCount: 0, failureCount: 1, errors: [error] };
    }

    console.log(`📄 Found ${pageElements.length} page elements to export`);

    try {
      this.isExporting = true;
      this.abortController = new AbortController();

      // Prepare export options
      const exportOptions = this.prepareImageExportOptions(options);
      console.log("⚙️ Export options prepared:", exportOptions);

      // Progress tracking
      const totalCount = pageElements.length;

      // Export pages to blobs
      onProgress?.(0, totalCount, "Starting page export...");

      if (!this.pageImageExportService.exportPagesAsImages) {
        throw new Error("Export service not available");
      }

      const batchResult = await this.pageImageExportService.exportPagesAsImages(
        pageElements,
        exportOptions
      );

      console.log("📊 Batch export result:", batchResult);

      // Prepare download data
      const downloadData: Array<{ blob: Blob; filename: string }> = [];
      const errors: Error[] = (batchResult.errors ?? []).map((err: unknown) =>
        err instanceof Error ? err : new Error(String(err))
      );

      for (let i = 0; i < batchResult.results.length; i++) {
        const result = batchResult.results[i];
        if (!result) continue;

        if (result.success && result.blob) {
          const pageNumber = i + 1;
          const filename = this.generatePageFilename(
            options.filenamePrefix ?? "word-cards",
            pageNumber,
            exportOptions.format,
            result.metrics?.resolution
          );

          downloadData.push({
            blob: result.blob,
            filename,
          });
        } else if (result.error) {
          errors.push(
            result.error instanceof Error
              ? result.error
              : new Error(String(result.error))
          );
        }
      }

      console.log(`💾 Prepared ${downloadData.length} files for download`);

      // Download files
      if (downloadData.length > 0) {
        onProgress?.(
          totalCount,
          totalCount,
          `Downloading ${downloadData.length} files...`
        );

        const downloadResults =
          await this.fileDownloadService.downloadBlobBatch(downloadData, {
            delay: 200, // 200ms delay between downloads
          });

        // Check download results
        const failedDownloads = downloadResults.filter(
          (r: DownloadResult) => !r.success
        );
        if (failedDownloads.length > 0) {
          console.warn("⚠️ Some downloads failed:", failedDownloads);
          failedDownloads.forEach((result: DownloadResult) => {
            if (result.error) {
              errors.push(result.error);
            }
          });
        }

        console.log(
          `✅ Successfully downloaded ${downloadData.length - failedDownloads.length} files`
        );
      }

      onProgress?.(
        totalCount,
        totalCount,
        `Export completed! ${batchResult.successCount} pages exported successfully.`
      );

      return {
        successCount: batchResult.successCount,
        failureCount: batchResult.failureCount,
        errors,
      };
    } catch (error) {
      console.error("❌ Export failed with error:", error);
      return {
        successCount: 0,
        failureCount: pageElements.length,
        errors: [error as Error],
      };
    } finally {
      this.isExporting = false;
      this.abortController = null;
    }
  }

  async exportSelectedPages(
    pageIndices: number[],
    options: {
      format?: "PNG" | "JPEG" | "WebP";
      quality?: number;
      scale?: number;
      filenamePrefix?: string;
    } = {},
    onProgress?: (current: number, total: number, message: string) => void
  ): Promise<{ successCount: number; failureCount: number; errors: Error[] }> {
    console.log("🎯 Starting export of selected pages:", pageIndices);

    // Get all page elements
    const allPageElements = this.getPrintablePageElements();

    // Filter to selected pages
    const selectedElements = pageIndices
      .filter((index) => index >= 0 && index < allPageElements.length)
      .map((index) => allPageElements[index]);

    if (selectedElements.length === 0) {
      const error = new Error(
        "No valid page elements found for selected indices"
      );
      console.error("❌ No valid selected elements:", error);
      return { successCount: 0, failureCount: 1, errors: [error] };
    }

    // For now, delegate to the full export method but with filtered elements
    // TODO: Could optimize this by modifying the export service to accept specific elements
    console.log(`📄 Exporting ${selectedElements.length} selected pages`);

    // This is a simplified implementation - in reality, we'd need to modify the export flow
    // For now, we'll export all and notify about the selected ones
    onProgress?.(
      0,
      selectedElements.length,
      "Note: Currently exports all pages (selection feature in development)"
    );

    return this.exportPrintablePagesAsImages(options, onProgress);
  }

  getPrintablePageElements(): HTMLElement[] {
    console.log("🔍 Searching for printable page elements in DOM");

    // Look for page elements with the expected data attribute or class
    const selectors = [
      "[data-page-id]",
      ".printable-page",
      ".page-wrapper .printable-page",
      ".pages-display .printable-page",
    ];

    let elements: HTMLElement[] = [];

    for (const selector of selectors) {
      const found = document.querySelectorAll(selector);
      if (found.length > 0) {
        elements = Array.from(found) as HTMLElement[];
        console.log(
          `✅ Found ${elements.length} page elements using selector: ${selector}`
        );
        break;
      }
    }

    if (elements.length === 0) {
      console.warn("⚠️ No printable page elements found with any selector");
      console.log("🔍 Available elements in DOM:");
      console.log(
        "- Elements with data-page-id:",
        document.querySelectorAll("[data-page-id]").length
      );
      console.log(
        "- Elements with class printable-page:",
        document.querySelectorAll(".printable-page").length
      );
    }

    return elements;
  }

  validateExportCapability(): {
    canExport: boolean;
    pageCount: number;
    issues: string[];
  } {
    const issues: string[] = [];

    // Check browser support
    if (!supportsFileDownload()) {
      issues.push("Browser does not support file downloads");
    }

    // Check for DOM elements
    const pageElements = this.getPrintablePageElements();
    const pageCount = pageElements.length;

    if (pageCount === 0) {
      issues.push("No printable page elements found in DOM");
    }

    // Check if already exporting
    if (this.isExporting) {
      issues.push("Export already in progress");
    }

    // Check for required services
    if (
      !this.pageImageExportService ||
      typeof this.pageImageExportService !== "object"
    ) {
      issues.push("Page image export service not available");
    }

    const canExport = issues.length === 0;

    console.log("🔍 Export capability validation:", {
      canExport,
      pageCount,
      issues,
    });

    return {
      canExport,
      pageCount,
      issues,
    };
  }

  cancelExport(): Promise<void> {
    if (this.isExporting && this.abortController) {
      console.log("🛑 Cancelling export operation");
      this.abortController.abort();
      void this.pageImageExportService.cancelExport?.();
      this.isExporting = false;
      this.abortController = null;
    }
    return Promise.resolve();
  }

  getDefaultExportOptions(): {
    format: "PNG" | "JPEG" | "WebP";
    quality: number;
    scale: number;
    filenamePrefix: string;
  } {
    return {
      format: "PNG",
      quality: 0.95,
      scale: 2.0, // 2x for high quality
      filenamePrefix: "word-cards",
    };
  }

  // Private helper methods

  private prepareImageExportOptions(options: {
    format?: "PNG" | "JPEG" | "WebP";
    quality?: number;
    scale?: number;
    filenamePrefix?: string;
  }): WordCardExportOptions {
    const defaults = this.getDefaultExportOptions();

    return {
      ...defaults,
      ...options,
      // Override with specific values
      format: options.format ?? defaults.format,
      quality: options.quality ?? defaults.quality,
      scale: options.scale ?? defaults.scale,
      includeMetadata: true, // Add required property
    };
  }

  private generatePageFilename(
    prefix: string,
    pageNumber: number,
    format: string,
    dimensions?: { width: number; height: number }
  ): string {
    const sanitizedPrefix = sanitizeFilename(prefix);
    const pageNumStr = pageNumber.toString().padStart(3, "0");
    const extension = format.toLowerCase();

    // Include dimensions in filename for reference
    const dimensionStr = dimensions
      ? `_${dimensions.width}x${dimensions.height}`
      : "";

    return generateTimestampedFilename(
      `${sanitizedPrefix}_page_${pageNumStr}${dimensionStr}`,
      extension,
      false // Don't include time, just date
    );
  }
}

