  setSelectedLength,
  setShowBeatNumbers,
  setTransparentBackground,
  startExport,
  startRegeneration,
  updateContainerSize,
  updateExportProgress,
  updateExportSetting,
} from "./display-state.svelte";
import createPageLayoutState from "./page-layout-state.svelte";
// createPageLayoutState imported above at line 14

// ============================================================================
// SEQUENCE CARD STATE FACTORY
// ============================================================================

export function createWordCardState(
  _browseService: IWordCardBrowseService,
  layoutService: IPrintablePageLayoutService,
  pageFactoryService: IPageFactoryService
) {
  // Create page layout state for printable mode
  const pageLayoutState = createPageLayoutState(
    layoutService,
    pageFactoryService,
    []
  );

  // Reactive sequence data (synchronous state, async loading)
  const allSequences = $state<SequenceData[]>([]);
  const isLoadingSequences = $state(false);
  const sequenceLoadError = $state<string | null>(null);

  // Filtered sequences using EXISTING ExploreService
  const filteredSequences = $derived.by(() => {
    if (displayState.selectedLength === 0) {
      return allSequences;
    }

    // Simple client-side filtering for now
    return allSequences.filter(
      (seq) => seq.beats.length === displayState.selectedLength
    );
  });

  // Current page sequences for grid/list view
  const currentPageSequences = $derived(() => {
    const sequences = filteredSequences;
    const startIndex = pageLayoutState.currentPage * 24; // items per page
    return sequences.slice(startIndex, startIndex + 24);
  });

  // Total pages for pagination
  const totalPages = $derived(() => {
    return Math.ceil(filteredSequences.length / 24);
  });

  // Progress message
  const statusMessage = $derived(() => {
    if (progressState.isExporting || progressState.isRegenerating) {
      return progressState.message;
    }

    if (isLoadingSequences === true) {
      return "Loading sequences...";
    }

    if (sequenceLoadError) {
      return `Error: ${sequenceLoadError}`;
    }

    const count = filteredSequences.length;

    if (count === 0) {
      return displayState.selectedLength === 0
        ? "No sequences available"
        : `No ${displayState.selectedLength}-beat sequences found`;
    }

    const lengthText =
      displayState.selectedLength === 0
        ? "all"
        : `${displayState.selectedLength}-beat`;
    return `${count} ${lengthText} sequence${count === 1 ? "" : "s"}`;
  });

  // Actions that connect to page layout
  async function switchToPrintableMode() {
    await pageLayoutState.createPages(filteredSequences);
  }

  async function refreshPages() {
    await pageLayoutState.regeneratePages();
  }

  return {
    // Display state
    displayState,
    exportSettings,
    progressState,
    layoutState,
    pageLayoutState,

    // Derived data (using existing services)
    get filteredSequences() {
      return filteredSequences;
    },
    get currentPageSequences() {
      return currentPageSequences;
    },
    get totalPages() {
      return totalPages;
    },
    get statusMessage() {
      return statusMessage;
    },

    // Actions
    setSelectedLength,
    setColumnCount,
    setShowBeatNumbers,
    setTransparentBackground,
    updateContainerSize,
    setDeviceCapabilities,
    startExport,
    updateExportProgress,
    finishExport,
    startRegeneration,
    finishRegeneration,
    updateExportSetting,
    resetExportSettings,
    switchToPrintableMode,
    refreshPages,
  };
}

export type WordCardState = ReturnType<typeof createWordCardState>;

/**
 * ActService - Implementation for Write tab Act operations
 */
import { injectable } from "inversify";
import type { ActSummary, IActService } from "../contracts/IActService";

@injectable()
export class ActService implements IActService {
  private acts: Map<string, ActSummary> = new Map();
  private initialized = false;

  constructor() {
    console.log("🎭 ActService initialized");
  }

  initialize(): void {
    if (this.initialized) return;

    console.log("🎭 ActService: Initializing...");

    // TODO: Load acts from persistent storage
    // For now, create some sample acts
    const sampleActs: ActSummary[] = [
      {
        id: "act-1",
        name: "Sample Act 1",
        description: "A sample act for testing",
        sequences: [],
      },
      {
        id: "act-2",
        name: "Sample Act 2",
        description: "Another sample act",
        sequences: [],
      },
    ];

    for (const act of sampleActs) {
      this.acts.set(act.id, act);
    }

    this.initialized = true;
    console.log("✅ ActService initialized with", this.acts.size, "acts");
  }

  cleanup(): void {
    console.log("🎭 ActService: Cleaning up...");
    this.acts.clear();
    this.initialized = false;
  }

  async loadAct(idOrPath: string): Promise<ActSummary | null> {
    await this.ensureInitialized();

    const act = this.acts.get(idOrPath);
    if (act) {
      console.log("🎭 ActService: Loaded act", act.name);
      return { ...act }; // Return a copy
    }

    console.warn("🎭 ActService: Act not found:", idOrPath);
    return null;
  }

  async getAllActs(): Promise<ActSummary[]> {
    await this.ensureInitialized();

    const acts = Array.from(this.acts.values()).map((act) => ({ ...act }));
    console.log("🎭 ActService: Retrieved", acts.length, "acts");
    return acts;
  }

  async addSequenceToAct(actId: string, sequenceId: string) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    if (!act.sequences) {
      act.sequences = [];
    }

    if (!act.sequences.includes(sequenceId)) {
      act.sequences.push(sequenceId);
      console.log("🎭 ActService: Added sequence", sequenceId, "to act", actId);
    }
  }

  async removeSequenceFromAct(
    actId: string,
    sequenceId: string
  ) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    if (act.sequences) {
      const index = act.sequences.indexOf(sequenceId);
      if (index > -1) {
        act.sequences.splice(index, 1);
        console.log(
          "🎭 ActService: Removed sequence",
          sequenceId,
          "from act",
          actId
        );
      }
    }
  }

  async exportAct(actId: string) {
    await this.ensureInitialized();

    const act = this.acts.get(actId);
    if (!act) {
      throw new Error(`Act not found: ${actId}`);
    }

    // TODO: Implement export functionality
    console.log("🎭 ActService: Exporting act", act.name);
    console.warn("🚧 ActService: Export functionality not yet implemented");
  }

  private async ensureInitialized(): Promise<void> {
    if (!this.initialized) {
      await this.initialize();
    }
  }
}

/**
 * MusicPlayerService - Implementation for music playback in Write tab
 */
import { injectable } from "inversify";
import type { IMusicPlayerService } from "../contracts/IMusicPlayerService";

@injectable()
export class MusicPlayerService implements IMusicPlayerService {
  private audioContext: AudioContext | null = null;
  private currentAudio: HTMLAudioElement | null = null;
  private initialized = false;

  constructor() {
    console.log("🎵 MusicPlayerService initialized");
  }

  async initialize(): Promise<void> {
    if (this.initialized) return;

    console.log("🎵 MusicPlayerService: Initializing audio context...");

    try {
      // Initialize Web Audio API context
      const AudioContextClass =
        window.AudioContext ??
        (window as unknown as { webkitAudioContext: typeof AudioContext })
          .webkitAudioContext;
      this.audioContext = new AudioContextClass();

      // Resume audio context if suspended (required for user interaction)
      if (this.audioContext.state === "suspended") {
        await this.audioContext.resume();
      }

      this.initialized = true;
      console.log("✅ MusicPlayerService initialized with audio context");
    } catch (error) {
      console.error(
        "❌ MusicPlayerService: Failed to initialize audio context:",
        error
      );
      // Fallback to basic HTML audio without Web Audio API
      this.initialized = true;
      console.log("⚠️ MusicPlayerService: Fallback to basic HTML audio");
    }
  }

  cleanup(): void {
    console.log("🎵 MusicPlayerService: Cleaning up...");

    if (this.currentAudio) {
      this.currentAudio.pause();
      this.currentAudio.removeEventListener(
        "loadedmetadata",
        this.handleLoadedMetadata
      );
      this.currentAudio.removeEventListener(
        "timeupdate",
        this.handleTimeUpdate
      );
      this.currentAudio.removeEventListener("ended", this.handleEnded);
      this.currentAudio.removeEventListener("error", this.handleError);
      this.currentAudio = null;
    }

    if (this.audioContext?.state !== "closed") {
      void this.audioContext?.close();
      this.audioContext = null;
    }

    this.initialized = false;
  }

  async play(track: string): Promise<void> {
    await this.ensureInitialized();

    console.log("🎵 MusicPlayerService: Playing track:", track);

    try {
      // Stop current audio if playing
      if (this.currentAudio) {
        this.currentAudio.pause();
      }

      // Create new audio element
      this.currentAudio = new Audio(track);
      this.setupAudioEventListeners();

      // Start playback
      await this.currentAudio.play();
      console.log("✅ MusicPlayerService: Track started playing");
    } catch (error) {
      console.error("❌ MusicPlayerService: Failed to play track:", error);
      throw new Error(`Failed to play track: ${track}`);
    }
  }

  pause(): void {
    if (!this.currentAudio) {
      console.warn("🎵 MusicPlayerService: No audio to pause");
      return;
    }

    this.currentAudio.pause();
    console.log("⏸️ MusicPlayerService: Playback paused");
  }

  stop(): void {
    if (!this.currentAudio) {
      console.warn("🎵 MusicPlayerService: No audio to stop");
      return;
    }

    this.currentAudio.pause();
    this.currentAudio.currentTime = 0;
    console.log("⏹️ MusicPlayerService: Playback stopped");
  }

  private async ensureInitialized(): Promise<void> {
    if (!this.initialized) {
      await this.initialize();
    }
  }

  private setupAudioEventListeners(): void {
    if (!this.currentAudio) return;

    this.currentAudio.addEventListener(
      "loadedmetadata",
      this.handleLoadedMetadata
    );
    this.currentAudio.addEventListener("timeupdate", this.handleTimeUpdate);
    this.currentAudio.addEventListener("ended", this.handleEnded);
    this.currentAudio.addEventListener("error", this.handleError);
  }

  private handleLoadedMetadata = (): void => {
    if (this.currentAudio) {
      console.log(
        "🎵 MusicPlayerService: Audio metadata loaded, duration:",
        this.currentAudio.duration
      );
    }
  };

  private handleTimeUpdate = (): void => {
    if (this.currentAudio) {
      // Could emit events here for UI updates
      // console.log("🎵 Time update:", this.currentAudio.currentTime);
    }
  };

  private handleEnded = (): void => {
    console.log("🎵 MusicPlayerService: Track playback ended");
  };

  private handleError = (event: Event): void => {
    console.error("❌ MusicPlayerService: Audio error:", event);
  };
}

/**
 * Data Transformation Service Implementation
 *
 * Handles all data transformation and derivation logic for UI components.
 * This includes merging different data sources, filtering, and computing display values.
 */

import type { MotionColor, MotionData, PictographData } from "$shared";
import { injectable } from "inversify";
import type {
  IDataTransformationService,
  MotionRenderData,
  PictographDisplayData,
} from "../contracts/IDataTransformationService";

@injectable()
export class DataTransformationService implements IDataTransformationService {
  /**
   * Transform pictograph data into display-ready format
   */
  transformPictographData(
    pictographData?: PictographData | null
  ): PictographDisplayData {
    const effectivePictographData =
      this.getEffectivePictographData(pictographData);

    return {
      effectivePictographData,
      hasValidData: this.hasValidPictographData(effectivePictographData),
      displayLetter: this.getDisplayLetter(effectivePictographData),
      motionsToRender: this.getMotionsToRender(effectivePictographData),
    };
  }

  /**
   * Get the effective pictograph data from multiple sources
   */
  getEffectivePictographData(
    pictographData?: PictographData | null
  ): PictographData | null {
    return pictographData ?? null;
  }

  /**
   * Check if pictograph data is valid for rendering
   */
  hasValidPictographData(data: PictographData | null): boolean {
    return data != null;
  }

  /**
   * Extract display letter from pictograph data
   */
  getDisplayLetter(data: PictographData | null): string | null {
    if (data?.letter) return data.letter;
    return null;
  }

  /**
   * Get motions that should be rendered (visible motions only)
   */
  getMotionsToRender(data: PictographData | null): MotionRenderData[] {
    if (!data?.motions) return [];
    // Convert undefined to null for type compatibility
    const normalizedMotions: Partial<Record<MotionColor, MotionData | null>> =
      {};
    for (const [key, value] of Object.entries(data.motions)) {
      normalizedMotions[key as MotionColor] = value ?? null;
    }
    return this.filterVisibleMotions(normalizedMotions);
  }

  /**
   * Filter motion data by visibility
   */
  filterVisibleMotions(
    motions: Partial<Record<MotionColor, MotionData | null>> | undefined
  ): MotionRenderData[] {
    if (!motions) return [];

    return Object.entries(motions)
      .filter(
        ([, motionData]): motionData is MotionData => motionData !== null && motionData !== undefined
      )
      .filter(([_, motionData]) => motionData.isVisible)
      .map(([color, motionData]) => ({
        color: color as MotionColor,
        motionData: motionData!,
      }));
  }
}

/**
 * Haptic Feedback Service Implementation
 *
 * Provides haptic feedback capabilities for mobile devices with proper
 * accessibility considerations, browser compatibility, and performance optimization.
 */

import { browser } from "$app/environment";
import type {
  HapticFeedbackConfig,
  HapticFeedbackType,
  IHapticFeedbackService,
} from "$shared";
import { injectable } from "inversify";

// Feedback patterns (in milliseconds)
const FEEDBACK_PATTERNS: Record<
  Exclude<HapticFeedbackType, "custom">,
  number[]
> = {
  selection: [70], // Standard haptic for all interactive elements
  success: [100, 30, 50], // Triple pulse for successful actions
  warning: [60, 0, 60], // Double tap for warnings
  error: [100, 0, 100, 0, 100], // Triple tap for errors
};

// Default configuration
const DEFAULT_CONFIG: HapticFeedbackConfig = {
  enabled: true,
  respectReducedMotion: true,
  throttleTime: 100,
  customPatterns: {},
};

@injectable()
export class HapticFeedbackService implements IHapticFeedbackService {
  private lastFeedbackTime: number = 0;
  private config: HapticFeedbackConfig = { ...DEFAULT_CONFIG };
  private deviceSupportsVibration: boolean = false;

  constructor() {
    this.initializeService();
  }

  // ============================================================================
  // PUBLIC API
  // ============================================================================

  public trigger(type: HapticFeedbackType = "selection"): boolean {
    if (!this.canTriggerFeedback()) {
      return false;
    }

    // Get the pattern based on the type
    const pattern =
      type === "custom"
        ? [] // Custom patterns are handled via triggerCustom
        : FEEDBACK_PATTERNS[type];

    return this.vibrate(pattern);
  }

  public setCustomPattern(name: string, pattern: number[]): void {
    this.config.customPatterns[name] = [...pattern];
  }

  public triggerCustom(name: string): boolean {
    if (!this.canTriggerFeedback()) {
      return false;
    }

    const pattern = this.config.customPatterns[name];
    if (!pattern) {
      console.warn(`Custom haptic pattern '${name}' not found`);
      return false;
    }

    return this.vibrate(pattern);
  }

  public isSupported(): boolean {
    return this.deviceSupportsVibration;
  }

  public setEnabled(enabled: boolean): void {
    this.config.enabled = enabled;
  }

  public isEnabled(): boolean {
    return this.config.enabled;
  }

  public getConfig(): HapticFeedbackConfig {
    return { ...this.config };
  }

  public updateConfig(config: Partial<HapticFeedbackConfig>): void {
    this.config = { ...this.config, ...config };
  }

  // ============================================================================
  // PRIVATE METHODS
  // ============================================================================

  private initializeService(): void {
    this.detectBrowserSupport();
    this.setupReducedMotionListener();
  }

  /**
   * Detect if the device supports haptic feedback
   */
  private detectBrowserSupport(): void {
    if (!browser) {
      this.deviceSupportsVibration = false;
      return;
    }

    try {
      this.deviceSupportsVibration =
        "vibrate" in navigator ||
        "mozVibrate" in navigator ||
        "webkitVibrate" in navigator;
    } catch (error) {
      console.warn("Error detecting haptic feedback support:", error);
      this.deviceSupportsVibration = false;
    }
  }

  /**
   * Setup listener for reduced motion preference changes
   */
  private setupReducedMotionListener(): void {
    if (!browser || !window.matchMedia) {
      return;
    }

    try {
      const mediaQuery = window.matchMedia("(prefers-reduced-motion: reduce)");

      // Check initial state
      this.handleReducedMotionChange(mediaQuery);

      // Listen for changes
      const handler = (event: MediaQueryListEvent) => {
        this.handleReducedMotionChange(event);
      };
      mediaQuery.addEventListener("change", handler);
    } catch (error) {
      console.warn("Could not set up reduced motion listener:", error);
    }
  }

  /**
   * Handle changes to reduced motion preference
   */
  private handleReducedMotionChange(
    mediaQuery: MediaQueryList | MediaQueryListEvent
  ): void {
    if (!this.config.respectReducedMotion) {
      return;
    }

    // Disable haptic feedback if user prefers reduced motion
    if (mediaQuery.matches) {
      this.config.enabled = false;
    }
  }

  /**
   * Check if feedback can be triggered (all conditions met)
   */
  private canTriggerFeedback(): boolean {
    // Skip if not in browser environment
    if (!browser) {
      return false;
    }

    // Skip if not supported
    if (!this.deviceSupportsVibration) {
      return false;
    }

    // Skip if disabled
    if (!this.config.enabled) {
      return false;
    }

    // Check throttling
    const now = Date.now();
    if (now - this.lastFeedbackTime < this.config.throttleTime) {
      return false;
    }

    return true;
  }

  /**
   * Execute the vibration with error handling
   */
  private vibrate(pattern: number[]): boolean {
    try {
      // Update throttle time
      this.lastFeedbackTime = Date.now();

      // Trigger vibration
      if ('vibrate' in navigator) {
        navigator.vibrate(pattern);
      }

      return true;
    } catch (error) {
      console.warn("Haptic feedback failed:", error);
      return false;
    }
  }
}

import { injectable } from "inversify";
import type { IResourceTracker } from "../contracts/IResourceTracker";

interface Disposable {
  dispose?(): void;
  close?(): void;
  cleanup?(): void;
}

type TrackedResource =
  | HTMLElement
  | HTMLImageElement
  | HTMLCanvasElement
  | Worker
  | WebGLRenderingContext
  | WebGL2RenderingContext
  | Path2D
  | Disposable
  | TrackedResource[]
  | Set<TrackedResource>
  | Map<unknown, TrackedResource>;

@injectable()
export class ResourceTracker implements IResourceTracker {
  private resources: Set<TrackedResource>;
  private active: boolean;
  private tracking: boolean = false;
  private usageHistory: Array<{
    timestamp: Date;
    memoryUsage: number;
    cpuUsage?: number;
  }> = [];

  constructor() {
    this.resources = new Set();
    this.active = true;
  }

  /**
   * Start tracking resources (IResourceTracker interface method)
   */
  startTracking(): void {
    this.tracking = true;
    console.log("🔍 Resource tracking started");
  }

  /**
   * Stop tracking resources (IResourceTracker interface method)
   */
  stopTracking(): void {
    this.tracking = false;
    console.log("⏹️ Resource tracking stopped");
  }

  /**
   * Get current resource usage (IResourceTracker interface method)
   */
  getCurrentUsage(): {
    memoryUsage: number;
    cpuUsage?: number;
    activeConnections: number;
  } {
    const memoryUsage = this.getMemoryUsage();
    const usage = {
      memoryUsage,
      activeConnections: this.resources.size,
    };

    // Add to history if tracking
    if (this.tracking) {
      this.usageHistory.push({
        timestamp: new Date(),
        memoryUsage,
      });

      // Keep only last 100 entries
      if (this.usageHistory.length > 100) {
        this.usageHistory.shift();
      }
    }

    return usage;
  }

  /**
   * Get resource usage history (IResourceTracker interface method)
   */
  getUsageHistory(): Array<{
    timestamp: Date;
    memoryUsage: number;
    cpuUsage?: number;
  }> {
    return [...this.usageHistory];
  }

  /**
   * Get memory usage estimate
   */
  private getMemoryUsage(): number {
    // Estimate memory usage based on tracked resources
    let estimate = 0;

    this.resources.forEach((resource) => {
      if (resource instanceof HTMLCanvasElement) {
        estimate += resource.width * resource.height * 4; // RGBA bytes
      } else if (resource instanceof HTMLImageElement) {
        estimate +=
          (resource.naturalWidth || 100) * (resource.naturalHeight || 100) * 4;
      } else {
        estimate += 1024; // Default 1KB estimate per resource
      }
    });

    return estimate;
  }

  public trackResource(resource: unknown): void {
    if (!this.active) {
      console.warn(
        "Attempting to track a resource after ResourceTracker has been disposed"
      );
      return;
    }

    this.resources.add(resource as TrackedResource);
  }

  public untrackResource(resource: unknown): void {
    this.resources.delete(resource as TrackedResource);
  }

  public disposeAll(): void {
    if (!this.active) return;

    try {
      this.resources.forEach((resource) => {
        this.disposeResource(resource);
      });

      this.resources.clear();
    } catch (e) {
      console.error("Error disposing resources:", e);
    } finally {
      this.active = false;
    }
  }

  public getTrackedCount(): number {
    return this.resources.size;
  }

  public isActive(): boolean {
    return this.active;
  }

  public deactivate(): void {
    this.active = false;
  }

  private disposeResource(resource: TrackedResource): void {
    if (!resource) return;

    if (resource instanceof HTMLElement) {
      if (resource.parentNode) {
        resource.parentNode.removeChild(resource);
      }

      if (resource instanceof HTMLImageElement) {
        resource.onload = null;
        resource.onerror = null;

        resource.src = "";
      } else if (resource instanceof HTMLCanvasElement) {
        const context = resource.getContext("2d");
        if (context) {
          context.setTransform(1, 0, 0, 1, 0, 0);
          context.clearRect(0, 0, resource.width, resource.height);
        }
      }
    } else if (
      resource instanceof WebGLRenderingContext ||
      resource instanceof WebGL2RenderingContext
    ) {
      const extension = resource.getExtension("WEBGL_lose_context");
      if (extension) extension.loseContext();
    } else if (resource instanceof Path2D) {
      // Path2D objects don't need explicit cleanup
    } else if (
      resource &&
      typeof resource === "object" &&
      "dispose" in resource &&
      typeof resource.dispose === "function"
    ) {
      resource.dispose();
    } else if (
      resource &&
      typeof resource === "object" &&
      "close" in resource &&
      typeof resource.close === "function"
    ) {
      resource.close();
    } else if (
      resource &&
      typeof resource === "object" &&
      "cleanup" in resource &&
      typeof resource.cleanup === "function"
    ) {
      resource.cleanup();
    } else if (Array.isArray(resource)) {
      resource.forEach((item) => this.disposeResource(item));
    } else if (resource instanceof Set || resource instanceof Map) {
      resource.forEach((item) => this.disposeResource(item));
      resource.clear();
    }
  }

  public isTracking(resource: unknown): boolean {
    return this.resources.has(resource as TrackedResource);
  }

  public get resourceCount(): number {
    return this.resources.size;
  }
}

import type { IPersistenceService, ISettingsService } from "$shared";
import { ensureContainerInitialized, resolve } from "../../inversify";
import { TYPES } from "../../inversify/types";

// Make isInitialized reactive so components using getSettings() will re-evaluate
let isInitialized = $state(false);
let settingsService: ISettingsService | null = null;
let persistenceService: IPersistenceService | null = null;

export async function initializeAppServices() {
  if (isInitialized) return;

  await ensureContainerInitialized();
  settingsService = await resolve(TYPES.ISettingsService);
  isInitialized = true;
}

export function clearAppServicesCache(): void {
  isInitialized = false;
  settingsService = null;
  persistenceService = null;
}

export function getSettingsServiceSync(): ISettingsService {
  if (!settingsService) {
    throw new Error(
      "Settings service not initialized. Call initializeAppServices first."
    );
  }
  return settingsService;
}

export function getSettingsService(): ISettingsService {
  if (!settingsService) {
    const resolved = resolve<ISettingsService>(TYPES.ISettingsService);
    if (!resolved) {
      throw new Error("Failed to resolve ISettingsService");
    }
    settingsService = resolved;
  }
  return settingsService;
}

export function getPersistenceService(): IPersistenceService {
  if (!persistenceService) {
    const resolved = resolve<IPersistenceService>(TYPES.IPersistenceService);
    if (!resolved) {
      throw new Error("Failed to resolve IPersistenceService");
    }
    persistenceService = resolved;
  }
  return persistenceService;
}

export function areServicesInitialized(): boolean {
  return isInitialized;
}

/**
 * Authentication Service Implementation
 *
 * Handles all Firebase authentication operations including social auth
 * (Google, Facebook) and email/password authentication.
 */

import {
  GoogleAuthProvider,
  FacebookAuthProvider,
  signInWithRedirect,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut as firebaseSignOut,
  setPersistence,
  indexedDBLocalPersistence,
  browserLocalPersistence,
  sendEmailVerification,
  updateProfile,
} from "firebase/auth";
import { auth } from "../../firebase";
import { injectable } from "inversify";
import type { IAuthService } from "../contracts";

@injectable()
export class AuthService implements IAuthService {
  // ============================================================================
  // SOCIAL AUTHENTICATION
  // ============================================================================

  async signInWithGoogle(): Promise<void> {
    console.log("🔐 [google] Starting sign-in process...");

    try {
      // Create Google provider
      const provider = new GoogleAuthProvider();
      provider.addScope("email");
      provider.addScope("profile");

      console.log("🔐 [google] Redirecting to Google sign-in...");
      await signInWithRedirect(auth, provider);
    } catch (error: unknown) {
      console.error("❌ [google] Sign-in error:", error);
      const message = error instanceof Error ? error.message : String(error);
      throw new Error(`Google sign-in failed: ${message}`);
    }
  }

  async signInWithFacebook(): Promise<void> {
    console.log("🔐 [facebook] Starting sign-in process...");

    try {
      // Create Facebook provider
      const provider = new FacebookAuthProvider();
      provider.addScope("email");
      provider.addScope("public_profile");

      console.log("🔐 [facebook] Redirecting to Facebook sign-in...");
      await signInWithRedirect(auth, provider);
    } catch (error: unknown) {
      console.error("❌ [facebook] Sign-in error:", error);
      const message = error instanceof Error ? error.message : String(error);
      throw new Error(`Facebook sign-in failed: ${message}`);
    }
  }

  // ============================================================================
  // EMAIL/PASSWORD AUTHENTICATION
  // ============================================================================

  async signInWithEmail(email: string, password: string): Promise<void> {
    console.log("🔐 [email] Starting sign-in process...");

    try {
      // Set persistence first
      await this.setPersistence();

      // Sign in with email and password
      const userCredential = await signInWithEmailAndPassword(
        auth,
        email,
        password
      );

      console.log("✅ [email] Sign-in successful:", userCredential.user.email);
    } catch (error: unknown) {
      console.error("❌ [email] Sign-in error:", error);
      const message = error instanceof Error ? error.message : 'Unknown error';
      throw new Error(`Email sign-in failed: ${message}`);
    }
  }

  async signUpWithEmail(
    email: string,
    password: string,
    name?: string
  ): Promise<void> {
    console.log("🔐 [email] Starting sign-up process...");

    try {
      // Set persistence first
      await this.setPersistence();

      // Create user with email and password
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        email,
        password
      );

      console.log("✅ [email] User created:", userCredential.user.email);

      // Update profile with display name if provided
      if (name && userCredential.user) {
        await updateProfile(userCredential.user, { displayName: name });
        console.log("✅ [email] Profile updated with name:", name);
      }

      // Send email verification
      if (userCredential.user) {
        await sendEmailVerification(userCredential.user);
        console.log("✅ [email] Verification email sent");
      }
    } catch (error: unknown) {
      console.error("❌ [email] Sign-up error:", error);
      const message = error instanceof Error ? error.message : 'Unknown error';
      throw new Error(`Email sign-up failed: ${message}`);
    }
  }

  // ============================================================================
  // SIGN OUT
  // ============================================================================

  async signOut(): Promise<void> {
    console.log("🔐 [auth] Signing out...");

    try {
      await firebaseSignOut(auth);
      console.log("✅ [auth] Sign-out successful");
    } catch (error: unknown) {
      console.error("❌ [auth] Sign-out error:", error);
      const message = error instanceof Error ? error.message : 'Unknown error';
      throw new Error(`Sign-out failed: ${message}`);
    }
  }

  // ============================================================================
  // PERSISTENCE
  // ============================================================================

  async setPersistence(): Promise<void> {
    try {
      // Try IndexedDB first (preferred)
      await setPersistence(auth, indexedDBLocalPersistence);
      console.log("✅ [auth] IndexedDB persistence set");
    } catch (indexedDBError) {
      console.warn(
        "⚠️ [auth] IndexedDB persistence failed, falling back to localStorage"
      );

      try {
        // Fallback to localStorage
        await setPersistence(auth, browserLocalPersistence);
        console.log("✅ [auth] localStorage persistence set");
      } catch (localStorageError: unknown) {
        console.error(
          "❌ [auth] Failed to set persistence:",
          localStorageError
        );
        const message = localStorageError instanceof Error ? localStorageError.message : 'Unknown error';
        throw new Error(
          `Failed to set persistence: ${message}`
        );
      }
    }
  }
}

/**
 * Authentication Store (Svelte 5 Runes)
 *
 * Manages authentication state across the application using Firebase Auth.
 * Provides reactive user data and auth status using Svelte 5 runes.
 */

import {
  onAuthStateChanged,
  signOut as firebaseSignOut,
  updateProfile,
  updateEmail,
  sendEmailVerification,
  EmailAuthProvider,
  reauthenticateWithCredential,
  type User,
} from "firebase/auth";
import { doc, getDoc, setDoc, serverTimestamp } from "firebase/firestore";
import { auth, firestore } from "../firebase";

interface FirebaseError extends Error {
  code?: string;
}

function isFirebaseError(error: unknown): error is FirebaseError {
  return error instanceof Error && 'code' in error;
}

/**
 * Update Facebook profile picture to high resolution
 * Facebook Graph API provides higher resolution pictures than the default Firebase photoURL
 */
async function updateFacebookProfilePictureIfNeeded(user: User) {
  try {
    // Check if user has Facebook provider
    const facebookData = user.providerData.find(
      (data) => data.providerId === "facebook.com"
    );

    if (!facebookData?.uid) {
      return; // Not a Facebook user
    }

    // Check if we need to update the profile picture
    // If photoURL doesn't contain graph.facebook.com, it's the low-res default
    if (user.photoURL && user.photoURL.includes("graph.facebook.com")) {
      return; // Already using high-res picture
    }

    // Facebook Graph API URL for high-resolution profile picture
    const photoURL = `https://graph.facebook.com/${facebookData.uid}/picture?type=large`;

    // Update the user's profile with the high-res photo URL
    await updateProfile(user, {
      photoURL: photoURL,
    });
  } catch (err) {
    console.error(
      `❌ [authStore] Failed to update Facebook profile picture:`,
      err
    );
    // Don't throw - this is a non-critical enhancement
  }
}

/**
 * Update Google profile picture to high resolution
 * Google profile pictures from Firebase Auth default to s96-c (96x96 pixels)
 * We can get higher resolution by replacing s96-c with s400-c or s512-c
 */
async function updateGoogleProfilePictureIfNeeded(user: User) {
  try {
    // Check if user has Google provider
    const googleData = user.providerData.find(
      (data) => data.providerId === "google.com"
    );

    if (!googleData?.uid) {
      return; // Not a Google user
    }

    // Check if we need to update the profile picture
    if (!user.photoURL || !user.photoURL.includes("googleusercontent.com")) {
      return; // Not a Google profile picture
    }

    // Check if it's already high-res (doesn't contain s96-c)
    if (!user.photoURL.includes("s96-c")) {
      return; // Already using high-res picture
    }

    // Replace s96-c with s400-c for 400x400 resolution
    // You can also use s512-c for 512x512 or higher values
    const highResPhotoURL = user.photoURL.replace("s96-c", "s400-c");

    // Update the user's profile with the high-res photo URL
    await updateProfile(user, {
      photoURL: highResPhotoURL,
    });
  } catch (err) {
    console.error(
      `❌ [authStore] Failed to update Google profile picture:`,
      err
    );
    // Don't throw - this is a non-critical enhancement
  }
}

/**
 * Create or update user document in Firestore
 * This ensures every authenticated user has a corresponding Firestore document
 * that can be displayed in the users explore panel
 */
async function createOrUpdateUserDocument(user: User) {
  try {
    const userDocRef = doc(firestore, `users/${user.uid}`);
    const userDoc = await getDoc(userDocRef);

    // Determine display name and username
    const displayName =
      user.displayName ?? user.email?.split("@")[0] ?? "Anonymous User";
    const username = user.email?.split("@")[0] ?? user.uid.substring(0, 8);

    if (!userDoc.exists()) {
      // Create new user document
      console.log(`✨ [authStore] Creating user document for ${user.uid}...`);

      await setDoc(userDocRef, {
        email: user.email,
        displayName,
        username,
        photoURL: user.photoURL ?? null,
        avatar: user.photoURL ?? null,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
        // Initialize counts
        sequenceCount: 0,
        collectionCount: 0,
        followerCount: 0,
        // Initialize gamification fields (denormalized for leaderboards)
        totalXP: 0,
        currentLevel: 1,
        achievementCount: 0,
        currentStreak: 0,
        longestStreak: 0,
        // Admin status (default false)
        isAdmin: false,
      });

      console.log(`✅ [authStore] User document created for ${user.uid}`);
    } else {
      // Update existing user document with latest auth data
      console.log(`🔄 [authStore] Updating user document for ${user.uid}...`);

      await setDoc(
        userDocRef,
        {
          email: user.email,
          displayName,
          username,
          photoURL: user.photoURL ?? null,
          avatar: user.photoURL ?? null,
          updatedAt: serverTimestamp(),
        },
        { merge: true } // Merge to preserve existing fields like counts
      );

      console.log(`✅ [authStore] User document updated for ${user.uid}`);
    }
  } catch (error) {
    console.error(
      `❌ [authStore] Failed to create/update user document:`,
      error
    );
    // Don't throw - this shouldn't block authentication
  }
}

interface AuthState {
  user: User | null;
  loading: boolean;
  initialized: boolean;
  isAdmin: boolean;
}

// ============================================================================
// REACTIVE STATE (Svelte 5 Runes - Module Pattern)
// ============================================================================

// 🚧 TEMPORARY DEBUG FLAG - Remove before production!
// Set this to true to bypass Firebase admin check for testing
const FORCE_ADMIN_MODE = false;

// Internal reactive state
let _state = $state<AuthState>({
  user: null,
  loading: true,
  initialized: false,
  isAdmin: FORCE_ADMIN_MODE, // Start with forced admin if debugging
});

// Cleanup function reference
let cleanupAuthListener: (() => void) | null = null;

// ============================================================================
// PUBLIC API - Reactive Getters
// ============================================================================

/**
 * Authentication store object with reactive getters
 */
export const authStore = {
  /**
   * Current authenticated user (or null)
   */
  get user() {
    return _state.user;
  },

  /**
   * Whether a user is currently authenticated
   */
  get isAuthenticated() {
    return !!_state.user;
  },

  /**
   * Whether auth state is currently loading
   */
  get isLoading() {
    return _state.loading;
  },

  /**
   * Whether auth has been initialized
   */
  get isInitialized() {
    return _state.initialized;
  },

  /**
   * Whether the current user is an admin
   */
  get isAdmin() {
    return _state["isAdmin"];
  },

  // ============================================================================
  // Methods
  // ============================================================================

  /**
   * Initialize the auth listener
   * Call this once when your app starts
   */
  async initialize() {
    if (cleanupAuthListener) {
      return; // Already initialized
    }

    // Check for old cached data
    if (typeof window !== "undefined") {
      try {
        const databases = await window.indexedDB.databases();
        const firebaseDbs = databases.filter(
          (db) =>
            db.name &&
            (db.name.includes("firebase") || db.name.includes("firestore"))
        );

        // CRITICAL: Check for old project
        const oldProjectDb = firebaseDbs.find((db) =>
          db.name?.includes("the-kinetic-constructor")
        );

        if (oldProjectDb) {
          console.error(
            "🚨 [authStore] OLD PROJECT DATABASE DETECTED:",
            oldProjectDb.name
          );
          console.error("🚨 This WILL cause auth failures!");
          console.error("🚨 Press Ctrl+Shift+Delete to clear cache");
        }
      } catch (error) {
        console.warn("⚠️ [authStore] Could not check IndexedDB:", error);
      }
    }

    cleanupAuthListener = onAuthStateChanged(
      auth,
      (user) => {
        void (async () => {
          let isAdmin = false;

          if (user) {
            // Create or update user document in Firestore
            // This MUST happen before checking admin status
            await createOrUpdateUserDocument(user);

            // Update Facebook profile picture if needed (async, non-blocking)
            void updateFacebookProfilePictureIfNeeded(user);

            // Update Google profile picture if needed (async, non-blocking)
            void updateGoogleProfilePictureIfNeeded(user);

            // Check if user is admin
            try {
              // 🚧 FORCE ADMIN MODE FOR DEBUGGING
              if (FORCE_ADMIN_MODE) {
                isAdmin = true;
              } else {
                const userDocRef = doc(firestore, `users/${user.uid}`);
                const userDoc = await getDoc(userDocRef);
                if (userDoc.exists()) {
                  const userData = userDoc.data();
                  isAdmin = userData["isAdmin"] === true;
                }
              }
            } catch (error) {
              console.warn("⚠️ [authStore] Could not check admin status:", error);
              // If forced admin mode, still set as admin even on error
              if (FORCE_ADMIN_MODE) {
                isAdmin = true;
              }
            }
          } else {
            // 🚧 Keep admin mode if forced (for debugging without login)
            if (FORCE_ADMIN_MODE) {
              isAdmin = true;
            }
          }

          _state = {
            user,
            loading: false,
            initialized: true,
            isAdmin,
          };

          // Revalidate current module after auth state changes
          // This allows admin module to be restored if user is admin
          if (typeof window !== "undefined") {
            try {
              // Dynamic import to avoid circular dependency
              void import("../../application/state/ui/module-state").then(
                (moduleState) => {
                  void moduleState.revalidateCurrentModule();
                }
              );
            } catch (error) {
              // Ignore - module state may not be available yet
            }
          }
        })();
      },
      (error) => {
        console.error("❌ [authStore] Auth state change error:", error);
        _state = {
          user: null,
          loading: false,
          initialized: true,
          isAdmin: false,
        };
      }
    );
  },

  /**
   * Sign out the current user
   */
  async signOut() {
    try {
      await firebaseSignOut(auth);
      // State will be updated automatically by onAuthStateChanged
    } catch (error) {
      console.error("❌ [authStore] Sign out error:", error);
      throw error;
    }
  },

  /**
   * Change user email (requires re-authentication)
   * @param newEmail - The new email address
   * @param currentPassword - Current password for re-authentication
   */
  async changeEmail(newEmail: string, currentPassword: string) {
    const user = _state.user;
    if (!user?.email) {
      throw new Error("No authenticated user");
    }

    try {
      // Re-authenticate user with current password
      const credential = EmailAuthProvider.credential(
        user.email,
        currentPassword
      );
      await reauthenticateWithCredential(user, credential);

      // Update email
      await updateEmail(user, newEmail);

      // Send verification email to new address
      await sendEmailVerification(user);

      return {
        success: true,
        message:
          "Email updated successfully. Please check your inbox to verify your new email address.",
      };
    } catch (error: unknown) {
      console.error("❌ [authStore] Email change error:", error);

      // Handle specific Firebase errors
      if (isFirebaseError(error)) {
        if (error.code === "auth/wrong-password") {
          throw new Error("Incorrect password. Please try again.");
        } else if (error.code === "auth/email-already-in-use") {
          throw new Error("This email is already in use by another account.");
        } else if (error.code === "auth/invalid-email") {
          throw new Error("Invalid email address format.");
        } else if (error.code === "auth/requires-recent-login") {
          throw new Error(
            "Please sign out and sign in again before changing your email."
          );
        }
      }

      const message = error instanceof Error ? error.message : String(error);
      throw new Error(message);
    }
  },

  /**
   * Update user's display name
   * @param displayName - The new display name
   */
  async updateDisplayName(displayName: string) {
    const user = _state.user;
    if (!user) {
      throw new Error("No authenticated user");
    }

    try {
      // Update display name
      await updateProfile(user, {
        displayName: displayName.trim() || null,
      });

      return {
        success: true,
        message: "Display name updated successfully.",
      };
    } catch (error: unknown) {
      console.error("❌ [authStore] Display name update error:", error);
      const message = error instanceof Error ? error.message : String(error);
      throw new Error(message);
    }
  },

  /**
   * Clean up the auth listener
   * Call this when your app unmounts (if ever)
   */
  cleanup() {
    if (cleanupAuthListener) {
      cleanupAuthListener();
      cleanupAuthListener = null;
    }
  },
};

// src/lib/components/backgrounds/auroraBorealis/AuroraBorealisBackgroundSystem.ts

import type { AccessibilitySettings } from "../../shared/domain/models/background-models";
import type {
  Dimensions,
  PerformanceMetrics,
  QualityLevel,
} from "../../shared/domain/types/background-types";
import type { IBackgroundSystem } from "../../shared/services/contracts/IBackgroundSystem";

export class AuroraBorealisBackgroundSystem implements IBackgroundSystem {
  private quality: QualityLevel = "medium";
  private accessibility: AccessibilitySettings = {
    reducedMotion: false,
    highContrast: false,
    visibleParticleSize: 2,
  };

  // Animation state
  private lightWaves: number[] = [];
  private isInitialized = false;

  // Aurora Borealis color palette
  private readonly auroraColors = [
    { r: 0, g: 25, b: 50, a: 0.4 }, // Deep blue
    { r: 0, g: 50, b: 100, a: 0.2 }, // Medium blue
    { r: 0, g: 100, b: 150, a: 0.1 }, // Light blue
    { r: 50, g: 150, b: 100, a: 0.15 }, // Blue-green
    { r: 100, g: 200, b: 150, a: 0.12 }, // Green
    { r: 150, g: 255, b: 200, a: 0.08 }, // Light green
  ];

  public initialize(_dimensions: Dimensions, quality: QualityLevel): void {
    this.quality = quality;
    this.isInitialized = true;

    // Initialize light waves with random phases for natural variation
    const numWaves = this.getNumWaves();
    this.lightWaves = [];
    for (let i = 0; i < numWaves; i++) {
      this.lightWaves.push(Math.random() * 2 * Math.PI);
    }
  }

  public update(_dimensions: Dimensions): void {
    if (!this.isInitialized) return;

    // Respect accessibility settings
    const animationSpeed = this.accessibility.reducedMotion ? 0.002 : 1.0;

    // Update light wave positions for smooth animation
    // Advance each wave at slightly different speeds for natural variation
    for (let i = 0; i < this.lightWaves.length; i++) {
      const currentWave = this.lightWaves[i];
      if (currentWave !== undefined) {
        const waveSpeed = (0.008 + i * 0.002) * animationSpeed; // Varying speeds with accessibility consideration
        const newValue = currentWave + waveSpeed;

        // Keep waves within reasonable bounds to prevent overflow
        if (newValue > 4 * Math.PI) {
          this.lightWaves[i] = newValue - 4 * Math.PI;
        } else {
          this.lightWaves[i] = newValue;
        }
      }
    }
  }

  public draw(ctx: CanvasRenderingContext2D, dimensions: Dimensions): void {
    if (!this.isInitialized) return;

    // Draw base gradient from dark to lighter
    this.drawBaseGradient(ctx, dimensions);

    // Draw aurora light waves
    this.drawAuroraWaves(ctx, dimensions);
  }

  public setQuality(quality: QualityLevel): void {
    this.quality = quality;
    if (this.isInitialized) {
      // Adjust number of waves based on quality
      const numWaves = this.getNumWaves();
      while (this.lightWaves.length > numWaves) this.lightWaves.pop();
      while (this.lightWaves.length < numWaves) {
        this.lightWaves.push(Math.random() * 2 * Math.PI);
      }
    }
  }

  public setAccessibility(settings: AccessibilitySettings): void {
    this.accessibility = settings;
    // Note: Accessibility settings would be used to modify animation behavior
    // For example, reducing motion if settings.reducedMotion is true
  }

  public cleanup(): void {
    this.lightWaves = [];
    this.isInitialized = false;
  }

  public getMetrics(): PerformanceMetrics {
    return {
      fps: 60, // Estimated
      warnings: [],
      particleCount: this.lightWaves.length,
    };
  }

  private getNumWaves(): number {
    switch (this.quality) {
      case "high":
        return 12;
      case "medium":
        return 10;
      case "low":
        return 6;
      case "minimal":
        return 4;
      default:
        return 10;
    }
  }

  private drawBaseGradient(
    ctx: CanvasRenderingContext2D,
    dimensions: Dimensions
  ): void {
    // Create base gradient from dark to lighter
    const baseGradient = ctx.createLinearGradient(
      0,
      0,
      dimensions.width,
      dimensions.height
    );
    baseGradient.addColorStop(0, "rgb(5, 10, 25)"); // Very dark blue
    baseGradient.addColorStop(0.5, "rgb(10, 20, 40)"); // Dark blue
    baseGradient.addColorStop(1, "rgb(15, 30, 60)"); // Medium dark blue

    ctx.fillStyle = baseGradient;
    ctx.fillRect(0, 0, dimensions.width, dimensions.height);
  }

  private drawAuroraWaves(
    ctx: CanvasRenderingContext2D,
    dimensions: Dimensions
  ): void {
    // Calculate wave positions for gradient
    const wavePositions: Array<[number, number]> = [];

    for (let i = 0; i < this.lightWaves.length; i++) {
      const wave = this.lightWaves[i];
      if (wave !== undefined) {
        const position = (Math.sin(wave) + 1) / 2; // Normalize to 0-1
        wavePositions.push([position, i]);
      }
    }

    // Sort positions to ensure proper gradient ordering
    wavePositions.sort((a, b) => a[0] - b[0]);

    // Create gradient with aurora colors
    const gradient = ctx.createLinearGradient(
      0,
      0,
      dimensions.width,
      dimensions.height
    );

    for (const [pos, waveIndex] of wavePositions) {
      const colorIndex = waveIndex % this.auroraColors.length;
      const color = this.auroraColors[colorIndex];

      if (color && this.lightWaves[waveIndex] !== undefined) {
        // Add some dynamic intensity variation
        const waveValue = this.lightWaves[waveIndex];
        if (waveValue !== undefined) {
          const intensityFactor = (Math.sin(waveValue * 1.5) + 1) / 2;
          const alpha = color.a * intensityFactor;

          gradient.addColorStop(
            pos,
            `rgba(${color.r}, ${color.g}, ${color.b}, ${alpha})`
          );
        }
      }
    }

    // Fill with the aurora gradient
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, dimensions.width, dimensions.height);

    // Add additional wave effects for more realism
    if (this.quality === "high") {
      this.drawAdditionalWaveEffects(ctx, dimensions);
    }
  }

  private drawAdditionalWaveEffects(
    ctx: CanvasRenderingContext2D,
    dimensions: Dimensions
  ): void {
    // Add subtle vertical wave patterns for enhanced aurora effect
    ctx.save();

    for (let i = 0; i < this.lightWaves.length; i += 2) {
      const wave = this.lightWaves[i];
      if (wave !== undefined) {
        const x = ((Math.sin(wave * 0.5) + 1) / 2) * dimensions.width;
        const width = 20 + Math.sin(wave) * 10;

        const waveGradient = ctx.createLinearGradient(
          x - width / 2,
          0,
          x + width / 2,
          0
        );
        const color = this.auroraColors[i % this.auroraColors.length];
        if (color) {
          const intensity = ((Math.sin(wave * 2) + 1) / 2) * 0.1;

          waveGradient.addColorStop(
            0,
            `rgba(${color.r}, ${color.g}, ${color.b}, 0)`
          );
          waveGradient.addColorStop(
            0.5,
            `rgba(${color.r}, ${color.g}, ${color.b}, ${intensity})`
          );
          waveGradient.addColorStop(
            1,
            `rgba(${color.r}, ${color.g}, ${color.b}, 0)`
          );

          ctx.fillStyle = waveGradient;
          ctx.fillRect(x - width / 2, 0, width, dimensions.height);
        }
      }
    }

    ctx.restore();
  }
}

/**
 * Deep Ocean Background System
 *
 * Creates an immersive underwater environment with:
 * - Floating bubbles with realistic physics
 * - Marine life (fish sprites, jellyfish)
 * - Particle effects for depth
 * - Dynamic light rays from surface
 * - Ocean gradient background
 */

import type {
  AccessibilitySettings,
  Dimensions,
  IBackgroundSystem,
  PerformanceMetrics,
  QualityLevel,
} from "../../shared";
import type {
  Bubble,
  DeepOceanState,
  FishMarineLife,
  FishSprite,
  JellyfishMarineLife,
  MarineLife,
  MarineLifeType,
  OceanParticle,
} from "../domain/models/DeepOceanModels";

export class DeepOceanBackgroundSystem implements IBackgroundSystem {
  private state: DeepOceanState;
  private quality: QualityLevel = "medium";
  private accessibility: AccessibilitySettings = {
    reducedMotion: false,
    highContrast: false,
    visibleParticleSize: 1,
  };
  private animationTime = 0;
  private readonly fishSprites: FishSprite[] = [
    { name: "Blue", path: "/assets/background/fish/kenney/fish_blue.png" },
    { name: "Orange", path: "/assets/background/fish/kenney/fish_orange.png" },
    { name: "Green", path: "/assets/background/fish/kenney/fish_green.png" },
    {
      name: "Grey Long",
      path: "/assets/background/fish/kenney/fish_grey_long_a.png",
    },
  ];
  private fishSpriteCache = new Map<
    string,
    { sprite: FishSprite; image: HTMLImageElement; ready: boolean }
  >();

  constructor() {
    this.state = {
      bubbles: [],
      marineLife: [],
      particles: [],
      currentGradient: {
        top: "#0d2d47", // Rich ocean blue
        bottom: "#091a2b", // Darker ocean depth
      },
      lightRays: [],
      pendingSpawns: [],
    };

    this.preloadFishSprites();
  }

  initialize(dimensions: Dimensions, quality: QualityLevel): void {
    this.quality = quality;
    this.animationTime = 0;

    // Initialize bubbles
    this.initializeBubbles(dimensions);

    // Initialize marine life
    this.initializeMarineLife(dimensions);

    // Initialize particles
    this.initializeParticles(dimensions);

    // Initialize light rays
    this.initializeLightRays(dimensions);

    // Pre-populate: Distribute elements across viewport so animation appears already running
    // Spread bubbles across full height (instead of starting at bottom)
    this.state.bubbles.forEach((bubble) => {
      bubble.y = Math.random() * dimensions.height;
    });

    // Spread particles across full height
    this.state.particles.forEach((particle) => {
      particle.y = Math.random() * dimensions.height;
    });

    // Randomize animation time so light rays appear mid-animation
    this.animationTime = Math.random() * 1000;

    console.log(
      `🌊 Deep Ocean background initialized with ${this.state.bubbles.length} bubbles, ${this.state.marineLife.length} marine life`
    );
  }

  private initializeBubbles(dimensions: Dimensions): void {
    const bubbleCount = this.getBubbleCount();
    this.state.bubbles = [];

    for (let i = 0; i < bubbleCount; i++) {
      this.state.bubbles.push(this.createBubble(dimensions));
    }
  }

  private initializeMarineLife(dimensions: Dimensions): void {
    const total = this.getMarineLifeCount();
    const fishCount = Math.max(2, Math.round(total * 0.65));
    const jellyCount = Math.max(1, total - fishCount);

    const school: MarineLife[] = [];
    for (let i = 0; i < fishCount; i++) {
      school.push(this.createFish(dimensions));
    }
    for (let i = 0; i < jellyCount; i++) {
      school.push(this.createJellyfish(dimensions));
    }

    this.state.marineLife = school;
  }

  private initializeParticles(dimensions: Dimensions): void {
    const particleCount = this.getParticleCount();
    this.state.particles = [];

    for (let i = 0; i < particleCount; i++) {
      this.state.particles.push(this.createParticle(dimensions));
    }
  }

  private initializeLightRays(dimensions: Dimensions): void {
    const rayCount =
      this.quality === "high" ? 8 : this.quality === "medium" ? 5 : 3;
    this.state.lightRays = [];

    for (let i = 0; i < rayCount; i++) {
      this.state.lightRays.push({
        x:
          (dimensions.width / rayCount) * i +
          Math.random() * (dimensions.width / rayCount),
        opacity: 0.08 + Math.random() * 0.1, // Subtler but visible: 0.08-0.18
        width: 20 + Math.random() * 40,
        angle: -5 + Math.random() * 10,
        phase: Math.random() * Math.PI * 2,
        speed: 0.003 + Math.random() * 0.004, // Slower: 0.003-0.007 instead of 0.006-0.012
      });
    }
  }

  private createBubble(dimensions: Dimensions): Bubble {
    return {
      x: Math.random() * dimensions.width,
      y: dimensions.height + Math.random() * 100,
      radius: 2 + Math.random() * 5, // Moderately smaller bubbles: 2-7 instead of 2-10
      speed: 0.2 + Math.random() * 0.6, // Much slower: 0.2-0.8 instead of 0.5-2.5
      sway: 0.3 + Math.random() * 0.8, // Gentler sway: 0.3-1.1 instead of 0.5-2
      opacity: 0.3 + Math.random() * 0.4,
      swayOffset: Math.random() * Math.PI * 2,
      startY: dimensions.height + Math.random() * 100,
    };
  }

  private preloadFishSprites(): void {
    if (typeof window === "undefined") return;

    this.fishSprites.forEach((sprite) => {
      if (this.fishSpriteCache.has(sprite.path)) return;

      const image = new Image();
      const cacheEntry = { sprite, image, ready: image.complete };

      image.onload = () => {
        this.fishSpriteCache.set(sprite.path, {
          sprite,
          image,
          ready: true,
        });
      };

      image.onerror = () => {
        console.warn(`Failed to load fish sprite: ${sprite.path}`);
        this.fishSpriteCache.delete(sprite.path);
      };

      image.src = sprite.path;
      this.fishSpriteCache.set(sprite.path, cacheEntry);
    });
  }

  private getRandomFishSpriteEntry() {
    if (this.fishSprites.length === 0) return undefined;
    const sprite =
      this.fishSprites[Math.floor(Math.random() * this.fishSprites.length)];
    if (!sprite) return undefined;
    const entry = this.fishSpriteCache.get(sprite.path);
    return entry;
  }

  private createFish(dimensions: Dimensions): FishMarineLife {
    const entry = this.getRandomFishSpriteEntry();
    const sprite = entry?.sprite ?? this.fishSprites[0]!;
    const direction: 1 | -1 = Math.random() > 0.5 ? 1 : -1;
    const baseWidth = entry?.image.naturalWidth ?? 96;
    const baseHeight = entry?.image.naturalHeight ?? 64;
    const scale = 0.35 + Math.random() * 0.25; // Moderately smaller: 35-60% instead of 55-90%
    const width = baseWidth * scale;
    const height = baseHeight * scale;
    const depthBand = {
      min: 90,
      max: Math.max(240, dimensions.height - 180),
    };
    const maxOffset = Math.max(140, dimensions.width * 0.2);

    const startX =
      direction === 1
        ? -width - Math.random() * maxOffset
        : dimensions.width + width + Math.random() * maxOffset;
    const baseY =
      depthBand.min +
      Math.random() * Math.max(60, depthBand.max - depthBand.min);

    const fish: FishMarineLife = {
      type: "fish",
      sprite,
      width,
      height,
      direction,
      speed: 12 + Math.random() * 15, // Much slower: 12-27 instead of 35-80
      verticalDrift: (Math.random() - 0.5) * 6, // Gentler drift: ±3 instead of ±9
      bobAmplitude: 3 + Math.random() * 4, // Subtler bob: 3-7 instead of 6-15
      bobSpeed: 0.01 + Math.random() * 0.015, // Slower bob: 0.01-0.025 instead of 0.02-0.05
      depthBand,
      x: startX,
      baseY,
      y: baseY,
      opacity: 0.35 + Math.random() * 0.15,
      animationPhase: Math.random() * Math.PI * 2,
    };
    if (entry?.image) {
      fish.image = entry.image;
    }
    return fish;
  }

  private createJellyfish(dimensions: Dimensions): JellyfishMarineLife {
    const size = 18 + Math.random() * 15; // Moderately smaller: 18-33px instead of 28-52px
    const baseY =
      dimensions.height * 0.25 + Math.random() * (dimensions.height * 0.45);
    const tentacleSeeds = Array.from(
      { length: 5 + Math.floor(Math.random() * 3) },
      () => Math.random() * Math.PI * 2
    );

    return {
      type: "jellyfish",
      size,
      color: this.getMarineLifeColor("jellyfish"),
      horizontalSpeed: (Math.random() - 0.5) * 8, // Slower: ±4 instead of ±10
      verticalSpeed: -6 - Math.random() * 6, // Slower upward: -6 to -12 instead of -18 to -32
      waveAmplitude: 4 + Math.random() * 4, // Gentler wave: 4-8 instead of 10-18
      waveFrequency: 0.008 + Math.random() * 0.012, // Slower wave: 0.008-0.02 instead of 0.015-0.04
      glowIntensity: 0.15 + Math.random() * 0.15, // Subtler glow: 0.15-0.3 instead of 0.35-0.65
      tentacleSeeds,
      baseY,
      x: 60 + Math.random() * (dimensions.width - 120),
      y: baseY,
      opacity: 0.45 + Math.random() * 0.35,
      animationPhase: Math.random() * Math.PI * 2,
    };
  }

  private createParticle(dimensions: Dimensions): OceanParticle {
    return {
      x: Math.random() * dimensions.width,
      y: Math.random() * dimensions.height,
      vx: (Math.random() - 0.5) * 0.2, // Slower horizontal: ±0.1 instead of ±0.25
      vy: -0.05 - Math.random() * 0.15, // Slower upward: -0.05 to -0.2 instead of -0.1 to -0.4
      size: 0.8 + Math.random() * 2, // Smaller: 0.8-2.8 instead of 1-4
      opacity: 0.15 + Math.random() * 0.25, // More subtle opacity for dark particles
      color: this.getParticleColor(),
      life: 0,
      maxLife: 100 + Math.random() * 200,
    };
  }

  private getMarineLifeColor(type: "fish" | "jellyfish"): string {
    if (type === "fish") {
      // Ocean fish in nice teals and blues
      return ["#3d7a8c", "#4a8fa3", "#548da0", "#4b8599"][
        Math.floor(Math.random() * 4)
      ]!;
    }

    // Jellyfish in soft purples and pinks
    return ["#7d5a7a", "#8b6d88", "#946f91", "#866783"][
      Math.floor(Math.random() * 4)
    ]!;
  }

  private getParticleColor(): string {
    // Light, visible particles in ocean water
    const colors = ["#a8d5e2", "#b3dde8", "#9ec9d8", "#aad3df"];
    return colors[Math.floor(Math.random() * colors.length)]!;
  }

  private getBubbleCount(): number {
    switch (this.quality) {
      case "high":
        return 40;
      case "medium":
        return 25;
      case "low":
        return 15;
      case "minimal":
        return 8;
    }
  }

  private getMarineLifeCount(): number {
    switch (this.quality) {
      case "high":
        return 2;
      case "medium":
        return 2;
      case "low":
        return 1;
      case "minimal":
        return 1;
    }
  }

  private getParticleCount(): number {
    switch (this.quality) {
      case "high":
        return 60;
      case "medium":
        return 40;
      case "low":
        return 20;
      case "minimal":
        return 10;
    }
  }

  update(dimensions: Dimensions, frameMultiplier: number = 1.0): void {
    this.animationTime += 0.016 * frameMultiplier; // Normalize to 60fps equivalent

    const motionFactor = this.accessibility.reducedMotion ? 0.25 : 1;
    const adjustedFrame = frameMultiplier * motionFactor;

    // Update bubbles
    this.updateBubbles(dimensions, adjustedFrame);

    // Update marine life
    this.updateMarineLife(dimensions, adjustedFrame);

    // Check and spawn pending marine life
    this.processPendingSpawns(dimensions);

    // Update particles
    this.updateParticles(dimensions, adjustedFrame);

    // Update light rays
    this.updateLightRays(adjustedFrame);
  }

  private updateBubbles(
    dimensions: Dimensions,
    frameMultiplier: number = 1.0
  ): void {
    for (let i = this.state.bubbles.length - 1; i >= 0; i--) {
      const bubble = this.state.bubbles[i];

      // Update position
      if (bubble) {
        bubble.y -= bubble.speed * frameMultiplier;
        bubble.x +=
          Math.sin(this.animationTime * bubble.sway + bubble.swayOffset) *
          0.5 *
          frameMultiplier;

        // Remove if off screen and create new one
        if (bubble.y < -bubble.radius * 2) {
          this.state.bubbles[i] = this.createBubble(dimensions);
        }
      }
    }
  }

  private updateMarineLife(
    dimensions: Dimensions,
    frameMultiplier: number = 1.0
  ): void {
    for (let i = this.state.marineLife.length - 1; i >= 0; i--) {
      const marine = this.state.marineLife[i];

      if (!marine) continue;

      switch (marine.type) {
        case "fish": {
          const fish = marine;
          const deltaSeconds = 0.016 * frameMultiplier;
          fish.animationPhase += fish.bobSpeed * frameMultiplier;
          fish.x += fish.direction * fish.speed * deltaSeconds;
          fish.baseY += fish.verticalDrift * deltaSeconds;
          fish.baseY = Math.max(
            fish.depthBand.min,
            Math.min(fish.depthBand.max, fish.baseY)
          );
          const bob = Math.sin(fish.animationPhase) * fish.bobAmplitude;
          fish.y = fish.baseY + bob;

          const offRight =
            fish.direction === 1 &&
            fish.x > dimensions.width + fish.width * 1.5;
          const offLeft = fish.direction === -1 && fish.x < -fish.width * 1.5;

          if (offRight || offLeft) {
            // Remove fish and schedule a new spawn with delay
            this.state.marineLife.splice(i, 1);
            this.scheduleMarineLifeSpawn("fish");
          }
          break;
        }
        case "jellyfish": {
          const jelly = marine;
          const deltaSeconds = 0.016 * frameMultiplier;
          jelly.animationPhase += jelly.waveFrequency * frameMultiplier;
          jelly.x += jelly.horizontalSpeed * deltaSeconds;
          jelly.baseY += jelly.verticalSpeed * deltaSeconds;
          jelly.y =
            jelly.baseY + Math.sin(jelly.animationPhase) * jelly.waveAmplitude;

          const wrappedLeft = jelly.x < -jelly.size * 2;
          const wrappedRight = jelly.x > dimensions.width + jelly.size * 2;
          const wrappedTop = jelly.y < -jelly.size * 2;
          const wrappedBottom = jelly.y > dimensions.height + jelly.size * 2;

          if (wrappedLeft || wrappedRight || wrappedTop || wrappedBottom) {
            // Remove jellyfish and schedule a new spawn with delay
            this.state.marineLife.splice(i, 1);
            this.scheduleMarineLifeSpawn("jellyfish");
          }
          break;
        }
      }
    }
  }

  private updateParticles(
    dimensions: Dimensions,
    frameMultiplier: number = 1.0
  ): void {
    for (let i = this.state.particles.length - 1; i >= 0; i--) {
      const particle = this.state.particles[i];

      if (!particle) continue;

      // Update position
      particle.x += particle.vx * frameMultiplier;
      particle.y += particle.vy * frameMultiplier;
      particle.life += frameMultiplier;

      // Update opacity based on life
      particle.opacity = Math.max(0, 1 - particle.life / particle.maxLife);

      // Remove if dead and create new one
      if (particle.life >= particle.maxLife || particle.y < -10) {
        this.state.particles[i] = this.createParticle(dimensions);
      }
    }
  }

  private updateLightRays(frameMultiplier: number = 1.0): void {
    const motionScale = this.accessibility.reducedMotion ? 0.3 : 1;
    this.state.lightRays.forEach((ray) => {
      ray.phase += ray.speed * frameMultiplier;
      const sway = Math.sin(ray.phase * 0.65) * motionScale;
      ray.opacity = 0.05 + Math.sin(ray.phase) * 0.05 * motionScale;
      ray.angle = -4 + sway * 8;
    });
  }

  private scheduleMarineLifeSpawn(type: MarineLifeType): void {
    // Schedule spawn with 3-5 second delay
    const delaySeconds = 3 + Math.random() * 2;
    const spawnTime = this.animationTime + delaySeconds;
    this.state.pendingSpawns.push({ type, spawnTime });
  }

  private processPendingSpawns(dimensions: Dimensions): void {
    // Check if any pending spawns are ready
    for (let i = this.state.pendingSpawns.length - 1; i >= 0; i--) {
      const spawn = this.state.pendingSpawns[i];
      if (!spawn) continue;
      if (this.animationTime >= spawn.spawnTime) {
        // Spawn the marine life
        if (spawn.type === "fish") {
          this.state.marineLife.push(this.createFish(dimensions));
        } else if (spawn.type === "jellyfish") {
          this.state.marineLife.push(this.createJellyfish(dimensions));
        }
        // Remove from pending spawns
        this.state.pendingSpawns.splice(i, 1);
      }
    }
  }

  draw(ctx: CanvasRenderingContext2D, dimensions: Dimensions): void {
    // Clear canvas
    ctx.clearRect(0, 0, dimensions.width, dimensions.height);

    // Draw ocean gradient background
    this.drawOceanGradient(ctx, dimensions);

    // Draw light rays
    this.drawLightRays(ctx, dimensions);

    // Draw particles (background layer)
    this.drawParticles(ctx, 0.3);

    // Draw marine life
    this.drawMarineLife(ctx);

    // Draw bubbles
    this.drawBubbles(ctx);

    // Draw particles (foreground layer)
    this.drawParticles(ctx, 1.0);
  }

  private drawOceanGradient(
    ctx: CanvasRenderingContext2D,
    dimensions: Dimensions
  ): void {
    const gradient = ctx.createLinearGradient(0, 0, 0, dimensions.height);
    gradient.addColorStop(0, this.state.currentGradient.top); // #0d2d47 - Rich ocean blue
    gradient.addColorStop(0.25, "#0f3854"); // Medium-light ocean
    gradient.addColorStop(0.5, "#0b2a42"); // Mid ocean
    gradient.addColorStop(0.75, "#0a2136"); // Deeper ocean
    gradient.addColorStop(1, this.state.currentGradient.bottom); // #091a2b - Ocean depth

    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, dimensions.width, dimensions.height);
  }

  private drawLightRays(
    ctx: CanvasRenderingContext2D,
    dimensions: Dimensions
  ): void {
    if (this.quality === "minimal") return;

    ctx.save();
    this.state.lightRays.forEach((ray) => {
      const gradient = ctx.createLinearGradient(
        ray.x,
        0,
        ray.x + Math.sin((ray.angle * Math.PI) / 180) * dimensions.height,
        dimensions.height
      );

      // Gentle, visible light rays filtering through water
      gradient.addColorStop(0, `rgba(100, 150, 180, ${ray.opacity})`);
      gradient.addColorStop(0.3, `rgba(70, 120, 150, ${ray.opacity * 0.6})`);
      gradient.addColorStop(1, "rgba(40, 90, 120, 0)");

      ctx.fillStyle = gradient;
      ctx.fillRect(ray.x - ray.width / 2, 0, ray.width, dimensions.height);
    });
    ctx.restore();
  }

  private drawBubbles(ctx: CanvasRenderingContext2D): void {
    ctx.save();
    this.state.bubbles.forEach((bubble) => {
      ctx.globalAlpha = bubble.opacity;
      ctx.beginPath();
      ctx.arc(bubble.x, bubble.y, bubble.radius, 0, Math.PI * 2);
      // Nice visible bubbles
      ctx.fillStyle = `rgba(200, 230, 245, 0.5)`;
      ctx.fill();

      // Bright highlight
      ctx.beginPath();
      ctx.arc(
        bubble.x - bubble.radius * 0.3,
        bubble.y - bubble.radius * 0.3,
        bubble.radius * 0.3,
        0,
        Math.PI * 2
      );
      ctx.fillStyle = `rgba(240, 250, 255, 0.7)`;
      ctx.fill();
    });
    ctx.restore();
  }

  private drawMarineLife(ctx: CanvasRenderingContext2D): void {
    ctx.save();
    this.state.marineLife.forEach((marine) => {
      ctx.save();
      ctx.globalAlpha = marine.opacity;

      if (marine.type === "fish") {
        this.drawFish(ctx, marine);
      } else {
        this.drawJellyfish(ctx, marine);
      }

      ctx.restore();
    });
    ctx.restore();
  }

  private drawFish(ctx: CanvasRenderingContext2D, fish: FishMarineLife): void {
    ctx.translate(fish.x, fish.y);
    ctx.scale(fish.direction, 1);

    const cacheEntry = this.fishSpriteCache.get(fish.sprite.path);
    const image = cacheEntry?.image ?? fish.image;
    const ready = cacheEntry?.ready ?? image?.complete ?? false;

    if (image && ready) {
      ctx.drawImage(
        image,
        -fish.width / 2,
        -fish.height / 2,
        fish.width,
        fish.height
      );
    } else {
      // Fallback simple shape while sprite loads
      const fallbackWidth = fish.width;
      const fallbackHeight = fish.height * 0.6;
      ctx.fillStyle = "#7dd3fc";
      ctx.beginPath();
      ctx.ellipse(
        0,
        0,
        fallbackWidth * 0.5,
        fallbackHeight * 0.5,
        0,
        0,
        Math.PI * 2
      );
      ctx.fill();
      ctx.beginPath();
      ctx.moveTo(fallbackWidth * 0.5, 0);
      ctx.lineTo(fallbackWidth * 0.65, -fallbackHeight * 0.25);
      ctx.lineTo(fallbackWidth * 0.65, fallbackHeight * 0.25);
      ctx.closePath();
      ctx.fill();
    }
  }

  private drawJellyfish(
    ctx: CanvasRenderingContext2D,
    jelly: JellyfishMarineLife
  ): void {
    ctx.translate(jelly.x, jelly.y);

    const radius = jelly.size * 0.55;
    const pulse = 1 + Math.sin(jelly.animationPhase * 0.9) * 0.05;
    const bellRadius = radius * pulse;

    const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, bellRadius);
    gradient.addColorStop(0, this.lightenColor(jelly.color, 0.4));
    gradient.addColorStop(0.65, jelly.color);
    gradient.addColorStop(1, this.darkenColor(jelly.color, 0.25));

    ctx.shadowColor = this.lightenColor(jelly.color, 0.5);
    ctx.shadowBlur = jelly.glowIntensity * 35;
    ctx.beginPath();
    ctx.fillStyle = gradient;
    ctx.arc(0, 0, bellRadius, 0, Math.PI * 2);
    ctx.fill();
    ctx.shadowBlur = 0;

    const tentacleOriginY = bellRadius * 0.4;
    ctx.lineWidth = Math.max(1.4, jelly.size * 0.08);
    ctx.lineCap = "round";
    ctx.strokeStyle = this.lightenColor(jelly.color, 0.2);

    jelly.tentacleSeeds.forEach((seed, index) => {
      const offset =
        jelly.tentacleSeeds.length === 1
          ? 0
          : index / (jelly.tentacleSeeds.length - 1) - 0.5;
      const sway =
        Math.sin(jelly.animationPhase + seed) * jelly.waveAmplitude * 0.6;
      const startX = offset * bellRadius * 0.9;
      const length =
        jelly.size * (1.2 + Math.sin(jelly.animationPhase + seed) * 0.15);

      ctx.beginPath();
      ctx.moveTo(startX, tentacleOriginY);
      ctx.bezierCurveTo(
        startX + sway * 0.4,
        tentacleOriginY + length * 0.35,
        startX - sway * 0.25,
        tentacleOriginY + length * 0.7,
        startX + sway,
        tentacleOriginY + length
      );
      ctx.stroke();
    });
  }

  private drawParticles(
    ctx: CanvasRenderingContext2D,
    layerOpacity: number
  ): void {
    ctx.save();
    this.state.particles.forEach((particle) => {
      if (
        (layerOpacity > 0.5 && particle.size > 2) ||
        (layerOpacity <= 0.5 && particle.size <= 2)
      ) {
        ctx.globalAlpha = particle.opacity * layerOpacity;
        ctx.fillStyle = particle.color;
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fill();
      }
    });
    ctx.restore();
  }

  private lightenColor(color: string, amount: number): string {
    return this.adjustColor(color, Math.abs(amount));
  }

  private darkenColor(color: string, amount: number): string {
    return this.adjustColor(color, -Math.abs(amount));
  }

  private adjustColor(color: string, amount: number): string {
    let hex = color.replace("#", "");
    if (hex.length === 3) {
      hex = hex
        .split("")
        .map((c) => c + c)
        .join("");
    }

    const num = parseInt(hex, 16);
    let r = (num >> 16) & 0xff;
    let g = (num >> 8) & 0xff;
    let b = num & 0xff;

    const delta = Math.round(255 * amount);
    r = Math.min(255, Math.max(0, r + delta));
    g = Math.min(255, Math.max(0, g + delta));
    b = Math.min(255, Math.max(0, b + delta));

    return (
      "#" +
      [r, g, b].map((channel) => channel.toString(16).padStart(2, "0")).join("")
    );
  }

  setQuality(quality: QualityLevel): void {
    this.quality = quality;
    // Reinitialize with new quality settings if needed
  }

  setAccessibility(settings: AccessibilitySettings): void {
    this.accessibility = { ...this.accessibility, ...settings };
  }

  cleanup(): void {
    this.state.bubbles = [];
    this.state.marineLife = [];
    this.state.particles = [];
    this.state.lightRays = [];
    this.state.pendingSpawns = [];
  }

  getMetrics(): PerformanceMetrics {
    const totalParticles =
      this.state.bubbles.length +
      this.state.marineLife.length +
      this.state.particles.length;

    return {
      fps: 60, // Estimated
      warnings:
        totalParticles > 100
          ? ["High particle count may impact performance"]
          : [],
      particleCount: totalParticles,
      renderTime: 16, // Estimated 16ms for 60fps
    };
  }
}

import { injectable } from "inversify";
import type { FishSprite } from "../../domain/models/DeepOceanModels";
import type { IFishSpriteManager } from "../contracts";

@injectable()
export class FishSpriteManager implements IFishSpriteManager {
  private readonly fishSprites: FishSprite[] = [
    { name: "Blue", path: "/assets/background/fish/kenney/fish_blue.png" },
    { name: "Orange", path: "/assets/background/fish/kenney/fish_orange.png" },
    { name: "Green", path: "/assets/background/fish/kenney/fish_green.png" },
    {
      name: "Grey Long",
      path: "/assets/background/fish/kenney/fish_grey_long_a.png",
    },
  ];

  private fishSpriteCache = new Map<
    string,
    { sprite: FishSprite; image: HTMLImageElement; ready: boolean }
  >();

  preloadSprites(): Promise<void> {
    if (typeof window === "undefined") {
      return Promise.resolve();
    }

    const loadPromises = this.fishSprites.map((sprite) => {
      if (this.fishSpriteCache.has(sprite.path)) {
        return Promise.resolve();
      }

      return new Promise<void>((resolve, reject) => {
        const image = new Image();
        // CRITICAL: Create cache entry with ready based on image.complete (matching monolith)
        const cacheEntry = { sprite, image, ready: image.complete };

        image.onload = () => {
          // Overwrite cache entry with ready=true (matching monolith)
          this.fishSpriteCache.set(sprite.path, {
            sprite,
            image,
            ready: true,
          });
          resolve();
        };

        image.onerror = (error) => {
          console.warn(`Failed to load fish sprite: ${sprite.path}`);
          this.fishSpriteCache.delete(sprite.path);
          reject(error);
        };

        // Set src BEFORE setting cache entry (images might load synchronously from browser cache!)
        image.src = sprite.path;

        // Set cache entry AFTER src (matching monolith pattern)
        this.fishSpriteCache.set(sprite.path, cacheEntry);
      });
    });

    return Promise.all(loadPromises).then(() => {});
  }

  getRandomSpriteEntry():
    | { sprite: FishSprite; image?: HTMLImageElement }
    | undefined {
    if (this.fishSprites.length === 0) {
      return undefined;
    }

    const sprite =
      this.fishSprites[Math.floor(Math.random() * this.fishSprites.length)];
    if (!sprite) return undefined;
    const entry = this.fishSpriteCache.get(sprite.path);

    return entry;
  }

  getAnyLoadedSpriteEntry():
    | { sprite: FishSprite; image: HTMLImageElement }
    | undefined {
    // Find any loaded sprite to use
    for (const entry of this.fishSpriteCache.values()) {
      if (entry.image && entry.image.complete && entry.ready) {
        return entry;
      }
    }
    return undefined;
  }

  getMarineLifeColor(type: "fish" | "jellyfish"): string {
    if (type === "fish") {
      // Ocean fish in nice teals and blues
      const colors = ["#3d7a8c", "#4a8fa3", "#548da0", "#4b8599"];
      return colors[Math.floor(Math.random() * 4)] ?? "#3d7a8c";
    }

    // Jellyfish in soft purples and pinks
    const colors = ["#7d5a7a", "#8b6d88", "#946f91", "#866783"];
    return colors[Math.floor(Math.random() * 4)] ?? "#7d5a7a";
  }

  isReady(): boolean {
    if (this.fishSpriteCache.size === 0) return false;

    for (const entry of this.fishSpriteCache.values()) {
      if (!entry.ready) return false;
    }

    return true;
  }
}

// src/lib/components/backgrounds/systems/nightSky/ConstellationSystem.ts
import type { AccessibilitySettings, QualityLevel, Star } from "$shared";
// Removed resolve import - calculation service now injected via constructor
import type { INightSkyCalculationService } from "../services";

export interface ConstellationConfig {
  maxLines: number;
  opacity: number;
  twinkleSpeed: number;
  enabledOnQuality: QualityLevel[];
}

interface ConstellationLine {
  a: Star;
  b: Star;
  opacity: number;
  dir: number;
}

export class ConstellationSystem {
  private constellationLines: ConstellationLine[] = [];
  private config: ConstellationConfig;
  private calculationService: INightSkyCalculationService;

  constructor(
    config: ConstellationConfig,
    calculationService: INightSkyCalculationService
  ) {
    this.config = config;
    this.calculationService = calculationService;
  }

  update(
    nearStars: Star[],
    quality: QualityLevel,
    a11y: AccessibilitySettings,
    frameMultiplier: number = 1.0
  ) {
    if (!this.config.enabledOnQuality.includes(quality)) {
      this.constellationLines = [];
      return;
    }

    if (!nearStars || nearStars.length === 0) return;

    // Initialize constellation lines if they don't exist yet
    if (this.constellationLines.length === 0 && nearStars.length > 1) {
      const numLines = Math.min(
        this.config.maxLines,
        Math.floor(nearStars.length / 2)
      );

      for (let i = 0; i < numLines; i++) {
        const aIndex = this.calculationService.randInt(0, nearStars.length - 1);
        let bIndex = this.calculationService.randInt(0, nearStars.length - 1);
        while (bIndex === aIndex) {
          // Ensure different stars
          bIndex = this.calculationService.randInt(0, nearStars.length - 1);
        }
        const starA = nearStars[aIndex];
        const starB = nearStars[bIndex];
        if (starA && starB) {
          this.constellationLines.push({
            a: starA,
            b: starB,
            opacity: Math.random() * this.config.opacity,
            dir: Math.random() > 0.5 ? 1 : -1,
          });
        }
      }
    }

    // Update twinkling with frame multiplier for consistent speed
    const effectiveSpeed = frameMultiplier * (a11y.reducedMotion ? 0.3 : 1);
    this.constellationLines.forEach((l) => {
      l.opacity += l.dir * this.config.twinkleSpeed * effectiveSpeed;
      if (l.opacity > this.config.opacity || l.opacity < 0) {
        l.dir *= -1;
        l.opacity = Math.max(0, Math.min(this.config.opacity, l.opacity));
      }
    });
  }

  draw(ctx: CanvasRenderingContext2D, a11y: AccessibilitySettings) {
    if (!this.constellationLines.length) return;

    ctx.lineWidth = 0.7;
    const baseColor = a11y.highContrast ? "#FFFFFF" : "#89A7FF";
    this.constellationLines.forEach((l) => {
      ctx.globalAlpha = l.opacity * (a11y.reducedMotion ? 0.5 : 1);
      ctx.strokeStyle = baseColor;
      ctx.beginPath();
      ctx.moveTo(l.a.x, l.a.y);
      ctx.lineTo(l.b.x, l.b.y);
      ctx.stroke();
    });
    ctx.globalAlpha = 1;
  }

  cleanup() {
    this.constellationLines = [];
  }
}

export class PerformanceTracker {
  private static instance: PerformanceTracker;
  private fps: number = 60;
  private frameCount: number = 0;
  private lastTime: number = 0;
  private particleCount: number = 0;
  private warnings: string[] = [];

  private constructor() {
    this.lastTime = performance.now();
  }

  public static getInstance(): PerformanceTracker {
    if (!PerformanceTracker.instance) {
      PerformanceTracker.instance = new PerformanceTracker();
    }
    return PerformanceTracker.instance;
  }

  public update(): void {
    const now = performance.now();
    this.frameCount++;

    if (now >= this.lastTime + 1000) {
      this.fps = this.frameCount;
      this.frameCount = 0;
      this.lastTime = now;

      if (this.fps < 30) {
        this.warnings.push(`Low FPS detected: ${this.fps}`);

        if (this.warnings.length > 5) this.warnings.shift();
      } else if (this.warnings.length > 0) {
        this.warnings = [];
      }
    }
  }

  public getPerformanceStatus(): {
    fps: number;
    particleCount: number;
    warnings: string[];
  } {
    return {
      fps: this.fps,
      particleCount: this.particleCount,
      warnings: this.warnings,
    };
  }

  public setParticleCount(count: number): void {
    this.particleCount = count;
  }

  public reset(): void {
    this.fps = 60;
    this.frameCount = 0;
    this.lastTime = performance.now();
    this.particleCount = 0;
    this.warnings = [];
  }
}

// src/lib/services/implementations/background/systems/core/ShootingStarSystem.ts

import type {
  ShootingStar,
  ShootingStarState,
} from "../../../snowfall/domain/models/snowfall-models";
import { SnowfallConfig } from "../../domain/constants/BackgroundConfigs";
import type { Dimensions } from "../../domain/types/background-types";

export const createShootingStarSystem = () => {
  const config = SnowfallConfig.shootingStar;

  function getRandomInterval(): number {
    return (
      Math.floor(
        Math.random() * (config.maxInterval - config.minInterval + 1)
      ) + config.minInterval
    );
  }

  function randomFloat(min: number, max: number): number {
    return Math.random() * (max - min) + min;
  }

  function getRandomColor(): string {
    if (!config.colors.length) return "#FFFFFF";
    const idx = Math.floor(Math.random() * config.colors.length);
    return config.colors[idx] || "#FFFFFF";
  }

  const spawnShootingStar = ({ width, height }: Dimensions): ShootingStar => {
    const middleY = height / 2;

    const startOptions = [
      { x: -0.1 * width, y: randomFloat(0.2, 0.8) * height },
      { x: 1.1 * width, y: randomFloat(0.2, 0.8) * height },
    ];
    const startPosition = startOptions[
      Math.floor(Math.random() * startOptions.length)
    ] || startOptions[0];

    let dx = randomFloat(0.3, 0.5) * (startPosition.x > 0 ? -1 : 1);
    let dy =
      Math.random() < 0.5 ? -randomFloat(0.05, 0.2) : randomFloat(0.05, 0.2);

    if (startPosition.y > middleY) dy -= randomFloat(0.02, 0.05);
    else dy += randomFloat(0.02, 0.05);

    const norm = Math.sqrt(dx * dx + dy * dy);
    dx /= norm;
    dy /= norm;

    return {
      x: startPosition.x,
      y: startPosition.y,
      dx,
      dy,
      size: randomFloat(config.minSize, config.maxSize),
      speed: randomFloat(config.minSpeed, config.maxSpeed),
      tail: [],
      prevX: startPosition.x,
      prevY: startPosition.y,
      tailLength: Math.floor(
        randomFloat(config.tailLength.min, config.tailLength.max)
      ),
      opacity: 1.0,
      offScreen: false,
      color: getRandomColor(),
      twinkle: Math.random() < 0.3, // Default twinkle chance
    };
  };

  const updateShootingStar = (
    star: ShootingStar | null,
    { width, height }: Dimensions,
    frameMultiplier: number = 1.0
  ): ShootingStar | null => {
    if (!star) return null;

    const newX = star.x + star.dx * star.speed * width * frameMultiplier;
    const newY = star.y + star.dy * star.speed * height * frameMultiplier;

    const steps = 5;
    const newTail = [...star.tail];

    for (let i = 0; i < steps; i++) {
      const interpX = star.prevX + (newX - star.prevX) * (i / steps);
      const interpY = star.prevY + (newY - star.prevY) * (i / steps);
      newTail.push({
        x: interpX,
        y: interpY,
        size: star.size * 0.8,
        color: star.color,
      });
    }

    if (newTail.length > star.tailLength * steps) {
      newTail.splice(0, steps);
    }

    const offScreen =
      newX < -width * 0.1 ||
      newX > width * 1.1 ||
      newY < -height * 0.1 ||
      newY > height * 1.1;

    let opacity = star.opacity;
    if (offScreen) {
      opacity -= 0.02; // Default fade rate
      if (opacity <= 0) return null;
    }

    return {
      ...star,
      x: newX,
      y: newY,
      prevX: star.x,
      prevY: star.y,
      tail: newTail,
      offScreen,
      opacity,
    };
  };

  const drawShootingStar = (
    star: ShootingStar | null,
    ctx: CanvasRenderingContext2D
  ): void => {
    if (!star || !ctx) return;

    ctx.save();
    const tailLength = star.tail.length;

    star.tail.forEach((segment, index) => {
      const fadeFactor = 5;
      const opacity = Math.max(
        0,
        ((index + 1) / tailLength) ** fadeFactor * star.opacity
      );

      ctx.globalAlpha = opacity;
      ctx.beginPath();
      ctx.arc(segment.x, segment.y, segment.size, 0, Math.PI * 2);
      ctx.fillStyle = segment.color;
      ctx.fill();
    });

    ctx.globalAlpha = star.opacity;
    ctx.beginPath();
    ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
    ctx.fillStyle = star.color;

    if (star.twinkle && Math.random() > 0.5) {
      ctx.shadowColor = star.color;
      ctx.shadowBlur = star.size / 2;
    }

    ctx.fill();
    ctx.restore();
  };

  const update = (
    state: ShootingStarState,
    dimensions: Dimensions,
    frameMultiplier: number = 1.0
  ): ShootingStarState => {
    const timer = state.timer + frameMultiplier;

    if (!state.star && timer >= state.interval) {
      return {
        star: spawnShootingStar(dimensions),
        timer: 0,
        interval: getRandomInterval(),
      };
    }

    return {
      star: updateShootingStar(state.star, dimensions, frameMultiplier),
      timer: state.star ? timer : 0,
      interval: state.interval,
    };
  };

  return {
    initialState: { star: null, timer: 0, interval: getRandomInterval() },
    update,
    draw: (state: ShootingStarState, ctx: CanvasRenderingContext2D): void => {
      if (state.star && ctx) drawShootingStar(state.star, ctx);
    },
  };
};

import { SnowfallConfig } from "../../shared/domain/constants/BackgroundConfigs";
import type { Dimensions } from "../../shared/domain/types/background-types";
import type { Snowflake } from "../domain/models/snowfall-models";

export const createSnowflakeSystem = () => {
  const config = SnowfallConfig;
  let windStrength = 0;
  let windChangeTimer = 0;

  const generateSnowflakeShape = (size: number): Path2D => {
    const path = new Path2D();
    const branches = 6; // Classic 6-pointed snowflake
    const complexity = Math.random() > 0.2 ? 2 : 1; // 80% chance of detailed snowflakes (was 50%)

    // Use consistent length for all branches of this snowflake (determined once per snowflake)
    const branchLength = size * (0.8 + Math.random() * 0.4);

    for (let i = 0; i < branches; i++) {
      const angle = (i * Math.PI * 2) / branches;

      // Main branch - all branches use the same length for perfect symmetry
      const endX = Math.cos(angle) * branchLength;
      const endY = Math.sin(angle) * branchLength;

      path.moveTo(0, 0);
      path.lineTo(endX, endY);

      // Add delicate side branches for more complex snowflakes
      if (complexity > 1) {
        for (let j = 1; j <= 2; j++) {
          const branchPos = j / 3;
          const branchX = Math.cos(angle) * branchLength * branchPos;
          const branchY = Math.sin(angle) * branchLength * branchPos;

          // Left side branch - consistent length for symmetry
          const leftAngle = angle - Math.PI / 4;
          const leftLength = size * 0.3 * (1 - branchPos);
          path.moveTo(branchX, branchY);
          path.lineTo(
            branchX + Math.cos(leftAngle) * leftLength,
            branchY + Math.sin(leftAngle) * leftLength
          );

          // Right side branch - consistent length for symmetry
          const rightAngle = angle + Math.PI / 4;
          path.moveTo(branchX, branchY);
          path.lineTo(
            branchX + Math.cos(rightAngle) * leftLength,
            branchY + Math.sin(rightAngle) * leftLength
          );
        }
      }
    }

    return path;
  };

  const randomSnowflakeColor = (): string => {
    const colors = config.snowflake.colors;
    if (!colors.length) return "#FFFFFF";
    return colors[Math.floor(Math.random() * colors.length)] || "#FFFFFF";
  };

  const createSnowflake = (width: number, height: number): Snowflake => {
    const size =
      Math.random() * (config.snowflake.maxSize - config.snowflake.minSize) +
      config.snowflake.minSize;
    const depth = Math.random(); // Depth for layering effects

    return {
      x: Math.random() * width,
      y: Math.random() * height,
      speed:
        (Math.random() *
          (config.snowflake.maxSpeed - config.snowflake.minSpeed) +
          config.snowflake.minSpeed) *
        (0.5 + depth * 0.5), // Vary speed by depth
      size: size * (0.4 + depth * 0.6), // Smaller flakes appear further away
      sway: (Math.random() * 1 - 0.5) * (1 + depth),
      opacity: (Math.random() * 0.6 + 0.3) * (0.6 + depth * 0.4),
      shape: generateSnowflakeShape(size),
      color: randomSnowflakeColor(),
      rotation: Math.random() * Math.PI * 2,
      rotationSpeed: (Math.random() - 0.5) * 0.02, // Gentle rotation
      sparkle: Math.random() > 0.7 ? Math.random() : 0, // Only some sparkle
      sparklePhase: Math.random() * Math.PI * 2,
      depth,
    };
  };

  const initialize = (
    { width, height }: Dimensions,
    quality: string
  ): Snowflake[] => {
    let adjustedDensity = config.snowflake.density;

    const screenSizeFactor = Math.min(1, (width * height) / (1920 * 1080));
    adjustedDensity *= screenSizeFactor;

    // Apply quality density adjustments
    if (quality === "low") {
      adjustedDensity *= 0.5;
    } else if (quality === "medium") {
      adjustedDensity *= 0.75;
    }

    const count = Math.floor(width * height * adjustedDensity);
    return Array.from({ length: count }, () => createSnowflake(width, height));
  };

  const update = (
    flakes: Snowflake[],
    { width, height }: Dimensions,
    frameMultiplier: number = 1.0
  ): Snowflake[] => {
    windChangeTimer += frameMultiplier;
    if (windChangeTimer >= config.snowflake.windChangeInterval) {
      windChangeTimer = 0;
      // Very gentle wind - much softer movement
      windStrength = (Math.random() * 0.08 - 0.04) * width * 0.000008;
    }

    return flakes.map((flake) => {
      // Enhanced movement with gentle curves
      const swayOffset = Math.sin(flake.y * 0.01 + flake.sparklePhase) * 0.5;
      const newX =
        flake.x + (flake.sway + windStrength + swayOffset) * frameMultiplier;
      const newY = flake.y + flake.speed * frameMultiplier;

      // Update rotation for gentle spinning
      const newRotation =
        flake.rotation + flake.rotationSpeed * frameMultiplier;

      // Update sparkle animation
      const newSparklePhase = flake.sparklePhase + 0.05 * frameMultiplier;

      if (newY > height) {
        return {
          ...flake,
          y: Math.random() * -20 - 10,
          x: Math.random() * width,
          rotation: newRotation,
          sparklePhase: newSparklePhase,
        };
      }

      if (newX > width || newX < 0) {
        return {
          ...flake,
          x: Math.random() * width,
          rotation: newRotation,
          sparklePhase: newSparklePhase,
        };
      }

      return {
        ...flake,
        x: newX,
        y: newY,
        rotation: newRotation,
        sparklePhase: newSparklePhase,
      };
    });
  };

  const draw = (
    flakes: Snowflake[],
    ctx: CanvasRenderingContext2D,
    _dimensions: Dimensions
  ): void => {
    if (!ctx) return;

    // Sort flakes by depth for proper layering (back to front)
    const sortedFlakes = [...flakes].sort((a, b) => a.depth - b.depth);

    sortedFlakes.forEach((flake) => {
      ctx.save();
      ctx.translate(flake.x, flake.y);
      ctx.rotate(flake.rotation);

      // Calculate sparkle intensity with smoother animation
      const sparkleIntensity =
        flake.sparkle > 0
          ? flake.sparkle * (0.6 + 0.4 * Math.sin(flake.sparklePhase))
          : 0;

      // Enhanced depth-based effects
      const depthFactor = 0.3 + flake.depth * 0.7;
      const baseOpacity = flake.opacity * depthFactor;
      const bloomRadius = flake.size * (1 + sparkleIntensity);

      // Multi-layer glow effect for magical appearance
      if (sparkleIntensity > 0.05) {
        // Outer soft glow
        ctx.globalAlpha = sparkleIntensity * 0.15;
        ctx.fillStyle = "#ffffff";
        ctx.shadowColor = "#b3d9ff";
        ctx.shadowBlur = bloomRadius * 3;
        ctx.fill(flake.shape);

        // Inner bright glow
        ctx.globalAlpha = sparkleIntensity * 0.25;
        ctx.shadowColor = "#ffffff";
        ctx.shadowBlur = bloomRadius * 1.5;
        ctx.fill(flake.shape);

        ctx.shadowBlur = 0;
      }

      // Main snowflake with enhanced colors
      ctx.globalAlpha = baseOpacity + sparkleIntensity * 0.3;
      ctx.fillStyle = flake.color;
      ctx.strokeStyle = flake.color;
      ctx.lineWidth = 0.3 + depthFactor * 0.4;

      // Enhanced rendering with both fill and stroke
      ctx.fill(flake.shape);
      ctx.stroke(flake.shape);

      // Crystalline sparkle highlights
      if (sparkleIntensity > 0.3) {
        ctx.globalAlpha = sparkleIntensity * 0.9;

        // Central bright spot
        ctx.fillStyle = "#ffffff";
        ctx.beginPath();
        ctx.arc(0, 0, flake.size * 0.15, 0, Math.PI * 2);
        ctx.fill();

        // Cross sparkle effect
        const sparkleSize = flake.size * 0.4;
        ctx.strokeStyle = "#f8faff";
        ctx.lineWidth = 1.5;
        ctx.beginPath();
        ctx.moveTo(-sparkleSize, 0);
        ctx.lineTo(sparkleSize, 0);
        ctx.moveTo(0, -sparkleSize);
        ctx.lineTo(0, sparkleSize);
        ctx.stroke();
      }

      // Subtle edge glow for depth
      if (flake.depth > 0.7) {
        ctx.globalAlpha = 0.1;
        ctx.strokeStyle = "#e6f3ff";
        ctx.lineWidth = 2;
        ctx.stroke(flake.shape);
      }

      ctx.restore();
    });
  };

  const adjustToResize = (
    flakes: Snowflake[],
    _oldDimensions: Dimensions,
    newDimensions: Dimensions,
    quality: string
  ): Snowflake[] => {
    const densityMultiplier =
      quality === "low" ? 0.4 : quality === "medium" ? 0.7 : 1;
    const targetCount = Math.floor(
      newDimensions.width *
        newDimensions.height *
        config.snowflake.density *
        densityMultiplier
    );

    const currentCount = flakes.length;

    if (targetCount > currentCount) {
      return [
        ...flakes,
        ...Array.from({ length: targetCount - currentCount }, () =>
          createSnowflake(newDimensions.width, newDimensions.height)
        ),
      ];
    } else if (targetCount < currentCount) {
      return flakes.slice(0, targetCount);
    }

    return flakes;
  };

  const setQuality = (_quality: string): void => {
    // future: adjust density dynamically
  };

  return {
    initialize,
    update,
    draw,
    adjustToResize,
    setQuality,
  };
};

import { TYPES } from "$shared/inversify/types";
import { inject, injectable } from "inversify";
import { createComponentLogger } from "../../../utils/debug-logger";
import { DeviceType } from "../../domain";
import type {
  DeviceCapabilities,
  ResponsiveSettings,
} from "../../domain/models/device-models";
import type { IDeviceDetector } from "../contracts/IDeviceDetector";
import type { IViewportService } from "../contracts/IViewportService";
/**
 * Device Detector Implementation
 *
 * Detects device capabilities and characteristics using browser APIs.
 */
@injectable()
export class DeviceDetector implements IDeviceDetector {
  private logger = createComponentLogger("DeviceDetector");
  private capabilitiesCallbacks: ((caps: DeviceCapabilities) => void)[] = [];
  private _cachedDeviceType: DeviceType | null = null;
  private _lastViewportWidth: number = 0;
  private _lastViewportHeight: number = 0;

  constructor(
    @inject(TYPES.IViewportService) private viewportService: IViewportService
  ) {
    // Subscribe to viewport changes to make device detection reactive
    this.viewportService.onViewportChange(() => {
      // Clear cached device type to force recalculation
      this._cachedDeviceType = null;

      // Get fresh capabilities with new viewport dimensions
      const newCapabilities = this.getCapabilities();

      // Notify all registered callbacks about the change
      this.capabilitiesCallbacks.forEach((callback) => {
        callback(newCapabilities);
      });

      this.logger.log("Viewport changed, notified callbacks", {
        width: this.viewportService.width,
        height: this.viewportService.height,
        callbackCount: this.capabilitiesCallbacks.length,
      });
    });
  }

  detectDeviceType(): DeviceType {
    const viewportWidth = this.viewportService.width;
    const viewportHeight = this.viewportService.height;

    // Check if we can use cached result
    if (
      this._cachedDeviceType !== null &&
      this._lastViewportWidth === viewportWidth &&
      this._lastViewportHeight === viewportHeight
    ) {
      return this._cachedDeviceType;
    }

    // Calculate device type
    const hasTouch = "ontouchstart" in window;
    let deviceType: DeviceType;

    // Mobile detection (for testing, use viewport width instead of screen width)
    if (viewportWidth < 768) {
      deviceType = DeviceType.MOBILE;
    }
    // Tablet detection
    else if (hasTouch && viewportWidth >= 768 && viewportWidth < 1024) {
      deviceType = DeviceType.TABLET;
    }
    // Desktop detection
    else {
      deviceType = DeviceType.DESKTOP;
    }

    // Cache the result
    this._cachedDeviceType = deviceType;
    this._lastViewportWidth = viewportWidth;
    this._lastViewportHeight = viewportHeight;

    this.logger.log(`Detected device type: ${deviceType}`);
    return deviceType;
  }

  isTouchDevice(): boolean {
    return "ontouchstart" in window || navigator.maxTouchPoints > 0;
  }

  isMobile(): boolean {
    return this.detectDeviceType() === DeviceType.MOBILE;
  }

  isTablet(): boolean {
    return this.detectDeviceType() === DeviceType.TABLET;
  }

  isDesktop(): boolean {
    return this.detectDeviceType() === DeviceType.DESKTOP;
  }

  /**
   * Check if device is in landscape mobile mode
   * Detects phone-like landscape viewports that should use side navigation
   *
   * Criteria for landscape mobile navigation:
   * - Currently in landscape orientation (width > height)
   * - Very wide aspect ratio (> 1.7:1) indicating phone-like proportions
   * - Low height (<= 600px) indicating phone/small tablet, not desktop
   *
   * This works for:
   * - Actual mobile phones rotated sideways (e.g., 844x390, 932x430)
   * - Small tablets in landscape (e.g., 1024x600)
   * - Desktop windows resized to phone-like proportions (for testing)
   *
   * This excludes (all use bottom navigation):
   * - Large tablets in landscape (e.g., 1024x768)
   * - Large foldables (e.g., 2208x1768)
   * - Desktop displays
   *
   * Note: This is intentionally independent of touch detection to allow desktop testing
   */
  isLandscapeMobile(): boolean {
    const viewportWidth = this.viewportService.width;
    const viewportHeight = this.viewportService.height;
    const aspectRatio = this.viewportService.getAspectRatio();

    // Check if in landscape orientation
    const isLandscape = this.viewportService.isLandscape();

    // Phone landscape criteria - wider aspect ratio threshold to include iPhone 6/7/8 landscape (1.78:1)
    const isWideAspectRatio = aspectRatio > 1.7; // Includes most phone landscape orientations
    const isLowHeight = viewportHeight <= 600; // Phone and small tablet height (increased from 500)

    // Only phones and small tablets in landscape should use side navigation
    const result = isLandscape && isWideAspectRatio && isLowHeight;

    if (result) {
      this.logger.log(`Landscape mobile layout active (phone-like):`, {
        viewportWidth,
        viewportHeight,
        aspectRatio: aspectRatio.toFixed(2),
        isWideAspectRatio,
        isLowHeight,
        reason: "Phone-like proportions in landscape orientation",
      });
    }

    return result;
  }

  /**
   * Check if device is in portrait mobile mode
   * Detects narrow portrait viewports that should use horizontal navigation
   *
   * Criteria for portrait mobile navigation:
   * - Currently in portrait orientation (height > width)
   * - Viewport width is relatively narrow (< 600px typical phone width)
   * - This ensures navigation gets maximum horizontal space
   *
   * This works for:
   * - Phones held upright (portrait mode)
   * - Desktop windows resized narrow for testing
   * - Foldable phones on front screen in portrait
   */
  isPortraitMobile(): boolean {
    const viewportWidth = this.viewportService.width;
    const viewportHeight = this.viewportService.height;

    // Check if in portrait orientation
    const isPortrait = this.viewportService.isPortrait();

    // Check if width is narrow (typical phone portrait width)
    const hasNarrowWidth = viewportWidth < 600;

    // Combine conditions
    const result = isPortrait && hasNarrowWidth;

    if (result) {
      this.logger.log(`Portrait mobile layout active:`, {
        viewportWidth,
        viewportHeight,
        reason: "Narrow width in portrait - using horizontal navigation",
      });
    }

    return result;
  }

  getScreenInfo() {
    return {
      width: window.screen.width,
      height: window.screen.height,
      pixelRatio: window.devicePixelRatio || 1,
    };
  }

  supportsFoldable(): boolean {
    // Basic check for foldable device features
    // This would need to be expanded with actual foldable detection logic
    return "screen" in window && "orientation" in window.screen;
  }

  getCapabilities(): DeviceCapabilities {
    const screenInfo = this.getScreenInfo();
    const deviceType = this.detectDeviceType();
    const hasTouch = this.isTouchDevice();

    return {
      primaryInput: hasTouch ? "touch" : "mouse",
      screenSize: this.getScreenSizeCategory(deviceType),
      hasTouch,
      hasPrecisePointer: !hasTouch,
      hasKeyboard: !this.isMobile(),
      viewport: {
        width: screenInfo.width,
        height: screenInfo.height,
      },
      pixelRatio: screenInfo.pixelRatio,
      colorDepth: window.screen.colorDepth || 24,
      supportsHDR: false, // Basic implementation
      hardwareConcurrency: navigator.hardwareConcurrency || 4,
    };
  }

  /**
   * Get navigation layout immediately without caching
   * This ensures navigation layout responds instantly to viewport changes
   *
   * ORIENTATION-BASED NAVIGATION (2026 Best Practice)
   * ==================================================
   * Navigation placement optimizes for screen real estate based on orientation:
   * - Portrait: Bottom nav (preserves precious vertical space)
   * - Landscape: Left nav (preserves precious horizontal space)
   * - Desktop: Top nav (traditional web convention)
   *
   * Layout Types:
   * - "top": Desktop-class devices (≥1024px) - traditional web convention
   * - "left": Landscape touch devices - maximizes vertical space
   * - "bottom": Portrait touch devices - maximizes horizontal space, thumb-optimized
   *
   * Decision Tree:
   * 1. Desktop (≥1024px OR non-touch) → Top nav
   * 2. Portrait orientation + touch → Bottom nav
   * 3. Landscape orientation + touch → Left nav
   *
   * Rationale:
   * - Simpler logic based on natural screen usage patterns
   * - Orientation determines the constraint (vertical vs horizontal space)
   * - Works perfectly for all device types including foldables
   */
  getNavigationLayoutImmediate(): "top" | "left" | "bottom" {
    const viewportWidth = this.viewportService.width;
    const viewportHeight = this.viewportService.height;
    const deviceType = this.detectDeviceType();
    const hasTouch = this.isTouchDevice();
    const isPortrait = viewportHeight > viewportWidth;
    const isLandscape = viewportWidth > viewportHeight;

    // ============================================================================
    // RULE 1: Desktop-Class Devices → Top Navigation
    // ============================================================================
    // Large displays (≥1024px) or non-touch devices use traditional top navigation.
    // Rationale:
    // - Desktop mental model: Users expect navigation at top on large screens
    // - Professional web app appearance
    // - Mouse/trackpad optimized: Easy to target at top of screen
    // - Ample screen real estate in both dimensions
    // Covers:
    // - Desktop monitors and laptops (≥1024px)
    // - Large tablets in landscape (iPad Pro 12.9" at 1366px, iPad 10.9" at 1180px)
    // - Non-touch displays
    if (deviceType === DeviceType.DESKTOP || !hasTouch) {
      this.logger.log(`Navigation layout: top (desktop-class)`, {
        deviceType,
        viewportWidth,
        viewportHeight,
        hasTouch,
        reason:
          deviceType === DeviceType.DESKTOP
            ? "Desktop-class device (≥1024px) - web convention"
            : "Non-touch device - mouse-optimized placement",
      });
      return "top";
    }

    // ============================================================================
    // RULE 2: Portrait Orientation → Bottom Navigation
    // ============================================================================
    // All touch devices in portrait use bottom navigation.
    // Rationale:
    // - Vertical space is the constraint in portrait
    // - Bottom nav uses minimal vertical space (50-70px)
    // - Thumb-optimized: Easy to reach at bottom of screen
    // - Industry standard: iOS HIG, Material Design 3
    // Covers:
    // - Phones in portrait (e.g., iPhone 393×851, Pixel 412×915)
    // - Tablets in portrait (e.g., iPad 834×1194, Galaxy Tab 800×1280)
    // - Foldables in portrait (e.g., Z Fold unfolded 619×720)
    // - Foldable cover screens in portrait (e.g., Z Fold cover 344×884)
    if (isPortrait && hasTouch) {
      this.logger.log(`Navigation layout: bottom (portrait orientation)`, {
        deviceType,
        viewportWidth,
        viewportHeight,
        orientation: "portrait",
        hasTouch,
        reason:
          "Portrait orientation - preserving vertical space, thumb-optimized",
      });
      return "bottom";
    }

    // ============================================================================
    // RULE 3: Landscape Orientation → Left Navigation
    // ============================================================================
    // All touch devices in landscape use left navigation rail.
    // Rationale:
    // - Horizontal space is the constraint in landscape
    // - Left nav uses minimal horizontal space (60-72px)
    // - Preserves maximum vertical space for content
    // - Consistent with Material Design 3 navigation rail pattern
    // Covers:
    // - Phones in landscape (e.g., iPhone 851×393, Pixel 915×412)
    // - Foldable cover screens in landscape (e.g., Z Fold cover 884×344)
    // - Foldables unfolded in landscape (e.g., Z Fold 720×619)
    // - Small tablets in landscape (< 1024px width)
    if (isLandscape && hasTouch) {
      this.logger.log(`Navigation layout: left (landscape orientation)`, {
        deviceType,
        viewportWidth,
        viewportHeight,
        orientation: "landscape",
        hasTouch,
        reason:
          "Landscape orientation - preserving vertical space with navigation rail",
      });
      return "left";
    }

    // ============================================================================
    // FALLBACK: Default to Top Navigation
    // ============================================================================
    // Safety fallback for any edge cases not caught by above rules.
    this.logger.log(`Navigation layout: top (fallback)`, {
      deviceType,
      viewportWidth,
      viewportHeight,
      hasTouch,
      reason: "Fallback to top navigation",
    });
    return "top";
  }

  getResponsiveSettings(): ResponsiveSettings {
    const capabilities = this.getCapabilities();
    const isMobile = this.isMobile();
    const isTablet = this.isTablet();
    const isDesktop = this.isDesktop();
    const isLandscapeMobile = this.isLandscapeMobile();

    // Use immediate navigation layout detection to avoid timing issues
    const navigationLayout = this.getNavigationLayoutImmediate();

    // Enhanced dual-density spacing for better desktop optimization
    // Mobile: 4px (optimized for touch, user-confirmed perfect)
    // Tablet: 8px (balanced middle ground)
    // Desktop: 10px base (aggressively reduced from 16px for compact desktop layout)
    // Note: CSS media queries in components will further compact (×0.6, ×0.5, ×0.4)
    const elementSpacing = isMobile ? 4 : isTablet ? 8 : 10;

    return {
      minTouchTarget: capabilities.hasTouch ? 44 : 32,
      elementSpacing,
      allowScrolling: true,
      layoutDensity: isMobile
        ? "compact"
        : isTablet
          ? "comfortable"
          : "spacious",
      fontScaling: isMobile ? 1.1 : 1.0,
      isMobile,
      isTablet,
      isDesktop,
      screenWidth: capabilities.viewport.width,
      screenHeight: capabilities.viewport.height,
      devicePixelRatio: capabilities.pixelRatio,
      touchSupported: capabilities.hasTouch,
      orientation:
        capabilities.viewport.width > capabilities.viewport.height
          ? "landscape"
          : "portrait",
      navigationLayout,
      isLandscapeMobile,
    };
  }

  onCapabilitiesChanged(
    callback: (caps: DeviceCapabilities) => void
  ): () => void {
    this.capabilitiesCallbacks.push(callback);

    // Return cleanup function
    return () => {
      const index = this.capabilitiesCallbacks.indexOf(callback);
      if (index > -1) {
        this.capabilitiesCallbacks.splice(index, 1);
      }
    };
  }

  private getScreenSizeCategory(
    deviceType: DeviceType
  ): "mobile" | "tablet" | "desktop" | "largeDesktop" {
    switch (deviceType) {
      case DeviceType.MOBILE:
        return "mobile";
      case DeviceType.TABLET:
        return "tablet";
      case DeviceType.DESKTOP:
        return window.screen.width > 1440 ? "largeDesktop" : "desktop";
      default:
        return "desktop";
    }
  }
}

/**
 * Enhanced foldable device detection utility
 * Provides comprehensive detection for foldable devices with a focus on Samsung Z Fold series
 */

import {
  DEBUG_MODE,
  FOLDABLE_DEVICE_SPECS,
} from "../domain/constants/device-constants";
import type { FoldableDetectionResult } from "../domain/models/foldable-models";

// --- Main Detection Function ---
/**
 * Detects if the current device is likely foldable and its state.
 * Prioritizes manual overrides, then device spec matching, then APIs,
 * then User Agent checks, and finally a dimension heuristic as a fallback.
 * @returns {FoldableDetectionResult} Object containing detection results.
 */
export function detectFoldableDevice(): FoldableDetectionResult {
  // Check for manual override first (most reliable if set)
  const manualOverride = checkManualOverride();
  if (manualOverride) {
    if (DEBUG_MODE)
      console.log("Foldable Detect: Using Manual Override", manualOverride);
    return manualOverride;
  }

  // Default result (not foldable)
  const finalResult: FoldableDetectionResult = {
    isFoldable: false,
    isUnfolded: false,
    detectedDevice: null,
    confidence: 0,
    aspectRatio: window.innerWidth / window.innerHeight,
    foldableType: "unknown",
    detectionMethod: "none",
  };

  // Ensure code only runs in a browser environment
  if (typeof window === "undefined" || typeof navigator === "undefined") {
    console.warn("Foldable Detect: Cannot run outside browser environment.");
    return finalResult;
  }

  // --- Gather Environment Info ---
  const ua = navigator.userAgent;
  const windowW = window.innerWidth;
  const windowH = window.innerHeight;
  const pixelRatio = window.devicePixelRatio;
  const aspectRatio = windowW / windowH;

  if (DEBUG_MODE) {
    console.log("Foldable Detect: UA:", ua);
    console.log("Foldable Detect: Window WxH:", windowW, "x", windowH);
    console.log("Foldable Detect: DPR:", pixelRatio);
    console.log("Foldable Detect: Aspect Ratio:", aspectRatio.toFixed(3));
  }

  // --- Detection Logic Pipeline ---

  // 1. Device Specification Matching (High Confidence)
  const specMatchFound = checkAgainstDeviceSpecs(
    ua,
    windowW,
    windowH,
    finalResult
  );
  if (specMatchFound) {
    if (DEBUG_MODE)
      console.log("Foldable Detect: Result from Spec Match", finalResult);
    saveDetectionResult(finalResult); // Save confident result
    return finalResult;
  }

  // 2. Screen Spanning / Window Segments APIs (High Confidence)
  const isScreenSpanning =
    window.matchMedia("(screen-spanning: single-fold-vertical)").matches ||
    window.matchMedia("(screen-spanning: single-fold-horizontal)").matches;
  let segmentCount = 0;
  try {
    // Experimental API
    if (
      "getWindowSegments" in navigator &&
      typeof navigator.getWindowSegments === "function"
    ) {
      segmentCount = (
        navigator as { getWindowSegments(): unknown[] }
      ).getWindowSegments().length;
    }
  } catch (e) {
    if (DEBUG_MODE) console.warn("Error accessing getWindowSegments", e);
  }

  let viewportSegments: unknown[] | undefined;
  try {
    // Experimental API
    if (window.visualViewport && "segments" in window.visualViewport) {
      viewportSegments = (window.visualViewport as { segments?: unknown[] })
        .segments;
    }
  } catch (e) {
    if (DEBUG_MODE) console.warn("Error accessing visualViewport.segments", e);
  }

  const hasSegments =
    segmentCount > 1 || (viewportSegments && viewportSegments.length > 1);

  if (isScreenSpanning || hasSegments) {
    if (DEBUG_MODE)
      console.log("Foldable Detect: Detected via Spanning/Segments API");
    finalResult.isFoldable = true;
    finalResult.confidence = 0.8; // Fairly high confidence
    finalResult.detectionMethod = isScreenSpanning
      ? "mediaQuery"
      : segmentCount > 1
        ? "getWindowSegments"
        : "visualViewport";
    // Heuristic for unfolded state when API detects foldability
    finalResult.isUnfolded = aspectRatio > 0.8 && aspectRatio < 1.3; // Assume nearly square is unfolded
    finalResult.foldableType = /galaxy z/i.test(ua) ? "zfold" : "other"; // UA hint for type

    if (DEBUG_MODE)
      console.log("Foldable Detect: Result from API Match", finalResult);
    saveDetectionResult(finalResult);
    return finalResult;
  }

  // 3. User Agent Platform Check (Exclude Desktops)
  const isLikelyDesktopUA =
    /Windows NT|Macintosh|Linux x86_64/i.test(ua) &&
    !/Android|iPhone|iPad|iPod|Mobile/i.test(ua);
  if (isLikelyDesktopUA) {
    // If UA clearly indicates a standard desktop OS, it's not a foldable phone.
    if (DEBUG_MODE)
      console.log(
        "Foldable Detect: Detected standard desktop platform via UA. Not foldable."
      );
    return finalResult; // Return default (isFoldable: false)
  }

  // 4. Generic Dimension + Pixel Ratio Heuristic (Lower Confidence Fallback)
  // Only attempt if not identified as desktop and APIs didn't confirm.
  if (DEBUG_MODE)
    console.log(
      "Foldable Detect: No API/Desktop match, trying generic dimension heuristic..."
    );
  // Check for somewhat square aspect ratio AND high pixel density
  if (
    windowW > 600 &&
    aspectRatio > 0.8 &&
    aspectRatio < 1.3 &&
    pixelRatio > 1.5
  ) {
    if (DEBUG_MODE)
      console.log(
        "Foldable Detect: Generic dimension heuristic PASSED (with pixelRatio check)."
      );
    finalResult.isFoldable = true;
    finalResult.isUnfolded = true; // Assume unfolded if dimensions match this heuristic
    finalResult.confidence = 0.5; // Lower confidence for heuristic
    finalResult.detectionMethod = "GenericDimensionsPixelRatio";
    finalResult.foldableType = /galaxy z/i.test(ua) ? "zfold" : "other"; // Still check UA for type hint
  } else {
    if (DEBUG_MODE)
      console.log("Foldable Detect: Generic dimension heuristic FAILED.");
    // Keep default finalResult (isFoldable: false)
  }

  // --- Log Final Result and Return ---
  if (DEBUG_MODE && finalResult.isFoldable) {
    console.log(
      "Foldable Detect: Result from Dimension Heuristic",
      finalResult
    );
  } else if (DEBUG_MODE && !finalResult.isFoldable) {
    console.log("Foldable Detect: Final Result - Not Foldable");
  }

  saveDetectionResult(finalResult); // Save if confidence is high enough
  return finalResult;
}

// --- Helper Functions ---

/**
 * Checks localStorage for a manual override setting.
 * @returns {FoldableDetectionResult | null} The override result or null if not found/invalid.
 */
function checkManualOverride(): FoldableDetectionResult | null {
  if (typeof window === "undefined" || typeof localStorage === "undefined")
    return null;
  try {
    const override = localStorage.getItem("foldableDeviceOverride");
    if (override) {
      const settings: unknown = JSON.parse(override);
      // Basic validation of stored settings
      if (
        settings &&
        typeof settings === 'object' &&
        'isFoldable' in settings &&
        'isUnfolded' in settings &&
        typeof (settings as Record<string, unknown>).isFoldable === "boolean" &&
        typeof (settings as Record<string, unknown>).isUnfolded === "boolean"
      ) {
        const settingsObj = settings as Record<string, unknown>;
        return {
          isFoldable: settingsObj.isFoldable as boolean,
          isUnfolded: settingsObj.isUnfolded as boolean,
          detectedDevice: (settingsObj.detectedDevice as string | null) || null,
          confidence: 1.0, // Max confidence for manual override
          aspectRatio: window.innerWidth / window.innerHeight,
          foldableType: (settingsObj.foldableType as "zfold" | "other" | "unknown") || "unknown",
          detectionMethod: "ManualOverride",
        };
      }
    }
  } catch (e) {
    if (DEBUG_MODE) console.error("Error checking for manual override:", e);
  }
  return null;
}

/**
 * Saves confident detection results to localStorage (if foldable and confidence >= 0.6).
 * @param {FoldableDetectionResult} result - The detection result to potentially save.
 */
function saveDetectionResult(result: FoldableDetectionResult) {
  if (typeof window === "undefined" || typeof localStorage === "undefined")
    return;
  // Only save reasonably confident foldable detections
  if (!result.isFoldable || result.confidence < 0.6) return;

  try {
    const dataToSave = {
      ...result,
      timestamp: Date.now(),
      width: window.innerWidth, // Save dimensions at time of detection
      height: window.innerHeight,
    };
    localStorage.setItem("foldableDeviceState", JSON.stringify(dataToSave));
    if (DEBUG_MODE)
      console.log("Foldable Detect: Saved state to localStorage", dataToSave);
  } catch (e) {
    if (DEBUG_MODE) console.error("Error saving detection state:", e);
  }
}

/**
 * Checks the User Agent and current window dimensions against known foldable device specifications.
 * Modifies the passed 'result' object directly if a match is found.
 * @param {string} ua - The navigator.userAgent string.
 * @param {number} width - Current window innerWidth.
 * @param {number} height - Current window innerHeight.
 * @param {FoldableDetectionResult} result - The result object to modify.
 * @returns {boolean} True if a spec match was found, false otherwise.
 */
function checkAgainstDeviceSpecs(
  ua: string,
  width: number,
  height: number,
  result: FoldableDetectionResult
): boolean {
  for (const [deviceKey, specs] of Object.entries(FOLDABLE_DEVICE_SPECS)) {
    // Check if UA contains any of the known model strings for this device
    const isMatchingModel = specs.models.some((model) => ua.includes(model));

    if (isMatchingModel) {
      if (DEBUG_MODE)
        console.log(
          `Foldable Detect: Spec Match - Found model match for ${deviceKey}`
        );
      result.isFoldable = true;
      result.foldableType = deviceKey.startsWith("zfold") ? "zfold" : "other";
      result.confidence = 0.9; // High confidence for specific model match
      result.detectionMethod = "DeviceSpecMatch";

      // Check current dimensions against UNFOLDED specs (allowing for orientation swap)
      const { min: minWUnfolded, max: maxWUnfolded } =
        specs.unfoldedDimensions.width;
      const { min: minHUnfolded, max: maxHUnfolded } =
        specs.unfoldedDimensions.height;

      const isUnfoldedMatch =
        (width >= minWUnfolded &&
          width <= maxWUnfolded &&
          height >= minHUnfolded &&
          height <= maxHUnfolded) ||
        (height >= minWUnfolded &&
          height <= maxWUnfolded &&
          width >= minHUnfolded &&
          width <= maxHUnfolded);

      result.isUnfolded = isUnfoldedMatch;
      if (DEBUG_MODE)
        console.log(
          `Foldable Detect: Spec Match - Unfolded state: ${result.isUnfolded}`
        );
      return true; // Stop checking once a model matches
    }
  }
  if (DEBUG_MODE)
    console.log("Foldable Detect: Spec Match - No matching model found.");
  return false; // No spec match found
}

// --- Utilities Export ---
/**
 * Provides methods for manually controlling or debugging foldable detection.
 */
export const FoldableDeviceUtils = {
  /**
   * Sets a manual override in localStorage for testing purposes.
   * Requires page reload to take effect.
   */
  setManualOverride(settings: {
    isFoldable: boolean;
    foldableType: "zfold" | "other" | "unknown";
    isUnfolded: boolean;
  }) {
    if (typeof localStorage !== "undefined") {
      localStorage.setItem("foldableDeviceOverride", JSON.stringify(settings));
      if (DEBUG_MODE) console.log("Foldable Utils: Set Override", settings);
    } else {
      console.warn(
        "Foldable Utils: Cannot set override, localStorage not available."
      );
    }
  },

  /**
   * Clears any manual override from localStorage.
   * Requires page reload to take effect.
   */
  clearManualOverride() {
    if (typeof localStorage !== "undefined") {
      localStorage.removeItem("foldableDeviceOverride");
      if (DEBUG_MODE) console.log("Foldable Utils: Cleared Override");
    } else {
      console.warn(
        "Foldable Utils: Cannot clear override, localStorage not available."
      );
    }
  },

  /**
   * Forces a re-run of the detection logic.
   * @returns {FoldableDetectionResult} The latest detection result.
   */
  refreshDetection() {
    if (DEBUG_MODE) console.log("Foldable Utils: Refreshing detection...");
    return detectFoldableDevice();
  },

  /**
   * Gathers various pieces of information useful for debugging detection issues.
   * @returns {object} An object containing debug information.
   */
  getDebugInfo: (): object => {
    if (typeof window === "undefined" || typeof navigator === "undefined") {
      return { error: "Cannot get debug info outside browser environment." };
    }
    const currentDetection = detectFoldableDevice(); // Run detection to get current state
    const hasManualOverride =
      localStorage.getItem("foldableDeviceOverride") !== null;
    let apiChecks = {};
    try {
      apiChecks = {
        isScreenSpanning:
          window.matchMedia("(screen-spanning: single-fold-vertical)")
            .matches ||
          window.matchMedia("(screen-spanning: single-fold-horizontal)")
            .matches,
        getWindowSegmentsLength:
          "getWindowSegments" in navigator &&
          typeof (navigator as { getWindowSegments?: () => unknown[] })
            .getWindowSegments === "function"
            ? (
                navigator as { getWindowSegments(): unknown[] }
              ).getWindowSegments().length
            : "N/A",
        visualViewportSegmentsLength:
          window.visualViewport && "segments" in window.visualViewport
            ? ((window.visualViewport as { segments?: unknown[] }).segments
                ?.length ?? "N/A")
            : "N/A",
      };
    } catch (e) {
      if (DEBUG_MODE) console.error("Error getting API check info:", e);
    }

    return {
      currentDetection,
      hasManualOverride,
      windowDimensions: {
        width: window.innerWidth,
        height: window.innerHeight,
        pixelRatio: window.devicePixelRatio,
        aspectRatio: (window.innerWidth / window.innerHeight).toFixed(3),
      },
      screenDimensions: {
        width: window.screen.width,
        height: window.screen.height,
        availWidth: window.screen.availWidth,
        availHeight: window.screen.availHeight,
      },
      userAgent: navigator.userAgent,
      apiChecks,
    };
  },
};

/**
 * Smart Contact Utilities
 *
 * Handles intelligent contact functionality that can detect Google sign-in status
 * and automatically open Gmail with pre-filled compose window when appropriate.
 *
 * Features:
 * - Detects if user is signed into Google using multiple methods:
 *   1. Google authentication cookies (most reliable)
 *   2. Domain access detection
 *   3. Local/session storage auth state
 *   4. Google API library presence
 *
 * - If Google sign-in detected: Opens Gmail compose with pre-filled content
 * - If not detected: Falls back to standard mailto: link
 *
 * - Provides user feedback during detection process
 * - Graceful fallback if any method fails
 *
 * Usage:
 * ```typescript
 * import { smartContact, DEV_CONTACT_OPTIONS } from './smart-contact';
 *
 * // Use predefined dev contact options
 * await smartContact(DEV_CONTACT_OPTIONS);
 *
 * // Or create custom options
 * await smartContact({
 *   to: 'user@example.com',
 *   subject: 'Custom Subject',
 *   body: 'Custom message body'
 * });
 * ```
 */

export interface ContactOptions {
  to: string;
  subject?: string;
  body?: string;
  cc?: string;
  bcc?: string;
}

/**
 * Checks if user is likely signed into Google using multiple detection methods
 */
async function isGoogleSignedIn(): Promise<boolean> {
  try {
    // Method 1: Check for Google accounts cookies (most reliable)
    if (
      document.cookie.includes("__Secure-3PSID") ||
      document.cookie.includes("SAPISID")
    ) {
      return true;
    }

    // Method 2: Try to detect if user has Gmail open in another tab
    // by checking if we can access gmail.com domain
    try {
      await fetch("https://accounts.google.com/signin/v2/identifier", {
        method: "HEAD",
        mode: "no-cors",
        credentials: "include",
      });
      // If this doesn't throw, user might be signed in
      return true;
    } catch {
      // CORS or network error - continue to next method
    }

    // Method 3: Check for Google Auth state in localStorage (if app uses Google Auth)
    const googleAuthState =
      localStorage.getItem("google_auth_state") ??
      localStorage.getItem("google_user") ??
      sessionStorage.getItem("google_auth_state");

    if (googleAuthState) {
      try {
        const authData = JSON.parse(googleAuthState) as { signedIn?: boolean; isSignedIn?: boolean; access_token?: string } | null;
        return Boolean(
          authData &&
          (authData.signedIn || authData.isSignedIn || authData.access_token)
        );
      } catch {
        // Invalid JSON - ignore
      }
    }

    // Method 4: Check if gapi is loaded and user is signed in
    if (typeof window !== "undefined") {
      const windowWithGapi = window as Record<string, unknown>;
      interface GoogleAuthInstance {
        isSignedIn?: { get?: () => boolean };
      }
      interface GoogleAuth2 {
        getAuthInstance?: () => GoogleAuthInstance;
      }
      interface GoogleAPI {
        auth2?: GoogleAuth2;
      }
      const gapi = windowWithGapi.gapi as GoogleAPI | undefined;
      if (gapi?.auth2?.getAuthInstance) {
        const authInstance = gapi.auth2.getAuthInstance();
        if (authInstance.isSignedIn?.get && authInstance.isSignedIn.get()) {
          return true;
        }
      }
    }

    // Default to false if no indicators found
    return false;
  } catch (error) {
    console.debug("Google sign-in detection failed:", error);
    return false;
  }
}

/**
 * Opens Gmail compose window with pre-filled fields
 */
function openGmailCompose(options: ContactOptions): void {
  const params = new URLSearchParams();

  params.set("to", options.to);
  if (options.subject) params.set("su", options.subject);
  if (options.body) params.set("body", options.body);
  if (options.cc) params.set("cc", options.cc);
  if (options.bcc) params.set("bcc", options.bcc);

  const gmailUrl = `https://mail.google.com/mail/?view=cm&fs=1&${params.toString()}`;
  window.open(gmailUrl, "_blank", "noopener,noreferrer");
}

/**
 * Opens default email client with mailto: link
 */
function openMailtoLink(options: ContactOptions): void {
  const params = new URLSearchParams();

  if (options.subject) params.set("subject", options.subject);
  if (options.body) params.set("body", options.body);
  if (options.cc) params.set("cc", options.cc);
  if (options.bcc) params.set("bcc", options.bcc);

  const mailtoUrl = `mailto:${options.to}?${params.toString()}`;
  window.location.href = mailtoUrl;
}

/**
 * Smart contact function that detects Google sign-in and chooses the best method
 */
export async function smartContact(options: ContactOptions): Promise<void> {
  try {
    // Show user we're detecting their preferred email method
    console.log("🔍 Detecting your preferred email method...");

    const isSignedIn = await isGoogleSignedIn();

    if (isSignedIn) {
      console.log("✅ Gmail detected - opening Gmail compose window");
      // User is signed into Google - open Gmail compose
      openGmailCompose(options);
    } else {
      console.log("📧 Opening default email client");
      // User is not signed in or detection failed - use mailto
      openMailtoLink(options);
    }
  } catch (error) {
    console.warn(
      "Smart contact detection failed, falling back to mailto:",
      error
    );
    // Fallback to mailto if anything goes wrong
    openMailtoLink(options);
  }
}

