/**
 * Copies text to clipboard using modern Clipboard API
 */
async function copyToClipboard(text: string): Promise<boolean> {
  // Try modern Clipboard API first
  try {
    await navigator.clipboard.writeText(text);
    return true;
  } catch {
    // Fallback for older browsers or when permission denied
    try {
      const textArea = document.createElement("textarea");
      textArea.value = text;
      textArea.style.position = "fixed";
      textArea.style.left = "-999999px";
      textArea.style.top = "-999999px";
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      const successful = document.execCommand("copy");
      document.body.removeChild(textArea);
      return successful;
    } catch (error) {
      console.error("Failed to copy to clipboard:", error);
      return false;
    }
  }
}

/**
 * Shows a temporary toast notification
 */
function showToast(message: string, duration: number = 3000): void {
  const toast = document.createElement("div");
  toast.textContent = message;
  toast.style.cssText = `
    position: fixed;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.9);
    color: white;
    padding: 1rem 1.5rem;
    border-radius: 0.5rem;
    font-size: 0.9375rem;
    font-weight: 600;
    z-index: 100000;
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
    animation: slideUp 0.3s ease;
  `;

  document.body.appendChild(toast);

  setTimeout(() => {
    toast.style.animation = "slideDown 0.3s ease";
    setTimeout(() => {
      document.body.removeChild(toast);
    }, 300);
  }, duration);
}

/**
 * Smart email contact using 2025 best practices:
 * 1. Web Share API (native OS sharing dialog) - PRIMARY
 * 2. Copy to clipboard - UNIVERSAL FALLBACK
 * 3. Mailto link - LAST RESORT
 *
 * This approach avoids the PayPal redirect bug and other mailto issues on mobile.
 */
export async function smartEmailContact(email: string): Promise<void> {
  try {
    // Try Web Share API first (works on all mobile browsers except Firefox)
    if (typeof navigator.share === 'function') {
      try {
        await navigator.share({
          title: "Email Contact",
          text: `Contact us at: ${email}`,
          url: `mailto:${email}`,
        });
        console.log("✅ Email shared via Web Share API");
        return;
      } catch (error: unknown) {
        // User cancelled or share failed
        const errorName = error instanceof Error && 'name' in error ? (error as { name: string }).name : '';
        if (errorName !== "AbortError") {
          console.debug("Web Share API failed:", error);
        }
      }
    }

    // Fallback to copy-to-clipboard
    const copied = await copyToClipboard(email);
    if (copied) {
      showToast(`✓ Email copied: ${email}`);
      console.log("✅ Email copied to clipboard");
      return;
    }

    // Last resort: mailto link
    console.log("📧 Opening mailto link");
    window.location.href = `mailto:${email}`;
  } catch (error) {
    console.error("Smart email contact failed:", error);
    // Final fallback
    window.location.href = `mailto:${email}`;
  }
}

import { injectable } from "inversify";
import type { IMobileFullscreenService } from "../contracts/IMobileFullscreenService";

/**
 * Type definitions for vendor-prefixed fullscreen APIs
 */
interface VendorDocument extends Document {
  webkitFullscreenEnabled?: boolean;
  mozFullScreenEnabled?: boolean;
  msFullscreenEnabled?: boolean;
  webkitFullscreenElement?: Element;
  mozFullScreenElement?: Element;
  msFullscreenElement?: Element;
  webkitExitFullscreen?: () => Promise<void>;
  mozCancelFullScreen?: () => Promise<void>;
  msExitFullscreen?: () => Promise<void>;
}

interface VendorHTMLElement extends HTMLElement {
  webkitRequestFullscreen?: () => Promise<void>;
  mozRequestFullScreen?: () => Promise<void>;
  msRequestFullscreen?: () => Promise<void>;
}

interface VendorNavigator extends Navigator {
  standalone?: boolean;
}

interface BeforeInstallPromptEvent extends Event {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
}

/**
 * Mobile Fullscreen Service Implementation
 *
 * Provides comprehensive fullscreen management for mobile devices
 */
@injectable()
export class MobileFullscreenService implements IMobileFullscreenService {
  private fullscreenCallbacks: ((isFullscreen: boolean) => void)[] = [];
  private installCallbacks: ((canInstall: boolean) => void)[] = [];
  private deferredPrompt: BeforeInstallPromptEvent | null = null;

  constructor() {
    this.setupEventListeners();
  }

  private setupEventListeners(): void {
    if (typeof window === "undefined") return;

    // Listen for fullscreen changes
    const fullscreenEvents = [
      "fullscreenchange",
      "webkitfullscreenchange",
      "mozfullscreenchange",
      "msfullscreenchange",
    ];

    fullscreenEvents.forEach((event) => {
      document.addEventListener(event, () => {
        const isFullscreen = this.isFullscreen();
        this.fullscreenCallbacks.forEach((callback) => callback(isFullscreen));
      });
    });

    // Listen for PWA install prompt
    window.addEventListener("beforeinstallprompt", (e) => {
      e.preventDefault();
      this.deferredPrompt = e as BeforeInstallPromptEvent;
      this.installCallbacks.forEach((callback) => callback(true));
      console.log("📱 PWA install prompt available");
    });

    // Listen for PWA install completion
    window.addEventListener("appinstalled", () => {
      this.deferredPrompt = null;
      this.installCallbacks.forEach((callback) => callback(false));
      console.log("✅ PWA installed successfully");
    });
  }

  isPWA(): boolean {
    if (typeof window === "undefined") return false;

    // Check if running in standalone mode (PWA)
    // Multiple display modes indicate PWA: standalone, fullscreen, minimal-ui
    const isStandalone = window.matchMedia(
      "(display-mode: standalone)"
    ).matches;
    const isFullscreenMode = window.matchMedia(
      "(display-mode: fullscreen)"
    ).matches;
    const isMinimalUI = window.matchMedia("(display-mode: minimal-ui)").matches;
    const iOSStandalone =
      (window.navigator as VendorNavigator).standalone === true;
    const isAndroidTWA = document.referrer.includes("android-app://");

    return (
      isStandalone ||
      isFullscreenMode ||
      isMinimalUI ||
      iOSStandalone ||
      isAndroidTWA
    );
  }

  canInstallPWA(): boolean {
    return this.deferredPrompt !== null;
  }

  async promptInstallPWA(): Promise<boolean> {
    if (!this.deferredPrompt) {
      console.warn("PWA install prompt not available");
      return false;
    }

    try {
      void this.deferredPrompt.prompt();
      const { outcome } = await this.deferredPrompt.userChoice;

      if (outcome === "accepted") {
        console.log("✅ User accepted PWA install");
        return true;
      } else {
        console.log("❌ User dismissed PWA install");
        return false;
      }
    } catch (error) {
      console.error("PWA install prompt failed:", error);
      return false;
    } finally {
      this.deferredPrompt = null;
    }
  }

  isFullscreenSupported(): boolean {
    if (typeof document === "undefined") return false;

    const vendorDoc = document as VendorDocument;
    return !!(
      document.fullscreenEnabled ||
      vendorDoc.webkitFullscreenEnabled ||
      vendorDoc.mozFullScreenEnabled ||
      vendorDoc.msFullscreenEnabled
    );
  }

  isFullscreen(): boolean {
    if (typeof document === "undefined") return false;

    const vendorDoc = document as VendorDocument;
    return !!(
      document.fullscreenElement ||
      vendorDoc.webkitFullscreenElement ||
      vendorDoc.mozFullScreenElement ||
      vendorDoc.msFullscreenElement
    );
  }

  async requestFullscreen(): Promise<boolean> {
    if (!this.isFullscreenSupported()) {
      console.warn("Fullscreen API not supported");
      return false;
    }

    try {
      const element = document.documentElement as VendorHTMLElement;

      if (element.requestFullscreen) {
        await element.requestFullscreen();
      } else if (element.webkitRequestFullscreen) {
        await element.webkitRequestFullscreen();
      } else if (element.mozRequestFullScreen) {
        await element.mozRequestFullScreen();
      } else if (element.msRequestFullscreen) {
        await element.msRequestFullscreen();
      }

      return true;
    } catch (error) {
      console.error("Failed to enter fullscreen:", error);
      return false;
    }
  }

  async exitFullscreen(): Promise<boolean> {
    if (!this.isFullscreen()) {
      return true; // Already not in fullscreen
    }

    try {
      const vendorDoc = document as VendorDocument;
      if (document.exitFullscreen) {
        await document.exitFullscreen();
      } else if (vendorDoc.webkitExitFullscreen) {
        await vendorDoc.webkitExitFullscreen();
      } else if (vendorDoc.mozCancelFullScreen) {
        await vendorDoc.mozCancelFullScreen();
      } else if (vendorDoc.msExitFullscreen) {
        await vendorDoc.msExitFullscreen();
      }

      return true;
    } catch (error) {
      console.error("Failed to exit fullscreen:", error);
      return false;
    }
  }

  async toggleFullscreen(): Promise<boolean> {
    if (this.isFullscreen()) {
      return await this.exitFullscreen();
    } else {
      return await this.requestFullscreen();
    }
  }

  showFullscreenPrompt(): void {
    const strategy = this.getRecommendedStrategy();

    switch (strategy) {
      case "pwa":
        this.showPWAPrompt();
        break;
      case "fullscreen-api":
        this.showFullscreenAPIPrompt();
        break;
      case "viewport-only":
        this.showViewportPrompt();
        break;
      default:
        console.log("Fullscreen not supported on this device");
    }
  }

  private showPWAPrompt(): void {
    // This would show a custom UI prompt for PWA installation
    console.log("💡 Showing PWA install prompt");
    // Implementation would depend on your UI framework
  }

  private showFullscreenAPIPrompt(): void {
    // This would show a custom UI prompt for fullscreen API
    console.log("💡 Showing fullscreen API prompt");
    // Implementation would depend on your UI framework
  }

  private showViewportPrompt(): void {
    // This would show tips for manual fullscreen (browser menu)
    console.log("💡 Showing viewport optimization tips");
    // Implementation would depend on your UI framework
  }

  onFullscreenChange(callback: (isFullscreen: boolean) => void): () => void {
    this.fullscreenCallbacks.push(callback);

    return () => {
      const index = this.fullscreenCallbacks.indexOf(callback);
      if (index > -1) {
        this.fullscreenCallbacks.splice(index, 1);
      }
    };
  }

  onInstallPromptAvailable(
    callback: (canInstall: boolean) => void
  ): () => void {
    this.installCallbacks.push(callback);

    return () => {
      const index = this.installCallbacks.indexOf(callback);
      if (index > -1) {
        this.installCallbacks.splice(index, 1);
      }
    };
  }

  getRecommendedStrategy():
    | "pwa"
    | "fullscreen-api"
    | "viewport-only"
    | "not-supported" {
    if (this.isPWA()) {
      return "pwa"; // Already installed as PWA
    }

    if (this.canInstallPWA()) {
      return "pwa"; // Can be installed as PWA
    }

    if (this.isFullscreenSupported()) {
      return "fullscreen-api"; // Use Fullscreen API
    }

    // Check if mobile browser supports viewport optimization
    if (typeof window !== "undefined" && "ontouchstart" in window) {
      return "viewport-only"; // Mobile browser, suggest manual fullscreen
    }

    return "not-supported";
  }

  async handleInstallRequest(): Promise<boolean> {
    if (this.canInstallPWA()) {
      // Try native install
      try {
        const accepted = await this.promptInstallPWA();
        if (accepted) {
          return true;
        }
        // If user dismissed, fall back to showing guide
        this.showInstallGuide();
        return true;
      } catch (error) {
        console.error("Failed to show native install prompt:", error);
        // Fall back to showing guide
        this.showInstallGuide();
        return true;
      }
    } else {
      // No native prompt available, show guide
      this.showInstallGuide();
      return true;
    }
  }

  private showInstallGuide(): void {
    // Dispatch event for PWA install guide to be shown
    if (typeof window !== "undefined") {
      window.dispatchEvent(new CustomEvent("pwa:open-install-guide"));
    }
  }
}

import { injectable } from "inversify";
import type {
  IPlatformDetectionService,
  Platform,
  Browser,
  PlatformInfo,
} from "../contracts/IPlatformDetectionService";

/**
 * Platform Detection Service Implementation
 *
 * Provides centralized platform and browser detection capabilities.
 */
@injectable()
export class PlatformDetectionService implements IPlatformDetectionService {
  detectPlatformAndBrowser(): PlatformInfo {
    return {
      platform: this.detectPlatform(),
      browser: this.detectBrowser(),
    };
  }

  detectPlatform(): Platform {
    if (typeof navigator === "undefined") return "desktop";

    const ua = navigator.userAgent.toLowerCase();

    if (/iphone|ipad|ipod/.test(ua)) {
      return "ios";
    }

    if (/android/.test(ua)) {
      return "android";
    }

    return "desktop";
  }

  detectBrowser(): Browser {
    if (typeof navigator === "undefined") return "other";

    const ua = navigator.userAgent.toLowerCase();

    // Check for Samsung Internet first (most specific)
    if (ua.includes("samsungbrowser")) {
      return "samsung";
    }

    // Check for Edge (before Chrome, as Edge UA contains "chrome")
    if (ua.includes("edg/")) {
      return "edge";
    }

    // Check for Firefox
    if (ua.includes("firefox") || ua.includes("fxios")) {
      return "firefox";
    }

    // Check for Chrome (excludes Edge and Samsung)
    if (ua.includes("chrome") || ua.includes("crios")) {
      return "chrome";
    }

    // Check for Safari (must be after Chrome/Edge/Firefox checks)
    if (ua.includes("safari")) {
      return "safari";
    }

    return "other";
  }

  supportsPWAInstall(platform: Platform, browser: Browser): boolean {
    // iOS only supports Safari
    if (platform === "ios") {
      return browser === "safari";
    }

    // Android supports Chrome, Edge, and Samsung Internet
    if (platform === "android") {
      return browser === "chrome" || browser === "edge" || browser === "samsung";
    }

    // Desktop supports Chrome and Edge
    if (platform === "desktop") {
      return browser === "chrome" || browser === "edge";
    }

    return false;
  }

  getBrowserDisplayName(browser: Browser): string {
    const names: Record<Browser, string> = {
      chrome: "Chrome",
      safari: "Safari",
      edge: "Edge",
      firefox: "Firefox",
      samsung: "Samsung Internet",
      other: "Unknown Browser",
    };

    return names[browser];
  }

  getPlatformDisplayName(platform: Platform): string {
    const names: Record<Platform, string> = {
      ios: "iOS",
      android: "Android",
      desktop: "Desktop",
    };

    return names[platform];
  }
}

/**
 * Arrow Grid Coordinate Service
 *
 * Uses the grid module's coordinate system instead of duplicating data.
 * Provides arrow-specific coordinate functionality using the authoritative grid data.
 */

import { GridLocation, GridMode, type MotionData } from "$shared";
import { Point } from "fabric";
import { injectable } from "inversify";
import { createGridPointData } from "../../../../grid/utils/grid-coordinate-utils";
import type { IArrowGridCoordinateService } from "../contracts";

@injectable()
export class ArrowGridCoordinateService implements IArrowGridCoordinateService {
  // Scene dimensions from grid module
  private readonly SCENE_SIZE = 950;
  private readonly CENTER_X = 475.0;
  private readonly CENTER_Y = 475.0;

  getInitialPosition(motion: MotionData, location: GridLocation): Point {
    const motionType = motion.motionType.toLowerCase();
    const gridMode = motion.gridMode;

    if (["pro", "anti", "float"].includes(motionType)) {
      // Shift arrows use layer2 points
      return this.getLayer2Coords(location, gridMode);
    } else if (["static", "dash"].includes(motionType)) {
      // Static/dash arrows use hand points
      return this.getHandPointCoords(location, gridMode);
    } else {
      // Default fallback
      return this.getSceneCenter();
    }
  }

  getSceneCenter(): Point {
    return new Point(this.CENTER_X, this.CENTER_Y);
  }

  getSceneDimensions(): [number, number] {
    return [this.SCENE_SIZE, this.SCENE_SIZE];
  }

  validateCoordinates(point: Point): boolean {
    return (
      point &&
      typeof point.x === "number" &&
      typeof point.y === "number" &&
      point.x >= 0 &&
      point.x <= this.SCENE_SIZE &&
      point.y >= 0 &&
      point.y <= this.SCENE_SIZE
    );
  }

  getAllHandPoints(
    gridMode: GridMode = GridMode.DIAMOND
  ): Partial<Record<GridLocation, Point>> {
    const gridData = createGridPointData(gridMode);
    const handPoints: Partial<Record<GridLocation, Point>> = {};

    // Map grid coordinate keys to GridLocation enum (strict points)
    const locationMap: Record<string, GridLocation> = {
      n_diamond_hand_point_strict: GridLocation.NORTH,
      e_diamond_hand_point_strict: GridLocation.EAST,
      s_diamond_hand_point_strict: GridLocation.SOUTH,
      w_diamond_hand_point_strict: GridLocation.WEST,
      ne_box_hand_point_strict: GridLocation.NORTHEAST,
      se_box_hand_point_strict: GridLocation.SOUTHEAST,
      sw_box_hand_point_strict: GridLocation.SOUTHWEST,
      nw_box_hand_point_strict: GridLocation.NORTHWEST,
    };

    // Extract hand points from grid data (using strict points for animation viewer)
    Object.entries(gridData.allHandPointsStrict).forEach(([key, value]) => {
      const location = locationMap[key];
      if (location && value.coordinates) {
        handPoints[location] = new Point(
          value.coordinates.x,
          value.coordinates.y
        );
      }
    });

    return handPoints;
  }

  getAllLayer2Points(
    gridMode: GridMode = GridMode.DIAMOND
  ): Partial<Record<GridLocation, Point>> {
    const gridData = createGridPointData(gridMode);
    const layer2Points: Partial<Record<GridLocation, Point>> = {};

    // For diamond mode, layer2 points are diagonal positions (strict points)
    if (gridMode === GridMode.DIAMOND) {
      // Diamond layer2 strict points map to diagonal positions
      layer2Points[GridLocation.NORTHEAST] = new Point(625.0, 325.0);
      layer2Points[GridLocation.SOUTHEAST] = new Point(625.0, 625.0);
      layer2Points[GridLocation.SOUTHWEST] = new Point(325.0, 625.0);
      layer2Points[GridLocation.NORTHWEST] = new Point(325.0, 325.0);

      // For cardinal directions, map to nearest diagonal
      layer2Points[GridLocation.NORTH] = layer2Points[GridLocation.NORTHEAST];
      layer2Points[GridLocation.EAST] = layer2Points[GridLocation.SOUTHEAST];
      layer2Points[GridLocation.SOUTH] = layer2Points[GridLocation.SOUTHWEST];
      layer2Points[GridLocation.WEST] = layer2Points[GridLocation.NORTHWEST];
    } else {
      // For box mode, use strict layer2 points from grid data
      const locationMap: Record<string, GridLocation> = {
        n_box_layer2_point_strict: GridLocation.NORTH,
        e_box_layer2_point_strict: GridLocation.EAST,
        s_box_layer2_point_strict: GridLocation.SOUTH,
        w_box_layer2_point_strict: GridLocation.WEST,
      };

      Object.entries(gridData.allLayer2PointsStrict).forEach(([key, value]) => {
        const location = locationMap[key];
        if (location && value.coordinates) {
          layer2Points[location] = new Point(
            value.coordinates.x,
            value.coordinates.y
          );
        }
      });
    }

    return layer2Points;
  }

  getSupportedLocations(): GridLocation[] {
    return [
      GridLocation.NORTH,
      GridLocation.EAST,
      GridLocation.SOUTH,
      GridLocation.WEST,
      GridLocation.NORTHEAST,
      GridLocation.SOUTHEAST,
      GridLocation.SOUTHWEST,
      GridLocation.NORTHWEST,
    ];
  }

  private getLayer2Coords(location: GridLocation, gridMode: GridMode): Point {
    const layer2Points = this.getAllLayer2Points(gridMode);
    return layer2Points[location] ?? this.getSceneCenter();
  }

  private getHandPointCoords(
    location: GridLocation,
    gridMode: GridMode
  ): Point {
    const handPoints = this.getAllHandPoints(gridMode);
    return handPoints[location] ?? this.getSceneCenter();
  }
}

/**
 * Arrow Lifecycle Manager Implementation
 *
 * Single responsibility service for coordinating all arrow lifecycle operations.
 * Orchestrates loading, positioning, and state management for arrows.
 */

import { inject, injectable } from "inversify";
import { TYPES } from "../../../../../inversify";
import type { MotionData, PictographData } from "../../../../shared";
import type { IArrowPositioningOrchestrator } from "../../../positioning/services";
import type { IArrowRenderer } from "../../../rendering";
import type {
  ArrowAssets,
  ArrowLifecycleResult,
  ArrowPosition,
  ArrowState,
} from "../../domain";
import {
  createArrowAssets,
  createArrowLifecycleResult,
  createArrowPosition,
  createArrowState,
} from "../../domain";
import type { IArrowLifecycleManager } from "../contracts";

@injectable()
export class ArrowLifecycleManager implements IArrowLifecycleManager {
  constructor(
    @inject(TYPES.IArrowRenderer) private arrowRenderer: IArrowRenderer,
    @inject(TYPES.IArrowPositioningOrchestrator)
    private positioningOrchestrator: IArrowPositioningOrchestrator
  ) {}

  /**
   * Load arrow assets for a single motion
   */
  async loadArrowAssets(motionData: MotionData): Promise<ArrowAssets> {
    const svgData = await this.arrowRenderer.loadArrowSvg(
      motionData.arrowPlacementData,
      motionData
    );

    return createArrowAssets({
      imageSrc: svgData.imageSrc,
      viewBox: svgData.viewBox,
      center: svgData.center,
    });
  }

  /**
   * Calculate position for a single arrow
   */
  async calculateArrowPosition(
    motionData: MotionData,
    pictographData: PictographData
  ): Promise<ArrowPosition> {
    const [x, y, rotation] =
      await this.positioningOrchestrator.calculateArrowPoint(
        pictographData,
        motionData
      );

    // Apply manual adjustments from keyboard controls (WASD)
    const manualAdjustX = motionData.arrowPlacementData.manualAdjustmentX ?? 0;
    const manualAdjustY = motionData.arrowPlacementData.manualAdjustmentY ?? 0;

    return createArrowPosition({
      x: x + manualAdjustX,
      y: y + manualAdjustY,
      rotation,
    });
  }

  /**
   * Determine if arrow should be mirrored
   */
  shouldMirrorArrow(
    motionData: MotionData,
    pictographData: PictographData
  ): boolean {
    return this.positioningOrchestrator.shouldMirrorArrow(
      motionData.arrowPlacementData,
      pictographData,
      motionData
    );
  }

  /**
   * Get complete arrow state for a single motion
   */
  async getArrowState(
    motionData: MotionData,
    pictographData: PictographData
  ): Promise<ArrowState> {
    try {
      // Load assets and calculate position in parallel
      const [assets, position] = await Promise.all([
        this.loadArrowAssets(motionData),
        this.calculateArrowPosition(motionData, pictographData),
      ]);

      const shouldMirror = this.shouldMirrorArrow(motionData, pictographData);

      return createArrowState({
        assets,
        position,
        shouldMirror,
        isVisible: true,
        isLoading: false,
        error: null,
      });
    } catch (error) {
      const errorMessage =
        error instanceof Error ? error.message : "Unknown error";
      return createArrowState({
        assets: null,
        position: null,
        shouldMirror: false,
        isVisible: false,
        isLoading: false,
        error: errorMessage,
      });
    }
  }

  /**
   * Coordinate complete arrow lifecycle for all motions in pictograph
   * This is the main coordination method that ensures proper loading order
   */
  async coordinateArrowLifecycle(
    pictographData: PictographData
  ): Promise<ArrowLifecycleResult> {
    if (!pictographData.motions) {
      return createArrowLifecycleResult({ allReady: true });
    }

    const positions: Record<string, ArrowPosition> = {};
    const mirroring: Record<string, boolean> = {};
    const assets: Record<string, ArrowAssets> = {};
    const errors: Record<string, string> = {};

    // Process all motions in parallel for better performance
    const motionPromises = Object.entries(pictographData.motions).map(
      async ([color, motionData]) => {
        if (!motionData) return;

        try {
          const arrowState = await this.getArrowState(
            motionData,
            pictographData
          );

          if (arrowState.error) {
            errors[color] = arrowState.error;
          } else if (arrowState.assets && arrowState.position) {
            positions[color] = arrowState.position;
            mirroring[color] = arrowState.shouldMirror;
            assets[color] = arrowState.assets;
          }
        } catch (error) {
          const errorMessage =
            error instanceof Error ? error.message : "Unknown error";
          errors[color] = errorMessage;
        }
      }
    );

    await Promise.all(motionPromises);

    const allReady =
      Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

    return createArrowLifecycleResult({
      positions,
      mirroring,
      assets,
      allReady,
      errors,
    });
  }

  /**
   * Reset arrow state (for data changes)
   */
  resetArrowState(): void {
    // This method can be used to clear any internal caches or state
    // Currently no internal state to reset, but provides extension point
  }
}

/**
 * Arrow Positioning Orchestrator
 *
 * Main implementation of the arrow positioning pipeline.
 * Coordinates with other services to compute final arrow positions.
 */

import type {
  IArrowAdjustmentCalculator,
  IArrowLocationCalculator,
} from "$shared";
import type {
  ArrowPlacementData,
  IArrowPositioningOrchestrator,
  MotionData,
  PictographData,
} from "$shared";
import { TYPES } from "$shared/inversify/types";
import { inject, injectable } from "inversify";
import type { IArrowRotationCalculator } from "../../../positioning/calculation/services/contracts";
import type {
  IArrowDataProcessor,
  IArrowGridCoordinateService,
} from "../contracts";

@injectable()
export class ArrowPositioningOrchestrator
  implements IArrowPositioningOrchestrator
{
  private locationCalculator: IArrowLocationCalculator;
  private rotationCalculator: IArrowRotationCalculator;
  private adjustmentCalculator: IArrowAdjustmentCalculator;
  private coordinateSystem: IArrowGridCoordinateService;
  private dataProcessor: IArrowDataProcessor;

  constructor(
    @inject(TYPES.IArrowLocationCalculator)
    locationCalculator: IArrowLocationCalculator,
    @inject(TYPES.IArrowRotationCalculator)
    rotationCalculator: IArrowRotationCalculator,
    @inject(TYPES.IArrowAdjustmentCalculator)
    adjustmentCalculator: IArrowAdjustmentCalculator,
    @inject(TYPES.IArrowGridCoordinateService)
    coordinateSystem: IArrowGridCoordinateService,
    @inject(TYPES.IArrowDataProcessor)
    dataProcessor: IArrowDataProcessor
  ) {
    this.locationCalculator = locationCalculator;
    this.rotationCalculator = rotationCalculator;
    this.adjustmentCalculator = adjustmentCalculator;
    this.coordinateSystem = coordinateSystem;
    this.dataProcessor = dataProcessor;
  }

  async calculateArrowPoint(
    pictographData: PictographData,
    motionData: MotionData
  ): Promise<[number, number, number]> {
    /**
     * Calculate arrow position asynchronously with full service coordination.
     */
    try {
      const motion = motionData;

      const location = this.locationCalculator.calculateLocation(
        motion,
        pictographData
      );
      const initialPosition = this.coordinateSystem.getInitialPosition(
        motion,
        location
      );

      const validPosition =
        this.dataProcessor.ensureValidPosition(initialPosition);

      const rotation = await this.rotationCalculator.calculateRotation(
        motion,
        location,
        pictographData
      );

      const adjustment = await this.adjustmentCalculator.calculateAdjustment(
        pictographData,
        motion,
        pictographData.letter ?? "A",
        location,
        motion.color
      );

      const [adjustmentX, adjustmentY] =
        this.dataProcessor.extractAdjustmentValues(adjustment);

      const finalX = validPosition.x + adjustmentX;
      const finalY = validPosition.y + adjustmentY;

      return [finalX, finalY, rotation];
    } catch (error) {
      console.error("Arrow positioning calculation failed:", error);
      const center = this.coordinateSystem.getSceneCenter();
      return [center.x, center.y, 0];
    }
  }

  async updateArrowPosition(
    pictographData: PictographData,
    color: string,
    _motionData?: MotionData
  ): Promise<PictographData> {
    /**
     * Update arrow position in pictograph data.
     */
    try {
      const motionData =
        pictographData.motions[color as keyof typeof pictographData.motions];
      if (!motionData) {
        console.warn(`No motion data found for color: ${color}`);
        return pictographData;
      }
      const arrowData = motionData.arrowPlacementData;

      const [x, y, rotation] = await this.calculateArrowPoint(
        pictographData,
        motionData
      );

      const shouldMirror = this.shouldMirrorArrow(arrowData, pictographData);

      const manualAdjustX = arrowData.manualAdjustmentX ?? 0;
      const manualAdjustY = arrowData.manualAdjustmentY ?? 0;

      const updates: Partial<ArrowPlacementData> = {
        positionX: x + manualAdjustX,
        positionY: y + manualAdjustY,
        rotationAngle: rotation,
        svgMirrored: shouldMirror,
        manualAdjustmentX: manualAdjustX,
        manualAdjustmentY: manualAdjustY,
      };

      const motionUpdates = {
        arrowLocation: location,
      } as unknown as Partial<MotionData>;

      return this.dataProcessor.updateArrowInPictograph(
        pictographData,
        color,
        updates,
        motionUpdates
      );
    } catch (error) {
      console.error("Arrow position update failed:", error);
      return pictographData;
    }
  }

  async calculateAllArrowPoints(
    pictographData: PictographData
  ): Promise<PictographData> {
    /**
     * Calculate positions for all arrows in the pictograph.
     */
    try {
      if (!pictographData.motions) {
        return pictographData;
      }

      let updatedPictograph = { ...pictographData };

      for (const color of Object.keys(pictographData.motions)) {
        const motionData =
          pictographData.motions[color as keyof typeof pictographData.motions];
        if (motionData) {
          const arrowData = motionData.arrowPlacementData;
          const calculatedLocation = this.locationCalculator.calculateLocation(
            motionData,
            updatedPictograph
          );

          const [x, y, rotation] = await this.calculateArrowPoint(
            updatedPictograph,
            motionData
          );

          const currentMotionData =
            updatedPictograph.motions[
              color as keyof typeof updatedPictograph.motions
            ];
          const shouldMirror = this.shouldMirrorArrow(
            arrowData,
            updatedPictograph,
            currentMotionData
          );

          const manualAdjustX = arrowData.manualAdjustmentX ?? 0;
          const manualAdjustY = arrowData.manualAdjustmentY ?? 0;

          const updates: Partial<ArrowPlacementData> = {
            positionX: x + manualAdjustX,
            positionY: y + manualAdjustY,
            rotationAngle: rotation,
            svgMirrored: shouldMirror,
            manualAdjustmentX: manualAdjustX,
            manualAdjustmentY: manualAdjustY,
          };

          const motionUpdates = {
            arrowLocation: calculatedLocation,
          } as unknown as Partial<MotionData>;

          updatedPictograph = this.dataProcessor.updateArrowInPictograph(
            updatedPictograph,
            color,
            updates,
            motionUpdates
          );
        }
      }

      return updatedPictograph;
    } catch (error) {
      console.error("Failed to calculate all arrow positions:", error);
      return pictographData;
    }
  }

  shouldMirrorArrow(
    _arrowData: ArrowPlacementData,
    pictographData?: PictographData,
    motionData?: MotionData
  ): boolean {
    /**
     * Determine if arrow should be mirrored based on motion type and prop rotation direction.
     *
     * Mirror conditions (matching desktop logic):
     * - Anti motion + clockwise → Mirror = True
     * - Anti motion + counterclockwise → Mirror = False
     * - Pro motion + clockwise → Mirror = False
     * - Pro motion + counterclockwise → Mirror = True
     * - Other motions follow "pro" rules
     */

    if (!pictographData?.motions || !motionData) {
      return false;
    }

    const motionType = motionData.motionType.toLowerCase();
    const propRotDir = motionData.rotationDirection.toLowerCase();

    const mirrorConditions = {
      anti: { cw: true, ccw: false },
      other: { cw: false, ccw: true },
    };

    const conditionKey = motionType === "anti" ? "anti" : "other";
    const shouldMirror =
      mirrorConditions[conditionKey][
        propRotDir as keyof typeof mirrorConditions.anti
      ] ?? false;

    return shouldMirror;
  }

  applyMirrorTransform(
    arrowItem: HTMLElement | SVGElement,
    shouldMirror: boolean
  ): void {
    /**
     * Apply mirror transformation to arrow graphics item.
     */
    if (shouldMirror) {
      arrowItem.style.transform = `${arrowItem.style.transform || ""} scaleX(-1)`;
    } else {
      const transform = arrowItem.style.transform || "";
      arrowItem.style.transform = transform
        .replace(/scaleX\(-1\)\s*/g, "")
        .trim();
    }
  }
}

/**
 * Arrow Adjustment Calculator - Consolidated Service
 *
 * Consolidated service that combines ArrowAdjustmentCalculator and ArrowAdjustmentLookup
 * to eliminate pure delegation layer. Maintains exact same interface and behavior.
 *
 * CONSOLIDATION BENEFITS:
 * - Removes unnecessary delegation layer
 * - Maintains identical logic and results
 * - Preserves all existing interfaces and test compatibility
 * - Better TypeScript organization
 */

import type { MotionTypeType } from "$lib/modules/animate/utils/motion-utils";
import type {
  IArrowAdjustmentCalculator,
  GridLocation,
  ArrowPlacementKeyService,
} from "$shared";
import type { MotionData, PictographData } from "$shared";
import type {
  IAttributeKeyGenerator,
  IDefaultPlacementService,
  IDirectionalTupleProcessor,
  IGridModeDeriver,
  ISpecialPlacementOriKeyGenerator,
  ISpecialPlacementService,
  ITurnsTupleKeyGenerator,
} from "$shared";
import { GridMode } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { Point } from "fabric";
import { inject, injectable } from "inversify";

@injectable()
export class ArrowAdjustmentCalculator implements IArrowAdjustmentCalculator {
  /**
   * Consolidated service combining lookup and calculation logic.
   * Eliminates the pure delegation layer while maintaining identical behavior.
   */

  // Lookup services (previously in ArrowAdjustmentLookup)
  private specialPlacementService: ISpecialPlacementService;
  private defaultPlacementService: IDefaultPlacementService;
  private orientationKeyService: ISpecialPlacementOriKeyGenerator;
  private placementKeyService: ArrowPlacementKeyService;
  private turnsTupleService: ITurnsTupleKeyGenerator;
  private attributeKeyService: IAttributeKeyGenerator;

  // Processing services
  private tupleProcessor: IDirectionalTupleProcessor;
  private gridModeService: IGridModeDeriver;

  constructor(
    @inject(TYPES.IGridModeDeriver) gridModeService: IGridModeDeriver,
    @inject(TYPES.ISpecialPlacementService)
    specialPlacementService: ISpecialPlacementService,
    @inject(TYPES.IDefaultPlacementService)
    defaultPlacementService: IDefaultPlacementService,
    @inject(TYPES.ISpecialPlacementOriKeyGenerator)
    orientationKeyService: ISpecialPlacementOriKeyGenerator,
    @inject(TYPES.IArrowPlacementKeyService)
    placementKeyService: ArrowPlacementKeyService,
    @inject(TYPES.ITurnsTupleKeyGenerator)
    turnsTupleService: ITurnsTupleKeyGenerator,
    @inject(TYPES.IAttributeKeyGenerator)
    attributeKeyService: IAttributeKeyGenerator,
    @inject(TYPES.IDirectionalTupleProcessor)
    tupleProcessor: IDirectionalTupleProcessor
  ) {
    // Store all injected services
    this.gridModeService = gridModeService;
    this.specialPlacementService = specialPlacementService;
    this.defaultPlacementService = defaultPlacementService;
    this.orientationKeyService = orientationKeyService;
    this.placementKeyService = placementKeyService;
    this.turnsTupleService = turnsTupleService;
    this.attributeKeyService = attributeKeyService;
    this.tupleProcessor = tupleProcessor;
  }

  async calculateAdjustment(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    location: GridLocation,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Calculate arrow position adjustment - IDENTICAL logic to original.
     */
    try {
      return await this.calculateAdjustmentResult(
        pictographData,
        motionData,
        letter,
        location,
        arrowColor
      );
    } catch (error) {
      console.error(`Adjustment calculation failed: ${String(error)}`);
      return new Point(0, 0);
    }
  }

  async calculateAdjustmentResult(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    location: GridLocation,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Calculate arrow position adjustment with proper error handling.
     * IDENTICAL logic to original ArrowAdjustmentCalculator.
     */
    try {
      // STEP 1: Look up base adjustment (special → default)
      const baseAdjustment = await this.getBaseAdjustment(
        pictographData,
        motionData,
        letter,
        arrowColor
      );

      // STEP 2: Process directional tuples for ALL motion types
      // JSON values are reference adjustments for a specific location (e.g., North)
      // They must be rotated via directional tuple matrices for each quadrant
      // This applies to ALL arrow types: PRO/ANTI/FLOAT/STATIC/DASH
      const finalAdjustment = this.tupleProcessor.processDirectionalTuples(
        baseAdjustment,
        motionData,
        location
      );

      return new Point(finalAdjustment.x, finalAdjustment.y);
    } catch (error) {
      console.error(
        `Adjustment calculation failed for letter ${letter}: ${String(error)}`
      );
      throw new Error(`Arrow adjustment calculation failed: ${String(error)}`);
    }
  }

  // === PRIVATE METHODS - Consolidated from ArrowAdjustmentLookup ===

  private async getBaseAdjustment(
    pictographData: PictographData,
    motionData: MotionData,
    letter: string,
    arrowColor?: string
  ): Promise<Point> {
    /**
     * Get base adjustment using streamlined lookup logic.
     * IDENTICAL to ArrowAdjustmentLookup.getBaseAdjustment()
     */
    if (!letter) {
      throw new Error("Missing letter data for adjustment lookup");
    }

    try {
      // Generate required keys for special placement lookup
      const [, , attrKey] = this.generateLookupKeys(pictographData, motionData);

      // console.log("🎯 Base Adjustment Lookup:", {
      //   letter,
      //   oriKey,
      //   turnsTuple,
      //   attrKey,
      //   arrowColor
      // });

      try {
        const specialAdjustment = await this.lookupSpecialPlacement(
          motionData,
          pictographData,
          arrowColor,
          attrKey
        );

        if (specialAdjustment) {
          // console.log("✅ Found special adjustment:", specialAdjustment);
          return specialAdjustment;
        } else {
          // console.log("⚠️  No special adjustment found, falling back to default");
        }
      } catch (error) {
        console.warn(`Error in special placement lookup for ${letter}:`, error);
      }

      // STEP 2: Fall back to default calculation
      const defaultAdjustment = await this.calculateDefaultAdjustment(
        motionData,
        pictographData
      );
      return defaultAdjustment;
    } catch (error) {
      console.error("Error in base adjustment lookup:", error);
      throw new Error(`Arrow adjustment lookup failed: ${String(error)}`);
    }
  }

  private generateLookupKeys(
    pictographData: PictographData,
    motionData: MotionData
  ): [string, string, string] {
    /**Generate all required keys for special placement lookup.*/
    try {
      const oriKey = this.orientationKeyService.generateOrientationKey(
        motionData,
        pictographData
      );
      const turnsTuple =
        this.turnsTupleService.generateTurnsTuple(pictographData);

      const color = motionData.color;
      const tempArrow = {
        id: "temp",
        arrowLocation: null,
        positionX: 0,
        positionY: 0,
        rotationAngle: 0,
        coordinates: { x: 0, y: 0 },
        svgCenter: { x: 0, y: 0 },
        svgMirrored: false,
        isVisible: true,
        isSelected: false,
      };

      const attrKey = this.attributeKeyService.getKeyFromArrow(
        tempArrow,
        pictographData,
        color
      );

      return [oriKey, turnsTuple.join(","), attrKey];
    } catch (error) {
      console.error("Failed to generate lookup keys:", error);
      throw new Error(`Key generation failed: ${String(error)}`);
    }
  }

  private async lookupSpecialPlacement(
    motionData: MotionData,
    pictographData: PictographData,
    arrowColor?: string,
    attributeKey?: string
  ): Promise<Point | null> {
    /**
     * Look up special placement using exact legacy logic.
     * IDENTICAL to ArrowAdjustmentLookup.lookupSpecialPlacement()
     */
    try {
      const adjustment =
        await this.specialPlacementService.getSpecialAdjustment(
          motionData,
          pictographData,
          arrowColor,
          attributeKey
        );

      if (adjustment) {
        return new Point(adjustment.x, adjustment.y);
      }

      return null;
    } catch (error) {
      console.error("Error in special placement lookup:", error);
      return null;
    }
  }

  private async calculateDefaultAdjustment(
    motionData: MotionData,
    pictographData: PictographData
  ): Promise<Point> {
    /**
     * Calculate default adjustment - IDENTICAL to ArrowAdjustmentLookup.
     */
    try {
      // Use gridMode from motion data
      const gridMode = motionData.gridMode;

      // console.log("🔧 Default Adjustment Calculation:", {
      //   letter: pictographData.letter,
      //   motionType: motionData.motionType,
      //   turns: motionData.turns,
      //   gridMode
      // });

      const keys = await this.defaultPlacementService.getAvailablePlacementKeys(
        motionData.motionType as MotionTypeType,
        gridMode as GridMode
      );
      const defaultPlacements: Record<string, unknown> = Object.fromEntries(
        keys.map((k: string) => [k, true])
      );

      const availableKeys = Object.keys(defaultPlacements);
      // console.log("📋 Available placement keys:", availableKeys);

      const placementKey = this.placementKeyService.generatePlacementKey(
        motionData,
        pictographData,
        availableKeys
      );
      // console.log("🔑 Generated placement key:", placementKey);

      const adjustmentPoint =
        await this.defaultPlacementService.getDefaultAdjustment(
          placementKey,
          motionData.turns,
          motionData.motionType as MotionTypeType,
          gridMode as GridMode
        );

      // console.log("✅ Default adjustment result:", adjustmentPoint);
      return new Point(adjustmentPoint.x, adjustmentPoint.y);
    } catch (error) {
      console.error("Error calculating default adjustment:", error);
      throw new Error(`Default adjustment calculation failed: ${String(error)}`);
    }
  }
}

