  calculateDashLocationFromPictographData(
    pictographData: PictographData,
    isBlueArrow: boolean
  ): GridLocation {
    /**
     * High-level method to calculate dash location from pictograph data.
     *
     * This method automatically extracts all necessary parameters from the pictograph data
     * using pictograph analysis, then calls the detailed calculation method.
     */
    // Extract motion data for the specified arrow
    const motion = isBlueArrow
      ? pictographData.motions.blue
      : pictographData.motions.red;
    const otherMotion = isBlueArrow
      ? pictographData.motions.red
      : pictographData.motions.blue;

    if (motion.motionType.toLowerCase() !== "dash") {
      // If not a dash motion, return start location as fallback
      return motion.startLocation;
    }

    // Use analysis service to extract all the parameters
    // For simplified implementation, using basic parameter extraction
    const letterInfo = this.getLetterInfo(pictographData);
    const gridInfo = this.getGridInfo(pictographData);

    // Call the detailed calculation method with all parameters
    return this.calculateDashLocation(
      motion,
      otherMotion,
      letterInfo.letterType,
      gridInfo.gridMode,
      gridInfo.shiftLocation,
      letterInfo.isPhiDash,
      letterInfo.isPsiDash,
      letterInfo.isLambda,
      letterInfo.isLambdaDash
    );
  }

  calculateDashLocation(
    motion: MotionData,
    otherMotion?: MotionData,
    letterType?: LetterType,
    gridMode?: GridMode,
    shiftLocation?: GridLocation,
    isPhiDash = false,
    isPsiDash = false,
    isLambda = false,
    isLambdaDash = false
  ): GridLocation {
    /**
     * Calculate dash arrow location using proven calculation algorithms.
     */

    // Φ_DASH and Ψ_DASH special handling
    if (isPhiDash || isPsiDash) {
      const location = this.getPhiDashPsiDashLocation(motion, otherMotion);
      console.groupEnd();
      return location;
    }

    // Λ (Lambda) zero turns special case
    if (isLambda && motion.turns === 0 && otherMotion) {
      const location = this.getLambdaZeroTurnsLocation(motion, otherMotion);
      console.groupEnd();
      return location;
    }

    // Λ_DASH (Lambda Dash) zero turns special case
    if (isLambdaDash && motion.turns === 0 && otherMotion) {
      const location = this.getLambdaDashZeroTurnsLocation(motion, otherMotion);
      console.groupEnd();
      return location;
    }

    // Zero turns - check for Type 3 or default
    if (motion.turns === 0) {
      const location = this.defaultZeroTurnsDashLocation(
        motion,
        letterType,
        gridMode,
        shiftLocation
      );
      console.groupEnd();
      return location;
    }

    // Non-zero turns
    const location = this.dashLocationNonZeroTurns(motion);
    console.groupEnd();
    return location;
  }

  private getLambdaDashZeroTurnsLocation(
    motion: MotionData,
    otherMotion: MotionData
  ): GridLocation {
    /**Handle Λ_DASH (Lambda Dash) zero turns special case.*/
    const key = `${motion.startLocation},${motion.endLocation},${otherMotion.endLocation}`;
    return (
      this.LAMBDA_DASH_ZERO_TURNS_LOCATION_MAP[key] ?? motion.startLocation
    );
  }

  private getPhiDashPsiDashLocation(
    motion: MotionData,
    otherMotion?: MotionData
  ): GridLocation {
    /**Handle Φ_DASH and Ψ_DASH location calculation.*/
    if (!otherMotion) {
      // Fallback to default logic if missing data
      return this.defaultZeroTurnsDashLocation(motion);
    }

    // Both motions have zero turns
    if (motion.turns === 0 && otherMotion.turns === 0) {
      const key = `${motion.color},${motion.startLocation},${motion.endLocation}`;
      return this.PHI_DASH_PSI_DASH_LOCATION_MAP[key] ?? motion.startLocation;
    }

    // Current motion has zero turns, other doesn't
    if (motion.turns === 0) {
      const oppositeLocation = this.dashLocationNonZeroTurns(otherMotion);
      return this.getOppositeLocation(oppositeLocation);
    }

    // Current motion has non-zero turns
    return this.dashLocationNonZeroTurns(motion);
  }

  private getLambdaZeroTurnsLocation(
    motion: MotionData,
    otherMotion: MotionData
  ): GridLocation {
    /**Handle Λ (Lambda) zero turns special case.*/
    const key = `${motion.startLocation},${motion.endLocation},${otherMotion.endLocation}`;
    return this.LAMBDA_ZERO_TURNS_LOCATION_MAP[key] ?? motion.startLocation;
  }

  private defaultZeroTurnsDashLocation(
    motion: MotionData,
    letterType?: LetterType,
    gridMode?: GridMode,
    shiftLocation?: GridLocation
  ): GridLocation {
    /**Calculate default zero turns dash location.*/

    // Type 3 scenario detection and handling
    if (letterType === LetterType.TYPE3 && gridMode && shiftLocation) {
      return this.calculateDashLocationBasedOnShift(
        motion,
        gridMode,
        shiftLocation
      );
    }

    // Default zero turns mapping
    const key = `${motion.startLocation},${motion.endLocation}`;
    return (
      this.DEFAULT_ZERO_TURNS_DASH_LOCATION_MAP[key] ?? motion.startLocation
    );
  }

  private dashLocationNonZeroTurns(motion: MotionData): GridLocation {
    /**Calculate dash location for non-zero turns.*/
    const rotationDirection = motion.rotationDirection.toLowerCase();
    if (
      rotationDirection === "norotation" ||
      rotationDirection === "none" ||
      rotationDirection === "no_rotation"
    ) {
      // Fallback for no rotation
      return motion.startLocation;
    }

    // Normalize rotation direction to map keys
    // RotationDirection enum uses "cw" and "ccw", but map uses full names
    let normalizedDirection: string;
    if (rotationDirection === "cw" || rotationDirection === "clockwise") {
      normalizedDirection = "clockwise";
    } else if (
      rotationDirection === "ccw" ||
      rotationDirection === "counter_clockwise" ||
      rotationDirection === "counterclockwise"
    ) {
      normalizedDirection = "counter_clockwise";
    } else {
      // If we don't recognize it, default to clockwise
      console.warn(
        `Unrecognized rotation direction: ${rotationDirection}, defaulting to clockwise`
      );
      normalizedDirection = "clockwise";
    }

    const directionMap =
      this.NON_ZERO_TURNS_DASH_LOCATION_MAP[normalizedDirection];
    return directionMap?.[motion.startLocation] ?? motion.startLocation;
  }

  private calculateDashLocationBasedOnShift(
    motion: MotionData,
    gridMode: GridMode,
    shiftLocation: GridLocation
  ): GridLocation {
    /**Calculate Type 3 dash location based on shift arrow location.*/
    const startLocation = motion.startLocation;

    if (gridMode === GridMode.DIAMOND) {
      const key = `${startLocation},${shiftLocation}`;
      return this.DIAMOND_DASH_LOCATION_MAP[key] ?? startLocation;
    } else if (gridMode === GridMode.BOX) {
      const key = `${startLocation},${shiftLocation}`;
      return this.BOX_DASH_LOCATION_MAP[key] ?? startLocation;
    }

    // Fallback to default if grid mode not recognized
    return this.defaultZeroTurnsDashLocation(motion);
  }

  private getOppositeLocation(location: GridLocation): GridLocation {
    /**Get opposite location using proven location mapping.*/
    const oppositeMap: Record<GridLocation, GridLocation> = {
      [GridLocation.NORTH]: GridLocation.SOUTH,
      [GridLocation.SOUTH]: GridLocation.NORTH,
      [GridLocation.EAST]: GridLocation.WEST,
      [GridLocation.WEST]: GridLocation.EAST,
      [GridLocation.NORTHEAST]: GridLocation.SOUTHWEST,
      [GridLocation.SOUTHWEST]: GridLocation.NORTHEAST,
      [GridLocation.SOUTHEAST]: GridLocation.NORTHWEST,
      [GridLocation.NORTHWEST]: GridLocation.SOUTHEAST,
    };
    return oppositeMap[location] || location;
  }

  // Simplified helper methods for extracting information from pictograph data
  private getLetterInfo(pictographData: PictographData): {
    letterType: LetterType;
    isPhiDash: boolean;
    isPsiDash: boolean;
    isLambda: boolean;
    isLambdaDash: boolean;
  } {
    const letter = pictographData.letter;

    // Use the reliable shared getLetterType function instead of regex
    let letterType: LetterType = LetterType.TYPE1; // Default fallback

    if (letter) {
      try {
        // Convert string to Letter enum and get type
        const letterEnum = letter as Letter;
        letterType = getLetterType(letterEnum);
      } catch (error) {
        console.warn(
          `Failed to determine letter type for "${letter}", defaulting to TYPE1:`,
          error
        );
        letterType = LetterType.TYPE1;
      }
    }

    return {
      letterType,
      isPhiDash: letter === Letter.PHI_DASH,
      isPsiDash: letter === Letter.PSI_DASH,
      isLambda: letter === Letter.LAMBDA,
      isLambdaDash: letter === Letter.LAMBDA_DASH,
    };
  }

  private getGridInfo(pictographData: PictographData): {
    gridMode: GridMode;
    shiftLocation?: GridLocation;
  } {
    // Use gridMode directly from motion data (now stored in each motion)
    const gridMode =
      pictographData.motions.blue?.gridMode ??
      pictographData.motions.red?.gridMode ??
      GridMode.DIAMOND;

    const result: { gridMode: GridMode; shiftLocation?: GridLocation } = {
      gridMode,
    };

    // For Type 3 pictographs, detect shift location using the shift arrow
    const blue = pictographData.motions.blue;
    const red = pictographData.motions.red;

    if (blue && red) {
      // Determine which motion is the shift motion (non-dash)
      const blueIsShift = ["pro", "anti", "float"].includes(
        blue.motionType.toLowerCase() || ""
      );
      const redIsShift = ["pro", "anti", "float"].includes(
        red.motionType.toLowerCase() || ""
      );

      let shiftMotion: MotionData | undefined;
      if (blueIsShift && !redIsShift) {
        shiftMotion = blue;
      } else if (redIsShift && !blueIsShift) {
        shiftMotion = red;
      }

      // Calculate shift location if we found a shift motion
      if (shiftMotion) {
        const shiftCalculator = new ShiftLocationCalculator();
        result.shiftLocation = shiftCalculator.calculateLocation(shiftMotion);
      }
    }

    return result;
  }
}

/**
 * Directional Tuple Processor
 *
 * Handles complex directional tuple processing for arrow positioning adjustments.
 * Direct TypeScript port of the Python DirectionalTupleProcessor.
 *
 * This service handles:
 * - Directional tuple generation based on motion data
 * - Complex adjustment processing for different motion types
 *
 * Note: Uses ArrowQuadrantCalculator for quadrant index calculations to avoid duplication.
 */

import type { MotionData } from "$shared";
import { GridLocation } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { Point } from "fabric";
import { inject, injectable } from "inversify";
import type { IDirectionalTupleCalculator } from "../contracts";
import type { IDirectionalTupleProcessor } from "../contracts";
import type { IArrowQuadrantCalculator } from "../../../../arrow/orchestration/services/contracts/IArrowQuadrantCalculator";

@injectable()
export class DirectionalTupleCalculator implements IDirectionalTupleCalculator {
  /**
   * Calculator for directional tuples used in arrow positioning.
   */

  calculateDirectionalTuple(
    _motion: MotionData,
    _location: GridLocation
  ): [number, number] {
    /**
     * Legacy parity: Additional directional tuple is not separately added.
     * The selection is made directly from the generated 4-tuples.
     */
    return [0, 0];
  }

  generateDirectionalTuples(
    motion: MotionData,
    baseX: number,
    baseY: number
  ): Array<[number, number]> {
    /**
     * Generate directional tuples using legacy mappings by motion type, rotation, and inferred grid.
     * Tuple order: indices 0..3 correspond to NE, SE, SW, NW quadrant mapping.
     */

    const mt = String(motion.motionType).toLowerCase();
    const rot = String(motion.rotationDirection).toLowerCase();

    const NE = GridLocation.NORTHEAST;
    const SE = GridLocation.SOUTHEAST;
    const SW = GridLocation.SOUTHWEST;
    const NW = GridLocation.NORTHWEST;
    const N = GridLocation.NORTH;
    const E = GridLocation.EAST;
    const S = GridLocation.SOUTH;
    const W = GridLocation.WEST;

    // Infer grid mode from motion locations (NOT arrow locations)
    // Diamond mode: motion uses cardinals (N, E, S, W) → arrows placed at diagonals
    // Box mode: motion uses diagonals (NE, SE, SW, NW) → arrows placed at cardinals
    const cardinalSet = new Set<GridLocation>([N, E, S, W]);
    const gridIsDiamond =
      cardinalSet.has(motion.startLocation) ||
      cardinalSet.has(motion.endLocation);

    // Helper to normalize rotation keys
    const isCW = rot === "clockwise" || rot === "cw";
    const isCCW = rot === "counter_clockwise" || rot === "ccw";
    const isNoRot = rot === "noRotation";

    // Mapping builders
    const tuple = (a: number, b: number) => [a, b] as [number, number];

    // SHIFT (pro/anti/float)
    const shiftDiamond = () => {
      if (mt === "float") {
        // Handpath-based mapping; approximate via start/end step direction
        const order = [NE, SE, SW, NW];
        const idxStart = order.indexOf(motion.startLocation as GridLocation);
        const idxEnd = order.indexOf(motion.endLocation as GridLocation);
        // Determine cw vs ccw step (1 step cw => cw; else ccw)
        const cwStep = (idxStart + 1) % 4 === idxEnd;
        if (cwStep) {
          return [
            tuple(baseX, baseY),
            tuple(-baseY, baseX),
            tuple(-baseX, -baseY),
            tuple(baseY, -baseX),
          ];
        } else {
          return [
            tuple(-baseY, -baseX),
            tuple(baseX, -baseY),
            tuple(baseY, baseX),
            tuple(-baseX, baseY),
          ];
        }
      }
      if (mt === "pro" && isCW)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      if (mt === "pro" && isCCW)
        return [
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
          tuple(-baseX, baseY),
        ];
      if (mt === "anti" && isCW)
        return [
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
          tuple(-baseX, baseY),
        ];
      if (mt === "anti" && isCCW)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      return [
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
      ];
    };

    const shiftBox = () => {
      if (mt === "float") {
        // Use box cw/ccw from start->end around N,E,S,W order
        const order = [N, E, S, W];
        const idxStart = order.indexOf(motion.startLocation as GridLocation);
        const idxEnd = order.indexOf(motion.endLocation as GridLocation);
        const cwStep = (idxStart + 1) % 4 === idxEnd;
        if (cwStep) {
          return [
            tuple(baseX, baseY),
            tuple(-baseY, baseX),
            tuple(-baseX, -baseY),
            tuple(baseY, -baseX),
          ];
        } else {
          return [
            tuple(-baseY, -baseX),
            tuple(baseX, -baseY),
            tuple(baseY, baseX),
            tuple(-baseX, baseY),
          ];
        }
      }
      if (mt === "pro" && isCW)
        return [
          tuple(-baseX, baseY),
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
        ];
      if (mt === "pro" && isCCW)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      if (mt === "anti" && isCW)
        return [
          tuple(-baseX, baseY),
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
        ];
      if (mt === "anti" && isCCW)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      return [
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
      ];
    };

    // DASH
    const dashDiamond = () => {
      if (isCW)
        return [
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
          tuple(-baseX, baseY),
          tuple(-baseY, -baseX),
        ];
      if (isCCW)
        return [
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
        ];
      if (isNoRot)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
        ];
      return [
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
      ];
    };

    const dashBox = () => {
      if (isCW)
        return [
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
          tuple(baseX, baseY),
        ];
      if (isCCW)
        return [
          tuple(-baseX, baseY),
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
        ];
      if (isNoRot)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      return [
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
        tuple(baseX, baseY),
      ];
    };

    // STATIC
    const staticDiamond = () => {
      if (isCW)
        return [
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
          tuple(-baseX, baseY),
          tuple(-baseY, -baseX),
        ];
      if (isCCW)
        return [
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
        ];
      return [
        tuple(baseX, baseY),
        tuple(-baseX, -baseY),
        tuple(-baseY, baseX),
        tuple(baseY, -baseX),
      ];
    };

    const staticBox = () => {
      if (isCW)
        return [
          tuple(baseX, baseY),
          tuple(-baseY, baseX),
          tuple(-baseX, -baseY),
          tuple(baseY, -baseX),
        ];
      if (isCCW)
        return [
          tuple(-baseY, -baseX),
          tuple(baseX, -baseY),
          tuple(baseY, baseX),
          tuple(-baseX, baseY),
        ];
      return [
        tuple(baseX, baseY),
        tuple(-baseY, baseX),
        tuple(-baseX, -baseY),
        tuple(baseY, -baseX),
      ];
    };

    // Dispatch by motion type and grid
    if (mt === "dash") {
      return gridIsDiamond ? dashDiamond() : dashBox();
    } else if (mt === "static") {
      return gridIsDiamond ? staticDiamond() : staticBox();
    } else {
      // pro/anti/float
      return gridIsDiamond ? shiftDiamond() : shiftBox();
    }
  }
}

@injectable()
