  /**
   * Calculate direction using Letter I's special maps.
   */
  calculate(motionData: MotionData): VectorDirection | null {
    const isRadial = this.orientationChecker.isRadial();
    const endLocation = getEndLocation(motionData);

    const map = isRadial ? LETTER_I_RADIAL_MAP : LETTER_I_NON_RADIAL_MAP;
    return map[endLocation as Loc][motionData.color as MotionColor] || null;
  }
}

/**
 * Prop SVG Loader Service - OPTIMIZED (2025 Best Practices)
 *
 * Fast, direct SVG loading for props with aggressive caching.
 *
 * Key optimizations:
 * - Multi-level caching (raw SVG + transformed SVG by color)
 * - Request deduplication (prevents duplicate concurrent fetches)
 * - Cached metadata parsing (viewBox, center)
 * - Performance monitoring (cache hit/miss tracking)
 */

import type { MotionData } from "../../../shared/domain/models/MotionData";
import type { PropPlacementData } from "../../domain/models/PropPlacementData";
import { injectable } from "inversify";
import type { PropRenderData } from "../../domain/models/PropRenderData";
import type { IPropSvgLoader } from "../contracts/IPropSvgLoader";
import { MotionColor } from "../../../shared/domain/enums/pictograph-enums";

@injectable()
export class PropSvgLoader implements IPropSvgLoader {
  // 🚀 OPTIMIZATION: Multi-level caching
  private rawSvgCache = new Map<string, string>(); // path -> raw SVG text
  private transformedSvgCache = new Map<string, PropRenderData>(); // path:color -> transformed data
  private loadingPromises = new Map<string, Promise<string>>(); // path -> loading promise (deduplication)
  private metadataCache = new Map<
    string,
    {
      viewBox: { width: number; height: number };
      center: { x: number; y: number };
    }
  >();

  // Performance monitoring
  private cacheHits = 0;
  private cacheMisses = 0;
  /**
   * Load prop SVG data with color transformation
   * 🚀 OPTIMIZED: Checks transformed cache first, then raw cache, then fetches
   * @param propData - Prop placement data
   * @param motionData - Motion data including prop type
   * @param useAnimatedVersion - If true, loads {propType}_animated.svg for animation canvas (300px width)
   */
  async loadPropSvg(
    propData: PropPlacementData,
    motionData: MotionData,
    useAnimatedVersion: boolean = false
  ): Promise<PropRenderData> {
    try {
      // Get prop type and color
      const propType = motionData.propType || "staff";
      const color = motionData.color || MotionColor.BLUE;

      // Create cache key including color for transformed prop cache
      // Use _animated version for animation canvas (scaled to 300px width)
      const suffix = useAnimatedVersion ? "_animated" : "";
      const path = `/images/props/${propType}${suffix}.svg`;
      const transformedCacheKey = `${path}:${color}`;

      // 🚀 OPTIMIZATION: Check transformed cache first (fastest path)
      if (this.transformedSvgCache.has(transformedCacheKey)) {
        this.cacheHits++;
        const cached = this.transformedSvgCache.get(transformedCacheKey)!;
        // Return with updated position/rotation (these are per-instance)
        return {
          ...cached,
          position: { x: propData.positionX, y: propData.positionY },
          rotation: propData.rotationAngle,
        };
      }

      this.cacheMisses++;

      // Fetch raw SVG (uses raw cache + deduplication)
      const originalSvgText = await this.fetchSvgContentCached(path);

      // Parse SVG for viewBox and center (uses metadata cache)
      const { viewBox, center } = this.parsePropSvgCached(
        originalSvgText,
        path
      );

      // Apply color transformation
      const coloredSvgText = this.applyColorToSvg(originalSvgText, color);

      // Extract SVG content
      const svgContent = this.extractSvgContent(coloredSvgText);

      const result: PropRenderData = {
        position: { x: propData.positionX, y: propData.positionY },
        rotation: propData.rotationAngle,
        svgData: {
          svgContent,
          viewBox,
          center,
        },
        loaded: true,
        error: null,
      };

      // 🚀 OPTIMIZATION: Cache transformed result
      this.transformedSvgCache.set(transformedCacheKey, result);

      return result;
    } catch (error) {
      console.error("❌ PropSvgLoader: Error loading prop SVG:", error);
      return {
        position: { x: 475, y: 475 },
        rotation: 0,
        svgData: null,
        loaded: false,
        error: error instanceof Error ? error.message : "Unknown error",
      };
    }
  }

  /**
   * 🚀 NEW: Fetch SVG content with caching and deduplication
   */
  private async fetchSvgContentCached(path: string): Promise<string> {
    // Check raw SVG cache
    if (this.rawSvgCache.has(path)) {
      return this.rawSvgCache.get(path)!;
    }

    // Check if already loading (prevents duplicate concurrent requests)
    if (this.loadingPromises.has(path)) {
      return this.loadingPromises.get(path)!;
    }

    // Create loading promise
    const loadingPromise = this.fetchSvgContent(path);
    this.loadingPromises.set(path, loadingPromise);

    try {
      const svgText = await loadingPromise;

      // Cache the raw SVG
      this.rawSvgCache.set(path, svgText);

      // Clean up loading promise
      this.loadingPromises.delete(path);

      return svgText;
    } catch (error) {
      // Clean up on error
      this.loadingPromises.delete(path);
      throw error;
    }
  }

  /**
   * Fetch SVG content from a given path - direct fetch
   */
  async fetchSvgContent(path: string): Promise<string> {
    const response = await fetch(path);
    if (!response.ok) {
      throw new Error(`Failed to fetch SVG: ${response.status}`);
    }
    return await response.text();
  }

  /**
   * 🚀 NEW: Parse prop SVG with caching
   */
  private parsePropSvgCached(
    svgText: string,
    cacheKey: string
  ): {
    viewBox: { width: number; height: number };
    center: { x: number; y: number };
  } {
    // Check metadata cache
    if (this.metadataCache.has(cacheKey)) {
      return this.metadataCache.get(cacheKey)!;
    }

    // Parse and cache
    const result = this.parsePropSvg(svgText);
    this.metadataCache.set(cacheKey, result);
    return result;
  }

  /**
   * Parse prop SVG to extract viewBox and center
   */
  private parsePropSvg(svgText: string): {
    viewBox: { width: number; height: number };
    center: { x: number; y: number };
  } {
    const parser = new DOMParser();
    const doc = parser.parseFromString(svgText, "image/svg+xml");
    const svgElement = doc.querySelector("svg");

    if (!svgElement) {
      throw new Error("Invalid SVG: No SVG element found");
    }

    // Extract viewBox
    const viewBoxAttr = svgElement.getAttribute("viewBox");
    let width = 100,
      height = 100;

    if (viewBoxAttr) {
      const [, , w, h] = viewBoxAttr.split(" ").map(Number);
      width = w || 100;
      height = h || 100;
    }

    return {
      viewBox: { width, height },
      center: { x: width / 2, y: height / 2 },
    };
  }

  /**
   * Apply color transformation to SVG - sophisticated approach matching arrows
   * Simple and correct: props are blue by default, change to red when needed
   * Also makes CSS class names unique to prevent conflicts between different colored props
   * Preserves accent colors like tan/gold for special prop features (e.g., minihoop grip)
   */
  private applyColorToSvg(svgText: string, color: MotionColor): string {
    const colorMap: Record<MotionColor, string> = {
      [MotionColor.BLUE]: "#2E3192",
      [MotionColor.RED]: "#ED1C24",
    };

    const targetColor = colorMap[color] || colorMap[MotionColor.BLUE];

    // Accent colors to preserve (like minihoop's gold/tan grip)
    const ACCENT_COLORS_TO_PRESERVE = [
      "#c9ac68", // Gold/tan color used for minihoop grip
    ];

    // Replace fill colors in both attribute and CSS style formats
    // BUT preserve accent colors and transparent fills
    let coloredSvg = svgText.replace(
      /fill="(#[0-9A-Fa-f]{3,6})"/gi,
      (match: string, capturedColor: string) => {
        const colorLower = capturedColor.toLowerCase();
        // Preserve accent colors
        if (
          ACCENT_COLORS_TO_PRESERVE.some(
            (accent) => accent.toLowerCase() === colorLower
          )
        ) {
          return match;
        }
        return `fill="${targetColor}"`;
      }
    );

    coloredSvg = coloredSvg.replace(
      /fill:\s*(#[0-9A-Fa-f]{3,6})/gi,
      (match: string, capturedColor: string) => {
        const colorLower = capturedColor.toLowerCase();
        // Preserve accent colors
        if (
          ACCENT_COLORS_TO_PRESERVE.some(
            (accent) => accent.toLowerCase() === colorLower
          )
        ) {
          return match;
        }
        return `fill:${targetColor}`;
      }
    );

    // Make CSS class names unique for each color to prevent conflicts
    // Replace .st0, .st1, etc. with .st0-blue, .st1-blue, etc.
    const colorSuffix = color.toLowerCase();
    coloredSvg = coloredSvg.replace(/\.st(\d+)/g, `.st$1-${colorSuffix}`);

    // Also update class references in elements
    coloredSvg = coloredSvg.replace(
      /class="st(\d+)"/g,
      `class="st$1-${colorSuffix}"`
    );

    // Remove the centerPoint circle entirely to prevent unwanted visual elements
    coloredSvg = coloredSvg.replace(
      /<circle[^>]*id="centerPoint"[^>]*\/?>/,
      ""
    );

    return coloredSvg;
  }

  /**
   * Extract SVG content (remove outer SVG wrapper)
   */
  private extractSvgContent(svgText: string): string {
    const parser = new DOMParser();
    const doc = parser.parseFromString(svgText, "image/svg+xml");
    const svgElement = doc.querySelector("svg");

    if (!svgElement) {
      return svgText;
    }

    return svgElement.innerHTML;
  }

  /**
   * 🚀 NEW: Clear caches (useful for testing or memory management)
   */
  clearCache(): void {
    this.rawSvgCache.clear();
    this.transformedSvgCache.clear();
    this.loadingPromises.clear();
    this.metadataCache.clear();
    this.cacheHits = 0;
    this.cacheMisses = 0;
  }

  /**
   * 🚀 NEW: Get cache statistics for performance monitoring
   */
  getCacheStats() {
    return {
      rawCacheSize: this.rawSvgCache.size,
      transformedCacheSize: this.transformedSvgCache.size,
      metadataCacheSize: this.metadataCache.size,
      cacheHits: this.cacheHits,
      cacheMisses: this.cacheMisses,
      hitRate:
        this.cacheHits + this.cacheMisses > 0
          ? (
              (this.cacheHits / (this.cacheHits + this.cacheMisses)) *
              100
            ).toFixed(2) + "%"
          : "0%",
    };
  }
}

/**
 * Static/Dash Motion Handler Implementation
 */

import type { MotionColor, VectorDirection } from "$shared";
import { GridLocation, GridMode, type MotionData } from "$shared";
import type { BoxLoc, DiamondLoc } from "../../domain/direction/DirectionMaps";
import {
  BOX_NON_RADIAL_MAP,
  BOX_RADIAL_MAP,
  DIAMOND_NON_RADIAL_MAP,
  DIAMOND_RADIAL_MAP,
} from "../../domain/direction/DirectionMaps";
import type { IDirectionCalculator } from "../contracts/IDirectionCalculator";
import type { IOrientationChecker } from "../contracts/IOrientationChecker";

export class StaticDashMotionHandler implements IDirectionCalculator {
  constructor(private orientationChecker: IOrientationChecker) {}

  calculate(motionData: MotionData): VectorDirection | null {
    const location = motionData.endLocation;
    const gridMode = this.getGridMode(location);
    const isRadial = this.orientationChecker.isRadial();

    if (gridMode === GridMode.DIAMOND) {
      const map = isRadial ? DIAMOND_RADIAL_MAP : DIAMOND_NON_RADIAL_MAP;
      return (
        map[location as DiamondLoc][motionData.color as MotionColor] || null
      );
    }

    const map = isRadial ? BOX_RADIAL_MAP : BOX_NON_RADIAL_MAP;
    return map[location as BoxLoc][motionData.color as MotionColor] || null;
  }

  private getGridMode(location: string): GridMode {
    const cardinalLocations = [
      GridLocation.NORTH,
      GridLocation.SOUTH,
      GridLocation.EAST,
      GridLocation.WEST,
    ] as string[];
    return cardinalLocations.includes(location)
      ? GridMode.DIAMOND
      : GridMode.BOX;
  }
}

/**
 * Pictograph Prop State
 *
 * Manages prop positioning, assets, and lifecycle with user-selected prop types.
 * Independent sub-state - no dependencies on arrow state.
 */

import type { PictographData, PropType, MotionData } from "$shared";
import type { PropAssets, PropPosition } from "../domain/models";
import type { IPropPlacementService } from "../services/contracts/IPropPlacementService";
import type { IPropSvgLoader } from "../services/contracts/IPropSvgLoader";
import type { IPropTypeConfigurationService } from "../services/contracts/IPropTypeConfigurationService";

export interface PropState {
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;
  calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void>;
}

export function createPropState(
  propSvgLoader: IPropSvgLoader,
  propPlacementService: IPropPlacementService,
  propTypeConfigService: IPropTypeConfigurationService
): PropState {
  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  async function calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void> {
    if (!pictographData || !pictographData.motions) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Use the prop type configuration service to map UI type to filename
      const propTypeFilename =
        propTypeConfigService.mapPropTypeToFilename(userPropType);

      // Use the service to create pictograph data with all props using user's selected type
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData =
        propTypeConfigService.applyPropTypeToPictographData(
          pictographData,
          userPropType
        );

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(pictographData.motions).map(
        async ([color, motionData]) => {
          try {
            if (!motionData) {
              throw new Error("Motion data is undefined");
            }
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // This ensures all props render as the user's chosen type
            const motionDataWithUserProp: MotionData = {
              motionType: motionData.motionType,
              rotationDirection: motionData.rotationDirection,
              startLocation: motionData.startLocation,
              endLocation: motionData.endLocation,
              turns: motionData.turns,
              startOrientation: motionData.startOrientation,
              endOrientation: motionData.endOrientation,
              isVisible: motionData.isVisible,
              propType: propTypeFilename as PropType,
              arrowLocation: motionData.arrowLocation,
              color: motionData.color,
              gridMode: motionData.gridMode,
              arrowPlacementData: motionData.arrowPlacementData,
              propPlacementData: motionData.propPlacementData,
              ...(motionData.prefloatMotionType !== undefined && {
                prefloatMotionType: motionData.prefloatMotionType,
              }),
              ...(motionData.prefloatRotationDirection !== undefined && {
                prefloatRotationDirection: motionData.prefloatRotationDirection,
              }),
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp
              ),
              propPlacementService.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssetsData = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssetsData;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        }
      );

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("⚠️ Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("❌ Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  return {
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },
    calculatePropPositions,
  };
}

/**
 * Data Transformation Service
 *
 * Handles data transformation and adaptation between different formats.
 * Extracted from PictographRenderingService.
 */

import { injectable } from "inversify";
import type { BeatData } from "../../../../../modules/create/shared/domain/models/BeatData";
import type { GridMode } from "../../../grid/domain/enums/grid-enums";
import type {
  GridData,
  GridPointData as RawGridData,
} from "../../../grid/domain/models/grid-models";
import { createPictographData } from "../../domain/factories/createPictographData";
import type { MotionData, PictographData } from "../../domain/models";
import type { IDataTransformer } from "../contracts/IDataTransformer";

// Interface moved to contracts/IDataTransformer.ts

@injectable()
export class DataTransformer implements IDataTransformer {
  /**
   * Convert beat data to pictograph data
   */
  beatToPictographData(beat: BeatData): PictographData {
    const motions: Record<string, MotionData> = {};
    if (beat.motions["blue"]) motions["blue"] = beat.motions["blue"];
    if (beat.motions["red"]) motions["red"] = beat.motions["red"];
    return createPictographData({
      id: `beat-${beat.beatNumber}`,
      motions,
      letter: beat.letter ?? null,
    });
  }

  /**
   * Adapt raw grid data to match the interface requirements
   */
  adaptGridData(rawGridData: RawGridData, mode: GridMode): GridData {
    // Filter out null coordinates and adapt to interface
    const adaptPoints = (
      points: Record<string, { coordinates: { x: number; y: number } | null }>
    ) => {
      const adapted: Record<string, { coordinates: { x: number; y: number } }> =
        {};
      for (const [key, point] of Object.entries(points)) {
        if (point.coordinates) {
          adapted[key] = { coordinates: point.coordinates };
        }
      }
      return adapted;
    };

    return {
      gridMode: mode,
      centerX: 0.0,
      centerY: 0.0,
      radius: 100.0,
      gridPointData: {
        allLayer2PointsNormal: adaptPoints(
          rawGridData.allLayer2PointsNormal ? rawGridData.allLayer2PointsNormal : {}
        ),
        allHandPointsNormal: adaptPoints(rawGridData.allHandPointsNormal ? rawGridData.allHandPointsNormal : {}),
        allHandPointsStrict: {},
        allLayer2PointsStrict: {},
        allOuterPoints: {},
        centerPoint: { coordinates: { x: 475, y: 475 } },
      },
    };
  }
}

/**
 * Pictograph State Management - Svelte 5 runes
 *
 * Reactive state management for pictograph rendering and arrow positioning.
 * Wraps pictograph services with runes for UI reactivity.
 * Follows TKA architecture: services handle business logic, runes handle reactivity.
 */

import type {
  IComponentManagementService,
  IDataTransformationService,
  MotionColor,
  MotionData,
  PictographData,
} from "$shared";
import { resolve, PropType } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { loadSharedModules } from "$shared/inversify/container";
import { getSettings } from "../../../application/state/app-state.svelte";
import type { ArrowAssets } from "../../arrow/orchestration/domain/arrow-models";
import type { IArrowLifecycleManager } from "../../arrow/orchestration/services/contracts/IArrowLifecycleManager";
import type { PropAssets, PropPosition } from "../../prop/domain/models";
import type { IPropSvgLoader } from "../../prop/services/contracts/IPropSvgLoader";
import type { IPropPlacementService } from "../../prop/services/contracts/IPropPlacementService";

export interface PictographState {
  // Data state
  readonly effectivePictographData: PictographData | null;
  readonly hasValidData: boolean;
  readonly displayLetter: string | null;
  readonly motionsToRender: Array<{
    color: MotionColor;
    motionData: MotionData;
  }>;
  readonly requiredComponents: string[];

  // Arrow positioning state
  readonly arrowPositions: Record<
    string,
    { x: number; y: number; rotation: number }
  >;
  readonly arrowMirroring: Record<string, boolean>;
  readonly arrowAssets: Record<string, ArrowAssets>;
  readonly showArrows: boolean;

  // Prop positioning state
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;

  // Loading state
  readonly isLoading: boolean;
  readonly isLoaded: boolean;
  readonly errorMessage: string | null;
  readonly loadedComponents: Set<string>;
  readonly allComponentsLoaded: boolean;

  // Actions
  calculateArrowPositions(): Promise<void>;
  calculatePropPositions(): Promise<void>;
  handleComponentLoaded(componentName: string): void;
  handleComponentError(componentName: string, error: string): void;
  clearLoadingState(): void;
  updatePictographData(newData: PictographData | null): void;
}

/**
 * Creates reactive state for pictograph rendering and management
 */
export function createPictographState(
  initialPictographData: PictographData | null = null
): PictographState {
  // Services will be resolved asynchronously to avoid container initialization errors
  let dataTransformationService: IDataTransformationService | null = null;
  let componentManagementService: IComponentManagementService | null = null;
  let arrowLifecycleManager: IArrowLifecycleManager | null = null;
  let propSvgLoader: IPropSvgLoader | null = null;
  let propPlacementService: IPropPlacementService | null = null;
  let servicesInitialized = $state(false);

  // Initialize services asynchronously
  async function initializeServices() {
    // Skip if already initialized or currently initializing
    if (servicesInitialized || initializationPromise) {
      await initializationPromise;
      return;
    }

    initializationPromise = (async () => {
      try {
        // CRITICAL: Ensure Tier 2 (pictograph module) is fully loaded before resolving services
        // This prevents race conditions where services are accessed before bindings exist
        await loadSharedModules();

        dataTransformationService = resolve<IDataTransformationService>(
          TYPES.IDataTransformationService
        );
        componentManagementService = resolve<IComponentManagementService>(
          TYPES.IComponentManagementService
        );
        arrowLifecycleManager = resolve<IArrowLifecycleManager>(TYPES.IArrowLifecycleManager);
        propSvgLoader = resolve<IPropSvgLoader>(TYPES.IPropSvgLoader);
        propPlacementService = resolve<IPropPlacementService>(TYPES.IPropPlacementService);
        servicesInitialized = true;
      } catch (error) {
        console.error("Failed to initialize pictograph services:", error);
        errorMessage = `Service initialization failed: ${String(error)}`;
        initializationPromise = null; // Reset so we can retry
        throw error;
      }
    })();

    await initializationPromise;
  }

  // Track initialization promise to prevent duplicate initialization
  let initializationPromise: Promise<void> | null = null;

  // Helper to ensure services are initialized before use
  async function ensureServicesInitialized() {
    if (!servicesInitialized) {
      await initializeServices();
    }
  }

  // Input data state
  let pictographData = $state<PictographData | null>(initialPictographData);

  // Component loading state
  let errorMessage = $state<string | null>(null);
  let loadedComponents = $state(new Set<string>());

  // Arrow positioning state
  let arrowPositions = $state<
    Record<string, { x: number; y: number; rotation: number }>
  >({});
  let arrowMirroring = $state<Record<string, boolean>>({});
  let arrowAssets = $state<Record<string, ArrowAssets>>({});
  let showArrows = $state(false);

  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  // Global prop type from settings - reactive to settings changes
  let currentPropType = $state<string | undefined>(undefined);

  // Watch for prop type changes in settings and trigger re-render
  $effect(() => {
    const settings = getSettings();
    const newPropType = settings.propType;

    // Only trigger recalculation if prop type actually changed and we have valid data
    if (newPropType !== currentPropType && dataState.hasValidData) {
      currentPropType = newPropType;
      void calculatePropPositions();
    } else if (currentPropType === undefined) {
      // Initialize on first run
      currentPropType = newPropType;
    }
  });

  // Derived data transformation state - only when services are ready
  const dataState = $derived.by(() => {
    if (!servicesInitialized || !dataTransformationService) {
      return {
        hasValidData: false,
        effectivePictographData: null,
        transformedData: null,
      };
    }
    return dataTransformationService.transformPictographData(pictographData);
  });

  // Derived component requirements - only when services are ready
  const requiredComponents = $derived.by(() => {
    if (!servicesInitialized || !componentManagementService) {
      return [];
    }
    return componentManagementService.getRequiredComponents(pictographData);
  });

  // Derived loading states
  const allComponentsLoaded = $derived.by(() => {
    return requiredComponents.every((component: string) =>
      loadedComponents.has(component)
    );
  });

  const isLoading = $derived.by(() => {
    return dataState.hasValidData && !allComponentsLoaded;
  });

  const isLoaded = $derived.by(() => {
    return dataState.hasValidData && allComponentsLoaded;
  });

  // Effect to recalculate positions when data changes
  $effect(() => {
    const currentData = dataState.effectivePictographData;
    if (currentData) {
      errorMessage = null;

      // Explicitly track motion data properties that affect positioning
      // This ensures recalculation when turns/orientations change (e.g., via edit panel)
      const redMotion = currentData.motions.red;
      const blueMotion = currentData.motions.blue;

      // Track key properties that affect beta offset calculations:
      // - endOrientation (radial vs non-radial determines if offset is applied)
      // - endLocation (both props must end at same location for beta offset)
      // - turns (changes orientation, which affects offset logic)
      if (redMotion) {
        void redMotion.endOrientation;
        void redMotion.endLocation;
        void redMotion.turns;
      }
      if (blueMotion) {
        void blueMotion.endOrientation;
        void blueMotion.endLocation;
        void blueMotion.turns;
      }

      // Don't clear loadedComponents - keep elements visible during transitions
      // Recalculate arrow and prop positions when data changes
      void calculateArrowPositions();
      void calculatePropPositions();
    }
  });

  // Actions
  async function calculateArrowPositions(): Promise<void> {
    // Ensure services are initialized before attempting calculations
    await ensureServicesInitialized();

    const currentData = dataState.effectivePictographData;

    if (
      !currentData?.motions ||
      !servicesInitialized ||
      !arrowLifecycleManager
    ) {
      // Only clear if we don't have valid data - don't clear during transitions
      arrowPositions = {};
      arrowMirroring = {};
      arrowAssets = {};
      showArrows = true;
      return;
    }

    try {
      // Use the arrow lifecycle manager to coordinate complete arrow loading
      const arrowLifecycleResult =
        await arrowLifecycleManager.coordinateArrowLifecycle(currentData);

      // Only update state after async loading completes - keeps old data visible during transitions
      arrowPositions = arrowLifecycleResult.positions;
      arrowMirroring = arrowLifecycleResult.mirroring;
      arrowAssets = arrowLifecycleResult.assets;
      showArrows =
        arrowLifecycleResult.allReady &&
        Object.keys(arrowLifecycleResult.positions).length > 0;

      // Log any errors
      if (Object.keys(arrowLifecycleResult.errors).length > 0) {
        console.warn(
          "⚠️ Arrow lifecycle had errors:",
          arrowLifecycleResult.errors
        );
      }
    } catch (error) {
      console.error("❌ Arrow lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      arrowPositions = {};
      arrowMirroring = {};
      arrowAssets = {};
      showArrows = false;
    }
  }

  async function calculatePropPositions(): Promise<void> {
    // Ensure services are initialized before attempting calculations
    await ensureServicesInitialized();

    const currentData = dataState.effectivePictographData;

    if (
      !currentData?.motions ||
      !servicesInitialized ||
      !propSvgLoader ||
      !propPlacementService
    ) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Get the user's selected prop type from settings
      const settings = getSettings();
      const selectedPropType = settings.propType ?? "Staff";

      // Map PropTypeTab IDs to actual filenames
      // PropTypeTab uses capitalized IDs, but filenames have specific formats
      const propTypeMapping: Record<string, string> = {
        Staff: "staff",
        Simplestaff: "simple_staff",
        Club: "club",
        Fan: "fan",
        Triad: "triad",
        Minihoop: "minihoop",
        Buugeng: "buugeng",
        Triquetra: "triquetra",
        Sword: "sword",
        Chicken: "chicken",
        Hand: "hand",
        Guitar: "guitar",
        Ukulele: "ukulele",
      };

      const userPropType =
        propTypeMapping[selectedPropType] || selectedPropType.toLowerCase();

      // Create an updated pictographData with all props set to user's selected type
      // UNLESS they're already set to "hand" (from hand path assembly mode)
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData = {
        ...currentData,
        motions: {
          red: currentData.motions.red
            ? {
                ...currentData.motions.red,
                propType: (currentData.motions.red.propType === PropType.HAND
                  ? PropType.HAND
                  : userPropType) as PropType,
              }
            : currentData.motions.red,
          blue: currentData.motions.blue
            ? {
                ...currentData.motions.blue,
                propType: (currentData.motions.blue.propType === PropType.HAND
                  ? PropType.HAND
                  : userPropType) as PropType,
              }
            : currentData.motions.blue,
        },
      };

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(currentData.motions)
        .filter(
          (entry): entry is [string, MotionData] => entry[1] !== undefined
        )
        .map(async ([color, motionData]: [string, MotionData]) => {
          try {
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // UNLESS it's already set to "hand" (from hand path assembly mode)
            // This ensures hand path assembly always uses hands regardless of user settings
            // Cast to PropType - PropSvgLoader uses it as a string for the path anyway
            const motionDataWithUserProp: MotionData = {
              ...motionData,
              propType: (motionData.propType === PropType.HAND
                ? PropType.HAND
                : userPropType) as PropType,
              motionType: motionData.motionType,
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader!.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp
              ),
              propPlacementService!.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssets = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssets;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        });

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("⚠️ Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("❌ Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  function handleComponentLoaded(componentName: string): void {
    loadedComponents.add(componentName);
    loadedComponents = new Set(loadedComponents); // Trigger reactivity
  }

  function handleComponentError(componentName: string, error: string): void {
    console.error(`❌ Component ${componentName} failed to load:`, error);
    errorMessage = `Failed to load ${componentName}: ${error}`;
  }

  function clearLoadingState(): void {
    errorMessage = null;
    loadedComponents.clear();
    loadedComponents = new Set(); // Trigger reactivity
  }

  // Update pictograph data (for external updates)
  function updatePictographData(newData: PictographData | null): void {
    pictographData = newData;
  }

  return {
    // Data state (derived)
    get effectivePictographData() {
      return dataState.effectivePictographData;
    },
    get hasValidData() {
      return dataState.hasValidData;
    },
    get displayLetter() {
      const data = dataState;
      return "displayLetter" in data ? data.displayLetter : null;
    },
    get motionsToRender() {
      const data = dataState;
      return "motionsToRender" in data ? data.motionsToRender : [];
    },
    get requiredComponents() {
      return requiredComponents;
    },

    // Arrow positioning state
    get arrowPositions() {
      return arrowPositions;
    },
    get arrowMirroring() {
      return arrowMirroring;
    },
    get arrowAssets() {
      return arrowAssets;
    },
    get showArrows() {
      return showArrows;
    },

    // Prop positioning state
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },

    // Loading state
    get isLoading() {
      return isLoading;
    },
    get isLoaded() {
      return isLoaded;
    },
    get errorMessage() {
      return errorMessage;
    },
    get loadedComponents() {
      return loadedComponents;
    },
    get allComponentsLoaded() {
      return allComponentsLoaded;
    },

    // Actions
    calculateArrowPositions,
    calculatePropPositions,
    handleComponentLoaded,
    handleComponentError,
    clearLoadingState,
    updatePictographData,
  };
}

/**
 * Pictograph Prop State
 *
 * Manages prop positioning, assets, and lifecycle with user-selected prop types.
 * Independent sub-state - no dependencies on arrow state.
 */

import type { PictographData, PropType, MotionData } from "$shared";
import type { PropAssets, PropPosition } from "../../../prop/domain/models";
import type { IPropPlacementService } from "../../../prop/services/contracts/IPropPlacementService";
import type { IPropSvgLoader } from "../../../prop/services/contracts/IPropSvgLoader";
import type { IPropTypeConfigurationService } from "../../../prop/services/contracts/IPropTypeConfigurationService";

export interface PictographPropState {
  readonly propPositions: Record<string, PropPosition>;
  readonly propAssets: Record<string, PropAssets>;
  readonly showProps: boolean;
  calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void>;
}

export function createPictographPropState(
  propSvgLoader: IPropSvgLoader,
  propPlacementService: IPropPlacementService,
  propTypeConfigService: IPropTypeConfigurationService,
  useAnimatedProps: boolean = false
): PictographPropState {
  // Prop positioning state
  let propPositions = $state<Record<string, PropPosition>>({});
  let propAssets = $state<Record<string, PropAssets>>({});
  let showProps = $state(false);

  async function calculatePropPositions(
    pictographData: PictographData | null,
    userPropType: string
  ): Promise<void> {
    if (!pictographData || !pictographData.motions) {
      // Only clear if we don't have valid data - don't clear during transitions
      propPositions = {};
      propAssets = {};
      showProps = true;
      return;
    }

    try {
      const positions: Record<string, PropPosition> = {};
      const assets: Record<string, PropAssets> = {};
      const errors: Record<string, string> = {};

      // Use the prop type configuration service to map UI type to filename
      const propTypeFilename =
        propTypeConfigService.mapPropTypeToFilename(userPropType);

      // Use the service to create pictograph data with all props using user's selected type
      // This ensures beta offset logic sees the correct prop types
      const updatedPictographData =
        propTypeConfigService.applyPropTypeToPictographData(
          pictographData,
          userPropType
        );

      // Process all motions in parallel for better performance
      const motionPromises = Object.entries(pictographData.motions).map(
        async ([color, motionData]) => {
          try {
            if (!motionData) {
              throw new Error("Motion data is undefined");
            }
            if (!motionData.propPlacementData) {
              throw new Error("No prop placement data available");
            }

            // Override the prop type with the user's selected type from settings
            // This ensures all props render as the user's chosen type
            const motionDataWithUserProp: MotionData = {
              motionType: motionData.motionType,
              rotationDirection: motionData.rotationDirection,
              startLocation: motionData.startLocation,
              endLocation: motionData.endLocation,
              turns: motionData.turns,
              startOrientation: motionData.startOrientation,
              endOrientation: motionData.endOrientation,
              isVisible: motionData.isVisible,
              propType: propTypeFilename as PropType,
              arrowLocation: motionData.arrowLocation,
              color: motionData.color,
              gridMode: motionData.gridMode,
              arrowPlacementData: motionData.arrowPlacementData,
              propPlacementData: motionData.propPlacementData,
              ...(motionData.prefloatMotionType !== undefined && {
                prefloatMotionType: motionData.prefloatMotionType,
              }),
              ...(motionData.prefloatRotationDirection !== undefined && {
                prefloatRotationDirection: motionData.prefloatRotationDirection,
              }),
            };

            // Load assets and calculate position in parallel
            // IMPORTANT: Pass updatedPictographData so beta offset logic sees all props with user's type
            const [renderData, placementData] = await Promise.all([
              propSvgLoader.loadPropSvg(
                motionData.propPlacementData,
                motionDataWithUserProp,
                useAnimatedProps
              ),
              propPlacementService.calculatePlacement(
                updatedPictographData,
                motionDataWithUserProp
              ),
            ]);

            if (!renderData.svgData) {
              throw new Error("Failed to load prop SVG data");
            }

            // Transform to expected format
            const propAssetsData = {
              imageSrc: renderData.svgData.svgContent,
              viewBox: `${renderData.svgData.viewBox.width} ${renderData.svgData.viewBox.height}`,
              center: renderData.svgData.center,
            };

            const position = {
              x: placementData.positionX,
              y: placementData.positionY,
              rotation: placementData.rotationAngle,
            };

            positions[color] = position;
            assets[color] = propAssetsData;
          } catch (error) {
            const errorMessage =
              error instanceof Error ? error.message : "Unknown error";
            errors[color] = errorMessage;
          }
        }
      );

      await Promise.all(motionPromises);

      // Only update state after async loading completes - keeps old data visible during transitions
      propPositions = positions;
      propAssets = assets;
      showProps =
        Object.keys(errors).length === 0 && Object.keys(positions).length > 0;

      // Log any errors
      if (Object.keys(errors).length > 0) {
        console.warn("⚠️ Prop lifecycle had errors:", errors);
      }
    } catch (error) {
      console.error("❌ Prop lifecycle coordination failed:", error);
      // Only clear on error - keeps old data visible if loading fails
      propPositions = {};
      propAssets = {};
      showProps = false;
    }
  }

  return {
    get propPositions() {
      return propPositions;
    },
    get propAssets() {
      return propAssets;
    },
    get showProps() {
      return showProps;
    },
    calculatePropPositions,
  };
}

/**
 * Letter Border Colors
 *
 * Clean utility for getting letter border colors based on the desktop app's
 * PictographBorderManager dual border system.
 */

import type { Letter } from "$shared";
import { getLetterType } from "$shared";
import { LETTER_TYPE_COLORS } from "../domain/constants/pictograph-constants";

/**
 * Letter type border color mapping from desktop app
 * Format: [primary, secondary] for outer and inner borders
 */

/**
 * Get border colors for a letter
 */
export function getLetterBorderColors(letter: Letter | null | undefined): { primary: string; secondary: string } {
  if (letter === null || letter === undefined) {
    return { primary: "#000000", secondary: "#000000" };
  }

  const letterType = getLetterType(letter);
  const [primary, secondary] = LETTER_TYPE_COLORS[letterType] || [
    "#000000",
    "#000000",
  ];

  return { primary, secondary };
}

/**
 * Get primary border color for a letter
 */
export function getLetterBorderColor(
  letter: Letter | null | undefined
): string {
  return getLetterBorderColors(letter).primary;
}

// Legacy aliases for backward compatibility
export const getLetterBorderColorsSafe = getLetterBorderColors;
export const getLetterBorderColorSafe = getLetterBorderColor;

/**
 * Pictograph Rendering Utilities
 *
 * Direct composition functions for rendering pictographs without orchestration service.
 * This replaces the PictographRenderingService with explicit composition.
 */

import type { MotionColor } from "$shared";
import {
  GridMode,
  resolve,
  type IArrowPositioningOrchestrator,
  type IGridModeDeriver,
  type PictographData,
} from "$shared";
import { TYPES } from "$shared/inversify/types";
import { Point } from "fabric";
// TODO: These services have been archived - need to refactor this file
// import type { IOverlayRenderer } from "../../../../modules/animator/services/contracts/IOverlayRenderer";
// import type { ISvgUtilityService } from "../../../../modules/animator/services/implementations/SvgUtilityService";
import type { ArrowPosition } from "../../arrow";
import type { IArrowRenderer } from "../../arrow/rendering/services/contracts";
import type { IGridRenderingService } from "../../grid";

// TODO: These services are archived - defining minimal interfaces for compilation
interface ISvgUtilityService {
  createBaseSVG(): SVGElement;
  createErrorSVG(message: string): SVGElement;
}

interface IOverlayRenderer {
  renderOverlays(svg: SVGElement, data: PictographData): Promise<void>;
  renderIdLabel(svg: SVGElement, data: PictographData): void;
  renderDebugInfo(
    svg: SVGElement,
    data: PictographData,
    positions: Map<string, ArrowPosition>
  ): void;
}

export async function renderPictograph(
  data: PictographData
): Promise<SVGElement> {
  console.log("🎨 renderPictograph called for letter:", data.letter);

  try {
    console.log("🎨 Rendering pictograph:", data.letter);

    // Resolve required services directly
    const svgUtility = resolve<ISvgUtilityService>(TYPES.ISvgUtilityService);
    const gridRendering = resolve<IGridRenderingService>(
      TYPES.IGridRenderingService
    );
    const arrowRendering = resolve<IArrowRenderer>(TYPES.IArrowRenderer);
    const overlayRendering = resolve<IOverlayRenderer>(TYPES.IOverlayRenderer);
    const gridModeService = resolve<IGridModeDeriver>(TYPES.IGridModeDeriver);

    // Get arrow positioning from InversifyJS container
    const arrowPositioning: IArrowPositioningOrchestrator = resolve(
      TYPES.IArrowPositioningOrchestrator
    );

    // Create base SVG
    const svg = svgUtility.createBaseSVG();

    // Determine grid mode
    const blueMotion = data.motions.blue;
    const redMotion = data.motions.red;
    const gridMode: GridMode =
      blueMotion !== undefined && redMotion !== undefined
        ? gridModeService.deriveGridMode(blueMotion, redMotion)
        : GridMode.DIAMOND;

    // Render grid
    await gridRendering.renderGrid(svg, gridMode);

    // Calculate arrow positions
    const updatedPictographData =
      await arrowPositioning.calculateAllArrowPoints(data);

    // Render arrows
    if (updatedPictographData.motions) {
      for (const [color, motionData] of Object.entries(
        updatedPictographData.motions
      )) {
        if (motionData === undefined) continue;
        if (motionData.isVisible && motionData.arrowPlacementData) {
          const position = Object.assign(
            new Point(
              motionData.arrowPlacementData.positionX,
              motionData.arrowPlacementData.positionY
            ),
            { rotation: motionData.arrowPlacementData.rotationAngle }
          );
          await arrowRendering.renderArrowAtPosition(
            svg,
            color as MotionColor,
            position,
            motionData
          );
        }
      }
    }

    // Render overlays
    await overlayRendering.renderOverlays(svg, data);
    overlayRendering.renderIdLabel(svg, data);

    // Render debug info
    const arrowPositions = new Map<string, ArrowPosition>();
    if (updatedPictographData.motions) {
      for (const [color, motionData] of Object.entries(
        updatedPictographData.motions
      )) {
        if (motionData === undefined) continue;
        if (motionData.isVisible && motionData.arrowPlacementData) {
          const arrowPosition = Object.assign(
            new Point(
              motionData.arrowPlacementData.positionX,
              motionData.arrowPlacementData.positionY
            ),
            { rotation: motionData.arrowPlacementData.rotationAngle }
          );
          arrowPositions.set(color, arrowPosition);
        }
      }
    }
    overlayRendering.renderDebugInfo(svg, data, arrowPositions);

    return svg;
  } catch (error) {
    console.error("❌ Error rendering pictograph:", error);
    const errorMessage =
      error instanceof Error ? error.message : "Unknown error";
    const svgUtility = resolve<ISvgUtilityService>(TYPES.ISvgUtilityService);
    return svgUtility.createErrorSVG(errorMessage);
  }
}

/**
 * Batch render multiple pictographs
 */
export async function renderPictographs(
  dataArray: PictographData[]
): Promise<SVGElement[]> {
  const promises = dataArray.map((data) => renderPictograph(data));
  return Promise.all(promises);
}

/**
 * PNG file parsing utilities for extracting sequence metadata
 * Copied from animator module to avoid path issues
 */

import type { SequenceData } from "../../../foundation/domain/models/SequenceData";

export interface PNGParseResult {
  success: boolean;
  data?: unknown[]; // Legacy array format from PNG files
  error?: string;
}

// PNG constants
const PNG_SIGNATURE = [0x89, 0x50, 0x4e, 0x47, 0x0d, 0x0a, 0x1a, 0x0a];
const METADATA_KEYWORD = "metadata";

export async function extractSequenceFromPNG(
  file: File
): Promise<PNGParseResult> {
  try {
    const arrayBuffer = await readFileAsArrayBuffer(file);
    const uint8Array = new Uint8Array(arrayBuffer);

    const metadata = parsePNGMetadata(uint8Array);
    if (metadata && metadata.sequence) {
      // Transform the raw sequence data to ensure it has the expected format
      const transformedData = transformSequenceData(metadata.sequence);
      return {
        success: true,
        data: transformedData,
      };
    } else {
      return {
        success: false,
        error: "No sequence metadata found in PNG file",
      };
    }
  } catch (error) {
    return {
      success: false,
      error:
        error instanceof Error ? error.message : "Unknown error parsing PNG",
    };
  }
}

function readFileAsArrayBuffer(file: File): Promise<ArrayBuffer> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();

    reader.onload = (e) => {
      const result = e.target?.result as ArrayBuffer;
      if (result) {
        resolve(result);
      } else {
        reject(new Error("Failed to read file"));
      }
    };

    reader.onerror = () => reject(new Error("Failed to read file"));
    reader.readAsArrayBuffer(file);
  });
}

interface PNGMetadata {
  sequence?: SequenceData;
  [key: string]: unknown;
}

function parsePNGMetadata(data: Uint8Array): PNGMetadata | null {
  // Verify PNG signature
  for (let i = 0; i < PNG_SIGNATURE.length; i++) {
    if (data[i] !== PNG_SIGNATURE[i]) {
      throw new Error("Invalid PNG file");
    }
  }

  let offset = 8; // Skip PNG signature

  while (offset < data.length) {
    // Read chunk length (4 bytes, big-endian)
    const length =
      (data[offset]! << 24) |
      (data[offset + 1]! << 16) |
      (data[offset + 2]! << 8) |
      data[offset + 3]!;
    offset += 4;

    // Read chunk type (4 bytes)
    const type = String.fromCharCode(
      data[offset]!,
      data[offset + 1]!,
      data[offset + 2]!,
      data[offset + 3]!
    );
    offset += 4;

    if (type === "tEXt") {
      // Parse text chunk
      const textData = data.slice(offset, offset + length);
      const textString = new TextDecoder("latin1").decode(textData);
      const nullIndex = textString.indexOf("\0");

      if (nullIndex !== -1) {
        const keyword = textString.substring(0, nullIndex);
        const text = textString.substring(nullIndex + 1);

        if (keyword === METADATA_KEYWORD) {
          try {
            return JSON.parse(text) as PNGMetadata;
          } catch {
            // Failed to parse metadata JSON
          }
        }
      }
    }

    // Skip chunk data and CRC (4 bytes)
    offset += length + 4;

    // Stop at IEND chunk
    if (type === "IEND") {
      break;
    }
  }

  return null;
}

/**
 * Transform raw sequence data from PNG to ensure it has the expected format
 */
function transformSequenceData(rawData: unknown): unknown[] {
  if (!Array.isArray(rawData)) {
    throw new Error("Sequence data must be an array");
  }

  if (rawData.length < 2) {
    throw new Error("Sequence must contain at least metadata and one step");
  }

  // First element is metadata - keep as is
  const metadata = rawData[0] as Record<string, unknown>;

  // Transform remaining elements to ensure they have beat numbers
  const transformedSteps = rawData
    .slice(1)
    .map((step: Record<string, unknown>, index: number) => {
      // If step already has a beat property, use it
      if (typeof step["beat"] === "number") {
        return step;
      }

      // If step doesn't have a beat property, assign one based on position
      // Skip the first step if it's a start position (has sequence_start_position)
      const isStartPosition = step["sequence_start_position"] !== undefined;
      const beatNumber = isStartPosition ? 0 : index;

      return {
        ...step,
        beat: beatNumber,
      };
    });

  // ✅ FIXED: Return array format for legacy compatibility
  return [metadata, ...transformedSteps] as unknown[];
}

/**
 * Universal Metadata Extractor for TKA Sequences
 *
 * Bulletproof metadata extraction with automatic fallback strategy:
 * 1. Try WebP EXIF extraction first (optimal performance)
 * 2. Fallback to PNG tEXt extraction (backward compatibility)
 * 3. Report extraction source for optimization tracking
 * 4. Guarantee 100% metadata extraction success for valid files
 *
 * This extractor ensures no metadata is ever lost during the WebP transition
 * and provides a seamless upgrade path from PNG to WebP storage.
 */

import { PngMetadataExtractor } from "./png-metadata-extractor";
import { WebpMetadataExtractor } from "./webp-metadata-extractor";

export interface MetadataExtractionResult {
  sequenceName: string;
  beats: Record<string, unknown>[];
  sequenceLength: number;
  extractionSource: "webp" | "png";
  extractionTime: number; // milliseconds
  [key: string]: unknown;
}

export class UniversalMetadataExtractor {
  /**
   * Extract sequence metadata with bulletproof fallback strategy
   * @param sequenceName - Name of the sequence
   * @param basePath - Base path without extension (e.g., '/static/Explore/ABC/ABC_ver1')
   * @returns Promise<MetadataExtractionResult> - Complete metadata with extraction info
   */
  static async extractMetadata(
    sequenceName: string,
    basePath: string
  ): Promise<MetadataExtractionResult> {
    const startTime = Date.now();

    // Construct file paths
    const webpPath = `${basePath}.webp`;
    const pngPath = `${basePath}.png`;

    // Strategy 1: Try WebP extraction first (optimal)
    try {
      const webpMetadata = await WebpMetadataExtractor.extractCompleteMetadata(
        sequenceName,
        webpPath
      );

      const extractionTime = Date.now() - startTime;

      return {
        ...webpMetadata,
        extractionSource: "webp",
        extractionTime,
      };
    } catch (webpError) {
      console.log(
        `📝 WebP extraction failed for ${sequenceName}, trying PNG fallback...`
      );

      // Strategy 2: Fallback to PNG extraction
      try {
        const pngMetadata = await PngMetadataExtractor.extractCompleteMetadata(
          sequenceName,
          pngPath
        );

        const extractionTime = Date.now() - startTime;

        // Log fallback usage for optimization tracking
        console.warn(
          `⚠️  Using PNG fallback for ${sequenceName} - consider WebP migration`
        );

        // PNG metadata has different structure - extract what we need
        const sequence = pngMetadata.sequence || [];
        const beats = sequence.filter(
          (step: Record<string, unknown>) =>
            step["letter"] && !step["sequence_start_position"]
        );

        return {
          sequenceName,
          beats,
          sequenceLength: beats.length,
          extractionSource: "png",
          extractionTime,
          ...pngMetadata, // Include all other metadata fields
        };
      } catch (pngError) {
        // Both extraction methods failed
        const webpErrorMsg =
          webpError instanceof Error ? webpError.message : String(webpError);
        const pngErrorMsg =
          pngError instanceof Error ? pngError.message : String(pngError);

        console.error(
          `❌ Complete metadata extraction failure for ${sequenceName}:`
        );
        console.error(`   WebP error: ${webpErrorMsg}`);
        console.error(`   PNG error: ${pngErrorMsg}`);

        throw new Error(
          `Failed to extract metadata from both WebP and PNG files for ${sequenceName}. ` +
            `WebP: ${webpErrorMsg}. PNG: ${pngErrorMsg}`
        );
      }
    }
  }

  /**
   * Batch extract metadata for multiple sequences with progress tracking
   * @param sequences - Array of {sequenceName, basePath} objects
   * @param onProgress - Optional progress callback
   * @returns Promise<MetadataExtractionResult[]> - Array of extraction results
   */
  static async extractBatchMetadata(
    sequences: Array<{ sequenceName: string; basePath: string }>,
    onProgress?: (completed: number, total: number, current: string) => void
  ): Promise<MetadataExtractionResult[]> {
    const results: MetadataExtractionResult[] = [];
    const errors: Array<{ sequenceName: string; error: string }> = [];

    for (let i = 0; i < sequences.length; i++) {
      const { sequenceName, basePath } = sequences[i]!;

      try {
        if (onProgress) {
          onProgress(i, sequences.length, sequenceName);
        }

        const result = await this.extractMetadata(sequenceName, basePath);
        results.push(result);
      } catch (error) {
        const errorMessage =
          error instanceof Error ? error.message : String(error);
        errors.push({ sequenceName, error: errorMessage });
        console.error(
          `❌ Batch extraction failed for ${sequenceName}:`,
          errorMessage
        );
      }
    }

    if (onProgress) {
      onProgress(sequences.length, sequences.length, "Complete");
    }

    if (errors.length > 0) {
      console.warn(
        `⚠️  Batch extraction completed with ${errors.length} errors out of ${sequences.length} sequences`
      );
      errors.forEach(({ sequenceName, error }) => {
        console.warn(`   ${sequenceName}: ${error}`);
      });
    }

    return results;
  }

  /**
   * Check which file format is available for a sequence
   * @param basePath - Base path without extension
   * @returns Promise<{webp: boolean, png: boolean}> - File availability
   */
  static async checkFileAvailability(basePath: string): Promise<{
    webp: boolean;
    png: boolean;
    preferredPath: string;
    preferredFormat: "webp" | "png" | "none";
  }> {
    const webpPath = `${basePath}.webp`;
    const pngPath = `${basePath}.png`;

    const [webpExists, pngExists] = await Promise.all([
      this.fileExists(webpPath),
      this.fileExists(pngPath),
    ]);

    // Determine preferred format (WebP if available, otherwise PNG)
    let preferredFormat: "webp" | "png" | "none";
    let preferredPath: string;

    if (webpExists) {
      preferredFormat = "webp";
      preferredPath = webpPath;
    } else if (pngExists) {
      preferredFormat = "png";
      preferredPath = pngPath;
    } else {
      preferredFormat = "none";
      preferredPath = "";
    }

    return {
      webp: webpExists,
      png: pngExists,
      preferredPath,
      preferredFormat,
    };
  }

  /**
   * Get extraction statistics for optimization tracking
   * @param results - Array of extraction results
   * @returns Extraction statistics
   */
  static getExtractionStats(results: MetadataExtractionResult[]): {
    totalExtractions: number;
    webpExtractions: number;
    pngExtractions: number;
    webpPercentage: number;
    averageWebpTime: number;
    averagePngTime: number;
    optimizationOpportunities: number;
  } {
    const webpResults = results.filter((r) => r.extractionSource === "webp");
    const pngResults = results.filter((r) => r.extractionSource === "png");

    const averageWebpTime =
      webpResults.length > 0
        ? webpResults.reduce((sum, r) => sum + r.extractionTime, 0) /
          webpResults.length
        : 0;

    const averagePngTime =
      pngResults.length > 0
        ? pngResults.reduce((sum, r) => sum + r.extractionTime, 0) /
          pngResults.length
        : 0;

    return {
      totalExtractions: results.length,
      webpExtractions: webpResults.length,
      pngExtractions: pngResults.length,
      webpPercentage:
        results.length > 0 ? (webpResults.length / results.length) * 100 : 0,
      averageWebpTime,
      averagePngTime,
      optimizationOpportunities: pngResults.length, // PNG fallbacks = optimization opportunities
    };
  }

  /**
   * Utility method to check if a file exists and is accessible
   */
  private static async fileExists(filePath: string): Promise<boolean> {
    try {
      const response = await fetch(filePath, { method: "HEAD" });
      return response.ok;
    } catch {
      return false;
    }
  }

  /**
   * Simple interface matching the original extractors for drop-in replacement
   * @param sequenceName - Name of the sequence
   * @param preferredPath - Preferred file path (WebP or PNG)
   * @returns Promise with original interface format
   */
  static async extractCompleteMetadata(
    sequenceName: string,
    preferredPath: string
  ): Promise<{
    sequenceName: string;
    beats: Record<string, unknown>[];
    sequenceLength: number;
    [key: string]: unknown;
  }> {
    // Determine base path from preferred path
    const basePath = preferredPath.replace(/\.(webp|png)$/i, "");

    const result = await this.extractMetadata(sequenceName, basePath);

    // Return in original format (without extraction metadata)
    const { extractionSource, extractionTime, ...originalFormat } = result;
    return originalFormat;
  }

  /**
   * Validate metadata integrity between WebP and PNG versions
   * @param sequenceName - Name of the sequence
   * @param basePath - Base path without extension
   * @returns Promise<boolean> - True if metadata matches between formats
   */
  static async validateMetadataIntegrity(
    sequenceName: string,
    basePath: string
  ): Promise<{
    isValid: boolean;
    webpExists: boolean;
    pngExists: boolean;
    metadataMatches: boolean;
    differences?: string[];
  }> {
    const webpPath = `${basePath}.webp`;
    const pngPath = `${basePath}.png`;

    try {
      // Check file availability
      const availability = await this.checkFileAvailability(basePath);

      if (!availability.webp || !availability.png) {
        return {
          isValid: true, // No comparison needed if only one format exists
          webpExists: availability.webp,
          pngExists: availability.png,
          metadataMatches: true,
        };
      }

      // Extract from both formats
      const [webpMetadata, pngMetadata] = await Promise.all([
        WebpMetadataExtractor.extractCompleteMetadata(sequenceName, webpPath),
        PngMetadataExtractor.extractCompleteMetadata(sequenceName, pngPath),
      ]);

      // Compare critical fields - handle different structures
      const differences: string[] = [];

      // Extract PNG beats from sequence structure
      const pngSequence = pngMetadata.sequence || [];
      const pngBeats = pngSequence.filter(
        (step: Record<string, unknown>) =>
          step["letter"] && !step["sequence_start_position"]
      );

      if (webpMetadata.sequenceLength !== pngBeats.length) {
        differences.push(
          `sequenceLength: WebP=${webpMetadata.sequenceLength}, PNG=${pngBeats.length}`
        );
      }

      if (webpMetadata.beats.length !== pngBeats.length) {
        differences.push(
          `beats.length: WebP=${webpMetadata.beats.length}, PNG=${pngBeats.length}`
        );
      }

      // Deep comparison of beats array
      const webpBeatsJson = JSON.stringify(webpMetadata.beats);
      const pngBeatsJson = JSON.stringify(pngBeats);

      if (webpBeatsJson !== pngBeatsJson) {
        differences.push("beats content differs");
      }

      return {
        isValid: differences.length === 0,
        webpExists: true,
        pngExists: true,
        metadataMatches: differences.length === 0,
        ...(differences.length > 0 ? { differences } : {}),
      };
    } catch (error) {
      return {
        isValid: false,
        webpExists: await this.fileExists(webpPath),
        pngExists: await this.fileExists(pngPath),
        metadataMatches: false,
        differences: [
          `Validation error: ${error instanceof Error ? error.message : String(error)}`,
        ],
      };
    }
  }
}

/**
 * Letter Query Service - Letter-based pictograph lookups
 *
 * Single responsibility: Query pictographs by letter using CodexLetterMappingRepo
 * Uses shared services for CSV loading, parsing, and transformation.
 */

import type { CodexLetterMapping } from "$learn/codex";
import type { ICodexLetterMappingRepo } from "$learn/codex/services/contracts";
import type { CSVRow, MotionType, PictographData, Letter } from "$shared";
import { GridMode, type ICSVPictographParser } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { inject, injectable, optional } from "inversify";
import type { ParsedCsvRow } from "../../../../../modules/create/generate/shared/domain";
import type { ICSVLoader } from "../../../../foundation/services/contracts/data";
import type { ILetterQueryHandler } from "../../../../foundation/services/contracts/data";

interface CsvParseError {
  error: string;
  rowIndex?: number;
  rawRow: string;
  lineNumber: number;
}

interface CsvParseResult {
  rows: ParsedCsvRow[];
  errors: CsvParseError[];
}

// Temporary interface definition
interface ICSVParser {
  parseCSV(csvText: string): CsvParseResult;
}

@injectable()
export class LetterQueryHandler implements ILetterQueryHandler {
  private parsedData: Record<
    Exclude<GridMode, GridMode.SKEWED>,
    ParsedCsvRow[]
  > | null = null;
  private isInitialized = false;

  constructor(
    @inject(TYPES.ICSVLoader)
    private csvLoader: ICSVLoader,
    @inject(TYPES.ICSVParser)
    private CSVParser: ICSVParser,
    @inject(TYPES.ICSVPictographParserService)
    private csvPictographParser: ICSVPictographParser,
    // OPTIONAL: Only needed for Codex-specific methods (getPictographByLetter, getAllCodexPictographs)
    // NOT needed for getAllPictographVariations (used by Generate)
    @inject(TYPES.ICodexLetterMappingRepo)
    @optional()
    private letterMappingRepo?: ICodexLetterMappingRepo
  ) {}

  /**
   * Initialize CSV data and letter mapping repository if not already loaded
   */
  private async ensureInitialized(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Initialize letter mapping repository first
      if (
        this.letterMappingRepo &&
        typeof this.letterMappingRepo.initialize === "function"
      ) {
        await this.letterMappingRepo.initialize();
      }

      // Load raw CSV data
      const csvData = await this.csvLoader.loadCSVDataSet();

      // Parse CSV data using shared service
      const diamondParseResult = this.CSVParser.parseCSV(
        csvData.data ? csvData.data.diamondData : ""
      );
      const boxParseResult = this.CSVParser.parseCSV(
        csvData.data ? csvData.data.boxData : ""
      );

      // Only log significant parsing errors (not empty row issues)
      const significantDiamondErrors = diamondParseResult.errors.filter(
        (error: CsvParseError) =>
          !error.error.includes("missing required fields") ||
          (error.rawRow &&
            error.rawRow.trim() !== "" &&
            !error.rawRow.split(",").every((v: string) => v.trim() === ""))
      );
      const significantBoxErrors = boxParseResult.errors.filter(
        (error: CsvParseError) =>
          !error.error.includes("missing required fields") ||
          (error.rawRow &&
            error.rawRow.trim() !== "" &&
            !error.rawRow.split(",").every((v: string) => v.trim() === ""))
      );

      if (significantDiamondErrors.length > 0) {
        console.warn(
          `⚠️ Diamond CSV parsing errors (${significantDiamondErrors.length} significant):`
        );
        significantDiamondErrors
          .slice(0, 3)
          .forEach((error: CsvParseError, index: number) => {
            console.warn(
              `  Error ${index + 1}: Row ${error.rowIndex} - ${error.error}`
            );
            console.warn(`  Raw row: ${error.rawRow.substring(0, 100)}...`);
          });
      }
      if (significantBoxErrors.length > 0) {
        console.warn(
          `⚠️ Box CSV parsing errors (${significantBoxErrors.length} significant):`
        );
        significantBoxErrors
          .slice(0, 3)
          .forEach((error: CsvParseError, index: number) => {
            console.warn(
              `  Error ${index + 1}: Row ${error.rowIndex} - ${error.error}`
            );
            console.warn(`  Raw row: ${error.rawRow.substring(0, 100)}...`);
          });
      }

      this.parsedData = {
        [GridMode.DIAMOND]: diamondParseResult.rows,
        [GridMode.BOX]: boxParseResult.rows,
        // SKEWED mode doesn't have separate data - it uses both diamond and box
      };

      this.isInitialized = true;
    } catch (error) {
      console.error("❌ LetterQueryHandler: Error loading CSV data:", error);
      throw new Error(
        `Failed to load CSV data: ${error instanceof Error ? error.message : "Unknown error"}`
      );
    }
  }

  /**
   * Get a specific pictograph by letter using CodexLetterMappingRepo
   */
  async getPictographByLetter(
    letter: Letter,
    gridMode: GridMode
  ): Promise<PictographData | null> {
    if (!this.letterMappingRepo) {
      console.error(
        "❌ CodexLetterMappingRepo not available for getPictographByLetter"
      );
      return null;
    }

    await this.ensureInitialized();

    try {
      // Convert Letter enum to string for repository lookup
      const letterString = letter.toString();

      // Get letter mapping from repository
      const mapping = this.letterMappingRepo.getLetterMapping(letterString);
      if (!mapping) {
        console.warn(`⚠️ No letter mapping found for letter: ${letterString}`);
        return null;
      }

      // Find matching CSV row
      const csvRow = this.findMatchingCsvRowByMapping(
        letterString,
        mapping,
        gridMode
      );
      if (!csvRow) {
        console.warn(`⚠️ No CSV data found for letter ${letter}`);
        return null;
      }

      // Transform CSV row to PictographData using existing service
      return this.csvPictographParser.parseCSVRowToPictograph(
        csvRow as unknown as CSVRow,
        gridMode
      );
    } catch (error) {
      console.error(`❌ Error getting pictograph for letter ${letter}:`, error);
      return null;
    }
  }

  /**
   * Get all pictographs from the codex using CodexLetterMappingRepo
   */
  async getAllCodexPictographs(gridMode: GridMode): Promise<PictographData[]> {
    if (!this.letterMappingRepo) {
      console.error(
        "❌ CodexLetterMappingRepo not available for getAllCodexPictographs"
      );
      return [];
    }

    await this.ensureInitialized();

    try {
      const allLetters = this.letterMappingRepo.getAllLetters();

      const pictographs: PictographData[] = [];
      for (const letterString of allLetters) {
        const letter = letterString as Letter; // Convert string to Letter enum
        const pictograph = await this.getPictographByLetter(letter, gridMode);
        if (pictograph) {
          pictographs.push(pictograph);
        }
      }

      return pictographs;
    } catch (error) {
      console.error("❌ Error getting all codex pictographs:", error);
      return [];
    }
  }

  /**
   * Get ALL pictograph variations from CSV data (not limited by letter mappings)
   * This returns every row in the CSV as a separate pictograph, including multiple variations per letter
   */
  async getAllPictographVariations(
    gridMode: GridMode
  ): Promise<PictographData[]> {
    await this.ensureInitialized();

    try {
      if (!this.parsedData) {
        console.error("❌ No parsed CSV data available");
        return [];
      }

      // For SKEWED mode, default to diamond data
      const actualGridMode =
        gridMode === GridMode.SKEWED ? GridMode.DIAMOND : gridMode;
      const csvRows =
        this.parsedData[actualGridMode as Exclude<GridMode, GridMode.SKEWED>];
      if (!csvRows || csvRows.length === 0) {
        console.error(`❌ No CSV data available for grid mode: ${gridMode}`);
        return [];
      }

      const pictographs: PictographData[] = [];
      for (let i = 0; i < csvRows.length; i++) {
        const row = csvRows[i];
        try {
          const pictograph = this.csvPictographParser.parseCSVRowToPictograph(
            row as unknown as CSVRow,
            actualGridMode
          );
          if (pictograph) {
            pictographs.push(pictograph);
          }
        } catch (error) {
          console.warn(
            `⚠️ Failed to convert CSV row ${i} (letter: ${row?.letter}):`,
            error
          );
        }
      }

      return pictographs;
    } catch (error) {
      console.error("❌ Error getting all pictograph variations:", error);
      return [];
    }
  }

  /**
   * Search pictographs by letter patterns
   */
  async searchPictographs(
    searchTerm: string,
    gridMode: GridMode
  ): Promise<PictographData[]> {
    if (!this.letterMappingRepo) {
      console.error(
        "❌ CodexLetterMappingRepo not available for searchPictographs"
      );
      return [];
    }

    await this.ensureInitialized();

    try {
      const allLetters = this.letterMappingRepo.getAllLetters();
      const matchingLetters = allLetters.filter((letter: string) =>
        letter.toLowerCase().includes(searchTerm.toLowerCase())
      );

      const pictographs: PictographData[] = [];
      for (const letterString of matchingLetters) {
        const letter = letterString as Letter; // Convert string to Letter enum
        const pictograph = await this.getPictographByLetter(letter, gridMode);
        if (pictograph) {
          pictographs.push(pictograph);
        }
      }

      return pictographs;
    } catch (error) {
      console.error(
        `❌ Error searching pictographs for "${searchTerm}":`,
        error
      );
      return [];
    }
  }

  /**
   * Get pictographs for multiple letters
   */
  async getPictographsByLetters(
    letters: Letter[],
    gridMode: GridMode
  ): Promise<PictographData[]> {
    await this.ensureInitialized();

    const pictographs: PictographData[] = [];
    for (const letter of letters) {
      const pictograph = await this.getPictographByLetter(letter, gridMode);
      if (pictograph) {
        pictographs.push(pictograph);
      }
    }

    return pictographs;
  }

  /**
   * Find matching CSV row by letter mapping
   */
  private findMatchingCsvRowByMapping(
    letter: string,
    mapping: CodexLetterMapping,
    gridMode: GridMode
  ): ParsedCsvRow | null {
    if (!this.parsedData) {
      return null;
    }

    // For SKEWED mode, default to diamond data
    const actualGridMode =
      gridMode === GridMode.SKEWED ? GridMode.DIAMOND : gridMode;
    const csvRows =
      this.parsedData[actualGridMode as Exclude<GridMode, GridMode.SKEWED>];
    if (!csvRows) {
      return null;
    }

    // Handle the mismatch between JSON config and LetterMapping interface
    const mappingData = mapping as CodexLetterMapping & {
      blueMotion?: MotionType;
      redMotion?: MotionType;
    };
    const matchingRow = csvRows.find(
      (row: ParsedCsvRow) =>
        row.letter === letter &&
        row.startPosition === mapping.startPosition &&
        row.endPosition === mapping.endPosition &&
        row.blueMotionType ===
          String(mappingData.blueMotion ? mappingData.blueMotion : mappingData.blueMotionType) &&
        row.redMotionType ===
          String(mappingData.redMotion ? mappingData.redMotion : mappingData.redMotionType)
    );

    return matchingRow || null;
  }
}

/**
 * Turn Color Interpreter
 *
 * Determines which color (blue or red) to apply to top and bottom turn numbers
 * based on the letter type and motion arrangement.
 *
 * Ported from legacy TurnsTupleInterpreter logic.
 */

import { injectable } from "inversify";
import type { PictographData } from "$shared";

export type TurnNumberColor = "#2E3192" | "#ED1C24"; // Blue or Red (matches arrow colors)

export interface TurnColors {
  top: TurnNumberColor;
  bottom: TurnNumberColor;
}

type LetterType =
  | "TYPE1_HYBRID"
  | "TYPE1_NON_HYBRID"
  | "TYPE2"
  | "TYPE3"
  | "TYPE4"
  | "TYPE5"
  | "TYPE6";

const BLUE_HEX: TurnNumberColor = "#2E3192"; // Matches ArrowSvgColorTransformer
const RED_HEX: TurnNumberColor = "#ED1C24"; // Matches ArrowSvgColorTransformer

@injectable()
export class TurnColorInterpreter {
  /**
   * Determine the colors for top and bottom turn numbers
   */
  interpretTurnColors(
    letter: string | null | undefined,
    pictographData?: PictographData
  ): TurnColors {
    if (!letter || !pictographData) {
      // Default: top = blue, bottom = red
      return { top: BLUE_HEX, bottom: RED_HEX };
    }

    const letterType = this.determineLetterType(letter);
    const blueMotion = pictographData.motions.blue;
    const redMotion = pictographData.motions.red;

    if (!blueMotion || !redMotion) {
      return { top: BLUE_HEX, bottom: RED_HEX };
    }

    // Get actual motion colors (in case they differ from default)
    const blueColor = this.getMotionColor(blueMotion.color);
    const redColor = this.getMotionColor(redMotion.color);

    switch (letterType) {
      case "TYPE2": {
        // Top = Shift motion, Bottom = Static motion
        const shiftMotion = this.isShiftMotion(blueMotion)
          ? blueMotion
          : redMotion;
        const staticMotion = this.isShiftMotion(blueMotion)
          ? redMotion
          : blueMotion;
        return {
          top: this.getMotionColor(shiftMotion.color),
          bottom: this.getMotionColor(staticMotion.color),
        };
      }

      case "TYPE1_HYBRID": {
        // Top = Pro motion, Bottom = Anti motion
        const blueActualType = this.getActualMotionType(blueMotion);

        const proMotion = blueActualType === "pro" ? blueMotion : redMotion;
        const antiMotion = blueActualType === "anti" ? blueMotion : redMotion;

        return {
          top: this.getMotionColor(proMotion.color),
          bottom: this.getMotionColor(antiMotion.color),
        };
      }

      case "TYPE3": {
        // Top = Shift motion, Bottom = Dash motion
        const isDashBlue = blueMotion.motionType.toLowerCase() === "dash";
        const shiftMotion = isDashBlue ? redMotion : blueMotion;
        const dashMotion = isDashBlue ? blueMotion : redMotion;
        return {
          top: this.getMotionColor(shiftMotion.color),
          bottom: this.getMotionColor(dashMotion.color),
        };
      }

      case "TYPE4": {
        // Top = Dash motion, Bottom = Static motion
        const isDashBlue = blueMotion.motionType.toLowerCase() === "dash";
        const dashMotion = isDashBlue ? blueMotion : redMotion;
        const staticMotion = isDashBlue ? redMotion : blueMotion;
        return {
          top: this.getMotionColor(dashMotion.color),
          bottom: this.getMotionColor(staticMotion.color),
        };
      }

      case "TYPE5":
      case "TYPE6":
      default: {
        // Top = Blue motion, Bottom = Red motion
        return {
          top: blueColor,
          bottom: redColor,
        };
      }
    }
  }

  /**
   * Determine letter type based on letter pattern
   */
  private determineLetterType(letter: string): LetterType {
    // TYPE5: Dash-Static letters with suffix (Φ-, Ψ-, Λ-)
    if (["Φ-", "Ψ-", "Λ-"].includes(letter)) {
      return "TYPE5";
    }

    // TYPE6: Beta letters (α, β, Γ)
    if (["α", "β", "Γ"].includes(letter)) {
      return "TYPE6";
    }

    // TYPE3: Cross-Shift letters (ending with '-' except TYPE5)
    if (letter.endsWith("-")) {
      return "TYPE3";
    }

    // TYPE4: Dash letters (Φ, Ψ, Λ)
    if (["Φ", "Ψ", "Λ"].includes(letter)) {
      return "TYPE4";
    }

    // TYPE2: Shift-only letters (W, X, Y, Z, Σ, Δ, θ, Ω)
    if (["W", "X", "Y", "Z", "Σ", "Δ", "θ", "Ω"].includes(letter)) {
      return "TYPE2";
    }

    // TYPE1 Hybrid: Specific shift-static letters
    if (["C", "F", "I", "L", "O", "R", "U", "V"].includes(letter)) {
      return "TYPE1_HYBRID";
    }

    // TYPE1 Non-Hybrid: All other standard letters
    return "TYPE1_NON_HYBRID";
  }

  /**
   * Get the actual motion type, considering prefloat motion type for float motions
   */
  private getActualMotionType(motion: { motionType?: string; prefloatMotionType?: string }): string {
    const motionType = motion.motionType?.toLowerCase();
    // If it's a float motion, use the prefloatMotionType instead
    if (motionType === "float" && motion.prefloatMotionType) {
      return String(motion.prefloatMotionType.toLowerCase());
    }
    return String(motionType || "");
  }

  /**
   * Check if a motion is a shift motion (pro/anti/float)
   */
  private isShiftMotion(motion: { motionType?: string }): boolean {
    const motionType = motion.motionType?.toLowerCase();
    return ["pro", "anti", "float"].includes(motionType || "");
  }

  /**
   * Get color hex from motion color string
   */
  private getMotionColor(color: string | undefined): TurnNumberColor {
    if (!color) return BLUE_HEX;

    const normalized = color.toLowerCase();
    if (normalized === "blue" || normalized.includes("blue")) {
      return BLUE_HEX;
    }
    if (normalized === "red" || normalized.includes("red")) {
      return RED_HEX;
    }

    return BLUE_HEX; // Default fallback
  }
}

/**
 * Canvas Management Service
 *
 * Manages canvas creation, pooling, and memory optimization for TKA image export.
 * This service provides efficient canvas resource management to prevent memory
 * leaks and improve performance during image export operations.
 */

import { injectable } from "inversify";
import type { ICanvasManagementService } from "../contracts";

interface CanvasPoolEntry {
  canvas: HTMLCanvasElement;
  lastUsed: number;
  inUse: boolean;
}

@injectable()
export class CanvasManagementService implements ICanvasManagementService {
  // Canvas pool management
  private canvasPool = new Map<string, CanvasPoolEntry[]>();
  private readonly MAX_POOL_SIZE_PER_DIMENSION = 10;
  private readonly POOL_CLEANUP_INTERVAL = 30000; // 30 seconds
  private readonly MAX_UNUSED_TIME = 60000; // 1 minute

  // Memory tracking
  private totalCanvasMemory = 0;
  private readonly MAX_TOTAL_MEMORY = 512 * 1024 * 1024; // 512MB limit

  // Cleanup timer
  private cleanupTimer: number | undefined;

  constructor() {
    this.startCleanupTimer();
  }

  /**
   * Create optimized canvas
   * Uses pooling for common sizes to improve performance
   */
  createCanvas(width: number, height: number): HTMLCanvasElement {
    if (width <= 0 || height <= 0) {
      throw new Error(`Invalid canvas dimensions: ${width}x${height}`);
    }

    // Check memory limits
    const requiredMemory = width * height * 4; // RGBA bytes
    if (!this.canAllocateMemory(requiredMemory)) {
      this.forceCleanup();
      if (!this.canAllocateMemory(requiredMemory)) {
        throw new Error(
          `Cannot allocate ${Math.round(requiredMemory / 1024 / 1024)}MB for canvas - memory limit exceeded`
        );
      }
    }

    // Try to get canvas from pool
    const poolKey = this.getPoolKey(width, height);
    const pooledCanvas = this.getCanvasFromPool(poolKey);

    if (pooledCanvas) {
      this.prepareCanvas(pooledCanvas, width, height);
      return pooledCanvas;
    }

    // Create new canvas
    const canvas = document.createElement("canvas");
    this.prepareCanvas(canvas, width, height);

    // Track memory usage
    this.totalCanvasMemory += requiredMemory;

    return canvas;
  }

  /**
   * Clone canvas
   * Creates an exact copy of the source canvas
   */
  cloneCanvas(source: HTMLCanvasElement): HTMLCanvasElement {
    if (!source) {
      throw new Error("Source canvas is required for cloning");
    }

    const clone = this.createCanvas(source.width, source.height);
    const ctx = clone.getContext("2d");
    if (!ctx) {
      throw new Error("Failed to get 2D context from cloned canvas");
    }

    // Copy source canvas to clone
    ctx.drawImage(source, 0, 0);

    return clone;
  }

  /**
   * Dispose canvas resources
   * Returns canvas to pool or disposes if pool is full
   */
  disposeCanvas(canvas: HTMLCanvasElement): void {
    if (!canvas) {
      return;
    }

    const poolKey = this.getPoolKey(canvas.width, canvas.height);

    // Try to return to pool
    if (this.returnCanvasToPool(canvas, poolKey)) {
      return;
    }

    // Pool is full or canvas too large, dispose completely
    this.forceDisposeCanvas(canvas);
  }

  /**
   * Get canvas memory usage
   * Returns total memory used by tracked canvases
   */
  getMemoryUsage(): number {
    return this.totalCanvasMemory;
  }

  /**
   * Clear canvas cache
   * Removes all pooled canvases and resets memory tracking
   */
  clearCache(): void {
    // Dispose all pooled canvases
    for (const pool of this.canvasPool.values()) {
      for (const entry of pool) {
        this.forceDisposeCanvas(entry.canvas);
      }
    }

    this.canvasPool.clear();
    this.totalCanvasMemory = 0;
  }

  /**
   * Prepare canvas for use
   */
  private prepareCanvas(
    canvas: HTMLCanvasElement,
    width: number,
    height: number
  ): void {
    canvas.width = width;
    canvas.height = height;

    // Clear canvas
    const ctx = canvas.getContext("2d");
    if (!ctx) {
      throw new Error("Failed to get 2D context from canvas");
    }
    ctx.clearRect(0, 0, width, height);

    // Set default rendering properties for high quality
    ctx.imageSmoothingEnabled = true;
    ctx.imageSmoothingQuality = "high";
  }

  /**
   * Get pool key for canvas dimensions
   */
  private getPoolKey(width: number, height: number): string {
    return `${width}x${height}`;
  }

  /**
   * Get canvas from pool if available
   */
  private getCanvasFromPool(poolKey: string): HTMLCanvasElement | null {
    const pool = this.canvasPool.get(poolKey);
    if (!pool || pool.length === 0) {
      return null;
    }

    // Find an unused canvas
    const entry = pool.find((e) => !e.inUse);
    if (entry) {
      entry.inUse = true;
      entry.lastUsed = Date.now();
      return entry.canvas;
    }

    return null;
  }

  /**
   * Return canvas to pool
   */
  private returnCanvasToPool(
    canvas: HTMLCanvasElement,
    poolKey: string
  ): boolean {
    let pool = this.canvasPool.get(poolKey);

    if (!pool) {
      pool = [];
      this.canvasPool.set(poolKey, pool);
    }

    // Check if pool is full
    if (pool.length >= this.MAX_POOL_SIZE_PER_DIMENSION) {
      return false;
    }

    // Check if canvas is too large for pooling
    const memorySize = canvas.width * canvas.height * 4;
    if (memorySize > 64 * 1024 * 1024) {
      // 64MB limit for pooling
      return false;
    }

    // Add to pool
    pool.push({
      canvas,
      lastUsed: Date.now(),
      inUse: false,
    });

    return true;
  }

  /**
   * Force dispose canvas (remove from memory tracking)
   */
  private forceDisposeCanvas(canvas: HTMLCanvasElement | null): void {
    if (canvas) {
      const memorySize = canvas.width * canvas.height * 4;
      this.totalCanvasMemory = Math.max(0, this.totalCanvasMemory - memorySize);

      // Clear canvas
      canvas.width = 0;
      canvas.height = 0;
    }
  }

  /**
   * Check if we can allocate memory
   */
  private canAllocateMemory(requiredBytes: number): boolean {
    return this.totalCanvasMemory + requiredBytes <= this.MAX_TOTAL_MEMORY;
  }

  /**
   * Start cleanup timer
   */
  private startCleanupTimer(): void {
    if (typeof window !== "undefined") {
      this.cleanupTimer = window.setInterval(() => {
        this.cleanupUnusedCanvases();
      }, this.POOL_CLEANUP_INTERVAL) as unknown as number;
    }
  }

  /**
   * Cleanup unused canvases from pool
   */
  private cleanupUnusedCanvases(): void {
    const now = Date.now();

    for (const [poolKey, pool] of this.canvasPool.entries()) {
      // Remove old unused canvases
      const filtered = pool.filter((entry) => {
        const isOld = now - entry.lastUsed > this.MAX_UNUSED_TIME;
        const shouldRemove = !entry.inUse && isOld;

        if (shouldRemove) {
          this.forceDisposeCanvas(entry.canvas);
        }

        return !shouldRemove;
      });

      if (filtered.length === 0) {
        this.canvasPool.delete(poolKey);
      } else {
        this.canvasPool.set(poolKey, filtered);
      }
    }
  }

  /**
   * Force cleanup when memory limit is reached
   */
  private forceCleanup(): void {
    // Remove oldest unused canvases first
    const allEntries: Array<{
      poolKey: string;
      entry: CanvasPoolEntry;
      index: number;
    }> = [];

    for (const [poolKey, pool] of this.canvasPool.entries()) {
      pool.forEach((entry, index) => {
        if (!entry.inUse) {
          allEntries.push({ poolKey, entry, index });
        }
      });
    }

    // Sort by last used time (oldest first)
    allEntries.sort((a, b) => a.entry.lastUsed - b.entry.lastUsed);

    // Remove oldest canvases until we're under memory limit
    for (const { poolKey, entry, index } of allEntries) {
      const pool = this.canvasPool.get(poolKey);
      if (!pool) continue;

      this.forceDisposeCanvas(entry.canvas);
      pool.splice(index, 1);

      if (pool.length === 0) {
        this.canvasPool.delete(poolKey);
      }

      // Check if we've freed enough memory
      if (this.totalCanvasMemory < this.MAX_TOTAL_MEMORY * 0.8) {
        break;
      }
    }
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): {
    totalPools: number;
    totalCanvases: number;
    memoryUsageMB: number;
    maxMemoryMB: number;
    utilizationPercent: number;
  } {
    let totalCanvases = 0;
    for (const pool of this.canvasPool.values()) {
      totalCanvases += pool.length;
    }

    const memoryUsageMB = this.totalCanvasMemory / (1024 * 1024);
    const maxMemoryMB = this.MAX_TOTAL_MEMORY / (1024 * 1024);
    const utilizationPercent =
      (this.totalCanvasMemory / this.MAX_TOTAL_MEMORY) * 100;

    return {
      totalPools: this.canvasPool.size,
      totalCanvases,
      memoryUsageMB,
      maxMemoryMB,
      utilizationPercent,
    };
  }

  /**
   * Optimize canvas for specific use case
   */
  optimizeCanvas(
    canvas: HTMLCanvasElement,
    purpose: "share" | "preview" | "thumbnail"
  ): HTMLCanvasElement {
    const ctx = canvas.getContext("2d");
    if (!ctx) {
      throw new Error("Failed to get 2D context from canvas");
    }

    switch (purpose) {
      case "share":
        // High quality settings for export
        ctx.imageSmoothingEnabled = true;
        ctx.imageSmoothingQuality = "high";
        break;

      case "preview":
        // Balanced quality for preview
        ctx.imageSmoothingEnabled = true;
        ctx.imageSmoothingQuality = "medium";
        break;

      case "thumbnail":
        // Fast rendering for thumbnails
        ctx.imageSmoothingEnabled = true;
        ctx.imageSmoothingQuality = "low";
        break;
    }

    return canvas;
  }

  /**
   * Create canvas with specific performance settings
   */
  createOptimizedCanvas(
    width: number,
    height: number,
    purpose: "share" | "preview" | "thumbnail"
  ): HTMLCanvasElement {
    const canvas = this.createCanvas(width, height);
    return this.optimizeCanvas(canvas, purpose);
  }

  /**
   * Batch create canvases
   */
  batchCreateCanvases(
    dimensions: Array<{ width: number; height: number }>,
    purpose: "share" | "preview" | "thumbnail" = "share"
  ): HTMLCanvasElement[] {
    return dimensions.map(({ width, height }) =>
      this.createOptimizedCanvas(width, height, purpose)
    );
  }

  /**
   * Batch dispose canvases
   */
  batchDisposeCanvases(canvases: HTMLCanvasElement[]): void {
    for (const canvas of canvases) {
      this.disposeCanvas(canvas);
    }
  }

  /**
   * Dispose of service and cleanup resources
   */
  dispose(): void {
    if (this.cleanupTimer !== undefined) {
      clearInterval(this.cleanupTimer);
      this.cleanupTimer = undefined;
    }

    this.clearCache();
  }

  /**
   * Debug method to test canvas management
   */
  debugCanvasManagement(): {
    createTest: boolean;
    poolTest: boolean;
    memoryTest: boolean;
  } {
    let createTest = false;
    let poolTest = false;
    let memoryTest = false;

    try {
      // Test canvas creation
      const testCanvas = this.createCanvas(100, 100);
      createTest = testCanvas.width === 100 && testCanvas.height === 100;

      // Test pooling
      this.disposeCanvas(testCanvas);
      const pooledCanvas = this.createCanvas(100, 100);
      poolTest = pooledCanvas.width === 100 && pooledCanvas.height === 100;

      // Test memory tracking
      const initialMemory = this.getMemoryUsage();
      const bigCanvas = this.createCanvas(1000, 1000);
      const afterMemory = this.getMemoryUsage();
      memoryTest = afterMemory > initialMemory;

      // Cleanup
      this.disposeCanvas(pooledCanvas);
      this.disposeCanvas(bigCanvas);
    } catch (error) {
      console.error("Canvas management debug failed:", error);
    }

    return { createTest, poolTest, memoryTest };
  }
}

/**
 * Simple Image Composition Service
 *
 * Dead-simple approach: Render pictographs directly onto a single canvas.
 * No intermediate canvases, no complex calculations, just straightforward rendering.
 */

import type { BeatData, PictographData, SequenceData } from "$shared";
import { TYPES } from "$shared/inversify/types";
import { inject, injectable } from "inversify";
import type { SequenceExportOptions } from "../../domain/models";
import { renderPictographToSVG } from "../../utils/pictograph-to-svg";
import type {
  IDimensionCalculationService,
  ILayoutCalculationService,
  ITextRenderingService,
} from "../contracts";
import {} from "../contracts";
import type { IImageCompositionService } from "../contracts";

@injectable()
export class ImageCompositionService implements IImageCompositionService {
  constructor(
    @inject(TYPES.ILayoutCalculationService)
    private readonly layoutService: ILayoutCalculationService,
    @inject(TYPES.ITextRenderingService)
    private readonly textRenderingService: ITextRenderingService,
    @inject(TYPES.IDimensionCalculationService)
    private readonly dimensionCalculationService: IDimensionCalculationService
  ) {}
  /**
   * Compose complete sequence image from sequence data
   */
  async composeSequenceImage(
    sequence: SequenceData,
    options: SequenceExportOptions
  ): Promise<HTMLCanvasElement> {
    if (!sequence.beats || sequence.beats.length === 0) {
      throw new Error("Sequence must have at least one beat");
    }

    // Step 1: Calculate layout using LayoutCalculationService
    // This service has the proper lookup tables matching the desktop application
    const beatCount = sequence.beats.length;
    const [columns, rows] = this.layoutService.calculateLayout(
      beatCount,
      options.includeStartPosition
    );

    // Step 2: Calculate canvas dimensions including title space
    const beatSize = options.beatSize || 120;
    const canvasWidth = columns * beatSize;

    // Calculate title height if word should be included (using desktop-compatible logic)
    const titleHeight =
      options.addWord && sequence.word
        ? this.calculateTitleHeight(beatCount, options.beatScale || 1)
        : 0;
    const canvasHeight = rows * beatSize + titleHeight;

    const canvas = document.createElement("canvas");
    canvas.width = canvasWidth;
    canvas.height = canvasHeight;

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      throw new Error("Failed to get 2D context");
    }

    // Step 3: Fill white background
    ctx.fillStyle = "white";
    ctx.fillRect(0, 0, canvasWidth, canvasHeight);

    // Step 4: Render title if enabled
    if (options.addWord && sequence.word && titleHeight > 0) {
      this.textRenderingService.renderWordText(
        canvas,
        sequence.word,
        {
          margin: options.margin || 0,
          beatScale: options.beatScale || 1,
        },
        beatCount
      ); // Pass beat count for proper font scaling
    }

    // Step 4.5: Render difficulty badge if enabled and title area exists
    if (options.addDifficultyLevel && titleHeight > 0) {
      this.renderDifficultyBadge(canvas, sequence, titleHeight);
    }

    // Step 5: Render each pictograph directly onto the canvas (offset by title height)
    // Render start position if needed (always at column 0, row 0)
    if (options.includeStartPosition && sequence.startPosition) {
      await this.renderPictographAt(
        ctx,
        sequence.startPosition,
        0,
        0,
        beatSize,
        0,
        titleHeight
      ); // beatNumber = 0 for start position
    }

    // Step 6: Render all beats in the grid (offset by title height)
    // Calculate how many beats per row based on the layout
    const startColumn = options.includeStartPosition ? 1 : 0;
    const beatsPerRow = columns - startColumn; // Available columns for beats

    for (let i = 0; i < sequence.beats.length; i++) {
      const beat = sequence.beats[i];
      // Calculate position: beats fill remaining columns, then wrap to next row
      const col = startColumn + (i % beatsPerRow);
      const row = Math.floor(i / beatsPerRow);
      const beatNumber = i + 1; // Beat numbers start from 1
      await this.renderPictographAt(
        ctx,
        beat,
        col,
        row,
        beatSize,
        beatNumber,
        titleHeight
      );
    }

    // Step 7: Draw cell borders only between occupied cells (offset by title)
    this.drawSmartCellBorders(
      ctx,
      columns,
      rows,
      beatSize,
      sequence,
      options,
      titleHeight
    );

    return canvas;
  }

  /**
   * Render a single pictograph directly onto the canvas at the specified grid position
   */
  private async renderPictographAt(
    ctx: CanvasRenderingContext2D,
    pictographData: BeatData | PictographData,
    column: number,
    row: number,
    beatSize: number,
    beatNumber?: number,
    titleOffset: number = 0
  ): Promise<void> {
    try {
      // Generate SVG with beat number
      const svgString = await renderPictographToSVG(
        pictographData,
        beatSize,
        beatNumber
      );

      // Convert SVG to image
      const img = await this.svgStringToImage(svgString);

      // Draw directly onto the canvas at the correct position (offset by title)
      const x = column * beatSize;
      const y = row * beatSize + titleOffset;

      ctx.drawImage(img, x, y, beatSize, beatSize);
    } catch (error) {
      console.error(`❌ Failed to render beat at (${column}, ${row}):`, error);
      // Draw error placeholder
      const x = column * beatSize;
      const y = row * beatSize;
      ctx.fillStyle = "#ffeeee";
      ctx.fillRect(x + 5, y + 5, beatSize - 10, beatSize - 10);
      ctx.fillStyle = "#cc0000";
      ctx.font = "14px Arial";
      ctx.textAlign = "center";
      ctx.fillText("Error", x + beatSize / 2, y + beatSize / 2);
    }
  }

  /**
   * Draw cell borders only between occupied cells (smart grid)
   */
  private drawSmartCellBorders(
    ctx: CanvasRenderingContext2D,
    columns: number,
    rows: number,
    beatSize: number,
    sequence: SequenceData,
    options: SequenceExportOptions,
    titleOffset: number = 0
  ): void {
    ctx.strokeStyle = "#e0e0e0"; // Light gray border color (matching workbench)
    ctx.lineWidth = 1;

    // Create a map of occupied cells
    const occupiedCells = this.getOccupiedCells(sequence, options, columns);

    // Helper function to check if a cell is occupied
    const isOccupied = (col: number, row: number): boolean => {
      return occupiedCells.has(`${col},${row}`);
    };

    // Draw vertical lines between horizontally adjacent occupied cells
    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < columns - 1; col++) {
        if (isOccupied(col, row) && isOccupied(col + 1, row)) {
          const x = (col + 1) * beatSize;
          ctx.beginPath();
          ctx.moveTo(x, row * beatSize + titleOffset);
          ctx.lineTo(x, (row + 1) * beatSize + titleOffset);
          ctx.stroke();
        }
      }
    }

    // Draw horizontal lines between vertically adjacent occupied cells
    for (let col = 0; col < columns; col++) {
      for (let row = 0; row < rows - 1; row++) {
        if (isOccupied(col, row) && isOccupied(col, row + 1)) {
          const y = (row + 1) * beatSize + titleOffset;
          ctx.beginPath();
          ctx.moveTo(col * beatSize, y);
          ctx.lineTo((col + 1) * beatSize, y);
          ctx.stroke();
        }
      }
    }
  }

  /**
   * Get a set of occupied cell coordinates
   */
  private getOccupiedCells(
    sequence: SequenceData,
    options: SequenceExportOptions,
    columns: number
  ): Set<string> {
    const occupied = new Set<string>();

    // Add start position if included
    if (options.includeStartPosition && sequence.startPosition) {
      occupied.add("0,0");
    }

    // Add all beats
    const startColumn = options.includeStartPosition ? 1 : 0;
    const beatsPerRow = columns - startColumn;

    for (let i = 0; i < (sequence.beats.length || 0); i++) {
      const col = startColumn + (i % beatsPerRow);
      const row = Math.floor(i / beatsPerRow);
      occupied.add(`${col},${row}`);
    }

    return occupied;
  }

  /**
   * Convert SVG string to HTMLImageElement
   */
  private async svgStringToImage(svgString: string): Promise<HTMLImageElement> {
    return new Promise((resolve, reject) => {
      const img = new Image();

      img.onload = () => resolve(img);
      img.onerror = () => reject(new Error("Failed to load SVG as image"));

      // Convert SVG string to data URL
      const blob = new Blob([svgString], { type: "image/svg+xml" });
      const url = URL.createObjectURL(blob);
      img.src = url;

      // Clean up blob URL after image loads
      img.onload = () => {
        URL.revokeObjectURL(url);
        resolve(img);
      };
    });
  }

  // Stub methods to satisfy interface (not used in simple version)
  composeFromCanvases(): HTMLCanvasElement {
    throw new Error("Not implemented in simple version");
  }

  applyBackground(canvas: HTMLCanvasElement): HTMLCanvasElement {
    return canvas;
  }

  optimizeForExport(canvas: HTMLCanvasElement): HTMLCanvasElement {
    // Simple version doesn't need optimization
    return canvas;
  }

  /**
   * Calculate title height based on beat count (matches desktop logic)
   * This must match the calculation in TextRenderingService
   */
  private calculateTitleHeight(beatCount: number, beatScale: number): number {
    let baseHeight = 0;

    // Match desktop logic exactly based on beat count
    if (beatCount === 0) {
      baseHeight = 0;
    } else if (beatCount === 1) {
      baseHeight = 150;
    } else if (beatCount === 2) {
      baseHeight = 200;
    } else {
      // beatCount >= 3
      baseHeight = 300;
    }

    // Apply beat scale
    return Math.floor(baseHeight * beatScale);
  }

  /**
   * Render difficulty badge in the title area
   * Positioned in the top-right corner of the title area
   */
  private renderDifficultyBadge(
    canvas: HTMLCanvasElement,
    sequence: SequenceData,
    titleHeight: number
  ): void {
    // Get difficulty level from sequence
    const difficultyLevel = this.getDifficultyLevel(sequence);
    if (difficultyLevel === 0) {
      return;
    }

    // Calculate badge dimensions using desktop logic
    const badgeArea =
      this.dimensionCalculationService.calculateDifficultyBadgeArea(
        titleHeight
      );
    if (!badgeArea.available) {
      return;
    }

    // Position badge in top-right corner of title area
    const x = canvas.width - badgeArea.size - badgeArea.inset;
    const y = badgeArea.inset;

    // Render the badge using TextRenderingService
    this.textRenderingService.renderDifficultyBadge(
      canvas,
      difficultyLevel,
      [x, y],
      badgeArea.size
    );
  }

  /**
   * Extract difficulty level from sequence data
   * Returns numeric level (1-7) for badge rendering
   */
  private getDifficultyLevel(sequence: SequenceData): number {
    // First try the numeric level property
    if (typeof sequence.level === "number" && sequence.level > 0) {
      return sequence.level;
    }

    // Fall back to string difficultyLevel property
    if (typeof sequence.difficultyLevel === "string") {
      switch (sequence.difficultyLevel.toLowerCase()) {
        case "beginner":
          return 1;
        case "intermediate":
          return 3;
        case "advanced":
          return 5;
        default:
          return 1;
      }
    }

    // Default fallback
    return 0; // No badge
  }
}

/**
 * Image Format Converter Service - CONSOLIDATED
 *
 * Uses native browser APIs + file-saver for clean, simple image conversion.
 * Consolidates functionality from FileExportService to eliminate redundancy.
 * Provides both format conversion and file download capabilities.
 */

import type { IFileDownloadService } from "$shared/foundation/services/contracts";
import { TYPES } from "$shared/inversify";
import * as pkg from "file-saver";
import { inject, injectable } from "inversify";
import type { IImageFormatConverterService } from "../contracts";

// Define missing types locally for now
interface ImageFormatOptions {
  format: "png" | "jpeg" | "webp";
  quality?: number;
  compression?: number;
}

interface OptimizationSettings {
  enableCompression: boolean;
  quality: number;
  progressive?: boolean;
}
const { saveAs } = pkg;

@injectable()
export class ImageFormatConverterService
  implements IImageFormatConverterService
{
  constructor(
    @inject(TYPES.IFileDownloadService)
    private fileDownloadService: IFileDownloadService
  ) {}
  /**
   * Convert Canvas to Blob using native browser API
   */
  async canvasToBlob(
    canvas: HTMLCanvasElement,
    options: ImageFormatOptions
  ): Promise<Blob> {
    return new Promise((resolve, reject) => {
      canvas.toBlob(
        (blob) =>
          blob ? resolve(blob) : reject(new Error("Conversion failed")),
        this.getMimeType(options.format),
        options.quality
      );
    });
  }

  /**
   * Convert Canvas to Data URL using native browser API
   */
  canvasToDataURL(
    canvas: HTMLCanvasElement,
    options: ImageFormatOptions
  ): string {
    return canvas.toDataURL(this.getMimeType(options.format), options.quality);
  }

  /**
   * Batch convert multiple canvases
   */
  async convertMultipleCanvasesToBlobs(
    canvases: HTMLCanvasElement[],
    options: ImageFormatOptions
  ): Promise<Blob[]> {
    return Promise.all(
      canvases.map((canvas) => this.canvasToBlob(canvas, options))
    );
  }

  /**
   * Download blob as file using file-saver
   */
  downloadBlob(blob: Blob, filename: string): void {
    saveAs(blob, filename);
  }

  // Simple implementations for interface compatibility
  optimizeForUseCase(
    blob: Blob,
    _optimization: OptimizationSettings
  ): Blob {
    return blob; // No optimization needed for dance notation
  }

  getOptimalFormat(_canvas: HTMLCanvasElement): "PNG" | "JPEG" | "WEBP" {
    return "PNG"; // PNG is fine for dance diagrams
  }

  validateFormatOptions(options: ImageFormatOptions): boolean {
    return options && ["PNG", "JPEG", "WEBP"].includes(options.format);
  }

  getSupportedFormats(): string[] {
    return ["PNG", "JPEG", "WEBP"];
  }

  estimateFileSize(
    _canvas: HTMLCanvasElement,
    _options: ImageFormatOptions
  ): number {
    return 100000; // Rough estimate - not critical for this app
  }

  getConversionStats() {
    return {
      totalConversions: 0,
      totalBytesProcessed: 0,
      averageCompressionRatio: 0,
      formatUsage: {},
    };
  }

  cleanup(): void {
    // No cleanup needed
  }

  /**
   * Download canvas as file (consolidated from FileExportService)
   * Handles canvas-to-blob conversion and browser downloads
   */
  async downloadCanvas(
    canvas: HTMLCanvasElement,
    filename: string,
    format: "PNG" | "JPEG" = "PNG",
    quality: number = 1.0
  ): Promise<void> {
    try {
      const blob = await this.canvasToBlob(canvas, {
        format: format.toLowerCase() as "png" | "jpeg",
        quality,
      });

      await this.fileDownloadService.downloadBlob(blob, filename);
    } catch (error) {
      throw new Error(
        `Download failed: ${error instanceof Error ? error.message : "Unknown error"}`
      );
    }
  }

  /**
   * Generate versioned filename (consolidated from FileExportService)
   * Matches desktop filename generation with versioning
   */
  generateVersionedFilename(
    word: string,
    format: string,
    timestamp?: Date
  ): string {
    // Sanitize word for filename use
    const sanitizedWord = this.sanitizeForFilename(word) || "sequence";

    // Use provided timestamp or current time
    const date = timestamp ?? new Date();
    const dateString = date.toISOString().slice(0, 10).replace(/-/g, ""); // YYYYMMDD

    // Generate version number (in real implementation, this would check for existing files)
    const version = 1;

    // Format extension
    const extension = format.toLowerCase();

    return `${sanitizedWord}_v${version}_${dateString}.${extension}`;
  }

  /**
   * Sanitize string for filename use
   */
  private sanitizeForFilename(input: string): string {
    // Replace invalid filename characters with underscores
    return input
      .replace(/[<>:"/\\|?*]/g, "_")
      .replace(/\s+/g, "_")
      .substring(0, 100); // Reasonable length limit
  }

  private getMimeType(format: string): string {
    const mimeTypes: Record<string, string> = {
      png: "image/png",
      jpeg: "image/jpeg",
      webp: "image/webp",
      PNG: "image/png",
      JPEG: "image/jpeg",
      WEBP: "image/webp",
    };
    return mimeTypes[format] ?? "image/png";
  }
}

/**
 * SVG to Canvas Converter Service - SIMPLIFIED
 *
 * Uses fabric.js for clean, reliable SVG-to-Canvas conversion.
 * Replaces 300+ lines of over-engineered conversion logic with ~50 lines.
 */

import { Canvas, loadSVGFromString, Rect, util } from "fabric";
import { injectable } from "inversify";
import type { RenderQualitySettings, SVGConversionOptions } from "../../domain";
import type { ISVGToCanvasConverterService } from "../contracts";

@injectable()
export class SVGToCanvasConverterService
  implements ISVGToCanvasConverterService
{
  private defaultQuality: RenderQualitySettings = {
    antialiasing: true,
    smoothScaling: true,
    imageSmoothingQuality: "high",
    scale: 1,
    dpi: 96,
  };

  /**
   * Convert SVG string to Canvas using fabric.js
   */
  async convertSVGStringToCanvas(
    svgString: string,
    options: SVGConversionOptions
  ): Promise<HTMLCanvasElement> {
    return new Promise((resolve, reject) => {
      // Create target canvas
      const targetCanvas = document.createElement("canvas");
      targetCanvas.width = options.width ?? 144;
      targetCanvas.height = options.height ?? 144;

      // Parse and load SVG
      loadSVGFromString(svgString)
        .then(({ objects, options: svgOptions }) => {
          try {
            // Create fabric canvas
            const fabricCanvas = new Canvas(targetCanvas, {
              width: targetCanvas.width,
              height: targetCanvas.height,
              backgroundColor: options.backgroundColor ?? "white",
            });

            // Add SVG objects to canvas
            const validObjects = objects.filter((obj) => obj !== null);

            if (validObjects.length > 0) {
              // Group all SVG elements
              const svgGroup = util.groupSVGElements(validObjects, svgOptions);

              if (svgGroup) {
                // CRITICAL: Don't scale! Use SVG at native size to prevent clipping
                // The canvas should be sized to accommodate the full SVG content
                const canvasWidth = fabricCanvas.width || targetCanvas.width;
                const canvasHeight = fabricCanvas.height || targetCanvas.height;

                // REMOVED SCALING - was causing pictograph content to be clipped
                // if (svgGroup.width && svgGroup.height) {
                //   const scaleX = canvasWidth / svgGroup.width;
                //   const scaleY = canvasHeight / svgGroup.height;
                //   const scale = Math.min(scaleX, scaleY);
                //   svgGroup.scale(scale);
                // }

                // Center the group at native size
                svgGroup.set({
                  left: canvasWidth / 2,
                  top: canvasHeight / 2,
                  originX: "center",
                  originY: "center",
                });

                fabricCanvas.add(svgGroup);
              }
            } else {
              // If no objects, add a placeholder
              const placeholder = new Rect({
                left: targetCanvas.width / 2,
                top: targetCanvas.height / 2,
                width: targetCanvas.width * 0.8,
                height: targetCanvas.height * 0.8,
                fill: "#f3f4f6",
                stroke: "#d1d5db",
                strokeWidth: 2,
                originX: "center",
                originY: "center",
              });
              fabricCanvas.add(placeholder);
            }

            // Render and return
            fabricCanvas.renderAll();
            resolve(fabricCanvas.getElement());
          } catch (error) {
            reject(
              new Error(
                `SVG conversion failed: ${error instanceof Error ? error.message : String(error)}`
              )
            );
          }
        })
        .catch((error) => {
          reject(
            new Error(
              `SVG parsing failed: ${error instanceof Error ? error.message : error}`
            )
          );
        });
    });
  }

  /**
   * Convert SVG element to Canvas
   */
  async convertSVGElementToCanvas(
    svgElement: SVGElement,
    options: SVGConversionOptions
  ): Promise<HTMLCanvasElement> {
    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(svgElement);
    return this.convertSVGStringToCanvas(svgString, options);
  }

  /**
   * Batch convert multiple SVG strings
   */
  async convertMultipleSVGsToCanvases(
    svgStrings: string[],
    options: SVGConversionOptions
  ): Promise<HTMLCanvasElement[]> {
    return Promise.all(
      svgStrings.map((svg) => this.convertSVGStringToCanvas(svg, options))
    );
  }

  // Simple implementations of required interface methods
  setDefaultQuality(settings: RenderQualitySettings): void {
    this.defaultQuality = { ...settings };
  }

  getQualitySettings(): RenderQualitySettings {
    return { ...this.defaultQuality };
  }

  validateSVG(svgContent: string | SVGElement): boolean {
    if (typeof svgContent === "string") {
      return (
        svgContent.trim().includes("<svg") && svgContent.includes("</svg>")
      );
    }
    return svgContent instanceof SVGElement;
  }

  getMemoryUsage() {
    return { activeConversions: 0, totalMemoryUsed: 0, peakMemoryUsed: 0 };
  }

  cleanup(): void {
    // No cleanup needed with fabric.js approach
  }
}

/**
 * Theme Service - Dynamic Theme Detection and Application
 *
 * Handles detection of the current background theme and applies appropriate
 * CSS variables for consistent theming across components like dropdowns,
 * option picker headers, and other UI elements.
 */

export class ThemeService {
  private static readonly SETTINGS_KEY = "tka-modern-web-settings";
  private static readonly DEFAULT_THEME = "nightSky";

  /**
   * Get the current background theme from localStorage
   */
  static getCurrentTheme(): string {
    if (typeof window === "undefined" || typeof localStorage === "undefined") {
      return this.DEFAULT_THEME;
    }

    try {
      const stored = localStorage.getItem(this.SETTINGS_KEY);
      if (stored) {
        const settings = JSON.parse(stored) as { backgroundType?: string };
        return String(settings.backgroundType || this.DEFAULT_THEME);
      }
    } catch (error) {
      console.warn("Failed to load current theme:", error);
    }

    return this.DEFAULT_THEME;
  }

  /**
   * Apply the current theme to CSS variables
   * This updates the --dropdown-*-current variables to match the active background
   */
  static applyCurrentTheme(): void {
    if (typeof window === "undefined" || typeof document === "undefined") {
      return;
    }

    const currentTheme = this.getCurrentTheme();
    const root = document.documentElement;

    // Map of theme CSS variable suffixes
    const themeVariables = [
      "dropdown-bg",
      "dropdown-text",
      "dropdown-description",
      "dropdown-hover",
      "dropdown-current",
      "header-bg",
      "header-border",
      "header-text",
      "panel-bg",
      "panel-border",
      "panel-hover",
      "card-bg",
      "card-border",
      "card-hover",
      "text-primary",
      "text-secondary",
      "input-bg",
      "input-border",
      "input-focus",
      "button-active",
    ];

    // Update each variable to use the current theme
    themeVariables.forEach((variable) => {
      const themeSpecificVar = `--${variable}-${currentTheme}`;
      const currentVar = `--${variable}-current`;

      // Get the theme-specific value
      const themeValue =
        getComputedStyle(root).getPropertyValue(themeSpecificVar);

      if (themeValue) {
        root.style.setProperty(currentVar, themeValue);
      }
    });
  }

  /**
   * Initialize theme service - should be called on app startup
   */
  static initialize(): void {
    this.applyCurrentTheme();

    // Listen for storage changes to update theme dynamically
    if (typeof window !== "undefined") {
      window.addEventListener("storage", (event) => {
        if (event.key === this.SETTINGS_KEY) {
          this.applyCurrentTheme();
        }
      });
    }
  }

  /**
   * Force theme update - useful when background changes
   */
  static updateTheme(_newTheme: string): void {
    this.applyCurrentTheme();
  }
}

/**
 * HMR State Backup Utility
 *
 * Provides automatic state backup to localStorage during development
 * to preserve application state across HMR reloads.
 */

// ============================================================================
// TYPES
// ============================================================================

export interface HMRStateBackupOptions {
  key: string;
  enabled?: boolean;
  debounceMs?: number;
}

export interface BackupState<T = unknown> {
  timestamp: number;
  data: T;
  version: string;
}

// ============================================================================
// HMR STATE BACKUP CLASS
// ============================================================================

export class HMRStateBackup<T = unknown> {
  private key: string;
  private enabled: boolean;
  private debounceMs: number;
  private debounceTimer: number | null = null;
  private version: string = "1.0.0";

  constructor(options: HMRStateBackupOptions) {
    this.key = `hmr-backup-${options.key}`;
    this.enabled = options.enabled !== undefined ? options.enabled : (import.meta.env.DEV || false);
    this.debounceMs = options.debounceMs ?? 500;
  }

  /**
   * Save state to localStorage with debouncing
   */
  save(state: T): void {
    if (!this.enabled || typeof localStorage === "undefined") {
      return;
    }

    // Clear existing timer
    if (this.debounceTimer) {
      clearTimeout(this.debounceTimer);
    }

    // Debounce the save operation
    this.debounceTimer = setTimeout(() => {
      try {
        const backupState: BackupState<T> = {
          timestamp: Date.now(),
          data: state,
          version: this.version,
        };

        localStorage.setItem(this.key, JSON.stringify(backupState));
        console.log(`💾 HMR: State backed up for key: ${this.key}`);
      } catch (error) {
        console.warn(
          `⚠️ HMR: Failed to backup state for key: ${this.key}`,
          error
        );
      }
    }, this.debounceMs) as unknown as number;
  }

  /**
   * Load state from localStorage
   */
  load(): T | null {
    if (!this.enabled || typeof localStorage === "undefined") {
      return null;
    }

    try {
      const stored = localStorage.getItem(this.key);
      if (!stored) {
        return null;
      }

      const backupState = JSON.parse(stored) as BackupState;

      // Check if backup is recent (within last hour)
      const hourAgo = Date.now() - 60 * 60 * 1000;
      if (backupState.timestamp < hourAgo) {
        console.log(`🗑️ HMR: Removing stale backup for key: ${this.key}`);
        this.clear();
        return null;
      }

      console.log(`📂 HMR: State restored from backup for key: ${this.key}`);
      return backupState.data as T | null;
    } catch (error) {
      console.warn(
        `⚠️ HMR: Failed to load backup state for key: ${this.key}`,
        error
      );
      return null;
    }
  }

  /**
   * Clear backup from localStorage
   */
  clear(): void {
    if (typeof localStorage === "undefined") {
      return;
    }

    try {
      localStorage.removeItem(this.key);
      console.log(`🗑️ HMR: Backup cleared for key: ${this.key}`);
    } catch (error) {
      console.warn(
        `⚠️ HMR: Failed to clear backup for key: ${this.key}`,
        error
      );
    }
  }

  /**
   * Check if backup exists
   */
  hasBackup(): boolean {
    if (!this.enabled || typeof localStorage === "undefined") {
      return false;
    }

    return localStorage.getItem(this.key) !== null;
  }
}

// ============================================================================
// CONVENIENCE FUNCTIONS
// ============================================================================

/**
 * Create a simple HMR state backup instance
 */
export function createHMRBackup<T>(
  key: string,
  options?: Partial<HMRStateBackupOptions>
): HMRStateBackup<T> {
  return new HMRStateBackup<T>({
    key,
    ...options,
  });
}

/**
 * Create a reactive state with automatic HMR backup
 */
export function createBackedUpState<T>(
  key: string,
  initialValue: T,
  options?: Partial<HMRStateBackupOptions>
): {
  backup: HMRStateBackup<T>;
  getInitialValue: () => T;
  save: (value: T) => void;
  load: () => T | null;
  clear: () => void;
} {
  const backup = createHMRBackup<T>(key, options);

  return {
    backup,
    getInitialValue: () => backup.load() ?? initialValue,
    save: (value: T) => backup.save(value),
    load: () => backup.load(),
    clear: () => backup.clear(),
  };
}

// ============================================================================
// SVELTE INTEGRATION HELPERS
// ============================================================================

/**
 * Create a Svelte rune state with HMR backup
 * Usage in component:
 *
 * const { state, saveState } = createHMRState('my-form', { step: 1, data: {} });
 *
 * $effect(() => {
 *   saveState(state);
 * });
 */
export function createHMRState<T>(
  key: string,
  initialValue: T,
  options?: Partial<HMRStateBackupOptions>
) {
  const backup = createHMRBackup<T>(key, options);
  const restoredValue = backup.load() ?? initialValue;

  return {
    initialValue: restoredValue,
    saveState: (value: T) => backup.save(value),
    clearBackup: () => backup.clear(),
    hasBackup: () => backup.hasBackup(),
  };
}

/**
 * Clear all HMR backups (useful for cleanup)
 */
export function clearAllHMRBackups(): void {
  if (typeof localStorage === "undefined") {
    return;
  }

  const keys = Object.keys(localStorage);
  const hmrKeys = keys.filter((key) => key.startsWith("hmr-backup-"));

  hmrKeys.forEach((key) => {
    localStorage.removeItem(key);
  });

  console.log(`🗑️ HMR: Cleared ${hmrKeys.length} backup entries`);
}
